INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/318606, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"久遠の園を象る者", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/318605, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/2, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【ＭＯＤＥ３】大切な人の声すら届かず、創造主\nは己を拒む者たちに終の力を放たんとする。仲間\nとともに、相棒とともに、眩き未来を取り戻せ。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力は、バトル中に受ける防御力ダウ\n　ンスキルの影響を受けない。\n・久遠の園を象る者から受けるすべてのダメージ\n　で発生する、パーティキャラのＴＰ回復量を中\n　ダウンさせる。\n・ユニオンバーストは、敵全体のバリアを解除し、\n　敵全体に自身の魔法攻撃力とキャラの最大ＨＰ\n　に応じた魔法割合ダメージを与える。敵のＨＰ\n　が50％未満の場合、束縛状態にする。このス\n　キルの状態異常はダメージを無効化しても付与\n　される。\n・敵全体のステータスアップ効果を解除した後、\n　敵全体に魔法中ダメージを与え、沈黙状態を付\n　与し、ＨＰ吸収を特大ダウンさせる。さらに、\n　自身の物理防御力と魔法防御力を中ダウンさせ\n　る。このスキルの状態異常と効果はダメージを\n　無効化しても付与される。\n・最も近い敵１キャラに魔法大ダメージを与える。\n・自身のＨＰが50％を切ると、行動パターンが\n　変化し、さらに攻撃が激しく凶暴になる。");
