INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/319700, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"フブキ", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/319700, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【物理】王家の装備を狙う、天才発明家の三女。\n浮遊兵器から銃撃の嵐や特大爆弾をお見舞いし、\n敵が慌てふためく姿を上空から嘲笑う。\n\n・バトル開始時、自身の物理防御力と魔法防御力\n　を極大アップさせる。この防御力アップ効果で\n　上昇した防御力は、バトル中に受ける防御力ダ\n　ウンスキルの影響を受けない。\n・バトル開始時、自身にＴＰダウン100％\n　カット効果を付与する。\n・フブキから受けるすべてのダメージで発生\n　する、パーティキャラのＴＰ回復量を中ダウン\n　させる。\n・フブキのＨＰが一定値以下になると、浮遊兵器\n　が墜落し行動不能になる。行動不能の間、自身\n　の物理防御力と魔法防御力が０になる。\n・ユニオンバーストは、敵全体のバリアを解除\n　し、物理特大ダメージを与え、さらにスタンさ\n　せる。このスキルの状態異常はダメージを無効\n　化しても付与される。\n・通常攻撃は最も近い敵１キャラに物理大ダ\n　メージを与え、さらにスタンさせる。このスキ\n　ルの状態異常はダメージを無効化しても付与さ\n　れる。\n・敵全体をノックバックさせ、フブキちゃんダイ\n　ナマイツ♡を召喚する。\n・フブキちゃんダイナマイツ♡は、一定時間経過\n　すると敵全体に物理大ダメージを与え、さらに\n　スタンを付与したあと、消滅する。このスキル\n　の状態異常はダメージを無効化しても付与され\n　る。ただしフブキちゃんダイナマイツ♡のＨＰ\n　が一定値以下になった場合は、フブキに物理固\n　定ダメージを与えたあと、消滅する。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/319800, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"ワカナ", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/319800, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/1, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【物理】盗賊ギルド、バンディ・シスターズ次女。\n戦闘中後方でおびえているワカナを助けるために、\nスパイク・ブラザーズが冒険者の行く手を阻む。\n\n・バトル開始時、自分の物理防御力と魔法防御力\n　を小アップさせる。この防御力アップ効果で上\n　昇した防御力はバトル中に受ける防御力ダウン\n　スキルの影響を受けない。\n・バトル開始時、自分にＴＰダウン100％カッ\n　ト効果を付与する。\n・ワカナとスパイク・ブラザーズから受けるすべ\n　てのダメージで発生する、パーティキャラの\n　ＴＰ回復量を中ダウンさせる。\n・ユニオンバーストは、敵全体に物理大ダメージ\n　を与え、さらにスタンさせる。このスキルの状\n　態異常はダメージを無効化しても付与される。\n　同時に、ワカナに物理固定ダメージを与える。\n・自分を除く味方全体の物理攻撃力を大アップ\n　させ、自分のＴＰを中回復させる。\n・バトル開始時、スパイク・ブラザーズが助けに\n　くる。その際、敵全体をノックバックさせる。\n・スパイク・ブラザーズは、行動する度にワカナ\n　の物理防御力と魔法防御力を極大アップさせる。\n・スパイク・ブラザーズは、自分の行動速度を特\n　大アップさせる。");
INSERT INTO `v1_2ea2fbd558f287f6d0d6d1e203f2c4a6c8ae75ae282f01bef2082bd007c47de8` VALUES (/*e2ea6ca2df6ca5fad61ed9ab9f7155aa7193a8760955face963dfd9853b8ff78*/320000, /*deb78dc40fc2c938a0308789e249dc690048140880730eef1aa92bb70a567551*/"カリド", /*7489fe449fadfca5f7c00a580dff1edad7378dfe631677c1c8ad5c7decf09097*/320000, /*f65bc453c735e13837d9d845d6f3d3ba35b94944b98325b380a630402a66df12*/0, /*e445d0d7c735188da12f8557093eb1b3da24f3f753e8228cc76d330609221a1a*/16, /*538cc696ee60936e32ae5f1bb41b19700481910b4bac2ac3de77c05f4e4f1a36*/270, /*d037b6ebee9d9ed3f8c62dbdef8a3f8594f4315306fe1fcb773c45f3dcaace97*/3000, /*1de54c59712133823b0e003b0e2ceec66380595b6c7aaf49bdff95d323e9b234*/2, /*6871bcc79c32f3f1163a01bb8ee7bb958c3ed85f4df80d69ddf47254e20623cb*/1.6, /*e5588ccd85bd235f1cb40f395303d70ad35eac150e751c308d5fe01fb0d4babf*/0, /*a28abb3506fac82150745aa33567be51f7e462609c341669e35a7002342b5b61*/0, /*cc1e8a5db0f07f1922f614ad3ca343c8fc92cfc06b36b4a047983eb397379541*/0, /*4546a910e3223aca0ad20dff3cdf5ab2bb5c028140baefaefad78a6d12737f23*/"【魔法】はるか古に作られし防衛兵器。邪なる者\nの願いにより封を解かれ、地中深くより現れる。\n\n・ユニオンバーストは、敵全体に魔法固定ダメー\n　ジを与え、魔法防御力を小ダウンさせる。この\n　スキルの状態異常はダメージを無効化しても付\n　与される。\n・最も近い敵１キャラを中心とした小範囲に魔法\n　大ダメージを与える。\n・自身に魔法小ダメージを与え、さらに物理防御\n　力と魔法防御力を小ダウンさせる。\n・自身のＨＰが50％を切ると、右腕部が破損\n　し、行動パターンが変化する。さらに自身の物\n　理防御力、魔法防御力を特大ダウンさせる。\n・最も近い敵１キャラに魔法大ダメージを与え\n　る。");
