INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (2, "2030/12/17 14:59:59", 13, 2104010, 106401, -363.0, 13003, "2015/12/17 15:00:00", 1, 0.0, 0.0, "シェフィ", "みんなとの大切な思い出が\nたくさん詰まってるのね\n一緒に振り返って、\n心に刻みたいわ", 1, 0);
INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 13, 0, 105801, -363.0, 13004, "2015/12/17 15:00:00", 1, 0.0, 0.0, "ペコリーヌ", "女の子との思い出が\nたくさん見られますよ！\nヒューヒュー♪\nモテモテですねー☆", 1, 0);
INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 14, 0, 105901, -363.0, 14001, "2015/12/17 15:00:00", 1, 0.0, 0.0, "コッコロ", "育成に必要なアイテムが\n獲得できるクエストです\nわたくし、主さまのために、\n強くなりますよ", 1, 0);
