INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 14, 0, 105801, -363.0, 14003, "2015/12/17 15:00:00", 1, 0.0, 0.0, "ペコリーヌ", "このクエストを攻略すれば、\nアイテムがもらえますよ\nたくさん集めて、どんどん\n強くなっちゃいましょう！", 1, 0);
INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 14, 0, 106001, -363.0, 14004, "2015/12/17 15:00:00", 1, 0.0, 0.0, "キャル", "クエストを攻略すると、\nアイテムが手に入るんだって\nもらえるものは、\nもらっておきましょ！", 1, 0);
