INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (2, "2030/12/17 14:59:59", 16, 2104010, 106401, -363.0, 16001, "2015/12/17 15:00:00", 1, 0.0, 0.0, "シェフィ", "ねぇ…クランに加入してみない？\n一人も楽しいけど、みんなと\n一緒も賑やかでいいと思うの", 1, 0);
