INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 24, 0, 109001, -385.0, 24001, "2019/01/31 12:00:00", 2, 8.0, 4.08, "エリコ（バレンタイン）", "あなた様が手伝ってくださった\nチョコレートケーキ\n美味しくないはずがありませんわ", 1, 10011);
INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 24, 0, 109001, -385.0, 24002, "2019/01/31 12:00:00", 2, 8.0, 5.0, "エリコ（バレンタイン）", "このような失敗、あなた様が\nするはずありません\nもう一度挑戦してみましょう", 2, 10011);
INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 24, 0, 109001, -385.0, 24003, "2019/01/31 12:00:00", 2, 8.0, 10.11, "エリコ（バレンタイン）", "あなた様のために\n作っているケーキを\nあなた様に手伝っていただく……\nことになるなんて……クスクス", 3, 10011);
INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 24, 0, 109001, -385.0, 24004, "2019/01/31 12:00:00", 2, 8.0, 4.0, "エリコ（バレンタイン）", "あまり気に病む必要はありませんわ\nこれからは私がたっぷりと\n時間をかけて教えて差し上げますから", 4, 10011);
INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 24, 0, 109001, -385.0, 24005, "2019/01/31 12:00:00", 2, 8.0, 6.0, "エリコ（バレンタイン）", "今回は調子が悪かった……のですか？\nあなた様が手伝ってくれたものなら\nわたしはどんなものでも……クスクス", 5, 10011);
INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 24, 0, 109002, 0.0, 24006, "2019/01/31 12:00:00", 1, 0.0, 0.0, "エリコ（バレンタイン）", "あなた様が手がけた\nチョコレートケーキは\n私にとっての宝物……\n侮辱する者は生かしておきませんわ", 6, 10011);
INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 24, 0, 109002, 0.0, 24007, "2019/01/31 12:00:00", 1, 0.0, 0.0, "エリコ（バレンタイン）", "とても素晴らしい出来栄えですわ！\nあなた様にふさわしい\nチョコレートケーキができました", 7, 10011);
INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 24, 0, 109002, 0.0, 24008, "2019/01/31 12:00:00", 1, 0.0, 0.0, "エリコ（バレンタイン）", "私を手伝ってくれたあなた様は\nすでに私の虜……\n甘い世界に誘って差し上げますわ", 8, 10011);
INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 24, 0, 109002, 0.0, 24009, "2019/01/31 12:00:00", 1, 0.0, 0.0, "エリコ（バレンタイン）", "あなた様の愛情\nしかと受け止めました\nこれが……私たちの初めての\n共同作業ですわね……クスクス", 9, 10011);
INSERT INTO `v1_2f3942bc1d5efe31ede5fab54bef72093e728da0606f0361fae468ab3f20841f` VALUES (1, "2030/12/17 14:59:59", 24, 0, 109002, 0.0, 24010, "2019/01/31 12:00:00", 1, 0.0, 0.0, "エリコ（バレンタイン）", "これは世界一の\nチョコレートケーキですわ\nだって、あなた様が腕によりをかけて\n作ってくださったんですもの", 10, 10011);
