INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (1, 1, 1.5, 0, 101, 105, 160, 0.029, 1, 0, 110, 1, "仲間と夏を楽しむ、夏海の魔法士。自分を変えたいという固い決意で、障害物を無効化して走り続ける。", 122601);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (2, 2, 2.5, 1, 102, 111, 120, 0.014, 0, 0, 105, 3, "太陽よりも輝く、夏海の元気娘。　持ち前の身体能力で、どんなコースも柔軟に対応し、駆け抜ける。", 122401);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (3, 3, 2.5, 0, 103, 105, 350, 0.017, 0, 1, 110, 2, "波打ち際に煌めく、夏海の剣士。　日々の鍛錬で鍛えた脚で、長い距離も速度を落とさず走り切る。", 122501);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (4, 4, 1.5, 1, 104, 105, 130, 0.016, 0, 0, 110, 3, "海の幸に目がない、渚のお姫様。　『王家の装備』の力を解放し、障害物をものともせず前へと進む。", 107501);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (5, 5, 2.5, 0, 105, 105, 160, 0.014, 1, 0, 110, 1, "海でも主さまをお世話する、白浜の巫女。精霊の力で自身を加速させ、誰よりも速くゴールを目指す。", 107601);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (6, 6, 3.5, 0, 106, 103, 255, 0.029, 0, 1, 120, 2, "水着姿を恥じらう、ツンデレ猫娘。お得意の魔法で、対戦相手の走りを妨害し、レースを有利に進める。", 107801);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (7, 7, 3.5, 0, 107, 103, 160, 0.013, 1, 0, 120, 1, "優雅に浜を駆ける、真夏のマホ姫。しゃなりしゃなりと、雅な身のこなしで障害物を跳び越えていく。", 110601);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (8, 8, 2.5, 0, 108, 103, 350, 0.014, 0, 1, 110, 2, "浜の平和を守る、真夏の狼娘。　　獣人の強靭なスタミナで、レース　後半にスパートをかける。", 110401);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (9, 9, 1.5, 1, 109, 100, 130, 0.012, 0, 0, 105, 3, "浜に力強く舞う、真夏の南国娘。　レースの喧噪の中でも精神を乱さず後半に大きな捲りを見せる。", 110501);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (10, 10, 2.5, 0, 110, 103, 200, 0.014, 1, 0, 105, 1, "浜の事件を嗅ぎ付けた、真夏の　　名探偵。高い推理力と魔法で、的確に対戦相手の走りを乱す。", 115701);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (11, 11, 2.5, 0, 111, 103, 350, 0.071, 0, 1, 105, 2, "再び鎧を脱いだ、灼熱の騎士団長。実戦で培った判断力で、瞬時にバリアを展開し、障害物を無力化する。", 113601);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (12, 12, 1.5, 1, 112, 103, 130, 0.014, 0, 0, 120, 3, "水が苦手な、海辺の気まぐれ猫娘。浜に現れた美少女義賊は、対戦相手のスピードを奪いとる。", 107901);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (13, 13, 1.5, 0, 113, 105, 200, 0.022, 1, 0, 110, 2, "バカンスも効率第一な、海の傭兵。レースでも最高効率を求め、どんな状況でも隙のない走りを見せる。", 108001);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (14, 14, 2.5, 0, 114, 108, 160, 0.031, 1, 0, 110, 1, "真夏の太陽が良く似合う、海辺の　サレンママ。多才で器用、どんな　障害物もエレガントに越えていく。", 110301);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (15, 15, 0.5, 1, 115, 105, 130, 0.036, 0, 0, 110, 1, "海に嵐を呼ぶ夏のドジっ娘メイド。日頃から災難に慣れており、多少　のトラブルには動じたりしない。", 107701);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (16, 16, 3.5, 0, 116, 95, 350, 0.048, 0, 1, 110, 2, "粋な夏を愉しむ、若夏の姉御。　　流麗な身体捌きで、始・継・終と、UBを重ねるごとに、加速する。", 113101);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (17, 17, 1.5, 1, 117, 105, 130, 0.043, 0, 0, 105, 3, "夏のトキメキに触れた、オタク魔法少女。多彩な魔法を操り、少しずつ他の走者の速度を落とし続ける。", 113301);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (18, 18, 1.5, 1, 118, 95, 130, 0.018, 0, 0, 105, 2, "海獄に降臨せし、真夏の冥姫。　　レース後半に魔力を全解放し、　　蒼き疾風と成りて浜を駆け抜ける。", 113201);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (19, 19, 2.5, 0, 119, 108, 160, 0.021, 1, 0, 110, 2, "夏の夕を火花で彩る、渚の壊し屋。『壊し屋』の異名と過激な愛情は、対戦相手にプレッシャーを与える。", 117001);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (20, 20, 2.5, 0, 120, 105, 255, 0.036, 0, 1, 110, 1, "青春に生きる者を導く、夏の先生。夢へと邁進する聖母の加護が、その身をあらゆる困難から守る。", 113501);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (21, 21, 2.5, 0, 121, 105, 350, 0.02, 0, 1, 105, 3, "笑顔の応援が眩しい、夏の超能力　ガール。超能力を駆使して、どんな障害物もひとっ飛びで乗り越える。", 113401);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (22, 22, 2.5, 1, 122, 105, 130, 0.018, 0, 0, 110, 1, "海辺で大人の魅力を放つ、夏の　　セクシー先生。その魅力に囚われた対戦相手は、走りを乱してしまう。", 110101);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (23, 23, 3.5, 0, 123, 111, 160, 0.03, 1, 0, 105, 3, "水着も絶対外さない、夏のカリスマモデル。走りのバイブスは超テンアゲで、スピードはおにやば。", 110001);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (24, 24, 1.5, 1, 124, 103, 130, 0.027, 0, 0, 110, 1, "南の島も盛り上げる、真夏のアイドル。悪天候でも笑顔を絶やさず後半もリズムを上げて走り切る。", 117201);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (25, 25, 3.5, 0, 125, 108, 160, 0.02, 1, 0, 105, 2, "海原に美声を響かす、渚の歌姫。　守護の賛歌と精霊の加護により、　障害物から身を守る。", 117301);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (26, 26, 2.5, 0, 126, 105, 350, 0.04, 0, 1, 110, 3, "夏でも天邪鬼な、夕凪のテーラー。張り巡らされた魔法の糸で、他の走者の自由を奪う。", 117401);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (27, 27, 3.5, 0, 127, 105, 350, 0.009, 0, 1, 110, 1, "向日葵が良く似合う、海辺の　　　お姉ちゃん。秘密結社の一員で、　抜群の身体能力が一番の武器。", 117101);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (28, 28, 3.5, 0, 128, 105, 255, 0.022, 0, 1, 120, 1, "渚でおっとり、夏色うさぎっこ。　うさぎさんパワー全開で、ぴょん　ぴょんとコースを跳ね回る。", 122901);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (29, 29, 2.5, 1, 129, 105, 130, 0.028, 0, 0, 110, 3, "無邪気さ弾ける、夏色いたずら娘。砂浜でも、いたずらの準備は万端！レースに大波乱が巻き起こる！？", 122801);
INSERT INTO `v1_3088d8106bd827cd44964b27e275be5704751fbc28df12a83cc4c53510316f68` VALUES (30, 30, 3.5, 0, 130, 108, 130, 0.023, 1, 0, 105, 2, "海でも真面目な、夏色優等生。　　高めた魔力を解き放ち、一瞬の内に他の走者を置き去りにする。", 122701);
