INSERT INTO `v1_310570ed9a99a3766ed1a58ab4109ffd0f330e33943227f64fd892a93e8c2f18` VALUES (1003, 0, "2020-02-15 15:00:00", 190801, 1003001, "2030-04-01 14:00:00", 11, "クウカさんを引っ張ってより遠く\nへ飛ばしてあげましょう！\n色々なハプニングでクウカさんに\nエクスタシーを届けましょう！");
