INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100101, "ここに困ってる人は\nいないよね…？\nよかった♪", 1, 1, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100101, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！", 1, 2, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100101, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ", 1, 3, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100101, "ここで特訓していい？\n物は壊さないように\n気をつけるね！", 1, 4, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100101, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？", 1, 5, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100101, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！", 1, 6, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100101, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 1, 7, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100101, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…", 1, 8, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100101, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！", 1, 9, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100101, "えっとね…しっぽは\n触っちゃダメだよ？", 1, 10, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100101, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！", 1, 11, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100101, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～", 1, 12, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100101, "えへへ…\n騎士くんの手だ…\nあったかいな…♪", 1, 13, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100101, "おはよー！\n今日も元気に\nいっちゃうよー！", 1, 14, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100101, "ユイちゃんに\nおやつ作って\nもらおうかな～♪", 1, 15, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100101, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…", 1, 16, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100101, "ちゃんとケアして\n傷が残らないように\nしないと…", 1, 17, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100101, "一回休むと\nこのままずるずる\nいきそう…", 1, 18, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100101, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！", 1, 19, 1, 3);
