INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100102, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！", 1, 31, 8, 1);
