INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100102, "ひゃうっ！　もう…\nしっぽはだめだってば\n次やったらデコピン！", 1, 37, 8, 2);
