INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100102, "色んなことを\n知っておくのは\n大事だよね！", 1, 49, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100201, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな", 1, 51, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100201, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…", 1, 52, 1, 1);
