INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100201, "え！　む、虫っ！？\n取って取って！\nはやく～！", 1, 61, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100201, "あのね…\n一緒におでかけ\nしたいんだけど…\nダメかな？", 1, 62, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100201, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 1, 63, 8, 2);
