INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100201, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…", 1, 66, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100201, "よし…\nこれなら\n褒めてもらえるかな？", 1, 67, 1, 3);
