INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100201, "…はへっ？\nも、もしかして今\n寝ちゃってた…？", 1, 71, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 100201, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 1, 72, 1, 3);
