INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100202, "え！　む、虫っ！？\n取って取って！\nはやく～！", 1, 84, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100202, "あ、あの…実は\n騎士クンのこと…\nううん、やっぱり\nなんでもない", 1, 85, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100202, "男の子って\nやっぱりおっきいね\nその…手とか\n他にもいろいろ…", 1, 86, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100202, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！", 1, 87, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100202, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～", 1, 88, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100202, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪", 1, 89, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100202, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？", 1, 90, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100202, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい", 1, 91, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100202, "よし…\nこれなら\n褒めてもらえるかな？", 1, 92, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100202, "がんばることも\n必要だけど\nお休みも大切だよね", 1, 93, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100202, "かわいい～♪\n騎士クンは\nこういうのって好き？", 1, 94, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100202, "戦う前にもこうやって\n落ち着けたら\nいいのに", 1, 95, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100202, "あの…！\nよ、良かったら\n一緒に…！", 1, 96, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 100202, "騎士クンと一緒に\n冒険するなら\n体力がないと…！", 1, 97, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 100202, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？", 1, 98, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100202, "…やっぱり\n月刊恋愛指南は\nためになる…", 1, 99, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100301, "装備の手入れでも\nしようかな…", 1, 101, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100301, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？", 1, 102, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100301, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 103, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100301, "仲間なんていらない\nそう思ってた頃も\nあったな…", 1, 104, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100301, "考えごとをしていると\nつい部屋中を\n歩いてしまうな", 1, 105, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100301, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 106, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100301, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1, 107, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100301, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ", 1, 108, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100301, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？", 1, 109, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100301, "なに？　私に相談？\n戦いについてなら\n喜んで", 1, 110, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100301, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！", 1, 111, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100301, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した", 1, 112, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100301, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 1, 113, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100301, "おはよう\n今日も充実した\n一日にしていこう", 1, 114, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100301, "そろそろ\nお茶の時間か\nキミも一緒にどう？", 1, 115, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100301, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 1, 116, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100301, "戦いを経てもなお\n美しくあるように…\nこれでよし♪", 1, 117, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100301, "戦ってばかりで\n休まないと、また\nユイに怒られるな", 1, 118, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100301, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 1, 119, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100301, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか", 1, 120, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100301, "時々は落ち着いた\n時間を過ごすのも\n悪くない…", 1, 121, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 100301, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 1, 122, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 100301, "食事は大切だけど\n食べ過ぎには\n注意しておこう…", 1, 123, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100301, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 1, 124, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100302, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう", 1, 126, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100302, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？", 1, 127, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100302, "彼は\nどこまで強くなるのか\n私も負けられないな", 1, 128, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100302, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね", 1, 129, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100302, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい", 1, 130, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100302, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど", 1, 131, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100302, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？", 1, 132, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100302, "どうしたの？\nなんでもない？\nそれなら良かった", 1, 133, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100302, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？", 1, 134, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100302, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう", 1, 135, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100302, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！", 1, 136, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100302, "ひょっとして\n今のも一本だなんて\n言わないよね？", 1, 137, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100302, "キミにはこれからも\n私のそばに…\nいや、なんでもない", 1, 138, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100302, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ", 1, 139, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100302, "休息も時には\n必要だと、仲間から\n教わったよ", 1, 140, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100302, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？", 1, 141, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100302, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！", 1, 142, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100302, "休息があってこそ\n万全な状態で\n戦えるんだ", 1, 143, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100302, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう", 1, 144, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100302, "体と同様に\n心もほぐれるね…\nはぁ…", 1, 145, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100302, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…", 1, 146, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 100302, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！", 1, 147, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 100302, "修行のせいか\n最近食べる量が\n増えてきたような…", 1, 148, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100302, "ユイ…！\nこ、こんな本を\n読んでいたのか…！", 1, 149, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100401, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 151, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100401, "落とし穴…どこに\n作ったんだっけ？", 1, 152, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100401, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 153, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100401, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 154, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100401, "かくれんぼする人\nこの指、と～まれ！", 1, 155, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100401, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！", 1, 156, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100401, "ミソギと遊ぶ？\nミソギに遊ばれる？", 1, 157, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100401, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～", 1, 158, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100401, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？", 1, 159, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100401, "にいちゃんの\n通りそうなところに\nバナナの皮を…", 1, 160, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100401, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1, 161, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100401, "ここ、ミソギ探検隊の\n基地にしようよ！", 1, 162, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100401, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 1, 163, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100401, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪", 1, 164, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100401, "にいちゃんが\n昼寝したら、\nイタズラし放題♪", 1, 165, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100401, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 1, 166, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100401, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 167, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100401, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪", 1, 168, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100401, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 1, 169, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100401, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 1, 170, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100401, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 1, 171, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 100401, "探検家は\n体力勝負だよー！", 1, 172, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 100401, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！", 1, 173, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100401, "こういう所に\n宝の地図が\n隠れてるんだよね～", 1, 174, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100402, "お宝の予感！\n部屋中を\n探検しちゃうぞ～", 1, 176, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100402, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！", 1, 177, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100402, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな", 1, 178, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100402, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？", 1, 179, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100402, "かくれんぼする人\nこの指、と～まれ！", 1, 180, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100402, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…", 1, 181, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100402, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？", 1, 182, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100402, "ミソギがしかけた\nイタズラ\n見つけられるかな？", 1, 183, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100402, "探検する？\n冒険する？\nそれとも…\nイタズラされる？", 1, 184, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100402, "将来の夢は探検家！\n探したい物はないけど", 1, 185, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100402, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！", 1, 186, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100402, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪", 1, 187, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100402, "ここ、ミソギ探検隊の\n基地にしようよ！", 1, 188, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100402, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！", 1, 189, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100402, "にいちゃんが昼寝したら\nイタズラし放題♪", 1, 190, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100402, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？", 1, 191, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100402, "イタズラには\nお色気も必要…\nうん、完璧！", 1, 192, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100402, "ゆっくりまったりして\n次のイタズラを\n考えよ～！", 1, 193, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100402, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪", 1, 194, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100402, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！", 1, 195, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100402, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！", 1, 196, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 100402, "探検家は\n体力勝負だよー！", 1, 197, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 100402, "探検のおやつに\nぴったりかも～！", 1, 198, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100402, "こういう所に\n宝の地図が\n隠れてるんだよね～", 1, 199, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100601, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな", 1, 201, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100601, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…", 1, 202, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100601, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ", 1, 203, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100601, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 204, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100601, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 205, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100601, "お兄ちゃんと私は\n相性抜群です♪", 1, 206, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100601, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 1, 207, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100601, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 1, 208, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100601, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？", 1, 209, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100601, "そんなところ\n触っちゃダメ♪", 1, 210, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100601, "お兄ちゃん、イジワル\nしないでください…", 1, 211, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100601, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！", 1, 212, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100601, "コーチ、\n今日も特訓\nお願いします！", 1, 213, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100601, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 1, 214, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100601, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…", 1, 215, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100601, "夜はこれからですよ\nお兄ちゃん♪", 1, 216, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100601, "世界一、かわいく\nなるんだから♪", 1, 217, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100601, "気持ちいい♪\nもう立てないよ～", 1, 218, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100601, "かわいい♪\nアカリのライバル\nだね", 1, 219, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100601, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 1, 220, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100601, "はぁ…アカリ、\nとろけちゃう…", 1, 221, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 100601, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！", 1, 222, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 100601, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね", 1, 223, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100601, "この楽譜、\nサックスで吹いて\nみたいなぁ♪", 1, 224, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100602, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…", 1, 226, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100602, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習", 1, 227, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100602, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？", 1, 228, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100602, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…", 1, 229, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100602, "双子って、\n好みのタイプも\n似ちゃうのかな？", 1, 230, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100602, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…", 1, 231, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100602, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！", 1, 232, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100602, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！", 1, 233, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100602, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…", 1, 234, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100602, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ", 1, 235, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100602, "お兄ちゃん、イジワル\nしないでください…", 1, 236, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100602, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…", 1, 237, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100602, "コーチ、\n今日も特訓\nお願いします！", 1, 238, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100602, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪", 1, 239, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100602, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん", 1, 240, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100602, "お風呂入ってきますね\nそのあとは…\n二人でい～っぱい\n遊びましょうね", 1, 241, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100602, "お姉ちゃんのマネ\nしちゃおうかな？", 1, 242, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100602, "気持ちいい♪\nもう立てないよ～", 1, 243, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100602, "かわいい♪\nアカリのライバル\nだね", 1, 244, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100602, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…", 1, 245, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100602, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい", 1, 246, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 100602, "汗をかくと\n気持ちいいですよね", 1, 247, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 100602, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪", 1, 248, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100602, "この楽譜\nサックスで吹いて\nみたいなぁ♪", 1, 249, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100701, "プリン、プリン、\nプリン、プリン、\nプリン、プリン～♪", 1, 251, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100701, "う～ら～め～し～や～\nなの～♪", 1, 252, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100701, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～", 1, 253, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100701, "幽霊の正体見たり、\nミヤコなの～", 1, 254, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100701, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪", 1, 255, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100701, "アイツがいて、\nプリンもある。\nきっとここが天国なの", 1, 256, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100701, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～", 1, 257, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100701, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪", 1, 258, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100701, "もし成仏した先に\nプリンがなかったら\n地獄なの～…", 1, 259, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100701, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！", 1, 260, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100701, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～", 1, 261, 1, 2);
