INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100702, "プリンの写真集が\nあればいいのに\nなの～ ", 1, 299, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100801, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！", 1, 301, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100801, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね", 1, 302, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100801, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 1, 303, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100801, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！", 1, 304, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100801, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？", 1, 305, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100801, "このボクがいるだけで\nあの人への\nおもてなしだよね", 1, 306, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100801, "ボクの可愛さは、\nふふ…あの人の\nためのものさ", 1, 307, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100801, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆", 1, 308, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100801, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 1, 309, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100801, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？", 1, 310, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100801, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？", 1, 311, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100801, "あの人も今、\n可愛いボクのこと\n考えてるかな…", 1, 312, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100801, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ", 1, 313, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100801, "美しいボクの\n美しい一日が\n始まるよ！", 1, 314, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100801, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪", 1, 315, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100801, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪", 1, 316, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100801, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 1, 317, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100801, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 1, 318, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100801, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！", 1, 319, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100801, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 1, 320, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100801, "気安く触ったら\n火傷する…\n炎はボクに似ているね", 1, 321, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 100801, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 1, 322, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 100801, "ボクが食べると、\n食べ物まで\n可愛いなあ…", 1, 323, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100801, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ", 1, 324, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100802, "ボクの美しさは\nみんなのために\nあるんだよ！", 1, 326, 1, 1);
