INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100802, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪", 1, 333, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100802, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから", 1, 334, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100802, "ボクの化粧品使う？\n一緒に可愛くなろうよ", 1, 335, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100802, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！", 1, 336, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100802, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ", 1, 337, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100802, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ", 1, 338, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100802, "朝日と共に\nボクの美しさも\n周りを照らすよ", 1, 339, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100802, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ", 1, 340, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100802, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪", 1, 341, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100802, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？", 1, 342, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100802, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！", 1, 343, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100802, "可愛いものを\n可愛いって言える\nボクって可愛いよね！", 1, 344, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100802, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！", 1, 345, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100802, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！", 1, 346, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 100802, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪", 1, 347, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 100802, "ボクが食べると\n食べ物まで\n可愛いなあ…", 1, 348, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100802, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ", 1, 349, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100901, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！", 1, 351, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100901, "今度シグルドに\n私の魔法を一つ\n伝授してやるか", 1, 352, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100901, "ここでは風が\n吹いてこないな…\n換気したい！", 1, 353, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100901, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 354, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100901, "まずは包帯をとって\n手から龍の形の\n炎を出して…", 1, 355, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100901, "シグルド…\n今頃何処で何を\nしているのだろう…", 1, 356, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100901, "最近、彼の姿が\nまぶしく見える…\nどうして…？", 1, 357, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100901, "辞書を知らないか？\n…探している\n理由は聞くな", 1, 358, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100901, "そうだ！\n貴様の技にも\n名前をつけてやろう！", 1, 359, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100901, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？", 1, 360, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100901, "何事だ？\nまさか我が瞳に\n魅入られたのか？", 1, 361, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100901, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 1, 362, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100901, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 1, 363, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100901, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！", 1, 364, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100901, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ", 1, 365, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100901, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！", 1, 366, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100901, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 367, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100901, "くっ…！\n私を眠りの国へ\n誘うつもりか！", 1, 368, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100901, "このようなものに\n懐柔されるものか！\n…されるものか！", 1, 369, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100901, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 1, 370, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100901, "これが我らの求めた\n安寧か…\n悪くない…", 1, 371, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 100901, "この程度\n前世での激闘に\n比べれば…！", 1, 372, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 100901, "最近金欠でな…\nいやぁありがたい…", 1, 373, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100901, "ここにも\n前世の記憶が\n眠っていたとは…", 1, 374, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100902, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな", 1, 376, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100902, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？", 1, 377, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100902, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！", 1, 378, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100902, "そうだ、ナナカに\nこの間借りた本を\n返さないと…", 1, 379, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100902, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…", 1, 380, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100902, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？", 1, 381, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100902, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…", 1, 382, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100902, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？", 1, 383, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100902, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ", 1, 384, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100902, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？", 1, 385, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100902, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！", 1, 386, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100902, "貴様がそばにいると\nとても安心できる…\n…不思議だ", 1, 387, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100902, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！", 1, 388, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100902, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！", 1, 389, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100902, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…", 1, 390, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100902, "夜になると、力が\n増した気分になるな\n…なりませんか？", 1, 391, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100902, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな", 1, 392, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100902, "お、おのれ\nヒュプノス…\n私は負けないぞ…", 1, 393, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100902, "くっくっくっ…\n貴様は特別団員に\nしてやろう", 1, 394, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100902, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ", 1, 395, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100902, "これが我らの求めた\n安寧か…\n悪くない…", 1, 396, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 100902, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！", 1, 397, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 100902, "食事は魔力に\n変換する体力の\n元になるのだ！", 1, 398, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100902, "フフフ…\nこれを覚えれば\nもっと強く…！", 1, 399, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101001, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ", 1, 401, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101001, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪", 1, 402, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101001, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 403, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101001, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 404, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101001, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか", 1, 405, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101001, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや", 1, 406, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101001, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1, 407, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101001, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 1, 408, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101001, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 1, 409, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101001, "王子はんは、\n絵本から出てきた人\nみたいやね～", 1, 410, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101001, "うちの話し方\nのんびりしてます\nやろか？", 1, 411, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101001, "マホ姫の手は\nつべたいから、\n王子はんが温めて？", 1, 412, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101001, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 1, 413, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 101001, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 414, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 101001, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ", 1, 415, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 101001, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ", 1, 416, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101001, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ", 1, 417, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101001, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ", 1, 418, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101001, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ", 1, 419, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101001, "心地ええどす。まるで\nお花畑にいるようやね", 1, 420, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101001, "ぽかぽかどす。\n心まであったかく\nなりますわぁ", 1, 421, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 101001, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～", 1, 422, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 101001, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ", 1, 423, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 101001, "絵本、絵本～\n幸せやわぁ♪", 1, 424, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101002, "王子はんにとって\n自慢の姫になるえ～", 1, 426, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101002, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ", 1, 427, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101002, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか", 1, 428, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101002, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～", 1, 429, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101002, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ", 1, 430, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101002, "マホ姫と王子はん\n絵本の世界より\nベストカップルや", 1, 431, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101002, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん", 1, 432, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101002, "たまには、王子はんの\n好きな絵本も\n知りたいな～", 1, 433, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101002, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ", 1, 434, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101002, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？", 1, 435, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101002, "絵本読むんも\nええなぁ…", 1, 436, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101002, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？", 1, 437, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101002, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？", 1, 438, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 101002, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ", 1, 439, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 101002, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ", 1, 440, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 101002, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ", 1, 441, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101002, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ", 1, 442, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101002, "王子はんも一緒に\nゆっくりしていっても\nええんよ？", 1, 443, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101002, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？", 1, 444, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101002, "癒しアイテムって\nほんま、好っきゃわぁ", 1, 445, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101002, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ", 1, 446, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 101002, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ", 1, 447, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 101002, "おいひいわ～\nマホマホ王国には\nない味やわぁ", 1, 448, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 101002, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ", 1, 449, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101101, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 451, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101101, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！", 1, 452, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101101, "い、いけない！\n縫い針落としちゃった", 1, 453, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101101, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！", 1, 454, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101101, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 455, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101101, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 1, 456, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101101, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～", 1, 457, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101101, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？", 1, 458, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101101, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 1, 459, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101101, "勉強、見てくれるん\nですか？　やった～♪", 1, 460, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101101, "服がほつれてますよ？\n直してあげます！", 1, 461, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101101, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪", 1, 462, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101101, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪", 1, 463, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 101101, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 464, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 101101, "お昼休憩に\n甘いものが\n食べたいです～", 1, 465, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 101101, "ねむいです～\nでももうちょっと\n勉強しないと～…", 1, 466, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101101, "この服、かわいい♪\n今度作って\nみよっと～！", 1, 467, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101101, "勉強で疲れたから\n休憩しよ～っと", 1, 468, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101101, "かわいい♪\n私が服を作って\nあげよっか？", 1, 469, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101101, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！", 1, 470, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101101, "のんびりしたら、\nまた勉強しよっと！", 1, 471, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 101101, "運動すると勉強も\nはかどるよね！", 1, 472, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 101101, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 1, 473, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 101101, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？", 1, 474, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101102, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…", 1, 476, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101102, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？", 1, 477, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101102, "い、いけない！\n縫い針落としちゃった", 1, 478, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101102, "笑う時には服着ます！", 1, 479, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101102, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？", 1, 480, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101102, "お兄ちゃんに、\nお洋服でも\n作ろうかな？", 1, 481, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101102, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？", 1, 482, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101102, "勉強、見てくれるん\nですか？　やった～♪", 1, 483, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101102, "クリームついてた！？\nさっき食べた、\nケーキかも…！", 1, 484, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101102, "おにいちゃん！\nそろそろ、おやつに\nしませんか？", 1, 485, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101102, "勉強は…\n明日にしようっと", 1, 486, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101102, "妹だけど、大人な\nレディですからね！", 1, 487, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101102, "な、ナデナデとか\n恥ずかしいな～", 1, 488, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 101102, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな", 1, 489, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 101102, "お昼休憩に\n甘いものが\n食べたいです～", 1, 490, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 101102, "お兄ちゃんは\n夜強いんですね\n私は眠くて…", 1, 491, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101102, "服装や髪形が変わると\n気分も変わるよね", 1, 492, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101102, "ここで寝ちゃったら\n受験勉強が～…\nぐーー", 1, 493, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101102, "かわいい♪\n私が服を作って\nあげよっか？", 1, 494, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101102, "いまならどんな試験も\n合格できそうです！", 1, 495, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101102, "のんびりしたら\nまた勉強しよっと！", 1, 496, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 101102, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！", 1, 497, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 101102, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！", 1, 498, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 101102, "この本の服\nかわいいなぁ♪\n私に作れるかな？", 1, 499, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101201, "う～ん、今なら\nどこでだって\n眠れちゃいそう…", 1, 501, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101201, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～", 1, 502, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101201, "…はっ！？\nね、寝てないよ？\nホントだよっ", 1, 503, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101201, "ぐっすり眠って\nパワーを\nフルチャージっ☆", 1, 504, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101201, "もし、超能力のことが\nバレたら…ブルブル", 1, 505, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101201, "テレパシーで\nキミの心を覗けたら\n…なんてね！", 1, 506, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101201, "キミを抱き枕に…\nじょ、冗談冗談！", 1, 507, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101201, "ん～、なになに？\n一緒にお昼寝する？", 1, 508, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101201, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？", 1, 509, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101201, "おおっ、相談ごと？\nなにかな\nなにかな～？", 1, 510, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101201, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！", 1, 511, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101201, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！", 1, 512, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101201, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆", 1, 513, 8, 2);
