INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 101201, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？", 1, 515, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 101201, "どんなにお昼寝しても\n夜は眠いんだ～", 1, 516, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101201, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？", 1, 517, 1, 3);
