INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101602, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイに\nしてあげる♪", 1, 584, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101602, "この雑誌の服\nガチめにほしぃ～", 1, 585, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101602, "うちのコーデで\nヒデサイをオシャンに\nしてあげるね♪", 1, 586, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101602, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？", 1, 587, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101602, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪", 1, 588, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 101602, "今日は早起き\nできたんだー\nメイクもばっち\nきまってるっしょ？", 1, 589, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 101602, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー", 1, 590, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 101602, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー", 1, 591, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101602, "この服ね～\n今年のトレンドなんだ♪\nかわたんでしょ！", 1, 592, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101602, "ちょっち疲れたかも？\nガン寝しちゃおっかな", 1, 593, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101602, "うわー\nこのぬいぐるみ\nもきゅすぎでしょ～！", 1, 594, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101602, "いいにおーい♪\nファンのみんなにも\n教えてあげよー♪", 1, 595, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101602, "あったまるー\nこれ、サイツヨに\n癒しじゃない？", 1, 596, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 101602, "食べた分は\n消費しないとね～", 1, 597, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 101602, "体形維持しないと\nいけないけど\nついつい食べちゃう！", 1, 598, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 101602, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！", 1, 599, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101701, "やっさー\n踊ってると、どんどん\n楽しくなるさー！", 1, 601, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101701, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？", 1, 602, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101701, "ハイターイ！　元気な\nあいさつが一番さ～！", 1, 603, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101701, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1, 604, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101701, "さぁ、みんなで歌って\n踊るさ～♪", 1, 605, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101701, "毎日、最高に幸せさ～\nキミのおかげだねー", 1, 606, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101701, "キミへの気持ちを\n踊りで表現\nしてみるさ～", 1, 607, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101701, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 1, 608, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101701, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～", 1, 609, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101701, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！", 1, 610, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101701, "空手の型の\n練習するさー\nキミもどう？", 1, 611, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101701, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～", 1, 612, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101701, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー", 1, 613, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 101701, "ハイターイ！\n今日も一日、\nがんばるさー！", 1, 614, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 101701, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！", 1, 615, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 101701, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ", 1, 616, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101701, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？", 1, 617, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101701, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～", 1, 618, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101701, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？", 1, 619, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101701, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆", 1, 620, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101701, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！", 1, 621, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 101701, "汗をかくのは\n気持ちいいさー！\nどんどん行くよー！", 1, 622, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 101701, "うーん、おいしいさ～\nゴーヤっぽいものを\n混ぜたいさ～", 1, 623, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 101701, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！", 1, 624, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101702, "焦らなくても\nなんくるないさ～\nなるようになるさ～", 1, 626, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101702, "さぁ、みんなで歌って\n踊るさ～♪", 1, 627, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101702, "元気がないときは\n私のところに来るさー", 1, 628, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101702, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ", 1, 629, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101702, "ノリノリな音楽と\n踊りで、疲れを\n吹き飛ばすさ～！", 1, 630, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101702, "毎日、最高に幸せさ～\nキミのおかげだねー", 1, 631, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101702, "幸せすぎて踊らずには\nいられないさ～", 1, 632, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101702, "海でひと泳ぎ\nしたいさ～\nキミもどう？", 1, 633, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101702, "ん？　どうしたの～？\nヒマなら一緒に\n踊ろうさ～", 1, 634, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101702, "キミと一緒だから\n毎日楽しいこと\nばっかりさー", 1, 635, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101702, "ハイターイ！　元気な\nあいさつが一番さ～！", 1, 636, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101702, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～", 1, 637, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101702, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ", 1, 638, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 101702, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！", 1, 639, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 101702, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ", 1, 640, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 101702, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ", 1, 641, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101702, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？", 1, 642, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101702, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～", 1, 643, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101702, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ", 1, 644, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101702, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ", 1, 645, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101702, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！", 1, 646, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 101702, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～", 1, 647, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 101702, "もぐもぐ…もっと\nおかわりがほしいさー", 1, 648, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 101702, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ", 1, 649, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101801, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう", 1, 651, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101801, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪", 1, 652, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101801, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？", 1, 653, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101801, "キンコンカンコ～ン♪\n授業をはじめま～す", 1, 654, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101801, "はー…\nちょっと肩が\nこっちゃった…", 1, 655, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101801, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…", 1, 656, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101801, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…", 1, 657, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101801, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！", 1, 658, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101801, "勉強勉強で\n疲れたよね？\n膝枕してあげる！", 1, 659, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101801, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？", 1, 660, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101801, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪", 1, 661, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101801, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～", 1, 662, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101801, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？", 1, 663, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 101801, "学校に\n遅刻しないように\n早く準備しないとね", 1, 664, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 101801, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～", 1, 665, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 101801, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪", 1, 666, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101801, "もしかして…\n少し露出多いかしら？", 1, 667, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101801, "リラックスすると\nα波が出て、勉強が\nはかどるんですって", 1, 668, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101801, "かわいい♪　あなたも\n私の生徒になる？", 1, 669, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101801, "学院にもこういう\n安らぐ設備が\n必要かしらね？", 1, 670, 1, 3);
