INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 101801, "運動部の顧問も\nできるかしら？", 1, 672, 1, 3);
