INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101802, "私の生徒に\n不良なんていないわ\nみーんないい子♪", 1, 676, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101802, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪", 1, 677, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101802, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？", 1, 678, 1, 1);
