INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102002, "はむ♪　いっぱい\n食べて、はやく\nおっきくなりたいな～", 1, 748, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102002, "ミミね！\nこのお話だーい好き！", 1, 749, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102101, "はぅぅ…\n部屋のどこにいたら\nいいんだろう…", 1, 751, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102101, "お外より\nお部屋の中の方が\n落ち着くな…", 1, 752, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102101, "クッキー焼いたら\nみんな食べてくれる\nかなぁ…", 1, 753, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102101, "あ、あめんぼあかいな\nあいうえお！", 1, 754, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102101, "お芝居には\n人間観察が大事…\nじーーーーーー…", 1, 755, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102101, "お兄ちゃんを見ると、\n顔が赤くなっちゃうの\n…な、なんでかな？", 1, 756, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102101, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 1, 757, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102101, "こ、この本、とっても\nおもしろいんですよ♪", 1, 758, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102101, "い、一緒に遊んで\nくれるんですか？\nや、やったぁ～♪", 1, 759, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102101, "ランドソルには\n慣れましたか？\nわ、私はまだ…", 1, 760, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102101, "お、お兄ちゃん、\nお、お話の練習に\n付き合って欲しいです", 1, 761, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102101, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 1, 762, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102101, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 1, 763, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102101, "今日は一日、\n恥ずかしがらずに\n過ごせるかなあ…？", 1, 764, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102101, "お昼ご飯、どうぞ♪\nわ、私、お料理、\nうまくなったかな？", 1, 765, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102101, "おにいちゃんが\n帰ってくるまで、\nがんばって\n起きてました", 1, 766, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102101, "可愛い女の子に変身！\n出来ますように…♪", 1, 767, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102101, "ふかふかで\nあったかくて…\nお兄ちゃんみたい♪", 1, 768, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102101, "この子を相手に、\n演技の練習～", 1, 769, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102101, "リボンや服に\n染み込ませておこう。\nいつでもいい匂い♪", 1, 770, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102101, "えへへ。\nぽっかぽかです…", 1, 771, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 102101, "はぁはぁ…\nふひぃ…\nはうぅぅぅ～～", 1, 772, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102101, "もぐもぐ…えへへ、\n美味しいものを\n食べてると幸せです", 1, 773, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102101, "戯曲を読んで\n自分が演じるところを\n想像するの…", 1, 774, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102102, "かわいいものを\nこうして並べて…\nはぁ…落ちつくぅ♪", 1, 776, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102102, "ふぁ～、この部屋は\n時間がゆったりと\n流れてるみたい", 1, 777, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102102, "この台本、感動…\n涙が止まらないよぉ\nいつか演じてみたいな", 1, 778, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102102, "お芝居には\n人間観察が大事…\nじーーーーーー…", 1, 779, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102102, "あえいうえおあお\nかけきくけ、かっ…\nむ、難しいですぅぅ", 1, 780, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102102, "お、お腹が空いた時は\n言ってくださいね\nが、頑張って\n作ります！", 1, 781, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102102, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…", 1, 782, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102102, "こ、この本、とっても\nおもしろいんですよ♪", 1, 783, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102102, "お兄ちゃんと\nいっぱい話せた日は、\n嬉しくなっちゃいます", 1, 784, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102102, "何か私にできる事\n…そうだ！\nお掃除しますね！", 1, 785, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102102, "私、ちょっとずつ、\n自分に自信が\nついてきたかもです…", 1, 786, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102102, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ", 1, 787, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102102, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪", 1, 788, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102102, "おはようございます♪\n声出しして、お芝居の\n練習しなくちゃ", 1, 789, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102102, "お茶にしますか？\nクッキー焼いたので、\nよかったら…", 1, 790, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102102, "寝る前に\n台本を読むと、\n覚えやすいんです", 1, 791, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102102, "可愛い女の子に変身！\n出来ますように…♪", 1, 792, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102102, "はぅ…\nフッカフカァ…\n癒されちゃいます…", 1, 793, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102102, "この子を相手に\n演技の練習～", 1, 794, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102102, "リボンや服に\n染み込ませておこう\nいつでもいい匂い♪", 1, 795, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102102, "えへへ\nぽっかぽかです…", 1, 796, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 102102, "え、演劇には\nた、体力も重要\nだから～", 1, 797, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102102, "残さず\n食べますね…！", 1, 798, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102102, "戯曲を読んで\n自分が演じるところを\n想像するの…", 1, 799, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102201, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1, 801, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102201, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな", 1, 802, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102201, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？", 1, 803, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102201, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？", 1, 804, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102201, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 805, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102201, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 1, 806, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102201, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね", 1, 807, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102201, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！", 1, 808, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102201, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな", 1, 809, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102201, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…", 1, 810, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102201, "アンタは人見知り\nしないタチよね？\nいいなあ～…", 1, 811, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102201, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 1, 812, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102201, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！", 1, 813, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102201, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 1, 814, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102201, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 1, 815, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102201, "夜はこれからよ！\n朝まで\nゲームするからね！", 1, 816, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102201, "アカリみたいに\n可愛くなるのは\n難しいかな", 1, 817, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102201, "寝っ転がりながら\nゲームができれば\n気分が良さそう", 1, 818, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102201, "可愛い…けど、\n私には似合わないかな", 1, 819, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102201, "はあ、リラックスして\nゲームができるわ", 1, 820, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102201, "ん…暖かくて、\n寝落ちしちゃいそう", 1, 821, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 102201, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね", 1, 822, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102201, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 1, 823, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102201, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 1, 824, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102202, "さあ、今日は\n何のゲームで\n遊ぼうかな？", 1, 826, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102202, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…", 1, 827, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102202, "レアカードをゲット！\nすごいでしょ！？", 1, 828, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102202, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…", 1, 829, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102202, "楽しいゲームって\nやめどきが\n見つからないわ", 1, 830, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102202, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね", 1, 831, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102202, "ね、ねえ\nもう少しその…\n一緒にゲームしない？", 1, 832, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102202, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？", 1, 833, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102202, "欲しいゲームが\n見つからなくて…\n探すの手伝って！", 1, 834, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102202, "ねえ、見て！\n新しいゲームを\n買って来たの！", 1, 835, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102202, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！", 1, 836, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102202, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！", 1, 837, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102202, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ", 1, 838, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102202, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！", 1, 839, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102202, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！", 1, 840, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102202, "夜はこれからよ！\n朝まで\nゲームするからね！", 1, 841, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102202, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？", 1, 842, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102202, "ふああ…\n徹夜明けには\n辛い誘惑だな…", 1, 843, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102202, "可愛い…けど、\n私には似合わないかな", 1, 844, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102202, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな", 1, 845, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102202, "寒い日は\nここでゲームするのが\n最高のぜいたくよね", 1, 846, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 102202, "最近ゲームばかりだし\nたまにはいいかな？", 1, 847, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102202, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ", 1, 848, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102202, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…", 1, 849, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102301, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』", 1, 851, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102301, "ぷうきちがいれば、\nなんでも出来ちゃうよ", 1, 852, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102301, "発声練習しよっと\nぷうきち\nフルスイ～ング！", 1, 853, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102301, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？", 1, 854, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102301, "この部屋、ぷうきちを\n置く場所はないのかな", 1, 855, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102301, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪", 1, 856, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102301, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～", 1, 857, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102301, "おやつは、\n私とぷうきちの\n二人分用意してね♪", 1, 858, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102301, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！", 1, 859, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102301, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』", 1, 860, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102301, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！", 1, 861, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102301, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと", 1, 862, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102301, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？", 1, 863, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102301, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～", 1, 864, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102301, "ふぁ～…ぷうきちと\nお昼寝してくるね…", 1, 865, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102301, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…", 1, 866, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102301, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？", 1, 868, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102301, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？", 1, 869, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102301, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！", 1, 870, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102301, "ぽわぽわする～\nぷうきちも\n気持ちいい？", 1, 871, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102301, "おいしい～\nぷうきちも\n食べてみる？", 1, 873, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102301, "ぷうきち！\nこの本、読んで～！", 1, 874, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102501, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1, 901, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102501, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 902, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102501, "どうしたら転ばなく\nなるんでしょうか？", 1, 903, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102501, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！", 1, 904, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102501, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～", 1, 905, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102501, "おいしい料理で\nお出迎えしなくちゃ！", 1, 906, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102501, "一生懸命\nご奉仕しますよ～♪", 1, 907, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102501, "耳かきしましょうか？\nえ？　こわい？", 1, 908, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102501, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？", 1, 909, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102501, "おかえりなさいませ\nご主人さま♪", 1, 910, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102501, "すみません\nすみません\nすみません～～", 1, 911, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102501, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…", 1, 912, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102501, "私のこと、\n専属メイドと思って\n頼ってくださいね", 1, 913, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102501, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！", 1, 914, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102501, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 1, 915, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102501, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 1, 916, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102501, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 1, 917, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102501, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！", 1, 918, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102501, "かわいい♪\nあとでお洗濯\nしてあげますね", 1, 919, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102501, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 1, 920, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102501, "ここでお洗濯ものを\n乾かしましょうか", 1, 921, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 102501, "あわわわっ…\nこ、転びそうです～！", 1, 922, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102501, "これくらい\nおいしいものを\n作ってみたいです～", 1, 923, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102501, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 1, 924, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102502, "失敗したら、この\nお部屋追い出され\nちゃうかも～？", 1, 926, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102502, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～", 1, 927, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102502, "どうやったら、立派な\nメイドになれるのかな", 1, 928, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102502, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？", 1, 929, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102502, "ご用があれば\nなんでも言って\nくださいね", 1, 930, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102502, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう", 1, 931, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102502, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　", 1, 932, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102502, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪", 1, 933, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102502, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！", 1, 934, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102502, "私、またなにか\nやっちゃいましたか？", 1, 935, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102502, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？", 1, 936, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102502, "転んだふりであの人に\n…って！\nなんでもないですよ！", 1, 937, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102502, "私、できることなら\nなんでもやりますよ\nあなたのためですから", 1, 938, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102502, "おはようございます♪\n今日も一日がんば…\nう、うわあ！", 1, 939, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102502, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！", 1, 940, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102502, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした", 1, 941, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102502, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ", 1, 942, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102502, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪", 1, 943, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102502, "かわいい♪\nあとでお洗濯\nしてあげますね", 1, 944, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102502, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです", 1, 945, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102502, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？", 1, 946, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 102502, "運動しようとすると\nいつも怪我するん\nですよね…", 1, 947, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102502, "こんなに美味しいもの\nメイドの私には\nもったいないです～！", 1, 948, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102502, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…", 1, 949, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102701, "あの方と私だけの\n部屋にしたいですわ", 1, 951, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102701, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！", 1, 952, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102701, "運命とは決して\n抗えないもの…\nクスクス…クスクス", 1, 953, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102701, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1, 954, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102701, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…", 1, 955, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102701, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 1, 956, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102701, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 1, 957, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102701, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？", 1, 958, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102701, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ", 1, 959, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102701, "お願いがあるのです。\n新しい研究の実験台に\n…", 1, 960, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102701, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 1, 961, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102701, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 1, 962, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102701, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…", 1, 963, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102701, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 1, 964, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102701, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…", 1, 965, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102701, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？", 1, 966, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102701, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…", 1, 967, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102701, "はっ！\nリラックスしたら\n閃きましたわ！", 1, 968, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102701, "あら、キメラの素体に\nぴったりですわ", 1, 969, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102701, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？", 1, 970, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102701, "あたたかい…\n加熱調合にも\n使えそうですわね", 1, 971, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 102701, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 1, 972, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102701, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 1, 973, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102701, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 1, 974, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102702, "あの方と私だけの\n部屋にしたいですわ", 1, 976, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102702, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…", 1, 977, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102702, "新しい薬\nどうやってあの方に\n飲ませようかしら", 1, 978, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102702, "部屋からあの方の\n香りが…\nああ、至福ですわ", 1, 979, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102702, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…", 1, 980, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102702, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…", 1, 981, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102702, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…", 1, 982, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102702, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね", 1, 983, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102702, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！", 1, 984, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102702, "お願いがあるのです\n新しい研究の\n実験台に…", 1, 985, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102702, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません", 1, 986, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102702, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね", 1, 987, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102702, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…", 1, 988, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102702, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ", 1, 989, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102702, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…", 1, 990, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102702, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？", 1, 991, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102702, "私ももう少し\n外見に気を配る\nべきでしょうか…", 1, 992, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102702, "はっ！\nリラックスしたら\n閃きましたわ！", 1, 993, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102702, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！", 1, 994, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102702, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？", 1, 995, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102702, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような", 1, 996, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 102702, "この運動エネルギー\n何かにうまく\n利用できないかしら？", 1, 997, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102702, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…", 1, 998, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102702, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ", 1, 999, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102801, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 1001, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102801, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね", 1, 1002, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102801, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね", 1, 1003, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102801, "お腹空いたわね、\n残り物で\n何か作ろうかしら", 1, 1004, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102801, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1, 1005, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102801, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 1, 1006, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102801, "焼きそばパンが\n食べたいな～\nあいつと…", 1, 1007, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102801, "ねぇあんた、どこか\n安いお店知らない？", 1, 1008, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102801, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪", 1, 1009, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102801, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね", 1, 1010, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102801, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪", 1, 1011, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102801, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！", 1, 1012, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102801, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの", 1, 1013, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102801, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ", 1, 1014, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102801, "午後のティータイム\nとかどう？\n良いカフェがあるの♪", 1, 1015, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102801, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？", 1, 1016, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102801, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪", 1, 1017, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102801, "これで仕事は一段落ね\nちょっと休憩…", 1, 1018, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102801, "うふふ、\nかわいいものを\n見るのは幸せね～", 1, 1019, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102801, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 1, 1020, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102801, "風邪を引かないように\n身体を温めなくちゃ", 1, 1021, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 102801, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 1, 1022, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102801, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？", 1, 1023, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102801, "知識から過去を学んで\n未来に活かすわよ", 1, 1024, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102802, "安売りのお店の情報\n整理しないと…", 1, 1026, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102802, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ", 1, 1027, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102802, "安く済んであいつが\n喜ぶ料理とか…\nないかしら", 1, 1028, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102802, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね", 1, 1029, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102802, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの", 1, 1030, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102802, "焼きそばパンが\n食べたいな～\nあいつと…", 1, 1031, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102802, "あいつと一緒に…\n色々なところに\nいきたいな", 1, 1032, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102802, "今度買い物に\n付き合いなさい\nセールがあるのよ", 1, 1033, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102802, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪", 1, 1034, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102802, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね", 1, 1035, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102802, "あんたとこうして\nのんびりするのも\nいいものね", 1, 1036, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102802, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ", 1, 1037, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102802, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪", 1, 1038, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102802, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ", 1, 1039, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102802, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪", 1, 1040, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102802, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら", 1, 1041, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102802, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！", 1, 1042, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102802, "これで仕事は一段落ね\nちょっと休憩…", 1, 1043, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102802, "ふふふ\nずっと眺めてたく\nなっちゃうわね", 1, 1044, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102802, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪", 1, 1045, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102802, "風邪を引かないように\n身体を温めなくちゃ", 1, 1046, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 102802, "さっ！　家の中でも\n効率的に\nトレーニングよ！", 1, 1047, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102802, "しっかり食べて\n栄養補給、栄養補給！", 1, 1048, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102802, "知識から過去を学んで\n未来に活かすわよ", 1, 1049, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102901, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？", 1, 1051, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102901, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！", 1, 1052, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102901, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 1053, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102901, "彼とデュエット\nしてみたいな～♪", 1, 1054, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102901, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～", 1, 1055, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102901, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 1, 1056, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102901, "写真集見なくても、\n本物がここに\nいるのにな～", 1, 1057, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102901, "握手会の練習、\n付き合ってくれない？", 1, 1058, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102901, "写真集のポーズ\n練習中～！\nど、どうかな？", 1, 1059, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102901, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！", 1, 1060, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102901, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！", 1, 1061, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102901, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ", 1, 1062, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102901, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪", 1, 1063, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102901, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1, 1064, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102901, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ", 1, 1065, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102901, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。", 1, 1066, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102901, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！", 1, 1067, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102901, "ふぅ…\nこんな姿、ファンには\n見せられないな～", 1, 1068, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102901, "かわいい～♪\nこれは強力な\nライバル登場かな！", 1, 1069, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102901, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！", 1, 1070, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102901, "体も暖まったし！\nダンスレッスンに\n行こうかな♪", 1, 1071, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 102901, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 1, 1072, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102901, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 1, 1073, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102901, "ステキなお話…\n新曲の作詞の\n参考になりそう♪", 1, 1074, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102902, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！", 1, 1076, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102902, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？", 1, 1077, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102902, "みんながくれた\nファンレター…\n私の大事な宝物…", 1, 1078, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102902, "彼とデュエット\nしてみたいな～♪", 1, 1079, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102902, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～", 1, 1080, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102902, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…", 1, 1081, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102902, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな", 1, 1082, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102902, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～", 1, 1083, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102902, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？", 1, 1084, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102902, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！", 1, 1085, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102902, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～", 1, 1086, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102902, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪", 1, 1087, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102902, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？", 1, 1088, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102902, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？", 1, 1089, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102902, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！", 1, 1090, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102902, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ", 1, 1091, 1, 2);
