INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 102902, "ベストタイム更新！\nライブに向けて\n体力つけないと…", 1, 1097, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102902, "太っちゃったら\nどうしよう…？\nでも手が止まらない～", 1, 1098, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102902, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…", 1, 1099, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103001, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？", 1, 1101, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103001, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 1102, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103001, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？", 1, 1103, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103001, "むむ、アクダイカンの\n気配がするデース", 1, 1104, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103001, "クニトリ合戦、\n楽しみデース！", 1, 1105, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103001, "ショーグンと\n出会えて、\nワタシは果報者デス！", 1, 1106, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103001, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1, 1107, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103001, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！", 1, 1108, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103001, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？", 1, 1109, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103001, "さ、さすが\nショーグンデス…\nいつの間に背後を！？", 1, 1110, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103001, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？", 1, 1111, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103001, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 1, 1112, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103001, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 1, 1113, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103001, "おはようデス！\n今日も一日\n忍法日和デス！", 1, 1114, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103001, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！", 1, 1115, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103001, "明日もきっと\nいい日デスよ、\nショーグン！", 1, 1116, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103001, "ちょんまげに\nしてみるのも\n面白いかもデス！", 1, 1117, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103001, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？", 1, 1118, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103001, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 1, 1119, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103001, "ワビサビというもの\nデスね！\n素晴らしいデス！", 1, 1120, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103001, "身体を暖めて\nイクサに向かうデス！", 1, 1121, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 103001, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！", 1, 1122, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103001, "うーん、\n美味しいデス…\n幸せデス～", 1, 1123, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103001, "これは…\nＢＵＳＨＩＤＯ…\nデスか？", 1, 1124, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103002, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～", 1, 1126, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103002, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ", 1, 1127, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103002, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？", 1, 1128, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103002, "むむ、アクダイカンの\n気配がするデース", 1, 1129, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103002, "クニトリ合戦\n楽しみデース！", 1, 1130, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103002, "最近、ショーグンの\nそばにいると\nドキドキするデス…", 1, 1131, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103002, "ショーグンの身は\n何があってもワタシが\n守るデス！", 1, 1132, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103002, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？", 1, 1133, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103002, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！", 1, 1134, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103002, "さ、さすがショーグン\nデス…いつの間に\n背後を！？", 1, 1135, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103002, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！", 1, 1136, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103002, "見つめられると\nドキドキするデス…\n新しい忍法デスか？", 1, 1137, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103002, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？", 1, 1138, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103002, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！", 1, 1139, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103002, "もうお昼デス\nお昼寝するデスか？", 1, 1140, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103002, "そろそろおネム\nデース…\n一緒に寝るデス？", 1, 1141, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103002, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス", 1, 1142, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103002, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？", 1, 1143, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103002, "これを変わり身の\n術に…かわいそう\nだから止めるデス", 1, 1144, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103002, "ワビサビというもの\nデスね！\n素晴らしいデス！", 1, 1145, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103002, "身体を暖めてイクサに\n向かうデス！", 1, 1146, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 103002, "ショーグンのお役に\n立つため、修行\nあるのみデス！", 1, 1147, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103002, "美味しいデス…\n酢昆布を和えて\nみたいデス", 1, 1148, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103002, "物事を知って\n来たるべきイクサに\n活かすデス", 1, 1149, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103101, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 1151, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103101, "父は、いつも男性に\nきびしいですね", 1, 1152, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103101, "占ってほしい人は、\nいつでも言って\nください", 1, 1153, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103101, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 1154, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103101, "この部屋、風水的にも\n運気が上がりそうです", 1, 1155, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103101, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い", 1, 1156, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103101, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 1, 1157, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103101, "女難の相が\n出ていますね…\n気をつけてください", 1, 1158, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103101, "今日のあなたの運勢は\n大きょ…\n大吉です", 1, 1159, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103101, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので", 1, 1160, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103101, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！", 1, 1161, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103101, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？", 1, 1162, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103101, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です", 1, 1163, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103101, "おはようございます\n今日の運勢を\n占いましょうか？", 1, 1164, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103101, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 1165, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103101, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが", 1, 1166, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103101, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ", 1, 1167, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103101, "一休みしましょう。\n憑かれて疲れました", 1, 1168, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103101, "かわいい…\n付喪神が宿らないかな", 1, 1169, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103101, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？", 1, 1170, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103101, "気持ちいいです…\n極楽浄土みたい…", 1, 1171, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 103101, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 1, 1172, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103101, "お父さんも\n食べたいの？\n死んでるのに？", 1, 1173, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103101, "この本…\nもしかして予言書？", 1, 1174, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103102, "ここにいる幽霊は\nみんな幸せそうです", 1, 1176, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103102, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています", 1, 1177, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103102, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね", 1, 1178, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103102, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも", 1, 1179, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103102, "お父さん、そろそろ\n子離れを…しない？\nそうですか…", 1, 1180, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103102, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？", 1, 1181, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103102, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？", 1, 1182, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103102, "この部屋、風水的にも\n運気が上がりそうです", 1, 1183, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103102, "シノブには指一本\n触れさせん！\nガブッ！！", 1, 1184, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103102, "あなたの手相は…\nて、手を握るのって\n緊張しますね", 1, 1185, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103102, "ラップ音？\nいえ、父のいびきです", 1, 1186, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103102, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪", 1, 1187, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103102, "前世って信じますか？\n私とあなたは\nもしかすると…", 1, 1188, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103102, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？", 1, 1189, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103102, "明るい時間は\nあまり得意では\nないんです…", 1, 1190, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103102, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです", 1, 1191, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103102, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ", 1, 1192, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103102, "うとうと…\nあれ…\n走馬灯が見える…", 1, 1193, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103102, "かわいい…\nお父さんみたい…", 1, 1194, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103102, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？", 1, 1195, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103102, "快適です…\n幽霊もどんどん\n集まってきますね…", 1, 1196, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 103102, "はぁはぁ…\nお、お父さん、\n代わりに運動して…", 1, 1197, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103102, "お父さんの分も、\n私が食べるね", 1, 1198, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103102, "この本の占い\n私の占いと違う…", 1, 1199, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103201, "私だって、一人で\n買い物ぐらい\nできますわ…きっと", 1, 1201, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103201, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 1202, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103201, "一日も早く、\n一人前の商人に\nなりたいものです", 1, 1203, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103201, "平和を買うのには\nどれほどのお金が\n必要なのかしら？", 1, 1204, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103201, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？", 1, 1205, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103201, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 1, 1206, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103201, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 1, 1207, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103201, "私と、庶民向けの\nお店へ一緒に\n参りませんか？", 1, 1208, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103201, "資産運用？\nお幾らほど\n増やしたいのです？", 1, 1209, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103201, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！", 1, 1210, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103201, "お金で解決するのは\n悪いことでは\nありませんわよね？", 1, 1211, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103201, "その、よろしければ\n私とお散歩など\nいかがです？", 1, 1212, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103201, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！", 1, 1213, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103201, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね", 1, 1214, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103201, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 1, 1215, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103201, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 1, 1216, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103201, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね", 1, 1217, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103201, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…", 1, 1218, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103201, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 1, 1219, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103201, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！", 1, 1220, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103201, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 1, 1221, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 103201, "健康はお金では\n買えませんが\n運動器具は別ですわ！", 1, 1222, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103201, "なかなか\n美味ですわ\nシェフはどちらに？", 1, 1223, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103201, "経済の仕組みを\nしっかりと\n学ばなくては…", 1, 1224, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103202, "そろそろ新しい\n案件を始めないと\nいけませんわね", 1, 1226, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103202, "ええと、これは\nおいくらで買ったん\nでしたっけ？", 1, 1227, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103202, "お金を見せたら\n態度が変わる人は\n信用できませんわ", 1, 1228, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103202, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね", 1, 1229, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103202, "この狭さも\n慣れれば\n快適ですわ♪", 1, 1230, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103202, "あの方はお金で雇った\nわけではないのに\n私といてくださる…", 1, 1231, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103202, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…", 1, 1232, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103202, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ", 1, 1233, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103202, "幼い頃\nお店屋さんごっこを\nなさいましたか？", 1, 1234, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103202, "資産運用？\nお幾らほど\n増やしたいのです？", 1, 1235, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103202, "今度一緒に…\nパーティにでも\n行きませんこと？", 1, 1236, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103202, "その、よろしければ\n私とお散歩など\nいかがです？", 1, 1237, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103202, "あなたさまの優しさは\nお金には\n代えられませんわね", 1, 1238, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103202, "なぜだか\n眠り足りないような\n感じがしますわ…", 1, 1239, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103202, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！", 1, 1240, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103202, "よろしければ\nお休み前のミルクを\n御一緒にいかが？", 1, 1241, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103202, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！", 1, 1242, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103202, "こういったものに\n腰を沈めると…\nき、気が緩んで…", 1, 1243, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103202, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！", 1, 1244, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103202, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ", 1, 1245, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103202, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…", 1, 1246, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 103202, "目標は遠くに\n置いた方が励みに\nなりますわ！", 1, 1247, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103202, "なかなか\n美味ですわ\nシェフはどちらに？", 1, 1248, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103202, "商人って、物語では\n悪役にされやすいの\nですね…", 1, 1249, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103301, "家の中のものを使って\n物ボケ、考えるべ！", 1, 1251, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103301, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！", 1, 1252, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103301, "うちに塀ができたら、\nへー、って言いたい！", 1, 1253, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103301, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ", 1, 1254, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103301, "エリザベスも\n連れてきてぇ～", 1, 1255, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103301, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪", 1, 1256, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103301, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！", 1, 1257, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103301, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！", 1, 1258, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103301, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？", 1, 1259, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103301, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 1, 1260, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103301, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 1261, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103301, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 1, 1262, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103301, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！", 1, 1263, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103301, "おはよう！　一緒に\n牛の乳しぼりいくべ？", 1, 1264, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103301, "お昼だ～腹が減っては\n笑いはできぬ～！", 1, 1265, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103301, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！", 1, 1266, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103301, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！", 1, 1267, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103301, "ナイスなイスに\n座りたい、\nなんちゃって！", 1, 1268, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103301, "めんこいのは、\nスベっても許される\nからずるいべな～？", 1, 1269, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103301, "必要以上に\n癒されちまうべや～", 1, 1270, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103301, "ぽかぽかだ…\nウケた会場みてぇだ", 1, 1271, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 103301, "お笑い芸人は、\n体が資本だべさ～！", 1, 1272, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103301, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 1, 1273, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103301, "ネタ作りのヒントが\n載ってねぇか探すべ", 1, 1274, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103302, "さぁ今日も張り切って\n漫才の練習するべさ！", 1, 1276, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103302, "この部屋でも、商品\n売っていいんだべか？", 1, 1277, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103302, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？", 1, 1278, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103302, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ", 1, 1279, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103302, "エリザベスも\n連れてきてぇ～", 1, 1280, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103302, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～", 1, 1281, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103302, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～", 1, 1282, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103302, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？", 1, 1283, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103302, "あんちゃん、百連\nツッコミ特訓だ～！", 1, 1284, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103302, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！", 1, 1285, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103302, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！", 1, 1286, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103302, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～", 1, 1287, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103302, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！", 1, 1288, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103302, "おはよう！\n今日もいい\nネタ作り日和だ！", 1, 1289, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103302, "お昼だな\nご飯を食べたら、\nネタの練習するべ！", 1, 1290, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103302, "もうこんな時間だべ\nネタ作りは\n明日にするべ…", 1, 1291, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103302, "うーん…衣装、\nずれてねぇべか？", 1, 1292, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103302, "ナイスなイスに\n座りたい\nなんちゃって！", 1, 1293, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103302, "はう～\nなまらめんこいべや～", 1, 1294, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103302, "必要以上に\n癒されちまうべや～", 1, 1295, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103302, "笑いをとることを\n忘れるほど\nあったけぇべ～", 1, 1296, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 103302, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！", 1, 1297, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103302, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～", 1, 1298, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103302, "幅広く教養を\n身につけて\nいいネタ作るべ！", 1, 1299, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103401, "通りにいる\nカップル…爆発\nすればいいのに…", 1, 1301, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103401, "う～ん、仕事なんて\nしないで、\n飲んでたいな～", 1, 1302, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103401, "今日は一日、\n外に出ない！\nもう決めたわ！", 1, 1303, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103401, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ", 1, 1304, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103401, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…", 1, 1305, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103401, "デートとか…\n誘っても嫌がられ\nないかな…？", 1, 1306, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103401, "もしかして運命の\n相手だったり…\nするのかな", 1, 1307, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103401, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？", 1, 1308, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103401, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね", 1, 1309, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103401, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…", 1, 1310, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103401, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～", 1, 1311, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103401, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 1, 1312, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103401, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな", 1, 1313, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103401, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…", 1, 1314, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103401, "おっひるー♪\nなに食べよっかな～♪", 1, 1315, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103401, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ", 1, 1316, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103401, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…", 1, 1317, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103401, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 1, 1318, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103401, "こういうの男の子に\nプレゼントして\nもらいたいな…", 1, 1319, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103401, "うわあ、\nこれ、私の好きな\n香りだー！", 1, 1320, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103401, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！", 1, 1321, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 103401, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！", 1, 1322, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103401, "もぐもぐ…\nおーいしー！\n…太らないよね？", 1, 1323, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103401, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 1, 1324, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103402, "仕事終わりの一杯の\nために、今日も\n頑張らないと", 1, 1326, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103402, "今日は\nのんびり\n過ごそうかな～", 1, 1327, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103402, "あ…\nお皿、洗って\nなかった…", 1, 1328, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103402, "もう少し、大人の\n魅力が出せるように\nなりたいな", 1, 1329, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103402, "素敵な彼氏…\n欲しいなぁ…", 1, 1330, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103402, "彼と私\n釣り合うかな…\n大丈夫よね？", 1, 1331, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103402, "今後のために\nちょっとは花嫁修業\nしてみようかな…？", 1, 1332, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103402, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？", 1, 1333, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103402, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？", 1, 1334, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103402, "もう…\n構ってくれないと\n泣いちゃうぞ～？", 1, 1335, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103402, "お姉さんが～\n可愛がって\nあげようか～？", 1, 1336, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103402, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…", 1, 1337, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103402, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！", 1, 1338, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103402, "さーて、今日も\nいっちょやるぞー！", 1, 1339, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103402, "後でのんびり\nストレッチでも\nしようっと♪", 1, 1340, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103402, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪", 1, 1341, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103402, "仕事の前に、髪とか\n整えないと…", 1, 1342, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103402, "自然体でゴロゴロ\nするのって\n幸せだなぁ…", 1, 1343, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103402, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？", 1, 1344, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103402, "うわあ\nこれ、私の好きな\n香りだー！", 1, 1345, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103402, "あったかーい…\nだ、だらけ\nちゃいそう…", 1, 1346, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 103402, "地道な努力が\nリア充への\n第一歩！", 1, 1347, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103402, "おーいしっ♪\nこのために\n生きてるわねえ", 1, 1348, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103402, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ", 1, 1349, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103801, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 1351, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103801, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？", 1, 1352, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103801, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 1353, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103801, "ちょっと、休憩…\n本でも読もうかな", 1, 1354, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103801, "もっとたくさんの本に\n囲まれて生活したいな", 1, 1355, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103801, "むかしむかしある所に\nシオリと\nあの人が…", 1, 1356, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103801, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな", 1, 1357, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103801, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 1358, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103801, "あ…お姉ちゃんと\n約束があったんだ…", 1, 1359, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103801, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 1, 1360, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103801, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 1, 1361, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103801, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです", 1, 1362, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103801, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 1, 1363, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103801, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 1364, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103801, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね", 1, 1365, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103801, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…", 1, 1366, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103801, "寝てばかりなので、\n髪が乱れてないかな\n…？", 1, 1367, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103801, "少し休もう\n…やっぱり\nたくさん休もう", 1, 1368, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103801, "かわいいものが\n枕元にあると\n安心できるんですよ", 1, 1369, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103801, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね", 1, 1370, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103801, "こうして温まって\nいると、病気も\n治る気がします", 1, 1371, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 103801, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…", 1, 1372, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103801, "少食ですけど\nこれならたくさん\n食べられそうです", 1, 1373, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103801, "続きが気になって\n読むのを止める\nタイミングが…", 1, 1374, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103802, "しっかり休まないと\n早く回復するように", 1, 1376, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103802, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　", 1, 1377, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103802, "病室よりも…\nずっとこの部屋に\nいたいですね…　", 1, 1378, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103802, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…", 1, 1379, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103802, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ", 1, 1380, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103802, "むかしむかしある所に\nシオリと\nあの人が…", 1, 1381, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103802, "あの人の\nためにも早く\n病気を治さないと…", 1, 1382, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103802, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？", 1, 1383, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103802, "新記録です！\n一週間連続、\n平熱を保ちました！", 1, 1384, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103802, "あ…お薬の時間…\n苦いけど我慢しなきゃ　", 1, 1385, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103802, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？", 1, 1386, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103802, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…", 1, 1387, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103802, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…", 1, 1388, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103802, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…", 1, 1389, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103802, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます", 1, 1390, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103802, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～", 1, 1391, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103802, "寝てばかりなので、\n髪が乱れてないかな\n…？　", 1, 1392, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103802, "少し休もう\n…やっぱり\nたくさん休もう", 1, 1393, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103802, "この子がいれば\n病室に一人でも\n寂しくないですね", 1, 1394, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103802, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？", 1, 1395, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103802, "こうして温まって\nいると、病気も\n治る気がします", 1, 1396, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 103802, "私も、少しは\n体力をつけないと\nいけませんよね　", 1, 1397, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103802, "いっぱい食べて\n健康な体にならないと", 1, 1398, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103802, "続きが気になって\n読むのを止める\nタイミングが…", 1, 1399, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104001, "友達ができたら、\n山の上でお弁当…\n夢が広がります…", 1, 1401, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104001, "あ、忘れずに\nお花に水を\nあげなきゃ…", 1, 1402, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104001, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…", 1, 1403, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104001, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…", 1, 1404, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104001, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…", 1, 1405, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104001, "ＢＢ団の活動日…\nまだかな…\nうふふふっ", 1, 1406, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104001, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 1, 1407, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104001, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！", 1, 1408, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104001, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？", 1, 1409, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104001, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？", 1, 1410, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104001, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！", 1, 1411, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104001, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！", 1, 1412, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104001, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 1, 1413, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104001, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！", 1, 1414, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104001, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪", 1, 1415, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104001, "もう眠いかもです…\n明日は友達、\nできるといいなあ…", 1, 1416, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104001, "私がもっと\n可愛ければ、\n友達できるかなあ？", 1, 1417, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104001, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです", 1, 1418, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104001, "かわいいなあ。\nこの子とも\n友達になりたい…", 1, 1419, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104001, "いい香りです…\n精神統一して\n友達のイメトレ…", 1, 1420, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104001, "灯りを見てると、\n心がなごみます～", 1, 1421, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104001, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 1, 1422, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104001, "おいしいです…\n私に友達がいたら、\n分けてあげたいな", 1, 1423, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104001, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 1, 1424, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104002, "友達作りは、焦らず\n慌てず、じっくりと…", 1, 1426, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104002, "ひとつ屋根の下\nって感じ…\nいいですねえ…", 1, 1427, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104002, "煮汁、煮汁が\n噴きこぼれて\nいませんか～", 1, 1428, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104002, "今度、みなさんと\nピクニックとか…\nいってみたいな", 1, 1429, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104002, "ぼっちでもいい\nたくましく育てば\n…なんて", 1, 1430, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104002, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…", 1, 1431, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104002, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…", 1, 1432, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104002, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！", 1, 1433, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104002, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！", 1, 1434, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104002, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？", 1, 1435, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104002, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！", 1, 1436, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104002, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ", 1, 1437, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104002, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです", 1, 1438, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104002, "朝ですね！\nこんないい朝は\n友達作りのチャンス！", 1, 1439, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104002, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…", 1, 1440, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104002, "もう眠いかもです…\n明日は友達\nできるといいなあ…", 1, 1441, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104002, "私…見た目\nそこまで変じゃ\nないですよね…？", 1, 1442, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104002, "ほわあ～…\nふかふかって\nいいものですね～", 1, 1443, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104002, "この子みたいな\nあいくるしさを\n目指したいです！", 1, 1444, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104002, "灯りを見てると\n心がなごみます～", 1, 1445, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104002, "ぬくぬくで、なんだか\n眠くなっちゃいますね", 1, 1446, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104002, "身体を鍛えて\nみなさんを\n守ります！", 1, 1447, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104002, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！", 1, 1448, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104002, "友達作りの\n参考になりそうなこと\n書いてないかな～？", 1, 1449, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104201, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです", 1, 1451, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104201, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？", 1, 1452, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104201, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 1453, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104201, "人々の平和を守る旅に\n同行してくれる人を\n探していて…", 1, 1454, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104201, "あとで曲を復習して\nおかないと…", 1, 1455, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104201, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…", 1, 1456, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104201, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 1, 1457, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104201, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 1458, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104201, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！", 1, 1459, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104201, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 1460, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104201, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 1, 1461, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104201, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 1, 1462, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104201, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 1, 1463, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104201, "おはようございます♪\n少し歌の練習を\nしてきますね", 1, 1464, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104201, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした", 1, 1465, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104201, "明日の予定は…\nアイドルはやることが\n多いみたいで…", 1, 1466, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104201, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 1467, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104201, "歌の調子をよくする\nためには休息も\n必要ですよね…", 1, 1468, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104201, "この子たちを\n相手に練習して\nみようかな…♪", 1, 1469, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104201, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 1, 1470, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104201, "ここにいると\n穏やかな旋律が\n浮かんできます…", 1, 1471, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104201, "歌うためには\n体力づくりは\n欠かせません", 1, 1472, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104201, "歌った後は\nお腹が空いて\nしまうんです", 1, 1473, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104201, "音楽について\n理論も勉強\nしないと…", 1, 1474, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104202, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…", 1, 1476, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104202, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな", 1, 1477, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104202, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです", 1, 1478, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104202, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…", 1, 1479, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104202, "楽譜を買いました\n思いついた歌を\n書き留めておくために", 1, 1480, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104202, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？", 1, 1481, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104202, "私の中にある\n私だけの歌…\n作ってみようかな？", 1, 1482, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104202, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね", 1, 1483, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104202, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？", 1, 1484, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104202, "魔物退治ですか！？\n…違います？\n良かった…", 1, 1485, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104202, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！", 1, 1486, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104202, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？", 1, 1487, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104202, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした", 1, 1488, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104202, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ", 1, 1489, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104202, "少し休んだら\n午後の練習に\n行ってきますね", 1, 1490, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104202, "曲を作っていたら\nもうこんな時間…\nおやすみなさい", 1, 1491, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104202, "見た目については\n前よりも気をつけてと\nノゾミに言われてます", 1, 1492, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104202, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです", 1, 1493, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104202, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね", 1, 1494, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104202, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります", 1, 1495, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104202, "ここにいると\n穏やかな旋律が\n浮かんできます…", 1, 1496, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104202, "もっと体力をつけて\n最高のライブを\nファンに届けます！", 1, 1497, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104202, "これでも一応\n体形には気をつけて\n食べてるんですよ？", 1, 1498, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104202, "読書は歌に\n新しい風を\n吹き込んでくれます", 1, 1499, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104301, "店の新メニューでも\n考えるかな", 1, 1501, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104301, "あたしの店から、\n出前を届けようか？", 1, 1502, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104301, "パトロール、\nパトロール…\nここは異常なしかな？", 1, 1503, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104301, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 1504, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104301, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 1505, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104301, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？", 1, 1506, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104301, "今日の献立は…\nあいつの好きなものに\nしてやるか", 1, 1507, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104301, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ", 1, 1508, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104301, "天気がいい日は\n外で運動したく\nならねぇか？", 1, 1509, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104301, "腹減ったか？　なんか\n作ってやろうか？", 1, 1510, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104301, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ", 1, 1511, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104301, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ", 1, 1512, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104301, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 1, 1513, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104301, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか", 1, 1514, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104301, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 1, 1515, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104301, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな", 1, 1516, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104301, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 1517, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104301, "ふわぁ、\n一休みすっかなぁ～", 1, 1518, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104301, "うっ！　か、かわいい\nじゃねぇか", 1, 1519, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104301, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 1, 1520, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104301, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな", 1, 1521, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104301, "オラオラオラーーー！\n燃えるぜーーー！", 1, 1522, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104301, "いい味してるぜ！\nうちの店でも\n出してみたいな", 1, 1523, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104301, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ", 1, 1524, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104302, "パトロール終了～\n本日も異常なしっと！", 1, 1526, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104302, "あたしの店から\n出前を届けようか？", 1, 1527, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104302, "あいつ、早く\n帰ってこないかな", 1, 1528, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104302, "あいつ\n腹すかせてないと\nいいんだけど…", 1, 1529, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104302, "のんびりし過ぎるのも\n…性に合わねぇな", 1, 1530, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104302, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？", 1, 1531, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104302, "今日の献立は…\nあいつの好きなものに\nしてやるか", 1, 1532, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104302, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ", 1, 1533, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104302, "天気がいい日は\n外で運動したく\nならねぇか？", 1, 1534, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104302, "腹減ったか？　なんか\n作ってやろうか？", 1, 1535, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104302, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？", 1, 1536, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104302, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！", 1, 1537, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104302, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ", 1, 1538, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104302, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？", 1, 1539, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104302, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ", 1, 1540, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104302, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？", 1, 1541, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104302, "た、たまにはその…\nかわいい格好とか\nしても…", 1, 1542, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104302, "ふぅ～♪\n疲れがとれるな～", 1, 1543, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104302, "うっ！　か、かわいい\nじゃねぇか", 1, 1544, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104302, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…", 1, 1545, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104302, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな", 1, 1546, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104302, "オラオラオラーーー！\n燃えるぜーーー！", 1, 1547, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104302, "いい味してるぜ！\nうちの店でも\n出してみたいな", 1, 1548, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104302, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…", 1, 1549, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104501, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 1551, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104501, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが", 1, 1552, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104501, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ", 1, 1553, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104501, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ", 1, 1554, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104501, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 1555, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104501, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！", 1, 1556, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104501, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 1, 1557, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104501, "視線を感じると\n思ったら…やはり\nドＳさんでしたか", 1, 1558, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104501, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ", 1, 1559, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104501, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？", 1, 1560, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104501, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？", 1, 1561, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104501, "クウカにナニをする\nつもりですか…\n期待してしまいます！", 1, 1562, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104501, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？", 1, 1563, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104501, "おはようございます。\n朝からお盛んですね？\nぐふふ…", 1, 1564, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104501, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり", 1, 1565, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104501, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…", 1, 1566, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104501, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ", 1, 1567, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104501, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン", 1, 1568, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104501, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 1, 1569, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104501, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！", 1, 1570, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104501, "ああ、身体が\n火照って…\nぐふ、ぐふふ…", 1, 1571, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104501, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ", 1, 1572, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104501, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？", 1, 1573, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104501, "この本の下に、\nそういう本を\nカムフラージュ？", 1, 1574, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104502, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…", 1, 1576, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104502, "最近なにも\nしてくれません…\n焦らしてるんですね", 1, 1577, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104502, "も、妄想が\n足りません…\n禁断症状がっ…！", 1, 1578, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104502, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！", 1, 1579, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104502, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ", 1, 1580, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104502, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね", 1, 1581, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104502, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！", 1, 1582, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104502, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！", 1, 1583, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104502, "あっ…\nジロジロ見てたの\n気づいちゃいました？", 1, 1584, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104502, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり", 1, 1585, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104502, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？", 1, 1586, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104502, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね", 1, 1587, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104502, "ドＳさんがお望みなら\nクウカは\nどんなことだって！", 1, 1588, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104502, "朝だからこそ\nできる妄想が\nあります…ぐふふ", 1, 1589, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104502, "こんな日の高いうち\nからドＳさんは…\nじゅるり", 1, 1590, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104502, "今夜は一晩中…\nなんですね？\nぐふふ…", 1, 1591, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104502, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…", 1, 1592, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104502, "くつろいで油断\nしているスキに何を\nされるのでしょう！？", 1, 1593, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104502, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ", 1, 1594, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104502, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！", 1, 1595, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104502, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！", 1, 1596, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104502, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね", 1, 1597, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104502, "こんなにいっぱい\n入りません…\nお腹にっ！", 1, 1598, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104502, "どんな本も\nよく見ると\nいやらしいです…！", 1, 1599, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104601, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪", 1, 1601, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104601, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…", 1, 1602, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104601, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪", 1, 1603, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104601, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？", 1, 1604, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104601, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…", 1, 1605, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104601, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪", 1, 1606, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104601, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪", 1, 1607, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104601, "うずうず…その指、\nじゃれついても\nいいかにゃ？", 1, 1608, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104601, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪", 1, 1609, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104601, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？", 1, 1610, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104601, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪", 1, 1611, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104601, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー", 1, 1612, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104601, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？", 1, 1613, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104601, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 1614, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104601, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…", 1, 1615, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104601, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ", 1, 1616, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104601, "客商売では、\n外見のチェックも\n欠かせないのにゃ！", 1, 1617, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104601, "うにゃ～！\nお昼寝に\nぴったりにゃ", 1, 1618, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104601, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！", 1, 1619, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104601, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！", 1, 1620, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104601, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…", 1, 1621, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104601, "本当は外で\n運動したいん\nだけどにゃ", 1, 1622, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104601, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！", 1, 1623, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104601, "ふむふむ…猫の\nしつけ方は、と…", 1, 1624, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104602, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪", 1, 1626, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104602, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪", 1, 1627, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104602, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！", 1, 1628, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104602, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…", 1, 1629, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104602, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…", 1, 1630, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104602, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？", 1, 1631, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104602, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…", 1, 1632, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104602, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？", 1, 1633, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104602, "あたしのこと…\nしっかり捕まえ\nといてにゃ？", 1, 1634, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104602, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪", 1, 1635, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104602, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！", 1, 1636, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104602, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？", 1, 1637, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104602, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！", 1, 1638, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104602, "いい朝にゃ！\n顔を洗ってくるにゃ！", 1, 1639, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104602, "そろそろおっひる～\nにゃーんか\n食べたい…", 1, 1640, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104602, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ", 1, 1641, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104602, "にゃにゃ…\n髪が乱れてるにゃ", 1, 1642, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104602, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ", 1, 1643, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104602, "これ、猫と匹敵する\nぐらいかわいいにゃ！", 1, 1644, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104602, "猫でもこわくない\n優しい灯りにゃ！", 1, 1645, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104602, "ぽっかぽか\nしあわせな気分にゃ～", 1, 1646, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104602, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！", 1, 1647, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104602, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！", 1, 1648, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104602, "本を読んで\n猫の気持ちを\n勉強するのにゃ！", 1, 1649, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104801, "内職手伝える人\nいないかしら？", 1, 1651, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104801, "次の依頼は、\nこういう計画で…\nうん、完璧ね！", 1, 1652, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104801, "次の特売セールに\n向けて、しっかり\n準備しないと！", 1, 1653, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104801, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね", 1, 1654, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104801, "一日に受ける\nバイトの数、もう少し\n増やしたいわね", 1, 1655, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104801, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！", 1, 1656, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104801, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 1, 1657, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104801, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？", 1, 1658, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104801, "相談事かしら？\nそれくらいなら\n無料でいいわよ", 1, 1659, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104801, "背後の気配なら\nすぐ分かるわ\n試してみる？", 1, 1660, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104801, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って", 1, 1661, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104801, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 1, 1662, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104801, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ", 1, 1663, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104801, "おはよう！\n今日も朝から\nバイトよ！", 1, 1664, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104801, "お昼時ね。\n少し休憩したら、\nまた働くわよ！", 1, 1665, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104801, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 1, 1666, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104801, "接客業の依頼が\nある時は、外見も\nキレイにしないとね", 1, 1667, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104801, "はぁ～バイトで\n疲れたから癒される～", 1, 1668, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104801, "これを内職で\n作ったら\n儲かるかしら？", 1, 1669, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104801, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！", 1, 1670, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104801, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！", 1, 1671, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104801, "働き続けるには\n健康第一！　それには\n運動が一番よ", 1, 1672, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104801, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！", 1, 1673, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104801, "日々絶えず学んで、\n生活に活かすのよ！", 1, 1674, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104802, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね", 1, 1676, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104802, "次の依頼は\nこういう計画で…\nうん、完璧ね！", 1, 1677, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104802, "ギルドの経費\nあとちょっとだけ\n削れそうね…", 1, 1678, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104802, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ", 1, 1679, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104802, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ", 1, 1680, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104802, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね", 1, 1681, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104802, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…", 1, 1682, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104802, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ", 1, 1683, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104802, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？", 1, 1684, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104802, "今から\n休憩するところなの\n一緒にどう？", 1, 1685, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104802, "ん？　どうしたの？\n私のことが\n気になるの？", 1, 1686, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104802, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ", 1, 1687, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104802, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね", 1, 1688, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104802, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？", 1, 1689, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104802, "お昼時ね\n少し休憩したら\nまた働くわよ！", 1, 1690, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104802, "もうこんな時間！\n明日もバイトで\n朝早いのよ…", 1, 1691, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104802, "女の子らしく…\nどうも忘れちゃう\nのよね", 1, 1692, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104802, "ここで少し\n休んだら\nまたバイトよ！", 1, 1693, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104802, "これを内職で\n作ったら\n儲かるかしら？", 1, 1694, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104802, "仕事から帰ってきて\nこういうのがあると\n素敵ね", 1, 1695, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104802, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ", 1, 1696, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104802, "これで休憩中も\n無駄なく\n鍛えられるわ♪", 1, 1697, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104802, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ", 1, 1698, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104802, "日々絶えず学んで\n生活に活かすのよ！", 1, 1699, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104901, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1, 1701, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104901, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1, 1702, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104901, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1, 1703, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104901, "くんくん、\n弟くんのにおい…\n近くにいるね？", 1, 1704, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104901, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 1, 1705, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104901, "この部屋を、\n私と弟くんの\n愛の巣に…", 1, 1706, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104901, "はぁ～…弟くん\n禁断症状だよ～", 1, 1707, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104901, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 1, 1708, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104901, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 1, 1709, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104901, "姉と弟の外出は\nデートって\nいうのかな？", 1, 1710, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104901, "もう！\nイタズラする子には\nお姉ちゃんチョップ！", 1, 1711, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104901, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪", 1, 1712, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104901, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 1, 1713, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104901, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 1, 1714, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104901, "出かけるなら\n明るいうちに\n帰ってくるんだよ！", 1, 1715, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104901, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆", 1, 1716, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104901, "きれいなお姉ちゃん\n好きだよね♪", 1, 1717, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104901, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 1, 1718, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104901, "かわいい♪\nお姉ちゃんチョップ！", 1, 1719, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104901, "この香り、\n弟くん好きかな？", 1, 1720, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104901, "これ、弟くんみたいに\nあったかいね～", 1, 1721, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104901, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 1, 1722, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104901, "あ、これ\n弟くんが好きな\n味付けだね！", 1, 1723, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104901, "弟くんが読んでいいか\nお姉ちゃんが先に\n中を確認しなくちゃ！", 1, 1724, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104902, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？", 1, 1726, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104902, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…", 1, 1727, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104902, "弟くんのお友達は\nみんな私の妹だよ♪", 1, 1728, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104902, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！", 1, 1729, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104902, "はっ！\n弟くんが困ってる\n気がする！", 1, 1730, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104902, "はぁ～…弟くん、\n禁断症状だよ～", 1, 1731, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104902, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪", 1, 1732, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104902, "お姉ちゃん\nキミのお願いなら\nなんでも聞いて\nあげちゃうぞっ", 1, 1733, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104902, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～", 1, 1734, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104902, "お姉ちゃんの胸に\n飛び込んでおいでっ♪", 1, 1735, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104902, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？", 1, 1736, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104902, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？", 1, 1737, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104902, "お風呂にする？\nごはんにする？\nそれとも…うふふ♪", 1, 1738, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104902, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね", 1, 1739, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104902, "お姉ちゃんが\nお昼ごはん\n作ってあげようか？", 1, 1740, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104902, "眠れないの？\nお姉ちゃんが子守歌\n歌ってあげるね！", 1, 1741, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104902, "弟くんを\n誘惑しちゃうぞ～♪", 1, 1742, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104902, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪", 1, 1743, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104902, "かわいい♪\nお姉ちゃんチョップ！", 1, 1744, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104902, "お肌に潤いが\nでてきたみたい～♪", 1, 1745, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104902, "これ、弟くんみたいに\nあったかいね～", 1, 1746, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 104902, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ", 1, 1747, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104902, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～", 1, 1748, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104902, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆", 1, 1749, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105001, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！", 1, 1751, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105001, "身長とバスト以外は\n完璧な大人\nなのになぁ～", 1, 1752, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105001, "アタシの大人コーデで\nあいつも夢中ね♪", 1, 1753, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105001, "朝顔の観察したら\n理系女子になれる\nかしら？", 1, 1754, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105001, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1, 1755, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105001, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！", 1, 1756, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105001, "アタシが待ってる\nなんて、アイツは\n幸せものよねー", 1, 1757, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105001, "わーい、アメだー♪\n食べる食べるー♪", 1, 1758, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105001, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！", 1, 1759, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105001, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？", 1, 1760, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105001, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！", 1, 1761, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105001, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 1, 1762, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105001, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 1, 1763, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105001, "朝のスキンケアは\n大事なの！", 1, 1764, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105001, "お腹すいたー\nダイエット\nやめちゃおうかなー", 1, 1765, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105001, "エイジングケアを\nしないとねー", 1, 1766, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105001, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら", 1, 1767, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105001, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 1, 1768, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105001, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！", 1, 1769, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105001, "オシャレ～♪\n大人なレディって\n感じよね♪", 1, 1770, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105001, "あったかーい♪\nここで宿題\nしようかしら？", 1, 1771, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 105001, "運動したら成長\nしないかしら？\n背とか胸とか…", 1, 1772, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105001, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ", 1, 1773, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105001, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 1, 1774, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105002, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！", 1, 1776, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105002, "恋人にするなら\nイケメンか\nただイケよね～", 1, 1777, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105002, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！", 1, 1778, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105002, "朝顔の観察したら\n理系女子になれる\nかしら？", 1, 1779, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105002, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！", 1, 1780, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105002, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…", 1, 1781, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105002, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ", 1, 1782, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105002, "なーに？\nアタシに甘えたいの？", 1, 1783, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105002, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！", 1, 1784, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105002, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！", 1, 1785, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105002, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？", 1, 1786, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105002, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？", 1, 1787, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105002, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！", 1, 1788, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105002, "レディの朝は\n忙しいのよ", 1, 1789, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105002, "お腹すいたー\nダイエット\nやめちゃおうかなー", 1, 1790, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105002, "エイジングケアを\nしないとねー", 1, 1791, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105002, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！", 1, 1792, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105002, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！", 1, 1793, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105002, "あたしがもっと\nオシャレにかわいく\nしてあげようか？", 1, 1794, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105002, "オシャレ～♪\n大人なレディって\n感じよね♪", 1, 1795, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105002, "あったかーい♪\nここで宿題\nしようかしら？", 1, 1796, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 105002, "運動したら成長\nしないかしら？\n背とか胸とか…", 1, 1797, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105002, "レディは、食べ物にも\n気を使うのよ", 1, 1798, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105002, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？", 1, 1799, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105101, "魔法と科学\nどっちも極めて\nみせるわ", 1, 1801, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105101, "仕事も片付いたし\n部屋で\n実験実験…♪", 1, 1802, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105101, "たまった治療代の\n請求書を\nまとめないと…", 1, 1803, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105101, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ", 1, 1804, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105101, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね", 1, 1805, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105101, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら", 1, 1806, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105101, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 1, 1807, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105101, "あら、ヒマなら\n薬草の整理を\n手伝って", 1, 1808, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105101, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？", 1, 1809, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105101, "具合が悪い？\nなら診てあげる\nまずは触診から…", 1, 1810, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105101, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…", 1, 1811, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105101, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 1, 1812, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105101, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 1, 1813, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105101, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？", 1, 1814, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105101, "あら、もうお昼？\nそろそろ一息\n入れましょうか", 1, 1815, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105101, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？", 1, 1816, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105101, "医者たるもの\nいつだって\n清潔に…♪", 1, 1817, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105101, "いいわねこれ\n仮眠の時に\n使わせてもらうわ", 1, 1818, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105101, "ぬいぐるみなら\n動物も…", 1, 1819, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105101, "いい香り…\n仕事場にも\n置いてみようかしら", 1, 1820, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105101, "ここにいると\nついウトウト\nしちゃうわ…", 1, 1821, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 105101, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね", 1, 1822, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105101, "研究していると\nつい食べるのを\n忘れちゃうのよね", 1, 1823, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105101, "実験もいいけど\nいつか成果を\n本にしないと…", 1, 1824, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105102, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…", 1, 1826, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105102, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？", 1, 1827, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105102, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？", 1, 1828, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105102, "毒を薄めると\n薬になるのって\n今でも不思議だわ", 1, 1829, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105102, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね", 1, 1830, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105102, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪", 1, 1831, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105102, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら", 1, 1832, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105102, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？", 1, 1833, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105102, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから", 1, 1834, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105102, "欲しい薬が\nあったら言ってね\n調合してみるから", 1, 1835, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105102, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる", 1, 1836, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105102, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？", 1, 1837, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105102, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも", 1, 1838, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105102, "朝しか取れない\n植物があるの\n採取しに行くわよ", 1, 1839, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105102, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ", 1, 1840, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105102, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね", 1, 1841, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105102, "身なりについて\n研究するのも\n楽しそうね", 1, 1842, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105102, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ", 1, 1843, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105102, "たまにはこういう\n可愛らしいものに\n触れないとね♪", 1, 1844, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105102, "いい香り…\n仕事場にも\n置いてみようかしら", 1, 1845, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105102, "仕事がなかったら\nこのまますーっと\n眠りたいのに…", 1, 1846, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 105102, "運動しないと\n体型の維持が…", 1, 1847, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105102, "適度な食事も\n大切よね", 1, 1848, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105102, "まさか私が\n恋愛読本を読む\n日が来るなんて…", 1, 1849, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105201, "ぺっぺっ！\n…い、いけないっ！", 1, 1851, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105201, "いっけな～い！\nひづめで床に傷が！", 1, 1852, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105201, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！", 1, 1853, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105201, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～", 1, 1854, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105201, "みんな、体重\nどれくらいなのかな？", 1, 1855, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105201, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 1, 1856, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105201, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪", 1, 1857, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105201, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪", 1, 1858, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105201, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？", 1, 1859, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105201, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？", 1, 1860, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105201, "力仕事ならまかせて！", 1, 1861, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105201, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 1, 1862, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105201, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 1, 1863, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105201, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 1864, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105201, "ランチは\nおしゃれなカフェで\nしてみたいな～♪", 1, 1865, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105201, "お肌のケアに\nパックしながら\n寝ようかな～", 1, 1866, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105201, "やだっ、\nまた寝ぐせついてる！\n直さないと！", 1, 1867, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105201, "ミシッっていった！\n私、重すぎるかも！？", 1, 1868, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105201, "あ！　あなたも\n美人さんね～♪", 1, 1869, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105201, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…", 1, 1870, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105201, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも", 1, 1871, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 105201, "な、なんだか野生に\n帰っちゃいそう！", 1, 1872, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105201, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！", 1, 1873, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105201, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 1, 1874, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105202, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～", 1, 1876, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105202, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？", 1, 1877, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105202, "動物苑…って\n嫌なギルド名ね…", 1, 1878, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105202, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！", 1, 1879, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105202, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪", 1, 1880, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105202, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう", 1, 1881, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105202, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？", 1, 1882, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105202, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！", 1, 1883, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105202, "どうしたの？\n急に抱きついてきて…\n癒されたいの？", 1, 1884, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105202, "ぺっぺっ！\n…い、いけないっ！", 1, 1885, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105202, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね", 1, 1886, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105202, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？", 1, 1887, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105202, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪", 1, 1888, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105202, "今朝は寝癖がひどいわ\nしっぽの辺りとか～", 1, 1889, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105202, "ダイエット中だから\nお昼は抜いてるの～", 1, 1890, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105202, "お肌のケアに\nパックしながら\n寝ようかな～", 1, 1891, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105202, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！", 1, 1892, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105202, "ミシッっていった！\n私、重すぎるかも！？", 1, 1893, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105202, "あ！　あなたも\n美人さんね～♪", 1, 1894, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105202, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～", 1, 1895, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105202, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！", 1, 1896, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 105202, "な、なんだか野生に\n帰っちゃいそう！", 1, 1897, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105202, "何を食べるかより\n誰と食べるかが\n大事なのよね", 1, 1898, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105202, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～", 1, 1899, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105301, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！", 1, 1901, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105301, "私は子供ではない…！\nが、お菓子は\nいただこう！", 1, 1902, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105301, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ", 1, 1903, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105301, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…", 1, 1904, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105301, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 1905, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105301, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！", 1, 1906, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105301, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…", 1, 1907, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105301, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？", 1, 1908, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105301, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 1909, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105301, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！", 1, 1910, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105301, "私ともっと高く、\n共に飛ぼう！", 1, 1911, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105301, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ", 1, 1912, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105301, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ", 1, 1913, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105301, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！", 1, 1914, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105301, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな", 1, 1915, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105301, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…", 1, 1916, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105301, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな", 1, 1917, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105301, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 1, 1918, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105301, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 1, 1919, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105301, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…", 1, 1920, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105301, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 1, 1921, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 105301, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！", 1, 1922, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105301, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！", 1, 1923, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105301, "これを読んで、新たな\n戦術を編み出すぞっ！", 1, 1924, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105302, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ", 1, 1926, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105302, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…", 1, 1927, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105302, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…", 1, 1928, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105302, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…", 1, 1929, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105302, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！", 1, 1930, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105302, "あいつは今頃、何を\nしているのだろうな…\nふふっ", 1, 1931, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105302, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…", 1, 1932, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105302, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？", 1, 1933, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105302, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！", 1, 1934, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105302, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…", 1, 1935, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105302, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！", 1, 1936, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105302, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか", 1, 1937, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105302, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ", 1, 1938, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105302, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！", 1, 1939, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105302, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか", 1, 1940, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105302, "寝る前に…あと少し\nだけ、鍛錬を…", 1, 1941, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105302, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては", 1, 1942, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105302, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…", 1, 1943, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105302, "うむ…このかわいさ…\n本国に持って\n帰りたい…", 1, 1944, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105302, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…", 1, 1945, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105302, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！", 1, 1946, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 105302, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！", 1, 1947, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105302, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！", 1, 1948, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105302, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな", 1, 1949, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105701, "私たちの騎空艇\nどんなデザインに\nしよう…？", 1, 1951, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105701, "団員募集の\nポスター\n作ろうっと♪", 1, 1952, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105701, "父さん…\n今頃どこで\n何してるんだろ？", 1, 1953, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105701, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな", 1, 1954, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105701, "父さん以上の\n世界一の\n騎空士になりたい！", 1, 1955, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105701, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ", 1, 1956, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105701, "父さん…\n私にもね、大切な\n仲間ができたよ", 1, 1957, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105701, "仕事の依頼？\nすぐに支度\nしてくるね！", 1, 1958, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105701, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！", 1, 1959, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105701, "なーにー？\nどうしたの？\n今呼んだよね？", 1, 1960, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105701, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて", 1, 1961, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105701, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！", 1, 1962, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105701, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！", 1, 1963, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105701, "おはよう！\n体の調子はどう？\n今日は頑張れそう？", 1, 1964, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105701, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった", 1, 1965, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105701, "…あれ？\nおしゃべりしてたら\nもうこんな時間？", 1, 1966, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105701, "どうせならオシャレな\n騎空士って\n呼ばれたいな", 1, 1967, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105701, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと", 1, 1968, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105701, "よし！　キミは\n今から団長代理だ！\n…なんてね♪", 1, 1969, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105701, "いい香り～♪\n気持ちが\n落ち着く～…", 1, 1970, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105701, "これって\n騎空艇に\n置けるかな…？", 1, 1971, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 105701, "どんな仕事でも\nこなせるような\n体力がないとね…！", 1, 1972, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105701, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪", 1, 1973, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105701, "空を冒険するには\n色んなことを\n知っておかないと…", 1, 1974, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105702, "…戻らなくなったよ\nどうすれば元に\n戻る…？", 1, 1976, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105702, "暇だよ…\nどこかに強い人は\nいないか？", 1, 1977, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105702, "ジョブチェンジ！\n…叫んでも\n戻らないよ", 1, 1978, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105702, "今のうちに、\n魔物退治の\n仕事に行くね！", 1, 1979, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105702, "ハァ～…\nうん、気の巡りは\n問題ないね！", 1, 1980, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105702, "私の力の源は\n仲間との\n絆だよ！", 1, 1981, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105702, "いつかこの拳を\n空を越えた、星まで\n届かせたいね", 1, 1982, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105702, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！", 1, 1983, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105702, "私はいつも通りに\n喋ってるつもり\nだよ？", 1, 1984, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105702, "どうした？\nもしかして手合せ\nしたいか？", 1, 1985, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105702, "騎空士としても\n拳法家としても\n目指すは世界一ね！", 1, 1986, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105702, "背中はあなたに\n預けたよ！\nよろしくね！", 1, 1987, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105702, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！", 1, 1988, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105702, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？", 1, 1989, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105702, "腹減ったね…\nなにか食べに\n行くよ！", 1, 1990, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105702, "修行のしすぎは\n良くないよ？\n気をつけるね", 1, 1991, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105702, "美しい技は\n美しい体が\nあってこそ！", 1, 1992, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105702, "よいしょっと…\nちょっとのんびり\nさせてもらうよ", 1, 1993, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105702, "…練習に\n使うのはさすがに\n可哀そうね", 1, 1994, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105702, "これは…\n瞑想の時にも\n使ってみたいね", 1, 1995, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105702, "出来ればもっと\n熱いほうが\n嬉しいよ", 1, 1996, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 105702, "ここでも\n修行出来るなんて\n最高だよ！", 1, 1997, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105702, "気力の源は体力！\n体力の源は\n健康とご飯！", 1, 1998, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105702, "んー…\n難しいことは\nわかんないよ！", 1, 1999, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105801, "わたしたちの\nギルドハウスへ\nようこそ～！", 1, 2001, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105801, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…", 1, 2002, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105801, "ん？\n食べ物のにおい？", 1, 2003, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105801, "みんなと仲良く食卓を\n囲みたいですね～", 1, 2004, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105801, "この部屋が、わたしの\n帰る場所です♪", 1, 2005, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105801, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！", 1, 2006, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105801, "あの人、\n大食いの子って\n嫌いかな？", 1, 2007, 8, 1);
