INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 106002, "ディナーに行くわよ！\nギルドの目的\n食べ歩きだもんね♪", 1, 2091, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106002, "今日もしっぽが\nいい感じ♪", 1, 2092, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106002, "うにゃ～\nどんどん眠く\nなる～", 1, 2093, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106002, "これ、あたしの能力で\n操れないかな？", 1, 2094, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106002, "あたし、みんなや\nあいつと同じ香りを\n身に着けてるんだ…", 1, 2095, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106002, "うっ、のんびり\nしたくなる…\n特訓しなきゃ\nいけないのに～", 1, 2096, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 106002, "特訓特訓！\nあたしは\nやればできる！", 1, 2097, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 106002, "おいしいものを\nたくさん食べる…\nギルド活動って\n楽しい～♪", 1, 2098, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 106002, "魔法を使うにも\n知識が必要だものね", 1, 2099, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105901, "この空間は主さまの\n香りがします\n…ふふふ♪", 1, 2101, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105901, "炊事、洗濯、お掃除、\nお買いものもしなくて\nはいけませんね", 1, 2102, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105901, "アメスさまへの、\nお祈りができそうな\n場所は…", 1, 2103, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105901, "さすが都会…\nおいしいものが\nたくさんあるのですね", 1, 2104, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105901, "主さまが不自由なく\n使える部屋に\nしなくては…", 1, 2105, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105901, "できるなら…\n主さまと二人だけの\n部屋の方が…", 1, 2106, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105901, "主さまの隣には\nわたくしが…\nわたくしの隣には、\n主さまが…幸せです", 1, 2107, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105901, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 1, 2108, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105901, "もしお疲れでしたら\n肩でも\nお揉みしましょうか？", 1, 2109, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105901, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 1, 2110, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105901, "今日のおこづかいです\n何かおいしいものを\n召し上がってください", 1, 2111, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105901, "主さまのおてて\n温かいですね…♪", 1, 2112, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105901, "主さまのおそばに\nいられて、わたくし、\n嬉しいです…", 1, 2113, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105901, "おはようございます。\n洗濯をしなければ…", 1, 2114, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105901, "そろそろお昼ですね。\nおいしいものを\n食べましょう", 1, 2115, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105901, "おやすみになる時は、\nお声をかけてください\nお供致します", 1, 2116, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105901, "もう少し…\nおっきくなりたい\nです", 1, 2117, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105901, "このふかふかの優しさ\n主さまのようです", 1, 2118, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105901, "かわいい♪\n主さまにそっくり♪", 1, 2119, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105901, "故郷の香りとは\n違いますが、これも\n心地よいものです…", 1, 2120, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105901, "ここで洗濯物を\n乾かせるでしょうか", 1, 2121, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 105901, "都会の人々は\nこんなふうに\n運動を…！？", 1, 2122, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105901, "これは…主さまにも\n食べさせて\n差し上げたい…\nあーんって♪", 1, 2123, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105901, "よく学んで、\n主さまのお役に\n立たなければ", 1, 2124, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105902, "主さまがいつ戻って\n来ても平気なよう\n部屋を綺麗に保たねば", 1, 2126, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105902, "節約のためにも\n自家菜園などするのは\nどうでしょう…　", 1, 2127, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105902, "アメスさまへの\nお祈りができそうな\n場所は…", 1, 2128, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105902, "瞑想をすると\n心が安らぎます…", 1, 2129, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105902, "都会の方の身なりを\n勉強した方が\n良いのでしょうか？", 1, 2130, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105902, "わたくしの居場所は\n主さまのお傍です\nずっと、ずっと…♪", 1, 2131, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105902, "主さまの隣には\nわたくしが…\nわたくしの隣には\n主さまが…幸せです", 1, 2132, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105902, "主さま、なにか\nしてほしいことは\nありませんか？\n欲しいものは？", 1, 2133, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105902, "わたくし、背が伸び\nましたでしょうか？\n…そうですか", 1, 2134, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105902, "お出かけですか？\nわたくしもお供\nいたします", 1, 2135, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105902, "ひゃん！\n主さま…\nいじわるです", 1, 2136, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105902, "主さま、なにか御用は\nありませんか？\nわたくし、主さまの\nお役にたちたいのです", 1, 2137, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105902, "主さまの優しさに\nわたくしはいつも\n癒されています…", 1, 2138, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105902, "おはようございます♪\n主さまの可愛らしい\n寝顔、堪能致しました", 1, 2139, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105902, "のんびりした時間帯\nですね…ふぁ～…\nし、失礼致しました", 1, 2140, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105902, "ふむ、都会は夜でも\nこんなにも、にぎやか\nなのですね", 1, 2141, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105902, "主さまに会う前に\n身なりを整えなくては\nいけませんね", 1, 2142, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105902, "このふかふかの優しさ\n主さまのようです", 1, 2143, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105902, "かわいい♪\n主さまにそっくり♪", 1, 2144, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105902, "この心地よさ…\n主さまのようです♪", 1, 2145, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105902, "ここで洗濯物を\n乾かせるでしょうか　", 1, 2146, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (6, 0, 0, 105902, "はぁっはぁっはぁっ\n…すべては\n主さまのため…", 1, 2147, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105902, "都会の味…\nとてもおいしいです\n…もぐもぐもぐ", 1, 2148, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105902, "都会のことを\n学ばないと…\nふむふむ", 1, 2149, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 190801, "ふぁ～…あっ\n見られちゃい\nましたか？", 1, 2151, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 190801, "ギルドハウスの\n使い心地は\nどうですか？", 1, 2152, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 190801, "ご用があれば\nなんでも言って\nくださいね～♪", 1, 2153, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 190801, "ときどきみなさんと\nクエストに行きたいな\nって思っちゃいます", 1, 2154, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 190801, "おはようございます！\nお掃除の方、\nはじめていきますね", 1, 2155, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 190801, "皆さん、お昼ごはんの\nご要望がありましたら\n用意致しますよ♪", 1, 2156, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 190801, "ここは夜でも\nにぎやかですね", 1, 2157, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104701, "…\n少しうとうと\nしてしまった", 1, 2158, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104701, "室内だし兜を脱ぐか\n…いや\nやはりやめておこう", 1, 2159, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104701, "部屋にいると\n鎧が蒸し暑くなるが\n我慢せねば…", 1, 2160, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104701, "仕事が門番だからか\n部屋にいても\nじっとしてしまう…", 1, 2161, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104701, "少年と会話したことを\n思いだすと\n自然と楽しくなるな", 1, 2162, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104701, "少年のことを考えると\n顔がにやけてしまう", 1, 2163, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104701, "少年は今頃\n平和に過ごせて\nいるかな…", 1, 2164, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104701, "ん？　悪いが\n兜は脱がないぞ", 1, 2165, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104701, "私と遊びたいのか？\nそう言われても\n何をしていいやら…", 1, 2166, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104701, "鎧が重くないかって？\n大丈夫。けっこう\n鍛えているからな", 1, 2167, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104701, "少年、寒くはないか？\nいや自分がこうなので\n人が薄着に見えてね", 1, 2168, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104701, "君といると、兜の中が\nいつもより暑いんだ", 1, 2169, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104701, "私としゃべりたい？\n物好きだな\nでも、嬉しいよ", 1, 2170, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104701, "早いな少年\nえ？　私の仕事に\n合わせてくれたのか", 1, 2171, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104701, "昼食の誘い？\n嬉しいけど\n人前では食べないんだ", 1, 2172, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104701, "王宮騎士団の\n日報を書かなければ…\n『何事もなし』、と", 1, 2173, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104701, "鎧を着ているから\n感触が分かりづらい\nのが残念だ…", 1, 2175, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104701, "か、かわいいな…\n私には似合わないが\nぎゅ…ってしよう", 1, 2176, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104701, "良い香りだ…\n普段はどうしても\n鉄くさくなるからな", 1, 2177, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104701, "わかっていたけど\n鎧を着たまま暖炉に\nあたると暑い…", 1, 2178, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104701, "兜を脱がずに食べる\nうん、段々コツが\n掴めてきた…", 1, 2180, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104701, "兜があっても\n意外と本くらいは\n難なく読めるんだ", 1, 2181, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102601, "居心地よくて\nゴロゴロしちゃうよ～\n…いつも通りだね", 1, 2183, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102601, "ドングリの木\n近くに生えてない？", 1, 2184, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102601, "快適な寝床があれば\nそれだけで幸せだねぇ", 1, 2185, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102601, "あたしと一緒に\nだらけてくれる人が\nいそうだねぇ", 1, 2186, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102601, "ここなら牧場よりも\n速く配達が\n届きそうだね～", 1, 2187, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102601, "あんパン買ったし\nあいつにも\n分けてあげよう", 1, 2188, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102601, "あいつと一緒に\nだらけられたら\n最高に幸せだね", 1, 2189, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 102601, "ドングリでも\nつまみながら\n一緒にゲームしようよ", 1, 2190, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102601, "横になりたいから\n枕になっておくれよ～\nあんたあったかいしさ", 1, 2191, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102601, "ドングリあげるから\nあんパン買ってきて～", 1, 2192, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102601, "う～\n疲れちゃったよ～\nおんぶして運んで～", 1, 2193, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 102601, "あんたといると\nリア充って\n感じがするよ", 1, 2194, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 102601, "あんたが一生\n世話してくれたら\n人生安泰なんだけどね", 1, 2195, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 102601, "ふわ～っ…\nおはよう…あたしは\nまだ眠いよ", 1, 2196, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 102601, "そろそろお昼寝でも\nしたい気分だねぇ", 1, 2197, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 102601, "徹夜でゲームするけど\nあんたもどうだい？\nあたしは強いよ", 1, 2198, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 102601, "あ…だめだこれ。\n気持ち良くて\n落ちそう…", 1, 2200, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 102601, "かわいいものに\n囲まれて寝るのも\nよさそうだねぇ…", 1, 2201, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 102601, "心身ともに\nリラックスした状態で\nぐうたら出来そうだね", 1, 2202, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 102601, "ふい～…\nごくらくごくらく…\n暖かいねぇ", 1, 2203, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 102601, "食べてすぐ寝ると\n牛になるって\n本当なのかな？", 1, 2205, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 102601, "おー！　この本なら\nあたしも居眠りせずに\n読めそうだよ", 1, 2206, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106301, "ランドソル…\nおっきな街だなぁ…", 1, 2208, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106301, "ロザリア…\nきっと、いるよね？", 1, 2209, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106301, "最近、弓の練習\nサボっちゃってるな…", 1, 2210, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106301, "今日の私は\n森番じゃなくて\n家番だよ！", 1, 2211, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106301, "ちょっとだけ\n慣れてきたかも？", 1, 2212, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106301, "いつか、\nあの人と一緒に…", 1, 2213, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106301, "…なんか最近、\nあの人のこと\nばっかり…", 1, 2214, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106301, "もしよかったら\n街に出かけない？", 1, 2215, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106301, "この部屋、\n妖精さんも\n気に入るかも！", 1, 2216, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106301, "今日は\nなににしよっか？", 1, 2217, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106301, "今日はどこ行く？", 1, 2218, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106301, "たまには二人で…\nな、なんちゃって！", 1, 2219, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106301, "最近、\nずっと楽しいんだ！\nあなたのおかげかな", 1, 2220, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 106301, "おはよう！\n今日も一緒に\n頑張ろうね！", 1, 2221, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 106301, "お昼はどうする？\nあなたのオススメに\n連れて行って欲しいな", 1, 2222, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 106301, "ランドソルは\n夜でも賑やかだね！", 1, 2223, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106301, "わぁっ！\nすっごく\nふわふわだよ！", 1, 2225, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106301, "かわい～！\nぎゅってしたく\nなっちゃうかも！", 1, 2226, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106301, "くんくん\nなんか…いい匂い？", 1, 2227, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106301, "すっごいなー\nあったかいなー！", 1, 2228, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 106301, "わぁっ、おいしい！\nもっと食べたい\nかも…！", 1, 2230, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 106301, "も、文字は苦手…\nでも、頑張らないと\nだよね…！", 1, 2231, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103601, "にんじんとピーマンを\n食べないでいい方法\nないかな…", 1, 2233, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103601, "カップケーキが\n食べたいな…", 1, 2234, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103601, "予習が終わりました！\nさて、次は復習を\nしないと…", 1, 2235, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103601, "あの人の部屋にいる\n私って…もしかして\nふしんしゃなんじゃ？", 1, 2236, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103601, "男の人は、みんな\nへんたいだと\n聞きました…", 1, 2237, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103601, "あの人に、一緒に\n遊んでほしいけど…\nな、なんていえば？", 1, 2238, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103601, "早く大人になりたいな\n…あの人と同じくらい", 1, 2239, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103601, "ぼ、防犯魔石を\n鳴らしますよ！？", 1, 2240, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103601, "へんたい！？　…\nあ、あなたでしたか～\nやっぱりへんたーい！", 1, 2241, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103601, "これ宿題です\nやっておいて\nくださいね", 1, 2242, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103601, "あ、遅刻ですよ\n待っていたんですから", 1, 2243, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103601, "にらめっこですか？\nあ、あなたの前で変顔\nするのはちょっと…", 1, 2244, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103601, "あの…何歳まで\n子供だと思いますか？", 1, 2245, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103601, "顔を洗って、着替えて\n朝ごはんを食べて、\n歯を磨いて…", 1, 2246, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103601, "て、鉄棒の練習！？　\nほ、他のことを\nしませんか？", 1, 2247, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103601, "ね、眠くないです\n子供ではありません\nから…ふわぁ", 1, 2248, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103601, "ひと休みしよっと\n………うん、\nひと休みおしまい♪", 1, 2250, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103601, "つ、つぶらな瞳には\n弱いんです", 1, 2251, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103601, "リラックスできますね\n集中力も\n上がりそうです", 1, 2252, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103601, "あったかいと\n眠くなっちゃうな…\n勉強しなくちゃなのに", 1, 2253, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103601, "ピーマンもこれくらい\nおいしければ\nいいのに…", 1, 2255, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103601, "この本、むずかしい\n…もっと勉強\nがんばらなくちゃ", 1, 2256, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101501, "今日はみんなに\nなにを読んで\nあげようかしら", 1, 2258, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101501, "子どもたちと同じ\n目線で考えるのが\n大事よね", 1, 2259, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101501, "お天気が良い日は\nみんなでお散歩に\n行きたいわね～", 1, 2260, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101501, "鬼ごっこにかくれんぼ\n…みんなは\nどっちが好きかしら", 1, 2261, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101501, "お遊戯会の配役、\n考え出すと\n大変だわ～", 1, 2262, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101501, "彼は優しいから、\nきっと良い\nお父さんになるわね", 1, 2263, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101501, "彼には私がいないと\n…っていうのは少し\n過保護すぎるかしら？", 1, 2264, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101501, "保育園の行事を\n考えてるんだけど…\n良い案ないかしら？", 1, 2265, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101501, "今度、子どもたちと\nおやつを作るから、\n食べに来てね", 1, 2266, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101501, "よかったら一緒に\n絵本を作らない？", 1, 2267, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101501, "あなたはいつも元気ね\n先生も見習わないと♪", 1, 2268, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101501, "先生は\nずーっと、ずーっと、\nあなたの味方だからね", 1, 2269, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101501, "あなたみたいな\nいい子をお世話できて\n先生は幸せものだわ", 1, 2270, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 101501, "おはよう～\nちゃんと朝ごはんは\n食べた？", 1, 2271, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 101501, "お昼寝の時間ね♪\nあなたも一緒に\n寝ましょうか", 1, 2272, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 101501, "すっかり真っ暗ね。\n今日も一日、\nよくできました♪", 1, 2273, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101501, "ふかふかねぇ♪\n年甲斐もなく、\n飛び跳ねちゃいそう", 1, 2275, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101501, "あらあら、かわいい！\n保育園のみんなに\n見せてあげたいわ", 1, 2276, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101501, "いい香りね～\n保育園にも\n置こうかしら", 1, 2277, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101501, "寒い日は、ここで\n子どもたちに絵本を\n読んであげましょ♪", 1, 2278, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 101501, "美味しい～♪\n遠足に持っていったら\nみんな喜びそう", 1, 2280, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 101501, "みんなが\n楽しめる読み聞かせの\n方法特集♪　素敵ねぇ", 1, 2281, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104401, "世界の前に、\nこの部屋を征服\nしてやろう♪", 1, 2283, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104401, "拠点とはやはり\n斯くあるべきじゃの", 1, 2284, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104401, "眷属の物はわらわの物\n故に、この部屋は\nわらわの部屋じゃな！", 1, 2285, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104401, "永く眠っている間に\n随分と世界は変わった\nようじゃな…", 1, 2286, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104401, "うるさいのう～\nまったく、近頃の\n若いもんは…", 1, 2287, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104401, "あやつが来てから\n動悸が治まらぬ…\n如何ような病で\nあろうかの…", 1, 2288, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104401, "眷属よ、どこに行った\nのじゃ？\nわらわの世話を\n許可するのじゃ", 1, 2289, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 104401, "いずれ世界は\n我が手中に収まる\nふふふ、\n楽しみじゃのう", 1, 2290, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104401, "眷属よ、わらわから\n離れてはならんぞ！\n子供に戻ってしまう\nではないか！", 1, 2291, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104401, "トマトジュースが\n欲しいのじゃ！\n野菜ジュースでは\nダメだぞ！", 1, 2292, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104401, "蚊にくわれたぞ！\nわらわの血を吸う\nとは～！", 1, 2293, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 104401, "こら、頭をなでるな\nお主はこの姿でも\n子供扱いするのか？", 1, 2294, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 104401, "お主が望むなら世界の\n半分をくれてやっても\n良いのじゃぞ？", 1, 2295, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 104401, "朝早くから感心じゃの\nさすがわらわの\n眷属じゃ", 1, 2296, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 104401, "太陽は苦手じゃ…\nこの部屋の中で\n過ごそうぞ", 1, 2297, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 104401, "夜も更けてきた！\nわらわ達の時間は\nこれからじゃ～♪", 1, 2298, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 104401, "よい座り心地じゃ\nわらわの玉座としよう", 1, 2300, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 104401, "可愛いではないか\nわらわの眷属に\nしてやろうかのう", 1, 2301, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 104401, "うむ、良い気持ちじゃ\nこれを作った者、\n褒めてつかわすぞ", 1, 2302, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 104401, "あたたかい…\n身も心も\n浄化されるようじゃ", 1, 2303, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 104401, "吸血鬼のわらわが、\nこのようなもの…\nうまい♪", 1, 2305, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 104401, "このまま大人の姿を\n保つ方法…どこかに\n書いてないかのう", 1, 2306, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105401, "いろんな人がいて\nいろんな服を着てて…\n目の保養になります♪", 1, 2308, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105401, "レイ様への新作\nどんな服にしよう…\n方向性が絞れない～！", 1, 2309, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105401, "忘れないうちに\n隠し糸のメンテナンス\nやっておこうっと！", 1, 2310, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105401, "むっ！　あの人の\nスリーサイズは…\nほほう…なるほど♪", 1, 2311, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105401, "ノゾミさんの\n衣装はああして\nチカさんのは…", 1, 2312, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105401, "私がアイドル…\n…実感ないけど\n精一杯やってみよう", 1, 2313, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105401, "まさか私が\nアイドルになるなんて\n思ってなかったです", 1, 2314, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105401, "なんですかぁ？\n衣装作りで疲れて\n眠いんですぅ…", 1, 2315, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105401, "あーっ！　また\n服がほつれてる…\nしょうがないですね", 1, 2316, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105401, "はっ！　ちょうど今\n新しいデザインが\n思い浮かびました！", 1, 2317, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105401, "暇そうですね？\n時間あるなら仕事\n手伝ってください！", 1, 2318, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105401, "どうしました？\n新しい服が\n欲しくなりましたか？", 1, 2319, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105401, "騎士さんのおかげで\nカルミナでも\n頑張れてますよ！", 1, 2320, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105401, "おはようございます…\nなんで朝って毎日\nやってくるんでしょう", 1, 2321, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105401, "お腹空きましたね\n騎士さんはちゃんと\n御飯食べましたか？", 1, 2322, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105401, "夜の方が\n作業が捗るのは\n私だけですかね？", 1, 2323, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105401, "このふかふか具合\nいい素材を\n使ってますね", 1, 2325, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105401, "小さくて可愛い♪\nそうだ！　レイ様の\nぬいぐるみを…！", 1, 2326, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105401, "こういう時くらい\n何も考えずに\nリラックスしないと♪", 1, 2327, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105401, "炎を使った服って\nカッコ良いかも…\nでも材料がな～…", 1, 2328, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105401, "何をするにも\n食事は大切！\n体力が資本です！", 1, 2330, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105401, "私もいつか\nデザインを\nまとめて本に…", 1, 2331, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107501, "またお魚を\n捕りにいこうかな♪", 1, 2333, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107501, "そろそろみんなも\nお腹が空くころ\nじゃないですか～？", 1, 2334, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107501, "ここなら、\n日焼けの心配は\nありませんね！", 1, 2335, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107501, "お刺身に焼き貝…\nお腹が魚介類を\n求めてますっ！", 1, 2336, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107501, "泳ぐのも食べるのも\n大得意です！", 1, 2337, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107501, "あの人と二人で\n浜辺を散歩…\n素敵ですねぇ", 1, 2338, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107501, "あの人に水着を\n褒めてもらえたら、\nすごく嬉しいなぁ", 1, 2339, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107501, "たまには釣りじゃなく\n一緒に素潜りでも\n行きませんか？", 1, 2340, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107501, "男の子に水着を\n見られるのって\n少し緊張しちゃいます", 1, 2341, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107501, "ギルドハウスの\nみんなとも、\n海に行きたいですね！", 1, 2342, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107501, "あなたの水着、\n私が選んであげます！", 1, 2343, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107501, "泳ぎの練習なら\n付き合いますよ！\n手取り足取り♪", 1, 2344, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107501, "水着姿が好きなら\nいくらでも\n着てあげますよぉ♪", 1, 2345, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 107501, "朝の海も\n幻想的で\nいいですよねぇ～！", 1, 2346, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 107501, "お昼はみんなで、\n海鮮バーベキューとか\nどうでしょう！", 1, 2347, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 107501, "夜の海は星が\nきれいですよ！\n見に行きましょう！", 1, 2348, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107501, "泳ぎ疲れたので\nちょっと休憩です～", 1, 2350, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107501, "あなたにも、\n可愛い水着を\n着せてあげたいです", 1, 2351, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107501, "なんだか、\nリゾート気分です…", 1, 2352, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107501, "ふい～、泳ぎで冷えた\n身体を暖めましょう～", 1, 2353, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 107501, "おいひ～です～\nこれ、海水浴にも\n持っていきたいですね", 1, 2355, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 107501, "南国のフルーツ図鑑\n…じゅるり…", 1, 2356, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 107501, "わ～♪　おいしそ…\nか、かわいいですね～", 1, 2358, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 107501, "これくらい大きな貝…\n食べたいな～\n食べたいな～～", 1, 2359, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107601, "この格好で\n部屋をうろつくのは\n不思議な感じがします", 1, 2360, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107601, "南国の果実を使って\n主さまにデザートを\nお作りしましょう", 1, 2361, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107601, "せっかくの水着ですし\nこれから定期的に\n海水浴に行きたいです", 1, 2362, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107601, "海の生き物も、\n森の生き物に負けず\n可愛かったです…", 1, 2363, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107601, "クロールの練習なら、\n部屋でもできそうです", 1, 2364, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107601, "主さまに、水着姿を\n褒めていただけた…\nえへへ♪", 1, 2365, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107601, "水着で主さまの\nお世話をしたら、\n喜びますでしょうか…", 1, 2366, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107601, "お疲れでしたら\nわたくしの膝を\nお使いくださいね", 1, 2367, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107601, "主さま、海の家で\nつかうおこづかいを\n渡しておきますね", 1, 2368, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107601, "海の美しさは、\n森とはちがう\n魅力がありました", 1, 2369, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107601, "この髪飾りですか？\n造花ですから\n水の中も平気です", 1, 2370, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107601, "水着でくっついて\n眠ると、いつもより\n密着して暖かいです", 1, 2371, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107601, "はじめての海が、\n主さまと一緒で\n幸せです♪", 1, 2372, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 107601, "おはようございます。\n泳ぎに行く準備は\n万端です", 1, 2373, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 107601, "午後は泳ぐのをやめて\nスイカ割りなど\nいかがでしょう？", 1, 2374, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 107601, "夜も更けてきました…\n波の音を聞きながら\n少しお話しませんか？", 1, 2375, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107601, "あぅ…この家具には、\n人をダメにする魔法が\nかけられています…", 1, 2377, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107601, "ふふっ♪\nあなたも、海水浴に\n行きますか？", 1, 2378, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107601, "この香り…\nきっと主さまも\n気に入ります", 1, 2379, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107601, "これは暖かいです…\n後ほど主さまと一緒に\nあたりましょう", 1, 2380, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 107601, "泳いだ後の食事は\n一段と美味しく\n感じます", 1, 2382, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 107601, "深海魚の生態…\nこれは主さまにも\nお伝えしなくては…", 1, 2383, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 107601, "顔立ちが、主さまに\n似ております…\nかわいらしい♪", 1, 2385, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 107601, "ふかふかですね\n主さまのようです♪", 1, 2386, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107701, "お掃除、お掃除\nふんふふ～ん☆\nあ、聞かれてました？", 1, 2387, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107701, "日焼けには回復魔法が\n効きますよ。どうして\n逃げるんですか～！", 1, 2388, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107701, "今日はドジを\nしませんように！", 1, 2389, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107701, "接客業を極めれば\nメイドとして\n成長できるかも？", 1, 2390, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107701, "水着って\n動きやすいんですけど\n人の目が気になります", 1, 2391, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107701, "あの人の気持ちを知る\n魔法とかあれば…\nな、なんでもないです", 1, 2392, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107701, "水着でご奉仕したら、\nあの人は喜ぶかな？", 1, 2393, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107701, "暑いのでしたら、\n私が魔法で涼しく…\nえ？　いらない？", 1, 2394, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107701, "何か御用ですか？\n特にないのでしたら、\nお話ししましょう♪", 1, 2395, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107701, "水着姿でも\nメイドはメイドです\nご奉仕しますよ！", 1, 2396, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107701, "ひゃっ！　あ、危うく\n転ぶところでした…\n…きゃうっ！", 1, 2397, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107701, "どうかしましたか？\nあなたの御用なら、\n喜んでお受けしますよ", 1, 2398, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107701, "水着姿をあなたに\n見られると、\nドキドキしちゃいます", 1, 2399, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 107701, "朝は暑くてやる気が\n出ない？　でしたら、\nえぇい、元気になぁれ", 1, 2400, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 107701, "お腹がぺこぺこなら、\nお昼は海の家で\n食べましょう♪", 1, 2401, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 107701, "夜の浜辺でお散歩も\nいいですね。たくさん\n転びそうですけど…", 1, 2402, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107701, "休憩しながら、\nお掃除すれば\n一石二鳥です！", 1, 2404, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107701, "きれいにすれば\nかわいさも倍増\nですよ～♪", 1, 2405, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107701, "素敵な香りです～\n救護院に置けば、\nみんな喜ぶかも～", 1, 2406, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107701, "あったかいですね…\nはっ！　いけません！\nメイドがサボるなんて", 1, 2407, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 107701, "料理のできない\nメイドなんて…\nはぐはぐ、おいしい♪", 1, 2409, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 107701, "水着姿で接客をすると\n売り上げが倍になる\n…本当ですか、これ", 1, 2410, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 107701, "えへへ♪\n南の島まで\n乗せてください！", 1, 2412, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 107701, "なんだか、\n人魚姫に\nなった気分です♪", 1, 2413, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107801, "みんなで過ごす\n休日っていいわよね…\nずっと続かないかな", 1, 2414, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107801, "海に浮輪でぷかぷか\n浮かぶのって\n癖になりそう", 1, 2415, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107801, "ビーチにいると\n日焼けが心配だわ", 1, 2416, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107801, "水着姿って楽よね。\n室内だとちょっと\n落ち着かないけど…", 1, 2417, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107801, "お、泳ぎの練習なんて\nしないからね！", 1, 2418, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107801, "楽しすぎて立場を\n忘れそう…あんたの\nせいなんだから！", 1, 2419, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107801, "みんなで遊んで\n美味しいもの食べて\nほんっとに楽しいっ♪", 1, 2420, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107801, "マジであっついわね…\nよっし！　海に入って\n涼みましょ！", 1, 2421, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107801, "シャワー浴びたばかり\nなのに…また、遊び\nたくなってきたわ！", 1, 2422, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107801, "遊び足りないって？\nしょうがないわね…\n付き合ってあげるわ♪", 1, 2423, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107801, "一応伝えておくけど\n猫って水が苦手なの！\nわかった！？", 1, 2424, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107801, "あたしと話したいの？\nふふ。\nまぁ、いいわよ。\nほら隣に座りなさい", 1, 2425, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107801, "こんなに楽しいの\n初めてかも…\nずっと続けばいいのに", 1, 2426, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 107801, "は、早起きしたのは、\n楽しみで眠れなかった\nからじゃないわよ！", 1, 2427, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 107801, "昼食後のストレッチ\n終わり！　さあ、\nビーチが待ってるわ！", 1, 2428, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 107801, "夜の海に散歩に\n行こうかな～\nあんたも来なさいよ☆", 1, 2429, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107801, "海ではしゃぎすぎて\n疲れたわ…ちょっと\n休もうかしら？", 1, 2431, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107801, "こんな魔物がいたら\n操るのも楽しいかも", 1, 2432, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107801, "にゃう～♪\nまるで南国にいる\n気分ね♪", 1, 2433, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107801, "あったかぁい♪\n身体が冷えてきたから\n丁度いいわ！", 1, 2434, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 107801, "運動後のご飯って\nなんでこんなに\n美味しいんだろ♪", 1, 2436, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 107801, "どんなカナヅチでも、\n絶対必ず泳げる方法、\n書いてないかしら？", 1, 2437, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 107801, "あんたもあたしが\n操ってあげる！\nなんてね～♪", 1, 2439, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 107801, "リゾートリゾートっ\nのんびりしましょ♪", 1, 2440, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108001, "ここなら思う存分\nデスクワークに\n励めるわね！", 1, 2441, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108001, "武器のメンテナンスも\nしっかりしないと\n最高効率は望めないわ", 1, 2442, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108001, "準備をしっかり整えて\nもっともっと\n稼がなくちゃね！", 1, 2443, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108001, "少し休んだら\n次のバイトに\n行ってくるわね！", 1, 2444, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108001, "この格好でここに\nいるのって\n気恥ずかしいわね…", 1, 2445, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108001, "キミが望むなら\n今日のバイト\n休んでもいいわよ？", 1, 2446, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108001, "キミだけね…\n私のことをここまで\n理解してくれるのは", 1, 2447, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108001, "普段と違う格好\nだからって\nそんなに見ないでよ…", 1, 2448, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108001, "キミを立派な\nバイト戦士にするのが\n師匠としての役目よね", 1, 2449, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108001, "そろそろ次のバイトに\n行かないと…\nキミも一緒に来る？", 1, 2450, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108001, "効率は大事だけど\nゆっくりするのも\n悪くないわね", 1, 2451, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108001, "一緒の時間が楽しくて\nバイトのことを\n忘れちゃいそうね", 1, 2452, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108001, "キミと一緒なら\nどんなミッションも\n楽勝ね！", 1, 2453, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 108001, "おはよう、いい朝ね！\n今日もめいっぱい\n稼ぐわよ！", 1, 2454, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 108001, "もうお昼だったのね…\n内職のバイトに\n集中しすぎちゃったわ", 1, 2455, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 108001, "スケジュールは、と…\nこういう管理の徹底が\n効率を生み出すのよ♪", 1, 2456, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108001, "しっかり働いて休む…\n最高効率にまた一歩\n近づいたわね", 1, 2458, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108001, "なごんじゃうわね…\nよぉし、次のバイトも\n頑張らないと！", 1, 2459, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108001, "いい香りね…\n働く意欲が\n湧いてくるわ", 1, 2460, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108001, "身体が冷えたから\n助かるわ…冷えは\n女の天敵だもの", 1, 2461, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 108001, "海のバイトは\n体力勝負よ\nエネルギーはしっかり\n摂取しないとね！", 1, 2463, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 108001, "座学も大切ね\nもっと知識を\n身につけないと…", 1, 2464, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 108001, "海の楽園みたいね…\n今だけは効率を\n忘れちゃいそう…", 1, 2466, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 108001, "落ち着くわ…\n海とひとつになった\n気分ね！", 1, 2467, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107901, "ちょっと休憩にゃ～\n潮風は体に\n良くないにゃ～", 1, 2468, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107901, "猫グッズで\n海の家を飾りたいにゃ", 1, 2469, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107901, "海の幸は美味しいけど\n水はイヤだにゃ…", 1, 2470, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107901, "開店にゃ！\nこのたい焼きなら\nきっと大繁盛にゃ！", 1, 2471, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107901, "海と言えば水着にゃ\n似合ってるかにゃ？", 1, 2472, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107901, "キミが頼むなら\n手伝ってあげても\nいいにゃ", 1, 2473, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107901, "あたしの宝は\nここにあるにゃ～", 1, 2474, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107901, "にゃ？\nそんなに見つめても\n何も出ないにゃ", 1, 2475, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107901, "今からキミを\nあたしの相棒に\n任命するにゃ！", 1, 2476, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107901, "これから\n開店の準備にゃ\nキミも手伝って\nくれるのかにゃ？", 1, 2477, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107901, "海の家で出す\n新しいメニューを\n考え中なのにゃ～", 1, 2478, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107901, "キミと一緒なら\n売上げナンバーワンに\nなれるにゃ！", 1, 2479, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107901, "キミと一緒にいると\n楽しくて、もっと\nいたくなるにゃ", 1, 2480, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 107901, "おはようにゃ！\n今日も頑張って\nいくにゃー！", 1, 2481, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 107901, "お腹すいたにゃ～…\nお魚、ちょっとだけ、\nつまみ食いするにゃ", 1, 2482, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 107901, "明日の準備は万端にゃ\nいつでも\n出発できるにゃ", 1, 2483, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107901, "このふかふか具合\nまるで子猫の\n毛並みのようにゃ～", 1, 2485, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107901, "にゃにゃ～\nなんてかわいさにゃ～", 1, 2486, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107901, "この匂い…\nもう少し香ばしいと\nいいかもしれないにゃ", 1, 2487, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107901, "にゃ～…\nあったかくて\nぬくぬくだにゃ～", 1, 2488, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 107901, "働くにはまず体力にゃ\nしっかり食べて\nスタミナアップ\nだにゃ!", 1, 2490, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 107901, "ふむふむ…\nここにお宝が\n眠ってるんだにゃ", 1, 2491, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 107901, "シードレイクにゃ！\n本物は怖かったけど、\nぬいぐるみなら\nかわいいにゃ～", 1, 2493, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 107901, "貝なのにふかふかの\nクッションにゃ！\n屋台の前にも\n置いてみたいにゃあ", 1, 2494, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103701, "人の家って、道場とは\n違った香りがするね", 1, 2495, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103701, "ちょっと退屈だね…\nあの人をからかいに\n行こうかな♪", 1, 2496, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103701, "普通の女の子みたいに\n遊びたいって思う\nこともあるんだよね", 1, 2497, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103701, "剣の手入れを怠らない\nようにしないと。\nいざという時、困るし", 1, 2498, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103701, "もっと\n上手く立ち回って\n出世したいな", 1, 2499, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103701, "おかえりなさい！\nふふ、なんだか奥さん\nになった気分だよ♪", 1, 2500, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103701, "恥ずかしくて、あなた\nを見られないよ…\n私らしくないなぁ…", 1, 2501, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 103701, "私、お話好きなんだ。\nどうせなら楽しい話を\nしたいな♪", 1, 2502, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103701, "女の子っぽい子と、\n男勝りな子、あなたは\nどっちが好き？", 1, 2503, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103701, "そろそろ見回りの\n時間かな。あなたも\n一緒に行く？", 1, 2504, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103701, "構って欲しいのかな？\nそれじゃ一緒に\nランニングに行こうか", 1, 2505, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 103701, "二人で出かけるなら、\n手を繋がないとね。\nあはは、冗談冗談♪", 1, 2506, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 103701, "ねえ、あなた♪\nおや、顔が真っ赤だ！\n…ふふ、私も、かな？", 1, 2507, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 103701, "私は朝から鍛錬だよ。\n慣れてるけど大変だ。\nお互いに頑張ろうね", 1, 2508, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 103701, "鍛錬でお腹ぺこぺこ。\nお昼ごはんが\n楽しみだね", 1, 2509, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 103701, "今日も一日見回りと\n鍛錬を頑張ったよ。\nあなたもお疲れさま", 1, 2510, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 103701, "いざという時のため\nにも、休憩は大事だね", 1, 2512, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 103701, "こういうものには\n今まで\n縁がなかったけど。\nうん、可愛いね♪", 1, 2513, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 103701, "精神統一に通じる\nものがあるね。\nふぅ、癒されるなぁ", 1, 2514, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 103701, "あったかいなぁ。\nたまにはのんびり\nしようか", 1, 2515, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 103701, "鍛錬後はすごくお腹が\n空くんだよね。\nう～ん、おいしい♪", 1, 2517, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 103701, "剣術書だけで強くなる\nには飛びぬけた才能と\n努力が必要だよね", 1, 2518, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100501, "正義の刃で\n悪を挫く！\n…キマったッス♪", 1, 2520, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100501, "ヒーローの\n秘密特訓を\n始めるッスよ～！", 1, 2521, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100501, "正義の味方目指して\n今日も明日も\n全力ッスよ♪", 1, 2522, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100501, "一日一善！\nヒーローだったら\n当たり前ッス♪", 1, 2523, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100501, "どうッスか\nこのポーズ！\nイイ感じッスか？", 1, 2524, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100501, "た、たまには\nよく頑張ったねって\n褒めてほしいッス…", 1, 2525, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100501, "自分を信じてくれる\nあんたと一緒なら\n何でもやれるッス！", 1, 2526, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 100501, "おつかれッス♪\n冒険の準備は\nばっちりッスよ！", 1, 2527, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100501, "どうしたんッスか？\n…はっ！　もしや敵が\n迫ってるんスかっ！？", 1, 2528, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100501, "べ、別に\n頭を撫でてほしいとか\n思ってないッスよ？", 1, 2529, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100501, "スーパーヒーロー\nトラタイガーは\n絶対無敵ッス！", 1, 2530, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 100501, "あんたと自分で\n正義の心を燃やして\n悪を一網打尽ッス！", 1, 2531, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 100501, "あんたと一緒だと\n幸せな気持ちに\nなるんッス…♪", 1, 2532, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 100501, "お、早いッスね～♪\n今日も一日一善\nよろしくッス！", 1, 2533, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 100501, "一休みしながら\nヒーローについて\n語り合うッスよ～！", 1, 2534, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 100501, "夜のパトロールで\n一緒に平和を\n守るッス！", 1, 2535, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 100501, "ふぅぅ…\n落ち着くッスね\n戦士の休息ッス♪", 1, 2537, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 100501, "くぅぅ、可愛いッス…\nヒーローが守るべき\n愛らしさッスね！", 1, 2538, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 100501, "なんスかこの香り？\nすぅぅ…ふぅぅ…\n力がみなぎるッス！", 1, 2539, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 100501, "優しい光ッスね…\nヒーローの優しさ\nそのものッス！", 1, 2540, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 100501, "あむあむあむ…\nんぅう、元気が溢れて\n止まらないッス！", 1, 2542, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 100501, "トラタイガーのお話は\n笑いあり涙ありで\n超おすすめッス！", 1, 2543, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108101, "ハッピーハロウィン\nお父さん、お菓子は…\n食べられないね", 1, 2545, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108101, "トリックオアトリート\nお菓子をくれないと\n不吉な占いをします", 1, 2546, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108101, "ふふ、あなたの後ろで\n霊のみなさんが\n踊り始めました", 1, 2547, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108101, "霊感がいつも以上に\n冴えているので\n占いの調子も良好です", 1, 2548, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108101, "父はハロウィンが\n大好きだそうです\n大胆な格好をする子が\n多いので…", 1, 2549, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108101, "あなたは霊たちにも\n大人気ですよね\n私も見てほしいな…", 1, 2550, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108101, "ハロウィンの賑やかさ\n今は好きです…\nあなたといられるので", 1, 2551, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108101, "霊のみなさんとの\nダンスパーティー…\n楽しみです", 1, 2552, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108101, "お父さん、元気だね\nえ？　ここは色んな\n女の子に会えて幸せ？", 1, 2553, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108101, "ハロウィンのお菓子\n食べすぎちゃったな…\nでも、美味しかった…", 1, 2554, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108101, "あなたの女難の相…\n悪化していますね\nお祓いが必要かも", 1, 2555, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108101, "あの、霊のみなさんが\nあなたと踊りたいと…\nえっと、私も…", 1, 2556, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108101, "あなたと手を繋ぐと\n胸が苦しくなります\nでも、繋ぎたいな…", 1, 2557, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 108101, "ハッピーハロウィン…\n父が元気なので\n今日の運気は良好です", 1, 2558, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 108101, "父はお昼寝中です\n本当に寝ているかは\n私にも分かりませんが", 1, 2559, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 108101, "お父さん、\nもう眠っちゃったの？\n…眠れるんだったっけ", 1, 2560, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108101, "落ち着きますね…\n霊のみなさんも\n喜んでいます", 1, 2562, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108101, "つん、つん。ふふっ♪\n癒やされます…", 1, 2563, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108101, "精神が研ぎ澄まされる\n香りですね…\n占いが捗ります", 1, 2564, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108101, "あったかいね\nお父さん…\nえ？　分からない？", 1, 2565, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 108101, "霊のみなさんは\n食べられないので…\n私がいただきます", 1, 2567, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 108101, "お父さんを元に戻す\n手掛かり…\n書かれていないかな？", 1, 2568, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 108101, "わぁ、お菓子が沢山…", 1, 2570, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108201, "無料でプリンが\nもらえるなんて、\nハロウィンはすごいの", 1, 2572, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108201, "生きてるときも、\nミヤコはハロウィンが\n好きだった気がするの", 1, 2573, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108201, "みんなに仮装を\n見せつけて、\nプリンをもらうの～", 1, 2574, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108201, "この仮装で、\nみんなに恐怖を\n教えてやるの！", 1, 2575, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108201, "アオーン、アオーン！\nミヤコ、狼女に\nなりきってるの～", 1, 2576, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108201, "アイツに秘密で、\nパーティーの\n飾り付けをするの♪", 1, 2577, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108201, "アイツが狼男の\n仮装をすれば、\nおそろいなの～", 1, 2578, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108201, "プリンをくれなきゃ、\nオマエをカボチャに\nしてやるの～", 1, 2579, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108201, "ハロウィンは幽霊でも\n参加できるから\n楽しいの～！", 1, 2580, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108201, "プリンがないなら\nカボチャプリンを\n食べればいいの！", 1, 2581, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108201, "プリンの可能性は\n無限大なの～", 1, 2582, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108201, "オマエの隣で\nプリンを食べると、\nいつもより美味しいの", 1, 2583, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108201, "オマエが死んでも、\nミヤコはずーっと\n一緒にいてやるの～", 1, 2584, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 108201, "朝ごはんは\nカボチャプリンなの！\nハロウィンっぽいの～", 1, 2585, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 108201, "ランチは豪華に\nプリンアラモードなの", 1, 2586, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 108201, "夜は幽霊たちと\nハロウィンするの！\nオマエも来るの！", 1, 2587, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108201, "幽霊にも休息は\n必要なの！\nプリンもってこいなの", 1, 2589, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108201, "オマエもミヤコの\nパーティーに\n招待してあげるの～", 1, 2590, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108201, "癒されるの～\nまるで天国なの～", 1, 2591, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108201, "ここでなら、\n焼きプリンが\n作れるの！", 1, 2592, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 108201, "腹が減っては\nハロウィンは\nできぬなの！", 1, 2594, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 108201, "丸ごとカボチャ\nプリン特集！？\n美味しそうなの～♪", 1, 2595, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 108201, "お菓子がたくさんで、\nミヤコはとっても\nハッピーなの♪", 1, 2597, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108301, "魔女以外にも、\n色々な仮装を\nしてみたいわね", 1, 2599, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108301, "チョコやアメよりも、\nアタシに相応しい\nスイーツがあるはず…", 1, 2600, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108301, "お菓子の食べ過ぎは、\nお母さんに\n怒られるかも…", 1, 2601, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108301, "この格好で学院に\n行ったら、\nみんな驚くかしら", 1, 2602, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108301, "大人のレディは、\nどういうハロウィンを\n過ごすべきかしら…？", 1, 2603, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108301, "お菓子、アイツの分も\nとっといてあげよ！\n大人の余裕ってやつね", 1, 2604, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108301, "アイツを魅了する\n魔法とかないかしら？", 1, 2605, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108301, "おしゃれスイーツを\nくれなきゃ\nイタズラするわよ！", 1, 2606, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108301, "アタシの大人センスで\nアンタに似合う仮装を\n選んであげるわ！", 1, 2607, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108301, "不気味よねぇ…\nジャックオーランタン\nこ、怖くないわよ？", 1, 2608, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108301, "ハロウィンが楽しくて\n宿題やり忘れてたわ…\nて、手伝って～！", 1, 2609, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108301, "お菓子をもらうのも\nアンタと一緒なら\n恥ずかしくないわ！", 1, 2610, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108301, "アタシのエスコートを\n任せられるのは、\nアンタだけなんだから", 1, 2611, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 108301, "朝食はオシャレに\nグリーンスムージー！\n…に、にがぁ", 1, 2612, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 108301, "昼下がりはカフェで\n優雅なティータイム\n…憧れるわ～", 1, 2613, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 108301, "ハロウィンナイトの\nはじまりよ！\n街に繰り出しましょ！", 1, 2614, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108301, "あう～、お菓子の\n食べすぎで\n動けな～い", 1, 2616, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108301, "アンタも仮装する？\nいまよりもーっと\nかわいくなるわよ！", 1, 2617, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108301, "大人な香りに\n包まれてる～♪", 1, 2618, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108301, "ポカポカね～♪\n…ああー！\nチョコが溶けてる！", 1, 2619, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 108301, "おいしい～♪\n…じゃなくて！\nび、美味ですわ～", 1, 2621, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 108301, "この服かわい～♪\nって、なにこの値段！\nお小遣い何年分よ！？", 1, 2622, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 108301, "お、お菓子なんかで\n喜ぶほど、子供じゃ…", 1, 2624, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107101, "次はどんな策で団長を\nからかってみようか…", 1, 2626, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107101, "む？\n血の臭いがするな\n祭りの時間か☆", 1, 2627, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107101, "平和に飼い殺される\n前に、混沌の愛を\n育みたいね", 1, 2628, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107101, "本気を出した団長と\nいつか手加減なしに\n殺り合いたいな", 1, 2629, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107101, "おばさん呼ばわりする\n新米騎士の小娘を\n懲らしめてやろうか", 1, 2630, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107101, "胸を触られるまで\n誓約のことなど\nすっかり忘れていたな", 1, 2631, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107101, "まさかこのワタシが\nプロポーズするとはな\n…笑えるじゃないか", 1, 2632, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107101, "面白い暇つぶしを\n思いついたんだ\n戯れに付き合え☆", 1, 2633, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107101, "退屈で死にそうだ\n愉しい話題を\nワタシに提供してくれ", 1, 2634, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107101, "なんだ、暇なのか？\n心躍ることでも探しに\n出かけようではないか", 1, 2635, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107101, "このワタシを愉快に\nさせてくれる事でも\nあったのか？", 1, 2636, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107101, "ワタシに求婚した男の\n数だと？\nいちいち覚えてないな", 1, 2637, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107101, "最強無敵の女と\n結婚できるんだ\n誇りに思えよ☆", 1, 2638, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 107101, "なにか愉快なことが\n起こりそうな朝だ☆\nワクワクするね", 1, 2639, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 107101, "うーむ、腹がすいたな\n愉快なことで\nこの空腹を満たしたい", 1, 2640, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 107101, "これからは大人の\n時間だ。愉しみ方を\n知りたいのかい？", 1, 2641, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107101, "このワタシとした事が\n穏やかな気分に\nなってしまうとは…", 1, 2643, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107101, "可愛い姿をしたものが\n惨たらしい\n醜態を晒すのを\n想像してしまう", 1, 2644, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107101, "悪くはない香りだ\nだが、ワタシは血の\n臭いを嗅ぎたいぞ☆", 1, 2645, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107101, "ぬるい、ぬるすぎる！\n地獄の業火には\nほど遠い熱さだ！", 1, 2646, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 107101, "ワタシの腕前に\n勝るとも劣らない\nなかなかの味だ", 1, 2648, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 107101, "意中の男性を絶対に\n落とす方法…？\nく、下らんっ！", 1, 2649, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105601, "明日はあの釣り場に\n行くとしようかな\nあの子も誘ってみるか", 1, 2651, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105601, "ぷはーっ！\n体を動かしたあとの\n一杯は格別だね", 1, 2652, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105601, "天気が良いと\n昼寝か釣りかで\n迷っちまうな…", 1, 2653, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105601, "大物が釣れたときの\n快感は、他じゃ\n味わえないね", 1, 2654, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105601, "ふあぁ～…\n昼寝でもするか", 1, 2655, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105601, "ふふっ…\nあの子、見た目より\n根性あるんだね", 1, 2656, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105601, "あの子が強くなるには\nどんな魚を食べさせて\nあげればいいのやら…", 1, 2657, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105601, "姉御って呼ぶんじゃ\nないよ。からかうと\nタダじゃおかないよ", 1, 2658, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105601, "釣りのコツ？\n何事も経験が大事だ\n今から行くかい？", 1, 2659, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105601, "どうしたのさ？\n助太刀が必要かい？\nアタシにまかせときな", 1, 2660, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105601, "困ってる人がいたら\nまた力を貸しておくれ\n頼りにしてるよ", 1, 2661, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105601, "なにか困りごとかい？\nどうやら昼寝は\nまた今度だね", 1, 2662, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105601, "よしよし！\nお人好しのお前さんを\n労ってあげるよ", 1, 2663, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105601, "おはよう、お前さん\n魚屋は早起きでなきゃ\nつとまらないよ？", 1, 2664, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105601, "昼飯かい？　今朝\n仕入れた魚があるんだ\n食べてみるかい？", 1, 2665, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105601, "夜釣りにぴったりの\n月が出てるよ\nいい釣りが出来そうだ", 1, 2666, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105601, "コイツに座って\nのんびり釣りが\nできりゃいいのにな", 1, 2668, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105601, "よしよし…♪\nおっと、かわいい物も\nつい抱きしめちまうな", 1, 2669, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105601, "これで魚の臭いを\n消せれば、猫も\n寄らなくなるのかね", 1, 2670, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105601, "冬場の釣りも\nこれくらい暖かけりゃ\nいいんだけどね", 1, 2671, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105601, "腹が減っては\n釣りもできぬ、ってな\nまずは腹ごしらえだ", 1, 2673, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105601, "へぇ…こんな魚が\nいたとは…いつか\n釣りたいもんだね", 1, 2674, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101301, "流石に室内で\n魔法ぶっぱしたら\n怒られるよねぇ", 1, 2676, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101301, "可愛い女の子が\nたくさんいる！\nここは楽園か！？", 1, 2677, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101301, "みんなに漫画とか\n勧めてみようかな！\n布教用のがあるし！", 1, 2678, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101301, "わ、私一人の\n部屋じゃないから\n色々自重せねば！", 1, 2679, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101301, "「ぬるぽ」\n…くっ、\n誰も反応なしか！", 1, 2680, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101301, "次はどの漫画を\n貸そうかなー♪", 1, 2681, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101301, "次の定期巡回で\nとっておきの店に\n連れてってあげよう！", 1, 2682, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101301, "君に似合いそうな\n衣装を買ったよ！\n君もいざ魔法少女に！", 1, 2683, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101301, "さあ！\n暇なら定期巡回に\nレッツラゴー！", 1, 2684, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101301, "戦闘だけじゃなくて、\n日常にも役立つ\n魔法を習得したいね！", 1, 2685, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101301, "新魔法思いついた！\n私って天才！？", 1, 2686, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101301, "決め台詞は超大事！\n君の決め台詞も\n私が考えてあげよう！", 1, 2687, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101301, "私ってもしやリア充？\n…そんなバカな！\n非リア代表のはずが！", 1, 2688, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 101301, "おはよー…\n徹夜で本読んでたから\nまだ眠い～", 1, 2689, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 101301, "昼飯食べて、\n午後の定期巡回も\n頑張りましょー！", 1, 2690, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 101301, "夜からが、\nオタ活本番でしょ！", 1, 2691, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101301, "ふいー！\nくつろいで本を読む！\n最高だねー！", 1, 2693, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101301, "君、可愛いね～♪\n魔法少女の\n使い魔みたい！", 1, 2694, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101301, "良い匂いに\n釣られてしまった…\nまさか、孔明の罠！？", 1, 2695, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101301, "暖かいけど、\nここで本を読むのは\nちょっち危ないかな", 1, 2696, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 101301, "ウマ―！\nこれの詳細きぼんぬ！", 1, 2698, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 101301, "こ、古代語の本！？\n現代語でおk…", 1, 2699, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 190901, "ふふふっ♪\n次はどんな悪戯を\nしてやろうかしら♪", 1, 2701, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 190901, "幻影湖の霧を吸った\n人間たちの\nリアクションって、\nサイコーよねぇ～♪", 1, 2702, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 190901, "室内だけとはいえ…\n自由に動けるって、\nやっぱりいいわ～♪", 1, 2703, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 190901, "改めて見ると良い\n部屋ね…悪戯とか\nしやすそうだしぃ♪", 1, 2704, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 190901, "私を封じていた部屋\nなのに…急に居心地が\n良くなったわね♪", 1, 2705, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 190901, "あんたの頭、座り心地\n良いわね…私専用の\n椅子にしてあげる♪", 1, 2706, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 190901, "外は大変みたいねぇ…\nまあ、私は好き勝手に\nやらせてもらうわ♪", 1, 2707, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 190901, "顔に落書された？\n誰か知らないけど、\n悪いことする奴も\nいるものねぇ～♪", 1, 2708, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 190901, "フィオのやつ…\nどこをウロチョロ\nしてるのかしら？", 1, 2709, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 190901, "人間の朝は大変ねぇ…\n私はその様を見ながら\nゆっくりするわね♪", 1, 2710, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 190901, "う～私も、お日様を\n浴びながら、お外を\nお散歩したーい！", 1, 2711, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 190901, "この暗さ…閉じ込め\nられていた時を\n思い出すわね…", 1, 2712, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 190901, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 1, 2714, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 190901, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 1, 2715, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 190901, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 1, 2716, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 190901, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 1, 2717, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 190901, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 1, 2719, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 190901, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 1, 2720, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108401, "メリークリスマス！\n歌で幸せを\n届けます…！", 1, 2722, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108401, "あなたと出会えたのは\n偶然ではなくて…\nクリスマスの奇跡だと\n思うんです", 1, 2723, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108401, "クリスマスは華やかで\nつい子供のように\nはしゃいでしまいます", 1, 2724, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108401, "曲が書けるのは\nあなたが側に\nいてくれるからです…", 1, 2725, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108401, "感動を与えられる歌…\nあなたと一緒になら\n生み出せるはず…", 1, 2726, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108401, "クリスマスの幸せを\nあなたと\n感じ合えたらな…", 1, 2727, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108401, "あなたの幸せを願った\nクリスマスソング…\nいつか披露しますね", 1, 2728, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108401, "みなさんの笑顔を\n守りたいから…\n私は歌い続けます", 1, 2729, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108401, "歌を届ける\nサンタさんに\nなりたいな…", 1, 2730, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108401, "お暇でしたら\nクリスマスのお祝いを\nしませんか？", 1, 2731, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108401, "ステージに立つ前に\n喉を整えないと…\n発声練習は念入りに…", 1, 2732, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108401, "まわりはかわいい子\nばっかりだけど…\nこの服なら\nアピールになるかな？", 1, 2733, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108401, "歌でならいくらでも\nあの人への気持ちを\n込められるのに…", 1, 2734, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 108401, "今日も一日\n幸せな歌で世界が\n包まれますように…", 1, 2735, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 108401, "お茶を飲みながら\n好きな音楽を\n語り合いませんか？", 1, 2736, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 108401, "ふふ、寝かしつけの\n子守唄を\nお望みですか？", 1, 2737, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108401, "ふかふかですね\n心地良すぎて\n眠ってしまいそう…", 1, 2739, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108401, "素敵…\n優しい歌みたいに\n気持ちが落ち着く…", 1, 2740, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108401, "インスピレーションが\n溢れてくる…\nいい曲が書けそう…", 1, 2741, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108401, "この光みたいに\n灯火になるような\n歌を歌いたい…", 1, 2742, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 108401, "喉の健康のためにも\n食事はしっかり\nとるようにしています", 1, 2744, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 108401, "物語には\n曲作りのヒントが\n詰まっているんです", 1, 2745, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 108401, "絵が飛び出してくる\nなんて…精霊が宿って\nいるのでしょうか？", 1, 2747, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 108401, "か、可愛い…♪\nちょっとだけ触っても\nいいですよね…？", 1, 2748, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108501, "クリスマスでも、\n演劇の練習は\nしっかりやらないと！", 1, 2749, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108501, "あうぅ…\nクリスマスの準備が\n終わらないよぅ…", 1, 2750, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108501, "アヤネちゃんが\n摘み食いしないように\n見ておかないと…", 1, 2751, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108501, "サンタさんみたいに、\n皆に笑顔で接する…\nうぅ…私には無理ぃ…", 1, 2752, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108501, "新しいお洋服～♪\nスズメお姉ちゃんに\nお礼をいわないと♪", 1, 2753, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108501, "お兄ちゃんの為に、\nプレゼントを\n用意しないと♪", 1, 2754, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108501, "クリスマス…\n恋人の日…ひゃわっ！\nわ、私ってば何を…！", 1, 2755, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108501, "メリー\nクリスマスです、\nお兄ちゃん♪", 1, 2756, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108501, "そ、そんなに\n見られると…\n恥ずかしいよぉ…", 1, 2757, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108501, "ひゃわぁっ！？\n急に触られると、\n驚くからやめてぇ…", 1, 2758, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108501, "あ…クリスマスの\n準備を手伝って\nくれるんですか？", 1, 2759, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108501, "お兄ちゃんと\n過ごすクリスマス、\n楽しみですぅ…♪", 1, 2760, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108501, "クリスマスはその…\n二人で…あうぅ…\nなんでもないですぅ…", 1, 2761, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 108501, "うう…パーティーが\n楽しみで、あんまり\n眠れなかったよぅ…", 1, 2762, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 108501, "ケーキを食べられなく\nなっちゃうから、\nお昼は軽めにしないと", 1, 2763, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 108501, "ちょっと眠いけど…\n今日は少しだけ\n夜更かししたいです♪", 1, 2764, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108501, "ふかふかぁ…\nクリスマスの準備中に\n眠くなっちゃいますぅ", 1, 2766, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108501, "この子と一緒に\nおままごとしたいな♪", 1, 2767, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108501, "いい匂い…♪\nみんなで\nアロマパーティー\nしたいなぁ…♪", 1, 2768, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108501, "ぬっくぬく…♪\nもう、ここから\n離れたくないよぉ…♪", 1, 2769, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 108501, "はむはむ…♪\nクリスマスのお料理\nより美味しいかも～♪", 1, 2771, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 108501, "すごい…\nクリスマスの\n演劇って、\nこんなにあるんだ…", 1, 2772, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 108501, "すごい…まるで、\nクリスマスの演劇を\n観てるようですぅ…♪", 1, 2774, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 108501, "とっても可愛いです♪\nお芝居の相手役に\nしたいなぁ…♪", 1, 2775, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108601, "甘ぁいケーキも、\nデッカイ七面鳥も、\nぜーんぶ私のだー♪", 1, 2776, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108601, "毎日が\nクリスマスだったら\nいいのにな～♪", 1, 2777, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108601, "プレゼントはあるし、\n料理は美味しいし、\nクリスマス最高ー♪", 1, 2778, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108601, "ここでぷうきちの\nクリスマス一発芸♪\n『無茶ぶりするな！』", 1, 2779, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108601, "ぷうきちがいつもより\n重いかも…太ったの？\n『衣装の重さだよ！』", 1, 2780, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108601, "お兄ちゃんが\n一緒にいてくれると\nクリスマス楽しいね♪", 1, 2781, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108601, "お兄ちゃんに抱きつく\nと、なんだかドキドキ\nする…なんでかな？", 1, 2782, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108601, "メリークリスマス♪\nプレゼントは、\nいつでも受付中だよ♪", 1, 2783, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108601, "このクマさん耳\n可愛いでしょ♪\nぷうきちと\nお揃いなんだー♪", 1, 2784, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108601, "お兄ちゃん…私、\n甘ぁいケーキが\n食べたいなぁ…♪", 1, 2785, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108601, "クリスマスぷうきちは\nぬくぬく感が凄いよ！\nお兄ちゃんもさわる？", 1, 2786, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108601, "えへへ…お兄ちゃんの\n手は、大きくて、\n暖かくて大好き～♪", 1, 2787, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108601, "来年のクリスマスも、\nこうやって\nお兄ちゃんに\n甘えていたいな～♪", 1, 2788, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 108601, "あれぇ…\n私のケーキはぁ…？\n『ちゃんと起きろ』", 1, 2789, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 108601, "お昼はデッカイ\n七面鳥が食べたーい♪\n『夜まで我慢しろ』", 1, 2790, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 108601, "『もう寝る時間だぞ』\nサンタさんが来る\nまで起きてるぅ…", 1, 2791, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108601, "このふかふか具合…\nぷうきち！\n負けてられないよ！", 1, 2793, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108601, "この子、\nすっごくかわいい～♪\n『ライバル登場か！』", 1, 2794, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108601, "良い匂い…あっ！\nぷうきちにこの\n匂いを移せば…！\n『匂い袋にするな』", 1, 2795, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108601, "あったか～い♪\nえへへ、ぷうきちも\nあっためてあげる♪", 1, 2796, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 108601, "ぷうきち！　これ、\nとってもおいしいよ♪\n『食べすぎるなよ』", 1, 2798, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 108601, "お、大人の私なら、\nこれぐらい…読め…\n『無理するな…』", 1, 2799, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 108601, "絵が飛び出す…\nぷうきちに絵本を\n入れれば、イタズラに\n使えるかも…♪", 1, 2801, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 108601, "か、可愛いけど…\nぷうきちサンタの方が\n可愛いんだからぁ…！", 1, 2802, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108901, "着物も慣れると\n動きやすいものさ", 1, 2803, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108901, "鼻緒が緩んでしまった\n下駄で修業は\n無理があったかな…", 1, 2804, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108901, "小腹が空いた…\nユイが作った餅、\nまだ食べられるかな？", 1, 2805, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108901, "服装も変わったし\nいつもと違う戦い方を\n想定しないと…", 1, 2806, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108901, "…ヒヨリは動きすぎて\n着崩れしてないか\n心配だ", 1, 2807, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108901, "これからもずっと\n仲間と、彼と\n一緒に…", 1, 2808, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108901, "願いを叶えるために\nもっと自分を\n鍛えていかないと！", 1, 2809, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108901, "頭の飾りがずれてた？\nそうか、直してくれて\nありがとう", 1, 2810, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108901, "着物がそんなに\n珍しい？\nキミも着てみる？\n…冗談だよ", 1, 2811, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108901, "肩掛けの触り心地が\n気に入った？\nならもっと触る？", 1, 2812, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108901, "ああ、この刀？\n釣り仲間に見繕って\nもらったんだ", 1, 2813, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108901, "キミといると\n気持ちが段々と\n安らいでいくよ", 1, 2814, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108901, "キミは私の行く先を\n照らしてくれる\n光のような存在だよ", 1, 2815, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 108901, "おはよう\n朝の修行を始めるけど\nキミもどう？", 1, 2816, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 108901, "この時間に軽く\n休憩をとるといい\n能率がよくなるよ", 1, 2817, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 108901, "もう夜か…\n明日もまた頑張ろう\nそれじゃ、おやすみ", 1, 2818, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108901, "…疲れていたのかな\nもう少しで眠る\nところだった…", 1, 2820, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108901, "ふふっ、正月衣装を\n着せてやるのも\nよさそうだ", 1, 2821, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108901, "ふぅ…いい香りだ\n慌ただしい日々から\n心が解放される", 1, 2822, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108901, "この心地よさ…\n寝てしまわないよう\n気をつけないと", 1, 2823, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 108901, "おいしいけど\n帯がきつくなる前に\n手を止めよう…", 1, 2825, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 108901, "外を出歩くのも\nいいけど、中で\n読書もありだな", 1, 2826, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108801, "…騎士クンって\nおせち料理で\n何が好きなんだろう？", 1, 2828, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108801, "着物でお掃除や\nお洗濯するの\n慣れてきた♪", 1, 2829, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108801, "今年は\nどんな福袋を\n買おうかな…♪", 1, 2830, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108801, "お餅に使う調味料\nみんなで違うから\n色々用意しなきゃ", 1, 2831, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108801, "ほっぺがお餅みたいに\nぷにぷに…\n食べ過ぎちゃった…", 1, 2832, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108801, "来年も、再来年も\nみんなで一緒に\n新年を迎えたいな", 1, 2833, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108801, "お願いです…\nいつかこの気持ちが\n伝わりますように…", 1, 2834, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108801, "髪飾りが落ちそう？\nじゃあ…騎士クン\nつけ直してくれる？", 1, 2835, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108801, "着物が着たいの？\nじゃあ今度一緒に\n買いに…行く？", 1, 2836, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108801, "あっ、騎士クン\nお雑煮のお餅\nいくつ入れたい？", 1, 2837, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108801, "外、寒かったよね？\nはいこれ。熱い甘粥\n飲むと温まるよ", 1, 2838, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108801, "今年は騎士クンと…\nえっ？　騎士クン！？\nな、なんでもないよ！", 1, 2839, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108801, "騎士クンの願い事\nわたしが\n叶えてあげたいな…", 1, 2840, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 108801, "おはよう\n太陽の光に当たると\n元気が出るよ", 1, 2841, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 108801, "お昼ごはん\n作らなきゃ…\n騎士クンも食べる？", 1, 2842, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 108801, "一日が終わるのは\nあっという間だよね\n一年も同じなのかな？", 1, 2843, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108801, "ふぅ…\n帯も少し\nゆるめちゃおう…", 1, 2845, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108801, "お正月用に\n着物を縫って\nあげたいな", 1, 2846, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108801, "これでお肌が\n保湿出来たら\nいいのになぁ", 1, 2847, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108801, "ん～…\nぬくぬくしてて\n動きたくないよ～", 1, 2848, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 108801, "…冬眠しないのに\n熊さんみたいに\n食べちゃいそう", 1, 2850, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 108801, "年末年始は\n告白のチャンス…\nむ、無理無理！", 1, 2851, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108701, "みんな忙しそう…\n待っててね、\nすぐ助けるから！", 1, 2853, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108701, "ユイちゃんと\nレイさん…よし！\nどっちも手伝おう！", 1, 2854, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108701, "あっ、着崩れてる！\nわわっ、どうしよ！\n帯が結べないよー！", 1, 2855, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108701, "初詣楽しみー！\n出店はどこから\n行こうかな？", 1, 2856, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108701, "お正月だし\nみんなで楽しく\n遊んじゃおー！", 1, 2857, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108701, "うーん…\n騎士くんはお正月\n何してるんだろ…", 1, 2858, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108701, "ううぅー…\n騎士くんを見てると\nドキってなっちゃう…", 1, 2859, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 108701, "あたしから\nおいしそうな匂い？\nくんくん…ソースだ！", 1, 2860, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108701, "ヘアピンが…\n拾ってくれたんだ！\nありがとう騎士くん！", 1, 2861, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108701, "髪飾りがかわいい？\n耳の近く触りたい？\n…優しくね？　", 1, 2862, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108701, "グローブで食べるの\n大変そう？　特訓して\n今はお箸も使えるよ！", 1, 2863, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 108701, "えっ、ほっぺが赤い？\nそうかな…？\n気のせい気のせい！", 1, 2864, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 108701, "騎士くんといるとね…\nなんでもない！\nまだ内緒っ！", 1, 2865, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 108701, "おはよー！\n今日も明るく元気に\nがんばろー！", 1, 2866, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 108701, "お腹すいてない？\n一緒にご飯食べよ！\n何にしようかなー♪", 1, 2867, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 108701, "今日もいっぱい\nがんばったー！\nいつも？　…そう？", 1, 2868, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 108701, "休憩休憩♪\nちょっと休んで\nガンガン動くぞー！", 1, 2870, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 108701, "かわいい♪\nでも、壊しそうで\nちょっと怖い…", 1, 2871, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 108701, "はぁー♪\nいい香りー…\n気分スッキリ♪", 1, 2872, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 108701, "ほっこりしてて\nぬくぬく…\n丸まりたい…", 1, 2873, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 108701, "ど、どうしよう…！\n手が、止まんないよ！", 1, 2875, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 108701, "へー…お年玉って\n昔はお餅だったんだ\n…今のほうがいいね", 1, 2876, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109001, "甘いチョコの香りを\n漂わせて…ふふっ\nこれで準備万端ですわ", 1, 2878, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109001, "ここを私のチョコで\n埋め尽くしますわ", 1, 2879, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109001, "エプロンを着て\nあの方を待つ私…\nまるで…クスクス", 1, 2880, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109001, "キッチンはどこかしら\n今のうちに作って\nおきたいものが…", 1, 2881, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109001, "あのお方を私のチョコ\nなしでは生きられない\n体に…ふふっ\n冗談ですわ…", 1, 2882, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109001, "このチョコは運命の味\n一口食べれば私の虜…\nそれも運命ですわ…", 1, 2883, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109001, "私ともあろうものが\nなんだかドキドキして\nきましたわ…", 1, 2884, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109001, "あなた様。私に\n隠しているものなど\nありませんよね…？", 1, 2885, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109001, "私の手作りチョコです\n味の方にも自信\nありますのよ？", 1, 2886, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109001, "少し苦いのは\nチョコがビターなだけ\nお気になさらず…", 1, 2887, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109001, "解毒薬はどこに\nいれたかしら…？", 1, 2888, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109001, "運命で結ばれている\nあなた様と私…\nチョコも特別でないと\nいけませんわ", 1, 2889, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109001, "チョコを食べた後は\nメインディッシュ…\nさぁ、私を召し上がれ", 1, 2890, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 109001, "目覚めのチョコですわ\nすっきり目が覚めます\nしばらく眠れなく\nなりますけど…", 1, 2891, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 109001, "おやつのチョコですわ\nデザートにどうぞ\n空腹を感じなく\nなりますわ…クスクス", 1, 2892, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 109001, "お夜食のチョコですわ\nとてもよく眠れます\nさ、溶けてしまう前に", 1, 2893, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109001, "この心地良さ…\n参考になりますわ\n次の薬は…クスクス", 1, 2895, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109001, "この中、何か\n入りそうですわね\n…チョコレート、とか", 1, 2896, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109001, "いい香り…この香りと\n同じものを調合して\n私も持っていたい", 1, 2897, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109001, "この暖かさ…\nチョコが溶けないよう\n気を付けなくては", 1, 2898, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 109001, "いい味をしていますわ\n私も腕を磨きましょう\n料理と、調合の腕を…", 1, 2900, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 109001, "勉強になりますわ\n知識はいくらあっても\n困りませんもの\n特に、人体の知識は…", 1, 2901, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109101, "弟くんのことを\n考えると\nあっという間に時間が\n過ぎちゃう…", 1, 2903, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109101, "お姉ちゃんの\n元気の源は、弟くんの\n喜ぶ顔なんだから♪", 1, 2904, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109101, "弟くんと会ったら\nまずは抱きしめて\nそれからそれから…", 1, 2905, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109101, "今日のお姉ちゃんは\n昨日のお姉ちゃんより\nいいお姉ちゃんだよ♪", 1, 2906, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109101, "弟くん、もっと\nお姉ちゃんに\n甘えてもいいのに♪", 1, 2907, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109101, "お姉ちゃんのチョコ\n食べさせてあげたい…", 1, 2908, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109101, "ドキドキしてきた…\nお姉ちゃんなのに\nどうしてかなぁ…", 1, 2909, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109101, "お姉ちゃんを\n好きなだけ\n触っていいよ♪", 1, 2910, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109101, "そんなにお姉ちゃんの\nチョコが欲しいの？\n食いしん坊さん♪", 1, 2911, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109101, "ナデナデするのは\nお姉ちゃんの仕事！", 1, 2912, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109101, "ありがとうなんて…\nお姉ちゃんとして\n当たり前だよ♪", 1, 2913, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109101, "どうしたのかなっ？\nお姉ちゃんはいつでも\n弟くんのそばにいるよ", 1, 2914, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109101, "チョコを作ったよ\n隠し味は、たくさんの\n弟くんへの愛情♪", 1, 2915, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 109101, "おはよ。お姉ちゃんが\n顔を洗うの\n手伝ってあげるね♪", 1, 2916, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 109101, "ご飯を食べたら\n眠たくなるよね…\nお姉ちゃんと寝る？", 1, 2917, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 109101, "そろそろ眠くなった？\nお姉ちゃんが添い寝\nしてあげるね♪", 1, 2918, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109101, "この気持ちよさは\nお姉ちゃんも太鼓判♪", 1, 2920, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109101, "このかわいさは\nお姉ちゃんも大満足♪", 1, 2921, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109101, "この香り、チョコに\n使えるかな…？", 1, 2922, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109101, "弟くんと一緒に\nぬくぬくしたいな…♪", 1, 2923, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 109101, "すごくおいしいねっ♪\nあとで運動しなきゃ…", 1, 2925, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 109101, "本を読んでる\n知的なお姉ちゃん\n素敵でしょ♪", 1, 2926, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106101, "アタシって…\nここにいても\nいいのか…？", 1, 2928, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106101, "ちゃんとした建物に\nいるのが久しぶりで\nそわそわする…", 1, 2929, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106101, "ここで天楼覇断剣\n出す練習したら\nアイツら怒るかな？", 1, 2930, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106101, "ギルドハウスか…\nこういう隠れ家\n欲しかったんだよな", 1, 2931, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106101, "めちゃくちゃ暇だ…\n…なんかやること\nないのか？", 1, 2932, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106101, "みんなが早く\nアタシのこと\n思い出さないかな", 1, 2933, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106101, "アイツはいいヤツだし\n戦う時はアタシも\n協力してやるか！", 1, 2934, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106101, "ちゃんはいらん！\nノウェムでいい！\n分かったか？", 1, 2935, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106101, "どうした？\n戦うのか！？\n…なんだ、違うのか", 1, 2936, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106101, "身長のことは\nあんまり言うな！\n気にしてるからな！", 1, 2937, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106101, "アタシの戦い方は\nなんでもアリだ！\nだから見習うなよ？", 1, 2938, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106101, "オマエとの思い出か…\n敵として戦ってばっか\nだったな！", 1, 2939, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106101, "オマエやオクトーと\n一緒に、あちこち\nいってみたいんだ！", 1, 2940, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 106101, "よっ！　おはよう！\n朝だぞー！\n起きてるかー？", 1, 2941, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 106101, "腹減ったー！\n飯食うぞ、飯！\nオマエも行くよな？", 1, 2942, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 106101, "ふわぁ…\nまだ起きてんのか？\n元気だなオマエ…", 1, 2943, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106101, "ふぃ～…\n極楽極楽…", 1, 2945, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106101, "おいオマエ！\n寝技の練習に\n付き合ってもらうぞ", 1, 2946, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106101, "くんくん…\nたまにはこういうのも\n悪くないかもな", 1, 2947, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106101, "あー…\nもっと前にコイツと\n出会いたかった…", 1, 2948, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 106101, "VRでも飯はうまい！\n腹が減るのはちゃんと\n生きてる気がするな", 1, 2950, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 106101, "勉強は大事だな！\nうん！…だめだ！\n全然わからん！", 1, 2951, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101401, "探偵というのは\n謎を追い求める\n生き物なのさっ！", 1, 2953, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101401, "事件のオチは\n往々にして\n取るに足らないものさ", 1, 2954, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101401, "読書で得る知識が\n探偵の\n血肉になるんだよ", 1, 2955, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101401, "この前の\n捜査資料…\nどこにあったかな？", 1, 2956, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101401, "どんな小さな謎も\n私にとっては\n宝石みたいなものさ！", 1, 2957, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101401, "助手くんと一緒だと\nよい発想が浮かぶ…\nどうしてだろうね？", 1, 2958, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101401, "助手くんの好きな\nタイプって…\nす、推理してみるっ！", 1, 2959, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 101401, "珈琲、淹れたてだよ\n一杯どうだい？", 1, 2960, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101401, "事件の考察を\nまとめてみたんだけど\n聞いてくれるかな？", 1, 2961, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101401, "小さな違和感を\n見逃さない…\n探偵の心得だよ", 1, 2962, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101401, "機能的な手帳とペン…\nこの仕事には\n欠かせないね！", 1, 2963, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 101401, "助手くんに\n珈琲…\n淹れてほしいなぁ～", 1, 2964, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 101401, "助手くんは\n私にとって\n最高の相棒だよ☆", 1, 2965, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 101401, "朝はいいひらめきが\n得られるんだ…\nさあ捜査を始めよう！", 1, 2966, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 101401, "一休みしたら\n推理の答え合わせを\nしてみようか？", 1, 2967, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 101401, "夜の張りこみかい？\nなら私も\n同行させてほしいな！", 1, 2968, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 101401, "脳を酷使した後は\nゆっくり休む…\nこれも仕事だよ？", 1, 2970, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 101401, "この愛らしさ…\n要捜査対象だね！", 1, 2971, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 101401, "思考が活性化される\n素敵な香りだね…", 1, 2972, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 101401, "くぅ、心地よすぎる…\n今だけは捜査も\n一休みだね♪", 1, 2973, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 101401, "いっぱい食べて\nいっぱい考える\n探偵の本分だね！", 1, 2975, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 101401, "探偵だからね\nミステリには\n一家言あるんだ", 1, 2976, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105501, "先輩、素敵だなぁ…\nもっと近づければ\nいいのに…", 1, 2978, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105501, "せ、先輩がいた部屋！\n私には刺激が強い…", 1, 2979, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105501, "先輩を観察しやすい\n場所はあるかな…", 1, 2980, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105501, "バ、バイト代出たし、\nせ、先輩をご飯とかに\n誘いたい…な", 1, 2981, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105501, "今頃先輩は\nあの辺かな…？\n見に行ってみよう！", 1, 2982, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105501, "私と先輩の出会いって\n思い返すと少女漫画\nみたい…えへへ", 1, 2983, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105501, "せ、先輩になら\n観察されてもいいなぁ", 1, 2984, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 105501, "バイト先に新しい子が\n入って来たんです…\nわ、私、先輩です♪", 1, 2985, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105501, "せ、先輩…？\nえっと、あの、その、\nあわわわわ…！", 1, 2986, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105501, "えへへ…えへへ～\nせ、先輩から\n話しかけてくれました", 1, 2987, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105501, "ま、また…\n先輩と一緒に\nバイトしたい…です", 1, 2988, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 105501, "私と先輩が同じ空間に\n…ゆ、夢みたいです", 1, 2989, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 105501, "せ、先輩のそばに\n居られて、私\n今、とっても\n幸せです…", 1, 2990, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 105501, "せ、先輩…！\nおひゃっ！\nおはようございます！", 1, 2991, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 105501, "先輩ランチに\n行きましょう！\nよし、練習はバッチリ", 1, 2992, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 105501, "あ、せ、先輩。\nえと、そ、外…\nもう真っ暗ですねぇ", 1, 2993, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 105501, "こ、ここで先輩も\n休んだり…はっ！\nわ、私ったらなにを！", 1, 2995, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 105501, "かわいいですねー\n君も一緒に、\n先輩を観察する？", 1, 2996, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 105501, "ここから先輩の\n香りがすればなあ", 1, 2997, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 105501, "先輩と一緒に\nぬくぬくできたら\n最高なんだけどなぁ", 1, 2998, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 105501, "おいし～\nこれは\n先輩も好きな味かな", 1, 3000, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 105501, "す、好きな人に\n近づく方法…", 1, 3001, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109201, "私の力は、大切な\n人たちを護るために\n役立てたいんだ", 1, 3003, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109201, "きれいな景色と\n美味しいご飯！\nこの世界も素敵だね", 1, 3004, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109201, "この世界のみんなとも\nもっと仲良く\nなりたいな！", 1, 3005, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109201, "わー！\nあれもこれも\n気になっちゃうな！", 1, 3006, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109201, "しっかりしなきゃって\n思ってても、\nグレアといるとつい\n甘えちゃうんだよね～", 1, 3007, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109201, "違う世界でも\n友だちができて、\nすごくうれしいよ！", 1, 3008, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109201, "みんなとも一緒に\n授業とか受けたいな～\n絶対楽しそうだもん！", 1, 3009, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109201, "歩き方がきれい？\nえへへ、そうかな…？", 1, 3010, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109201, "勉強は好きだよ！\n知識が増えていくのは\nやっぱり楽しいから", 1, 3011, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109201, "私たちの国マナリアは\n人々の間に魔法が\n伝えられた、その\n始まりの地なんだ！", 1, 3012, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109201, "この部屋にも、\nもっとたくさん本棚が\n増えないかな～…？", 1, 3013, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109201, "あなたも魔法、\n覚えてみる？\n私、教えるよ！", 1, 3014, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109201, "お茶の時間に\nしたいな～…\n一緒にどう？", 1, 3015, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 109201, "おはようっ！\n今日も素敵な\n一日にしようね！", 1, 3016, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 109201, "お昼ご飯は\nちゃんと食べた？\n午後も頑張ろうね！", 1, 3017, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 109201, "つい本の続きが\n気になって、夜更かし\nしちゃうんだよね～…", 1, 3018, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109201, "はぁ、気持ちいい～…\nちょっとだけ\n眠っちゃおうかな…", 1, 3020, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109201, "わあ、これ可愛い！\nグレアにも見せて\nあげたいな", 1, 3021, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109201, "いい香り～！\nなんだか気持ちが\n落ち着くね", 1, 3022, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109201, "あぁ、あったかくて\n幸せ～♪　身体が\nポカポカしてきたよ！", 1, 3023, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 109201, "こ、これ美味し～！\n止められないよー！", 1, 3025, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 109201, "ふんふん…\n…はっ！　む、夢中に\nなっちゃってた…", 1, 3026, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109301, "新しい建物に入ると\n必ず一度は迷子に\nなっちゃうですよ…", 1, 3028, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109301, "おやつはどこですか？\nお腹が空いて\nきちゃったですぅ", 1, 3029, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109301, "宿題、７つぐらい\n出しそびれたですけど\nまあ大丈夫ですぅ！", 1, 3030, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109301, "不思議なこと\nばかりですけど…\nまぁ大丈夫です！\nこれもせいしゅんです", 1, 3031, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109301, "なにか面白そうなもの\nないですかね～？", 1, 3032, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109301, "ここのみんなとも\nもうお友だちですぅ！", 1, 3033, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109301, "おなかが減ったです…", 1, 3034, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109301, "ごはんも美味しいし\nここはいいところです\nルゥは幸せですぅ！", 1, 3035, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109301, "魔法の話ですか？\nそれはアンせんぱいに\n聞いてほしいですぅ…", 1, 3036, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109301, "あなたもお勉強\n苦手ですか？", 1, 3037, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109301, "ルゥの帽子、\nかわいいです？\nお気に入りですぅ♪", 1, 3038, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109301, "お友だちいっぱいで、\nルゥはうれしいです♪", 1, 3039, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109301, "一緒にお勉強…！？\nぐえぇ、イヤですぅ！", 1, 3040, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 109301, "朝ですねぇ…ふわわ…\nルゥはまだ夢の中に\nいたいですぅ…", 1, 3041, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 109301, "午後もいっぱい\n遊ぶですよ～！", 1, 3042, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 109301, "夜ふかしって\nドキドキするです！\nせいしゅんですぅ！", 1, 3043, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109301, "ふかふかですぅ～♪\nおひるねが\nしたくなるですぅ！", 1, 3045, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109301, "これふかふかですぅ！\n机でうとうとする時の\n枕によさそうですぅ", 1, 3046, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109301, "ふぁぁ、いいにおい…\nなんだか気持ちが\nよくなってきたですぅ", 1, 3047, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109301, "ぽっかぽかですぅ～！\nあったまったら\nまた遊びにいくですぅ", 1, 3048, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 109301, "お、おいしいですぅ！\n全部ルゥに\nくださいですぅ！", 1, 3050, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 109301, "本は…読むと、\nすぐ眠気がぁ…\n…ぐぅ…", 1, 3051, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109401, "ピアノ…\nどこかにないかな？", 1, 3053, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109401, "ふぅ…\n落ち着くな…", 1, 3054, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109401, "マナリアはいつも\n賑やかなんだ…", 1, 3055, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109401, "みんな…\n今頃何してるかな？", 1, 3056, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109401, "まさか違う世界に\n来ちゃうなんて…\nびっくりだよね…", 1, 3057, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109401, "人と話すのは\n得意じゃないな…", 1, 3058, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109401, "ここのみんなと一緒に\n学院に通えたら…\nちょっと面白いかも", 1, 3059, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109401, "やっぱり、私の翼や\n尻尾…気になる？", 1, 3060, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109401, "一番得意なのは、\nやっぱり炎魔法かな…\n加減は難しいけど…", 1, 3061, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109401, "私の故郷は竜の国…\n険しい山脈を\n越えた先にあるんだ", 1, 3062, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109401, "アン…？\nあっ、ご、ごめんね！\n間違えちゃった…", 1, 3063, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109401, "ふん…ふん…♪\n…わっ！\n今の、聞いてた…？\nは、恥ずかしい…", 1, 3064, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109401, "私に手伝えること、\nなにかある…？\nなんでも言ってね", 1, 3065, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 109401, "朝の空気って\nすごく\n静かで透き通ってて…\nなんだか落ち着くよね", 1, 3066, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 109401, "学院食堂のランチも\n美味しいけど…\nここのご飯も好きかな", 1, 3067, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 109401, "そろそろベッドに\n行かないとだね\n…寝相、今夜こそ\n気を付けよう…！", 1, 3068, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109401, "羽を伸ばしたく\nなっちゃうね…ふふ", 1, 3070, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109401, "わ、私が持つには\nかわいすぎるかな…\nでも、素敵だね", 1, 3071, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109401, "いい香り…\nこれ、\nアンも好きそう…", 1, 3072, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109401, "火を見ていると\n少し落ち着くんだ…\nへ、変かな？", 1, 3073, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 109401, "わ、美味しい…！\nこれ、どうやって\n作ったのかな…？", 1, 3075, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 109401, "この本…アンにも\n見せてあげたら\n喜ぶかな、ふふっ", 1, 3076, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109501, "着物はきれいですが\n歩きにくいです…\nいっそ脱いで…ぐふふ", 1, 3078, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109501, "あっ、いけません\n着物がはだけて…はっ\nもしやドＳさんが…！", 1, 3079, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109501, "この部屋は妄想する\nのにうってつけです…\nぐふふ…じゅるり", 1, 3080, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109501, "扇をゆっくりと開く\nこの感触…なぜだか\n艶めかしさを感じます", 1, 3081, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109501, "「マッサージをすると\n言いながらドＳさんの\n手は次第に」…ぐふふ", 1, 3082, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109501, "…ドＳさんの視線を\n思い出しながら踊りの\n練習です…イイ！", 1, 3083, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109501, "よいではないか\nよいではないか～\nいけませんご堪忍を～", 1, 3084, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109501, "あ～れ～～～！\nドＳさんもお好きな人\nですねぇ…ぐふふ", 1, 3085, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109501, "どこにいてもドＳさん\nの気配を感じ取れる\nようになりました…", 1, 3086, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109501, "こんな場所で\nなんというプレイを！\n…妄想でした", 1, 3087, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109501, "クウカを押し倒して\nそのまま…じゅるり\nああっ、着物が…！", 1, 3088, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109501, "ドＳさんは優しいです\n優しいのにドＳとは\nさすが人格破綻者…！", 1, 3089, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109501, "ドＳさんの考えている\nことが伝わってきます\nはい…クウカ脱ぎます", 1, 3090, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 109501, "眩しい朝日がクウカの\n体を焦がします\n…それもイイ！", 1, 3091, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 109501, "お昼のランチは\nクウカがいいだなんて\n大胆過ぎです…じゅる", 1, 3092, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 109501, "…今夜のドＳさんは\n大胆です…ぐふふ…\nはっ、視線が…！？", 1, 3093, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109501, "この心地良さ、\n心が休まりすぎて\n体が疼いてきます…！", 1, 3095, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109501, "すごく愛らしいです…\n妄想も出てこない\nくらい…じゅるり", 1, 3096, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109501, "この香りに包まれて\nいると、いい気持ちに\nなって…いきますぅ…", 1, 3097, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109501, "「熱を帯びた体は\nどうしようもなく\n誰かを求め…」ぐふふ", 1, 3098, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 109501, "お腹が膨らんで\nいきます…それは\nまるで…ぐふふ", 1, 3100, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 109501, "クウカの探している\nジャンルはどこに\nありますかね？", 1, 3101, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109601, "カチョーフーゲツは\nトーゴクの心を\n表した言葉デスね", 1, 3103, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109601, "オー！　新しい忍法を\n思いついたデス！\nさっそく試すデス！", 1, 3104, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109601, "トーゴクの天気には\n槍が降る日も\nあるそうデス…", 1, 3105, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109601, "我がショーグンは\n永遠にフメツデース\nいざ出陣デース！", 1, 3106, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109601, "壁に耳あり\n障子に…ん？\nメアリーって誰デス？", 1, 3107, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109601, "ショーグンがいれば\nワタシはどんな\n忍法も使いこなせマス", 1, 3108, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109601, "不届き者はどこで\nゴザルデス？　忍法で\n退治してくれヨウ！", 1, 3109, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109601, "ショーグン！　新しい\n忍法を覚えたデス！\n見てほしいデース！", 1, 3110, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109601, "レッツ手裏剣道デス！\n投げてソウロウで\nゴザルデース！", 1, 3111, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109601, "ふりかかる火の粉は\nワタシが払うデス！\nバンバン！", 1, 3112, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109601, "とっておきの忍法が\n吹き荒れるデス！\nよそ見はダメデスヨ？", 1, 3113, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109601, "ショーグンは\n何階建てのお城を\n建てたいデスか？", 1, 3114, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109601, "テンシュカクから\n平和な城下町を\n眺めてみたいデス…", 1, 3115, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 109601, "おはようございマス！\n本日はオヒガラモヨク\n良い日になりそうデス", 1, 3116, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 109601, "お腹すいたデース…\nショーグンもご飯に\nしまショウ！", 1, 3117, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 109601, "闇討ちに遭わぬよう\n夜は気を付けて\n歩くデスよー？", 1, 3118, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109601, "天下分け目の\n戦いに備えて…\nマッタリデース", 1, 3120, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109601, "口寄せの術で\n召喚したいくらい\nかわいいデース", 1, 3121, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109601, "いい匂いデ～ス\n香り玉の開発の\n参考になりそうデスね", 1, 3122, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109601, "ワタシの懐よりも\nゾーリが暖まりマス…\nライバル出現デス…！", 1, 3123, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 109601, "腹が減っては\n戦はできぬデース！\n天下統一に向けて\n腹ごしらえデス！", 1, 3125, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 109601, "ムム、暗号文デス！？\n解読には時間が\nかかりそうデース…", 1, 3126, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109701, "掃除も終わりましたし\nそろそろ、食事の\n準備をしなくては", 1, 3128, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109701, "異世界の演劇…\n許されるならば、\n観てみたいです", 1, 3129, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109701, "こちらの世界には、\nマヨネーズが普通に\n存在するのですね…", 1, 3130, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109701, "こちらの世界は食材も\n面白い物が多いですね\nなにかレシピを\n考えてみましょうか", 1, 3131, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109701, "みょんみょんみょん…\n魔法の練習は\nこれぐらいですね", 1, 3132, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109701, "倒木の撤去で思わぬ\n収入となりました\nサレン様に\nお渡ししておかないと", 1, 3133, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109701, "料理を美味しく\n召し上がって頂けると\nメイド冥利につきます", 1, 3134, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109701, "お出かけでしたら、\nレムも同行\nさせてくださいね", 1, 3135, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109701, "レムにできること\nでしたら、なんでも\nお申し付けください", 1, 3136, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109701, "じーー。いえ、\nその髪の長さが少し、\nとても少し、かなり\n少し気になったもので", 1, 3137, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109701, "鉄球ですか？\nあれは護身用ですので\n問題はありませんよ？", 1, 3138, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109701, "計算の勉強は\n進んでおりますか？\nレムでよろしければ、\nいつでもお教えします", 1, 3139, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109701, "次はもっと、大きな\n魔物料理に挑戦します\nワイバーンの姿焼き\nなんてどうでしょうか", 1, 3140, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 109701, "おはようございます\n朝食の準備が\nできておりますので、\n食卓におこしください", 1, 3141, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 109701, "昼食の時間ですね\n何か食べたいものは\nありますか？", 1, 3142, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 109701, "寝ないのか、ですか？\nメイドの就寝は、\n屋敷で最後と\n決まっておりますので", 1, 3143, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109701, "ふんわり…\nこのふんわりさは、\n鬼をも黙らせます…", 1, 3145, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109701, "ふふっ…異世界でも\nかわいいものは\n共通なのですね", 1, 3146, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109701, "よい香りですね\n魔女教の悪臭とは\n大違いです", 1, 3147, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109701, "ぬくぬくです…はっ！\nこれはいけません！\n人を…いえ、\n鬼をだめにします！", 1, 3148, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 109701, "はむはむ…美味しい！\nこの味を再現できたら\n褒めてもらえる\nかもしれません…！", 1, 3150, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 109701, "絵がいっぱい…\n異世界の本は\nとても面白いですね", 1, 3151, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109801, "異世界であろうと、\nラムのやるべきことは\n変わらないわ\nまずは休憩するわよ", 1, 3153, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109801, "マヨネーズがある\nということは、\nバルスの故郷が近い\nのかもしれないわね", 1, 3154, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109801, "この世界では、バルス\nの言う言葉が使われる\nときがあるのね\n忌々しいかぎりだわ", 1, 3155, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109801, "ラムたち以外にも\nメイドが居るのね\nラムが…いえ、\n仕事を押しつけるわ", 1, 3156, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109801, "…千里眼で視るかぎり\n周囲に異変はないわね\n危険がないなら、\nあとはレムに任せるわ", 1, 3157, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109801, "…ラムもまた、\n「お姉ちゃん」って\n呼ばれる日が\nくるのかしらね", 1, 3158, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109801, "ハッ！　ラムが作る\n蒸かし芋は完璧ね\n…気が向いたら、また\n作ってやろうかしら", 1, 3159, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109801, "ラムに構う暇が\nあるなら働きなさい\nそうすれば、\nラムが楽をできるわ", 1, 3160, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109801, "ラムに何かを\n期待しても無駄よ\nラムに命令できるのは\nロズワール様だけだわ", 1, 3161, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109801, "暇なら買い物に\n付き合いなさい\n荷物を\n持たせてあげるわ", 1, 3162, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109801, "ラムになんの用？\nふざけた内容なら\nねじ切るわよ", 1, 3163, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109801, "そういえば、あなたは\n記憶喪失だったわね…\n思い出す努力をなさい\n…家族が悲しむわよ", 1, 3164, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109801, "蒸かし芋が欲しい？\n随分と厚かましいわね\n…まだ残りがあるから\n好きに食べなさい", 1, 3165, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 109801, "だらしない顔ね\n顔を洗って\nしゃんとなさい", 1, 3166, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 109801, "お腹が空く時間ね\nラムの昼食を\n用意させてあげるわ", 1, 3167, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 109801, "メイドが寝るのは\n屋敷で最後…\nラムが寝るために、\nさっさと寝なさい", 1, 3168, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109801, "…ここから動かない\nという、誓いを\n立てたくなるわね", 1, 3170, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109801, "これは…かわいいわね\nもちろん、\nラムほどではないけど", 1, 3171, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109801, "…香炉かしら？\nこちらの世界にも\n質のよい物があるのね", 1, 3172, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109801, "見つけてしまったの\nかもしれないわ…\nラムの楽園を！", 1, 3173, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 109801, "…はむっ\nラムの蒸かし芋ほど\nではないけど、\nなかなかいけるわね", 1, 3175, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 109801, "異世界ならではの\n面白い内容もあるわね\nとても興味深いわ", 1, 3176, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109901, "ねえパック、\nにゃんにゃか\nにゃかにゃんしよっか", 1, 3178, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109901, "時間もあるし、\nこっちの世界の石で\n石積みしようかな", 1, 3179, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109901, "私もお姉さんだし、\nサレンさんを\n見習わないと…うん！\n私、すごーく頑張る！", 1, 3180, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109901, "この世界は、銀髪で\nハーフエルフでも受け\n入れてくれるのね…\nなんだか嬉しいな", 1, 3181, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109901, "今日のご飯…\nピーマルは\n入ってないわよね…？", 1, 3182, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109901, "また、みんなで\n一緒に買い物に\n行きたいな", 1, 3183, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109901, "パック以外から\n家族って呼ばれたの\n初めて…ふふっ♪\nちょっと嬉しいかも！", 1, 3184, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 109901, "今からパックの\n毛繕いをするけど…\n一緒にする？", 1, 3185, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109901, "私も何かお手伝いを…\nあ、そうだ！\n料理とかどうかな！", 1, 3186, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109901, "ねえ、ここで\n歌の練習って、\nしてもいいかな？", 1, 3187, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109901, "…あ、ごめんなさい！\n話しかけられるのって\nあまり慣れてなくて…", 1, 3188, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 109901, "すごーく頑張るから、\nこれからも勉強を\n手伝ってね", 1, 3189, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 109901, "せーの、びくとりー！\nふふっ…また、一緒に\nラジーオタイソー\nしようね♪", 1, 3190, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 109901, "ちゃんと起きてる？\nふふっ…よかった。\nあ、べ、別に心配した\nわけじゃないからね？", 1, 3191, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 109901, "お昼ご飯が楽しみね♪\nあ、こっちの世界って\nまよねーずはあるの？", 1, 3192, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 109901, "そろそろ寝る時間よ。\n夜更かししたら、\nめっ！　だからね！", 1, 3193, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 109901, "勉強も終わったし…\nちょっ、ちょっとだけ\n休んでもいいよね…？", 1, 3195, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 109901, "か、かわいい…！\nパックのかわいさに\n匹敵するかも…！", 1, 3196, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 109901, "良い香り…\nロズワールの屋敷にも\nこういうのって\nあるのかな？", 1, 3197, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 109901, "あったかい…\nずっとここに…はっ！\nダメダメ！　王選の\nために勉強しないと！", 1, 3198, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 109901, "あ…これ美味しい！\nこっちの世界は色んな\n食べ物があるのね。", 1, 3200, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 109901, "知らないことが\nいっぱい書いてある…\n異世界の本って、\n面白いのね", 1, 3201, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110001, "海ってマジ最高！\nビーチの撮影も\n超楽しいしー♪", 1, 3203, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110001, "20パーって何パ？\n今度ヒデサイに\n聞いてみよっ！", 1, 3204, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110001, "夏の思い出を\nヒデサイと一緒に\n作りたいな～", 1, 3205, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110001, "テンションあがる\n水着ゲットして\n超ラッキーだよ～♪", 1, 3206, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110001, "休憩が終わったら\n撮影にもどらないと！\n気合入れてこ♪", 1, 3207, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110001, "ヒデサイとビーチで\nパーティーしたいな～\n超楽しそうだよね！", 1, 3208, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110001, "ヒデサイといると\n楽しくてうれしくて…\nカレシってこんな感じ\nなのかなぁ？", 1, 3209, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110001, "見て見て～！\n夏らしいネイルに\nしてみたんだ～♪", 1, 3210, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110001, "ヒデサイ～\nこの水着どうかな？\nイケてるっしょ♪", 1, 3211, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110001, "水着姿の写真？\nもちいいよ！\nかわいく撮ってね！", 1, 3212, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110001, "うちだって\n夏の海みたいに\nキラキラしちゃうよ！", 1, 3213, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110001, "ヒデサイとうちの\nフィーリングって\nめっちゃエモいよね♪", 1, 3214, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110001, "ねーねーヒデサイ！\n一緒に泳ぎにいこっ！\nほら、手繋いで♪", 1, 3215, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 110001, "水着オッケー！\nネイルもばっちり！\n朝から遊んじゃおー♪", 1, 3216, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 110001, "お昼食べたら\n昼寝して\nリフレッシュしよー♪", 1, 3217, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 110001, "水着ってマジで\n最強かも…このまま\nお風呂にも入れるし♪", 1, 3218, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110001, "マジ気持ちいいー♪\nおに癒やされるん\nですけど～！", 1, 3220, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110001, "やばばばばー！\nかわいすぎだってば！\nマジらぶだよ～♪", 1, 3221, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110001, "あ、この香り超好き！\nモデル仲間に\n教えたげよ～♪", 1, 3222, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110001, "あったか～い♪\n夏の太陽みたい！", 1, 3223, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 110001, "マジ美味しいねこれ♪\nビーチで\n流行っちゃうかも！", 1, 3225, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 110001, "夏のために\n最新ファッションを\n勉強ちう！", 1, 3226, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 110001, "なにコレもにカワ～♪\nなんて生き物？\nヒデサイ知ってる？", 1, 3228, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 110001, "うわっ超フカフカ～\nなんかうち眠く\nなってきちゃった…", 1, 3229, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110101, "海の家でのんびり\n過ごしましょう\n熱中症には注意してね", 1, 3230, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110101, "はぁ、どうやったら\n泳げるように\nなれるのかしら～", 1, 3231, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110101, "海で日焼け止めを\n塗らないと、あとで\n大変なことになるわよ", 1, 3232, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110101, "久しぶりに水着を\n着たんだけど\n変じゃなかったかな…", 1, 3233, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110101, "波の音って落ち着くわ\n授業中に聞いたら\nみんな寝ちゃうかも？", 1, 3234, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110101, "海が楽しいって\n思えたのも\nあの子のおかげね", 1, 3235, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110101, "あの子と二人で海に\n来られてよかったわ\n一日があっという間ね", 1, 3236, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110101, "男の子と海だなんて\n少女漫画みたいで\n憧れてたの～", 1, 3237, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110101, "夏休みの宿題は\nちゃんとやった？\n遊んでばかりではダメ", 1, 3238, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110101, "昼の海と夜の海\nどっちが好き？　私は\n君といる海が一番よ", 1, 3239, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110101, "ふう、疲れたわ～\n冷たいジュースで\n元気回復ね！", 1, 3240, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110101, "海で見た君の背中\nすごく大きく見えたわ\nやっぱり男の子なのね", 1, 3241, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110101, "君と一緒に見た\n夜の海…\n忘れられそうにないわ", 1, 3242, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 110101, "準備ができたら\n海に出かけようよ\n今日も楽しみましょう", 1, 3243, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 110101, "あら、もうお昼？\n海の家でランチに\nしましょ♪", 1, 3244, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 110101, "夜の海岸についてきて\nくれる？　暗がりは\n一人じゃ怖くて…", 1, 3245, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110101, "泳ぐ練習をしたら\n疲れちゃったわ～\nちょっと一休み…", 1, 3247, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110101, "かわいいわね～\n浮き輪に乗せて\n海に浮かべたいわ～", 1, 3248, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110101, "この匂い…\nなんだか夏の海を\n思い出すわ～", 1, 3249, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110101, "夜の海風で冷えない\nように、しっかりと\n身体を暖めましょうね", 1, 3250, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 110101, "海で遊ぶ前に\nしっかり食べて\n体力つけましょう", 1, 3252, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 110101, "夏休み中でも\nしっかりお勉強\nしないと！", 1, 3253, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 110101, "この子に乗れば\n海の上でも\nすいすい進めるかしら", 1, 3255, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 110101, "ビーナスの誕生！\n…なんちゃって\nはしゃぎすぎかしら？", 1, 3256, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110301, "夏の海には商機が\nあるはず…\nのがす手はないわね♪", 1, 3257, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110301, "あたしが作った\n日焼け止め…\nこれは売れそうね！", 1, 3258, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110301, "子どもたちのために\n浮き輪を用意して\nおかないと…", 1, 3259, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110301, "海っていいわよね！\nみんなで羽を伸ばしに\n行きたいわ！", 1, 3260, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110301, "波の音を聞いて\nのんびりするのも\n悪くないわね…", 1, 3261, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110301, "ただの砂遊びも\nあんたと一緒なら\n楽しいから不思議ね…", 1, 3262, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110301, "あんたがあたしの\nなんであれ、大切な\n存在ではあるのよね…", 1, 3263, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110301, "夏の海って\nすごい人よね…\n迷子にならないでよ？", 1, 3264, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110301, "ぼんやり海に浮かんで\n波に揺られるのも\n悪くないわよ？", 1, 3265, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110301, "ビーチで子どもたちと\n一緒に砂のお城を\n作りたいわ♪", 1, 3266, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110301, "海で食べる\n焼きそばパン…\nいいかもしれないわね", 1, 3267, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110301, "あたしの水着\n似合ってる？\nそう、ありがとう♪", 1, 3268, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110301, "あんたが居ると\nうれしい…って、\n子どもたちが\n言ってたわ！", 1, 3269, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 110301, "朝の浜辺って\nいいわよね\n散歩しに行きたいわ", 1, 3270, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 110301, "海の家のご飯って\nなんであんなに\nおいしいのかしら♪", 1, 3271, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 110301, "夜の海って、\nロマンチックよね。\nずっと眺めてたいわ…", 1, 3272, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110301, "やわらかくて\n気持ちいい…\nこのまま寝ちゃいそう", 1, 3274, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110301, "海に一緒に入れる\nぬいぐるみとか\nないかしら？", 1, 3275, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110301, "夏らしい\nいい香りね…\nなんの香りかしら？", 1, 3276, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110301, "あったかい…\n濡れた体を乾かすには\nちょうどいいわね♪", 1, 3277, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 110301, "泳いだ後の食事は\n普段の何倍も\nおいしく感じるわね！", 1, 3279, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 110301, "海に行く前にいろいろ\n調べなきゃね。備え\nあれば憂いなしよ！", 1, 3280, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 110301, "海の守り神？\nずいぶんかわいらしい\n神様ね♪", 1, 3282, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 110301, "このぐらい\n大きな貝があったら\n食費も浮くかしら？", 1, 3283, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110401, "浜辺で食うものって\nなんであんなに\nうまいのかな～", 1, 3284, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110401, "いてて…\nちょっと日焼け\nしすぎたかな", 1, 3285, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110401, "砂浜で走ると\nいいトレーニングに\nなるんだぜ", 1, 3286, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110401, "夏ならではの\n料理…か\n何がいいかな…", 1, 3287, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110401, "やっぱりここは\n涼しくて快適だな", 1, 3288, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110401, "泳ぎの練習だったら\nいつでも付き合って\nやるからな！", 1, 3289, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110401, "水着で部屋の中に\nいるのって\n変な感じだな…", 1, 3290, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110401, "うおっ！\nきゅ、急に\n触るなって…\nびっくりした", 1, 3291, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110401, "え、砂がついてた？\n払ってくれたのか\nありがとよ", 1, 3292, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110401, "夏バテしてないか？\nここで休んでけよ", 1, 3293, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110401, "…そう見られると\n照れるっていうか…", 1, 3294, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110401, "カキ氷とか\n食いたくないか？\n氷、削ってやるよ", 1, 3295, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110401, "おまえと\n夏の思い出ができて\nよかったよ", 1, 3296, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 110401, "ふう…\n朝一番で泳ぐの\nやっぱ気持ちいいな", 1, 3297, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 110401, "一番あちぃ時間だ\n水分補給は\nこまめにな！", 1, 3298, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 110401, "今日もお疲れ\n明日のパトロールも\nよろしくな", 1, 3299, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110401, "泳ぎ疲れたからな\n少し休憩だ…", 1, 3301, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110401, "か、かわいい…\nいや、なんでもねぇ！", 1, 3302, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110401, "この香り落ち着くな\nこういうのも\n悪くねーかも…", 1, 3303, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110401, "あったけえ…！\n泳ぎ終わった後って\nけっこう体冷えてよ…", 1, 3304, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 110401, "海で食べると\nまた一味違うよな", 1, 3306, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 110401, "夏のサバイバル特集…\nちょっと読んでみるか", 1, 3307, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 110401, "へー！　こいつ、\nよくできてんな！\nいいなあ、実物も\n乗れたらいいのに", 1, 3309, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 110401, "か、かわ…！\nいや、じゃなくて\nあ、あたしがここに\n寝ていいのか…？", 1, 3310, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110501, "故郷の花を飾っても\nいいかな～？\n部屋が楽しくなるよ～", 1, 3311, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110501, "水着で歩いてると\n泳ぎたくなるさ～\n我慢できないさ～！", 1, 3312, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110501, "夏が眩しいのは\nてぃーだがにこにこ\n笑ってるからさ～！", 1, 3313, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110501, "泳ぎ疲れたら\n涼しい木陰で\nお昼寝するさ～", 1, 3314, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110501, "クロールも\n犬かきも\n大得意さ～！", 1, 3315, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110501, "なんだか歌いたい\n気分さ～\nキミもどう～？", 1, 3316, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110501, "海の近くに住んで\nキミと歌って踊って\nそれが理想さ～！", 1, 3317, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110501, "私とキミ\nどっちがたくさん\n泳げるか\n競争するさ～！", 1, 3318, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110501, "キミも三線に興味が\nあるのかな～？\n私が教えるさ～！", 1, 3319, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110501, "嫌なことがあっても\n私と歌って踊れば\nなんくるないさー！", 1, 3320, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110501, "たまには時間を忘れて\n私とのーんびり\n海を眺めるさ～", 1, 3321, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110501, "ぶるぶるぶるっ！\n毛が濡れちゃっても\nへっちゃらさ～！", 1, 3322, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110501, "泳ぐ前は準備体操～！\n背中押してあげるから\nしっかりやるさ～！", 1, 3323, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 110501, "朝の浜辺は\nちゅらさんよ～\n一緒に歩こうよー！", 1, 3324, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 110501, "お昼ご飯は\n決まったかな～？\n私はタコライスさ～！", 1, 3325, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 110501, "星と波音の中で\n三線鳴らすのも\nオツなものさ～", 1, 3326, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110501, "ちょうどよかったさー\nゆっくり休んだら\nまた泳ぎに行くさ～！", 1, 3328, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110501, "砂浜に飾って\nおきたいさ～\nきっとかわいいさ～！", 1, 3329, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110501, "くんくん…はあ\n癒されるさ…\n故郷の夏の匂いさ～", 1, 3330, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110501, "おおー…！\n濡れた髪が\nみるみる乾いてくさ～", 1, 3331, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 110501, "ん～！　これ、\n海で食べたら\nもっとおいしいさ～", 1, 3333, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 110501, "海の生物図鑑ね～\nこの前みかけた魚も\n載ってるかな～？", 1, 3334, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 110501, "わぁ～！　みてみて！\n背中に乗れるさー！\nキミと一緒に\n乗りたいさ～！", 1, 3336, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 110501, "ふかふかさ～\n海の底の\n人魚になった気分さ～", 1, 3337, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105801, "あの人が\nわたしの騎士だったら\nいいのにな…", 1, 3338, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105801, "あなたのおかげで\nわたしの心はいつも\nお腹いっぱいです♪", 1, 3339, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105901, "許されるのならば、\n今すぐにでも、\n主さまのおそばに\n行きたいです…", 1, 3340, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105901, "主さま…\nお慕いしております…\nいつまでも、おそばに\n居させてください…", 1, 3341, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 106001, "なんか最近、\nあいつのことしか\n考えてない…\nあたし…どうしよう…", 1, 3342, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 106001, "そ、外に行くなら、\n連れていきなさいよね\nその…あたしが\n守ってあげるから…", 1, 3343, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110601, "部屋の中は涼しくて\n気持ちええどすなぁ\n天国やわぁ", 1, 3344, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110601, "クラゲはんって\nフリルいっぱいの\nドレスみたいやねぇ", 1, 3345, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110601, "外でセミはんが\n元気に鳴いてはるねぇ\n今日もええ天気やわぁ", 1, 3346, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110601, "こないに暑いと\n海に入って\n涼みたくなるわぁ", 1, 3347, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110601, "いつか\nマホマホ王国にも\n海の家作りまひょ♪", 1, 3348, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110601, "うちも\n付き合いますえ\n泳ぎの練習しまひょ♪", 1, 3349, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110601, "うちの里帰り\n王子はんも一緒に\n来まへんか？", 1, 3350, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110601, "王子はんは日焼けより\n色白美人の方が\n好きでっしゃろか？", 1, 3351, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110601, "うちと一緒に\n浮き輪で海、\nプカプカしまへん？", 1, 3352, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110601, "日が落ちたら\n線香花火\nやりたいわぁ♪", 1, 3353, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110601, "木陰でのんびり\nお昼寝…\n最高やね～", 1, 3354, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110601, "お祭ではぐれても\n王子はんなら見つけて\nくれはります？", 1, 3355, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110601, "楽しい夏やわぁ\nずっとこんな日が\nつづくとええね", 1, 3356, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 110601, "うちにしては早起き？\n暑くて目が早う\n覚めてもうたんどす", 1, 3357, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 110601, "お昼は冷たい\nおそうめんとか\nどうでっしゃろ", 1, 3358, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 110601, "夜は星がきれいやね\n二人で浜辺でも\n行きまへんか？", 1, 3359, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110601, "うーん、遊び疲れた\n身体にちょうどええ\nほっこり具合やわぁ", 1, 3361, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110601, "かいらしい子は\nみーんな\nマホマホ王国の\n住人どすえ♪", 1, 3362, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110601, "この香り…\n南国にいる気分やねぇ\n癒されるわぁ～", 1, 3363, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110601, "これで\n冷えた身体も\nポカポカやねぇ", 1, 3364, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 110601, "外はまだ暑いやろうし\nバテんよう\nしっかり食べんとね", 1, 3366, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 110601, "海の中にもお城が\nあるんやねぇ\n行ってみたいわぁ", 1, 3367, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 110601, "この子、ふかふかやね\nこのままマホマホ王国\n乗せてってくれはる？", 1, 3369, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 110601, "人魚姫になっても\n最後は王子はんと\nハッピーエンド\nどすえ～", 1, 3370, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107001, "真似したくなる\n人がここには\n大勢いますね", 1, 3371, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107001, "偽物を一人\n紛れこませました\n…冗談です", 1, 3372, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107001, "動物に変身して\nその辺りを\n散歩してきます", 1, 3373, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107001, "失った記憶は\nどの程度回復するの\nでしょうね", 1, 3374, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107001, "人や物だけでなく\nいずれは世界すら\n真似てみせます", 1, 3375, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107001, "次は彼に\nどんなことを\n教えましょうか…", 1, 3376, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107001, "知識はともかく\n私の力はまだ\n必要なようですね", 1, 3377, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 107001, "どうしました？\n…何でもない？\n…そうですか", 1, 3378, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107001, "退屈しています\n芸でもして\n楽しませなさい", 1, 3379, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107001, "研究のためです\n少し話し相手に\nなりなさい", 1, 3380, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107001, "何か真似して欲しい？\n私の能力を何だと\n思っているのですか", 1, 3381, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 107001, "貴方にはまだ色々と\n教えるべきことが\nありますね", 1, 3382, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 107001, "随分多くの女性と\n知り合いなのですね\n驚きました", 1, 3383, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 107001, "…\nおはようございます\n…まだ朝ですよ", 1, 3384, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 107001, "食事にします\n…どうしました？\n早く持ってきなさい", 1, 3385, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 107001, "早く寝なさい\n夜更かしすると\n背が伸びませんよ？", 1, 3386, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 107001, "マサキより\n座り心地は\nいいですね", 1, 3388, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 107001, "意外に複雑な\n構造ですね", 1, 3389, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 107001, "真似るなら\n香りまで\n再現しなくては", 1, 3390, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 107001, "発火現象の\n模倣…なかなか\n難しいものです", 1, 3391, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 107001, "偽物や分身を作るには\nエネルギーが\n必要不可欠なのです", 1, 3393, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 107001, "暇つぶしには\nちょうどよさそうです\n…", 1, 3394, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110701, "学院かぁ…\n友達の作り方の\n授業はないかな？", 1, 3396, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110701, "学院でぼっちに\nなったときのために、\n避難場所を\n探しておかないと…", 1, 3397, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110701, "ぼっちの私が学院…\nな、なんだか\n目まいがしてきました", 1, 3398, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110701, "クラスメイトに\n話しかける練習…\nむ、難しすぎます…！", 1, 3399, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110701, "目指せ友達100人…\n無理かな…10人…\nや、やっぱり一人！", 1, 3400, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110701, "あなたと学院生活が\n送れるなんて、\nとっても\nうれしいです！", 1, 3401, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110701, "えへへ…\n学院でも、\nあなたと一緒に\n居られてうれしいな♪", 1, 3402, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110701, "が、学院ってあんなに\n人が居るんですね…\n友達作りのイメトレを\nやり直さないと！", 1, 3403, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110701, "私にとって\n部活動とはＢＢ団！\n今日もたくさん\n活動しましょうね！", 1, 3404, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110701, "休み時間は寝たふり…\nご飯はおトイレ…\nぼっち対策は完璧です", 1, 3405, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110701, "学院で友達を作る\n勉強は完璧ですよ！\n購買部でパンを買って\n渡すんですよね！", 1, 3406, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110701, "学院でも、\nBB団の活動を\nしていきましょうね♪", 1, 3407, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110701, "ぼっちに学院生活は\n厳しいですね…でも、\nあなたと一緒なら\n乗りこえられそうです", 1, 3408, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 110701, "もうこんな時間…\nあの…一緒に学院に\n行ってくれませんか？", 1, 3409, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 110701, "お昼ですね！\n言ってくれれば、\nパンでもお握りでも\n買ってきますよ！", 1, 3410, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 110701, "今日もぼっち…\nいつか友達と一緒に、\nパジャマパーティー\nしたいなぁ…", 1, 3411, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110701, "森とは違う\n心地良さが…このまま\n眠っちゃいそうです…", 1, 3413, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110701, "かわいい…この子を、\n話す練習の相手役に\nしてみようかな？", 1, 3414, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110701, "いい香り…\nいつ友達ができても\nいいように、私の\n部屋にも置こうかな", 1, 3415, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110701, "こ、これは…\nぼっちで\n日陰者の私には\n暖かすぎます！", 1, 3416, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 110701, "おいしい…！\nお弁当にして、学院に\n持っていけないかな？", 1, 3418, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 110701, "学院の授業に\nついていくためにも、\n勉強しておこうかな…", 1, 3419, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110801, "あんた、何してんの。\nいいけど、なんでも", 1, 3421, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110801, "邪魔。どいて。\nいや怒ってないし。\nコレがフツーだから", 1, 3422, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110801, "いいけど、\n散らかしめの部屋だと\n片付けたくなるよね…", 1, 3423, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110801, "暇なときは明日の\nコーデ考えてるよ。\n考えるのはタダだし", 1, 3424, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110801, "昔よくシオリン\n構いすぎてハツネに\n文句言われたなァ…", 1, 3425, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110801, "なんかさ。あんたって\n謎に女子トモ多いよね\n…いいけど、べつに", 1, 3426, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110801, "ねえ。あのさあ。\nあんた弁当で好きな\n具とかってあんの", 1, 3427, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110801, "あ？　何。なんか用。\nや、ないならないで\nいいけど、べつに", 1, 3428, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110801, "ごめ、ちょっと\n黙っててくんない。\n明日の試験ヤバだから", 1, 3429, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110801, "歩き方が静か？\n癖になってるんだよね\n足音を殺すの", 1, 3430, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110801, "うちの血液型？\n大雑把のОだけど。\nなんか文句あんの", 1, 3431, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110801, "ひゃんっ！\nや、だからさ…急に\n話しかけんなっての", 1, 3432, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110801, "な、何…あんまジッと\n見ないでくんない…\nなんか、その、ハズい", 1, 3433, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 110801, "早起きは得意な方だと\n思うけど。昔から\nそれがデフォだったし", 1, 3434, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 110801, "こんな時間か…\n昼ごはん\nサクッと作っちゃお", 1, 3435, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 110801, "あ？　夜遊び？\nはッ…そんなん\n５歳で卒業したっての", 1, 3436, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110801, "あーヤバじゃんコレ…\nふかついてる場合\nじゃないっつーのに…", 1, 3438, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110801, "なんだこれ\nかわいいかよ", 1, 3439, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110801, "ふーん。\nこの匂い…\nなんかいいじゃん", 1, 3440, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110801, "暖炉はアドじゃん。\n煮物、焼き物、蒸し物\nいろいろ作れそう", 1, 3441, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 110801, "何コレ\n食べ物困んないじゃん\nうちにも一台くんない", 1, 3443, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 110801, "本かァー…んー\n学校の勉強だけで\nいいかなァ…", 1, 3444, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111101, "イタズラってなにを\nしたらいいのかな？", 1, 3446, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111101, "カップケーキ\n上手に作れました\n食べてくれるかな…", 1, 3447, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111101, "あの人、\nお菓子くれるかな？\nくれなかったら…ふふ", 1, 3448, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111101, "オバケ…いないよね？\n怖くなんてないけど！", 1, 3449, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111101, "ハロウィンは\n楽しいけどオバケの\nお祭りなんだよね…", 1, 3450, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111101, "にゃ～、にゃお～！\nちゃんと怖いかな？\n早く帰ってこないかな", 1, 3451, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111101, "あの人のことだから\nお菓子欲しがるよね…\n準備はバッチリです♪", 1, 3452, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111101, "えへへ。猫さんのお耳\nよく出来てるでしょ？", 1, 3453, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111101, "触りましたね？\nイタズラしましたね？\nへんた～い！", 1, 3454, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111101, "フー！　頭を撫でたら\n怒りますよ！　あれ？\n猫みたいでした？", 1, 3455, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111101, "きゃっ！　も、もう、\n脅かさないでください\n怖くありませんけど！", 1, 3456, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111101, "お菓子ですか？\nありがとうございます\nハッピーハロウィン♪", 1, 3457, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111101, "ゴロゴロ…\nね、猫さんの真似です\n…似てましたか？", 1, 3458, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 111101, "さぁ、お菓子作りを\n始めますよ。下準備は\n大事ですからね！", 1, 3459, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 111101, "今、つまみ食い\nしましたね！？　あっ\nおやつの時間でしたか", 1, 3460, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 111101, "さぁ、ハロウィンの\n始まりです…ふぁ…\nね、眠くありません！", 1, 3461, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111101, "ちょっと疲れたかも…\nパーティの時間まで\nひと休みしなきゃ", 1, 3463, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111101, "私も今、こんな感じ\nなのかな？　こんなに\nかわいいといいなぁ", 1, 3464, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111101, "甘い匂いがいっぱい…\nお菓子の香りかな？", 1, 3465, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111101, "温かくなると、\n丸まりたくなって…\n…猫になっちゃう！？", 1, 3466, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 111101, "ニンジンもこれくらい\nおいしければ\nいいのに…", 1, 3468, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 111101, "ハロウィンの本、\nオバケがたくさん\n載ってて怖いかも…", 1, 3469, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 111101, "美味しそうなお菓子が\nいっぱい…これなら\nイタズラされませんね", 1, 3471, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111201, "たくさんいたずら\nできるから\nハロウィン大好き！", 1, 3473, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111201, "ハロウィンだから\nいつもよりすごい\nいたずらしたいな～！", 1, 3474, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111201, "カボチャを綺麗に\nくり抜いてすごく怖い\nランタン作っちゃお！", 1, 3475, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111201, "お菓子がたくさんで\nどれから食べるか\n迷っちゃうな～！", 1, 3476, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111201, "どうすればみんなを\nびっくりさせられる\nかな～？", 1, 3477, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111201, "カボチャ見てたら\nお腹空いて来たな～\nお菓子食べちゃお♪", 1, 3478, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111201, "にいちゃんには\n特別ないたずらを\n用意してあげるね！", 1, 3479, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111201, "カボチャにクラッカー\nを仕掛けて…わっ！？\nみ、見ちゃダメだよ！", 1, 3480, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111201, "ん？　なぁに？\nお菓子ほしいの？\nもう、しかたないな～", 1, 3481, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111201, "にいちゃんの\n面白い仮装が\n見てみたいな～！", 1, 3482, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111201, "お菓子くれたお礼に\nとびっきりの\nいたずらしちゃうね！", 1, 3483, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111201, "にいちゃんは\nハロウィンでも最高の\nいたずら相手だよ！", 1, 3484, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111201, "にいちゃんと一緒に\nハロウィンパレード\n歩きたいな～！", 1, 3485, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 111201, "おはようにいちゃん！\n仮装して出かける\n準備しなきゃ！", 1, 3486, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 111201, "お腹すいたな～\nもらったお菓子\n食べちゃだめかな？", 1, 3487, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 111201, "たくさんいたずらして\nもうクタクタ～\nミソギもう寝るね…", 1, 3488, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111201, "やわらか～い…\nここでひと休みして\nまたいたずらしよう！", 1, 3490, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111201, "かわいいもので\n油断させてわっ！\nってするのどうかな？", 1, 3491, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111201, "いいにお～い！\nいたずらのアイデアが\nわいてくるよ！", 1, 3492, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111201, "もらったお菓子\nあったかくしたら\nおいしいかな～？", 1, 3493, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 111201, "お菓子ばっかりじゃ\n飽きちゃうもんね！\nご飯も食べよう！", 1, 3495, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 111201, "本のなかに\nいたずらのヒント\nないかな～？", 1, 3496, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 111201, "お菓子がたくさん！\n一人で食べていいの？\nやっぱりだめか～！", 1, 3498, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101101, "お勉強は順調ですよ\nやっぱりお兄ちゃんの\nおかげなんでしょうか", 1, 3500, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101101, "お兄ちゃんとの\n思い出の場所\nまた行きましょうね", 1, 3501, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111301, "えへへ～♪\nハロウィンの\nミイラさんうさぎさん\nぴょんぴょ～ん！", 1, 3502, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111301, "るんたった～\nハロウィンハロウィン\n楽しいな～♪", 1, 3503, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111301, "ハロウィンって\nどんなお菓子を\nもらえるのかな～？", 1, 3504, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111301, "お菓子をもらう\n練習をしなくちゃ！\nトリックオア\nトリート～♪", 1, 3505, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111301, "おにいちゃんと\nハロウィンするの\nミミ楽しみだな～♪", 1, 3506, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111301, "おにいちゃんと\nいっしょにお菓子を\n食べたいな～", 1, 3507, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111301, "おにいちゃんと\n毎日ハロウィン\nできたらいいな～♪", 1, 3508, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111301, "えへへ～\nおにいちゃん！\nハッピーハロウィン♪", 1, 3509, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111301, "おにいちゃんも\nいっしょに\nハロウィンパレード\nしようね～！", 1, 3510, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111301, "ミイラさんうさぎさん\nだぞ～…えへへ\nミミこわかったかな？", 1, 3511, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111301, "おにいちゃん！\nハロウィンのお菓子\nおいしいね～♪", 1, 3512, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111301, "おにいちゃんと\nいっしょなら\nおばけさんも\nこわくないよ！", 1, 3513, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111301, "ミミね\nハロウィンも\nおにいちゃんも\nだ～いすき♪", 1, 3514, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 111301, "おひさまさんも\nハロウィンの\nおようふく\n着たいかな～？", 1, 3515, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 111301, "ねえねえおにいちゃん\nお昼ごはんはもらった\nお菓子でもい～い？", 1, 3516, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 111301, "まだハロウィン…？\nうぅん…眠くないよぅ\nでもちょっと…ふぁ～", 1, 3517, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111301, "ふかふか～♪\nおばけさんが\n中にいるのかな～？", 1, 3519, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111301, "わぁ、かわいい～♪\nミミといっしょに\nあそぼ～！", 1, 3520, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111301, "ふわぁ～\nミミ、このにおい\nだ～いすき♪", 1, 3521, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111301, "あったかいね～♪\nおばけさんも\nポカポカしてるかな？", 1, 3522, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 111301, "おいしい～♪\nおにいちゃんも\n食べようよー！", 1, 3524, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 111301, "この本\nおにいちゃんと\nいっしょに\n読みたいな～", 1, 3525, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 111301, "ハッピーハロウィン♪\nえへへ、お菓子が\nいっぱいだね～！", 1, 3527, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111401, "ここ、好きだよ。\nみんな優しいから…", 1, 3529, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111401, "なんかさみしいな", 1, 3530, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111401, "知らない人怖いな", 1, 3531, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111401, "お部屋おっきいねー", 1, 3532, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111401, "ルナね…\nお昼寝したいかも", 1, 3533, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111401, "おにいさん！\n今日は何して遊ぶの？", 1, 3534, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111401, "あのね、\nお料理教えてほしい！", 1, 3535, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111401, "あっ、おにいさんだ！", 1, 3536, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111401, "ルナは元気だよ！\nいいこだもん！", 1, 3537, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111401, "今日は何しようかな", 1, 3538, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111401, "ルナと遊んでよ！", 1, 3539, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111401, "ねーねー、\nルナとお出かけして！", 1, 3540, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111401, "おにいさんと、\n遊びたいなー！", 1, 3541, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 111401, "ふふっ！\nなんかまぶしいね！", 1, 3542, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 111401, "ルナ、お腹空いたな", 1, 3543, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 111401, "どこに行こうかな？", 1, 3544, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111401, "ルナ、これ好きだな", 1, 3546, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111401, "かわいい！　好き！", 1, 3547, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111401, "なんかいい匂い！", 1, 3548, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111401, "あったかいねー", 1, 3549, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 111401, "ふふっ\nおいしいね", 1, 3551, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 111401, "ルナ、\n眠くなっちゃう…", 1, 3552, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105201, "えへへ。あの人と\nランドソルでデート\nできたらうれしいな♪", 1, 3554, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105201, "あなたとなら\nどんなことだって\nできる気がするわ♪", 1, 3555, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106501, "あーあ、退屈だな\nカレでも探しに\nいこうかな", 1, 3556, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106501, "この前のケンカは\n最高に燃えたなぁ\n…拳が疼いてきたぜ", 1, 3557, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106501, "一体、この世界は\nどうなっちまったって\nいうんだ…？", 1, 3558, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106501, "もう仕事の時間か\nボスも人使いが荒いぜ\n…ま、いいけどさ！", 1, 3559, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106501, "オレたち以外に\nドラゴン族はあと何人\nいるんだろうな…", 1, 3560, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106501, "カレの真剣な顔\n面白かったな\nまた遊びたいぜ", 1, 3561, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106501, "どうしてカレがそばに\nいると、腹の底から\n力が湧いてくるんだ？", 1, 3562, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106501, "よっす！\nなんかオイシイ話でも\nあったりしない？", 1, 3563, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106501, "なあ、オレと遊ぼうぜ\n心配すんなよ。危ない\nことはよしとくからさ", 1, 3564, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106501, "どうした。オレに\nケンカ売ろうってか？\n…ははは、冗談だよ！", 1, 3565, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106501, "ちょうど退屈してた\nところだ、特訓に\n付き合ってくれよ", 1, 3566, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106501, "ったくよぉ…\nオレがかわいいなんて\n冗談はもうよせよ？", 1, 3567, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106501, "次の仕事も一緒に\n来るだろ？　断っても\n連れて行くけどな！", 1, 3568, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 106501, "おはよう！　今日の\n予定はまずケンカして\nで、その後もケンカだ", 1, 3569, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 106501, "よっす！\n腹ごしらえが済んだら\nオレと遊ぼうぜ！", 1, 3570, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 106501, "夜は危ないから\nあんま出歩くな？\n…だから面白えんだろ", 1, 3571, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106501, "次のケンカに備えて\n今は休んでおくか…\n全力で暴れたいからな", 1, 3573, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106501, "ボスの飼ってる\nラーゴが、中に\n入ってるんじゃ？", 1, 3574, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106501, "ああ、たぎる匂いが\nする…ひと暴れ\nしたくなってきた！", 1, 3575, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106501, "おおぉ…\nオレの拳もなんだか\n熱くなってきたぜ…！", 1, 3576, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 106501, "いつでも全力で\nケンカするために\n力を蓄えないとな", 1, 3578, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 106501, "やっぱりな…オレが\n知ってる地図とは\nまるで違ってやがる", 1, 3579, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111501, "さぁプレゼントで\n埋め尽くしてやろう！\n大いに喜ぶがいいよ！", 1, 3581, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111501, "メリークリスマス☆\n聖夜の宴だ\n派手に楽しむとしよう", 1, 3582, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111501, "煙突から入る？\nいいや、ワタシは\nいつでも正面からさ♪", 1, 3583, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111501, "サンタクロースの\nお出ましだ！\n歓迎するがいいよ♪", 1, 3584, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111501, "飾り付けが足りん！\nワタシが演出して\nやろうじゃないか！", 1, 3585, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111501, "人の喜ぶ顔を見るのも\n悪くはないな…おっと\nワタシらしくないな☆", 1, 3586, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111501, "プレゼントはまだか？\nワタシを喜ばせたら\nお返しは弾むぞ♪", 1, 3587, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111501, "ワタシを呼んだか？\n退屈な要件だったら\n覚悟しておくことだ", 1, 3588, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111501, "なんだ？\nワタシへの挑戦か？\nよし、受けた♪", 1, 3589, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111501, "おっと、当たらんよ\nワタシの乱数聖域は\n絶対だからな…♪", 1, 3590, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111501, "なんだ、オマエも\n暇なのか？\n奇遇だな、ワタシもさ", 1, 3591, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111501, "なにか愉しいことでも\nしようじゃないか\nさぁ、何をする？", 1, 3592, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111501, "こうもワタシに\n気安く触れるのは\nオマエくらいだよ…♪", 1, 3593, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 111501, "朝だろうと関係ない！\nさぁ愉しく苛烈に\n騒ごうじゃないか♪", 1, 3594, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 111501, "調子の出てくる時間だ\nしっかりワタシを\n愉しませてくれよ♪", 1, 3595, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 111501, "眠いなどと言わせんぞ\nまだまだ一日は\n終わらんからな！", 1, 3596, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111501, "いい具合じゃないか\nこのまま持って帰って\nしまおうか…♪", 1, 3598, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111501, "なかなか愛らしい\n造形をしているな？\nワタシには負けるがな", 1, 3599, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111501, "ほう、悪くない香りだ\n戦いの匂いをうまく\n隠してくれそうだ…♪", 1, 3600, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111501, "体が温まってくると\nひと暴れしたくなるな", 1, 3601, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 111501, "ワタシの舌は厳しいぞ\n…ふーむ、なるほど\n認めざるをえんな", 1, 3603, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 111501, "クリスティーナさんは\n知識の収集も\n欠かさないんだよ", 1, 3604, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 111501, "これは驚いたな\nだがもっと、もっとだ\nワタシを退屈させるな", 1, 3606, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 111501, "こんな戯れだって\n軽くこなしてしまう\nクリスティーナさんさ", 1, 3607, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111601, "メリークリスマス！\nさあ、パーティーを\n盛り上げるよ！", 1, 3608, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111601, "衣装一つで全然\n気分が違うね！\n最高のクリスマスに\nなりそうだよ！", 1, 3609, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111601, "ツリーのてっぺんの\n星って、なんだか\n欲しくなるんだよね", 1, 3610, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111601, "めいっぱい\nクリスマスを楽しむよ\n遊びだって本気\nなんだからね！", 1, 3611, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111601, "赤、白、緑、金\nクリスマスカラー\nってお洒落だよね", 1, 3612, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111601, "雪、降らないかな？\nホワイトクリスマス\nってちょっと\n憧れるなぁ", 1, 3613, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111601, "キミと過ごすこの\n楽しい時間がずーっと\n続けばいいのにな", 1, 3614, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111601, "クリスマスライブ\nチケット用意するから\n絶対来てよね！", 1, 3615, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111601, "カンパーイ！\nジュースだけど\nちょっと大人の\n気分だね", 1, 3616, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111601, "クリスマス衣装どう？\nかわいいでしょ\nあっ、どこ見てるの！", 1, 3617, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111601, "ねえ、後で街に\n行こうよ\n大きなツリーを\n一緒に見たいんだ♪", 1, 3618, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111601, "ねえ、ダンスの練習に\n付き合ってくれない？\n大丈夫、手取り足取り\n教えてあげるから♪", 1, 3619, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111601, "来年のクリスマスも\n隣にキミが\nいてくれたら\nうれしいのにな", 1, 3620, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 111601, "クリスマスって朝から\nドキドキしちゃうね\nなんだか子どもみたい", 1, 3621, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 111601, "パーティーの準備も\n大分進んだね\n一息ついて\nランチにしよっか", 1, 3622, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 111601, "静かな夜だね\n心臓の音まで\n聞こえちゃいそう…", 1, 3623, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111601, "あ～、この絶妙な\n柔らかさ…\nいい仕事してるなぁ…", 1, 3625, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111601, "アイドル顔負けの\nかわいさ、ズルい！\nでもかわいい！", 1, 3626, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111601, "ん～、落ち着くなぁ\n今日はゆっくり\n眠れそうだよ", 1, 3627, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111601, "ぬくぬく～\nどうしよう、ここから\n動きたくないなぁ", 1, 3628, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 111601, "ダンスの練習もしたし\nちょっとくらいなら\nいいよね！", 1, 3630, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 111601, "アイドルといえど\nかわいいだけじゃダメ\n知識も必要だよね", 1, 3631, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 111601, "面白ーい！\nこの演出、ライブでも\nできないかな？", 1, 3633, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 111601, "もふもふ～♪\n寒い日には最高の\n抱き心地だね！", 1, 3634, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101801, "彼ともっとお話し\nしたいな…もう少し\n補習を増やそうかしら", 1, 3635, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101801, "君のこともっともっと\n教えて欲しいな。私に\n補習授業してくれる？", 1, 3636, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111701, "この姿ならあやつも\n子ども扱いできまい！\n…じゃがなぜか、少し\n物足りないような…", 1, 3637, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111701, "時間もあるし、\n聖夜の宴の\n準備をするかのう♪", 1, 3638, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111701, "…わらわの知る聖夜と\n随分違うが、宴は宴\n楽しませて\nもらおうかのう♪", 1, 3639, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111701, "大人の姿で\nできること…ふむ\n少しあやつを\nからかってやるか♪", 1, 3640, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111701, "…クリスマスは\n恋人たちの夜！？\nち、近頃の若いもんは\n破廉恥なのじゃ！", 1, 3641, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111701, "…あやつがおらんと、\n宴も物足りぬな\n…馬鹿者が\n早く帰ってこい…", 1, 3642, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111701, "誰も見ておらぬな…？\n…よし、あやつへの\nプレゼントを\n準備するとしようか！", 1, 3643, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111701, "クリスマスの\nチキンにケーキ…\nべ、別に楽しみになど\nしておらぬぞ！", 1, 3644, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111701, "この姿では、わらわの\n頭をなでるのは\n難しかろう…べ、\n別に残念ではないわ！", 1, 3645, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111701, "メリー\nクリスマスじゃ！\nさあ、わらわのために\n宴の準備をするのじゃ", 1, 3646, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111701, "そんなにこの衣装が\n気になるのか？\n…だったら、直に\n触ってみるがよい♪", 1, 3647, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111701, "いつもと逆に、お主を\n子ども扱いしてやるか\nほれ、膝枕をしてやる\nからそばに来い♪", 1, 3648, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111701, "…せ、聖夜の予定は\n空けておくのじゃぞ？\nなんでって…\nな、なんでもじゃ！", 1, 3649, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 111701, "もう朝か…聖夜の宴が\n楽しみで…ではなく！\n準備のせいで、\nあまり眠れんかったぞ", 1, 3650, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 111701, "…ずっと雪が\n降っておれば、\n陽光が強いこの時間も\n怯えずにすむのにのう", 1, 3651, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 111701, "時は来た…\nさあ眷属よ！\nわらわと共に、\n聖夜の宴を楽しもうぞ", 1, 3652, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111701, "良い椅子ではないか\nここから聖夜の宴を\n眺めるのもよいかもな", 1, 3654, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111701, "か、かわい…こほん\nなかなか良い\nデザインをしておるな", 1, 3655, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111701, "ふむ、良い香りじゃ\n聖夜の宴でも\nこれを使うとしよう", 1, 3656, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111701, "これが癒し…はっ！\nあやうく浄化される\nところであった…", 1, 3657, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 111701, "ニ、ニンニクは\n入っておらぬよな？\n…うむ、美味じゃ♪", 1, 3659, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 111701, "聖夜は恋人たちの夜…\n…き、気になるわけ\nではないが、\n少し調べてみるか", 1, 3660, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 111701, "ほう…これは面白い\n魔法の使い方じゃな\nまるで本物の\n演劇のようじゃ", 1, 3662, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 111701, "こ、この程度の\nかわいさに誘惑される\nわらわでは…\nふわもこなのじゃ～♪", 1, 3663, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112001, "おせち料理も\n魔物尽くしだったり\nしないわよね！", 1, 3664, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112001, "初詣で何をお祈り\nしようかしら…\n定番でいいわよね？", 1, 3665, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112001, "初日の出って\n特別な感じがして\nいいわよね…", 1, 3666, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112001, "寒い中並んで飲む\n甘酒って不思議なほど\nおいしいわよね", 1, 3667, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112001, "羽根つきで勝負…\nあいつにだったら\n勝てるかしら？", 1, 3668, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112001, "年賀状になんて書こう\n…いろいろと\n恥ずかしくなるのよね", 1, 3669, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112001, "今年もあいつと\n居られること…\n結構うれしいわ", 1, 3670, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112001, "何じっと\n見てんのよ？\nお年玉はあげないわよ", 1, 3671, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112001, "おせち料理一緒に\n食べる？\n結構おいしいわよ", 1, 3672, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112001, "羽根つきで負けたら\n顔に墨塗られるのよ\n覚悟しなさい！", 1, 3673, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112001, "門松飾るから\nちょっと手伝って\nくれる？", 1, 3674, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112001, "あんたと一緒に\n初詣行くのも悪くない\n…かも", 1, 3675, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112001, "初詣で何をお祈り\nしたか？　うっさい！\n内緒よ！内緒！", 1, 3676, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 112001, "もう朝なの…\n正月くらいゆっくり\n寝ていたいわよね…", 1, 3677, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 112001, "おせちに飽きたら\n次は何を食べよう\nかしら…", 1, 3678, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 112001, "そろそろ寝ましょ\nいい初夢が見られると\nいいわね", 1, 3679, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112001, "この格好でいるのも\nなかなか疲れるわね\n休憩～", 1, 3681, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112001, "か、かわいい…う～ん\n鏡餅も顔を描けば\nかわいくなるかしら？", 1, 3682, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112001, "いい匂いね\n落ち着いて\n眠れそう…", 1, 3683, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112001, "ここでお餅焼いたら\n一段とおいしく\nなりそうね", 1, 3684, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 112001, "正月に食べるものは\nなんでもおいしく\n感じちゃうわね！", 1, 3686, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 112001, "魔物を使わない\nおせちを\n調べておかなきゃ…", 1, 3687, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112101, "新年を気持ちよく\n過ごせるように\nお掃除がんばります！", 1, 3689, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112101, "神殿に通っていたら\nいつの間にか足腰が\n鍛えられたような…", 1, 3690, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112101, "神殿付近は屋台も\nあるので、良い香りが\n漂ってくるんですよ～", 1, 3691, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112101, "おせちを作れるように\nなれば、子どもたちも\n喜んでくれるかな…？", 1, 3692, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112101, "コホォォォォォ…！\nふぅ、深呼吸すると\n落ち着きますね♪", 1, 3693, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112101, "お仕事以外の時も\nあなたと…い、いえ！\nなんでもないです！", 1, 3694, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112101, "あなたと一緒だと\nなんだかポカポカ\nしてくるんです…", 1, 3695, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112101, "この格好をしていると\nなんだか神殿にいる\n気分になりますね", 1, 3696, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112101, "そういえば神殿で\n甘酒をもらったんです\nあなたも飲みますか？", 1, 3697, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112101, "あっ、髪飾りが\nズレちゃってました！\nいけないいけない…", 1, 3698, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112101, "ひゃわっ！？\nく、くすぐったり\nしないでください～", 1, 3699, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112101, "そ、そんなに\n見つめられると\nて、照れちゃいます…", 1, 3700, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112101, "今度、私が考えた\n運気上昇のおまじない\nかけてあげますね♪", 1, 3701, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 112101, "おはようございます\n神殿のお手伝いに行く\n準備もしなきゃですね", 1, 3702, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 112101, "お昼になりましたし\nご飯は私が作…\nえっ、ダメですか！？", 1, 3703, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 112101, "明日も早いですし\nそろそろ寝る支度を…\nふわぁ…", 1, 3704, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112101, "お掃除も終わったので\n少し休憩を…ふわぁ…\n急に眠気が…", 1, 3706, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112101, "新年を気持ちよく\n過ごせるようこの子も\nきれいにしましょう♪", 1, 3707, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112101, "神殿のお香のような\n香りだと落ち着いて\n過ごせそうですよね", 1, 3708, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112101, "ぽかぽかですね～…\n甘酒も飲んで体の\n芯から温めましょう", 1, 3709, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 112101, "今年こそ\n料理も上手に…\nもぐもぐもぐもぐ…", 1, 3711, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 112101, "失敗続きだと\n逆に運が良くなる…\nほ、本当ですかぁ…？", 1, 3712, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103401, "キミにならもう\nどんな私を見られても\n大丈夫かな…♪", 1, 3714, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103401, "なになにどしたのぉ？\nお姉さんが撫でたげる\nこっちにきなさぁい♪", 1, 3715, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111901, "わたくしの舞を\n主さまは喜んで\nくださるでしょうか…", 1, 3716, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111901, "年を越したあとに\n食べるおそばも\nおいしいです", 1, 3717, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111901, "甘酒を飲むと\n心まで温まる\n気がいたします", 1, 3718, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111901, "主さまのために\n真心を込めたおせちを\nご用意しなくては", 1, 3719, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111901, "おみくじを引くのは\nドキドキしつつも\n楽しいものです", 1, 3720, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111901, "主さまの温もりは\n心まで温めてくれる\nようです", 1, 3721, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111901, "うまくできなくとも\n心を込めて精いっぱい\n舞いましょう", 1, 3722, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111901, "主さま、この晴れ着は\n似合っている\nでしょうか？", 1, 3723, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111901, "お餅はゆっくりと\n召し上がって\nくださいまし", 1, 3724, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111901, "主さまのお顔の\n福笑いなら、完璧に\nできる気がいたします", 1, 3725, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111901, "主さまはどのような\n初夢をご覧になった\nのでしょう？", 1, 3726, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111901, "甘酒の飲み過ぎは\nいけませんよ？\nめっ、でございます", 1, 3727, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111901, "二人羽織とは\nあんなにも暖かい\nものなのですね…", 1, 3728, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 111901, "おはようございます\n寒い季節の朝日は\nとても美しいですね", 1, 3729, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 111901, "おいしくとも\n食べ過ぎは正月太りの\nもとでございます", 1, 3730, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 111901, "いい夢が見られるよう\n暖かくして\n眠るといたしましょう", 1, 3731, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111901, "ふかふか…\nこのまま\n寝てしまいそうです", 1, 3733, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111901, "あなたと一緒に\nお正月遊びを\n楽しんでみたいです", 1, 3734, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111901, "神殿の独特な香りを\nおうちに持ち込むのは\nどうでしょうか", 1, 3735, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111901, "風邪をひきませんよう\n暖かくいたしましょう", 1, 3736, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 111901, "食べ過ぎは\n正月太りの\nもとだというのに…", 1, 3738, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 111901, "主さまのために\n参拝の作法を\n予習しておきましょう", 1, 3739, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112201, "…この杖、変身前には\n無いパーツがあるけど\nこれはどこから…うん\n考えないようにしよう", 1, 3741, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112201, "フ、フリフリの\nかわいい衣装…はっ！\n素に戻ってはダメだ！", 1, 3742, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112201, "魔法探偵\nミスティ★カスミ！\n魔法の力で捜査開始！\n…練習終わりっと", 1, 3743, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112201, "怪しい視線を感じる…\nまさか、近くに\n闇の力を持つ人が！？", 1, 3744, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112201, "時間もあるし、\n魔法少女の修行を…\nじゃ、じゃなくて！\n探偵の仕事をしよう！", 1, 3745, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112201, "助手くんとずっと\n一緒に居られるし、\n魔法少女も\n案外悪くないかも♪", 1, 3746, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112201, "魔法少女の訓練のため\n助手くんの所に行こう\nかな…あ、あくまで\n訓練のためにね！", 1, 3747, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112201, "…髪型が急に\n変わって驚いた？\nき、気になるなら…\n少し触ってみるかい？", 1, 3748, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112201, "助手くんは魔法少女に\nなりたいのかい？\nその場合は魔法騎士…\nいや、魔法助手とか？", 1, 3749, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112201, "頭に浮かぶ呪文…\n強制変身…助手くん\n魔法少女とは\n呪いか何かなのかな？", 1, 3750, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112201, "あ、あまり触らないで\nくれ…魔法少女になる\nと感覚が鋭くなって、\n耳も敏感に…ひゃう！", 1, 3751, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112201, "や、やっぱりこの\n格好は変かな…え、\nかわいい？　じゃあ…\nいや、でも、う～ん", 1, 3752, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112201, "…この変身は助手くん\nのせいでもあるんだよ\nだから…せ、責任を\n取ってもらうからね！", 1, 3753, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 112201, "おはよう助手くん\nさあ、そろそろ\n街のパトロールに\n行こうじゃないか！", 1, 3754, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 112201, "もうお昼…助手くん、\nそろそろ本業に戻ろう\n…その顔は何かな？\n私の本業は探偵だぞ！", 1, 3755, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 112201, "夜だけど、私は\nもう少し仕事をするよ\n魔法少女の強い体は\nこんなときに便利だね", 1, 3756, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112201, "気持ちいい…\nこの癒しを、魔法と\n論理の二つの観点から\n解明したいね…", 1, 3758, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112201, "ダ、ダメ！！\nここで感情のままに\n飛びついたら、私の\nイメージがぁ～！", 1, 3759, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112201, "心が安らぐ香りだね…\n…今なら、魔法少女も\nすんなりと受け入れ…\nてはダメだっ！", 1, 3760, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112201, "あったかい…\n魔法少女だろうと\n休息は必要だし、少し\n休んでいこうかな", 1, 3761, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 112201, "魔法少女も探偵も、\nやっぱり体が資本！\n食べられるときに\n食べておかないとね♪", 1, 3763, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 112201, "…魔法少女\nセリイちゃんの本は\nどこにあるのかな？", 1, 3764, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112301, "魔法少女かぁ…\nせっかくだし思う存分\n体を動かしてみたいな", 1, 3766, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112301, "ニャットちゃん…\nこの格好を見て\n私って気づくかな？", 1, 3767, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112301, "ふふ…\n今日はなんだか\n調子がいいかも", 1, 3768, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112301, "魔法は便利だけど\nあまり頼りすぎちゃ\nダメだよね", 1, 3769, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112301, "さっき街で小さな子に\n声かけられちゃった\n照れるけどうれしいな", 1, 3770, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112301, "あの人がいると\nなんだかホッとする\n不思議…", 1, 3771, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112301, "魔法少女になっても\nあの人への気持ちは\nずっと同じ…", 1, 3772, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112301, "今日の私…ですか？\nはいとっても元気です\n…あなたのおかげかも", 1, 3773, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112301, "よかったら魔法の\n練習に付き合って\nくれませんか？", 1, 3774, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112301, "あの…\nこの格好どうですか？\n似合ってると…いいな", 1, 3775, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112301, "…な、なんですか？\nあまり見られると\n恥ずかしいです…", 1, 3776, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112301, "私、あなたのそばに\nいられたら、それで…", 1, 3777, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112301, "想い人と結ばれる\n魔法は…ひゃっ！？\n今の聞いてましたか？", 1, 3778, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 112301, "おはようございます\n今日も街の平和の\nために頑張らなきゃ", 1, 3779, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 112301, "お腹すきましたね。\nあの…一緒にランチは\nいかがですか？", 1, 3780, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 112301, "あ、もうこんな時間…\nふふ、まだこんなに\n体力が余ってるのに", 1, 3781, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112301, "気持ちいい…\nこの気持ち良さは\nどんな魔法も勝てない", 1, 3783, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112301, "何これ、かわいい…\nお姉ちゃんにも\n見せてあげたいな", 1, 3784, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112301, "ん～、いい香り\n…これ、魔法で\n出せたりしないかな？", 1, 3785, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112301, "あったか～い\nえへへ…なんだか\nお姉ちゃんみたい", 1, 3786, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 112301, "おいしいなぁ、これ\n魔法少女になってから\n食欲旺盛かも…", 1, 3788, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 112301, "ふふ…\n魔法少女になっても\n読書はやめられないな", 1, 3789, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101001, "みらくるまほりん\nくるりんぱ～♪\n王子はんのために\n花嫁修業や～♪", 1, 3791, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101001, "あ、あのな王子はん\nできたらその…\nお姫さまだっこを\nお願いしてもええ…？", 1, 3792, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 180401, "やっぱりわたしたちの\nギルドハウスの\n居心地は最高ですね☆", 1, 3793, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180401, "王宮じゃできないこと\nやっちゃいましょう♪\n例えば、えっと～…", 1, 3794, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180401, "そうだ、隠しておいた\nおやつがありました！\nまだ食べられますね☆", 1, 3795, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180401, "ごはんはみんなで\n楽しく食べましょう！\nそれが一番の\n調味料です♪", 1, 3796, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 180401, "やっぱりここが、\nわたしの帰る場所です\n…ただいま☆", 1, 3797, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 180401, "なんだか物足りません\nきっと、あの人が隣に\nいないからですね…", 1, 3798, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 180401, "どこを見ても、大切な\n思い出がたくさん\n詰まっていますね♪", 1, 3799, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 180401, "無礼者～、なんて\n冗談です♪　どんどん\n触っちゃってください", 1, 3800, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180401, "ぷにぷにでしょう～？\nい～っぱい食べるのが\nぷにぷにの秘訣です！", 1, 3801, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180401, "握手したいんですか？\nはい、にぎにぎ～！\nもう離しませんよ♪", 1, 3802, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180401, "どうかしましたか？\n退屈してるんでしたら\nお散歩に行きましょう", 1, 3803, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 180401, "ごはん王子♪\nわたしは王女ですから\nこれでピッタリですね", 1, 3804, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 180401, "大好きです～\n愛してます～\n…ほんと、ですよ♪", 1, 3805, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 180401, "朝ごはんは大事です！\n山盛りごはんを食べて\n元気いっぱいですよ☆", 1, 3806, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 180401, "お腹ぺこぺこになる\n時間ですね～！\nごはん特盛、一つ！", 1, 3807, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 180401, "お腹いっぱい、今日は\nよく眠れそうですね♪\n明日もおいしい一日を", 1, 3808, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180401, "休憩休憩～♪\nお腹を休めて、次の\nごはんに挑みます…☆", 1, 3810, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180401, "かわいらしい\nぬいぐるみですね～\nキャルちゃんみたい☆", 1, 3811, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180401, "すんすん…この匂い\n食欲増進の効果が\nありそうですね…？", 1, 3812, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 180401, "体温が上がってくると\n眠くなっちゃいます…\nほわぁ～", 1, 3813, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 180401, "もぐもぐもぐもぐもぐ\nむぐむぐむぐむぐむぐ\nはぐはぐはぐはぐはぐ", 1, 3815, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 180401, "教養を身に着けるには\n読書が一番です！\nえっと次のレシピは～", 1, 3816, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112401, "アイドルらしくなる\nコツって、何か\nあるんでしょうか？", 1, 3818, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112401, "色んな人と話すのが\n好きなんです。つい\n長くなっちゃって…", 1, 3819, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112401, "アイドルを目指すって\n決めてから、毎日が\nワクワクなんです♪", 1, 3820, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112401, "ちゃんとストレッチ\nしておかなくっちゃ\nよいしょ、よいしょ…", 1, 3821, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112401, "リンちゃんミオちゃん\n二人に負けないように\n私、頑張ります！", 1, 3822, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112401, "私の笑顔は素敵だって\n誰かに褒められた…\nそんな気がするんです", 1, 3823, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112401, "私たちを支えてくれる\n誰かが、ずっと一緒に\nいてくれたような…", 1, 3824, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112401, "どうかしましたか？\n何か困ってるのなら\nお手伝いしますよ！", 1, 3825, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112401, "みなさん個性的で\n普通じゃない人ばかり\nいるような気が…", 1, 3826, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112401, "今日はポカポカした\nいい天気ですね～\nお散歩日和です！", 1, 3827, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112401, "今、ミルクティーを\n淹れてたんです♪\nあなたも飲みますか？", 1, 3828, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112401, "私の得意なこと？\nうーんなんでしょう…\nあっ、肩たたきです！", 1, 3829, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112401, "生・ハム・メローン！\nあ、ライブの掛け声を\n練習してたんですよ♪", 1, 3830, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 112401, "おはようございます！\n今日も一日\n頑張りましょうね！", 1, 3831, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 112401, "しっかりお昼を食べて\n午後からも張り切って\nいきましょうね♪", 1, 3832, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 112401, "ぐっすり眠れるよう\n早めに用事を済ませて\nお休みしましょうね", 1, 3833, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112401, "ちょっと休んだら\nレッスンの続きも\n頑張ります！", 1, 3835, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112401, "えへへ～\nふわふわもこもこで\nかわいいです～♪", 1, 3836, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112401, "リラックスできる\nいい香りですね～♪", 1, 3837, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112401, "わぁ、ポカポカです♪\n温かい飲み物も\nほしくなりますね", 1, 3838, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 112401, "レッスンしたら\nお腹が空いちゃって…\nぱくぱくもぐもぐ…", 1, 3840, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 112401, "レッスンに役立つ\n運動の情報とか\n載ってるでしょうか…", 1, 3841, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112601, "きらきらーん☆\nよし、次のステージは\nこれで行けるかも！", 1, 3843, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112601, "私の元気でみんなも\n元気になってくれたら\nうれしいな～", 1, 3844, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112601, "ちょっとレッスンで\n疲れちゃったかも…\nでも頑張るぞっ！", 1, 3845, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112601, "晩ごはん何にしよう？\nそーだ、しぶりんにも\n聞いてみよっと！", 1, 3846, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112601, "元気だから笑顔になる\nじゃなくて\n笑顔だから\n元気になるんだよね～", 1, 3847, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112601, "声援を聞いてる時ね\nすごく懐かしいって\n思うことがあるんだ", 1, 3848, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112601, "私を応援してくれる\n一番大事な人が\n隣にいた気がする…", 1, 3849, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112601, "なになにー？\nミオちゃんに\n何か用かな～？", 1, 3850, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112601, "おっ、やっほー！\n元気そうだね～、キミ\n私？\nもちろん元気～！", 1, 3851, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112601, "ねぇねぇ、ちょっと\n遊びに行かない？\nたまには息抜きで！", 1, 3852, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112601, "ね、この魔物料理さ\nプリンって言い張れば\n食べてもらえそうかな", 1, 3853, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112601, "えへへ\nいつもありがとねっ！\nパワーもらってるよ～", 1, 3854, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112601, "どうしたどうした～\nミオちゃんの魅力に\nメロメロなのかい？", 1, 3855, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 112601, "おっはよ～！\n一日の始まりだし\nテンション上げてこ！", 1, 3856, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 112601, "お腹空いた～…\nねぇねぇ、一緒に\nお昼食べようよ～", 1, 3857, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 112601, "ふわぁ～…\n眠くなってきちゃった\nそろそろ休もっかなぁ", 1, 3858, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112601, "わぁ、ふっかふか～！\nレッスンの疲れも\n吹っ飛んじゃうね☆", 1, 3860, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112601, "何これ…かわいーっ！\n早くしまむーに\n教えてあげなきゃ！", 1, 3861, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112601, "すぅ～…\nうんっ、これ私の\n好きな香りだな～♪", 1, 3862, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112601, "ポッカポカだね～♪\n…あ～ん、ここから\n動きたくないーっ！", 1, 3863, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 112601, "わっ、おーいしー！\nこれはテンション\n上がっちゃうね♪", 1, 3865, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 112601, "ふむふむ…\nふむふむふむ…！\n勉強になった、ブイ☆", 1, 3866, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100101, "なんでドキドキが\n止まらないの…？\n騎士くんのせいだー！", 1, 3868, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100101, "ドキドキしてるの\nあたしだけ、かな…\n騎士くんはどう…？", 1, 3869, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100201, "騎士クンの前で\n好きって言えるように\n練習しないと…", 1, 3870, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100201, "騎士クン…大好き\n…き、騎士クン！？\nなんでここに！？", 1, 3871, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100301, "困ったな…\n彼が居ないだけで、\nここまで\n寂しいと思うなんて…", 1, 3872, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100301, "えっと…\n今日は訓練じゃなくて\nその…二人でゆっくり\n過ごしたいな…", 1, 3873, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112501, "アイドルの心構え…\nどういう風に考えると\nしっくりくるのかな", 1, 3874, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112501, "何か始めるのって\nドキドキするよね\nだから最近楽しいんだ", 1, 3875, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112501, "冒険者の時って毎日\nバタバタしてたけど…\n目標ができると違うね", 1, 3876, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112501, "少しお腹が空いた…\nチョコレート一粒だけ\n食べようかな", 1, 3877, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112501, "ウヅキもミオも\n頑張ってるし\n私も負けられないね", 1, 3878, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112501, "レッスンしてる時さ\n何か、誰かが足りない\nそう思うことがあるの", 1, 3879, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112501, "大事なのは笑顔…\n誰か、大切な人から\n言われた気がするんだ", 1, 3880, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112501, "どうしたの、何か用？\n手伝えることがあれば\nすぐに言ってね", 1, 3881, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112501, "姿勢がいい？\n普段から鍛えてるし\n自然とそうなるのかも", 1, 3882, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112501, "さっき散歩してたら\n犬を見かけたんだ\n人懐っこい子だったよ", 1, 3883, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112501, "今から冒険？\nなら私も一緒に行くよ\n前衛は任せておいて", 1, 3884, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112501, "今からレッスンに\n行くつもりだったんだ\n少し手伝ってくれる？", 1, 3885, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112501, "蒼の剣を受けよ！\nえ、何してるのって？\nただの発声練習だけど", 1, 3886, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 112501, "おはよう、早いね\n今日は依頼もないし\nゆっくりしようかな", 1, 3887, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 112501, "そろそろお昼だね\nうーん、今日は\n何を食べよう…", 1, 3888, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 112501, "もうこんな時間…\n明日も早いし\nそろそろ寝なくちゃ", 1, 3889, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112501, "レッスンで疲れたし\nちょっとだけ\n休んでおこうかな", 1, 3891, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112501, "ふふっ、かわいい\nふわふわしたものって\n撫でたくなるんだよね", 1, 3892, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112501, "うん、いい香り…\nこういうものがあると\n落ち着くからいいよね", 1, 3893, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112501, "温かい場所って\n居心地がいいから\nのんびりしちゃうよ", 1, 3894, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 112501, "レッスン頑張ったら\nすぐお腹が空くし\n食べておかないと…！", 1, 3896, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 112501, "アイドルのことを\nもっと勉強して\n成長しなくちゃ", 1, 3897, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111001, "ああ、知りたい…\nなんでもかんでも\nとにかく知りたい…", 1, 3899, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111001, "ああ、眠い…\nいつでもどこでも\nとにかく眠い…", 1, 3900, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111001, "君、気を付けたまえ\nぼーっと歩いていると\nほわっ！　転んだ…", 1, 3901, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111001, "ミツキ師にいただいた\n新作サプリ…丁度よい\n被験体はいないものか", 1, 3902, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111001, "サレンディア救護院に\nユニちゃん８歳として\n潜入済みだが何か？", 1, 3903, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111001, "君がユニちゃんズの\n同志であること、\nぼくは誇りに思うぞ", 1, 3904, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111001, "ぼくは青春がしたい\nおい、君\nぼくと青春しないか", 1, 3905, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111001, "ああ、そうだな。\nぼくもそう思う。\n……えっ、何が？", 1, 3906, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111001, "待て、話しかけるな。\n今の閃きをメモに…\n…ああっ、忘れた！", 1, 3907, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111001, "歩き方が綺麗？\n何を今更なこと\n言っとるんだ君は", 1, 3908, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111001, "ぼくの血液型？\n大らかのОだ。\n何か異論あるかね", 1, 3909, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111001, "君の声は精神に\n高揚をもたらすな…\n波形を分析したいぞ", 1, 3910, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111001, "妙だな…最近\n君に話しかけられると\n心拍に乱れを覚える", 1, 3911, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 111001, "おっと、朝だな。\nそろそろ寝るか、\n朝更かしは体に悪い", 1, 3912, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 111001, "ふむ、昼餉時か…\n今日のランチは\nサプリ定食にしよう", 1, 3913, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 111001, "もう夜だな、\nそろそろ起きるか。\n最高の一日にするぞ！", 1, 3914, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111001, "ほわぁ～\n副交感神経が…\n優位に…ふふっひ", 1, 3916, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111001, "かわいすぎるな…\nセクシーユニには\nミスマッチか…", 1, 3917, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111001, "ほぁあアあァぁー…\nいい香りー…\nあアァあ…イイ…", 1, 3918, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111001, "炎はいいぞ\nそこには人類発展の\nドラマがある", 1, 3919, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 111001, "フオオー！\n次から次へと固形食物\n原理気になるー！", 1, 3921, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 111001, "良書は時間を忘れる\n…ふむ、今度\n布団を持ってこよう", 1, 3922, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101201, "きらーん☆\nハツネちゃんに\n不可能はないのだ☆", 1, 3924, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101201, "キミと一緒だと\n寝ちゃうのが\nもったいないよ～", 1, 3925, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 192101, "ちぇるーん☆", 1, 3933, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110901, "ちぇる～ん☆\n今日もチエルは\nかわいいですよね～？", 1, 3951, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110901, "どこかに楽しいもの\n落ちてないかにゃー", 1, 3952, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110901, "チエルのオキニは\n甘々カフェラテ！\nシクヨロでぇ～す☆", 1, 3953, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110901, "先輩、ひまですよね？\nチエル遊んであげても\nいいですけど？", 1, 3954, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110901, "はぁー…しゅき…\nカルミナ尊い…\n一生すこれる…", 1, 3955, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110901, "先輩、ひまー\nちょっとは構えよーう\nデュクシ！デュクシ！", 1, 3956, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110901, "先輩なんか\nおごって～！\nてか…おごらなくても\nいいから遊んでよ…", 1, 3957, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 110901, "え、なんですか？\n告白だったら\n間に合ってますけど", 1, 3958, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110901, "ちょっと静かに！\n今ヤバかわポーズの\nイメトレ中なんです！", 1, 3959, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110901, "足取りが軽やか？\nあー…ダンスとか\n習ってるからですかね", 1, 3960, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110901, "チエルの血液型？\n大型新人のОです\n何か問題でも？", 1, 3961, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 110901, "先輩って何気に\nいい声してますよね\n言うことは変だけど", 1, 3962, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 110901, "え、なんですか？\nたまにだったら\n告白してもいいですよ", 1, 3963, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 110901, "おはちぇる～ん☆\nハイ、ご一緒に～？", 1, 3964, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 110901, "今日のランチは\nどこで\n映えよっかにゃー", 1, 3965, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 110901, "こんばんちぇるるん☆\nハイ、ご一緒に～？", 1, 3966, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 110901, "ふかふかチエルン\n略して、ふかルン♪", 1, 3968, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 110901, "かわいいこの子と\nかわいいチエル！\n映ぁ～え～る～！", 1, 3969, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 110901, "かわいい香り～！\n持って帰ろっと！\nすぅーっ…", 1, 3970, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 110901, "マシュマロは\n焼かない方のが\nかわいくないです？", 1, 3971, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 110901, "チエル食べても\n太らない体質\nなんでぇ～！", 1, 3973, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 110901, "読書は好きですよ\nシンプルに楽しいし\nギャップ萌えでしょ？", 1, 3974, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112701, "みんなのあたしを\n見る目が違うね\nま、気にしないけどね", 1, 3976, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112701, "今日も疲れた～\nやる気ゼロだし\nもう寝ちゃおうかな", 1, 3977, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112701, "あたしの代わりに誰か\n働いてくれないかね", 1, 3978, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112701, "労働の後に食べる\nあんパン最高～", 1, 3979, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112701, "仕事の合間に\nちょっと一息いれよ\nあ～ドングリ美味し～", 1, 3980, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112701, "報酬も入ったことだし\nあいつに何か\n買ってあげようかな", 1, 3981, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112701, "次の休みはあいつと\n一日中だらだら\nしたいな～", 1, 3982, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112701, "あたしは褒められて\n伸びるタイプなんだ\nだから褒めて褒めて", 1, 3983, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112701, "お、差し入れ？\n気が利くねぇ\nあんがと", 1, 3984, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112701, "新しい依頼？\n今日はもう無理～\n閉店で～す", 1, 3985, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112701, "サ、サボってないよ！\nちょっと休憩してた\nだけだから！", 1, 3986, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112701, "ねえ、一緒に\nサボろうよ？\n働き過ぎはよくないよ", 1, 3987, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112701, "あんたが近くにいると\n仕事の疲れも\n和らぐんだよね～", 1, 3988, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 112701, "はぁ…もう起きなきゃ\nつらい労働の\nはじまりだ～", 1, 3989, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 112701, "うぅ、お昼に\nあんパン食べ過ぎた…\n苦しい…眠い…", 1, 3990, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 112701, "明日は仕事ないし\nガッツリ夜ふかし\nしちゃうよ～", 1, 3991, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112701, "猫みたいに\n丸くなっちゃうよ～\nあたしリスだけどね", 1, 3993, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112701, "ふかふか～\nあたしの尻尾と\n互角とはやるね～", 1, 3994, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112701, "あんパンの香りの\nアロマとか\nあればいいのにな～", 1, 3995, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112701, "これは危険な\nあたたかさだね～\n労働意欲が\nなくなるよ～", 1, 3996, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 112701, "いっぱい働いたから\nいっぱい食べても\nカロリー０だよ", 1, 3998, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 112701, "あたしだってたまには\n本くらい読むって\n全然似合わないけどね", 1, 3999, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104601, "あたしの作った\nオリジナルたい焼き…\nあいつ、\n喜んでくれるかにゃ？", 1, 4001, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104601, "あ、お帰りにゃー♪\nえへへ…キミに\nお帰りって言えるのが\nなんかうれしいにゃ♪", 1, 4002, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112801, "フロンティア\nスピリッツを\nお見せするべ！", 1, 4003, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112801, "この鎧を着ると\n気が引き締まる\n感じがするべ", 1, 4004, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112801, "搾りたての牛乳飲んで\n今日も一日\nがんばるべ！", 1, 4005, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112801, "みんなに牧場の\n乳製品を\n味わってもらいてぇべ", 1, 4006, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112801, "ここはいいとこだべ\nうちの動物たちも\n連れてきてやりてぇな", 1, 4007, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112801, "みんなオラより\n若ぇのに働いてて\n立派だべな", 1, 4008, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112801, "牧場もギルドの\nみんなもオラが\nまとめて面倒見るべ！", 1, 4009, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112801, "さあ、今日も\n忙しくなるべよ\n気合入れてくべ！", 1, 4010, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112801, "お、あんちゃん\n丁度いいとこに！\n一緒に街に\n行商しに行くべ！", 1, 4011, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112801, "牧場仕事で培った力が\n戦いでも役立つなら\n一石二鳥だべさ！", 1, 4012, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112801, "疲れた時は牧場産の\nミルク菓子で\n糖分補給だべ", 1, 4013, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112801, "これ、牧場の\n新製品だべ\n一番最初に味見\nしてほしいべさ", 1, 4014, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112801, "働きづめはよくねぇベ\n膝枕してやっから\nこっち来るべさ", 1, 4015, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 112801, "ふぁあ…\n酪農家の朝は\n早ぇんだべ…", 1, 4016, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 112801, "ごはんの時間だべ！\n腹が減っては酪農も\n戦いもできねえべ！", 1, 4017, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 112801, "そろそろ寝るべ！\n健全なお笑いは\n健全な肉体に宿るべ！", 1, 4018, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112801, "牧草みたいに\nふっかふかだべ～", 1, 4020, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112801, "この子、目の感じが\nエリザベスに似てるべ", 1, 4021, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112801, "いい匂いだべ～\n疲れた体に\nしみるべな～", 1, 4022, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112801, "あったけぇな～\n牧場にも\n置いてみっかな～", 1, 4023, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 112801, "うまいべ！\n生産者のこだわりを\n感じるべさ", 1, 4025, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 112801, "酪農も戦いも\nまずは知識だべ！", 1, 4026, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113001, "ぴょん、ぴょん♪\nこの服のおかげで、\n体が軽いなぁ…♪", 1, 4028, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113001, "今の内に部屋の\n掃除をしておこっと…\nみんな喜んで\nくれるといいなぁ", 1, 4029, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113001, "耳と尻尾…この服、\n本当にウサギさん\nみたいだなぁ", 1, 4030, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113001, "なんだか先輩の\n気配がするような…\n気のせいかな？", 1, 4031, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113001, "ウサギさんの真似を\nしたら、存在感が薄い\nとか言われないかな？\nア、アユミだぴょん♪", 1, 4032, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113001, "えへへ…\nいつもとは違う先輩が\nたくさん見られて、\nうれしいな♪", 1, 4033, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113001, "本の中の世界で先輩と\n旅をするなんて…\n幸せすぎて、罰が\n当たりそうだよぉ…", 1, 4034, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113001, "じ～…はっ！？\nす、すみません！\n話しかけてもらえる\nなんて思ってなくて！", 1, 4035, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113001, "先輩がすぐそばに…\nし、幸せすぎて\n倒れそうですぅ！", 1, 4036, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113001, "え、えっと…あの…！\nうう…いざ先輩と\n向き合うと\n緊張しちゃうよぉ…", 1, 4037, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113001, "はうっ！？\nしょ、正面から見る\n先輩は、私にとって\n神々しすぎますぅ…！", 1, 4038, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113001, "先輩…その…\nい、一緒にご飯でも\nどうでひゅか！\nうう…またかんだぁ…", 1, 4039, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113001, "せ、先輩…\nハーブティーを\n入れたんですが、\n一緒にどうですか…？", 1, 4040, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 113001, "せ、先輩！\nおはようございます！\nえへへ、練習通りに\nバッチリ言えたぁ♪", 1, 4041, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 113001, "あ、もうお昼ですね\n時計を持ってて\n良かったです", 1, 4042, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 113001, "もうこんな時間…\nお休みする\n準備をしちゃいますね", 1, 4043, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113001, "あ…ちょっとだけ\n先輩の匂いがする…\nえへへ、幸せだなぁ♪", 1, 4045, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113001, "…私もウサギさんの\nぬいぐるみとして、\n先輩のそばに…はっ！\n私ったら何を…！", 1, 4046, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113001, "あ、いい香り…\n先輩もこういうのが\n好きなのかな？", 1, 4047, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113001, "先輩が隣に\n居たら、もっと\n暖かいのになぁ…♪", 1, 4048, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 113001, "おいしい…でも、\n先輩と一緒に\n食べたら、もっと\nおいしいだろうな…", 1, 4050, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 113001, "ウサギさんって、穴を\n掘ったりするんだ…\n私も、隠れたいときに\n掘ってみようかな？", 1, 4051, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 180501, "ネビアさまのように\n普段からパタパタと\n飛んでみるのも…", 1, 4053, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180501, "ご立派になられた\n主さまと並べるように\nもっと精進しなくては", 1, 4054, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180501, "月々の食費が\n増えてしまいました\n申し訳ございません…", 1, 4055, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180501, "ワンワンのほうの\n主さまは、この姿に\n驚くでしょうか…？", 1, 4056, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 180501, "主さまと離れていても\n主さまとの絆は\n胸に感じております…", 1, 4057, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 180501, "このギルドハウスは\nみなさまの帰る場所…\nわたくしが守ります", 1, 4058, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 180501, "どんな未来が\n待ち受けようとも\n主さまと添い遂げます", 1, 4059, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 180501, "おでかけですか？\nではわたくしも\nお供いたします", 1, 4060, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180501, "もう二度と主さまと\n離れたくはありません\nいつ何時も…", 1, 4061, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180501, "なにかお困りごと\nでございますか？\nわたくしに何なりと…", 1, 4062, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180501, "どうぞゆっくりと\n心ゆくまでくつろいで\nくださいまし、主さま", 1, 4063, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 180501, "この羽で主さまを\nどこまでも遠く…\n高くへと導きます", 1, 4064, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 180501, "主さま、主さま…♪\nおそばにいられるのが\nうれしくて、つい…", 1, 4065, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 180501, "おはようございます\n今日も一日、主さまの\nサポートをいたします", 1, 4066, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 180501, "今日も頑張っている\n主さまに、スタンプを\nもう一つ…ポン！", 1, 4067, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 180501, "寝付けないのでしたら\nわたくしが子守唄を\n歌ってさしあげます", 1, 4068, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180501, "ふかふか…\nまるで主さまのような\n柔らかさでございます", 1, 4070, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180501, "あなたも一緒に\n主さまと遊びますか？\n至福の時間ですよ♪", 1, 4071, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180501, "主さまの優しくて\nくすぐったい匂いが\nたまらなく恋しいです", 1, 4072, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 180501, "主さまの心と\n繋がったときのような\n温もりを感じます", 1, 4073, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 180501, "いけませんっ…！\n元の姿に戻ると、また\n食べてしまうのに…！", 1, 4075, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 180501, "主さまに読み聞かせる\n絵本は…妖精が主役の\n物語にいたしましょう", 1, 4076, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104801, "…彼のこと弟みたいに\n思ってきたのに…私、\nどうしたらいいの…？", 1, 4078, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104801, "よ、予定がなかったら\nでいいんだけど…\nその、ふ、二人で\n訓練とか…どうかな？", 1, 4079, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112901, "おめかしもしたし\nあとはお兄ちゃんを\n待つだけですね～♪", 1, 4080, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112901, "お兄ちゃんの身の回り\nをお片付け～、これぞ\nデキる妹アピール！", 1, 4081, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112901, "あ、衣装がほつれてる\nこれくらいなら自分で\n繕えますね…！", 1, 4082, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112901, "この格好で歩いてると\n見慣れた景色も\n違うものに見えますね", 1, 4083, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112901, "勉強もちゃんとします\n…でも、一人だと\n集中力がもちません～", 1, 4084, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112901, "お兄ちゃんを大好きな\n妹が待ってますよ～\n…まだですかね～", 1, 4085, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112901, "お兄ちゃんのために\n掃除洗濯をする私って\nお嫁さんみたい…\n…えへへ～", 1, 4086, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 112901, "おに～いちゃん！\n縫い物、一緒に\nやりましょうよ！", 1, 4087, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112901, "かわいい妹を\n愛でたくなりました？\n思う存分、どうぞ♪", 1, 4088, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112901, "わっ、ビックリした～\nもう、お兄ちゃんてば\nイタズラっ子ですね～", 1, 4089, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112901, "一緒にお買い物\n行きませんか？\n妹とデートですよっ♪", 1, 4090, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 112901, "お兄ちゃん…頭を\n撫でてくれませんか？\nえへへ、これが一番♪", 1, 4091, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 112901, "お兄ちゃん大好き♪\n何度でも言いますよ！\nお兄ちゃん大好きっ♪", 1, 4092, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 112901, "おはよ～お兄ちゃん！\n妹モーニングコールの\n時間ですよっ♪", 1, 4093, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 112901, "そろそろ休憩ですね！\n甘いお菓子で\n元気回復しましょ～！", 1, 4094, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 112901, "夜更かし厳禁！\nでも、もう少しだけ\nお話ししたいなぁ～", 1, 4095, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 112901, "ふっかふかですよ！\nお兄ちゃんと一緒に\n座りたいなぁ…♪", 1, 4097, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 112901, "このぬいぐるみの\nかわいさ…う～ん\n負けられません…！", 1, 4098, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 112901, "香りにも気をつかって\nパーフェクトな妹に\nなっちゃいますから！", 1, 4099, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 112901, "はぁ～ぬくぬくです～\nお兄ちゃんと一緒に\n温まりたいなぁ…♪", 1, 4100, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 112901, "これは止まりませんね\nもぐもぐ…いくらでも\n食べられちゃいます", 1, 4102, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 112901, "これ、デザインの\n参考になりそう！\n私にも作れるかなぁ", 1, 4103, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106601, "退屈です…\nなにか楽しいことは\nないですか？", 1, 4105, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106601, "ギャングの仕事は\n今日はお休みです\nのんびりするですよ", 1, 4106, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106601, "ひうっ！　あたしの\n尻尾なんて食べても\nおいしくないですよ？", 1, 4107, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106601, "ふう…どうやら\n例の件はまだボスに\nバレてないですね…", 1, 4108, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106601, "下克上…いい言葉です\nって、ボスに聞かれて\nないですよね…？", 1, 4109, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106601, "いつか絶対ビッグに\nなってやるです！\nあたしを信じるです！", 1, 4110, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106601, "あたしが覚醒したら\nきっと誰よりも\n強くなるはずです！", 1, 4111, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106601, "なんですか？\n貢ぎ物なら\nいつでも歓迎ですよ！", 1, 4112, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106601, "あたしだって\nやる時はやるですよ！\nその時がこないだけ！", 1, 4113, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106601, "しりとり勝負ですか？\nいい度胸ですね、\nかかってくるです！", 1, 4114, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106601, "お兄さんは\nヒマそうですね…\nうらやましいです", 1, 4115, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106601, "かまってほしいです？\nまったく…いいですよ\nしょうがないですね", 1, 4116, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106601, "お兄さんのことは\nこのあたしが\n守ってやるですよ！", 1, 4117, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 106601, "デキるギャングの\n朝は早いのです…\nふぁあ…", 1, 4118, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 106601, "お腹がすいたですね\nなにか食べにいくです\nお兄さんのおごりで！", 1, 4119, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 106601, "ふぁあ…むにゅう…\nまだ起きてるですよぉ", 1, 4120, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106601, "はぁ～…\nもうここから一歩も\n動きたくないです", 1, 4122, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106601, "なかなかいいモフりを\nしてやがるです…\n舎弟にしてやるですよ", 1, 4123, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106601, "なんかおしゃれな\n匂いがするですね…", 1, 4124, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106601, "ふふーん！\nあたしのブレスの方が\n火力は上ですね！", 1, 4125, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 106601, "あたしに貢ぎ物とは\nよく分かってるですね\n食べてやるですよ", 1, 4127, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 106601, "ふーむ…\nもっと楽しい本は\nないですかね？", 1, 4128, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113201, "海に眠る秘宝…\n心躍る響きだ！\n早く見つけたい…！", 1, 4130, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113201, "うう～、暑いぃ…\nかき氷を食べて\nすっきりしたいぃ…", 1, 4131, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113201, "アターック！\nいや、もっとこう…\nアタァーック！", 1, 4132, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113201, "スイカ割りで\n目を回さない\nコツはなんだろうか…", 1, 4133, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113201, "せっかく海に来たのだ\n泳ぎも上手に\nなりたいものだな", 1, 4134, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113201, "ふっふっふ…\n疼く…疼くぞ！\n日焼けの痕が…", 1, 4135, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113201, "シグルドと私は\n来世でも共にある…\nつまり我らはずっと…\nはわわ…！", 1, 4136, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113201, "シグルド、私と共に\n日光浴に行かないか？\n光の力を集めよう！", 1, 4137, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113201, "この格好だからできる\n必殺技を考えている\n何か案はないか？", 1, 4138, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113201, "シグルド～！\nスイカを貰ったから\n一緒に食べよう！", 1, 4139, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113201, "こう日差しが強いと\n日焼けしそうだな\n跡になってないか…？", 1, 4140, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113201, "我らの夏の黙示録\nしっかりと記録\nしておかねばな", 1, 4141, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113201, "ちゃんと日焼けどめを\n塗ってもらうべき\nだったかな…？\nシ、シグルドに…！", 1, 4142, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 113201, "朝だぞシグルド！\nさぁ、日光を浴びて\n光の加護を得るのだ！", 1, 4143, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 113201, "もう昼食の時間か…\n私の魔力も減ったし\n早く補給しなければ", 1, 4144, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 113201, "夜でも我らの活動は\n終わることはない！\nさぁ準備を…ふわぁ…", 1, 4145, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113201, "海で遊んでいると\n疲れてしまうからな\n少し休まるとしよう", 1, 4147, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113201, "えへへ～、君の名前は\n何にしまちょうかね♪\n…はっ、見たのか！？", 1, 4148, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113201, "心地よい香りで\n我が精神も安らぐ…", 1, 4149, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113201, "別に寒くなどないぞ！\n寒くないが、ちょっと\n暖まりたくなってな！", 1, 4150, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 113201, "もぐ…食べ過ぎた分は\n魔力に変換されるのだ\n…たぶん", 1, 4152, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 113201, "…なな、なんだ！？\nシグルドはこういう\n趣味なのか…！？", 1, 4153, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 113201, "むむ、この乗り心地…\nいざゆかん、\nシードラゴンよ！", 1, 4155, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 113201, "疾風の冥姫の名に\nふさわしき\n深海の玉座よ…\nふああ…柔らかい～", 1, 4156, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 180201, "この姿でいると\nわたしもお姫様に\nなれた気がする…♪", 1, 4157, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180201, "お腹空いてきちゃった\nまずは買い出しに…\n…変身解かなくちゃ！", 1, 4158, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180201, "胸のところが空いてて\nスースーするの\nまだ慣れないな…", 1, 4159, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180201, "治癒魔法に使ってた\n魔力を攻撃魔法に…\n頭の中でも練習練習", 1, 4160, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 180201, "何もできなかった\nわたしから、少しは\n変われたのかな…？", 1, 4161, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 180201, "騎士クン…\n今どこにいるの…？\n…会いたいな", 1, 4162, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 180201, "離れてても分かるよ…\n温かい力で騎士クンに\n守られてるの", 1, 4163, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 180201, "わたしが戦えてるのは\n騎士クンのおかげだよ\nいつもありがとう！", 1, 4164, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180201, "お腹空いてそう…？\n…内緒で作ったおやつ\n騎士クンも食べる？", 1, 4165, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180201, "ひゃうっ！　え！？\nせ、背中はダメ～！\n言ってから触って～！", 1, 4166, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180201, "この格好、どうかな？\nえ？　似合ってる…？\n本当？　…うれしい！", 1, 4167, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 180201, "かわいい？　そうだね\nひらひらが…わたし？\nええっ、そ、そんな！", 1, 4168, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 180201, "騎士クン、これからも\nずっと…わたしの\nそばにいてください", 1, 4169, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 180201, "朝からこんなに\n食べる日が来るなんて\n思ってなかったよ…", 1, 4170, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 180201, "お昼を作らなくちゃ\n騎士クン、一緒に\nお料理してみる？", 1, 4171, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 180201, "騎士クン、もう眠い？\n眠るまで、わたしが\nずっとそばにいるね", 1, 4172, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180201, "攻撃参加するからこそ\n適度に体を休めるのは\n大切だよね！", 1, 4174, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180201, "かわいい…騎士クンと\nシェフィちゃんにも\nプレゼントしたいな", 1, 4175, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180201, "いい香り～…\nあれ？　お腹が空いて\nきちゃった…！？", 1, 4176, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 180201, "温かい…\n騎士クンがくれる\n力みたい…", 1, 4177, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 180201, "ぱくっ、\nあむ、はむっ…\nううぅ～…やっぱり\n手が止まらないよぉ～", 1, 4179, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 180201, "新しく覚えたいな…\nみんなで食べられる\n大皿向けの料理♪", 1, 4180, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104901, "お姉ちゃんの愛\nこのままじゃ\nあふれちゃうよ…♪", 1, 4182, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104901, "弟くん♪\n弟くんっ♪\nおっとうっとくんっ♪", 1, 4183, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113101, "泳ぐのもいいけど\n今日は砂浜で\nのんびりしようかね", 1, 4184, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113101, "さてと\nちょっくらひと泳ぎ\n行ってくるかね", 1, 4185, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113101, "あっついねぇ…\nうちわがないと\nしんどいったらないよ", 1, 4186, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113101, "冷たすぎる飲み物で\n体を冷やさないよう\n気をつけないと…", 1, 4187, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113101, "夏はあっという間だし\n悔いがないように\n楽しまないとね", 1, 4188, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113101, "きっとこの夏は\nアタシにとって大事な\n想い出になるね…", 1, 4189, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113101, "…前に比べたら\nアタシたちの距離も\n近づいたみたいだね", 1, 4190, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113101, "もし暇なら\n今日も笹舟釣り\n一緒にどうだい？", 1, 4191, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113101, "お前さん、釣りに\n興味が湧いたなら今度\n道具を見に行こうよ", 1, 4192, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113101, "今から砂浜に行くんだ\nお前さんも一緒に\n散歩するかい？", 1, 4193, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113101, "え、日焼け跡？\nおや…本当だ\nすぐ焼けちまうねえ", 1, 4194, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113101, "ひゃっ！？\nちょっと！\n日焼け跡をいきなり\n触らないでおくれ！", 1, 4195, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113101, "お前さんは、その…\nアタシと一緒にいて\n楽しかったかい？", 1, 4196, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 113101, "おはようさん\n今日も砂浜に行って\nゆっくりしようか", 1, 4197, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 113101, "お昼か…\nそうだ、よかったら\n刺身定食、作ろうか？", 1, 4198, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 113101, "夜の空を見てるとさ\nなつかしい気持ちに\nなるんだよね", 1, 4199, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113101, "適度に休憩して\n夏バテしないよう\n気をつけないとね", 1, 4201, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113101, "あんたたちも\n海を楽しみたいって\n思っているのかね", 1, 4202, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113101, "ん～…香りでも\nバカンスらしさを\n楽しめるもんだね", 1, 4203, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113101, "こういう格好だと\n少し冷えちまうんだよ", 1, 4204, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 113101, "もぐもぐもぐ…\n遊んだ分、お腹が\n空いちまってねぇ", 1, 4206, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 113101, "…ん、なんだいこの本\n催眠…うーん読めない\nナナカに聞いてみるか", 1, 4207, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 113101, "へえ…\nなかなかいい\n乗り心地じゃないか", 1, 4209, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 113101, "そうそうハマグリって\n竿でも釣れるんだよ\n知ってたかい？", 1, 4210, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113301, "海を満喫しなきゃね\nちょっとひと泳ぎ\n行ってこようかな？", 1, 4211, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113301, "う～ん…♪\nいつもと違った\n開放感～\nなんちゃって！", 1, 4212, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113301, "今のナナカちゃんは\nたぶん水属性！", 1, 4213, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113301, "水着もバッチリ！\nサイカワサイツヨ\nナナカちゃ～ん☆", 1, 4214, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113301, "んっふっふ\nドキ！　水着だらけの\nギルドハウス！\nとかどうかな？", 1, 4215, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113301, "そんなに見られると\nちょ～っと\n恥ずかしいかも…\nなんてねなんてね！", 1, 4216, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113301, "ちょっとくらい\nドキッとしてくれても\nいいのにな～、もう…", 1, 4217, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113301, "水着魔法少女の\n相方がほしいなぁ～…\nね、君やってみない？", 1, 4218, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113301, "いつもの格好と同じ？\nも～、わかってない！\nセクスィー度が違う！", 1, 4219, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113301, "どうかなどうかな？\nこのひらひらのとこが\nかわいーかなー\nとか思うんだけど！", 1, 4220, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113301, "せっかくだから\nギャルゲーイベント\nごっことか\nやってみる？", 1, 4221, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113301, "ナナカちゃんの\n好感度を上げたい？\nならおでかけだー！", 1, 4222, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113301, "おうちで水着って\nなんだかコスプレ\nみたいで楽しいかも♪", 1, 4223, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 113301, "おっはよーう！\n今日はバカンス日和！\n海へゴーですぞ～♪", 1, 4224, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 113301, "お昼ご飯どうしよ～…\nあ、ナナカちゃんと\nどっか食べに行く？", 1, 4225, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 113301, "夜は本を読むのが\n捗りまくりですな～！\n今日はなに読もっかな", 1, 4226, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113301, "次のイベントもあるし\nちょっと休んで体力を\n回復しなきゃね～", 1, 4228, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113301, "こういうマスコット\n私にもいたらなぁ\nいっそ作っちゃう…？", 1, 4229, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113301, "水着魔法少女に\n似合う香りってのも\n考えてみよっかな♪", 1, 4230, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113301, "夜になると急に冷えて\n肌寒いんだよね～…", 1, 4231, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 113301, "あんまり食べると\nお腹が出て…でも…\nうう、止まらない～！", 1, 4233, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 113301, "この催眠魔法の本\n誰にもばれないように\n保管しとかなきゃね…", 1, 4234, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 113301, "ゴーゴーゴーゴーゴー\nナナカちゃーん♪", 1, 4236, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 113301, "ナナカちゃんの\n誕生～\nなんちゃって", 1, 4237, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113401, "ファイトファイト☆\n一生懸命\n応援するね！", 1, 4238, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113401, "みんながんばってる！\n私も応援でみんなを\n盛り上げなきゃ！", 1, 4239, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113401, "もし元気のない人が\nいたら…応援で少し\nでも元気にしたいな！", 1, 4240, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113401, "がんばれ～☆\nよし、発声練習OK！\n応援は、声も大事！", 1, 4241, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113401, "せっかく水着なんだし\n泳いだり潜ったりして\n遊びたいな～！", 1, 4242, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113401, "チアリーダーとして\nキミを精一杯\n応援したいな…！", 1, 4243, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113401, "キミへの応援\nちゃんと\n届いてるかな…", 1, 4244, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113401, "えへへ…今ね、\nキミへの応援歌を\n考えてたのだ～♪", 1, 4245, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113401, "どうしたの？\n元気がないなら\n私が応援するよ☆", 1, 4246, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113401, "がんばったあとは休憩\nしなきゃね！　私と\n一緒にお昼寝する？", 1, 4247, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113401, "シオリンとおそろいの\n水着で応援…\nしてみたいなぁ～", 1, 4248, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113401, "次はなにして遊ぶ？\nキミがしたいことを\n教えてよ！", 1, 4249, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113401, "キミを応援\nすることができて…\n私、すごく幸せ…", 1, 4250, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 113401, "キミって早起き\nなんだね！　私も\n見習わなきゃ！", 1, 4251, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 113401, "ちょっとお疲れかな？\nそれじゃあ\n応援してあげるね♪", 1, 4252, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 113401, "遅い時間まで\nがんばってえらいね！\nでも無理はダメだよ♪", 1, 4253, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113401, "ちょっとだけ休憩…\n…あう、座り心地が\n良すぎて…眠気が…", 1, 4255, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113401, "ここにいるみんなを\n私の応援でもっと\n元気にするのだ～！", 1, 4256, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113401, "元気の出る香り～♪\nこれもうまく応援に\n活かせないかな～？", 1, 4257, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113401, "ぽかぽかあったまって\n元気をチャージ\nするのだ～☆", 1, 4258, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 113401, "いっぱい食べて、\nみんなを応援する\nパワーをつけなきゃ！", 1, 4260, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 113401, "みんなを元気にする\n方法は…どこかに\n書いてないかなぁ…？", 1, 4261, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 113401, "わぁ、この子\n怪獣さんみたいで\nかわいいね～♪", 1, 4263, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 113401, "水着でこれに\n乗ってると海にいる\n気分になれるね！", 1, 4264, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113501, "すごく忙しいけど\nとても充実した気分\nこれが青春なのかしら", 1, 4265, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113501, "森の静かな雰囲気も\n海の開放的な空気も\nどちらも素敵ね～", 1, 4266, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113501, "汚れたお洗濯物は\nみんなが頑張った証\nなんだか愛おしいわ", 1, 4267, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113501, "みんなを見てると\n何だか私も\n走りたくなっちゃうわ", 1, 4268, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113501, "せっかくの水着だし\n泳ぐ練習でも\nしてみようかしら", 1, 4269, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113501, "みんな～ファイト～！\n応援するのも大事よね\n練習しておかなきゃ♪", 1, 4270, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113501, "先生に教えられること\nまだまだい～っぱいね\n次は何がいいかしら♪", 1, 4271, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113501, "今日も暑くなりそうね\nこまめな水分補給を\n忘れないでね", 1, 4272, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113501, "手伝ってくれるの？\nありがとう♪\n先生、頼りにしてるわ", 1, 4273, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113501, "休憩はこまめに\nとりましょうね\n無理は厳禁よ", 1, 4274, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113501, "海の遊びって\nどんなのがあるの？\n先生に教えてね", 1, 4275, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113501, "水着は初めてだけど\nどう？　変じゃない？\nふふ、ありがとう", 1, 4276, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113501, "あなたと過ごした\nこの夏は、きっと…\n忘れられない思い出ね", 1, 4277, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 113501, "おはよう～\n朝練前にちゃんと\nごはん食べましょうね", 1, 4278, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 113501, "お弁当作ってきたの\n栄養いっぱい摂って\n午後も頑張ろうね", 1, 4279, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 113501, "もうお休みしなきゃね\n先生が子守唄を歌って\nあげましょうか？", 1, 4280, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113501, "ふふ、いい気持ちねぇ\n先生、少しだけ\n休憩しちゃおうかしら", 1, 4282, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113501, "あらかわいい♪\nチームにもこういう\nマスコットが欲しいわ", 1, 4283, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113501, "あら、海の香り？\n波の音まで聞こえて\nきちゃいそうだわ", 1, 4284, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113501, "うふふ\nこれなら濡れた水着を\n乾かすのも簡単ね♪", 1, 4285, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 113501, "これ、美味しいわね♪\nでも、食べ過ぎは\n禁物よ", 1, 4287, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 113501, "先生だって日々勉強よ\n新しいことを知るのは\nとても楽しいわ♪", 1, 4288, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 113501, "あらあら、楽しいわね\nみんなもきっと\nおおはしゃぎね", 1, 4290, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 113501, "ふふ、これで\n夏らしい気分を\n満喫するわよ♪", 1, 4291, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102301, "サンドイッチも\n作ったし、\nお兄ちゃんのお出迎え\n準備は完璧だね♪", 1, 4292, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102301, "えへへ、お兄ちゃんと\nぷうきちが居てくれる\nだけで、なんだか\nうれしくなっちゃう♪", 1, 4293, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113601, "少し赤くなっているな\n日焼けなんてしたのは\nいつ以来だろう", 1, 4294, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113601, "この格好で\n過ごすのは\n少し勇気がいるな…", 1, 4295, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113601, "暑いのは平気だが\n肌が外気に触れるの\nにはまだ慣れないな…", 1, 4296, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113601, "この格好でも\n堂々としていれば\n恥ずかしくはない…\nのかな？", 1, 4297, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113601, "おっと…鎧姿の時と\n重心が違うせいか\nちょっと不安定だな", 1, 4298, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113601, "この水着…\n私に似合って\nいるのだろうか…？\n少年には聞きづらいな", 1, 4299, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113601, "少年との\n海のバカンス…\nまた行きたい\nものだな", 1, 4300, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113601, "あまり見るのは\nよしてくれ…\n恥ずかしいじゃ\nないか…", 1, 4301, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113601, "どうかしたのかい？\n私でよければ\n話し相手くらいには\nなろうじゃないか", 1, 4302, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113601, "ひゃっ！\nす、すまない\nちょっと過敏に\nなっているようだ", 1, 4303, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113601, "…っくしゅん！\nすまない\n薄着には慣れて\nないものでね", 1, 4304, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113601, "まあ、少年になら\n見られても…\nい、いや！\nなんでもない！", 1, 4305, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113601, "私がこうして\nゆっくりできるのは\n少年がそばにいて\nくれるからだよ", 1, 4306, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 113601, "この時間なら\nビーチにも人は\n少ないだろうね…\n遊びに行くかい？", 1, 4307, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 113601, "昼食のお誘いかい？\nいいよ、付き合おう", 1, 4308, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 113601, "もう夜か…\n一日が経つのは\nはやいものだね", 1, 4309, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113601, "これは楽でいいな…\nいつもは鎧姿で\n家具を傷つけないか\n気を使っているからね", 1, 4311, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113601, "うん…ふわふわの\n感触が\nよくわかるな…", 1, 4312, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113601, "いつもは\n鎧に匂いが\n移ってしまうのが\n悩みなんだよ…", 1, 4313, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113601, "うーむ…\n涼しいのに暖かい\nなんだか奇妙な気分だ", 1, 4314, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 113601, "兜を気にせず\n食べられるのは\n楽でいいな…", 1, 4316, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 113601, "ふむ…鎧がないと\nページがめくりやすい", 1, 4317, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 113601, "身軽でいいな…\n鎧を着ていると\n騎乗訓練の時\n馬に申し訳がなくてね", 1, 4319, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 113601, "貝をうまく使えば\n水中でも\n守りを固められそうだ", 1, 4320, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106801, "どこをアジトと\nつなげようかな～…\nこのへんにしよっと♪", 1, 4321, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106801, "ここってちょっと\n変わった建物だね\n妖精がいるでしょ？", 1, 4322, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106801, "色んな人を観察できて\n眼福眼福…♪", 1, 4323, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106801, "家具の配置変更なら\nアタシにおまかせあれ\n…家の形は変えない？", 1, 4324, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106801, "新メニューどうしよう\n思いついた人がいたら\nアタシに教えてね！", 1, 4325, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106801, "少年、早く帰って\nこないかな…\nよし、迎えに行こう！", 1, 4326, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106801, "結局またあちこち\n移動する日々だし…\n少年に会いたいな～", 1, 4327, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106801, "どしたの少年？\nお姉さんに何でも\n相談してみな～？", 1, 4328, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106801, "あれをああして…\nおや少年！　ごめんね\nすぐに気づけなくて！", 1, 4329, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106801, "何？　クリスが\nアタシを探してる？\n…いないって言って！", 1, 4330, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106801, "よければリノちゃんと\nシズルちゃんにも\n顔を見せてあげて", 1, 4331, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106801, "少年～！\nもうヘトヘトだよ～！\nお願い癒して～！", 1, 4332, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106801, "…アタシが素直に\n助けてって言えるのは\n少年だけだよ", 1, 4333, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 106801, "朝だよ少年！\n爽やかな朝の\nクレープはいかが？", 1, 4334, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 106801, "お腹空いたな～\nお昼のクレープ\n少年は食べる？", 1, 4335, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 106801, "夜までクレープは\nやめとけ？　ごめん\nそれだけは無理…！", 1, 4336, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106801, "アタシこんなに\nゆっくりしちゃって\nいいんだっけ～？", 1, 4338, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106801, "…これ\nネネカが変身\nしてないよね？", 1, 4339, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106801, "いい香りだ…\n解析完了\n今度生成しようっと♪", 1, 4340, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106801, "安心するな～…\nアジトに置けないから\n羨ましいよ", 1, 4341, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 106801, "食べ物を消化するって\n仕組み、どんなものか\n気になる…", 1, 4343, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 106801, "速読できるけど\n本を読む時くらい\n文字にじっくり\n浸かりたいんだよね…", 1, 4344, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113701, "エンジェルとして\nみんなを元気に…！\nアカリ、頑張ります♪", 1, 4346, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113701, "お姉ちゃんと一緒に\nお兄ちゃんを…えへへ\nまだかな、お兄ちゃん", 1, 4347, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113701, "エンジェルの音色を\n目指して、今日も\nサックスの練習です♪", 1, 4348, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113701, "お兄ちゃんとの特訓も\n頑張らないと…\n厳しくしてくださいね", 1, 4349, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113701, "かわいいエンジェルの\n格好、お兄ちゃんなら\n喜んでくれますよね♪", 1, 4350, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113701, "エンジェルの力で\nお兄ちゃんのハートも\nイチコロですよ♪", 1, 4351, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113701, "アカリが一番\n元気にしたいのは\nお兄ちゃんですよ♪", 1, 4352, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113701, "お兄ちゃん？\n触りたいなら…\nこことかどうですか♪", 1, 4353, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113701, "特訓の時間ですか？\nアカリ、楽しみに\nしてたんですよ♪", 1, 4354, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113701, "あ、お兄ちゃん！\nそろそろアカリと\n休憩…しませんか♪", 1, 4355, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113701, "この羽、触り心地\nいいですか？　あんっ\nくすぐったいですよぉ", 1, 4356, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113701, "お兄ちゃんも元気に\nなりたいんですか？\nアカリにお任せです♪", 1, 4357, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113701, "やんっ♪\nお兄ちゃんったら\nダイタンですね♪", 1, 4358, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 113701, "お兄ちゃん、朝ですよ\nすっごく気持ちのいい\nお天気ですよ…♪", 1, 4359, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 113701, "お昼寝しませんか\nお兄ちゃん？\nアカリの膝枕で…♪", 1, 4360, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 113701, "夜ですね、お兄ちゃん\nアカリ、なんだか胸が\nドキドキしちゃいます", 1, 4361, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113701, "フカフカですね…\n隣にお兄ちゃんが\nいてくれたらなぁ…♪", 1, 4363, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113701, "えへへ、アカリも\nこんな風にかわいく\nなりたいな～", 1, 4364, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113701, "お兄ちゃんが好きな\n匂いなのかな？\n参考にしますね！", 1, 4365, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113701, "体が火照ってきて\nなんだかアカリ…\nクラクラしちゃう…", 1, 4366, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 113701, "はむ、んぐ…あっ\nほっぺについちゃった\nベトベトですよぉ～", 1, 4368, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 113701, "エンジェルって色んな\n物語に出てきますよね\nもっと調べなくちゃ！", 1, 4369, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103201, "あの方の前では\n常に気高く\nありたいですわね", 1, 4371, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103201, "どんなに道に迷っても\nあなたさまの元には\n絶対に辿り着く\n自信がありますわ", 1, 4372, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102801, "あんたと一緒に\nいるときが一番楽しい\n一時かもしれないわ", 1, 4373, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102801, "いつかあんたと一緒に\nサレンディア救護院を\n切り盛り…なんてね♪", 1, 4374, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113801, "今日も\nエンジェルとして、\nみんなの幸せの\nために頑張らないと", 1, 4375, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113801, "せっかく羽が\nついてるのに\n飛べないのが残念だわ", 1, 4376, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113801, "この格好、子供たちに\n妙にウケがいいわね\nま、悪くないけど", 1, 4377, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113801, "や、やっぱりみんな\n注目してくるわね…\nエンジェルって珍しい\nからしかたないか", 1, 4378, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113801, "エンジェルになると\n人見知りがなおる！\n…わけないか", 1, 4379, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113801, "エンジェルの活動は\n大変だけど、自分が\n成長してるって\n実感がうれしいわね", 1, 4380, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113801, "もし誰かの願いを\nかなえられるなら\n私はアイツの…\nって何考えてるのよ！", 1, 4381, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113801, "何ボサっとしてるの！\nさあ、街に出て\n人助けするわよ！", 1, 4382, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113801, "エンジェリック\nアロー！\nなんてね…え、アンタ\nいつから見てたのよ！", 1, 4383, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113801, "コラ！\n羽は触らないで！\n壊れたらどうするのよ", 1, 4384, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113801, "エンジェルじゃなく\nても、アンタはいつも\n人助けしてるわよね\n…ちょっと尊敬するわ", 1, 4385, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113801, "この服なら他の子より\n私を見てくれるかも…\nって、な、何\n見てるのよ！", 1, 4386, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113801, "ね、ねえ、もし私が\n困ってたら、一番に\n駆けつけて…やっぱり\n何でもないわ！", 1, 4387, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 113801, "おはよう…あ、あの、\nエンジェルとして\n町に行くんだけど…\n一緒に行かない？", 1, 4388, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 113801, "ふぅ、いいことした\n後のランチは\nおいしいわね", 1, 4389, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 113801, "もうこんな時間…\n夜更かししてないで\n早く寝なさいよね", 1, 4390, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113801, "いい座り心地ね～\nまさに天にも昇る\n心地よさだわ～", 1, 4392, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113801, "ふふ、かわいい…\nあ、羽付けたら\nもっとかわいく\nなるんじゃないかな？", 1, 4393, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113801, "はぁ～落ち着くわ～\n回復アイテムみたいで\n癒やされちゃうわ～", 1, 4394, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113801, "わっ、とと…\n危ない、羽が\nこげちゃうところ\nだったわ", 1, 4395, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 113801, "人の役に立つのって\nすごく大変だから、\nこれでライフ回復よ！", 1, 4397, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 113801, "エンジェルに関する\n本はあるかしら…あ！\nこれ探してた攻略本！", 1, 4398, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113901, "ふっふっふ\n吸血鬼らしく\n驚かせてあげましょう", 1, 4400, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113901, "獲物はどこですか～！\nう～ん、もうちょっと\n迫力が欲しいですね", 1, 4401, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113901, "トリックオアトリート\n…返事がないので\nイタズラしておきます", 1, 4402, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113901, "ハロウィンですし\nたまには騎士さんでも\n誘ってあげますかね", 1, 4403, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113901, "マントは動きに\nメリハリがついて\nいいですね", 1, 4404, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113901, "色んな仮装が見られて\nやっぱりハロウィンは\n最高ですね", 1, 4405, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113901, "吸血鬼らしい行動って\n噛みついたり…いや\nそれは大胆過ぎです！", 1, 4406, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 113901, "呼びましたか？\n手短にお願いしますよ\n忙しいんですから！", 1, 4407, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113901, "え？　衣装に穴が？\nありがとうございます\n…よく見てるんですね", 1, 4408, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113901, "ふわあ…吸血鬼の\n姿でも、眠い時は\n眠いんですよね…", 1, 4409, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113901, "ハロウィンってライブ\nとは違った賑やかさが\nあってワクワクします", 1, 4410, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 113901, "私を驚かそうとしても\n無駄ですよ！\nすぐ分かるんですから", 1, 4411, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 113901, "騎士さん、一緒に\nハロウィンを…いいえ\nなんでもないですっ！", 1, 4412, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 113901, "仕事で寝不足だから\n太陽がまぶしいです…\n私、吸血鬼っぽいかも", 1, 4413, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 113901, "お昼ご飯は真っ赤な…\nトマト尽くしのお弁当\nにしてみました", 1, 4414, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 113901, "夜はハロウィンが\n盛り上がる時間…\n行きますよ騎士さん！", 1, 4415, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 113901, "このフィット感\nどうにか服作りにも\n活かせれば…！", 1, 4417, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 113901, "よく出来てますね…！\nぬいぐるみの服を作る\nのも楽しそうですね！", 1, 4418, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 113901, "いい感じですね…\n服に合わせた香りって\nいうのも、いいかも…", 1, 4419, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 113901, "はぁ、温まりますね…\nおっと、マントに火が\n点いちゃいそうです！", 1, 4420, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 113901, "あぁ、美味しい～\n食べた分は、働いて\n燃焼しますからね！", 1, 4422, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 113901, "伝説の吸血鬼…\n夜を統べる者…\nほうほう…", 1, 4423, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 113901, "やっぱりハロウィンと\nいえばお菓子です！\nさぁ配りましょうか！", 1, 4425, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114101, "この格好…\nカッコイイのに\n動きやすくて\n着心地も最高ッス！", 1, 4427, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114101, "キメポーズはこう…\nいやこっちのほうが…\nなやましいッス…！", 1, 4428, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114101, "トリックオア\nジャスティス！\nって言ったら何か\nもらえるッスかね？", 1, 4429, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114101, "新しい必殺技の\n特訓もしなくちゃ\nいけないッスね…\n忙しいッス！", 1, 4430, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114101, "自分のこの姿\nトモね～ちゃんにも\nみせてあげるッス！", 1, 4431, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114101, "ホワイトタイガー\n参上ッス！\n今のポーズは\nキマったッス！", 1, 4432, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114101, "あの人と一緒だと\nほんとにヒーローに\nなれる気がするッス！", 1, 4433, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114101, "どうッスか？\nこの色、自分に\n似合ってるッスかね？", 1, 4434, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114101, "ヒマだったら\n必殺技の特訓に\n付き合ってもらって\nいいッスか？", 1, 4435, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114101, "ちょうどいいところに\n来たッスね！\nこのキメポーズ\nどうッスか？", 1, 4436, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114101, "お！　この格好、\n気になるッスか？\nじっくり見ても\nいいッスよ♪", 1, 4437, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114101, "あんたも一緒に\n新しい技の練習\nどうッスか？", 1, 4438, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114101, "今日もあんたと一緒に\nランドソルの平和を\n守るッスよ！", 1, 4439, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 114101, "朝ッス！\n一緒に早朝特訓\nやるッスよ！", 1, 4440, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 114101, "お昼休憩ッス！\nヒーローにも\n休息は必要ッス！", 1, 4441, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 114101, "闇夜に映える\n白い衣装で\n平和を守るッスよ！", 1, 4442, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114101, "トラも狩りの時以外は\nごろごろしてるって\n本で読んだッスよー", 1, 4444, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114101, "もこもこッスね♪\nこのかわいらしさを\n守ってあげるのも\nヒーローの役目ッス！", 1, 4445, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114101, "いい匂いッスね…\nリラックスして\n瞑想するッス！", 1, 4446, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114101, "燃える炎は\nヒーローの心意気ッス\nいつまでも\n見てられるッスよ", 1, 4447, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 114101, "もぐもぐ…\nいっぱい食べて\n強いヒーローに\nなるッスよ！", 1, 4449, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 114101, "ちゃんと勉強の本も\n読んでるッスよ…！\nヒーローには賢さも\n必要らしいッス！", 1, 4450, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 114101, "わわっ、お菓子が\nいっぱいッス！\nみんなで\n食べるッスよ！", 1, 4452, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103001, "ワタシがチューシンと\nなった今、天下統一は\n目の前デス！", 1, 4454, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103001, "もうショーグンってば\nそれはセイシツに\nすることデ～ス…", 1, 4455, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114001, "女幽霊…うらめしや…\nうーん、これは\nちょっと違う気が\nするな…", 1, 4456, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114001, "何だか寒気がする…\nま、まさか\nこの部屋にも幽霊が？", 1, 4457, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114001, "ハロウィンは楽しいね\n童心に帰ったような\n気分だよ", 1, 4458, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114001, "今度のハロウィンは\nユイとヒヨリを\n誘ってみようかな", 1, 4459, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114001, "ハロウィンのお菓子は\nどれもかわいいね\n…一つくらいなら\nいいかな？", 1, 4460, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114001, "次は彼と\nどんなハロウィンを\n過ごそうかな…", 1, 4461, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114001, "こんな賑やかさも\nいいものだね\n彼といられるのなら\nなおさら…", 1, 4462, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114001, "…いたずらしようと\nしてない、キミ？\nふふ、お見通しだよ", 1, 4463, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114001, "え、私にもお菓子を？\nありがとう…ふふ\nうれしいものだね\nハッピーハロウィン！", 1, 4464, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114001, "どうしたの？\n出かけるなら\n私も同行しようか", 1, 4465, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114001, "この格好、舞踏会にも\n出られそうだね\n一曲、踊ってみる？", 1, 4466, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114001, "やるからには本気で\nやらせてもらおう\nトリックオア\nトリート！", 1, 4467, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114001, "他の子に目移りする\nのも分かるが、その…\nせっかくの特別な衣装\n見てほしいな", 1, 4468, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 114001, "寝起きの体を\n慣らしておこうか\nほら、剣を取って", 1, 4469, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 114001, "お菓子もいいけど\nちゃんと昼食も\nとるんだよ", 1, 4470, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 114001, "さあ、行こうか！\nハロウィンは\nこれからが本番だよ", 1, 4471, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114001, "ハロウィンはまだ\nこれからだ\n一休みしようか", 1, 4473, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114001, "キミたちも\n仮装に興味は\nあるかな？　…ふふ", 1, 4474, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114001, "いい香りだけど\n幽霊が集まったり\nしないだろうか…？", 1, 4475, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114001, "暖かい…一息ついたら\nハロウィンの\nパレードを\n見に行こうかな", 1, 4476, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 114001, "ハロウィンを本気で\n楽しむなら\n食事もしっかり\nとっておかないとね", 1, 4478, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 114001, "ハロウィンの\nホラー小説か…\nけ、結構\n怖いものだね…？", 1, 4479, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 114001, "お菓子がいっぱいだ…\nこういうのって\n見ているだけで心が\n躍ってしまうね", 1, 4481, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114201, "こんなにかわいい\n武器で戦えるとは…\nすごいものだな\n魔法少女は", 1, 4483, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114201, "決めポーズの訓練も\nしっかりやっておくか\n…誰も見ていないな？", 1, 4484, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114201, "こういう格好は\nユキあたりがすごく\n似合いそうだな", 1, 4485, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114201, "子どもたちにサインを\n求められてしまった…\nこれもまた私の使命！", 1, 4486, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114201, "この格好は目立つな…\nいや、提督とは人々の\n視線を集めるものだ！", 1, 4487, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114201, "あいつと作戦会議を\nしておくか。あれで\nなかなか頼れるからな", 1, 4488, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114201, "あいつには私の勇姿を\nちゃんと見ていて\nほしいものだな", 1, 4489, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114201, "一仕事終えたことだし\n一緒に甘いものでも\n食べに行くか？", 1, 4490, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114201, "怪しげな気配がするな\n貴公も警戒を\n怠るなよ！", 1, 4491, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114201, "シオリに魔法少女の\n本を借りたんだ\n一緒に読まないか？", 1, 4492, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114201, "巡回の時間まで\nお茶にでもするか\n貴公も付き合うがいい", 1, 4493, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114201, "貴公がサポートして\nくれるのなら心強いな\n頼りにしているぞ", 1, 4494, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114201, "頭を撫でるな～！\n…まったく、貴公だけ\nだぞ、こんな風に私を\n撫でてくるのは…", 1, 4495, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 114201, "起きろ貴公！\n魔法少女の朝は\n早いぞ！", 1, 4496, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 114201, "ほら、食後の\nデザートだ\nて、適度な糖分は\n体に必要なんだ！", 1, 4497, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 114201, "ふわぁ…魔法少女も…\n眠気、には…い、いや\n寝てないぞ！？", 1, 4498, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114201, "任務や訓練で\n疲れた体に\nこれは効くなぁ…", 1, 4500, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114201, "うむ、かわいいな\nモーラのやつを隣に\n並べようか", 1, 4501, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114201, "ああ、身も心も\n安らぐ…\nまるで魔法だな…", 1, 4502, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114201, "この温かさ…\n愛と希望に\n通じるものがある…", 1, 4503, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 114201, "食事の大切さは\n魔法少女も軍人も\n変わりない\nしっかり食べるんだ！", 1, 4505, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 114201, "魔法少女の本を\n読んでるのだが\nわ、私もこんななのか", 1, 4506, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103301, "オラとあんちゃんは\nボケもツッコミも\nあうんの呼吸だべ♪", 1, 4508, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103301, "あんちゃんと二人で\nネタ合わせ…初めての\n共同作業だべ♪", 1, 4509, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 192501, "ぴっぷる！", 1, 4510, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 192501, "ぴっぷるぴっぷる♪", 1, 4511, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 192501, "ぴっ！", 1, 4512, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 192501, "ぷっぷー", 1, 4513, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 192501, "ぴぃ？", 1, 4514, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 192501, "ぴっぷるんるん♪", 1, 4515, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 192501, "ぷぅ！", 1, 4516, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 192501, "ぴぴっ♪", 1, 4517, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 192501, "ぷぅー…", 1, 4518, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 192501, "ちゅ！", 1, 4519, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 192501, "ぷはー", 1, 4520, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 192501, "ぴぇー", 1, 4521, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114301, "魔法少女になったら\n髪型も変わるんだね\n色々試してみようかな", 1, 4531, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114301, "…煌く剣技で、\n悪を断つ！\nうん、今日も\n完璧なポーズだね♪", 1, 4532, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114301, "ミクマ流剣術で、\n魔法少女の技に使え\nそうなものは…よし、\n早速試してみよっと♪", 1, 4533, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114301, "魔法少女になったから\nには、サインの練習も\nしないと…\nよし、頑張るぞ！", 1, 4534, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114301, "魔法少女の姿はすごく\nかわいいけど…この\n姿はジュンさんたちに\n見せられないなぁ…", 1, 4535, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114301, "あの人は魔法少女\nのこと好きなのかな？\n…好きだったら\nうれしいな♪", 1, 4536, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114301, "…やっぱり、\nあの人がいないと\n特訓に身が入らない…\n早く帰ってこないかな", 1, 4537, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114301, "もし時間があるなら、\n私と街の見回りに\n行かない？", 1, 4538, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114301, "あ、いいところに！\n新しい魔法剣術を\n編み出したから、\n見てくれないかな？", 1, 4539, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114301, "魔法少女の姿が気に\nなるのは分かるけど…\nず、ずっと見られる\nのは照れるからやめて", 1, 4540, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114301, "変身ポーズ中の動き、\n少し変えた方が\nいいかな…\nあなたはどう思う？", 1, 4541, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114301, "よければ、\n魔法少女の特訓に\n付き合ってくれない？\nその…二人きりでさ…", 1, 4542, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114301, "私…あなただけの\n魔法少女になっても…\nふふっ、な～んてね♪", 1, 4543, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 114301, "今日もいい朝だね\nさあ、一緒に\n街の見回りに行こうか", 1, 4544, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 114301, "もう昼か…なにか\n食べたいものある？\n簡単なのでいいなら、\n私が作ってあげるよ", 1, 4545, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 114301, "そろそろ休もうか\n…なんなら、添い寝\nしてあげようか？\nふふっ、冗談だよ♪", 1, 4546, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114301, "これは…\n心身ともに安らぐね\nなんだか\n眠くなっちゃいそう", 1, 4548, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114301, "か、かわいい…これ、\n魔法で動かせたり\nしないかな？", 1, 4549, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114301, "いい香りだね\nなんだか頭が冴えて\nきたし、新しい技でも\n考えようかな", 1, 4550, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114301, "あったかいな…\nこの温もりは、\n魔法少女の魔法でも\n再現不可能だね…", 1, 4551, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 114301, "こ、これは…\nおいしすぎて手が\n止まらないかも", 1, 4553, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 114301, "この本の\nキャラの動き、\n変身ポーズの\n参考にできるかも！", 1, 4554, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114401, "メリークリスマス！\nさあ、素敵な\nパーティーを始めると\nいたしましょう！", 1, 4556, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114401, "ふふっ、\nいくつになっても、\nやはりクリスマスは、\nワクワクしますわね♪", 1, 4557, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114401, "自分で料理をしてみて\nシェフたちのすごさが\n改めて分かりましたわ", 1, 4558, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114401, "何事も自分で\n体験してみると\n新しい発見が\nあるものですわね♪", 1, 4559, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114401, "やはり、クリスマスは\n街が活発になり\nますわね…これは、\n絶好の商機ですわ♪", 1, 4560, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114401, "他のクリスマス料理も\n作れるようになったら\nあのお方は食べて\nくださるかしら…？", 1, 4561, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114401, "あのお方に贈る\nプレゼント…\nこれはかなりの\n難題ですわね…", 1, 4562, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114401, "メリークリスマス！\nよろしければ、一緒に\nクリスマスの準備を\nいたしませんか？", 1, 4563, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114401, "まあ、クリスマスの\n飾りつけを手伝って\nいただけるのですか？\nありがとうございます", 1, 4564, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114401, "クリスマスに合わせて\nあつらえた自慢の衣装\nなのですが…その、\nいかがでしょうか？", 1, 4565, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114401, "幼い頃はサンタさんが\nいらっしゃるのを\n待ちわびたものです\n今は…内緒ですわ♪", 1, 4566, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114401, "も、もしお時間が\nあるようでしたら、\nクリスマスの街を\n一緒に歩きませんか？", 1, 4567, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114401, "来年のクリスマスも\nあのお方と一緒に\n迎えられたら…", 1, 4568, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 114401, "時は金なり…\nせっかくの朝ですもの\n時間を有効活用\nいたしましょう！", 1, 4569, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 114401, "お昼になりましたわね\nさあ、\nパーティーの準備を\n進めますわよ！", 1, 4570, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 114401, "静かな夜ですわね…\nもうちょっとだけ、\nあなたさまとお話を\nしていたい気分です", 1, 4571, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114401, "適度な休息は\n成功の秘訣ですわ！", 1, 4573, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114401, "このかわいらしさ…\nいつまでも見て\nいられそうですわ♪", 1, 4574, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114401, "人をもてなすには\nこうしたところにも\n気を配らないと\nいけませんわね", 1, 4575, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114401, "暖かい…\nこういう憩いの\nひと時こそ、大事に\nしたいものですわね", 1, 4576, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 114401, "この奥深い味わい…\n何か隠し味など\n使われていまして？", 1, 4578, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 114401, "クリスマス料理の\nレシピも、\nこれだけあると\n迷ってしまいますわね", 1, 4579, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 114401, "この絵本、とっても\n面白いつくりですわね\nうちの商品に\nしたいぐらいですわ", 1, 4581, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 114401, "これだけ大きな\n着ぐるみ…\n職人の技術が\n光りますわね", 1, 4582, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114601, "クリスマスといえば\nパーティーよね！\nいっぱい飲むわよ～♪", 1, 4583, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114601, "私を麦しゅわだけの\n女と思ったら\n大間違いなんだから♪", 1, 4584, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114601, "クリスマスらしい\n飲み物も結構いいわね\n私も今度\n作ってみようかしら", 1, 4585, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114601, "暖かい部屋で飲む\n麦しゅわも\nまた格別なのよね…", 1, 4586, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114601, "今日はオフの日だし\n朝から飲んでも\n怒られないわよね？", 1, 4587, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114601, "クリスマスの\nデートスポットで\n彼と一緒に…ってなに\n考えてるのユカリ！？", 1, 4588, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114601, "背伸びしなくても\nありのままの自分を\n受け入れてくれるって\nすごく…幸せ…", 1, 4589, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114601, "大丈夫よ～\n飲んでない\n飲んでないひゃら～", 1, 4590, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114601, "クリスマスは\n特別だもの\n特別な一杯を\n飲まないとね♪", 1, 4591, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114601, "メリークリスマース！\nほらほらぁ\n乾杯しましょ♪", 1, 4592, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114601, "お姉さんの\n頼れるところ\n見せてあげるわね", 1, 4593, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114601, "何かあったら\n頼ってくれても\nいいんだからね？", 1, 4594, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114601, "ふふ…これからも\nキミのこと…\n頼りにしちゃうからね", 1, 4595, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 114601, "さわやかな朝ね\n…二日酔いがないと\n朝ってこんなに\n気持ちいいのね", 1, 4596, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 114601, "昼間から飲むのって\nまた格別よね…って\nガマン、ガマンよ…", 1, 4597, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 114601, "もうこんな時間かあ…\nあとは寝るだけだし\nちょっとくらいは…", 1, 4598, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114601, "ゴロゴロするのって\n最高ね～\nあとは麦しゅわが\nあればなぁ", 1, 4600, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114601, "私だって…\nかわいいものは\n好きでいいわよね？", 1, 4601, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114601, "んっ～…いい香りね\n日頃の疲れが\n取れる気がするわ", 1, 4602, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114601, "ポカポカする～\n…なんだかのどが\nかわいてきたわね", 1, 4603, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 114601, "もぐもぐ…労働の\n後のご飯はやっぱり\nおいしいわぁ…", 1, 4605, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 114601, "クリスマスデート\n完全攻略マニュアル？\n…読んでおかなくちゃ", 1, 4606, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 114601, "あら、絵が飛び出して\n見えるけど…\n酔っ払ってるせいじゃ\nないわよね…？", 1, 4608, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 114601, "いい手触りね～\nウチにもほしいけど\n麦しゅわをこぼしたら\n大変そうだわ…", 1, 4609, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101601, "ヒデサイの視線\nずっと独り占め\nしてたいな～", 1, 4610, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101601, "ヒデサイといるだけで\nうち、おに楽しいし！\nヒデサイもっしょ～？", 1, 4611, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114501, "メリークリスマス！\nふふ、口にするだけで\n何だか楽しくなるわね", 1, 4612, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114501, "いつもは子どもたちを\n見守る方だけど\n今ははしゃいでも\nいいわよね？", 1, 4613, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114501, "ツリーや料理\nそしてプレゼント！\nいくつになっても\nやっぱり心躍るわね♪", 1, 4614, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114501, "飾り付けも料理も\nいいセンスしてるわね\n救護院のパーティーの\n参考にさせてもらうわ", 1, 4615, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114501, "焼きそばパンを\nクリスマスの定番に\nするにはどうすれば…", 1, 4616, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114501, "これ、あいつの\n好きそうな料理だわ\n取っといて食べさせて\nあげようかしら♪", 1, 4617, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114501, "あいつのプレゼント\nちょっとだけ奮発\nしたのよね…\n喜んでくれるかしら", 1, 4618, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114501, "このドレス\n似合ってるかしら？\n今だけは見とれても\n許してあげるわ♪", 1, 4619, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114501, "見て、このケーキ！\nおいしそうでしょ\nたまにはこんな贅沢も\nいいわよね♪", 1, 4620, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114501, "料理も飲み物も\nおいしいわね！\nいっぱい食べて\n元を取らないと♪", 1, 4621, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114501, "子どもたちと一緒に\n雪遊び、楽しそうね\nあんたもどう？", 1, 4622, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114501, "せっかくの\nクリスマスなんだもの\nエスコート\nよろしくね♪", 1, 4623, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114501, "寒い？　そう…\nなら、もっとこっち\n来ていいわよ…？", 1, 4624, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 114501, "ほら、早く起きて？\nパーティー用の\n買い出しに\n付き合ってちょうだい", 1, 4625, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 114501, "パーティーの料理の\n準備をしながらお昼も\n作っておいたわ\n我ながらいい手際ね", 1, 4626, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 114501, "にぎやかなパーティー\nもいいけど、聖夜の\n余韻を感じるのも\n結構素敵ね", 1, 4627, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114501, "いい座り心地だわ～\n腕のいい職人の仕事ね\nうちでも扱いたいわ", 1, 4629, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114501, "かわいい！\n子どもたちに\nプレゼントしたら\n喜んでくれるかも", 1, 4630, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114501, "華やかな香りね\nクリスマスに\nピッタリだわ", 1, 4631, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114501, "寒い日にはやっぱり\nこれよね…\nはぁ、極楽極楽♪", 1, 4632, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 114501, "んー、おいしいわ！\n焼きそばパン味の\nものも、作れない\nかしら？", 1, 4634, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 114501, "たまにはのんびり\n読書でも…なんだか\nラブロマンスばかりね\nクリスマスだから？", 1, 4635, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 114501, "これ、面白いわね！\n子どもたちの\nプレゼントに\nピッタリだわ", 1, 4637, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 114501, "こんないい毛並み\n初めて見るわ…\n一体どこで手に\n入るのかしら？", 1, 4638, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114701, "アタシもみんなと\n一緒に正月を\n楽しんでいいのか？", 1, 4639, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114701, "今年こそ、もう少し\n身長が伸びます\nように…", 1, 4640, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114701, "げっ！　お年玉が\n全然残ってないぞ！？\nそんな、嘘だろ…？", 1, 4641, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114701, "大勢で過ごす\n正月か…初めてだけど\n悪くないもんだな", 1, 4642, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114701, "んー…ここじゃ\n天楼覇断剣の\n出番はなさそうだな", 1, 4643, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114701, "アイツらと作る\n戦い以外の思い出も\nもっと増やしたいな", 1, 4644, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114701, "こんなに賑やかな\n正月は初めてだ\n…温かくて、いいな", 1, 4645, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 114701, "記憶がなくったって\n楽しい思い出は\n新しく作れるよな？", 1, 4646, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114701, "早くでかけるぞ！\nアタシはもっと\n正月を楽しみたい！", 1, 4647, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114701, "なあ、オマエはもう\nノウェムって名前\n覚えてくれたよな？", 1, 4648, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114701, "この服、アタシに\n似合ってるのか？\n…そっか。\nありがとな！", 1, 4649, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 114701, "オマエらと見た朝日は\nきっと忘れないぞ！\n思い出ができたな！", 1, 4650, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 114701, "いつかオクトーも\n一緒に、みんなで\n正月を過ごせたらな…", 1, 4651, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 114701, "朝から元気だな！\nアタシもいっちょ\n見習ってみるか！", 1, 4652, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 114701, "昼飯は何にする？\n正月はうまいものが\n多くて迷うよな！", 1, 4653, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 114701, "いい夜だな！\nアタシと一緒に\n夜更かしを楽しむか？", 1, 4654, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 114701, "正月らしく、たまには\nゆっくりするのも\nいいかもな…", 1, 4656, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 114701, "なんだ？\nオマエも初詣に\n行きたいのか？", 1, 4657, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 114701, "すーはー…\n神殿のニオイがするな\nいい事ありそうだ！", 1, 4658, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 114701, "コイツがあれば\nうまい魚が\n焼けそうだな…", 1, 4659, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 114701, "ううー\n食べ過ぎて帯が\nきつくなってきたぞ…", 1, 4661, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 114701, "おみくじって\n書いてあることが\nむずかしいんだよな…", 1, 4662, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 180101, "困ってる人は\nいないみたいだし…\nちょっとここで、\n特訓しようかな♪", 1, 4664, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180101, "空が飛べるんだから\nもっとたくさんの人を\n助けにいけちゃう！\n…ここじゃ飛べないや", 1, 4665, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180101, "炎の翼\nかっこいいなー！\nこれ、もっと大きく\nできないのかな？", 1, 4666, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180101, "やっぱり変身すると\nお腹すくんだね…\n…今ならペコさんの\n食欲の理由が分かるよ", 1, 4667, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 180101, "色なんかは違うけど…\nマントがあるから\n騎士くんとお揃いに\nなったみたい♪", 1, 4668, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 180101, "…騎士くんがいないと\nなんか調子でない…\n早く会いたいよぉ…", 1, 4669, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 180101, "この姿になると、\n騎士くんを近くに\n感じる…なんだろ\nドキドキしちゃう…", 1, 4670, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 180101, "騎士くん騎士くん！\n今大丈夫？\nトレーニングメニュー\n考えるの手伝って！", 1, 4671, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180101, "…うにゃっ！？\nこ、この姿になっても\n尻尾は触っちゃ\nダメー！", 1, 4672, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180101, "もしかして、この服が\n気になってるの？\nだったら、もっと\n近くで見ていいよ♪", 1, 4673, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180101, "あ、騎士くん！\n困ってる人がいないか\n見回りに行くんだけど\nついてきて欲しいな！", 1, 4674, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 180101, "耳に触りたいの？\nだったら頭もなでて！\nそしたら、好きなだけ\n触っていいよ♪", 1, 4675, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 180101, "…あたし、強くなって\n騎士くんを守るよ！\nだから…これからも\n一緒にいていい？", 1, 4676, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 180101, "おっはよー！\nさあさあ騎士くん！\n今日も一緒に\nはりきっていこー♪", 1, 4677, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 180101, "お昼、お昼～♪\n今日のご飯は何かな～\nあ、騎士くん！\n早くお昼食べ行こ！", 1, 4678, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 180101, "今日もお疲れ様ー！\n騎士くん、あたしが\nマッサージしよっか？\n結構評判なんだよ！", 1, 4679, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180101, "うにゃぁ…\nこの気持ちよさ…\n溶けちゃいそう…\nにゃぁ…", 1, 4681, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180101, "か、かわいい～\nこれ、なでなで\nしていいんだよね…？\nよしよし…♪", 1, 4682, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180101, "とってもいい香り！\n気分も落ち着くし、\n特訓の後なんかに\nいいかもー！", 1, 4683, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 180101, "ぬくぬくだぁ…\nなんだか、\nふにゃふにゃに\nなっちゃうよぉ…", 1, 4684, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 180101, "お、おいひぃ…！\nこの姿になると\nお腹すくし、すっごく\n助かっちゃった！", 1, 4686, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 180101, "この本で勉強して、\nぶんぶりょーどーの\n超ウルトラヒヨリに\nなっちゃうぞー！", 1, 4687, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111801, "今年もみんなで\nおいしいものを\nたくさん食べたいです", 1, 4689, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111801, "挑戦は大切ですよね☆\nというわけで\nおせちに魔物を…", 1, 4690, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111801, "おせちは縁起物です！\n食べれば食べるほど\n幸せになるはずです！", 1, 4691, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111801, "食べ物がたくさん\n入った福袋は\nないんでしょうか…？", 1, 4692, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111801, "初詣で何を\nお願いしましょう…\n五穀豊穣？", 1, 4693, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111801, "ずっとこうして\n年の始まりを\n過ごしたいですね", 1, 4694, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111801, "いつか二人きりで\n初日の出を\n見たいです", 1, 4695, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 111801, "お餅おかわりです！\nいくらでも\n食べられますよ！", 1, 4696, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111801, "おせちの食材を\n取りに行きませんか？\nいい運動です☆", 1, 4697, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111801, "凧揚げって\n難しいですね～\n糸が絡まっちゃいます", 1, 4698, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111801, "一年の計は元旦にあり\n今年一年分の\n献立を作りましょう！", 1, 4699, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 111801, "ほらほら！\nもっと近くで着物を\n見ていいんですよ☆", 1, 4700, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 111801, "あなたはどんな\n初夢を見ましたか？\nわたしは…内緒です☆", 1, 4701, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 111801, "年明けの朝は\n心なしか\n気持ちいいですね☆", 1, 4702, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 111801, "お雑煮のあとは\nおしるこで\nおやつタイムです！", 1, 4703, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 111801, "あたたかくして\n幸せな初夢を\n見ましょうね", 1, 4704, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 111801, "はぁ～屋台巡りを\n満喫しました☆\n一休み一休み", 1, 4706, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 111801, "縁起のいい\nぬいぐるみを並べて\n福を呼びましょう！", 1, 4707, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 111801, "おしるこのアロマって\nないんでしょうか？\nそれなら作るしか…", 1, 4708, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 111801, "ここでお餅を\n焼いたらおいしく\nなりそうですね～♪", 1, 4709, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 111801, "おいしすぎて\n食べる手が\n止まりません～", 1, 4711, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 111801, "新しいおせち料理の\nヒントは…なるほど…\nふむふむ…", 1, 4712, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100601, "お兄ちゃんとあんな\n特訓やこんな特訓が\nしたいなぁ、えへへ♪", 1, 4714, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100601, "お兄ちゃんに褒めて\nもらうと、身体が\nじぃーんと\n熱くなっちゃいます♪", 1, 4715, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 115001, "見目麗しい衣装に\n身を包むのも\n悪い気分では\nありませんね", 1, 4716, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115001, "縁起物で\n験を担ぐというのも\nたまにはよいでしょう", 1, 4717, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115001, "非効率なので\nあまり行いませんが\n私も手料理くらいは\n作れるのですよ", 1, 4718, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115001, "研究のためなら\n時に遠路を超え\n実地へ赴くことも\n必要なのです", 1, 4719, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 115001, "トゥンヌスの\n突然変異…\n大いに興味を\nそそられますね", 1, 4720, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 115001, "漁師の技法も\n再現できたことで\nまた一つ、仮説が\n実証できました", 1, 4721, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 115001, "正月くらいは\n私ものんびりしたい\n気持ちなのですよ\n意外ですか？", 1, 4722, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 115001, "模倣に頼らずとも\n衣装に合わせた\n所作程度は\nできて当然です", 1, 4723, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115001, "お年玉の使い道は\n思いつきましたか\nゆっくり考えても\nいいのですよ", 1, 4724, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115001, "トランプでも花札でも\n相手をしましょう\n手心が必要なら\nいつでも言いなさい", 1, 4725, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115001, "初詣でお祈りする\n内容ですか\n…ヒミツ、として\nおきましょう", 1, 4726, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 115001, "私たちでオーマ中の\nトゥンヌスを\n釣り上げましょうか\n…冗談ですよ", 1, 4727, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 115001, "見返りを求めない願い\nそれは祈りに通じるの\nかもしれませんね", 1, 4728, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 115001, "…貴方がその眠たげな\n顔を洗ってくる間に、\n温かいお茶でも\n淹れておきましょう", 1, 4729, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 115001, "さて、本格的に\n活動を再開します\n貴方も同行しなさい\n昼食は済みましたか？", 1, 4730, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 115001, "私はもう少し\n作業を片付けてから\n寝ることにします\n早く眠りなさい", 1, 4731, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115001, "少し休みます…\n時間が来たら\n起こすように", 1, 4733, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115001, "贈り物を模倣して\n潜入…試してみる\n価値はありそうですね", 1, 4734, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115001, "嗅覚から得られる\n情報量は侮れません\n動物の模倣において\n注意すべき点ですよ", 1, 4735, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 115001, "穏やかな炎による\nリラックス効果は\n本能によるものかも\nしれませんね", 1, 4736, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 115001, "いかなる強者でも\n兵站、補給は\n必要なものです", 1, 4738, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 115001, "あらゆる学問は\n完璧な模倣のための\n大きな礎となります", 1, 4739, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 115501, "新たに得たこの力で\n仲間と目指す未来を\n切り拓いてみせます", 1, 4741, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115501, "ここは、みなさまと…\nわたくしの\n帰る場所でございます", 1, 4742, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115501, "主さまも精霊の召喚を\n試してみますか？", 1, 4743, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115501, "主さま、おこづかいは\n足りていますか？\n遠慮は無用ですよ", 1, 4744, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 115501, "この装束をまとうと\n自然と背筋が伸びる\n思いがいたします", 1, 4745, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 115501, "進化した精霊に\n負けぬよう\n修行に励みます", 1, 4746, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 115501, "仲間たちといられる\nこの幸せが、末永く\n続きますように…", 1, 4747, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 115501, "主さまのお手手は\n大きくて温かいですね\nぎゅっぎゅっ…ふふ♪", 1, 4748, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115501, "今度、主さまにも\n里に伝わる昔話を\nお話しいたしましょう", 1, 4749, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115501, "里で採れた果物を\n持って参りました\n召し上がってください", 1, 4750, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115501, "里のお土産を\nここのみなさまに\nお配りいたしましょう", 1, 4751, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 115501, "仲間と囲む食卓…この\n温もりは、みなさまの\n心の温かさなのですね", 1, 4752, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 115501, "主さまに名前を\n呼んでいただくと\n心が温かくなります♪", 1, 4753, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 115501, "ふふっ…主さま\n寝癖がついて\nおりますよ？", 1, 4754, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 115501, "今日の昼食は\n里の伝統料理です\nお口に合うでしょうか", 1, 4755, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 115501, "主さまが眠れるよう\n安らぎの風を…\nおやすみなさいませ", 1, 4756, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115501, "この癒し…\n主さまにも味わって\nいただきたい…", 1, 4758, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115501, "この愛らしさ…\n精霊たちと似たものを\n感じます…♪", 1, 4759, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115501, "精霊よ…この安らぐ\n香りを、隅々まで\n届けてくださいまし", 1, 4760, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 115501, "瞑想するのに\nちょうどよい暖かさで\nございます", 1, 4761, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 115501, "進化した精霊を\n使役するためにも\nよく食べて体力を…！", 1, 4763, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 115501, "風の精霊について\nもっと詳しく知って\nおかなければ…", 1, 4764, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 115601, "儀式の時を思い出して\n精霊に呼びかける…\n出ておいで～♪", 1, 4766, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115601, "ひゃうっ！　もう…\n急に背中を触るなんて\nいたずら好きな精霊に\n育てた覚えはないのに", 1, 4767, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115601, "儀式用の衣装…\nあんまり激しい動きは\nできないよね…でも\n戦う時は気にしない！", 1, 4768, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115601, "そうだ…精霊に\n騎士クンのそばで\n見守っていてって\n頼んでみようかな…", 1, 4769, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 115601, "ふふっ、こうやって、\n楽しく精霊とお話し\nしていられる、平穏な\n日々が続くといいな", 1, 4770, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 115601, "き、きき、騎士クン！\nわたしと、あの…！\nうう…練習ですら\n上手く誘えないよぉ…", 1, 4771, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 115601, "あっ、精霊が…\nふふ、応援ありがとう\nわたしの想い…いつか\n必ず伝えるね", 1, 4772, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 115601, "新しく覚えた料理\n作ってみたんだけど\n騎士クンも食べない？", 1, 4773, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115601, "騎士クン、\n疲れてない？\nわたしと精霊で\n治療するよ？", 1, 4774, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115601, "もう、いたずらしない\n…騎士クン！？\nご、ごめんなさい！\n精霊だと思って！", 1, 4775, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115601, "騎士クン！\n時間が合ったら、\n精霊と一緒に\nお話しない？", 1, 4776, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 115601, "…こういう服着るの\n初めてなんだけど…\nえっ、かわいい…？\nあ、ありがとう…！", 1, 4777, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 115601, "困ったことがあったら\n相談してね。わたし、\n騎士クンのためなら…\n色々できるよ、色々！", 1, 4778, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 115601, "おはよう騎士クン！\n今からお外で\n瞑想するんだけど…\n騎士クンもどうかな？", 1, 4779, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 115601, "お昼の時間だ\nご飯ならわたしが\n用意するから\nゆっくりしてて", 1, 4780, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 115601, "もうこんな時間…\n騎士クン、はいどうぞ\n寝る前の温かいミルク\nぐっすり眠れるよ♪", 1, 4781, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115601, "…うん、落ち着く\n精霊との魔法で\nこんな風に誰かを\n安心させたいな", 1, 4783, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115601, "かわいい…\n精霊も気に入ってる…\nそうだ、同じ物\n作ってあげようかな？", 1, 4784, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115601, "いい香り…これ、\nうちに置いたら、\nお母さん喜ぶかも！", 1, 4785, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 115601, "あったかい…\nまるで誰かが\n抱きしめてくれた\nみたい…", 1, 4786, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 115601, "自然の恵みに\n感謝して…\nいただきます♪", 1, 4788, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 115601, "もっと精霊について\n勉強して、みんなを\n守れるように\nなりたいな…", 1, 4789, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102201, "アイツといると胸が\nぎゅーっとなる…\nこれってもしかして…", 1, 4791, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102201, "アンタとならきっと\nどんなゲームでも…\nそ、その、たた、\n楽しいって思えるわ！", 1, 4792, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106401, "色んな人と話したら\n記憶探しの手がかりが\n見つかるかも…？", 1, 4793, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106401, "アイスクリームでも\n作ろうかしら？\nバニラにミントに…", 1, 4794, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106401, "ネビアに話しかけると\n逃げられるのよね…\nもう、襲わないのに…", 1, 4795, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106401, "「コッコロさん」、\n「コッコロさん」…\n…間違えないように…", 1, 4796, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106401, "最近少し太ったかも？\nきっとおいしいもの、\n食べ過ぎね…", 1, 4797, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106401, "ここにいると、\nとても落ち着くわ…\n自分の家、だものね", 1, 4798, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106401, "みんなと一緒の時間、\nずっと記憶に残したい\n…そう思っているのよ", 1, 4799, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106401, "ドラゴン族って、\nそんなに珍しいの？\nあまり実感ないけど…", 1, 4800, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106401, "料理、練習してるのよ\n…味見してもらえる？\n期待はしないでね", 1, 4801, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106401, "頭を叩いたら、\n記憶が戻ったり\nしないかしら…？\nやらなくていいわよ？", 1, 4802, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106401, "赤ちゃんみたいだった\n時のことは、忘れて…\nお願いだから…", 1, 4803, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106401, "ひゃんっ！？\nし、しっぽを掴むのは\nダメよ？", 1, 4804, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106401, "記憶が戻っても…\n私は、あなたたちと\n一緒にいたいわ", 1, 4805, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 106401, "おはよう。\n寝ぐせついてるわよ？\n…ふふっ、これでよし", 1, 4806, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 106401, "そろそろお昼かしら？\nおいしいごはんを、\nみんなで一緒に…ね", 1, 4807, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 106401, "もうこんな時間ね。\nそろそろ休みましょ？\n…ふわぁ…", 1, 4808, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106401, "ふかふか、ぎゅう～…\n…あっ、これはその…\nストレッチしててっ…", 1, 4810, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106401, "あまり、興味ないわ。\nけど…けど…\n…か、かわいい…", 1, 4811, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106401, "いい香りね。\nとっても落ち着くわ…\n…っくしゅっ", 1, 4812, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106401, "冷やす方が得意だけど\nあったかいのも、\nキライじゃないのよ", 1, 4813, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 106401, "別に、食いしん坊\nじゃないのよ？\nでも、もう一口だけ…", 1, 4815, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 106401, "ん…なるほどね。\n勉強になるわ…", 1, 4816, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 115701, "水着の探偵か…\nちょっと恥ずかしい\n気がしてきたよ…", 1, 4818, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115701, "依頼があれば\n海でも山でも\n赴くさ！", 1, 4819, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115701, "今のうちに\n助手くんの秘密を…\nわ…私はなにをっ！？", 1, 4820, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115701, "休暇も時には必要さ\n海でゆっくりと…\nむっ？　事件の気配！", 1, 4821, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 115701, "探偵の秘密道具…\n水着だと隠す\n場所がない…！", 1, 4822, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 115701, "そろそろ助手くんが\n現れる時間かな？\nふふっ、楽しみだね", 1, 4823, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 115701, "真実は私と助手くん\n二人だからこそ\n解き明かせるのさ", 1, 4824, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 115701, "ひゃああっ！？\nわ、私の不意を突く\nとは…やるね助手くん", 1, 4825, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115701, "さて、なにか気づいた\nことはあるかな？\n言ってごらん♪", 1, 4826, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115701, "この感触…分かった！\n助手くんだね！\nふふっ、やっぱり♪", 1, 4827, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115701, "水着…どうかな？\n探偵らしさは\n残してるつもりだけど", 1, 4828, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 115701, "ん？　依頼かな？\n助手くんの頼みなら\nなんでもお安い御用さ", 1, 4829, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 115701, "助手くん、私と海に\n行かないかな\n…事件じゃないよ？", 1, 4830, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 115701, "珈琲でも淹れて\n目を覚ますとしよう\n一緒に飲むかい？", 1, 4831, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 115701, "昼食は済ませた？\nなにを食べたか\n当ててあげよう！", 1, 4832, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 115701, "夜は謎が深まる時間…\n闇に覆い隠された真実\n必ず暴いてみせる！", 1, 4833, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115701, "さて、推理の時間だ\n…サボってないよ？\n考えてるんだからね？", 1, 4835, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115701, "これはもしや、中に\n秘密のメッセージが？\n…かわいいだけだね", 1, 4836, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115701, "ふむ、なんだか\n頭がスッキリするね\n…閃いた！", 1, 4837, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 115701, "ポカポカするね…\n助手くん\n君もおいでよ", 1, 4838, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 115701, "はむ、はむ…んぐっ\nた、食べてるところを\n見つめられると～…", 1, 4840, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 115701, "秘密の依頼とか\nこういう場所に\n隠したりするよね…♪", 1, 4841, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 115701, "これはいい乗り心地だ\n事件現場にこれで登場\n…は、やめておこう", 1, 4843, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 115701, "不思議な感触だね…\nでも、いい発想が\n生まれてきそうだ…♪", 1, 4844, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 180601, "これはあたしが\n前へ進むために\nあいつがくれた\n大切な力…！", 1, 4845, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180601, "チェンジ・\nプリンセスフォーム！\n練習練習、っと", 1, 4846, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180601, "もうあいつに助けて\nもらいっぱなしの\nあたしは…\n卒業しなきゃね", 1, 4847, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180601, "気ままに、お気楽に\nあいつと冒険する…\nそれって幸せよね", 1, 4848, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 180601, "プリンセスフォーム…\nお姫様みたいね\nあたしがお姫様か…\nそっかぁ…ふふ♪", 1, 4849, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 180601, "あいつと繋がって\n生まれたこの絆は\nあたしの宝物だわ", 1, 4850, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 180601, "あたしの命の使い方…\n全て受け止めてくれた\nあいつにだったら…", 1, 4851, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 180601, "ふふ～ん♪\nプリンセスフォームの\n天才魔法使い\nキャル様に用かしら？", 1, 4852, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180601, "これからは\nあたしの力で\nなんでもパパっと\n解決してあげるわね！", 1, 4853, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180601, "魔力がみなぎってる…\nねえ、もしかして\n今のあたしって本気で\nすごいんじゃない？", 1, 4854, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180601, "変身の反動で\nお腹が空いても\n魔物料理だけは\nいやだからね！？", 1, 4855, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 180601, "ねぇ、あんたになら\nあたしの心…\n全部預けていいかも", 1, 4856, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 180601, "あいつと一緒に\nいられたら、他には\n何も…って、あああ\nあんたいつの間に！？", 1, 4857, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 180601, "早起きも悪くないわね\n読みかけの魔導書\n今のうちに\n読んじゃおうかしら", 1, 4858, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 180601, "ねぇ～お腹すいたー\nあんたなんか\n作りなさいよ\n魔物料理以外でね！", 1, 4859, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 180601, "ふぁぁ…眠すぎて\n今なら変身しても\nお腹が減らない\n自信があるわ…", 1, 4860, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180601, "あー♪　最っ高！\n休んで魔力を高めて…\nあいつの役に\n立ってあげなきゃね", 1, 4862, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180601, "なにこれかわいい…！\nコレクションにしたく\nなっちゃうじゃない", 1, 4863, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180601, "この香り…すごく\nリラックスできるわ…\nあ、いい感じの魔法\n思いついちゃったかも", 1, 4864, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 180601, "あったかい…\n落ち着くわ…\nあいつの手の\nぬくもりみたいね", 1, 4865, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 180601, "プリンセスフォーム…\nこ、こんなに\nお腹が減るのね\n甘く見てたわ…！", 1, 4867, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 180601, "ま、魔物料理の本！？\n誰よこんな本\n買ったの！　って…\n一人しかいないか", 1, 4868, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 115801, "憧れの舞踏会…\n私、きちんと\n踊れるかしら", 1, 4870, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115801, "アン・ドゥ・トロワ\nアン・ドゥ・トロワ\n練習はばっちりね♪", 1, 4871, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115801, "こんなにすてきな\nドレスを着られて\n本当に夢みたいだわ♪", 1, 4872, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115801, "魔法はいつか解ける…\nでも、その時間は心に\n残り続けるわ♪", 1, 4873, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 115801, "ガラスの靴は夢へ\n踏み出す魔法の道具…\n私、がんばっちゃう！", 1, 4874, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 115801, "舞踏会であなたと\n一緒に踊れたら\nいいのにな", 1, 4875, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 115801, "舞踏会に行く前に\n運命の王子様には\n出会ってるのよね", 1, 4876, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 115801, "どうかしら？\n私、きちんとドレス\n着られてる？", 1, 4877, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115801, "あなたも一緒に\n踊ってみる？\nしゃなりしゃなり♪", 1, 4878, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115801, "あなたにエスコート\nされてきれいなお城を\n歩いてみたいわ♪", 1, 4879, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115801, "どう？　レッスンの\n成果はきちんと\n出てるかしら？", 1, 4880, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 115801, "あなたが隣にいれば\n私も立派なお姫様に\n見えるかしら♪", 1, 4881, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 115801, "私にとって\n一番の魔法はあなたが\nくれる勇気よ♪", 1, 4882, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 115801, "さぁ！　今日も一日\n立派な淑女を目指して\nいきましょう♪", 1, 4883, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 115801, "お昼ごはん代わりに\nメタモルアップルを\n食べておきましょう", 1, 4884, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 115801, "そろそろ魔法が解ける\n時間…でも淑女の\n心得は忘れないわ♪", 1, 4885, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115801, "淑女にも休息は\n必要よね…少しだけ\n休ませてもらおっと", 1, 4887, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115801, "かわいいものに\n囲まれた部屋も\n女の子の夢よね…", 1, 4888, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115801, "すてきな香り…\nお花かしら？\n森で探してみようかな", 1, 4889, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 115801, "暖かい…暖炉のそばで\nおとぎ話を聞くのも\nいいわよね♪", 1, 4890, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 115801, "レッスンのために\n栄養補給して\nおかないと！", 1, 4892, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 115801, "教養を身に着ける\nことも大切よね！\nなになに…", 1, 4893, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 115901, "や、やっぱスカートは\n落ち着かねえな…\nなんか、\nスースーするしよ…", 1, 4895, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115901, "せっかくこんな格好\nしてるし、いつもとは\n違う剣術の訓練を\nしてみるかな", 1, 4896, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115901, "さて、他の連中が\n帰ってくる前に、\nなんか料理でも\n作っといてやるか", 1, 4897, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115901, "こ、こんな服着てる\nわけだし、紅茶とか\n楽しんじゃっても\n変じゃないよな！", 1, 4898, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 115901, "暇だし運動でも…\nあ、あぶねえ！\nスカートなの忘れてた\n…誰も見てねえよな？", 1, 4899, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 115901, "紅茶が切れてるな…\nよし、あいつと一緒に\n買い物にでも行くか♪", 1, 4900, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 115901, "…あいつがいねえと、\nつまんねえな…ったく\n飯用意してやるから、\n早く帰ってこいよな…", 1, 4901, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 115901, "あ、あんまり\nジロジロ見んなよ\n…結構、\n恥ずかしいんだからな", 1, 4902, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115901, "ダンスの相手は\n無理だが、\n剣の訓練なら\nいつでも付き合うぜ！", 1, 4903, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115901, "この服に\n触りたいのか？\nすごい作りだし、\n気持ちは分かるぜ", 1, 4904, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115901, "お、いいところに！\nクッキーを焼く\nところだったんだが、\nおまえも食うか？", 1, 4905, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 115901, "おまえにもこういう服\n似合うんじゃねえか？\n今度あたしが\n選んでやるよ♪", 1, 4906, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 115901, "…おまえの前でなら、\nこういう格好を\nすんのも…\nな、なんでもねえよ！", 1, 4907, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 115901, "朝の訓練を始めるか\n時間あるなら、\nおまえも付き合えよな", 1, 4908, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 115901, "もうこんな時間か…\n昼飯作るけど、\nなんか\n食いたいものあるか？", 1, 4909, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 115901, "眠れないなら、\nホットミルク飲むか？\n体あったまって、\n眠くなると思うぜ", 1, 4910, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 115901, "ちょっと休憩して…\nおっと、服にしわが\nできないように\nしねえとな", 1, 4912, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 115901, "だ、誰も\n見てねえよな？\n…よ～しよしよし、\nかわいいなおまえー♪", 1, 4913, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 115901, "すげぇいい香りだな\nうちの居酒屋にも…\nいや、合わねえか", 1, 4914, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 115901, "あったけぇ…\nちょっとだけなら、\n寝ちまっても\nいいよな…？", 1, 4915, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 115901, "これうめえな！\nたぶん材料は…よし！\nあたしも作ってみるか", 1, 4917, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 115901, "この本は…\n誰でも分かる作法…か\n最低限このぐらいは\n勉強しとくか…", 1, 4918, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102001, "ミミがおとなに\nなっても\nおにいちゃんとは\nずーっといっしょ♪", 1, 4920, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102001, "ミミとくべつなだっこ\nしてほしいな…うん！\nおひめさまだっこ～♪", 1, 4921, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100401, "次はにいちゃんに\nどんなイタズラしよう\n早く会いたいな～", 1, 4922, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100401, "ミソギの特別な\nイタズラ見てくれる？\nいいよねっ♪", 1, 4923, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103601, "大人になったら、\nあの人と何しようかな\n…楽しみだなぁ", 1, 4924, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103601, "頭を撫でないで\nください！　もう、\n少しだけ、ですからね", 1, 4925, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116001, "くるくる〜♪\nうちのドレス\nかわええなぁ", 1, 4926, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116001, "日差しは苦手やけど\nたまにはひなたの下で\nぽかぽかしたいわぁ", 1, 4927, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116001, "外はお花が咲いてる\n久しぶりにお花を\n活けたいなぁ", 1, 4928, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116001, "お花をたくさん集めて\nギルドハウスに飾れば\n喜んでくれはるかな", 1, 4929, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116001, "あたたかい日は\n紅茶を飲みながら\nゆっくりしたいなぁ", 1, 4930, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116001, "お稽古事を始めるなら\nうちに言ってな\nどんなことも教えたる", 1, 4931, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116001, "うちにいろんな夢を\n見せてくれはって\nほんま、おおきにな♪", 1, 4932, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116001, "王子はん\n一緒におとぎの国へ\nお散歩にいきまひょ♪", 1, 4933, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116001, "ふんふんふ〜ん♪\n今な、王子はんに\n花冠を作ってるんよ", 1, 4934, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116001, "王子はんは\nお菓子好きどす？\nうち、作りますわ", 1, 4935, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116001, "うーん。ドレスの柄は\nお花とお星さま\nどっちにしまひょか", 1, 4936, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116001, "王子はんとの\n思い出が増えるたび\nうちの宝物も増えます", 1, 4937, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116001, "王子はんと一緒なら\nどんな場所でも\n夢の国になりますわ", 1, 4938, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 116001, "夢の中におりたいけど\n王子はんに会いたくて\n起きてもうたわ♪", 1, 4939, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 116001, "王子はん一緒に\nアフタヌーンティー\nいかがやろか？", 1, 4940, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 116001, "夜の舞踏会…\n夢が広がるわぁ\nもちろんお相手は…♪", 1, 4941, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116001, "ふわふわや〜\nなんや心地よぉて\n眠ってしまいそうや…", 1, 4943, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116001, "かいらしいなぁ\n新しいお洋服でも\n作ってあげまひょか♪", 1, 4944, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116001, "お花やろうか…\nリラックスできて\nええ香りやねぇ", 1, 4945, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116001, "こういう場所で\n絵本を読むと\n気分が出てええなぁ♪", 1, 4946, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 116001, "舞踏会を楽しむなら\nたくさん食べて\nたくさん踊りまひょ", 1, 4948, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 116001, "うち、この本好きや\n王子さまと結ばれる\nお姫さまのお話なんよ", 1, 4949, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116301, "はぁ、宿題めんどい…\nこれは楽しくない…\nこんな青春いらない…", 1, 4951, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116301, "奨学金の道は遠いなぁ\nメルクリちゃんたちが\nお金くれればいいのに", 1, 4952, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116301, "アオイちゃん元気かな\nチエルに会いたくて\n泣いてるんだろうな…", 1, 4953, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116301, "暇だし鏡でも見るか…\nうわ、顔良っ！\nえ誰この超絶美少女！", 1, 4954, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116301, "ワンツーワンツー♪\nうーん…カルミナの\n振りコピ難しいにゃー", 1, 4955, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116301, "先輩も運動してます？\nチエルが一緒に走って\nあげてもいいですよ？", 1, 4956, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116301, "来年のテレフェスも\n今から楽しみだなあ…\nまた一緒にお化け屋敷\n入ってくださいね", 1, 4957, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116301, "ちぇる～ん☆\nフェス仕様でも\nかわいいちえるん！", 1, 4958, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116301, "いま話しかけないで！\n来年の聖学祭ライブの\nイメトレ中なんです！", 1, 4959, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116301, "クロエ先輩のウェア姿\nめっちゃカッコよ！\n思わずおへそ触ったら\nぶたれましたけど", 1, 4960, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116301, "ユニ先輩の体操着姿…\nあれはあれで実に良き\n一生見てられますよね", 1, 4961, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116301, "先輩って体操着持って\nないんですか？　ハ？\nチエルのを貸せと…？\nえっ…変態かな？", 1, 4962, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116301, "テレフェスでは先輩も\n地味に頑張ってました\nね。まあ格好よかった\nんじゃないですか…", 1, 4963, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 116301, "おはちぇる～ん☆\nさあ今日も楽しいを\n探しに行きますよー！", 1, 4964, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 116301, "先輩ビッグニュース！\n今からお昼ごはん\nおごらせてあげます！", 1, 4965, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 116301, "こんばんちぇるるん☆\nさあ朝まで楽しいを\n探しに行きますよー！", 1, 4966, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116301, "はぁ…ふかふか～…\n運動の後は…眠たく…\nなり…ま…", 1, 4968, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116301, "ユニ先輩に似合いそ！\nいやクロエ先輩かな？\nチエルは当然すぎて…", 1, 4969, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116301, "やん、いい香り～！\nまるでチエルのために\n存在するみたい！", 1, 4970, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116301, "この火を使って\n聖火リレーしましょう\n先輩、一本抜いて！", 1, 4971, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 116301, "あむあむ…ちゃんと\n食べないとユニ先輩\nみたいになっちゃう！", 1, 4973, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 116301, "ふむふむ…ちゃんと\n読まないとクロエ先輩\nみたいになっちゃう！", 1, 4974, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102901, "今なら書ける気がする\n私の本当の\n想いを込めた\nラブソングを！", 1, 4976, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102901, "輝きを増した私を\nいっぱい見てね！\n…ちょ、ちょっと\n見つめすぎだよ！", 1, 4977, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116201, "テレフェスかぁー…\nあんまいい記憶ないな\nよし、忘れとこ", 1, 4978, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116201, "暇だし鏡でも見るか…\nふふ～ん…いいじゃん\nあ？　何見てんだコラ", 1, 4979, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116201, "ワンツー、ワンツー♪\nああクソ、難しいな…\nあ？　何見てんだコラ", 1, 4980, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116201, "アオイ元気してるかな\nまた絶叫したり白目\n剥いたりしてんのかな", 1, 4981, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116201, "ミフユねえさんが\nバイトリーダーしてる\n工場…うちも行くか…", 1, 4982, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116201, "うーん…今日の晩御飯\n何にすっかなァ…\nあんたも食べてく？", 1, 4983, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116201, "来年のテレフェスは\nどーなンだかねえ？\nま、一緒にまた弟の\n相手してやってよ", 1, 4984, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116201, "体操着なんてフツーに\n着てもつまんないし？\nその辺センス出るよね", 1, 4985, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116201, "奨学金の道、遠くね？\nアキノ先輩と仲良く\nなった方が早いかも…", 1, 4986, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116201, "あんた体操着は持って\nないの。ハ？　うちの\n貸せって？　いいけど\n…いや、よくねえよ", 1, 4987, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116201, "にゃんっ！\nや、だからさ…急に\n話しかけんなっての", 1, 4988, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116201, "え、なに？　この服？\nハ？　似合ってる？\n…え、聞こえなかった\nちょ、もっかい言って", 1, 4989, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116201, "あんたもテレフェスで\n一応は頑張ってたよね\nま格好よかったんじゃ\nないの。知んないけど", 1, 4990, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 116201, "ふぁ～あ…おはーす\n昨日も寝たの遅くて\nまだ目がショボだよ…", 1, 4991, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 116201, "なに。あんた昼御飯\nまだなの。じゃ、うち\n適当になんか作ろうか", 1, 4992, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 116201, "夜っていいよね。色々\n見えすぎない感じとか\n…この話したっけ？", 1, 4993, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116201, "あ、ヤバ…運動の後に\nこのフカついたのは…\n秒でスヤれる…すやぁ", 1, 4995, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116201, "へーこんなんチエルに\n似合いそうじゃん。\n…いやユニ先輩かな？", 1, 4996, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116201, "お、なんか良さげな\n香りじゃん。これは\n何の…いや分かんね", 1, 4997, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116201, "熱ッ！　この野郎なに\n爆ぜてンだコラァ！\n濡らすぞ木、てめえ！", 1, 4998, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 116201, "何コレよく分かんない\nけど、今日モヤシしか\n食ってないから助かる", 1, 5000, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 116201, "今日、体操着だしね。\n読書には逆アドかな。\nうん帰ろ。いやあ残念", 1, 5001, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116501, "ビッグボス・イノリ様\nふっふっふ…\nしびれる響きです！", 1, 5003, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116501, "いつかお兄さんを\nあたしの側近に\nしてやるですよ！", 1, 5004, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116501, "ビッグボスになったら\nみんなからたくさん\nお菓子をもらうです！", 1, 5005, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116501, "ふふん！\nこの服はあたしにしか\n着こなせませんよ！", 1, 5006, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116501, "ばーんと立って\nどーんと腕を組む！\nこれがボスのポーズ！", 1, 5007, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116501, "イノリ様の側近に\n選ばれるなんて\n名誉なことですよ！", 1, 5008, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116501, "未来の側近として\nあたしをしっかり\n甘やかすですよ！", 1, 5009, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116501, "あたしがお兄さんを\n立派なギャングに\nしてあげますからね！", 1, 5010, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116501, "側近たるもの\nボスを甘やかなさいと\nだめですよ！", 1, 5011, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116501, "なに見てやがるです…\nくぅ、分かりました！\nお菓子は半分こです！", 1, 5012, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116501, "お兄さんも\nあたしがボスなら\nうれしいですよね！", 1, 5013, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116501, "お兄さんが側近なら\nあたしも安心して\n夜眠れるですよ！", 1, 5014, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116501, "ビッグボスになったら\nお兄さんにいい思い\nさせてあげますから！", 1, 5015, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 116501, "ボスたるもの\n早起きは鉄則ですよ！\nふぁあ…", 1, 5016, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 116501, "お腹が空きました…\nご飯食べにいきますよ", 1, 5017, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 116501, "ボスたるものぉ〜…\n夜更かしぐらい当然…\nぐー…", 1, 5018, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116501, "は〜これは人をだめに\nしやがるやつですね\nあたし専用です", 1, 5020, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116501, "これはけしからん\nかわいらしさですね\nあたしにぴったりです", 1, 5021, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116501, "高級な香りがするです\nボスになったら\n部屋に置いてやるです", 1, 5022, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116501, "あったかいですねぇ\nちょっとこのまま\n一休憩です…", 1, 5023, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 116501, "はむはむ♪\nボスたるもの食べねば\n戦はできませんよ！", 1, 5025, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 116501, "ボスなら経済書ぐらい\n…う。難しくて\n読めねーです…", 1, 5026, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102501, "あなたはドジな私を\n受け入れてくれる…\nだから、頑張れます！", 1, 5028, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102501, "何でも仰ってください\nちょっぴり大胆な\nことでも…な～んて♪", 1, 5029, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116601, "あーあー、よし\nのどの調子もいいし\nはりきってガイド\nしてくるか！", 1, 5030, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116601, "さあ、そろそろ行くか\nガイドが\n遅刻したんじゃ\n話になんないからな", 1, 5031, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116601, "しれっとドラゴン族が\n旅行に来たり\nしてくれないかな", 1, 5032, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116601, "たぎるガイド先…\n火山、魔物の巣…\nダメだ、そんなとこ\nオレしか行けないや", 1, 5033, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116601, "この前行った絶景\nスポットよかったな\nキレイな景色を見る\nのもいいもんだぜ", 1, 5034, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116601, "お、今日はカレと\n一緒か。なんだか\nやる気出てきたぜ！", 1, 5035, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116601, "カレにガイドして\nもらったら\nどこに行っても\n楽しいだろうな", 1, 5036, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116601, "よっしゃあ！\nどっちが上手く\nガイドできるか\n勝負しようぜ！", 1, 5037, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116601, "飯でも食いがてら\nおススメの店の\n下調べでもしようぜ！", 1, 5038, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116601, "なかなかいいだろ？\nこの格好。キミも\n作ってもらったら\nどうだ？", 1, 5039, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116601, "普通は行けない\n場所とか行きたいよな\nキミならどんな\nとこがいい？", 1, 5040, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116601, "ガイドの仕事は\nキミの方が得意そうだ\n参考にさせてもらうぜ\nよろしくな！", 1, 5041, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116601, "キミの探してきた店\n結構よさそうじゃん\nそうだ、今度二人で\n下見に行こうぜ！", 1, 5042, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 116601, "いい朝だな。絶好の\n仕事日和だぜ！\n今日もよろしくな！", 1, 5043, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 116601, "よし、昼飯にしよう\nこの後も激務だし\n飯抜きじゃあ\nぶっ倒れちまうからな", 1, 5044, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 116601, "お疲れさん！　さーて\n次はオレと遊んで\nくれよ。まだまだ\nいけるだろ？", 1, 5045, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116601, "ガイドは一日\n歩きっぱなしだからな\nゆっくり休ませて\nもらうぜ", 1, 5047, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116601, "これ、どこで\n売ってるんだろ？\n土産の情報も\n仕入れとかなきゃな", 1, 5048, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116601, "アロマとかよく\n分かんねえけど\n土産にはいいかもな\n覚えておくぜ", 1, 5049, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116601, "体が温まると心も\n熱くなるんだよな\nいい仕事が\nできそうだぜ", 1, 5050, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 116601, "食べ放題か\nこれはお客さんも\n喜んでくれそうだな！", 1, 5052, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 116601, "へえ、こんなスポット\nあったんだ。やっぱ\nたまには勉強も\nしとくもんだな", 1, 5053, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116701, "バイト先になじめる\nように笑顔の練習を！\nに、にへぇ〜…", 1, 5055, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116701, "働いて働いて働いて\n…いつかその先に\nぼっちの出口が\n見えるのかも…！", 1, 5056, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116701, "バイトのイメトレを\n始めます。ポーズを\n決めて…よし！\n角度を変えて…よし！", 1, 5057, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116701, "バイト仲間に入る\nためには厳しい修業が\n必須と聞きました…！", 1, 5058, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116701, "どんどん技術を磨いて\nバイト仲間として\n認めてもらうんです！\n目指せ職人技…！", 1, 5059, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116701, "あの人が応援して\nくれる…だから必ず\nバイト仲間の輪に\n入ってみせます…！", 1, 5060, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116701, "あの人がいてくれると\n勇気が湧いてきます…\nぼっち卒業を夢見ても\nいいんだ、って…♪", 1, 5061, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116701, "緊張するけど\n思い切って声をかけて\nみます…バイト仲間に\n入るために…！", 1, 5062, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116701, "ぼっちの私が\nおもちゃ作りで\n人と共同作業…！\nか、感動です…！", 1, 5063, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116701, "好きなおもちゃって\nありますか？　私は\nもちろんマイフレンド\nくんです！", 1, 5064, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116701, "私が作ったおもちゃも\n誰かの友達に\nなってたら\nいいなぁ…♪", 1, 5065, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116701, "今日も明日もバイト\nですね！　バイト\n仲間ができる日が\n楽しみです…♪", 1, 5066, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116701, "脱ぼっちを目指して\nこれからも一緒に\n頑張りましょう！\n目指せ、バイト仲間！", 1, 5067, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 116701, "おはようございます\n今日も気合いを入れて\nおもちゃ作りします！", 1, 5068, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 116701, "ランチの時間です…♪\nもちろん工場の隅で\nぼっち飯です…！", 1, 5069, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 116701, "ふわぁ…今日も\nお疲れさまでした\n明日も働きます～！", 1, 5070, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116701, "バイトで頑張った日は\nふわふわの誘惑に\n勝てません…", 1, 5072, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116701, "こういうおもちゃの\nアイデアも\n素敵かも…♪", 1, 5073, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116701, "いい香りがする\nおもちゃはどうかな…\n森の薬草を使って…", 1, 5074, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116701, "ぽかぽか…\n幸せなひとときですね", 1, 5075, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 116701, "もぐもぐ…たくさん\n食べて厳しいバイトを\n乗り切ります…！", 1, 5077, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 116701, "ふむふむ…おもちゃの\n設計図を見るのも\n勉強になりますね！", 1, 5078, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116801, "う～ん\n次はどんなたい焼きを\n作ろうかにゃぁ…", 1, 5080, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116801, "あたしが売る\nたい焼きには\n夢が詰まってるにゃ！", 1, 5081, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116801, "いつか絶対に\nたい焼き工場を\n建て直してみせるにゃ", 1, 5082, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116801, "たまにたい焼き以外の\nおやつも食べるにゃ\nたとえば煮干しとか！", 1, 5083, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116801, "悪い商売人がいたら\nファントムキャッツに\n知らせるにゃ！", 1, 5084, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116801, "キミと一緒にいると\nたい焼きのアイデアが\n次々出てくるにゃ♪", 1, 5085, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116801, "出来ないことも\nキミと一緒にいれば\n出来る気がするにゃ♪", 1, 5086, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116801, "なんにゃ～？\nお姉さんにかまって\nほしいのかにゃ♪", 1, 5087, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116801, "キミ、いいところに！\nあたしとライバル店へ\n視察にいくにゃ！", 1, 5088, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116801, "くんくん…この匂い！\nたい焼き持ってるなら\nちょうだいにゃ♪", 1, 5089, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116801, "次はどんなたい焼きを\n売ろうかにゃ～\nキミの案も欲しいにゃ", 1, 5090, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116801, "ファントムキャッツに\n相棒が必要にゃ\nたとえばキミとか…", 1, 5091, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116801, "相棒！\n…ってキミのこと\n呼んでいいかにゃ？", 1, 5092, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 116801, "早起きは三文の徳にゃ\n今日もたい焼き\n売れますよーににゃ！", 1, 5093, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 116801, "お腹空いたにゃ…\n賄いのたい焼きを\nいただくにゃ！", 1, 5094, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 116801, "猫は夜が本番にゃ\nでもあたしは…\nふわぁ～眠いにゃ…", 1, 5095, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116801, "ふかふかにゃ～\nここで体を丸めて\n一眠りしたいにゃ", 1, 5097, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116801, "これは…！\n猫にも負けない\nかわいさだにゃ…！", 1, 5098, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116801, "いい香りにゃ～\nゴロゴロのどが\n鳴りそうだにゃ～", 1, 5099, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116801, "ふにゃ～…\nこのまま働かずに\nまるまってたいにゃ…", 1, 5100, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 116801, "商売は戦いにゃ\nたくさん食べて\nたくさん働くにゃ！", 1, 5102, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 116801, "ふむふむ。たい焼きの\nアレンジレシピ集…\nためになるにゃ…", 1, 5103, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102101, "お芝居の前に\nお兄ちゃんの顔を\n見ると、とっても\n落ち着きますぅ…♪", 1, 5105, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102101, "お兄ちゃんにもっと\n見てもらいたい…\nだから私…\n強くなりたいです…！", 1, 5106, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116901, "さらなる効率化で\nみんなといい仕事を\nしたいわね！", 1, 5107, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116901, "他のバイトとの\nスケジュールを\n調整しないと…", 1, 5108, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116901, "時給がアップしたわ！\n働きが認められると\nやる気が出るわね", 1, 5109, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116901, "効率的に動ければ\n業務を追加できる…\n燃えてきたわ！", 1, 5110, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116901, "帳簿付けのコツ…\nユカリさんに教わって\nみようかしら", 1, 5111, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116901, "バイトリーダーとして\nみんなをビシバシ\n鍛えていくわ！", 1, 5112, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116901, "戦いでもバイトでも\nキミなら安心して\n背中を預けられるわね", 1, 5113, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116901, "どうしたの？\n仕事のことなら\nなんでも相談してね！", 1, 5114, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116901, "さすがは私の弟子ね\nこれなら安心して\n仕事を任せられるわ", 1, 5115, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116901, "みんなのお手本になる\n働きぶりを見せないと\nいけないわね！", 1, 5116, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116901, "キミのこと…もっと\n頼りにしちゃっても\nいいのかしら…？", 1, 5117, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116901, "ふふ、キミも一人前の\nバイト戦士として\n立派に成長したわね！", 1, 5118, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116901, "新しいおもちゃの\n企画書を作ってみたの\nちょっと見てくれる？", 1, 5119, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 116901, "朝は一番効率がいい\n時間帯よ！\nバリバリ働くわよ！", 1, 5120, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 116901, "さ、お昼に行きましょ\n今日の日替わり定食は\n何かしら…楽しみね♪", 1, 5121, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 116901, "もうこんな時間…\nちゃんと寝て明日に\n備えなくちゃね", 1, 5122, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116901, "適度な労働と休息の\nサイクルが最大効率を\n生み出すのよ…ふぅ…", 1, 5124, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116901, "はぁ～なんて素敵な\n抱き心地なのかしら…\n最高効率の癒やしね♪", 1, 5125, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116901, "落ち着く香りね…\nなんだか集中力が\nアップした気がするわ", 1, 5126, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116901, "ふぅ…癒されるわね…\n仕事のオンオフの\n切り替えは重要よね", 1, 5127, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 116901, "働くためには\nエネルギーをちゃんと\n摂っておかないと", 1, 5129, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 116901, "効率を上げるため\n生産管理の勉強も\nしておきたいわね", 1, 5130, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 180301, "変身している間に\n色々と試しておこう\nまずは…", 1, 5132, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180301, "飛べるというのは\n便利だな。料理が\n暖かいうちに\n持ち帰れる", 1, 5133, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180301, "プリンセスか…\n柄ではないけれど\n嫌な気はしないな", 1, 5134, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180301, "この力で\n何を成すべきなのか…\n剣に聞いてみよう", 1, 5135, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 180301, "変身したとて私は私\n一時的に強くなろうと\n元が弱くてはだめだ", 1, 5136, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 180301, "変身していない時でも\n彼がそばにいると\n力が湧いてくる…", 1, 5137, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 180301, "私の思い…\nいつか伝えてみせるよ\n…きっといつか！", 1, 5138, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 180301, "何者かと戦うの？\nそれとも修行？\nどちらだとしても\n付き合うよ", 1, 5139, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180301, "変身し続けたら\nどれだけ空腹に…\n私は試さないからね？", 1, 5140, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180301, "本物のプリンセスに\nなるのは御免だが\nナイトがいて\nくれるのは頼もしいよ", 1, 5141, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180301, "いつかこの力を\n誰かを救うために…\n戦い以外に使える時が\nくることを願うよ", 1, 5142, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 180301, "キミに感じてほしいな\n私に流れてくる\nキミからの力を…\nその…私の、思いも…", 1, 5143, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 180301, "思いを告げる…\nそれがこんなにも\n難しいなんて…\nでも、いつか必ず…！", 1, 5144, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 180301, "おはよう！\nいい変身日和だ！\n…ふふっ、冗談だよ", 1, 5145, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 180301, "いつもより多く\n昼食を用意しよう\n変身が解けたら大変だ", 1, 5146, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 180301, "一日お疲れ様\n私もそろそろ変身を\n解いて休むとするよ", 1, 5147, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 180301, "変身してしまうと\n動かずにいても\n空腹になるのかな…？", 1, 5149, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 180301, "私もキミたちくらい\n可愛げがある姿に\nなっているかな？", 1, 5150, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 180301, "香りに対する感覚も\nいつもより鋭く\nなっているみたいだ…", 1, 5151, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 180301, "ありがたいな\n少し足元が\n冷えていたところだ", 1, 5152, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 180301, "これは…！\n持ち運べるように\n改良できないかな？", 1, 5154, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 180301, "祖父に貰った\n技法書を読んで\n初心に帰ろう", 1, 5155, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117001, "最強の惚れ薬を作って\nあのおかたの心を\n私の思いのままに…", 1, 5157, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117001, "夏といえば海にスイカ\n花火に水着…\nそして惚れ薬ですわ…", 1, 5158, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117001, "あのおかたの心を\n手に入れるための\n準備を進めましょう\nこの夏こそ確実に…", 1, 5159, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117001, "あのおかたに近づく\n虫どもを、どうやって\n片付けようかしら…\nまったく忌々しいこと", 1, 5160, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117001, "…クスクス\n薬の調合を考えるのは\n至福の時間ですわ…", 1, 5161, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117001, "あのおかたの全てが\n早く私のものに\nなりますように…", 1, 5162, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117001, "かき氷も花火も海も…\nあなた様がいてこそ…\nそう、あなた様は\n私の全てなのですわ", 1, 5163, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117001, "二人だけの夏を\n楽しみましょう…？\n…クスクス", 1, 5164, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117001, "あら、あなた様\n私に秘密でどこに\n行こうとして\nいますの…？", 1, 5165, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117001, "海に行きませんか？\nあなた様と一緒に\n過ごすには\n最高の場所ですから…", 1, 5166, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117001, "ちょうどいいところに\nいらっしゃいました\nできたての薬を…\nさぁどうぞ…クスクス", 1, 5167, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117001, "夏の海のきらめきも\nあなた様の存在が\nなければ色あせて\nしまいますわ…", 1, 5168, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117001, "私とあなた様は\n結ばれる運命に\nあるのです…\n逃がしませんわ…", 1, 5169, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 117001, "起きてすぐに会いに\n来てくださるなんて\nようやく私の伴侶たる\n自覚が…クスクス", 1, 5170, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 117001, "お食事はまだですか？\n特製の薬草ランチを\n召し上がれ…クスクス", 1, 5171, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 117001, "私の夢を見られる薬を\n差し上げますわ…\nおやすみなさいませ\nあなた様…クスクス", 1, 5172, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117001, "いい肌触りですわ…\n考え事にもぴったり\n眠くなるのが\n玉に瑕ですが…", 1, 5174, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117001, "こういったものに\n揮発性の薬を仕込む\nのも一つの手ですわね", 1, 5175, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117001, "この香り…開発中の\n薬に使えそうな気が\nしますわ…クスクス", 1, 5176, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117001, "あのおかたの\nぬくもりを\n思い出しますわ…", 1, 5177, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 117001, "美味しい…この味なら\n薬を混ぜても違和感は\nなさそうですわ…", 1, 5179, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 117001, "この薬の調合法…\n今度試してみましょう\nもちろんあのおかたに\n飲んでいただいて…", 1, 5180, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 117001, "二人乗りができると\nいいですわね…", 1, 5182, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 117001, "ふかふかですわ…\nあのおかたの分も\n欲しくなります…", 1, 5183, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101701, "今日はキミと\nいつまでも歌って\n踊りたい気分さ～♪", 1, 5184, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101701, "あは～♪\nキミの隣は、故郷より\n落ち着くかも～…♪", 1, 5185, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117101, "この水着…\n一番に弟くんに\n見せたいな♪", 1, 5186, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117101, "弟くんをこっそり\n海に運んで…\nサプライズしちゃお♪", 1, 5187, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117101, "いつもと違う髪型に\nしてみたけど…\n気に入ってくれるかな", 1, 5188, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117101, "海は楽しみだけど\n日焼けしないように\n気を付けなきゃね", 1, 5189, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117101, "まだかな、弟くん\n水着のお姉ちゃんが\nお出迎えだよっ♪", 1, 5190, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117101, "海が見えるおうちで\n弟くんと一緒に\n暮らしたいなぁ～", 1, 5191, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117101, "弟くんの水着…\nどんな感じかなぁ\n…あ、想像してたら\nドキドキして…えへへ", 1, 5192, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117101, "こーら、水着の\nお姉ちゃんは\nおさわり禁止♪\n…なんてね？", 1, 5193, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117101, "なぁに？\nふふっ、何回でも\n呼んでいいんだよ♪", 1, 5194, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117101, "海で迷子に\nならないように\n手を繋ごうね♪", 1, 5195, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117101, "お姉ちゃんが\n海の楽しみ方を\n教えてあげるねっ！", 1, 5196, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117101, "わっ、弟くん\nどこ触ってるの？\nも～驚いちゃったぞ♪", 1, 5197, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117101, "お姉ちゃん、海でも\nずっとキミを見てるよ\n…安心してね", 1, 5198, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 117101, "まだ眠そうだね？\nほら、海が待ってるよ\n早く起きて起きて♪", 1, 5199, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 117101, "大丈夫、疲れてない？\n海で遊び疲れた時は\n素直に休憩しようね", 1, 5200, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 117101, "ほら、早く寝ないと\n明日もお姉ちゃんと\n遊べないぞ♪", 1, 5201, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117101, "だらけたお姉ちゃんは\n弟くんには\n見せられないな～", 1, 5203, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117101, "ふふっ、かわいいね♪\n弟くんが持つと\nもっとかわいいよっ♪", 1, 5204, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117101, "いい香りだね…\n一味違うお姉ちゃんに\nなれそう…♪", 1, 5205, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117101, "あったか～い♪\nほら、弟くんも\nおいでよ♪", 1, 5206, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 117101, "よく食べて、いつでも\n弟くんの呼び声に\n応える準備を…！", 1, 5208, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 117101, "弟くんの水着の写真\nこの辺りに隠して\nおこうかな", 1, 5209, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 117101, "この子の背に乗って\nお姉ちゃんと弟くんの\n楽園へ…な～んて♪", 1, 5211, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 117101, "海を感じる感触だね\n弟くんとの楽しい夏を\n思い出しちゃうな…♪", 1, 5212, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117201, "この夏、誰よりも\nキラキラしてみせる！\nだって私は\nアイドルだから！", 1, 5213, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117201, "日傘にアームカバー\n帽子にサングラス…\n紫外線対策は完璧！", 1, 5214, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117201, "とっておきの\nサマーソングを\nみんなに届けるよ！", 1, 5215, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117201, "なんにもしないのって\n苦手かも…\n彼、早く来ないかな？", 1, 5216, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117201, "ラムネって\n夏って感じするよね\n後で彼と飲もっと♪", 1, 5217, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117201, "夏は恋の季節！\n燃えるような恋…\nいつかしたいな", 1, 5218, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117201, "今年の夏の目標は\n…彼との思い出を\nいっぱい作ること！", 1, 5219, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117201, "私の水着写真集\n見てくれたんだ！\nどう、かわいかった？", 1, 5220, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117201, "スイカ割りしたいの？\nいいね、楽しそう！\nみんなも誘おうよ♪", 1, 5221, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117201, "ねぇ、見て見て！\n水着の映える\nポーズ考えたんだ！", 1, 5222, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117201, "ライブ中に\n花火を打ち上げたら\n絶対盛り上がるよね！", 1, 5223, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117201, "最近、忙しいけど…\nキミの顔見たら\n疲れなんて\n消えちゃった！", 1, 5224, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117201, "この夏もいーっぱい\nドキドキさせるから！\n覚悟しててよね♪", 1, 5225, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 117201, "外は朝から暑いね\nけど、負けないから！\nお仕事、頑張るぞー！", 1, 5226, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 117201, "自分へのご褒美に、\nアイス食べちゃお♪\nキミも一緒に食べよ！", 1, 5227, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 117201, "夏の夜って\nワクワクするね♪\nホタル見に行こうよ！", 1, 5228, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117201, "ふわぁ…眠い…\n仕事とかの疲れ、\nたまってるのかな…？", 1, 5230, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117201, "私のぬいぐるみが\nグッズになったら…\n彼にかわいがって\nもらえるかな？", 1, 5231, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117201, "この香り…\n夏限定のやつでしょ？\nいいセンスしてるね♪", 1, 5232, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117201, "暑～い！\nけど…汗かいて\n気持ちいいかも！", 1, 5233, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 117201, "レッスンの後のご飯は\n特別おいしい♪\n…もうひとつだけ\n食べちゃおっかな", 1, 5235, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 117201, "あ、この前撮影した\n水着グラビアだ\nうんうん、いい感じ！", 1, 5236, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 117201, "この子、もふもふだね\n触るのクセになりそう\nもふもふもふもふ…", 1, 5238, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 117201, "わ、かわいい♪\n今度のグラビア撮影で\nこれ使ってもいい？", 1, 5239, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117401, "水着っていいですよね\n海に浮かぶ一輪の花\nって感じがして！", 1, 5240, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117401, "自分の水着を作るのも\n誰かの水着を作るのも\n楽しいですね♪", 1, 5241, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117401, "水着の最終チェック！\n伸縮性や動きやすさは\n…いい感じですね！", 1, 5242, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117401, "振り付けや衣装作りに\n歌や踊りの練習…\n…やることがいっぱい", 1, 5243, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117401, "はぁ…レイ様の水着…\n私に作らせて\nもらえないかなぁ～…", 1, 5244, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117401, "騎士さんのことは\n気に入らないですけど\n別に嫌いという\nわけじゃなくて…", 1, 5245, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117401, "騎士さん暇そうですね\nに、荷物持ちとして\n誘ってみようかな…", 1, 5246, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117401, "騎士さんいいところに\nこの衣装\nどう思います？", 1, 5247, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117401, "何見てるんですか？\nもしかしてこの水着の\n良さが分かります！？", 1, 5248, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117401, "真夏でも涼しい…\nそれも水着の良さの\n一つですね", 1, 5249, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117401, "仕立て屋も\nアイドルも\n諦めません！", 1, 5250, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117401, "夏と言えば\nバカンスですけど…\n予定、あるんですか？", 1, 5251, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117401, "う～ん…騎士さんを\n海へ誘うには…って\nなんでもないですよ！", 1, 5252, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 117401, "ふー…夏はやっぱり\n朝が一番過ごしやすい\nですね～！", 1, 5253, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 117401, "暑い時こそ海です！\n体力づくりのためにも\n泳ぎにいきますよ！", 1, 5254, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 117401, "外で虫が鳴いてる…\n夏って感じがして\n風流ですねぇ…", 1, 5255, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117401, "少し休憩です…\nこのまま寝ないよう\n気をつけないと…", 1, 5257, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117401, "か、かわいい…！\nこの感動を次の\nステージ衣装にも…！", 1, 5258, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117401, "いい香りですね～\n波の音が聞こえて\nきそうです…", 1, 5259, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117401, "泳いだ後は\n少し寒いので\nちょうどいいですね", 1, 5260, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 117401, "夏バテしないように\n栄養はしっかり\n取らないと！", 1, 5262, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 117401, "次のトレンドは…\nふむふむ、やっぱり\nこう来ましたか…", 1, 5263, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 117401, "デザインはもちろん\n触り心地も\n最高ですね♪", 1, 5265, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 117401, "夏の撮影スポットに\n活用できそうですね\nさっそく衣装考えて…", 1, 5266, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104001, "えへへ…♪\n団長さんが初めての\n友達で本当に良かった", 1, 5267, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104001, "あっ、団長さん！\n今日のＢＢ団の活動\nなんですけど…二人で\n大縄跳びしませんか！", 1, 5268, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117301, "この衣装で\nライブするのは\n恥ずかしいな…", 1, 5269, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117301, "せっかくの夏だし\n肝試しに\n行ってみたいかも…", 1, 5270, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117301, "歌もダンスも\nより上を目指すために\n夏合宿をしよう…！", 1, 5271, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117301, "夏の暑さにも負けない\n熱いライブに\nしてみせる…！", 1, 5272, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117301, "新曲のアイデア\nまとめたいな…\n少し海辺を散歩しよう", 1, 5273, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117301, "かき氷、食べたいな\n…あの人は何味が\n好きなんだろう？", 1, 5274, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117301, "水着、似合ってるって\n言ってもらえるかな…", 1, 5275, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117301, "サインして、ですか？\nいいですけど…\n少し照れますね…", 1, 5276, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117301, "少し待ってください\n今、新曲の楽譜を\n書いているので…", 1, 5277, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117301, "なんでしょう？\nもしかして…\nボイトレに興味が？", 1, 5278, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117301, "今度のライブでは\n夏らしい爽やかな歌を\nファンにお届けします", 1, 5279, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117301, "…水鉄砲で遊ぼう？\nふふ。楽しそうですね\nお手柔らかに…♪", 1, 5280, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117301, "あなたの声を\n聴いていると、\n何だか落ち着きます…", 1, 5281, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 117301, "今日も暑くなりそう…\nお互いしっかりと\n水分補給しましょうね", 1, 5282, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 117301, "お腹空きましたよね\n海の家で、\nなにか買ってきます", 1, 5283, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 117301, "夜の海で月明かりと\n波の音を楽しもうかと\n…一緒に行きますか？", 1, 5284, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117301, "レッスンだけでなく\nリラックスすることも\n大事、ですよね", 1, 5286, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117301, "ふふ、かわいい\n精霊も負けないくらい\nかわいいですけどね", 1, 5287, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117301, "夏らしい爽やかで\nいい匂い…あなたに\nピッタリな香りですね", 1, 5288, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117301, "海に入った後は、\n体が冷えてしまいます\n一緒に温まりましょう", 1, 5289, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 117301, "アイドルとして、\n食べすぎには\n気をつけないと…", 1, 5291, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 117301, "夏は恋の季節…\nガンガン攻めるべし…\nなるほど…", 1, 5292, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 117301, "この子に乗りながら\nライブをする…\nというのはどうですか", 1, 5294, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 117301, "まるで人魚になった\n気分…なんて、ふふ", 1, 5295, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118301, "お姉ちゃんとして\nシオリンにかっこいい\nとこ見せなくっちゃ☆", 1, 5296, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118301, "いつかシオリンと\n一緒に空を飛んで\n空中散歩してみたいな", 1, 5297, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118301, "すこし眠くなってきた\nかも…シオリンと\nお昼寝しようかな", 1, 5298, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118301, "お散歩してこよっと！\nシオリンとお出かけ\nするのにいい場所\n探さなくっちゃ！", 1, 5299, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118301, "シオリンに過保護って\n言われちゃった…♪\nそれでも心配しちゃう\nのが姉心…だよね？", 1, 5300, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118301, "気持ちいい日差し！\nシオリンと彼と、\nお昼寝したら\n気持ちよさそ～♪", 1, 5301, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118301, "今度天気がいい日には\nシオリンと彼を誘って\n一緒にお出かけ\nしたいな～♪", 1, 5302, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118301, "今日はご機嫌なんだ♪\nだって、シオリンの\n調子がいいんだもん☆", 1, 5303, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118301, "今からシオリンと\nピクニックに\n行くんだ♪　そうだ！\nキミも一緒にどう？", 1, 5304, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118301, "わっ、びっくりした！\nシオリンから借りた本\n読むのに集中\nしてたから…", 1, 5305, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118301, "うん？　どうしたの？\nあっ、もしかして\nこの服、気になる？", 1, 5306, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118301, "シオリンもキミも元気\nいっぱいだと、私も\n元気になっちゃうよ☆", 1, 5307, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118301, "シオリンの前だけ\nじゃなくて、キミにも\nかっこいい私の姿を\n見てほしいな☆", 1, 5308, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 118301, "おはよう！　えへへっ\n今日は早起きなんだ♪\nシオリンにかっこ悪い\nとこ見せたくないから", 1, 5309, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 118301, "おなかすいたなぁ～…\nシオリンと一緒に\nおいしいもの食べに\n行こうかな！", 1, 5310, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 118301, "もうこんな時間！？\nシオリンと一緒だと\n時間が早く\n過ぎていっちゃうね！", 1, 5311, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118301, "この快適な寝心地…！\nシオリンにもおすすめ\nしなきゃ…！", 1, 5313, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118301, "わあ、かわいい！\nそれにさわり心地も\nいいし、抱き枕に\nちょうどいいかも！", 1, 5314, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118301, "いいにおい…♪\nなんだかリラックス\nできて…すやぁ…", 1, 5315, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118301, "ぽかぽか～☆\nシオリンと一緒に\nぬくぬくしたら\n気持ちよさそう♪", 1, 5316, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 118301, "仲のいい人が一緒だと\nつい食が進んじゃうね\nえへへ…♪", 1, 5318, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 118301, "ふむふむ…この本…\nシオリンが好きかも！\n今度おすすめしよっと", 1, 5319, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118401, "お姉ちゃん、\n私のために無理は\nしてないかな…？", 1, 5322, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118401, "新しく買った絵本…\nこういうのなら\nお姉ちゃんと楽しく\n読めるよね", 1, 5323, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118401, "ニャットちゃんを\nここに連れてきて\nみんなで遊べないかな", 1, 5324, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118401, "本屋さんに行こうかな\nみんなで読める\n面白い本が\nあるといいなぁ", 1, 5325, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118401, "たまには…読むだけ\nじゃなくて、自分で\nお話を書いて\nみようかな…？", 1, 5326, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118401, "時々…お姉ちゃんと\nあの人が似てるって\n思うことがあります…", 1, 5327, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118401, "お姉ちゃんとあの人…\n優しくて頼もしい\n二人に囲まれて…\n私、幸せ者だなぁ…", 1, 5328, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118401, "どうかしましたか？\nもしかして…あなたも\n一緒にこの本を\n読みたい…とか？", 1, 5329, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118401, "けほっ…けほっ…\nあっ、心配いりません\n飲み物を飲んで\nむせただけですから", 1, 5330, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118401, "この格好…\n似合ってますか…？\nえへへ、照れますね…", 1, 5331, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118401, "なぁに、お姉ちゃん？\n…あっ、すみません\n読書に集中していて…\n間違えちゃいました…", 1, 5332, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118401, "お姉ちゃん、時々無理\nしちゃうから…\nあなたも一緒だと\n安心できます", 1, 5333, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118401, "お姉ちゃんとあなたと\n頼れる人に囲まれて\n私は幸せものですね", 1, 5334, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 118401, "なんだか朝から\n調子がいい…今日は\nいい日になりそうです", 1, 5335, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 118401, "せっかくだから、\nお姉ちゃんを誘って\nお外でランチも\nいいかも", 1, 5336, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 118401, "お姉ちゃんに心配\nかけないように…もう\nちょっと本を読んだら\n寝なくちゃね", 1, 5337, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118401, "ふんわり柔らかくて\n気持ちいい手触り…\nお姉ちゃんが寝るのに\nちょうどよさそう", 1, 5339, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118401, "ふふっ、かわいい♪\n絵本の物語みたいに\nおしゃべりできない\nかな…？", 1, 5340, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118401, "素敵な落ち着く香り♪\nなんだかお姉ちゃん\nみたい、ふふっ", 1, 5341, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118401, "あったかくてほっと\nするなぁ\nここでゆっくり\n読書しようかな", 1, 5342, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 118401, "この味…\nお姉ちゃんが好きそう\n少しもらって、\n持って行こうかな…？", 1, 5344, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 118401, "この本みたいな、\n心躍る大冒険も\nお姉ちゃんと一緒なら\nできちゃいそう", 1, 5345, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117501, "いい部屋ね\nここを私の分院に\nするのもいいかも\nしれないわね…♪", 1, 5348, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117501, "珍しいカンポーが\n手に入ったから\n色々試してみたいわ\n色々と、ね♪", 1, 5349, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117501, "この服を着ると\n自分が医者だって\nより実感するわね\nまあ、闇医者だけどね", 1, 5350, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117501, "新薬開発のために\n薬草を採取して\nおこうかしら", 1, 5351, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117501, "いつ急患が来ても\n対応できるように\nしておきましょうか", 1, 5352, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117501, "あの子の研究資料も\nだいぶ増えてきたわね\nちゃんとまとめて\nおかないと♪", 1, 5353, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117501, "あの子は大事な\n研究対象。だから\nいつまでも健康で\nいてもらわないとね♪", 1, 5354, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117501, "薬に興味があるの？\nなら、今度ゆっくりと\n教えてあげるわね", 1, 5355, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117501, "少し時間があいたから\n一緒に新薬の実験を\nしましょうか♪", 1, 5356, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117501, "どうしたの？\nまさか急患…！？\nあら、違うのね\nならよかったわ", 1, 5357, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117501, "具合が悪い時はすぐに\n言うのよ。君には\nいつでも健康でいて\nほしいから", 1, 5358, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117501, "少し仮眠したいわね\n肩…いいえ、膝を\n貸してもらえる？", 1, 5359, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117501, "たまには…君の隣なら\n闇医者も悪い研究者も\nお休みにするのも\n悪くないわね♪", 1, 5360, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 117501, "たまには朝の陽ざしを\n浴びるのも…\n悪くないわね", 1, 5361, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 117501, "天気がいい日は\nいい薬草が採れるの\n君も集めるの、\n手伝ってくれる？", 1, 5362, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 117501, "いけない、もうこんな\n時間なのね\n診察に没頭すると\n時間感覚がなくなるわ", 1, 5363, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117501, "もう少し固い方が\n私好みだけど…\n悪くない寝心地ね", 1, 5365, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117501, "このぬいぐるみ…\n闇医者として\n興味深いわね…", 1, 5366, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117501, "落ち着く香りね\n私も診察室で\n使ってみようかしら", 1, 5367, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117501, "冷えは病につながるわ\nしっかり温めて\n体をいたわりましょ", 1, 5368, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 117501, "おいしい♪\n研究のお供に\nもってこいの\n味だわ", 1, 5370, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 117501, "医学は日進月歩…\n闇医者とはいえ\n最新の論文には\n目を通しておかないと", 1, 5371, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117601, "白衣の天使かあ\nまさにボクのための\n呼び方だね", 1, 5400, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117601, "美しさとは\n日々の努力が\n作るものなんだよ", 1, 5401, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117601, "大変だ…\nボクは美しく\nなりすぎたかも", 1, 5402, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117601, "ボクのかわいさで\n癒やされて\nみんな健康に\nなっちゃうね", 1, 5403, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117601, "どんな服装をしても\nボクの美しさは\n変わらないね", 1, 5404, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117601, "これからはボクも\n美の伝道師として\nがんばらないとね♪", 1, 5405, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117601, "ボクのかわいい笑顔を\n近くで見る以上の\n癒やしはないよね♪", 1, 5406, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117601, "どう？　看護師姿の\nボクもかわいいでしょ\nもっと近くで見る？", 1, 5407, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117601, "ほら！　美しいボクの\n看護師姿を見たら\n元気でちゃうでしょ？", 1, 5408, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117601, "白衣の天使のボクも\n最高に美しいね\n存分に癒されてよ♪", 1, 5409, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117601, "具合が悪いの？\nならボクの美しい顔を\n処方してあげる♪", 1, 5410, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117601, "さあ今日もボクと\n一緒に美の追求に\n付き合ってもらうよ♪", 1, 5411, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117601, "かわいい天使のボクが\nいつもそばにいるんだ\nキミって幸せ者だね♪", 1, 5412, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 117601, "さすがボク\n目が覚めるような\n美しさでしょ？", 1, 5413, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 117601, "美は食で作られる\n美容にいいものを\n食べようよ！", 1, 5414, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 117601, "ふああ…早く寝よ？\n夜更かしは美容の\n大敵だからね", 1, 5415, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117601, "ふう～…\nリラックスする\nボクも美しい", 1, 5417, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117601, "ボクが抱きしめると\n普段よりかわいく\n見えちゃうね♪", 1, 5418, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117601, "美しいボクは\n常にいい香りを\nしていなくちゃね", 1, 5419, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117601, "光がボクを照らしてる\nんじゃなくて、ボクが\n光を輝かせてるのさ", 1, 5420, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 117601, "バランスのいい食事が\n美しいボクを\n作るのさ", 1, 5422, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 117601, "ふむふむ…なるほど\nこうすればより美しく\nなれるかもしれない", 1, 5423, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105701, "仲間のためなら\nきっと…どこまでも\n強くなれるよね", 1, 5425, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105701, "プリンセスフォーム？\n私と同じような力を\n持ってる人がいるの？\n会ってみたいな～！", 1, 5426, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118001, "ここの空気は、\nどうもゆるすぎて…\n落ち着きませんわね", 1, 5427, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118001, "陛下のお越しになる\n別宅としては、\n少し警備が\n甘すぎでは？", 1, 5428, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118001, "あらあら？\nサッシにホコリが！\nちょっとネコ助！\n掃除は念入りに！", 1, 5429, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118001, "いつまで入り浸って\nいるのかと、\n秘書に叱られて\nしまいましたわ…", 1, 5430, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118001, "もういっそ、\nここに間借りして\n仕事しようかしら…？", 1, 5431, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118001, "邪魔している身ですし\n家事くらい\nお手伝いさせなさいな", 1, 5432, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118001, "本当は、意外と\n居心地が良いのかも…\n陛下の気持ち、\n少し分かりましたわ", 1, 5433, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118001, "ちょっ…わたくしを\n叩いても、ホコリなど\nあまり出ませんわよ！", 1, 5434, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118001, "陛下っ？\nなんだ…貴方でしたの\n用件は手短にお願いね", 1, 5435, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118001, "別に、好き嫌いは\nありませんけれど…\n魔物料理って、意外と\n美味なものですわね", 1, 5436, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118001, "言っておきますけれど\nわたくしは商売人です\n荒事は専門外ですわよ", 1, 5437, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118001, "プリンセスナイトの\n能力…なにか儲け話に\n使えませんかしらね", 1, 5438, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118001, "貴方がたのことも、\n少しくらいは\n信用してないことも\nありませんでしてよ", 1, 5439, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 118001, "おはようございます\nさ、今日もバリバリ\n働きますわよ！", 1, 5440, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 118001, "お昼ですわね\n栄養補給をしつつ\n働きますわよ！", 1, 5441, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 118001, "定時ですって？\n関係ありません！\nわたくし、まだまだ\n働きますわよ～っ！", 1, 5442, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118001, "うっ…いけませんわ\n日頃の疲れが…\nふわぁ…", 1, 5444, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118001, "もふもふ…ふふっ♪\n…なんですのっ、\nなにかおかしくて！？", 1, 5445, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118001, "いい香りですわね\nわたくしの執務室にも\n置きたいくらいですわ", 1, 5446, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118001, "いけませんわ…\nこんなに暖かいと\n日頃の疲れが…ふわぁ", 1, 5447, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 118001, "もう一つ食べたら\n仕事に戻りますわよ？\nあと一つだけ…", 1, 5449, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 118001, "商人にとって\n知識は武器ですわ\n陛下のため、貴方も\nもっと勉強なさい？", 1, 5450, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117701, "故郷のお菓子を\nいろいろ作ったんだ～\nみんなに\n配ってくるさ～♪", 1, 5452, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117701, "彼ってどんな仮装が\n似合うかな～？\nシーサーとか？", 1, 5453, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117701, "カボチャチャンプルー\n作ってみよっと！\nきっとおいしいさ～♪", 1, 5454, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117701, "視線を感じるさ～\nもしかして幽霊？\nおしゃべりするさ～♪", 1, 5455, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117701, "さっき作った\nサーターアンダギー、\n早く彼に\n食べてほしいさ～♪", 1, 5456, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117701, "彼のために、\n特別なイタズラを\n考えとくさ～♪", 1, 5457, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117701, "いつか彼と、\n故郷のハロウィンを\n楽しみたいさ～♪", 1, 5458, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117701, "ハロウィンは\n私たちが守るさ～♪\nさ、見回りに行こ！", 1, 5459, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117701, "この制服、\nハロウィンらしくて\n大好きさ～♪", 1, 5460, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117701, "イタズラする気ね～？\nどんなことされるのか\n楽しみさ～♪", 1, 5461, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117701, "カボチャのランタン、\n一緒に作ろ！\n絶対楽しいよ～♪", 1, 5462, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117701, "お菓子くれるの？\nあは～、ありがとー♪\nもぐもぐ…\n優しい味がするさ～♪", 1, 5463, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117701, "キミと一緒に過ごす\nハロウィンは、\nドキドキとワクワクが\n止まらないさ～♪", 1, 5464, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 117701, "ハロウィンは\n朝から大忙しさ～！\nお菓子作り、\n手伝ってほしいさ～", 1, 5465, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 117701, "お化け屋敷に行こ！\n一緒にいっぱい\nドキドキしようね～♪", 1, 5466, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 117701, "ここからが\nハロウィン本番さ～！\n幽霊たちと\nいっぱい遊ぶさ～♪", 1, 5467, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117701, "ちょっと休んだら、\nお菓子作りを\n再開するさ～♪", 1, 5469, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117701, "でーじかわいいね～♪\nこの子に仮装させたら\nきっと似合うさ～♪", 1, 5470, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117701, "お菓子みたいに\n甘い匂い…\nハロウィンらしい\nいい香りさ～♪", 1, 5471, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117701, "ランドソルの秋って\n冷えるけど…\nこれがあれば\n体も心もぽかぽかさ～", 1, 5472, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 117701, "故郷のご飯も\nおいしいけど、\nこっちのご飯も\nでーじおいしいさ～♪", 1, 5474, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 117701, "パンプキンパイの\nレシピは…なるほど～\n勉強になるさ～♪", 1, 5475, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 117701, "全部おいしそうで\nどれから食べるか\n迷っちゃうさ～♪", 1, 5477, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117801, "ムっ！　幽霊の気配を\n感じるデス！\nさあ姿を現すデス！", 1, 5479, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117801, "ムムム…\n妖魔の力を忍法に\n使えないでショウか…", 1, 5480, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117801, "怪しい妖魔を\n成敗するデス！\nハロウィンの平和は\nワタシが守りマスよ！", 1, 5481, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117801, "ワタシの祭囃子が\n聞こえマスかー？\nさあ集まるデース！\n陽気に踊りまショウ！", 1, 5482, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117801, "お祭りは\n楽しんだ者勝ちデス！\n踊らにゃ\nソンソンデス！", 1, 5483, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117801, "幽霊になる前に\n天下統一を\n果たしたいデスね", 1, 5484, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117801, "あの世に行っても\nショーグンは\nワタシの大事な\nショーグンデス！", 1, 5485, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117801, "ショーグン！\n悪霊退治に\n出かけるデース！", 1, 5486, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117801, "妖魔を家来に\nしてみるのは\nどうデス？", 1, 5487, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117801, "マントを使って\nウツセミの術の精度を\n高めるデス！", 1, 5488, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117801, "ハロウィンにちなんだ\n新しい忍法…\nすなわち妖忍法の\n開発を急ぐデス！", 1, 5489, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117801, "あの世でも\nショーグンに\nお仕えしマスよー！", 1, 5490, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117801, "ショーグンもワタシと\nハロウィンパレードを\n踊り明かしまセンか？", 1, 5491, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 117801, "おはようございマス！\n天下統一に向けて\n急いで準備するデス！", 1, 5492, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 117801, "お腹減ってまセンか？\nハロウィンスイーツで\n休憩するデ〜ス", 1, 5493, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 117801, "夜更かしすると幽霊が\nでるかもしれまセン…\nヒュ〜ドロロロ〜…", 1, 5494, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117801, "もふもふなところが\nわたあめちゃんに\n似ていマス。わんっ！", 1, 5496, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117801, "ぬいぐるみや人形には\n魂が宿るという\n言い伝えがありマス…\nマカフシギデ〜ス", 1, 5497, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117801, "ウ〜ム…\nなんとも怪しい\n香りがしマスね！", 1, 5498, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117801, "パレードの熱気に\n負けないくらい\n熱いデス！", 1, 5499, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 117801, "ついつい食べすぎて\nしまうのは\n妖魔のせいデスか！？", 1, 5501, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 117801, "トーゴク妖怪の\n図鑑デス！　個性的な\n妖怪ばかりデスね！", 1, 5502, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 117801, "ハロウィンの甘味を\n集めまシタよ！\nショーグンへの\n貢ぎ物デス！", 1, 5504, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101301, "彼と二人で\n買い物に…って…\nこれってデートに\nなっちゃうのかな！？", 1, 5531, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101301, "とうっ！　どうどう？\n今の決めポーズ！\nかっこいいよね♪", 1, 5532, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117901, "ヒデサイとおそろの\nハロウィンコスとか\nしてみたいな～", 1, 5533, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117901, "カボチャ料理かぁ…\n作ったらヒデサイ\n食べてくれるかな～", 1, 5534, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117901, "ハロウィンスイーツ…\nどれもおいしすぎて\n激ヤバなんですけど！", 1, 5535, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117901, "霊界ってどんなとこ\nなんだろ～\nわりと近所だったり？", 1, 5536, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117901, "服をハロウィンっぽく\nアレンジするのも\nたのしーよね♪", 1, 5537, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117901, "ヒデサイと一緒に\nハロウィンパーティー\n…えへへ、楽しみ～", 1, 5538, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117901, "ヒデサイといると\nドキドキする…\nなんでだろ～？", 1, 5539, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 117901, "ねねね、うちらも\nハロウィンパレード\n見に行こー？", 1, 5540, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117901, "ハロウィンセイバーズ\nしゅつどーう！\nヒデサイも行こ～♪", 1, 5541, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117901, "マントのロゴ\nかっこいいっしょ～？\nカボきちってゆーんだ", 1, 5542, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117901, "トリックオア\nトリート！\nねえねえヒデサイ\nお菓子ちょうだい♪", 1, 5543, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 117901, "ヒデサイも\nメイクしてみる？\nハロウィンぽいやつ♪", 1, 5544, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 117901, "ヒデサイのこと\nうちがいつでも\nあっためたげるね！\n…あ！　手、手の話！", 1, 5545, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 117901, "ハロウィンのイベント\n朝からでも\nやってるかな～？", 1, 5546, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 117901, "カボチャでおやつ\n作ってみたんだ～\nヒデサイ食べてみて！", 1, 5547, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 117901, "ハロウィンの夜って\nやっぱテンション\n上がるよね～♪", 1, 5548, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 117901, "超ふかふかしてる～\nちょっと休憩させて\nもらっちゃお", 1, 5550, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 117901, "はぁ…\nもにカワすぎて\nつらいんですけど～！", 1, 5551, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 117901, "ハロウィン限定の\nフレグランスじゃん！\nいい匂いする～♪", 1, 5552, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 117901, "やばばば…\nあったかくて\n眠くなっちゃう～", 1, 5553, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 117901, "これ食べて\nもうひとがんばり\nしよーっと！", 1, 5555, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 117901, "ハロウィンっぽい\nファッションとか\n載ってないかなぁ？", 1, 5556, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 117901, "お菓子いっぱいだ～！\nあとでヒデサイにも\n持ってってあげよ～", 1, 5558, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118501, "お掃除が終わったら\n次は書類を片付けて…\nその次は…", 1, 5560, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118501, "皆さんが冒険している\n間の留守は私に\nお任せください♪", 1, 5561, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118501, "美食殿の皆さんが\n持ってきてくれる\nお土産が楽しみです♪", 1, 5562, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118501, "ネビアさんとは\nときどき一緒にお茶\nする仲なんです", 1, 5563, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118501, "ギルドハウスの乱れは\nギルドの乱れ\n整理整頓が大切です", 1, 5564, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118501, "素敵なギルドハウスの\n管理人ができて\nとっても幸せです♪", 1, 5565, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118501, "皆さんをほんの少し\n特別扱いしちゃう\n私がいます", 1, 5566, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118501, "何かわからないことが\nあるときは私に\n聞いてくださいね", 1, 5567, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118501, "書類を書いていると\n落ち着きます…\n一緒にどうですか？", 1, 5568, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118501, "あなたのまわりは\nいつも賑やかで\n楽しいですね♪", 1, 5569, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118501, "あっ！　お頼みしたい\nことがあって\n探してたんです♪", 1, 5570, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118501, "ギブアンドテイクは\n大切です。たまには\nお礼させてください♪", 1, 5571, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118501, "陰に日向にあなたを\n支えていきます♪\n期待していてください", 1, 5572, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 118501, "今日も一日\n管理人として\nがんばります♪", 1, 5573, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 118501, "お腹がすきました…\nそろそろお昼に\nしませんか？", 1, 5574, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 118501, "もうこんな時間…\n皆さんといると一日が\nあっという間です", 1, 5575, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118501, "良質なお仕事は\n良質な休憩から\nです♪", 1, 5577, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118501, "かわいいものに\n囲まれてお仕事できる\nのは幸せですね♪", 1, 5578, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118501, "お仕事に集中できる\nアロマを設置するのも\nよさそうですね♪", 1, 5579, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118501, "ポカポカ温まりながら\n書類仕事をするのも\nいいものですね…", 1, 5580, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 118501, "栄養補給しないと\n頭が回りませんからね\nきちんと食べましょう", 1, 5582, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 118501, "新条例はこのように\n活用されている、と…\nふむふむ…", 1, 5583, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106701, "会合用の資料と\n連載のネーム…\nどっちを片付けよう？", 1, 5585, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106701, "イノリちゃん、また\nお金くすねてる…\n帰ってきたら\nお尻ペンペンだ♪", 1, 5586, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106701, "カヤちゃんの装備\n傷むの早いなぁ〜\nメンテしてあげないと", 1, 5587, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106701, "そうだ、ラーゴのご飯\n買いに行かないと\n今日は何がいいかな？", 1, 5588, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106701, "ここの人たちは見てて\n飽きない人ばっかり♪\n漫画の参考にしよ〜", 1, 5589, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106701, "また一緒に買い物にも\n行きたいし、冒険も\nしたいな〜…♪", 1, 5590, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106701, "…君は見てるだけで\n満足なの？ ちゃんと\n触って欲しいな…♪", 1, 5591, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106701, "私の目が見たいの？\nいいけど…魅了されて\n魂が壊れちゃうよ…？\n…冗談で〜す☆", 1, 5592, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106701, "ラーゴがね、君に\n遊んで欲しいって☆\n仲良くしてあげてね", 1, 5593, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106701, "アシスタントの仕事\n気に入ってくれたなら\nまたお願いしていい？", 1, 5594, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106701, "私が覚醒できるか？\n見せてあげたいけど…\nすごいことになるから\nまた今度で♪", 1, 5595, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106701, "楽しいトラブルが\nあったら、とりあえず\n声はかけて欲しいな♪", 1, 5596, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106701, "マッサージお願い♪\n変なところは…\n少しだけなら☆", 1, 5597, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 106701, "おはよう〜♪\n今日も一日、一緒に\nがんばっていこうね", 1, 5598, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 106701, "お腹空いちゃった…\n一緒にお昼行かない？\nいいお店があるんだ〜", 1, 5599, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 106701, "君、眠れないの？\n誰かに見られてる？\n…きっと気のせいだよ", 1, 5600, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106701, "ちょっと休憩〜…\nこのまま原稿すれば\n疲れなくなるのかな？", 1, 5602, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106701, "かわいい〜♪\nラーゴのお友達に\nなってくれる？", 1, 5603, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106701, "いい香り〜♪\nこの目でどう見えるか\n伝えられたらいいのに", 1, 5604, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106701, "ほどほどなのが\nちょうどいい時も\nあるよね…♪", 1, 5605, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 106701, "この味付け…\nあっちでも再現\nできるかな？", 1, 5607, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 106701, "ふふっ、面白い♪\nイノリちゃんの買う\n漫画にハズレなし☆", 1, 5608, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 119001, "私…23歳だもん\n魔法少女だなんて\n名乗れないわ…", 1, 5610, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119001, "あん、どぅ、とろわ〜\nみんなイイ子に\nな〜る♪", 1, 5611, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119001, "あっ！　イケナイ子の\n気配がするわ！\n風紀の乱れは\n先生が許しませんっ！", 1, 5612, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119001, "ここで闇の力が\n発動したら大変だわ！\nやだっ…そう思ったら\n緊張して…ダメぇ〜！", 1, 5613, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 119001, "オイタした子はみんな\nどうしてうれしそうに\n私の愛のムチを\n欲しがるのかしら…？", 1, 5614, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 119001, "あの子に次は何を\n教えてあげようかしら\n愛？　希望？\nそれとも…♪", 1, 5615, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 119001, "あの子のことを\n考えると、愛と希望で\n胸がいっぱいになるの", 1, 5616, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 119001, "先生なのに闇の力を\n持ってるのは秘密よ？\n他の子には\n知られたくないから…", 1, 5617, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119001, "秘密の授業の内容？\nそれは…あんなことや\nこんなことよ♪\nそれから、それから…", 1, 5618, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119001, "しーっ！　私の正体が\nルーセント学院の\n先生だって、ここでは\n秘密にしててね？", 1, 5619, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119001, "あら？　補習して\nほしいの？　いいわよ\n君のお願いなら\nなんでも聞いちゃう♪", 1, 5620, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 119001, "イイ子にはごほうびを\nあげなくちゃ！\n君はなにが欲しい？\n遠慮しちゃダメよ？", 1, 5621, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 119001, "君との秘密が\nまた一つ\n増えちゃったね♪", 1, 5622, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 119001, "今日も一日\n元気に過ごしましょう\n先生が指導してあげる", 1, 5623, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 119001, "夜まで頑張れるように\nお昼ご飯はしっかり\n食べましょう〜！", 1, 5624, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 119001, "夜更かしはダメよ？\nイケナイ子が\nすることです！", 1, 5625, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119001, "闇の力に振り回されて\nもうヘトヘトよ〜\n心も体も\n癒されたいわ〜", 1, 5627, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119001, "『秘密の学び舎』に\nこっそり連れて\n行っちゃおうかしら♪", 1, 5628, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119001, "イケナイ甘い香り…\nなんだか胸の奥が\n熱くなってきたわ…♪", 1, 5629, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 119001, "やんっ♪\n燃え上がる愛みたい♪\n火照ってきちゃった♪", 1, 5630, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 119001, "生徒の見本に\nなれるように\n好き嫌いはしないの", 1, 5632, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 119001, "『学び舎に咲く禁断の\n愛』？　気になるわ…\nううん、ダメよっ！\nでも…ちょっとだけ…", 1, 5633, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 106301, "次に会う時は、\nまたあそこに一緒に…\nえへへ…！", 1, 5635, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 106301, "横顔見てるの、\nバレてないよね…？\n…ちらっ", 1, 5636, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105301, "背が少し伸びたか！？\nあ、あいつのところへ\n報告に行かねばっ！", 1, 5637, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105301, "私も貴公の隣に\n立っていられるよう\n日々精進していくぞ！", 1, 5638, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 119101, "ああ～\nドＭミストが\nあふれてしまいます～", 1, 5639, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119101, "魔法少女として\nクウカ頑張ります～！\n愛と希望の妄想を…！", 1, 5640, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119101, "決めポーズの練習より\nやられポーズの練習を\nしておきます…♪", 1, 5641, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119101, "快感を溜めに溜めて\n放つドＭビームは\n最高ですよ～", 1, 5642, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 119101, "はっ！\nこれは闇の気配！\n…クウカ自身でした", 1, 5643, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 119101, "ドＳさん…ぐふふ…\nクウカを縛りあげ…\nじゅるりぃ～♪", 1, 5644, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 119101, "ドＳさんに攻められる\nことで、クウカは進化\nしてゆくのです～！", 1, 5645, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 119101, "はぅっ！\nもっと激しく…！\n情熱的に～～～！", 1, 5646, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119101, "クウカの変身シーンを\nじっくり眺めたい！？\nは、恥ずかしいです～", 1, 5647, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119101, "「本当に魔法少女か\n確かめてやるよ…！」\nドＳさんはそう言って\nクウカを…ぐふふ…♪", 1, 5648, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119101, "愛と希望の力が\nクウカの中に…\nこの胸の高鳴りが…？", 1, 5649, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 119101, "ひゃあんっ！\nそこです～っ！\nもっと、もっと～♪", 1, 5650, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 119101, "クウカをこんなに\n悦ばせるのは\nドＳさんだけです…♪", 1, 5651, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 119101, "明るい時間に出歩くと\n目立ちますね\n魔法少女の衣装…", 1, 5652, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 119101, "お昼の見回りです～♪\nああ～うっかりドＭを\n増やしてしまいました", 1, 5653, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 119101, "夜！　それはクウカが\n活性化する時間です！\n闇の活動開始です～♪", 1, 5654, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119101, "気の緩んだクウカを\nドＳさんの鋭い牙が\n狙っています～♪", 1, 5656, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119101, "魔法少女のマスコット\nにいいかもしれません\nもっと鎖とか飾りを…", 1, 5657, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119101, "魅惑的な香りです～\nドＭミストの香りに\n似て…いや違いますね", 1, 5658, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 119101, "火照る体…紅潮した\nクウカの肢体を熱く\n見つめるドＳさん…", 1, 5659, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 119101, "食欲を満たした次は\n何を満たせば\nいいですかねぇ～♪", 1, 5661, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 119101, "魔法少女がひどい目に\n遭う物語は…\nなさそうですね…", 1, 5662, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 119901, "ケーキも悪くないけど\nプリンが一番なの\nでもせっかくだから\n両方もらってやるの！", 1, 5664, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119901, "クリスマスって聞くと\nなんだかあったかい\n気分になるの～", 1, 5665, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119901, "煙突がなくても\nミヤコはどこからでも\nおうちに入れるの♪", 1, 5666, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119901, "イブもプリン！\nクリスマスもプリン！\nプレゼントもプリン！", 1, 5667, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 119901, "クリスマスツリーの\n高いところの\n飾りつけは、ミヤコに\nまかせろなの～！", 1, 5668, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 119901, "アイツのプレゼント\n楽しみなの～\nきっと最っ高に特別な\nプリンなの～♪", 1, 5669, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 119901, "ミヤコがサンタなら\nアイツはトナカイなの\nつまりお似合いって\nことなの～♪", 1, 5670, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 119901, "ミヤコ、プレゼントを\n配りにいくの～♪\nオマエも手伝えなの！", 1, 5671, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119901, "オマエにも飾りつけ\nしてやるの！\n頭にプリンを乗せると\nかわいいの♪", 1, 5672, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119901, "幽霊のミヤコが\nいるなら、サンタも\nいて当たり前なの～！", 1, 5673, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119901, "靴下にプリンを\n入れたらつぶれるの\nだからプリンは直接\nミヤコによこすの～！", 1, 5674, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 119901, "今日はプリンだけじゃ\n満足してやらないの！\nミヤコをたくさん\n甘やかせなの！", 1, 5675, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 119901, "ミヤコのプリン\nオマエになら一個…\nうう～、一口だけ\nあげてもいいの！", 1, 5676, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 119901, "サンタの朝は早いの～\nミヤコも早起きして\nプレゼント準備したの\nとってもえらいの♪", 1, 5677, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 119901, "今日は一日中\nみんなと遊ぶの～！\nオマエも来いなの！", 1, 5678, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 119901, "みんなが寝たら\nチャンスなの～\nこっそりプレゼント\n配りにいくの♪", 1, 5679, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119901, "むふー、気持ちいいの\nこういうプレゼントも\nいいかもしれないの", 1, 5681, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119901, "これ気に入ったの～\nプリンとアイツの\n次くらいなの♪", 1, 5682, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119901, "いいにおいなの～\nうっかり成仏\nしちゃいそうなの…", 1, 5683, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 119901, "ここに飾っといた\n雪だるま\nなくなっちゃったの\nきっと成仏したの～", 1, 5684, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 119901, "これはオマエがくれる\nプリンとは\n別腹なの～", 1, 5686, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 119901, "プリンケーキ…\nこれならミヤコにも\n作れそうなの！\nさっそく作るの！", 1, 5687, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 119901, "びっくりしたの～！\n幽霊のミヤコを\n驚かすなんて…\nなかなかやるの！", 1, 5689, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 119901, "コイツの中…\nあったかいの～♪\n昼寝にぴったりなの～", 1, 5690, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 119201, "メリークリスマス！\n聖夜に笑顔を\nお届けするべ♪", 1, 5691, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119201, "クリスマス前は\n乳製品がよく売れて\nありがてぇけんど…\n目が回る忙しさだ～", 1, 5692, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119201, "牧場で売る\nクリスマスリースを\n作っておかねぇと…", 1, 5693, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119201, "うちの動物たちにも\nクリスマスプレゼント\n用意しなきゃなぁ", 1, 5694, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 119201, "クリスマスに向けた\n限定商品とか\n作ってみるべか？", 1, 5695, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 119201, "あんちゃんへの\nプレゼント選び…\nここでボケるか…？\nやめておくべか…？", 1, 5696, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 119201, "オラ、あんちゃんから\n本当にたくさんの物を\nもらってんなぁ…", 1, 5697, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 119201, "雪遊びがしたいべか？\nへへっ、あんちゃん\nには負けねぇど！", 1, 5698, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119201, "ディナーと一緒に\n聖夜のお笑いライブを\nすんのはどうだべか？\nツッコミは任せたべ！", 1, 5699, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119201, "寒くなってくると\n鍋が食べたくなるべや\nあんちゃんはどうだ？", 1, 5700, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119201, "あんちゃんは\nサンタさんのこと\n信じてるだか？", 1, 5701, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 119201, "どでかいクリスマス\nケーキをこさえるべ\nあんちゃんも作るの\n手伝ってくんろ♪", 1, 5702, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 119201, "あんちゃんが\nオラの旦那だったら…\nって、あんちゃん！\n今の聞いてただか！？", 1, 5703, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 119201, "牧場の朝は早いど！\nさぁてと、まずは\n雪かきしに行くべや", 1, 5704, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 119201, "あんちゃんも一緒に\nお昼食べてくか？\n採れたての冬野菜は\nうんまいど～♪", 1, 5705, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 119201, "よい子は早く寝ねぇと\nサンタさんが\n来てくれねぇべよ？", 1, 5706, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119201, "こういうのリンリンが\n好きそうだな…\nプレゼント候補に\n入れておくべ！", 1, 5708, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119201, "もふもふしてて\nなまらめんこくて…\nあぁ…癒されるべや…", 1, 5709, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119201, "この匂い…\n気持ちが和むべや\n夢心地になっちまうな", 1, 5710, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 119201, "あったけぇところは\n落ち着くなぁ…\nつい眠くなっちまう", 1, 5711, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 119201, "こ、この味は…！\n…うーん、もっと\nリアクション芸も\n練習しねぇとな", 1, 5713, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 119201, "シオシオって\nどんな本読んでんだ？\n…難しい字が\nぎっしりだべや…", 1, 5714, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 119201, "この仕掛け…\nオラのコントに\n活かせるかも\nしんねぇな", 1, 5716, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 119201, "この着ぐるみで\nリマリマのモノマネを\n練習してみるべか…？", 1, 5717, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105001, "もっとキレイになって\nアイツにぎゃふんと\n言わせてやるんだから", 1, 5718, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105001, "アタシのこと\nどう思う？\nかわいいって…そこは\nキレイって言ってよね", 1, 5719, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 119301, "ハートマークの刺繍…\nは、やりすぎですかね\nいやちょっとくらい\nアピールしてみても…", 1, 5720, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119301, "雪降らないかな～\nそしたらお兄ちゃんと\n雪だるま作ったり\nできるのに～", 1, 5721, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119301, "プレゼントを入れる\n靴下…いくつ編んでも\n足りません～！", 1, 5722, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119301, "お兄ちゃんとの\nクリスマスデートは\n早い者勝ちですからね\n負けませんよ～！", 1, 5723, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 119301, "クリスマスだし…\n受験勉強は今日だけ\nお休みです♪", 1, 5724, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 119301, "お兄ちゃんと\nおそろいのコーデで\nクリスマスデート…\nしたいなぁ～！", 1, 5725, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 119301, "お兄ちゃんに\nプレゼントする編み物\nこれで３着目～と♪\nあれ、作りすぎ？", 1, 5726, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 119301, "えへへ、もっと近くで\n見たくありませんか？\n私のとっておきの\nクリスマスコーデ♪", 1, 5727, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119301, "お兄ちゃんが風邪を\n引いたら一大事です！\n私の編み物で暖かく\nしてあげます♪", 1, 5728, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119301, "お兄ちゃん、ほっぺに\nケーキのクリームが\nついちゃってますよ♪\n私がとってあげます！", 1, 5729, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119301, "お兄ちゃん！\nケーキ、あーんって\nしてあげます！\nほら、あーん♪", 1, 5730, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 119301, "クリスマスは大切な\n人と過ごすものっ！\nお兄ちゃん…妹の予定\n空いてますよっ！", 1, 5731, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 119301, "クリスマスはずっと、\nお兄ちゃんと一緒に\nいたい…って言ったら\n欲張り…すぎですか？", 1, 5732, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 119301, "早起きは３本お得！\n…クリスマスなら\n追加で10本ぐらい\nもらえちゃうかも！？", 1, 5733, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 119301, "お昼はケーキ？\nそれとも七面鳥？\nごちそういっぱいで\n迷っちゃいますね～♪", 1, 5734, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 119301, "お兄ちゃんと\nクリスマスに夜更かし\n…えへへ、ちょっぴり\nドキドキしちゃう♪", 1, 5735, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 119301, "編み物をするには\n絶好のくつろぎ空間\nですね～♪", 1, 5737, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 119301, "なんというかわいさ…\nクリスマス\nプレゼントに\nもってこいですね！", 1, 5738, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 119301, "これがお兄ちゃんの\nお部屋の香り…？\nんん～！　ずっと\nかいでいたいです～♪", 1, 5739, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 119301, "ふぁ、あったか～い…\n冬場はこういうのが\nありがたいですね～♪", 1, 5740, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 119301, "ごちそうだぁ～♪\nほら、お兄ちゃんも\n食べて食べてっ！", 1, 5742, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 119301, "クリスマスデートの\n必勝法…こ、これは\n読むしかないです！", 1, 5743, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 119301, "わっ、すごいです～！\nこれを飾っておけば\nクリスマスが一層\n賑やかになりますね♪", 1, 5745, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 119301, "さぷらーいずっ！\nリマちゃんじゃなくて\nリノちゃんでした～！", 1, 5746, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120601, "ミミにもミソギにも\nあの人にも頼りに\nしてもらえるように\nしっかりしなきゃ！", 1, 5747, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120601, "ミミはにんじんを\n食べられてすごいな…\n私も見習わなきゃ\nがんばろう…！", 1, 5748, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120601, "ミソギから怖い話\n聞いちゃった…\nうぅ…誰か一緒に\nいてくれないかな", 1, 5749, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120601, "立派な大人に\nなれるように\nいっぱい勉強\nしておかなくっちゃ！", 1, 5750, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120601, "なんだか…あの人の\nあやしい視線を\n感じるような…？", 1, 5751, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120601, "あの人に勉強を\n教えられるように\nしっかり予習を\nしておこうっと…！", 1, 5752, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120601, "今日はあの人と\n一緒に冒険できる…\nなんだかワクワク\nしてきちゃった…！", 1, 5753, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120601, "あ、いいとこに来て\nくれましたね！\nあなたに頼みたい\nことがあるんです！", 1, 5754, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120601, "きゃあっ！？\nへ、変なところを\n触らないでください！", 1, 5755, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120601, "どうですかこの格好？\nかわいい、ですか…？\nそ、そうですか…\nありがとうございます", 1, 5756, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120601, "ひゃあっ…！\nすみません、勉強に\n集中してたので\n驚いちゃいました", 1, 5757, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120601, "一緒に勉強して立派な\n大人になりましょうね\n私がそばで見張ってて\nあげます", 1, 5758, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120601, "まだ立派ではないかも\nですけど、私よりも\n大人なあなたのことは\n頼りにしてますからね", 1, 5759, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 120601, "立派な大人は\n朝の時間の使い方が\n上手だと聞きました\nあなたはどうですか？", 1, 5760, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 120601, "ミミとミソギと\nあなたと…大事な人と\n一緒のお昼ごはんは\nやさしい味がしますね", 1, 5761, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 120601, "うぅ…怖い話を\n思い出しちゃいました\n寝るまで一緒に\nいてくれますか…？", 1, 5762, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120601, "すごい座り心地…！\nとっても勉強が\nはかどりそう…！", 1, 5764, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120601, "この子…なんだか\nミミに似てるような\n気がする…ふふっ♪", 1, 5765, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120601, "いい香り…♪\n勉強で疲れた\n頭がリフレッシュ\nできそうです", 1, 5766, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120601, "ぽかぽかだ…♪\nミミとミソギと\nあの人と一緒に\nいるときみたい", 1, 5767, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 120601, "いっぱい食べたら\n早く大人になれたり\nしないかな…？", 1, 5769, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 120601, "あう…\n知らない単語が…\nちゃんと調べて\n覚えなくっちゃ…！", 1, 5770, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120401, "うさぎさんが\nぴょ～んぴょん♪\nみんなえがおで\nるんたった～♪", 1, 5773, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120401, "キョウカちゃんも\nいつかにんじんさんを\n好きになってくれたら\nうれしいな～♪", 1, 5774, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120401, "今日のミソギちゃんの\nイタズラ、びっくり\nしちゃったけど\nおもしろかったな～♪", 1, 5775, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120401, "このおうち、いつも\nいろんな人がいて\nドキドキぴょんぴょん\nって楽しいな～♪", 1, 5776, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120401, "いいお天気～♪\nお外でみんなと\nいっぱい遊びたいな♪", 1, 5777, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120401, "ミソギちゃんも\nキョウカちゃんも\nおにいちゃんがいると\nずっと笑顔だね～♪", 1, 5778, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120401, "おにいちゃんにはやく\nあいたいな～♪\nまだかな、まだかな♪", 1, 5779, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120401, "えへへ♪\nおにいちゃんに\nなでなでされると\nぽかぽかするね♪", 1, 5780, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120401, "聞いて、おにいちゃん\nさっきね、かわいい\nちょうちょさんに\n会ったんだよ～♪", 1, 5781, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120401, "ふわっ…！\nおにいちゃんの顔を\nお絵かきしてたから\nびっくりしちゃった♪", 1, 5782, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120401, "おにいちゃんも\n遊びたいの？\nそれじゃあ二人も\n呼んで遊ぼう♪", 1, 5783, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120401, "おにいちゃんも\nみんなも、優しい人\nばっかりで\nミミ幸せ～♪", 1, 5784, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120401, "おにいちゃん♪\n高い高いしてほしいな\nすっごくぴょんぴょん\nできて、好きなんだ♪", 1, 5785, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 120401, "おはよう～♪\nおにいちゃん、今日は\nおひさまみたいに\nきらきら元気だね～♪", 1, 5786, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 120401, "おひるごはんだね～♪\nにんじんさんを\nいっぱい食べて\n元気いっぱい♪", 1, 5787, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 120401, "ふわあ…\n眠くなってきちゃった\nおにいちゃん、\n一緒に寝よう～？", 1, 5788, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120401, "ふわぁ、気持ちいい♪\nずっとこうやって\n座ってたくなるね～♪", 1, 5790, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120401, "わあ、かわいい～\nぎゅ～っ、ぎゅ～っ♪\nこれで仲よしだね\nえへへ♪", 1, 5791, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120401, "気持ちいいにおい～♪\nぴょんぴょんしたく\nなってきちゃった♪", 1, 5792, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120401, "あったかぽかぽか♪\nおにいちゃんも\nきてくれたら、もっと\nあったかくなりそう♪", 1, 5793, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 120401, "もぐもぐ…ごっくん♪\nおっきくなりたいから\nいっぱい食べるよ～♪", 1, 5795, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 120401, "えへへ、おもしろ～い\nこの絵本ね、\nキョウカちゃんから\n教えてもらったんだ♪", 1, 5796, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120501, "この部屋…探索の\nしがいがありそう！\nついでにイタズラも\nしかけちゃお♪", 1, 5799, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120501, "ミミってああ見えて\n実は大胆で度胸も\nあるよね…ちょっと\nかっこいいな…！", 1, 5800, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120501, "怖い話を聞いた時の\nキョウカって\n面白いんだよね～♪", 1, 5801, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120501, "にいちゃんが\n慣れちゃわないように\nまた新しいイタズラを\n考えなきゃ！", 1, 5802, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120501, "イタズラもいいけど\nミミとキョウカと\nにいちゃんと、普通に\n遊ぶのも楽しいなぁ♪", 1, 5803, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120501, "ミミにキョウカ、\nそれににいちゃん！\nみんなそろえば愉快な\n冒険が始まりそう！", 1, 5804, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120501, "次はどんなイタズラを\nしたら、にいちゃんは\n喜んでくれるかな？", 1, 5805, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120501, "今ミソギに触ったね？\nヤバいよにいちゃん…\n逃げないとトラップが\n発動しちゃうよ…！", 1, 5806, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120501, "わわっ！？　びっくり\nしたあ…！　新しい\nトラップを作ってて\n気づかなかったよ～", 1, 5807, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120501, "お？　にいちゃん、\nイタズラする側にも\n興味があるの？", 1, 5808, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120501, "にいちゃんなにか用？\nあ、もしかして…\nミソギと遊びたく\nなっちゃった？", 1, 5809, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120501, "ベストタイミングだよ\nにいちゃん！\n今からみんなで探検に\n行くところなんだ！", 1, 5810, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120501, "にいちゃんってさ\nホントに優しいよね\nなにしても怒らないし\n…ありがとね♪", 1, 5811, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 120501, "にいちゃんおはよー！\n景気づけに本日\n一発目のイタズラ、\nやっちゃうよ～♪", 1, 5812, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 120501, "お昼ごはんはロシアン\nサンドイッチだよ！\nさあ、にいちゃん！\n好きなの選んでよ！", 1, 5813, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 120501, "夜ってなんだか\nワクワクしてなかなか\n眠れないんだよね\nにいちゃんはどう？", 1, 5814, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120501, "この座り心地のよさ…\nなにかのトラップに\nうまく使えないかな？", 1, 5816, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120501, "こういうのに細工して\nミミやキョウカを\n驚かせると、いい反応\nするんだよね♪", 1, 5817, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120501, "においをうまく使った\nイタズラって…\nなにかないかな？", 1, 5818, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120501, "あったまりながら\n新しいイタズラを\n考えると、いい案が\n出て来たりするんだ♪", 1, 5819, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 120501, "おいしくてついつい\n食べ過ぎちゃうなぁ…\nはっ…！？　これって\nまさかトラップ…！？", 1, 5821, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 120501, "このキョウカから\n借りた冒険物語\nすっごく\nおもしろいんだよね！", 1, 5822, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120801, "晴れ着を着ると\nなんだか心が躍るね\n足取りも軽く\nなるってもんさ", 1, 5825, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120801, "正月の食べ物ってのは\n美味いもんだね\nいくらでも\n食べれちまうよ", 1, 5826, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120801, "普段は好き勝手してる\nアタシらも\n正月くらいは\n大人しくするさ", 1, 5827, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120801, "年越しそばに雑煮\nこの季節は\n温かいものが\n体に沁みるね", 1, 5828, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120801, "正月だからって\nのんびりしすぎたね\nちょっと釣りにでも\n行こうかね", 1, 5829, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120801, "いい正月だね\nいつかあの子と\n一杯やりたいもんだよ", 1, 5830, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120801, "大事な人と過ごす\n正月ってのがこんなに\n楽しいもんだとはね", 1, 5831, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120801, "部屋があったかくて\n顔が火照っちまったよ\nちょいと外に涼みに\n行かないか？", 1, 5832, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120801, "餅ってのは\n何をつけてもうまいね\nお前さんは\nどの味が好きだい？", 1, 5833, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120801, "お祝いの鯛が\n足りないって？\nそれじゃあアタシが\n釣ってきてやるよ", 1, 5834, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120801, "おせちが美味いのは\n分かるけど\n食べ過ぎには\n気を付けるんだよ", 1, 5835, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120801, "お前さんのおかげで\nいい正月を\n迎えられたよ\nありがとさん！", 1, 5836, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120801, "慣れない晴れ着も\nお前さんに見て\nもらえたなら\n着たかいがあったね", 1, 5837, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 120801, "おはようさん\n寒いのは分かるけど\nいい加減布団から\n出て顔洗ってきな", 1, 5838, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 120801, "今日の昼飯は雑煮だ\nアタシの郷土の味\nしっかり味わって\nおくれよ", 1, 5839, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 120801, "おや、お前さんも\n夜ふかしかい？\n退屈してたところさ\n付き合っておくれよ", 1, 5840, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120801, "晴れ着だと座り方にも\n気を遣うね\nしわになったら大変さ", 1, 5842, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120801, "せっかくだし\nこの子にも晴れ着を\n着せてあげたいね", 1, 5843, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120801, "これは冬の花の香り…\n風情があって\nいいじゃないか", 1, 5844, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120801, "あったかいねぇ…\nこんな寒い日には\nまさに天国だよ", 1, 5845, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 120801, "いつも食べてる\nものでも正月に\n食べると\n新鮮な気がするね", 1, 5847, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 120801, "せっかくの正月休みだ\nたまにはゆっくりと\n本でも読もうかね", 1, 5848, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120701, "新年のあいさつ…\n私も街のみんなに\nしてみようかしら…", 1, 5850, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120701, "正月はゆっくり過ごす\nのよね…ゆっくりって\n何したらいいのかしら", 1, 5851, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120701, "この格好も\n慣れてくれば\n動きやすいわ", 1, 5852, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120701, "う～ん、今年の抱負…\nやりたいことが\nたくさんあるのよね", 1, 5853, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120701, "おせち料理\nネビアの分も\n用意しなくちゃね", 1, 5854, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120701, "ふふっ\n正月っていいわね\nみんな楽しそうだもの", 1, 5855, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120701, "新年の思い出…\nこれからもっと作るわ\nそれが私の抱負よ", 1, 5856, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120701, "今日は家で過ごすの？\nあなたと一緒なら\nきっと楽しいわね", 1, 5857, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120701, "どう？　この格好？\nコッコロさんは\n褒めてくれたけど…", 1, 5858, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120701, "ちょうど良かったわ\nあなたも大掃除\n手伝ってくれる？", 1, 5859, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120701, "これから料理するけど\nお雑煮と焼き餅\nどっちが好きかしら？", 1, 5860, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120701, "『今の私』にとって\n初めての正月だもの\nあなたと楽しまないと", 1, 5861, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120701, "去年よりも今年…\nもっと成長するからね\n…お兄ちゃん♪", 1, 5862, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 120701, "おはよう\nせっかくの正月だもの\n朝から楽しまないとね", 1, 5863, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 120701, "ねぇ…\n良かったら正月の遊び\n教えてくれるかしら？", 1, 5864, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 120701, "ふわぁ～…今日が\n終わっちゃうのが…\nもったいないわね…", 1, 5865, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120701, "ふぅ…お参り後だから\n心地よすぎて眠って\nしまいそうだわ…", 1, 5867, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120701, "ふふっ\nかわいいものって\n癒されるわね…", 1, 5868, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120701, "…ん\nこれが梅の香り？\n…いい匂いね", 1, 5869, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120701, "キャルじゃないけど…\nこのまま丸く\nなりたいところね…", 1, 5870, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 120701, "はむはむっ…♪\nせっかくの正月だもの\n堪能しなくちゃね♪", 1, 5872, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 120701, "…ふんふん\n帯の結び方って\nこんなにあるのね…", 1, 5873, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104201, "今の私が作れる\n最高の一曲…\nあの人に届くといいな", 1, 5875, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104201, "あの、歌を…\n聞いてくれませんか？\nあなたに贈る\n特別な歌を", 1, 5876, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120901, "ひい、ふう…\nむむ、みなに配る\nお年玉のポチ袋が\n残りわずかなのじゃ…", 1, 5877, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120901, "宴の準備はわらわに\n任せておくのじゃ！\n支配者の手腕、\n見せつけてくれる！", 1, 5878, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120901, "新年の願掛けなら\nわらわにすればよい\n支配者に頼むほうが\n確実じゃろう？", 1, 5879, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120901, "みなからもらった\nこの年賀状とやら…\nわらわも\n書いてみようかの♪", 1, 5880, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120901, "正月飾りにもいろいろ\n種類があるんじゃの～\nどれも華々しくて\nわらわ好みじゃ♪", 1, 5881, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120901, "あやつへのお年玉は\nちと多めにしておくか\n…日ごろの感謝も\n込めてのう", 1, 5882, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120901, "あやつのことを想うと\nそれだけで行く年\n来る年が楽しみになる\nふふ、愉快なやつじゃ", 1, 5883, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120901, "みなに晴れ着を\n褒められて、とても\nよい気分なのじゃ♪\n次はお主の番じゃぞ？", 1, 5884, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120901, "お主へのお年玉は\nいちばん最後じゃ！\n残り物には福が宿る\nそうじゃからのう", 1, 5885, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120901, "この季節は手が\nかじかむのう…ほれ、\nお主！　わらわの手を\nあたためるのじゃ！", 1, 5886, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120901, "お主のために雑煮を\n手作りしてみたのじゃ\n食べていくがよい♪", 1, 5887, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120901, "眷属のお主に\n正月休みはまだ早い！\nずっとわらわのそばに\n控えておくのじゃ♪", 1, 5888, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120901, "ふふ、これほど楽しい\n正月は初めてじゃ…\nお主と過ごす時間は\nいつだって特別じゃな", 1, 5889, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 120901, "早起きしてわらわを\n待っておったのか？\nさすがはわらわの眷属\n殊勝な心がけじゃな♪", 1, 5890, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 120901, "食事どきじゃな\n正月の宴会料理が\nわらわたちを\n待っておるぞ♪", 1, 5891, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 120901, "夜も更けてきたのう\n冬の夜は長い…\n心ゆくまでお主と\n過ごせるわけじゃな♪", 1, 5892, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120901, "実に心地がよい♪\nこのまま寝正月も\n悪くないかも\nしれぬのう", 1, 5894, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120901, "愛いやつじゃのう\nわらわのそばに置いて\nやってもよいぞ♪", 1, 5895, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120901, "品のある香りじゃのう\n寝室に置いておけば\nなにかご利益に\nあずかれそうじゃな", 1, 5896, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120901, "心地よい…\nわらわはしばらく\nここから動かぬぞ\nお主も付き合うがよい", 1, 5897, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 120901, "舌鼓が鳴るのう♪\n正月の宴には\n持ってこいの品じゃ！", 1, 5899, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 120901, "ほう、新春にはそんな\n習わしが…正月を\n制するには、まだまだ\n勉強が必要じゃのう", 1, 5900, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121001, "久しぶりの我が家って\n感じです！　やっぱり\nここが一番ですね〜！", 1, 5902, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121001, "さあ食べましょう！\nみんなで囲める食卓を\n心から味わいますよ☆", 1, 5903, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121001, "ランドソルに\n帰ってきた途端に\nお腹が空きました〜！", 1, 5904, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121001, "ただいま☆\n幸せ特盛りの\nわたしの日常！", 1, 5905, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121001, "ようやくランドソルに\n帰ってきたことですし\nやっぱり魔物料理を\nいただきましょう☆", 1, 5906, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121001, "あの人との絆を\n守るために\nもっともっと食べて\n強くならなくちゃ☆", 1, 5907, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121001, "いつもありがとうって\nあの人を思いっきり\n抱きしめたいです☆", 1, 5908, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121001, "あなたの想いは\nこの身をかけて\n全力で受け止めます！", 1, 5909, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121001, "おかえりなさい☆\nここにいるみんなも\nあなたの帰りをずっと\n待っていましたよ！", 1, 5910, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121001, "このギルドハウスで\n冒険の疲れを\n癒しましょう〜！", 1, 5911, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121001, "あなたの想いが\n全力全開を超える\n力をくれました！", 1, 5912, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121001, "日常が続く幸せを\nあなたと一緒に\nかみしめたいです☆", 1, 5913, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121001, "ぎゅ〜っ！\nわたしたちは\nどんなことがあっても\nずっと一緒ですよ？", 1, 5914, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 121001, "おはようございます！\nあなたと過ごせる\n幸せを、今日も\n味わいますよ〜！", 1, 5915, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 121001, "ご飯の時間ですよー！\nあなたも一緒に\n食べましょうね！", 1, 5916, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 121001, "明日もあなたが隣に\nいてくれることを\n心から願ってますよ\nおやすみなさい☆", 1, 5917, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121001, "ふい〜…\n安心できる場所で\nくつろげるって\n幸せですね〜…", 1, 5919, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121001, "じゅるり…\n食べたくなっちゃう\nくらい、かわいい☆", 1, 5920, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121001, "くんくん…おおー！\nステーキの香り！\nやばいですね☆", 1, 5921, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121001, "体がぽかぽかしてきて\nわたしの食欲も\n燃えてきましたよー！", 1, 5922, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 121001, "わたしの食欲に\nついてこられますか？\nまだまだ食べますよ！", 1, 5924, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 121001, "これがキャルちゃんの\n言っていた、キスで\n目覚める王子様の\nおとぎ話ですか〜☆", 1, 5925, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121201, "自分で自分を\n分析するのも、これは\nこれで面白い…♪", 1, 5927, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121201, "強過ぎる力の扱いには\n注意しないと…\n七冠がいい例だね", 1, 5928, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121201, "少年の交友関係は\n計り知れないな…\nアタシも含むのか！", 1, 5929, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121201, "原理を徹底解明して\n再発を防ぐぞー！\n…誰か手伝って～！", 1, 5930, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121201, "これは祝福にも\n呪いにもなる…\n忘れずにいよう", 1, 5931, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121201, "少年が\n目を覚ましてくれて\n本当によかった…", 1, 5932, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121201, "彼以外を選んでいたら\n今回の事態は\n乗り切れなかったな…\nさすがだよ、少年", 1, 5933, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121201, "少年！　調子はどう？\nクレープ食べられる？\nっていうか食べて？", 1, 5934, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121201, "元気そうで何よりだ\nまた店を手伝いに\nきてよ。二人もキミに\n会いたがってたしさ", 1, 5935, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121201, "キミの診察もあるし\n今はなるべくそばに\nいるよ。迷惑じゃ\nなければだけど", 1, 5936, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121201, "やっぱり少年は\nあちこちで人を\n助けてる時が\n一番輝いてるよ", 1, 5937, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121201, "アタシは大したこと\nしてないけど\nみんなのことは\n褒めてあげてね", 1, 5938, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121201, "みんなを笑顔に\nできるのは\nやっぱり少年だね", 1, 5939, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 121201, "やあ少年\n朝寝坊はしてない？\nそれじゃ、よい１日を", 1, 5940, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 121201, "おや、少年！\nまかないのクレープ\n作ったけど、食べる？", 1, 5941, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 121201, "ふわぁ…もう夜か…\n研究してたら\nあっという間だね…\nおやすみ、少年", 1, 5942, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121201, "体がなまってたのか\n前線に出ると疲れる…\nまさか…年のせい！？", 1, 5944, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121201, "キミたちも今の\nアタシと同じ感じに\nアレンジしてあげよう", 1, 5945, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121201, "誰が置いたんだろう？\nいい香りだねぇ…\nうん、気に入った♪", 1, 5946, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121201, "何も考えてない時間も\n実は思考にとって\n大事なんだよね～…", 1, 5947, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 121201, "食の大切さは\nいつの食事からも\n感じられるね♪", 1, 5949, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 121201, "ふむ…面白い！\n今度全巻\n借りにこようっと", 1, 5950, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116401, "聖学祭か…\n良い記憶はないな\nよし忘れよう", 1, 5952, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116401, "健全なる精神は\n健全なる肉体に宿る…\nはっ、知ったことかよ", 1, 5953, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116401, "暇だし鏡でも見るか…\nえへへ、ユニだよ♪\nよし子供料金いけるな", 1, 5954, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116401, "アオイ君は元気に\nしているだろうか…\n彼女は人一倍ぼくに\n懐いていたからな…", 1, 5955, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116401, "そろそろシズル君は\n姉という幻想から\n目覚めただろうか…", 1, 5956, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116401, "ぼくは来年も聖学祭に\n行くさ…卒業しても\n君らとは永遠だ", 1, 5957, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116401, "最近同志の登校率が\n減少傾向だな…\nまあ仕方ないのだが…\nないのだが…", 1, 5958, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 116401, "ほわあああ！\nおい急に話しかけるな\n学びがこぼれる", 1, 5959, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116401, "この体操着、周囲から\n大層評判が良いのだよ\nたいそうだけにな\nどやっ！", 1, 5960, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116401, "何を着ていようと\nぼくはぼくさ\n外見など所詮は\n魂の識別符に過ぎん", 1, 5961, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116401, "救護院のアヤネ君が\n最近ぼくの自称年齢に\n疑念を持ち始めてな…\nあの熊…殺るか…", 1, 5962, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 116401, "うん？　なんだね？\nなに、用は無い？\n用も無いのに呼ぶとは\nもしや…これが友達！", 1, 5963, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 116401, "この服が似合ってる？\nな、なんだね突然…\nやめたまえよでゅふふ", 1, 5964, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 116401, "ふぁ～あ…\n朝も更けてきたな…\nそろそろ寝るか…", 1, 5965, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 116401, "ふぁ～あ…もう昼だ…\n人間の起きている\n時間ではないな…\n早く寝なくては…", 1, 5966, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 116401, "うーん、よく寝た！\n気持ちのいい夜だな\n今日も一日がんばるぞ", 1, 5967, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 116401, "ほわぁ～…\nもしやこれは都で噂の\n人を駄目にするやつ…", 1, 5969, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 116401, "ふむ、愛くるしいな\nだが似合い過ぎる…\nぼくが持つと我ながら\n子供にしか見えん", 1, 5970, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 116401, "アロマか、興味深いな\nミツキ印ポーションも\n気化させて鼻腔から\n吸入してみるか…", 1, 5971, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 116401, "んふ、んふふ…\n火ってイイよね…\nずっと見てられるね…", 1, 5972, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 116401, "や、やめろ…\nとめどなく固形食物を\n生産するのはやめろ…", 1, 5974, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 116401, "この可愛げのない筆致\nやはり作者は理系か！\nぐああ目が理系になる", 1, 5975, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121101, "…あいつのためなら\n無理くらいいくらでも\nしてやるわよ", 1, 5977, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121101, "この姿、\n魔力の制御ムズ過ぎ…\nま、あたしは優秀だし\nどうとでもなるけど！", 1, 5978, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121101, "あいつのこと、\n全部受け入れるって\n決めたから！", 1, 5979, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121101, "今なら誰だろうと\n負ける気がしないわ\nドラゴンでもなんでも\nかかってこいっての！", 1, 5980, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121101, "散々助けられた分、\n今度はあたしが\nあいつのこと助けるわ", 1, 5981, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121101, "あいつとの\nこれまでの冒険は\nどれも、あたしの\n大切な思い出…", 1, 5982, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121101, "もしまたあいつが\n寝たままになったら\nなんだってするわ\nキ…キスでも…", 1, 5983, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121101, "なに、お腹空いたの？\nなら、おいしいご飯を\n食べに行きましょ♪", 1, 5984, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121101, "やっぱ、\nあんたのアホ面見ると\n安心するわ♪", 1, 5985, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121101, "【美食殿】はあんたと\nぺコリーヌがいなきゃ\n始まんないっての", 1, 5986, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121101, "ひゃあっ！？\n急に声かけないで！？\n魔力が暴発しても\n知らないわよ！", 1, 5987, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121101, "…あんたとこうして\nぼーっと過ごす時間、\nまあ…嫌いじゃないわ", 1, 5988, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121101, "あんたが信じて\n見守っててくれれば…\nあたし、\nなんだってできるわ", 1, 5989, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 121101, "おはよ\n早く顔洗いなさいよ、\nこのねぼすけ♪", 1, 5990, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 121101, "今日のお昼は\nどこで食べる？\nあんたが\n決めていいわよ", 1, 5991, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 121101, "もしあんたがまた\n夢に囚われても\nあたしが助けるわ\n安心して寝なさいよ", 1, 5992, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121101, "気持ちいい～…♪\nあいつもたまには\nこうして休ませなきゃ", 1, 5994, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121101, "一目惚れしたかも…！\nこの子、\n部屋に飾りたいわー♪", 1, 5995, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121101, "へぇ、あいつって\nこういう匂いが\n好きなのかしら…？", 1, 5996, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121101, "暖かくて最高…♪\n丸くなっちゃいそう…\nうにゃあ…♪", 1, 5997, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 121101, "イケるわね、これ！\nやっぱり食べるって\n大事よねー♪", 1, 5999, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 121101, "ファーストキスは\nどんな味、ね…\nふ、ふ～ん…？\nなるほど…", 1, 6000, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118101, "お友だち…\nできるかしら…？\nこんなわたしでも…", 1, 6002, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118101, "あまり、きょろきょろ\nしちゃダメよね…\n変な子だと、\n思われてしまうわ…", 1, 6003, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118101, "広い場所、\n落ち着かない…\nはぅ…どこにいれば…", 1, 6004, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118101, "～♪", 1, 6005, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118101, "【レイジ・レギオン】\n…またいつか、\nみんなと…", 1, 6006, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118101, "…一度でいいから、\nわたしもママって…\nあっ、その…\n…な、なんでもないわ", 1, 6007, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118101, "ここが、今のわたしに\nとっての帰る場所…\n…そう思っても、\nいいのかしら…？", 1, 6008, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118101, "ええと…\nお腹、空いてる？\nおねむじゃない？", 1, 6009, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118101, "ここにいると\n邪魔かしら…？\nならもっと隅っこに…", 1, 6010, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118101, "お喋り…？\nわたしでいいの…？\nうれしい…", 1, 6011, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118101, "あなたの好きな歌…\n教えて…？\n練習、するから…", 1, 6012, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118101, "ここは賑やかだけど…\nうるさいとは感じない\nむしろ、心地いい…", 1, 6013, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118101, "安心、して…\nわたしの歌で、\nあなたを守るから…", 1, 6014, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 118101, "ちょっとだけ…\n朝は、苦手…\n…ふぁ…", 1, 6015, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 118101, "お昼は、食べた？\n良かったら、一緒に…\n少し…焦げてるけど…", 1, 6016, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 118101, "眠れないのかしら…\nじゃ、じゃあ…\n子守歌…聴く？", 1, 6017, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118101, "こんな特等席…\n恐縮、です…", 1, 6019, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118101, "い、今のうちに、\nこの子でお世話の\n練習を…", 1, 6020, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118101, "いい香り…\n…つらい気持ちも\n癒してくれる", 1, 6021, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118101, "あったかい…\nいつまでも、\nこうしていられそう…", 1, 6022, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 118101, "おいしく出来てる…\nすごいわ…\n見習わなくっちゃ…", 1, 6024, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 118101, "『赤ちゃんとママの\nスキンシップ入門』…\n…べ、勉強になるわ…", 1, 6025, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121301, "きれいなお部屋…！\n立派な舞台の\nセットみたい…♪", 1, 6027, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121301, "お芝居では私が先輩\nなんだから、しっかり\n私がミサキちゃんを\nエスコートしなきゃ", 1, 6028, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121301, "～～♪\n歌うお芝居も多いから\nもっと上手く\nなりたいな…！", 1, 6029, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121301, "ここにいる人たちと\n仲良くなりたいな…\nそしたらきっと演技の\n幅も広げられるよね", 1, 6030, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121301, "機会があったら\nアヤネちゃんとも\nお芝居してみたいな♪", 1, 6031, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121301, "お兄ちゃんに褒めて\nもらえるように\nお芝居の練習\nいっぱいしなきゃ…♪", 1, 6032, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121301, "あう…お兄ちゃんを\n意識したら、台詞が\n飛んじゃったよぅ…！", 1, 6033, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121301, "あっ、お兄ちゃん…♪\nお芝居の練習を\n見てくれませんか…？", 1, 6034, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121301, "いまの発声練習\nどうでしたか？\nひゃん…！？\n頭は撫でないでぇ～", 1, 6035, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121301, "ミサキちゃんも\nお芝居、とっても\n上達してるんですよ\n私も負けられません♪", 1, 6036, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121301, "また物語を聞かせて\nほしい、ですか…？\n私なんかでよければ\nもちろんよろこんで♪", 1, 6037, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121301, "新しいお話を\n考えてみました\n聞いてくれますか…？\nよかった…♪", 1, 6038, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121301, "いつか…舞台上じゃ\nなくても、照れずに\nお兄ちゃんに気持ちを\n伝えてみせますね…♪", 1, 6039, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 121301, "おはようございます♪\nマヒルさんにもらった\nミルク、一緒に\n飲みませんか？", 1, 6040, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 121301, "牧場のみなさんに\nドングリパンを\nいただきました～\nおひとつどうぞ♪", 1, 6041, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 121301, "寝る前にまたココアを\n飲みながら、お話を\n聞いてもらえますか？\nえへへ、うれしいな♪", 1, 6042, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121301, "お芝居の練習の\nあとは、ここで\nお兄ちゃんと\nゆっくりしたいな♪", 1, 6044, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121301, "今日も演技の\n練習に付き合ってね♪", 1, 6045, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121301, "素敵な香り…\nこのイメージも\nお芝居に\n活かせないかな…？", 1, 6046, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121301, "私もお芝居で\nみんなの心を\nぽかぽか温められる\nようになりたいな…♪", 1, 6047, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 121301, "舞台に立つんだから\n食べ過ぎないように\n気をつけないと。\nでも…おいしいなぁ♪", 1, 6049, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 121301, "このお話、面白い…\n脚本を書く\n参考にもなりそう…♪", 1, 6050, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 107501, "えへへ…♪\n二人で見た夕日…\nキレイでしたね…", 1, 6052, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 107501, "これから泳ぎの\n練習ですか？\n付き合いますよ♪", 1, 6053, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 107601, "主さまを導くことで\nわたくしも未知なる\n領域へと辿り着けます", 1, 6054, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 107601, "主さまとなら\n海よりも深く\n空よりも高い場所へも\n目指せそうです", 1, 6055, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 107801, "あいつと二人で\n夏の遠い海に…って\nあたしってば\nなに考えてるのよ！", 1, 6056, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 107801, "あたしが楽しく\n過ごせるのはあんたが\nいてくれたからよ\nか、感謝してるわ", 1, 6057, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121401, "はぁ…アタシってば\nこんなにかわいくって\nキレイなんて罪よね～", 1, 6058, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121401, "舞台の衣装を\nクリーニング\nしとかなくちゃ…", 1, 6059, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121401, "舞台映えするメイク…\nスズナなら\n知ってるかしら？", 1, 6060, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121401, "物産展の会計係と\n山盛りの算数の宿題…\nどっちが簡単かしら…", 1, 6061, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121401, "牧場と温泉の仕事に\nお芝居の練習に…\nちょ、ちょっと休憩～", 1, 6062, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121401, "お客さんの視線も\nうれしいけど…\nあいつも見てて\nくれてるかしら？", 1, 6063, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121401, "もっともっと\nセクシーになって\nあいつをビックリ\nさせてやるわ♪", 1, 6064, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121401, "アンタも休憩？\nしっかり働くために\n休むのも大事よね", 1, 6065, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121401, "ふふん♪　アタシも\n料理ができるように\nなったんだから！\n簡単なのだけだけど…", 1, 6066, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121401, "温泉ってお肌に\nいいのよね？\nもっとキレイに\nなったらどうしよう〜", 1, 6067, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121401, "物産展の荷物は\nこれでよし…っと\nそれじゃアンタも\n運ぶの手伝ってよね♪", 1, 6068, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121401, "いつもと違うアタシに\n見とれちゃった？\n仕方ないわね～♪", 1, 6069, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121401, "お花見って風流で\n大人って感じ♪\n…でもお団子も\n忘れないでよね！", 1, 6070, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 121401, "みんなに朝ごはんを\n作ってみたら\n喜んでくれるかしら？", 1, 6071, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 121401, "お客さんがいっぱい\n来る時間帯ね！\n元気出していくわ！", 1, 6072, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 121401, "そろそろ寝ないと…\n睡眠不足はお肌の\n大敵なんだから", 1, 6073, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121401, "ふかふかしてて\n気持ちいい～♪\n疲れが癒されるぅ…", 1, 6075, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121401, "この子もアタシと\nおそろいの衣装に\nしてあげようかしら♪", 1, 6076, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121401, "素敵な衣装で\nいい香りを\n身にまとう…ふふ、\n大人のたしなみよね♪", 1, 6077, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121401, "あったか～い♪\nここでお昼寝したら\n気持ちよさそう…", 1, 6078, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 121401, "お仕事がんばると\nいつもよりお腹\n空いちゃう～！", 1, 6080, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 121401, "最新のトレンド\n載ってないかしら？\n物産展の売り物に\n取り入れなくちゃ！", 1, 6081, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121501, "怪盗のこと…\nお父さんお母さんに\nもし知られたら\nなんて説明しよう…", 1, 6083, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121501, "たとえ怪盗でも、人の\n家にお邪魔するときは\n身だしなみをしっかり\nしないと…", 1, 6084, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121501, "鍵開けって難しい…\nうーん、もっと\n練習しておかなくちゃ", 1, 6085, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121501, "姿が見えない\n怪盗の噂…？\nわ、私のことじゃ\nありませんように…！", 1, 6086, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121501, "予告状って…\n切手はどこに貼れば\nいいんだろう…？", 1, 6087, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121501, "怪盗活動にも\n慣れてきたけど…\nやっぱり先輩が\nいないと心細いなぁ…", 1, 6088, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121501, "せ、先輩の心も\nいつか盗めるかな…？\n…わわわ、私ってば\nなに言ってるんだろ…", 1, 6089, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121501, "怪盗が大活躍って\n新聞で読んだ…？\nだっ、誰のこと\nでしょうね…あはは", 1, 6090, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121501, "尾行に潜入…\n存在感がないのって\n意外と役立つことも\nあるんですね", 1, 6091, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121501, "せせせ、先輩っ！\n帽子をぎゅーって\nかぶったら、抜けなく\nなっちゃいました…！", 1, 6092, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121501, "ぬ、盗み聞きなんて\nしてませんよ？\nみんなが私に気づかず\n話し始めるだけで…", 1, 6093, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121501, "秘密道具が見たい？\nそ、それは内緒です！\n秘密道具ですから…\nで、でも先輩になら…", 1, 6094, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121501, "えへへ\n見つかっちゃいました\n先輩はいつも私を\n見つけてくれますね…", 1, 6095, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 121501, "おはようございまふ…\n秘密道具のお手入れで\n夜更かししちゃって…\nちょっと眠たいです～", 1, 6096, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 121501, "怪盗が昼に活動する…\n案外そのほうが\n意外性が\nあるのかも…？", 1, 6097, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 121501, "外が暗くなりましたね\n…そろそろ怪盗の活動\n始めましょうか？", 1, 6098, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121501, "ここでくつろぐと\n疲れが取れます～\n…今日も怪盗\nがんばろっと", 1, 6100, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121501, "わぁ、かわいい…！\n心を盗まれるって\nこういうことかな…？", 1, 6101, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121501, "素敵な香り…でも\n服に香りがついたら\n隠れてるときに\n見つかっちゃうかな…", 1, 6102, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121501, "ぽかぽかで\nいい気持ち…\n夜道を歩く前に\n温まっておこうっと…", 1, 6103, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 121501, "怪盗活動に備えて\n今のうちにしっかり\n食べておかなきゃ…！", 1, 6105, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 121501, "女の子は秘密が\n多いほうが魅力的…？\nそ、そうなんだ…", 1, 6106, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104501, "ドＳさんはいつも\nクウカの妄想の中に…\nぐふふ、じゅるり～♪", 1, 6108, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104501, "やさしい触り方も\n嫌いじゃありません…\nは…恥ずかしいですが", 1, 6109, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121601, "この大怪盗さまに\n盗めないものなんて\nないです！　たぶん！", 1, 6110, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121601, "あたしにふさわしい\nビッグなお宝は\nどこにあるですかね～", 1, 6111, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121601, "そろーりそろーり…\n気配を消せばお菓子も\n食べ放題…にやり", 1, 6112, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121601, "またサボってたのが\nバレたです…\nボスの地獄耳～！", 1, 6113, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121601, "ギャングで大怪盗…！\nこの肩書の多さ！\nまさに有能の証です！", 1, 6114, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121601, "お兄さんだったら…\nお宝を山分けして\nあげてもいいですよ", 1, 6115, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121601, "お兄さんはアレですよ\n大怪盗イノリさまの\n相棒みたいなものです", 1, 6116, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121601, "何事も用心深さが\n大切ですよ。ビ…\nビビりじゃないです！", 1, 6117, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121601, "ぬ、盗み聞き？\nしてないですよっ？\nぴゅーぴゅーぴゅー", 1, 6118, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121601, "情報が欲しければ\nお菓子と交換ですよ\nさぁ、交換交換っ♪", 1, 6119, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121601, "しーっ！\n今、尾行中なんです\n話はあとで…", 1, 6120, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121601, "お兄さんと話してると\n時間が速いような…？\nこの時間泥棒！", 1, 6121, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121601, "ちょうどよかった！\nいい話があるんです\n一口乗りませんか？", 1, 6122, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 121601, "おてんとうさまが\n出てやがるですね…\n悪事がやりにくいです", 1, 6123, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 121601, "もうこんな時間ですか\nご飯にお昼寝…\nいやー忙しいです♪", 1, 6124, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 121601, "ふっふっふ！　夜こそ\n大怪盗の時間です！\n……ふぁあ", 1, 6125, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121601, "うーん…\n警備の目がないと\n気楽でいいですねぇ", 1, 6127, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121601, "くう…！\nこのかわいさは\n盗めないです…♪", 1, 6128, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121601, "犯行現場に残ったのは\n香水の匂いだけ…\nふふ、おしゃれ大怪盗", 1, 6129, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121601, "意外とこういう場所に\n隠し扉があったり…\nしないです？", 1, 6130, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 121601, "もぐもぐ…\nこの美味しさ…\nとんでもない罠ですね", 1, 6132, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 121601, "むむむ…ちっとも\n分からないです…\nほとんど暗号ですよ", 1, 6133, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121701, "あら、サレンさんは\nどこに行かれた\nのかしら？", 1, 6135, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121701, "ふんふんふーん♪\nなんだか華やいだ\n気分ですわ♪", 1, 6136, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121701, "さて…次の事業は\nどうしましょう\n何か面白いことは…", 1, 6137, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121701, "おーっほっほっほ！\nあら…楽しくて、つい\n笑ってしまいましたわ", 1, 6138, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121701, "センスがあって\nいいお部屋ですわね\n私も見習いたいですわ", 1, 6139, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121701, "サレンさんとあのお方\n仲がいいですわね\nも、もちろん仲がいい\nのはよいことですわ！", 1, 6140, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121701, "どんなときでも\n正々堂々！\nそれが私の\n誇りですわ！", 1, 6141, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121701, "ふふ、ごきげんよう\n私、絶好調でしてよ", 1, 6142, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121701, "どうかされましたか？\n何かお困りごと\nでしたら\n是非力になりますわよ", 1, 6143, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121701, "たまにはこうして\nゆっくりと\nお話しするのも\nいいものですわね", 1, 6144, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121701, "次の事業に関して\n考え事をしてましたの\nよければ相談に乗って\nくださるかしら？", 1, 6145, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121701, "よろしければ\nお話しませんこと？\nサレンさんのお話を\n聞かせてほしいですわ", 1, 6146, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121701, "ふふっ…\n先ほどサレンさんと\nあなたさまのお話を\nしていたところですわ", 1, 6147, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 121701, "さあ！\n今日も一日\n全力で参りますわ！", 1, 6148, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 121701, "お昼はどうしましょう\nサレンさんをお誘い\nしてみようかしら", 1, 6149, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 121701, "もう少しだけ…\n明日の準備をして\nおかないと…", 1, 6150, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121701, "はぁ～…いいですわね\nはっ、私としたことが\nはしたないですわね", 1, 6152, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121701, "かわいらしいですわ\nいくつになっても\nぎゅっと\nしたくなりますわね", 1, 6153, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121701, "落ち着きますわね\nサレンさんはどんな\n香りが好きなのかしら", 1, 6154, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121701, "ふふっ、燃え盛る\n情熱の炎…\nいいですわね", 1, 6155, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 121701, "このおいしさで\n元手がゼロ…\nすばらしい\n商品ですわ！", 1, 6157, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 121701, "これは…ふむふむ\n新しい事業のヒントに\nなりそうですわ", 1, 6158, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121801, "さて、と…\n今日は何をしなくちゃ\nいけないんだっけ？", 1, 6161, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121801, "やることは山積み\nだけど、手を動かす\nのは嫌いじゃないわ♪", 1, 6162, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121801, "あら？　アキノさんの\n声が聞こえたような\n気がしたんだけど…", 1, 6163, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121801, "アキノさんといると\nついこっちまで笑顔に\nなっちゃうのよね", 1, 6164, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121801, "弱音なんか\nはいてられないわ\nアキノさんに\n笑われちゃうもの", 1, 6165, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121801, "気が付けばアキノさん\nのペースだけど…\n不思議と嫌では\nないのよね", 1, 6166, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121801, "あたしを好敵手って\n言ってくれる\nアキノさんのためにも\nしっかりしなくちゃ", 1, 6167, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121801, "どうしたの？\nアキノさんなら\nさっきそこに\nいなかった？", 1, 6168, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121801, "あたしなら大丈夫\n救護院だって\nみんなが助けて\nくれるからね", 1, 6169, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121801, "さっきアキノさんが\nあんたを探してたわよ\nなにかしたの？", 1, 6170, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121801, "こう見えてヒマじゃ\nないんだけど…\n少しくらいなら\n付き合ってあげるわ", 1, 6171, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121801, "ねえ、あんたと\nアキノさんって…\nううん、やっぱり\nなんでもないわ…", 1, 6172, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121801, "アキノさんも誘って\nお茶でもしない？\n話したいことが\nたくさんあるの♪", 1, 6173, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 121801, "早起きは三文の得よ\nいい一日を迎えるため\n目を覚まさなきゃ", 1, 6174, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 121801, "今日のお昼は…\nアキノさんと同席する\n予定だったわね\nあんたもどう？", 1, 6175, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 121801, "ふぁ…っと、だめね\nたるんだところを\nアキノさんに\n見せるわけには", 1, 6176, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121801, "ん～…\n落ち着くわね\nこのまま\n寝ちゃいそう…", 1, 6178, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121801, "救護院の皆も\nぬいぐるみが\n大好きなのよ", 1, 6179, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121801, "いい香りね…\nこれはアキノさんの\n趣味かしら？", 1, 6180, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121801, "こんなに激しく\n燃えているのに\nあったかくて落ち着く\nまるでアキノさんね", 1, 6181, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 121801, "どんどん出てくる…\nこれ、救護院にも\nほしいわね…", 1, 6183, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 121801, "ふむふむ…\n前にアキノさんが\n言ってたのは\nこのことかしら？", 1, 6184, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122001, "この衣装、私には\nちょっとかわい過ぎて\n恥ずかしいな…", 1, 6187, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122001, "一部の人からは\n海賊より海賊らしいと\n評判の父です", 1, 6188, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122001, "潮風で錆びないよう\n武器の手入れを\nしておきましょうか", 1, 6189, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122001, "宝探しのお手伝いなら\n力になれると思います\n父のやる気次第ですが", 1, 6190, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122001, "除霊は気を遣います\n一歩間違えると父まで\n成仏させてしまうので", 1, 6191, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122001, "あの人と見た\n海の景色を…ずっと\n忘れずにいたいな", 1, 6192, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122001, "幽霊になっても\n一目会えるなら…\n私はきっとあの人に…", 1, 6193, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122001, "ボートを漕ぐの、少し\n上手になったんですよ\n今度お見せしますね", 1, 6194, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122001, "航海に向けた\n買い出しが必要ですね\n予定は空いていますか", 1, 6195, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122001, "呪われた財宝の\n解呪依頼が多くて…\nふぅ…えっと次は…", 1, 6196, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122001, "夜の航海では\n父が明かり代わりに\nなってくれるんです", 1, 6197, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122001, "船の上では食材が\n限られるので料理の\nいい練習になります", 1, 6198, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122001, "この先もずっと\nあなたと一緒に\nたくさんの思い出を\n作っていきたいです", 1, 6199, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 122001, "あの人の寝顔…\nもう少しだけ\n眺めていたいな…", 1, 6200, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 122001, "船の上では\n規則正しい生活が\n重要ですから", 1, 6201, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 122001, "夜の海は幽霊が\n見分けやすいんです\n青白く光るので", 1, 6202, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122001, "海に揺られるような\n心地よさですね\n気が休まります…", 1, 6204, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122001, "あなたも一緒に\n海を見に行きますか？\n…なんて。ふふっ", 1, 6205, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122001, "爽やかな香りですね…\n一面の青い海が\n目に浮かぶようです", 1, 6206, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122001, "温かい…\n潮風で冷えた体に\n沁みますね", 1, 6207, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 122001, "あ、おいしい…\n日持ちするなら船旅に\n持っていこうかな…", 1, 6209, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 122001, "アンナさんに教えて\nもらった小説は…\nあ、これですね", 1, 6210, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102601, "なにもしないを\nしてるんだよ\nあ～いそがしいそがし", 1, 6212, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102601, "はぁ～あんたといると\nどこまでも気持ちよく\nダラダラできるね～", 1, 6213, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121901, "暗黒騎士にして、\n伝説の海賊でもある\n私に不可能はないっ！", 1, 6214, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121901, "海賊団の船員、\n随時募集中です…と\nこれで新たな盟友が\n増えるはずだ…！", 1, 6215, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121901, "肩に乗せるペットが\nほしいな。今のうちに\nペット用品を買いに\n行かなくては…！", 1, 6216, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121901, "財宝の地図はどこで\n売っているのだろう…\n古本屋？　古物商？", 1, 6217, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121901, "この海賊衣装に\n身を包んでいると\n前世の新たな記憶が\nよみがえってくるな！", 1, 6218, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121901, "いずれはシグルドと\n海原への冒険に\n繰り出したいな…\nきっと楽しいぞ！", 1, 6219, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121901, "待ちわびたぞ盟友よ！\n私と貴様あっての\n海賊団…だからその…\n今日はそばにいてくれ", 1, 6220, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 121901, "シグルド！　港町で\nお魚のおすそ分けを\nもらったんだ！\n一緒に食べよう！", 1, 6221, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121901, "シグルドもこのコート\n羽織ってみないか？\nふふっ、きっとすごく\n似合うと思うぞ！", 1, 6222, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121901, "海賊船の新たな\n秘密兵器を作ったぞ！\nシグルド、これから\n見に来ないか！？", 1, 6223, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121901, "私は伝説の海賊だから\n泳ぐのだって得意だ！\nふ、深いところは\n少し怖いが…", 1, 6224, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 121901, "ふふ、いつ無人島に\n漂着してもいいように\n非常食は準備済みだ\nシグルドの分もある！", 1, 6225, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 121901, "冥風戦記の新章が\n完成したぞ…！\nシグルド、まずは\n貴様に読んでほしい！", 1, 6226, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 121901, "ふっ、天気明朗\n絶好の航海日和だ！\n早速海に繰り出そう！", 1, 6227, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 121901, "昼食用に魚を\n釣っておいたぞ！\nふふ、いかにも\n海賊っぽいだろう？", 1, 6228, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 121901, "夜の海は真っ黒で\n少しだけ怖いな…\nシグルド、私の\nそばを離れるなよ…！", 1, 6229, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 121901, "大海賊がくつろぐのに\n相応しい逸品だ…！\nえっと…これ、どこの\nお店で買えますか？", 1, 6231, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 121901, "わわ、かわいい…！\n船室のベッドに\n置いておきたいな…\n連れてってはダメか？", 1, 6232, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 121901, "おぉ、この香りは…！\n集中力が上がりそうだ\n海賊船の船長室に\nぜひ置きたいな！", 1, 6233, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 121901, "これが船乗りの\n伝承に聞く、海原を\nさまよう神秘の火か！\n…暖まるな！", 1, 6234, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 121901, "腹が減っては\n海賊はできぬ！\n…小説も書けぬ！", 1, 6236, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 121901, "女海賊と騎士はその後\n恋に落ち…？　恋愛\nモノではないかっ！？\nき、興味深くはあるが", 1, 6237, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122101, "いつもの山籠もりが\nブームになるなんて…\n人生、なにが起きるか\n分からないなぁ", 1, 6239, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122101, "キャンプ道具…\nいえ、キャンプギア！\nカッコいい響き…♪", 1, 6240, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122101, "レッツ、ＢＢキャン！\nこのフレーズ\n流行るかも…？", 1, 6241, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122101, "キャンプ仲間…\n募集してみようかな？\n友達も兼任で…", 1, 6242, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122101, "ユカリさんと\nジュンさん…元気かな\nまた一緒に\nキャンプしたいなぁ…", 1, 6243, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122101, "私の取材が載った本…\n【フォレスティエ】に\n送らなくちゃ…♪", 1, 6244, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122101, "もし、あの人が\n落ち込むことが\nあったら…その時は\n私が力になりたいな", 1, 6245, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122101, "ブッシュクラフトは\nマイフレンドくんの\nカスタムにも\n活かせる技術です！", 1, 6246, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122101, "会話に花が咲く…\nって言いますよね\n私の場合、会話相手が\nお花そのものですけど", 1, 6247, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122101, "この服、実は…\n昔ミサト先生に\nこしらえてもらった\n物なんです！", 1, 6248, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122101, "昔はやまびこで会話の\n練習をしてたんです\nほとんどオウム返しで\n気まずかったなぁ～", 1, 6249, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122101, "どうもマイスターです\n…あ、すみません\nみなさんの期待に\n応えようと練習中で…", 1, 6250, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122101, "たとえぼっちを\n卒業しても…\n私は一生ＢＢ団です！", 1, 6251, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 122101, "小鳥さんのさえずりで\n目が覚める…\n心地いいものですね", 1, 6252, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 122101, "キャンプといえば\nおいしいご飯です！\n腕を振るいますよ！", 1, 6253, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 122101, "すっかり夜ですね…\nおやすみの前に\n星でも見ませんか？", 1, 6254, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122101, "都会でくつろぎ…\n自然の中でくつろぐ…\nくつろぎ無限ループ…", 1, 6256, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122101, "しっかり観察すれば\nブッシュクラフトで\n再現できるかも…？", 1, 6257, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122101, "はぁ～…やっぱり\n木の香りを嗅ぐと\n肩の力が抜けますねぇ", 1, 6258, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122101, "ああ…あったまります\nなんだか心に\nゆとりがでますね…", 1, 6259, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 122101, "このおいしさ…！\n今度はキャンプに\n持っていこうかな…", 1, 6261, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 122101, "ふむふむ…\nいろんな知識があると\nキャンプはもっと\n楽しくなりますよね", 1, 6262, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122201, "部屋がキャンプ用品で\n埋まっていく…\nキャンプ…なんて\n恐ろしい趣味なの…？", 1, 6264, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122201, "仕事でも\nアウトドアでも\n頼れるお姉さんに\nなれたらいいなぁ…", 1, 6265, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122201, "この服、かわいい上に\n本当に快適ね～♪\nおしゃれにキャンプを\n楽しめて最高だわ！", 1, 6266, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122201, "ん〜次はどんな\nキャンプ飯を\n作ろうかしら？\n彼の好物は、っと…", 1, 6267, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122201, "早く週末が\n来ないかしら…\n仕事を忘れてパーっと\nキャンプ飲みを…！", 1, 6268, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122201, "新しいキャンプ飯の\nレシピを覚えたわ！\n彼も喜んで\nくれるはず…よね♪", 1, 6269, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122201, "彼の分の食器も\nこっそり買い揃えちゃ\nおうかな…そ、それは\nさすがにまだ早いか…", 1, 6270, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122201, "今度、財団主催で\n大規模なキャンプを\nやってみるのも\nいいかもしれないわね", 1, 6271, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122201, "ソロキャンプも\n悪くはないけど\n私はやっぱりキミと…\nな、なんでもない！", 1, 6272, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122201, "キャンプのことなら\n私に聞いて。なんでも\n教えてあげるから！\n…調べた上でね！", 1, 6273, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122201, "キャンプ場で飲む\n最高の一杯は\n他じゃ味わえないわ！\nさあ、乾杯っ！", 1, 6274, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122201, "うん、決めた！　次は\n禁しゅわキャンプ！\nだってキミとゆっくり\n話したいんだもん♪", 1, 6275, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122201, "キミのおかげで\n本当の最高の一杯が\n味わえたわ…\nありがとう♪", 1, 6276, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 122201, "んー、気持ちいい！\nキャンプが趣味に\nなってから、寝覚めが\nよくなったわ♪", 1, 6277, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 122201, "お昼は私がごちそう\nするわ。キャンプ飯で\n鍛えた料理の腕前を\n披露しちゃうわよ～！", 1, 6278, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 122201, "そろそろ寝ましょ？\n明日も朝日を眺める\nためにね。もちろん\nコーヒー付きで♪", 1, 6279, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122201, "くつろぐのに\n持ってこいね～\nテントの中に置いても\nよさそうだわ！", 1, 6281, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122201, "ふふ、かわいいわね～\n…今度キャンプ場に\n連れていこうかしら？", 1, 6282, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122201, "うん、いい匂い…♪\nキャンプ場を思い出す\n自然の香りだわ〜", 1, 6283, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122201, "はぁ、温まるぅ～\n冬のキャンプで\n使ってみたいわね", 1, 6284, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 122201, "おいしい～♪\nキャンプ飲みの\nおつまみに\nぴったりね！", 1, 6286, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 122201, "へえ…こんなキャンプ\n飯もあるんだ？\n彼、好きだったり\nするかな…", 1, 6287, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100701, "一生ついててやるの\n幽霊の一生は\nなっが～～～いの！", 1, 6289, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100701, "む～…あんまり触ると\nすり抜けてやるの！\nウソなの。むふふ♪", 1, 6290, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122301, "んー…\n今日はベスちゃんと\nなにして遊ぼーかなー", 1, 6291, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122301, "ヴァンピィちゃんも\nけんぞくの友だちと\n仲良くなりたいな♪", 1, 6292, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122301, "けんぞくのお部屋は\nヴァンピィちゃんが\n守ってあげなくちゃ！", 1, 6293, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122301, "お掃除するなら\nヴァンピィちゃんも\n手伝ってあげよー♪", 1, 6294, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122301, "ふーん、ふーん♪\nけんぞくってこーゆー\nお部屋が好きなんだー", 1, 6295, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122301, "ひまだなー\nけんぞくをさそって\nお散歩いこっかな♪", 1, 6296, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122301, "今日のスープに\nけんぞくの大好物を\n入れてあげよっと♪", 1, 6297, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122301, "けんぞくー！\nヴァンピィちゃんと\nお出かけしよ♪", 1, 6298, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122301, "よしよし！\n今日のけんぞくも\nいい子だね♪", 1, 6299, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122301, "んんー…\nやっぱりけんぞくと\nベスちゃんは似てる？", 1, 6300, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122301, "いーい？\nヴァンピィちゃんの\nいうこと聞くのよ？", 1, 6301, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122301, "ヴァンピィちゃんの\nけんぞくだもん！\n大切にするからね！", 1, 6302, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122301, "えへへっ！\nやっぱりけんぞくって\nやさしー匂いがする♪", 1, 6303, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 122301, "おはよー…\nヴァンピィちゃんは\nもうちょっと眠るねー", 1, 6304, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 122301, "ヴァンピィちゃん\nひなたぼっこ\nだーいすき！", 1, 6305, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 122301, "んー！　夜だ♪\nヴァンピィちゃん\n絶好調だよ！", 1, 6306, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122301, "わー！\nふかふかだ！\nきもちぃー♪", 1, 6308, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122301, "かわいー♪\nベスちゃんに負けない\n抱き心地！", 1, 6309, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122301, "この香り…なんだろ？\nヴァンピィちゃん\n気に入っちゃった♪", 1, 6310, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122301, "あったかいねー…\nヴァンピィちゃん\nずっとここにいるー…", 1, 6311, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 122301, "はむはむ…\nんー！\nおいしー♪", 1, 6313, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 122301, "ヴァンピィちゃん\nこの本\n好きなんだよねー♪", 1, 6314, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122501, "夏といえば\nかき氷だよね\n近くにお店は…", 1, 6316, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122501, "この格好でいるのも\nなじんできたし\nちょっと鍛錬でも…", 1, 6317, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122501, "動きやすいけれど…\nいつもと違う服装は\n少し落ち着かないな", 1, 6318, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122501, "夏の空気に\n当てられると思わず\n気が緩んでしまうね", 1, 6319, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122501, "砂浜で走ると\nいい具合に体幹を\n鍛えられそうだ", 1, 6320, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122501, "終わらない夏は\nないけれど…\nつい願ってしまうな", 1, 6321, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122501, "胸を張って彼と\n並び立てるよう\n私も、もっと強く…！", 1, 6322, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122501, "どうしたの？\n行きたい場所が\nあるなら付き合おうか", 1, 6323, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122501, "快晴で風は穏やか…\n絶好の釣り日和だね\nさあ、出かけようか", 1, 6324, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122501, "今日の予定を\n立ててるんだけど…\nキミの希望も\n聞きたいな", 1, 6325, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122501, "ビーチスポーツか…\nキミとなら\nいい勝負ができそうだ", 1, 6326, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122501, "私たちの夏に\n遠慮は無用だよ\n思いきり楽しもう！", 1, 6327, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122501, "いつかまた一緒に\n花火を見に行こう\n…約束だよ？", 1, 6328, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 122501, "潮騒の音で\n目覚めるのも\nいいものだね", 1, 6329, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 122501, "今朝釣ってきた魚が\nあるんだ。これで\nお昼ご飯にしようか", 1, 6330, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 122501, "夜にもイベントが\nあるみたいなんだ\n一緒に見に行かない？", 1, 6331, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122501, "気持ちが安らぐね…\nうっかり眠って…\nしまい…そうだ…", 1, 6333, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122501, "肌触りのよさが\nこの格好だと\nよく分かるよ", 1, 6334, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122501, "海をイメージさせる\n香りだね…\n爽やかで落ち着くよ", 1, 6335, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122501, "濡れたままだと\n風邪を引いてしまうよ\nほら、キミもおいで", 1, 6336, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 122501, "夏を楽しみきるには\nエネルギーが\n必要だからね", 1, 6338, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 122501, "大切な人と過ごせる\n観光スポット紹介…\n…こ、このページは\nもういいかな！", 1, 6339, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 122501, "濡らしてもいいなら\n海に持っていって\n乗るのもよさそうだ", 1, 6341, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 122501, "こういう名画を\n見た気がするな…\nこんな視点なんだね", 1, 6342, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122401, "水着って\n動きやすくて\nいいよねー♪\nいっちにーさんしー♪", 1, 6343, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122401, "ユイちゃんにもらった\n日焼け止め\n塗っておいて\nよかった～", 1, 6344, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122401, "なんだか暑いなあ…\nせっかくだし\nプールで泳いで\nこようかな？", 1, 6345, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122401, "この格好で\n出かけようとしたら\nレイさんに\n怒られちゃった…", 1, 6346, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122401, "たとえ水着姿でも\n困ってる人を見たら\n助けに行くからね！", 1, 6347, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122401, "バカンス楽しかった～\nまた騎士くんと\n一緒に行きたいな♪", 1, 6348, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122401, "この水着、あたしに\n似合ってるかなあ？\n騎士くんはどう\n思ってるんだろ…", 1, 6349, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122401, "騎士くん！\n海に行くなら\n準備運動\n忘れずにね！", 1, 6350, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122401, "泳ぐ練習がしたいの？\nいいよ！　水着だから\nなにかあったらすぐに\n助けてあげるね！", 1, 6351, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122401, "騎士くんの水着姿も\nかっこよかったよ！\nまた遊びに行こ！\n約束ね！", 1, 6352, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122401, "このグローブの向日葵\nかわいいでしょ？\nユイちゃんが\n選んでくれたんだ♪", 1, 6353, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122401, "実はこの水着\nいつも着てる\n服とおなじなんだ…\nって、冗談だよ？", 1, 6354, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122401, "変な日焼けしてないか\n見てもらってもいい？\n…なし！　今のなし！\nやっぱりなしー！", 1, 6355, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 122401, "夏のお日様に\n負けないよう\n今日も元気いっぱい\nがんばるぞー！", 1, 6356, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 122401, "お腹減ってきちゃった\n騎士くんはお腹\n空いてない？\n…聞こえちゃった♪", 1, 6357, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 122401, "今日も一日\n楽しかったー！\n明日もまた\n楽しもうね！", 1, 6358, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122401, "ふわあ…\nふわふわなのが\nいつもより\nよくわかるよー…", 1, 6360, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122401, "そうだ！　水着なら\n濡れてもいいし\n今度洗ってあげるね♪", 1, 6361, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122401, "んー…いい匂い♪\nなんだか\n身体がぽかぽか\nしてきちゃった！", 1, 6362, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122401, "水着だから、えっと…\n暖か涼しい…！\n不思議な感じー", 1, 6363, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 122401, "思いっきり身体を\n動かした後だから\nいくらでも\n食べちゃえるよー！", 1, 6365, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 122401, "ふむふむ…\nなるほどー\n水着の種類って\nそんなにあるんだぁ…", 1, 6366, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 122401, "乗り心地バッチリだね\nしゅっぱつしんこー！\nなんちゃって♪", 1, 6368, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 122401, "わっ、ぽよんぽよん！\nトランポリンみたいで\nた～のし～！", 1, 6369, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101501, "彼のことを考えてると\n幸せで胸が温かくなる\n…私ったら、もう", 1, 6370, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101501, "うふふふふ\nくすぐったいわ～♪\nもう、イタズラっ子ね\n次は先生の番よ～♪", 1, 6371, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122601, "騎士クンの汗\n拭いてあげたいな…\n余計なお世話かな…？", 1, 6372, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122601, "夏バテ気味かも…\nそうだ、今夜は\nかば焼きにしよう♪", 1, 6373, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122601, "青い空、白い雲\n眩しい日差し…！\n夏って好きだな…", 1, 6374, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122601, "ヒリヒリする…？\n日に焼けちゃったかな\nふー…ふー…", 1, 6375, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122601, "花火大会があるんだ…\n夜、浜辺…2人きり…\nむ、無理だよ〜！", 1, 6376, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122601, "水着って不思議…\n引っ込み思案が少し\nおさまる…気がする", 1, 6377, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122601, "騎士クンとまた\n海に行きたいな…\nまた、一緒に…", 1, 6378, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122601, "ダ、ダイエット！？\nしてないよ！？\n元々これ位だよ…？", 1, 6379, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122601, "スイカ割りのコツ？\nわたしは、騎士クンに\n教わりたいな…", 1, 6380, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122601, "騎士クン。うちわで\n扇いであげるね\nどこに置いたかな…", 1, 6381, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122601, "うぅ…暑いね…\n騎士クン、温度計は\n見ない方がいいよ～", 1, 6382, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122601, "じ、じっと見ないで…\nええっと…ちょっと！\nちょっとならいいよ…", 1, 6383, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122601, "え、肝試し？\nう〜ん、うう〜ん…\n…騎士クンとなら！", 1, 6384, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 122601, "おはよう騎士クン\n見て、あっちの空\nもう入道雲が出てる", 1, 6385, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 122601, "夕立、来るかなぁ…\nもし雷が鳴ったら\nそばにいてくれる…？", 1, 6386, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 122601, "しー。…ほら、虫の声\n…わ、わたしの心臓の\n音は気にしないで…！", 1, 6387, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122601, "ふふ、水着でゴロゴロ\nするのってなんだか…\nいけないことみたい", 1, 6389, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122601, "かわいい～♪\n日光浴させて\nあげたいな♪", 1, 6390, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122601, "ほんのり潮の香りが…\n香ばしくて塩辛くて…\nあ…おなか鳴っちゃう", 1, 6391, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122601, "水着、乾かせるかも…\nそうだ、みんなの分も\n持ってこよう！", 1, 6392, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 122601, "夏バテなんてしてたら\nみんなの足を\n引っ張っちゃうよね\nいただきます！", 1, 6394, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 122601, "騎士クンの、どうして\n海はしょっぱいの？\nって質問の答えは…\n分かんないよぉ～", 1, 6395, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 122601, "顔は怒ってるのに\nなんだかかわいい…\n誰かに似てる…？", 1, 6397, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 122601, "これを枕に、ビーチで\n騎士クンと…なんて…\nなんて…なんて…！", 1, 6398, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122901, "夏休み、\n毎日たのしいな～♪\nつぎはなにしよう？", 1, 6399, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122901, "自由研究、\nなににしよっかな～？\nプチグリフォンさんの\nかんさつにっきとか？", 1, 6400, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122901, "夏休みって、\n給食ないから…\nそれはちょっぴり\nさみしいかも…？", 1, 6401, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122901, "砂浜をはだしで歩くの\nきもちいいよね♪\nくすぐったいけど\nミミ、好きなんだ～♪", 1, 6402, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122901, "きれいな貝がら\n見つけちゃった♪\nミミのたからもの！", 1, 6403, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122901, "ミミ、浴衣を\n買ってもらったんだ♪\n今度、おにいちゃんに\n見せてあげたいな！", 1, 6404, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122901, "ミミ、おにいちゃんと\nいっぱい夏休みの\n思い出つくりたいな♪", 1, 6405, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122901, "ねえ、おにいちゃん…\n夏休みの宿題、\nわかんないとこ\nおしえてくれる…？", 1, 6406, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122901, "おかあさんにね、\n麦茶つくるの上手って\nほめられたんだ～♪\n飲んでみて♪", 1, 6407, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122901, "夏休みの宿題で\nポスターかくんだ～♪\nおにいちゃんを\nかいてもいい？", 1, 6408, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122901, "線香花火やりたいな♪\nミミ、長持ちさせるの\n上手なんだよ♪", 1, 6409, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122901, "おにいちゃんの\n好きな本ってなあに？\nその本で読書感想文\n書こうかな♪", 1, 6410, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122901, "海であそんだ後ね、\nおにいちゃんに\n髪を拭いてもらうの\nすきなんだ～♪", 1, 6411, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 122901, "夏休みって、\nワクワクして\n早起きしちゃうよね♪", 1, 6412, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 122901, "わぁ♪　お昼は、\n流しそうめんなんだ？\nミミ、流してみたい♪", 1, 6413, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 122901, "こわい話聞いて、\nねむれないの…\nねえ、おにいちゃん、\nいっしょに寝ていい？", 1, 6414, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122901, "ちょっぴりやすんだら\nまた、いっしょに\n海であそぼうね♪", 1, 6416, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122901, "すっごくかわいいね♪\nこの子のこと、\n絵日記にかこうっと♪", 1, 6417, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122901, "くんくん…\nスイカのにおいだね♪\nこのにおいだいすき！", 1, 6418, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122901, "およいだあとは、\nちゃんと体を\nあっためなくちゃね！", 1, 6419, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 122901, "夏休みのご飯って\nなんだかとくべつ\nおいしい気がするね♪", 1, 6421, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 122901, "夏の星座って\nすっごくきれいだね♪\nいつかみんなと\nいっしょに見たいな♪", 1, 6422, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 122901, "わぁ♪　この子、\nとってもかわいいね！\nおともだちに\nなりたいな♪", 1, 6424, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 122901, "ミミ、人魚姫に\nなったみたい！\nおにいちゃん、\nとなりにすわって♪", 1, 6425, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122801, "水着姿になると\nバカンス気分を\n味わえちゃうね！", 1, 6426, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122801, "海に行くの待ちきれ\nなくて、もう水着に\n着替えちゃった！", 1, 6427, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122801, "海に行ったら\nずぶぬれ系のイタズラ\nたくさんしかけよう！", 1, 6428, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122801, "部屋の中で泳ぎの練習\nしよう！　にいちゃん\nも水着に着替えて！", 1, 6429, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122801, "お日さまとお揃いの\n水着なんだ～♪\n似合ってるかなぁ？", 1, 6430, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122801, "海といえば冒険！\n海底に眠る財宝を\n見つけちゃおう！", 1, 6431, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122801, "にいちゃんと一緒だと\n部屋にいても海気分♪\nスイカわりしよっか！", 1, 6432, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122801, "にいちゃん！\n早く海に行く準備\nしなよ～！", 1, 6433, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122801, "ねーにいちゃん\n何秒息止められるか\n勝負しよーよ！", 1, 6434, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122801, "にいちゃんはアイスと\nかき氷どっちが好き？\nミソギは…どっちも！", 1, 6435, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122801, "日焼け止めクリーム\nキョウカに\nもらったんだ～", 1, 6436, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122801, "水中メガネがあれば\n水鉄砲くらっても\n平気だね！", 1, 6437, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122801, "水着なら、毎日夏休み\n気分！　にいちゃんも\n着てみなよ～！", 1, 6438, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 122801, "にいちゃんおはよー！\n早く顔洗って、\nいっぱい遊んじゃお！", 1, 6439, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 122801, "にいちゃんデザートは\nイチゴのかき氷だよ！\n…激辛ってバレた？", 1, 6440, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 122801, "今日は楽しかった～！\n明日もイタズラ日和に\nなるといいな！", 1, 6441, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122801, "落とし穴の下に\nやわらかいの置けば\nケガしないよね！", 1, 6443, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122801, "かわいいね～！\nこの子も海に\n連れてっちゃおうよ！", 1, 6444, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122801, "このにおいを利用して\nイタズラするには…\nにいちゃんどう思う？", 1, 6445, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122801, "水着を着て暖炉で\nあったまるのって、\n変でおもしろいね～！", 1, 6446, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 122801, "海辺で食べる\nお昼ごはんって最高♪\nってことで海行こう！", 1, 6448, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 122801, "いつか海を越えて、\n地図にも載ってない\n場所を見つけたいな！", 1, 6449, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 122801, "この口から水が出たら\nみんな驚くよね！\nぷぷ…細工しちゃお！", 1, 6451, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 122801, "変な音がするイタズラ\nを仕込んどいて…\n誰か座らないかな～♪", 1, 6452, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102701, "あなた様と私の愛を\n周りに見せつけて\n差し上げましょう♪", 1, 6453, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 102701, "呼びましたか？\n呼びましたね？\nでは愛を語りましょう\nそうしましょう", 1, 6454, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122701, "水着で歩き回るのは\n恥ずかしいな…\n誰も見てないよね？", 1, 6455, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122701, "夏はやっぱり\nかき氷ですよね\n何色がいいかなぁ…♪", 1, 6456, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122701, "あの人、泳げないって\n聞きました！\n私が教えてあげないと", 1, 6457, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122701, "夏休みの宿題は\n計画的に進めましょう\n当たり前のことです！", 1, 6458, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122701, "防犯魔石、どこに\n着けておこうかなぁ\n…ここにしようかな", 1, 6459, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122701, "海の楽しみ方は\n予習済みです！\nあとは実践ですね♪", 1, 6460, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122701, "水着…きっとあの人に\n見られちゃいますよね\n…褒めてくれるかな…", 1, 6461, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 122701, "触りたいんですか？\n…いいですよ\n帽子くらいなら", 1, 6462, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122701, "夏は体調管理に\n気をつけましょう\n私が管理してあげます", 1, 6463, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122701, "海に行くのが楽しみで\nたまらないんですか？\n私はそこまで…嘘です", 1, 6464, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122701, "いつもより薄着だから\nなんだか恥ずかしい…\nみ、見ないでください", 1, 6465, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 122701, "へんたいふしんしゃ～\nえっ？　ああ、これは\n海に叫ぶ練習です", 1, 6466, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 122701, "あなたと一緒に\n海に行くなんて…\nなんだか大人…えへへ", 1, 6467, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 122701, "おはようございます\nさぁ、海に行って\n私と運動しましょう", 1, 6468, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 122701, "あっつ～い…\nはっ！？　ダメですよ\nシャキッとしないと！", 1, 6469, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 122701, "明日も海で\n遊ぶために、しっかり\nお休みしましょうね", 1, 6470, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 122701, "夏を楽しむときは\n休憩も大事ですよね\n計画的に、計画的に！", 1, 6472, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 122701, "かわいいなぁ～\n海のお土産屋さんに\n売ってるかなぁ？", 1, 6473, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 122701, "大人な香りがします\n私も違いが分かる\n素敵な女性になります", 1, 6474, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 122701, "濡れた体を\n乾かしましょう\nタオル、タオル…", 1, 6475, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 122701, "泳いだ後は\n栄養補給…だけど\n…おなか膨らんじゃう", 1, 6477, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 122701, "遊ぶだけじゃなくて\nお勉強もしてますよ\nあなたもどうですか？", 1, 6478, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 122701, "ミミが喜びそう…♪\nわ、私ははしゃいだり\nしませんけど…っ！", 1, 6480, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 122701, "ミソギがいたずらに\n使いそう…しっかりと\n試しておかないと！", 1, 6481, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123001, "あたしがまさか\nここにいるなんて…\n自分でも不思議だわ", 1, 6482, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123001, "昔みたいにあちこち\n飛び回れないのって\nちょっと不便かも", 1, 6483, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123001, "こうして誰かと一緒に\n暮らせるのもあいつや\nみんなのおかげね", 1, 6484, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123001, "ここで厄介になるなら\nあたしも家事とか\n手伝わないと…", 1, 6485, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123001, "…だめ！　誰かの\n世話をせずには\nいられないわ！", 1, 6486, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123001, "あいつたちが\n帰ってくるところだし\n綺麗にしないと…", 1, 6487, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123001, "せっかくあたしが\nいるっていうのに\n少しは構いなさいよ…", 1, 6488, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123001, "おいしい店とか\n知らないの？\n食べに行きましょ", 1, 6489, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123001, "あんた暇でしょ？\nあたしのやること\n手伝いなさい", 1, 6490, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123001, "コッコロたんと\n遊んでくるから\n留守番よろしく♪", 1, 6491, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123001, "あらおかえり…って\nボロボロじゃない！\nまた人助けしてたの？", 1, 6492, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123001, "やっぱりあたしは\nあんたのそばが\n一番落ち着くみたい", 1, 6493, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123001, "どこまでだって\nあんたと一緒に\nいてやるんだから", 1, 6494, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 123001, "ふわぁ…おはよう\n…大きくなったせいか\n朝は特に体が重いわ", 1, 6495, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 123001, "お昼ご飯が食べたい？\nしょうがないわね…\nなんか作って\nあげるから", 1, 6496, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 123001, "あんたまだ成長期だし\nさっさと寝なさい\n大きくなれないわよ？", 1, 6497, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123001, "たまにはあたしも\nゆっくりしないと\nもたないのよ…", 1, 6499, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123001, "かわいいじゃな～い♪\nコッコロたんに\nプレゼントしたいわ", 1, 6500, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123001, "いい香りね\n塔にもこういうの\n置いておこうかしら", 1, 6501, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123001, "炎か…前は\n羽が焼けそうで\n怖かったっけ", 1, 6502, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 123001, "あむ…妖精に\n体重増減の設定って\nあったっけ…？", 1, 6504, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 123001, "記憶のデータと違って\n物語はいいわね\n結末が読めなくて", 1, 6505, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123101, "冒険のお土産\nみんなにおすそわけ\nせなあきまへんな", 1, 6507, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123101, "おとぎ話よりも\n不思議なことが\nあるもんやねぇ", 1, 6508, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123101, "冒険しに出かけるなら\n動きやすい服装に\nせななぁ", 1, 6509, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123101, "地図に水筒に…\n他に忘れもんは\nあらへんやろか？", 1, 6510, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123101, "これまでの冒険のこと\nノートに書いて\nまとめてますんよ", 1, 6511, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123101, "王子はんとの冒険は\nどれもマホマホ王国の\n大事な歴史や♪", 1, 6512, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123101, "どこへ冒険するときも\nうちと王子はんは\n一緒どす♪", 1, 6513, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123101, "魔法で火ぃつける\n練習してますんよ～\nよう爆発しますけど…", 1, 6514, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123101, "うちと王子はんで\n冒険道具をお揃いに\nするんもええなぁ♪", 1, 6515, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123101, "テント張りも随分\n上達したんよ\n王子はんのおかげやね", 1, 6516, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123101, "ランスロットたちの\n新しい冒険衣装\n思いついたんよ♪", 1, 6517, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123101, "迷子になっても\n王子はんと一緒なら\nきっと大丈夫やね♪", 1, 6518, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123101, "王子はんのこと、\nきっちり守れるように\nなってみせますえ", 1, 6519, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 123101, "お寝坊さんは\n布団めくって\n起こしにいきますえ～", 1, 6520, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 123101, "支度が終わったら\n今日の冒険に\n出かけまひょか", 1, 6521, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 123101, "もうちょっと\nおしゃべりしたいん？\nあと少しだけやで♪", 1, 6522, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123101, "冒険で疲れた体に\nこの癒しぱわーは\nあきまへん…すや…", 1, 6524, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123101, "ふふ、あんさんも\nうちと一緒に\n冒険します？", 1, 6525, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123101, "虫除けのアロマは\n冒険の必需品どす\n忘れんようにせんと", 1, 6526, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123101, "うちも魔法使わんでも\n火ぃ起こせるよう\n練習せんとあかんなぁ", 1, 6527, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 123101, "この料理…\n冒険するときに\n作ってみまひょか", 1, 6529, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 123101, "王子はんとの\n愛と冒険のおとぎ話…\nはぁ…素敵やわぁ♪", 1, 6530, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103101, "あの人との\n最良の未来のために\n占いも霊能力ももっと\n精進しなきゃ", 1, 6532, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103101, "あなたともっと深く\nつながりたいです\n絆も、そして魂までも", 1, 6533, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104401, "絆の契約…\nあやつが望むのなら\n考えぬでもないがのう", 1, 6534, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104401, "なんじゃ？\nわらわの姿に\n見惚れたかの？", 1, 6535, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123201, "旅の荷物って多いな…\nねぇぷうきち～\nすこしは持ってよぉ～", 1, 6536, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123201, "傘はなくてもいっか\nぷうきちがいるし♪\n『おいなんだと…？』", 1, 6537, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123201, "旅先で人見知りなんか\nしてちゃだめだよね…\nまずはあいさつだ…！", 1, 6538, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123201, "おみやげはやっぱり\nご当地ぬいぐるみ\nだよね♪　ワクワク…", 1, 6539, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123201, "知らない場所かぁ…\nうぅ…考えただけで\nすでにホームシック…", 1, 6540, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123201, "野宿するってことは…\nお兄ちゃんのマントで\n寝るチャンスかも…！", 1, 6541, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123201, "どんなに危険な冒険も\nお兄ちゃんと一緒なら\n大丈夫！　…だよね？", 1, 6542, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123201, "ぷうきちのアイマスク\nも腹巻も持ってくの！\n旅の必需品なの～！", 1, 6543, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123201, "これ全部背負うのって\nけっこうむずかしいの\n順番まちがえると大変", 1, 6544, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123201, "えへへ～\nなでなでして？　帽子\nの上からでもいいよ♪", 1, 6545, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123201, "『悪いが絆創膏の部分\nはアンタッチャブルだ\n綿が飛び出ちまう』", 1, 6546, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123201, "お兄ちゃんと一緒に\nもっともっといろんな\n景色が見たいな！", 1, 6547, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123201, "旅って、終わる時は\nきっとさびしいよね…\nお兄ちゃん、帰り道は\n手をつないでもいい？", 1, 6548, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 123201, "出発が楽しみすぎて\n寝られなかった…\n『旅行のお約束だな』", 1, 6549, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 123201, "わぁ　いいお天気！\n旅の間もずーっと\nぴーかんだといいな♪", 1, 6550, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 123201, "知らない場所で寝るの\n怖いな…寝る時だけ\n帰ってこようかな…", 1, 6551, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123201, "旅先にはクッションも\nソファもないよね…\n『俺でいいだろ』", 1, 6553, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123201, "ぬいぐるみのみんな\n旅の無事を\n祈ってて…！", 1, 6554, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123201, "旅先の香りも\n思い出に持って\n帰れたらいいのにな～", 1, 6555, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123201, "旅行用に携帯用暖炉\nがあるといいかも…！\n『まだ荷物増やす？』", 1, 6556, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 123201, "『もう出発だぜ？』\nえーまってまって！\n腹ごしらえがまだ…", 1, 6558, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 123201, "世界ってこんなに広い\nんだ…！　はやく自分\nの目で見てみたいな～", 1, 6559, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123301, "はちゃめちゃヒマ～\nなーんか楽し～コト\nないかなー", 1, 6561, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123301, "ここ、いートコだねー\nこんどカリザきゅんも\nつれてこよー", 1, 6562, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123301, "かわゆいショタが\n見た～い♪\n触りた～～～い♪", 1, 6563, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123301, "なんか服きっついなー\nちょい太ったかー？\nん～\nま・気のせーっしょ♪", 1, 6564, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123301, "あ～やべ\nおねえちゃんに\nメッセすんの忘れたー\nまいっかー", 1, 6565, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123301, "パンケーキ食いてー\n騎士きゅん\n誘ってみよーっと♪", 1, 6566, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123301, "きょーも～\n好きぴに囲まれて～\nシ・ア・ワ・セ♪", 1, 6567, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123301, "アタシと遊ばな～い？\nってなんか\nナンパみスゴー\nウケる～", 1, 6568, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123301, "んー？\n今はだるいからパース\nあとでねー", 1, 6569, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123301, "カリザきゅん\n見なかった～？\nおねえちゃんから\n逃げるとかオシオキー", 1, 6570, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123301, "ネイルしたいけど\n無理なんよねー\n爪の間にめっちゃ\nスライム入っからさー", 1, 6571, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123301, "おねえちゃんに\nかまってほし～の？\nかわゆ～い♪", 1, 6572, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123301, "な～に騎士きゅん♪\nここでイチャイチャ～\nし・ちゃ・う～？", 1, 6573, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 123301, "ねんむ～\nけど外から\nショタの声したんで～\nつい起きちった～", 1, 6574, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 123301, "ランチの後はやっぱ\nデザートっしょー\nベツバラってヤツ～♪", 1, 6575, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 123301, "こんな時間まで\n起きてるコは～\n食べちゃうぞ～♪", 1, 6576, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123301, "あーラクー\nいっしょーここで\nキューケーできるわー", 1, 6578, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123301, "なにこれ～\nかわいいじゃ～ん\nショタの次くらいに～", 1, 6579, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123301, "いーニオイする～\nなんのニオイとかー\nよくわかんないけどー", 1, 6580, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123301, "あったか～♪\n溶けてスライム\n戻りそ～", 1, 6581, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 123301, "ン・ン・ン～♪\nお・い・し～♪\nも～っと食べるぅ～♪", 1, 6583, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 123301, "わ～なんかムズー\n読めなー\nウケるー", 1, 6584, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123501, "あんパンって\nお菓子に入るのかな？\nハロウィンで\nもらえたりしない？", 1, 6586, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123501, "お菓子をくれなきゃ\nぐうたらするぞ～！\nハロウィン以外でも！", 1, 6587, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123501, "お化けになったら\n仕事しないで\nゴロゴロし放題\nなのかな？", 1, 6588, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123501, "おっと…この仮装\n袖がすごく長いからさ\n引っ掛けないように\n気を付けないとね～", 1, 6589, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123501, "イタズラするのも\n体力がいるよね～\n楽にできそうなの\n何かないかな？", 1, 6590, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123501, "あいつって\n仮装とかするのかな？\n似合いそうなのは\nどんなお化けだろ？", 1, 6591, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123501, "ずっとハロウィンなら\nあいつと遊び放題\nなんだけどな～", 1, 6592, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123501, "今の街はハロウィン\n一色だからね～\n人混みが大変だよ", 1, 6593, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123501, "この仮装、どうかな？\n気に入ったなら\n来年もしてあげるよ～", 1, 6594, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123501, "実はこの長い袖\nドングリとかを\nため込めるんだよね…", 1, 6595, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123501, "カボチャランタンが\nあるんなら\nドングリランタンも\nあっていいよね～♪", 1, 6596, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123501, "ハロウィンでも\nやる気があれば\nぐうたらできる…\nうん、いいこと言った", 1, 6597, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123501, "一緒にお菓子食べよ？\n寝転びながら堪能する\nハロウィンスイーツは\n最高だからね～", 1, 6598, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 123501, "お化けは寝る時間…\n今のあたしはお化け…\nだから、おやすみ～…", 1, 6599, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 123501, "お昼か～…\n食後のデザートは\nハロウィンのお菓子で\n決まりだね～♪", 1, 6600, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 123501, "ふぁ～、寝よ…\nお化けは夜に\n出るものだけど\nあたしはリスだからね", 1, 6601, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123501, "ハロウィンを楽しむ\nためにも、ぐっすり\n寝とかないとね～", 1, 6603, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123501, "…袖から手が\n出せないから\nこのまま撫でよう\nよしよし", 1, 6604, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123501, "リラックスできる\n素敵な香りだね～\n成仏しそうだよ～", 1, 6605, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123501, "あったかいね～\nここで寝てもいい？\n…着替えないとダメか", 1, 6606, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 123501, "もぐもぐ…\nお化けもリスも\nお腹は空くんだよ", 1, 6608, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 123501, "キョンシーって\nどんなお化けなのか\n本で調べようかな？", 1, 6609, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 123501, "より取り見取りだね～\nとりあえず、これと\nこのお菓子はキープで", 1, 6611, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123601, "よし、衣装もばっちり\nハロウィンパレードへ\n出かけよう♪", 1, 6613, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123601, "仮装してても\n剣は手放せないね\nほとんど体の一部だよ", 1, 6614, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123601, "見回りに誘うふりして\n二人でパレードに\n参加しちゃおうかな♪", 1, 6615, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123601, "…あれ？　手錠の鍵\n落としちゃった…？\nやば、出かける前に\n見つけないと…！", 1, 6616, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123601, "お家でコスプレも\nこれはこれで楽しいな\n決めポーズの練習\nたくさんしておこう！", 1, 6617, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123601, "他にも、お姫様に\nメイドさんに学生服…\nやりたいコスプレが\n多すぎるよ～", 1, 6618, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123601, "漫画もゲームも好き！\n仮装するのも大好き！\n…好きを言葉にすると\nもっと好きになる♪", 1, 6619, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123601, "ずっと目が合ってるね\n見つめてくれるのは\nいいんだけど、今日は\n衣装を見てほしいな♪", 1, 6620, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123601, "トリックオアトリート\nお菓子をくれなきゃ\n逮捕しちゃうぞ♪", 1, 6621, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123601, "あなたにだけはこの姿\nいつでも見せてあげる\nふふ、なんちゃって♪", 1, 6622, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123601, "どうしたの？\nイタズラするなら\n隙を突かなきゃ\nこんなふうに、ね♪", 1, 6623, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123601, "コスプレしてても\n剣の稽古は休まないよ\nちょっと相手になって\nくれるかな？", 1, 6624, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123601, "ひゃっ、びっくりした\n急に触らないでよ～\nハロウィンだからって\nイタズラしないでね？", 1, 6625, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 123601, "おはよう！\n今日も朝から\nパトロールに行こう！", 1, 6626, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 123601, "カボチャのケーキを\n用意してるよ！\nデザートを食べて\n午後も頑張ろう！", 1, 6627, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 123601, "暗くなってきたね\nカボチャのランタンに\n火を灯そうよ♪", 1, 6628, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123601, "ふかふか気持ちいい～\nあっでも衣装がシワに\nなっちゃうかも…？\n…でも気持ちいい～", 1, 6630, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123601, "…かわいい\nこの子もパレードに\n連れて行きたいな", 1, 6631, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123601, "はぁ～癒される♪\nハロウィンの見回りが\n終わった後は、\nまったりくつろごう", 1, 6632, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123601, "ここで二人寄り添って\nカボチャスープ飲もう\n…なんて想像したら\nポカポカしてきた…", 1, 6633, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 123601, "パレードに参加すると\n体力を使うから、\nいっぱい食べておこう", 1, 6635, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 123601, "この絵本のお姫様\nかわいいね\nこの仮装もいいな♪", 1, 6636, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 123601, "わあ、おいしそう！\n見回りに行く前に\n少しつまんじゃおう♪", 1, 6638, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103701, "今日こそは、あの人に\n気持ちを打ち明けて…\nそのあとは…うう\nど、どうしよう？", 1, 6640, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103701, "私が剣なら、あなたは\nさしずめ私の鞘…\nふふ、私の帰る\n場所ってことだよ♪", 1, 6641, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123701, "やっぱり\nコスプレしてると\nテンションあがる～♪", 1, 6642, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123701, "キャラコスにも\n興味あるけど…\nマジカル、ラブリー\nどっちにしよっかな～", 1, 6643, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123701, "キャラコスといえば\nミスティーシャイニー\nもいいんだよねえ…\nうはー！　悩むー！", 1, 6644, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123701, "ギルドハウスで\n宅コスしてる人\n実は結構\nいるんじゃないかな～", 1, 6645, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123701, "コスプレだけど普段着\nって言いはれそう…\nって、ダメダメ！\nオタクは節度が大事！", 1, 6646, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123701, "フェスで買ってきた\n薄い本～♪\nギルドハウスに持って\nきちゃおっかな～", 1, 6647, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123701, "ようこそ\nコスプレ沼へ～\nなんちゃって♪", 1, 6648, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123701, "どうどう？\nナナカちゃんの\nハロウィンコスプレ\n似合ってる？", 1, 6649, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123701, "ハロウィン魔法少女\n参上カボ～！\n…やっぱ今の語尾\nナシで！", 1, 6650, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123701, "ハロウィンといえば\nトリックオア～…\nコスプレ！\nなんちゃって", 1, 6651, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123701, "ランフェスの\n戦利品～♪\nねえねえ\n見たい？　見たい？", 1, 6652, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123701, "私にしてほしい\nコスプレがあったら\n教えて♪　君のために\nや～ってやるぜぇ！", 1, 6653, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123701, "一緒にコスプレしたい\nならナナカちゃんは\nいつでも大歓迎だよ♪", 1, 6654, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 123701, "ふぁあ…ねむ\nって、ありゃりゃ　\n昨日コスプレしたまま\n寝ちゃってたかあ…", 1, 6655, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 123701, "今日のお昼は\nなんにしよ？\nカボチャ料理に\nしよっかな～", 1, 6656, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 123701, "もうこんな時間かあ\nそういえばオタクって\n夜行性の人が多い\n気がするぜぇ…", 1, 6657, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123701, "ふっふっふ\nこのパニエは\n座るときはちゃんと\nふにゃってなるのさ！", 1, 6659, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123701, "いや～\nかわいいものをみると\n癒されますな～", 1, 6660, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123701, "新しい薄い本の匂いの\nアロマとか…ダメ？\n私はあの匂い、なんか\n好きなんだよね～", 1, 6661, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123701, "暖か～い…\nけど近づきすぎて\n衣装が燃えないように\n気を付けなきゃ", 1, 6662, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 123701, "コス衣装のために\n体型維持は必須だけど\nついつい\n食べちゃうんだよね～", 1, 6664, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 123701, "おっ、次はこの本に\nのってるキャラのコス\nしてみよっかな？", 1, 6665, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (12, 0, 0, 123701, "おおっ！\nお菓子がいっぱい！\nベルルちゃんチョコ\n入ってないかな～？", 1, 6667, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123801, "むっ…\nこの耳の手触り…\n驚嘆に値する…！", 1, 6669, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123801, "獲物を狩りまくる…\nそんなウサギがいても\nいいだろう？", 1, 6670, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123801, "ワナなど怖くないぞ？\nむしろ積極的に\n踏んでいく性分だ", 1, 6671, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123801, "一匹狼ならぬ\n一匹ウサギか\nどっちでもかまわん♪", 1, 6672, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123801, "人間とて本を正せば獣\n本能に従えばよいのだ\n遠慮は無用さ☆", 1, 6673, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123801, "脱兎のごとく…\n逃げるものか\n坊やを追い回すのさ♪", 1, 6674, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123801, "獣の道は険しいが…\n坊やめ、音を上げずに\nちゃんとついてきたな\n褒めてやるとしよう♪", 1, 6675, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123801, "ニンジンはいらん\n好物は好敵手だ\nさぁどんどんおくれ♪", 1, 6676, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123801, "尻尾に触りたいだと？\nワタシの乱数聖域を\n崩せたらな…☆", 1, 6677, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123801, "頭で考えるな\n体に率直になれ\n己を野放しにするのさ", 1, 6678, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123801, "そうさモコフワさ♪\nなで心地は最高だが\n意外にどう猛だぞ…？", 1, 6679, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123801, "ワタシにピョンピョン\n跳ねてほしいのか？\nあとでじっくり、な♪", 1, 6680, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123801, "ワタシと番になるか？\nオマエさえよければ\nいつでもかまわんぞ☆", 1, 6681, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 123801, "太陽と共に目覚め\n活動する\nそれが獣の生き方さ☆", 1, 6682, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 123801, "昼寝をすればいい\nワタシが周囲を\n警戒していてやる", 1, 6683, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 123801, "暗くなれば眠るだけさ\nそうすれば余計な\nストレスなどたまらん", 1, 6684, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123801, "むむっ！？\n尻尾がつっかえて\nうまく座れんな…", 1, 6686, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123801, "愛らしさでワタシに\n張り合おうというのか\n５億年後に出直せ☆", 1, 6687, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123801, "野生の芳香か…\n本能が呼び覚まされる\nいいセンスだ♪", 1, 6688, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123801, "寒さも野生のうちだ\n…とはいえ\n温まっていくか♪", 1, 6689, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 123801, "悪くはないが物足りん\nこう見えて肉食なのだ\n肉はないのか肉は☆", 1, 6691, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 123801, "ウサギ族は地獄耳？\nワタシとの共通点だな", 1, 6692, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 106401, "ここが私の家…\nそれはこれからも\nずっと変わらないわ…", 1, 6694, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 106401, "ええと、その…今から\nデート…じゃなくって\n散歩でもどうかしら？\n…ふ、二人きりで…", 1, 6695, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123901, "ギルドハウスに\nヒーロー参上！\nみんな仲良くッスよ！", 1, 6696, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123901, "料理もできて\n鎧も作れる！\nお母さんはすごいッス", 1, 6697, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123901, "ううう…\nまたマントをドアに\n挟んじゃったッス…", 1, 6698, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123901, "ずっと鎧を着ていれば\nジュンさんに近づける\nかも？　燃えるッス！", 1, 6699, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123901, "サインの練習…\nもっとしておいた\nほうがいいッスかね～", 1, 6700, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123901, "半人前でも\n胸を張るッス！\nそのうち似合う日が\nくるッスから！", 1, 6701, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123901, "種族の違いも\n強い魂があれば\n超えられるッス！\n信じてるッスよ！", 1, 6702, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 123901, "困ったときは\n自分を呼ぶッス！\nあと楽しいときにも\n呼んでほしいッス！", 1, 6703, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123901, "特訓でも行くッスか？\n望むところッス！\n目指せ、一人前！", 1, 6704, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123901, "新しいショーの台本を\n読みこんでるッス！\nところで…この言葉の\n意味、分かるッスか？", 1, 6705, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123901, "ヒミツがなくなると\nなんか恥ずかしいッス\nあんたはどうッスか？", 1, 6706, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 123901, "ヒーローは遅れて\nやってくる…\nあんたもそうッスね♪", 1, 6707, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 123901, "誰よりも早く\nあんたに見せたいッス\n一人前になった自分！", 1, 6708, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 123901, "うーん、今日も\nよく眠れたッス～！\n絶好調、間違いなし！", 1, 6709, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 123901, "おお、もうお昼ッスか\nどうりでお腹が空くと\n思ったッス～えへへ", 1, 6710, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 123901, "パトロールのあとは\n歯みがきに宿題に…\n大忙しッスね、夜！", 1, 6711, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 123901, "こんなにリラックス\nできるなんて…\n平和な証拠ッスね～", 1, 6713, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 123901, "か、かわいい…♪\nさあ、自分と握手…\nううん、抱っこッス♪", 1, 6714, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 123901, "優しい匂いッスね…♪\nこれくらいみんなを\n包み込めるように\nなりたいッス～", 1, 6715, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 123901, "ひっくちゅ！　うー…\nちょっと冷えるッスね\nぬくぬくするッス～♪", 1, 6716, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 123901, "特訓した分、\nいっぱい食べるッス！\nでっかくなるッス！", 1, 6718, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 123901, "いつか自分の\nヒミツ図鑑が出たり\nして…いやいやいや", 1, 6719, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106901, "牢獄よりは\nマシな環境ね\n我慢してあげるわ", 1, 6721, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106901, "退屈ね…\nキャルでもいじって\n暇をつぶそうかしら", 1, 6722, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106901, "ここにいる連中\n私を天気予報士と\n勘違いしてない？", 1, 6723, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106901, "音波情報が多いわね…\nここ、もう少し静かに\nならないの…？", 1, 6724, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106901, "共同生活なんて\n久しぶりだわ\n…懐かしいわね", 1, 6725, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106901, "あの子たちのギルド\n経営が雑すぎるわ…！\n教育が必要みたいね", 1, 6726, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106901, "とどめを刺される\n準備はできているのに\nまだなのかしら…？", 1, 6727, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 106901, "私と戦う気になった？\nあら、まだなのね…\nその時を待ってるわよ", 1, 6728, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106901, "私というリソースは\n有効活用しなさい\n子守なんてごめんだわ", 1, 6729, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106901, "どうしてそんなに\n頭がいいか？\n生まれつきよ", 1, 6730, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106901, "視力は権能に\n影響しないわよ\n当たり前じゃない", 1, 6731, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 106901, "あなたまで天気を\n教えろですって…？\nぶっ殺すわよ？", 1, 6732, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 106901, "暇なら付き合いなさい\n好きなゲームでいいわ\nどうせ私が勝つもの", 1, 6733, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 106901, "人生朝露…\n悔いのない一日を\n過ごしなさい", 1, 6734, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 106901, "午睡はいいわよ…\nふわぁ…眠りは思考に\n必要不可欠なの…", 1, 6735, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 106901, "夙興夜寝なんて\nやめておきなさい\n休息は大切よ", 1, 6736, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 106901, "私を満足させるなんて\n大したものね…晶に\n大量生産させるわ", 1, 6738, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 106901, "キャルの代わりに\nズタズタにする物が\n欲しかったの…♪", 1, 6739, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 106901, "いい香りじゃない\n見たことのない調合ね\n誰が用意したの？", 1, 6740, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 106901, "パワーが足りないわね\nもっとよ、もっと私を\n寛がせなさい", 1, 6741, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 106901, "栄養価はともかく\n味のバランスは\n悪くないわね", 1, 6743, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 106901, "つまらない本は\n私が書き直すわ\nこれはどうかしら？", 1, 6744, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124001, "今年はアカリと\nお姉ちゃんでサンタが\n二人…つまり幸せも\n二倍ですね♪", 1, 6746, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124001, "クリスマスに向けての\nサックス練習…\nお兄ちゃんと一緒に\n猛特訓です！", 1, 6747, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124001, "クリスマスツリーの\n飾りつけ、完璧です♪\nお兄ちゃんお姉ちゃん\n喜んでくれるかな？", 1, 6748, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124001, "わあ、お外が真っ白！\n生クリームが\nいーっぱいの\nケーキみたいです♪", 1, 6749, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124001, "煙突から入ると\nお洋服が汚れちゃう…\n玄関からじゃ\nダメなのかなぁ？", 1, 6750, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124001, "お兄ちゃんが来たら\nサンタのお洋服\nいっぱい褒めて\nもらおっと♪", 1, 6751, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124001, "聖夜をお兄ちゃんと\n過ごせたら…\nアカリもちょっぴり\n大人になれるかも♪", 1, 6752, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124001, "今のアカリは\nサンタさんですから♪\nなんでもお願いして\nくださいね？", 1, 6753, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124001, "お外は寒いですから\nアカリとくっついて\nお出かけしましょ♪", 1, 6754, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124001, "お兄ちゃんは\nアカリのトナカイさん\nですから、ずーっと\nそばにいてください♪", 1, 6755, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124001, "お兄ちゃん、助けて！\nアカリのカラダで\nラッピング練習したら\n絡まっちゃいました…", 1, 6756, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124001, "プレゼント、いっぱい\n準備できました♪\nお兄ちゃんの分は…\nまだヒミツでーす♪", 1, 6757, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124001, "アカリ、プレゼントは\n大人っぽいのがいいな\n早くお兄ちゃんに\n追いつきたいから♪", 1, 6758, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 124001, "おはようございます♪\nふふ、アカリサンタは\nいい子のお手本なので\n早寝早起きなんです♪", 1, 6759, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 124001, "あれ、もうお昼？\nクリスマスナンバーの\n練習してたら\nもうこんな時間…", 1, 6760, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 124001, "冬の夜空は星が\nキレイですね～\nお兄ちゃん、一緒に\n見に行きませんか？", 1, 6761, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124001, "はぁ、快適すぎて\nとろけちゃいそう…\nこういうプレゼントも\nいいかもです…", 1, 6763, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124001, "やん、かわいいです♪\nえへへ、お兄ちゃんは\nどんなプレゼントが\n欲しいですか～？", 1, 6764, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124001, "わわ、大人な香り…\nロマンチックな\nクリスマスが\n過ごせそうです♪", 1, 6765, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124001, "これでカラダを温めて\nアカリの温もり…\nお兄ちゃんに\nおすそ分けしよっと♪", 1, 6766, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 124001, "お腹がパンパンです…\nでもこれで\nちょっとサンタさんに\n近づけたかも…？", 1, 6768, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 124001, "クリスマスイブの\n恋人との過ごし方…？\nはわわ、アカリには\nまだ早いかもです…！", 1, 6769, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 124001, "わっ、すごいすごい！\n不思議な絵本ですね♪\nこの本でお兄ちゃんに\nイタズラしちゃお♪", 1, 6771, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 124001, "アカリ、ホカホカに\nなっちゃいました～♪\n…ぎゅってしても\nいいですよ？", 1, 6772, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124101, "またエンジェルになる\nかもとは思ってたけど\n…サンタさん要素の\n追加は想定外だったわ", 1, 6773, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124101, "ラフィたちがいない\nときは私がなんとか\nしないとね\n一応…エンジェルだし", 1, 6774, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124101, "部屋の飾りつけとか\nするのも…あり？\nふふ、浮かれすぎかな", 1, 6775, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124101, "パーティーだったら\nみんなで大人数向けの\nボードゲームもできる\nかも…やったっ", 1, 6776, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124101, "この服のモコモコ…\n気づいたら触っちゃう\n…やるわね、ラフィ", 1, 6777, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124101, "アイツと一緒にいると\n人見知りとは違う\n緊張があるのよね…\nって今の聞いてたっ？", 1, 6778, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124101, "ぬいぐるみ…\nいじめてないわよね？\n…大事にしてるって？\nそれはそれで…あうぅ", 1, 6779, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124101, "いい子と悪い子って\nどう見分けるのかしら\n…まさか素直さとか？\nだったら困る！", 1, 6780, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124101, "プレゼントといえば\nやっぱりゲーム！\n年末商戦サイコー♪", 1, 6781, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124101, "サンタさんはお仕事を\n見られちゃ\nいけないのよね？\n…天職？", 1, 6782, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124101, "この服は汚れ対策も\nバッチリなのよね\n煙突に入れるように\n…いや私はNGよ！？", 1, 6783, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124101, "アンタのお願い…\n今なら一つくらい\n聞いてあげるわよ？\nほら、サンタ衣装だし", 1, 6784, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124101, "私たちって\nなぜかぬいぐるみと\n縁があるわよね\n…ふふ", 1, 6785, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 124101, "お、おはよっ\n…ふぅ、寝ぐせを\n直すのが間に合って\nよかったぁ", 1, 6786, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 124101, "え…一緒にごはん？\nそ、そう…うん。OK\nまあ、せっかくだし？", 1, 6787, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 124101, "この時間のゲームって\n妙に楽しいのよね\n…ということで、\nちょっと遊ばない？", 1, 6788, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124101, "ああ～ダメになるぅ…\nもうここでずっと\nゲームしてた～い", 1, 6790, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124101, "プレゼントのために\n研究は必要よね、うん\n…はぁ、かわいい～♪", 1, 6791, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124101, "集中力を高める香りも\nあるのよね…\n勝負をするときには\n持ち歩こうかしら", 1, 6792, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124101, "なんだかまったり\nしちゃうわね…\n時間が溶けるわ…", 1, 6793, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 124101, "お腹いっぱい\n食べられるのも\n幸せなことよね", 1, 6795, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 124101, "ちっちゃいときは\nアカリと一緒に絵本を\n読んだなぁ…懐かしい", 1, 6796, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 124101, "飛び出す絵本…\nこれでゲームブックを\n作ったらおもしろく\nなる気がするわ…", 1, 6798, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 124101, "あったまる…\nモフモフ…ふわふわ…\nちょっと休憩しても…\nいいよね…？", 1, 6799, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105101, "あら、どうしたの？\nもしかして、私と\n新しい実験を\n始めたくなった？", 1, 6800, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105101, "調子が悪いときは\n診療所に来て\n君は特別に、じっくり\n診察してあげるわ♪", 1, 6801, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124201, "特別じゃなくても\nまったり過ごす\nクリスマスも\nいいものだね", 1, 6802, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124201, "ニットの帽子に\nマフラーに耳当て…\n素顔用の防寒対策を\n整えなければ", 1, 6803, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124201, "靴下が\n見当たらなかったから\n枕元にはかわりに\n鎧を置いておこう", 1, 6804, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124201, "聖女なんて言われる程\n素晴らしくはないけど\n心は聖くいたいものだ\n日々精進だね", 1, 6805, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124201, "兜がないから\nイルミネーションが\nいつもよりまぶしく\n感じるよ", 1, 6806, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124201, "皆の幸福を\n祈願して\nクリスマスリースを\n飾ろうか", 1, 6807, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124201, "私にはかわいすぎる\n衣装だが…開き直って\nクリスマス気分を\n楽しんでしまおう", 1, 6808, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124201, "ツリーの飾りつけ\n手伝ってくれるかい？\nセンスは少年に\nお任せするよ", 1, 6809, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124201, "パーティーは\n用意してる時も楽しい\nねえ少年、この気持ち\n分かってくれるかい？", 1, 6810, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124201, "なんでトナカイの鼻は\n赤いのかって？\n無灯ソリ運転は違反で\n取り締まられるからだ", 1, 6811, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124201, "トモちゃんや\nマツリちゃんは\nいい子だから、きっと\nサンタさんに会えるね", 1, 6812, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124201, "ヒイラギの髪飾り\n実は気にいってるんだ\n少年にも\n褒めてもらいたいな", 1, 6813, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124201, "ねえ少年…そろそろ\n見慣れてほしい\nそんなにまじまじ\n見つめられると照れる", 1, 6814, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 124201, "朝日に照らされて\n街がキラキラ白銀に\n輝いてるよ", 1, 6815, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 124201, "お昼ご飯を食べて\nちょっと散歩しようか\n祝祭に沸く街は\nとてもきれいだよ", 1, 6816, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 124201, "やっぱりクリスマスは\n夜が本番って感じだね\nキャンドルを灯して\n厳かに過ごそうか", 1, 6817, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124201, "鎧姿では無縁だったが\nふかふかしていて\n気持ちいいな…\nふかふか…ふかふか…", 1, 6819, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124201, "ふふ、かわいいね\n抱きしめると\nあの雪の日に産まれた\n赤ちゃんを思い出す", 1, 6820, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124201, "兜を脱いだおかげで\nこの香りに出会えた\n…クリスマスの魔法も\n悪くないかもね", 1, 6821, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124201, "指先から体の芯まで\n暖まるね\nこれなら雪の日でも\n安心だよ", 1, 6822, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 124201, "もぐ、もぐ…\nあまり食べるところ\n見ないでほしい", 1, 6824, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 124201, "素敵なレストランの\n特集記事が載ってる\n聖夜に予約が取れたら\n最高に幸せだろうね", 1, 6825, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 124201, "わっ！\n…………いや\n驚いてなどいない", 1, 6827, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 124201, "ふふ…頬に直接\nふかふかを感じられる\n温かくて気持ちいいよ", 1, 6828, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124301, "ピアノに読書…\nグレアと一緒に\nなにをしようかな～♪", 1, 6829, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124301, "元の世界へ戻った時\n授業に遅れないよう\nグレアと勉強しとこう", 1, 6830, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124301, "街に魔物料理屋さんが\nあるんだ！　グレアと\n食べに行ってみよう♪", 1, 6831, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124301, "せっかく新しい魔法を\n覚えたし、グレアにも\n見てもらいたいな！", 1, 6832, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124301, "グレアも誘って\nランドソルをもっと\n探索したい！", 1, 6833, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124301, "私たちも\nあの人を見習って\nバイトとかしよう！", 1, 6834, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124301, "いつかあの人を\n私たちの学院に\n招待したいな！", 1, 6835, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124301, "あのね！\nこれからグレアと\n魔法愛好会に\n行くんだ！", 1, 6836, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124301, "これからなにするの？\n私たちもついて行って\nいいかな！？", 1, 6837, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124301, "グレアと一緒に\nあなたも魔法の練習\nしてみない？", 1, 6838, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124301, "グレアとよく話すの\nマナリアの制服\nあなたに着せたいって", 1, 6839, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124301, "グレアのそばって\n温かいんだ～！\nぬくぬくだよ～♪", 1, 6840, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124301, "あなたは\nグレアと私の友達！\nこれからもずっと！", 1, 6841, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 124301, "おはよう！\n今朝はグレアと一緒に\n外を散歩してきたよ！", 1, 6842, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 124301, "お腹空いたな…\nよし、グレアと一緒に\n魔物料理を作ろう！", 1, 6843, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 124301, "もうこんな時間だね\n明日のために\nそろそろ寝ようかな！", 1, 6844, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124301, "はぁ～この感触…\nグレアの尻尾みたいで\n安らぐ～", 1, 6846, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124301, "ぬいぐるみって\nいいよね…私もよく\nグレアに抱き付くんだ", 1, 6847, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124301, "いい香り～…\nグレアもこういうの\n好きなんだよ！", 1, 6848, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124301, "あったかいね～…\nグレアにくっついてる\n時を思い出すよ～…", 1, 6849, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 124301, "この世界にしかない\nおいしいものが\nたくさんあるよね！", 1, 6851, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 124301, "世界を繋ぐ魔法の\nヒント…本に書いて\nないかなぁ…", 1, 6852, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124401, "ピアノに読書…\n今日のアンは\nなにをするのかな…", 1, 6855, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124401, "私はアンほど\n勉強はできないから\nちゃんと自習しないと", 1, 6856, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124401, "一度始めたら\nとことん追求するのが\nアンなんだよね…", 1, 6857, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124401, "ふふ…夢中になってる\nアンを眺めてるだけで\n一日があっという間…", 1, 6858, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124401, "ランドソルは広いね…\nアンと一緒にいろんな\n場所へ行ってみたい", 1, 6859, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124401, "アンやあの人の笑顔を\n見てると、すごく\n安心するんだ…", 1, 6860, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124401, "もしあの人が私たちの\n学院に編入したら…\nふふ、楽しそうだね", 1, 6861, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124401, "アンもあなたも\nすごいな…\nみんなとすぐに\n仲良くなっちゃうから", 1, 6862, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124401, "あなたはこれから\nなにをするの？\nその…アンと一緒に\nついていってもいい？", 1, 6863, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124401, "アンから魔法の練習に\n誘われた？　実は私も\n…一緒にどうかな？", 1, 6864, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124401, "あなたがマナリアに\n編入したら\n楽しいのになって\nアンとよく話すんだ…", 1, 6865, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124401, "こっちの世界にも\nアンのように\n私を受け入れてくれる\n人たちがいるんだね…", 1, 6866, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124401, "元の世界へ帰る日が\n来たとしても私たちは\n「さよなら」より\n「またね」がいいよね", 1, 6867, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 124401, "おはよう。さっき\nランドソルの朝日を\nアンと見てきたんだ", 1, 6868, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 124401, "私たちは\nこれからなんだけど\nあなたはお昼ごはん\n何を食べたの？", 1, 6869, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 124401, "そろそろ\nアンが眠る時間だ…\nあなたもおやすみ", 1, 6870, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124401, "はぁ、疲れが取れる…\nこの尻尾…\nちょっと重いんだよね", 1, 6872, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124401, "これ…\nアンに見せたら\n喜ぶだろうな…", 1, 6873, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124401, "いい香り…\nアンの感想も\n聞いてみたいな", 1, 6874, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124401, "あったかい…\nアンがここにいたら\n本を読みながら\n寝ちゃうんだろうな", 1, 6875, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 124401, "おいしいからたくさん\n食べれちゃうけど\nまた尻尾…\n大きくなっちゃうかな", 1, 6877, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 124401, "アンがおススメ\nしてくれた本は\nこれかな…？", 1, 6878, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124701, "凶、凶、凶…大凶？\nなによこのおみくじ\n自宅用だからって\n排出率が適当すぎよ…", 1, 6881, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124701, "今年こそ最高の新薬が\nもはや偶然に近い\n最小限の手間で\n作れますように", 1, 6882, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124701, "喉に詰まる前に\n溶けてなくなるお餅…\n最優先の研究課題だわ", 1, 6883, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124701, "よし、完成♪\nミツキ特製…\n「新解釈おせち料理」\n医学的根拠が満載よ", 1, 6884, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124701, "お年玉を彼に…\nあげるべき…？\n色んな意味で微妙ね…", 1, 6885, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124701, "ふふ、今年もまた一年\n彼のバイタルを\n記録できるのね♪", 1, 6886, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124701, "今年こそ\n私の薬で彼を魔改造…\nううん、大人の男に！", 1, 6887, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124701, "コマ回しのコマを\n見てたら目が回った？\nそう…手当は不要よ…", 1, 6888, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124701, "私の顔で\n福笑いを作った？\nあら…隻眼まで\n再現度が高いわね♪", 1, 6889, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124701, "門松よし、しめ縄よし\nこんなギルドでもね\n一応縁起を担ぐのよ", 1, 6890, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124701, "本気の薬草を煮込んだ\n本気の七草粥よ\n色は気にしちゃダメ", 1, 6891, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124701, "君が今年も健康で\nつつがなく…実験台を\n務められますように", 1, 6892, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124701, "いくつ新年を迎えても\n俗世の垢にまみれない\nそんな君こそが…\n私には最高のお薬よ", 1, 6893, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 124701, "お正月なんだから\nいつまでも寝てないで\nさぁ、実験開始よ♪", 1, 6894, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 124701, "カルタも双六もしたし\nあとは実験くらいしか\nやることがないわね", 1, 6895, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 124701, "日頃からたいがい\n意味深な夢を見るけど\n初夢はまた別かしら…", 1, 6896, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124701, "さすがにこの格好で\n寝正月を敢行するのは\nためらわれるわね", 1, 6898, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124701, "や、闇医者たる者\nこんな愛くるしい物に\n心奪われるわけには…", 1, 6899, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124701, "お雑煮のいい香り…\n食欲が増進されるわ\nアロマも立派なお薬ね", 1, 6900, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124701, "胸元は閉じないわよ？\nでも時々ここで\nこっそり暖まるの…", 1, 6901, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 124701, "無から有が生まれてる\n新年早々物理の法則を\n無視してくるのね…", 1, 6903, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 124701, "どれもこれも読んだわ\n新しい婚活本の\n入荷はまだかしら…", 1, 6904, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124601, "彼が一生懸命\n書いてくれた年賀状\n気持ちがこもっていて\nすごくうれしいわ♪", 1, 6906, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124601, "しっかり丁寧に\n大掃除したから\nとってもいい気持ちで\n新年を迎えられたわ♪", 1, 6907, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124601, "彼の着物姿\nきっと凛々しいわよね\n想像しただけで\n笑顔になるわ～♪", 1, 6908, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124601, "お雑煮は地域によって\n違いがあるけれど\n愛情が隠し味なのは\nどこも一緒よね♪", 1, 6909, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124601, "彼と凧揚げして\n遊びたいけど\n森では引っかかって\nできないかしら…？", 1, 6910, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124601, "今年も彼が素直に\n成長できるように\nたくさんいい子いい子\nしてあげましょう…♪", 1, 6911, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124601, "絵馬に彼と\n書いた通り\n今年もみんなが笑って\n過ごせますように", 1, 6912, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124601, "先生と初詣に\n行きましょう〜♪\n迷子にならないように\n手を繋いで、ね？", 1, 6913, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124601, "初売りセールいいわね\nでも気を付けないと\nあれこれ欲しくなって\n買い過ぎちゃうかも…", 1, 6914, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124601, "風邪をひかないように\n温かくするんですよ～\nえ？　先生、温めて？\nふふ、甘えん坊さんね", 1, 6915, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124601, "あなたのために\nちょっと豪華なおせち\n用意したのよ\nお口に合うかしら？", 1, 6916, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124601, "あなたも着物着たい？\nふふ、貫禄出ちゃって\n先生ドキドキ\nしちゃうかも…♪", 1, 6917, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124601, "あなたの成長は\nとってもうれしいけど\n今年もまだ、あなたの\n先生でいたいな…♪", 1, 6918, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 124601, "あら、起きたの？\nお雑煮できてるから\nちゃんとお顔\n洗ってらっしゃいね", 1, 6919, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 124601, "お昼ご飯を\nしっかり食べて\nみんなでかるた大会\nしましょう〜♪", 1, 6920, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 124601, "は～い、良い子は\nもう寝る時間ですよ\n素敵な初夢が\n見られますように♪", 1, 6921, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124601, "このフカフカちゃんも\n天日干ししたいけど…\nその前にちょっと\n休憩しちゃおうかしら", 1, 6923, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124601, "うふふ、かわいい～\nアオイちゃんや\nハツネちゃんも\nきっと気に入るわね♪", 1, 6924, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124601, "お正月は空気も\n透き通ってるから\nいつも以上に\n素敵な香りに感じるわ", 1, 6925, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124601, "火のぬくもりに\n抱かれていると\n不思議と眠気が…\nふああ…", 1, 6926, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 124601, "愛情のこもった料理は\nおいしいだけじゃなく\n心も満たされちゃう♪", 1, 6928, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 124601, "お正月のお作法も\nたくさんあるのね～\n先生も日々勉強だわ～", 1, 6929, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104701, "少年への感謝をどんな\n形で伝えるべきか…\n言葉よりも物の方が\n良いのだろうか？", 1, 6931, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104701, "話し相手になって\nもらってもいいかな？\n少年と一緒にいると\nとても楽しいんだ", 1, 6932, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124501, "お正月だからって\nギャングのボスは\n油断できないよ～？", 1, 6933, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124501, "イノリちゃんがお年玉\n欲しいんだって♪\n悪い子だったし\n10ルピでいいかな☆", 1, 6934, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124501, "お正月だからお雑煮を\n作ったんだけど\nカヤちゃんには\n大好評だったよ♪", 1, 6935, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124501, "ラーゴがお餅を\n食べたがってるけど…\nこの子にあげるのは\nちょっと心配かな～？", 1, 6936, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124501, "お正月は晴れ着の子が\nやっぱり多いよね～\n今度、私の漫画でも\n出してみようかな？", 1, 6937, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124501, "君と二人っきりで\n神殿に初詣…\n行ってみたいな～♪", 1, 6938, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124501, "君とお正月を\n過ごしていると\n面倒なことも\n全部忘れられそう…", 1, 6939, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124501, "この着物、どう思う？\n似合ってたら\nうれしいけどな～♪", 1, 6940, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124501, "袖を引っ張ったり\nしないでね？\n着崩れしちゃうから♪", 1, 6941, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124501, "お正月ならではの\n漫画ネタってある？\n参考にしたいんだよね", 1, 6942, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124501, "昔は凧あげとか\n駒回しとかしてたけど\n大人になるとそういう\n機会も減るよね…", 1, 6943, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124501, "君にもお年玉を\nあげようか？\n好きな額を言ってね☆", 1, 6944, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124501, "来年も、再来年も…\nこうしてみんなで\n新年がお祝いできると\n素敵だね～♪", 1, 6945, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 124501, "おはよ～♪\n朝ごはんは\n焼いたお餅とか\nどうかな？", 1, 6946, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 124501, "お昼だね～\nおせちを用意したから\nみんなで食べよっか", 1, 6947, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 124501, "もうこんな時間だね～\n一日中着物だったから\n肩が凝っちゃったよ", 1, 6948, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124501, "このまま寝たら\nしわになるかな？\n着替えた方がいい？", 1, 6950, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124501, "こういうのって\nラーゴのおもちゃに\nちょうどいいかも♪", 1, 6951, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124501, "いい香りだけど\n着物に移らないように\n気をつけないとね", 1, 6952, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124501, "ちょうどいい\n火加減だね♪\nぽかぽかするよ～♪", 1, 6953, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 124501, "着物の時は\nほどほどにしないと…\n欲張ったら、お腹が\n苦しくなるからね～", 1, 6955, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 124501, "ふーん\nイノリちゃんは最近\nこんな漫画を\n読んでるんだ～♪", 1, 6956, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124801, "楽園の気配は…ナシ！\nみんな前を向いてるね\nそうこなくっちゃ！", 1, 6958, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124801, "レッスンの成果も\nこの格好くらい\n目に見えたら\n分かりやすいのになぁ", 1, 6959, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124801, "次の曲はワイルドで\n悪い感じもいいなぁ…\nカヤとムイミちゃん\nみたいなイメージで！", 1, 6960, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124801, "私…服、着てるよね？\nうんうん、着てる！\nあんまり体が軽いと\n急に焦る瞬間あるな～", 1, 6961, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124801, "最近ファンの年齢層が\n幅広くなってきたかも\nこれって私…国民的\nアイドルに近づいた？", 1, 6962, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124801, "パティスリーとコラボ\nしたらどんな感じかな\n…ノゾミンマカロン♪\nとか？　…いけるかも", 1, 6963, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124801, "彼と一緒にいると…\nうれしくって苦しいな\nでも両方諦められない\n…アイドルらしいや", 1, 6964, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124801, "この格好、チカと\nツムギも似合うと\n思うんだけどな～\nキミもそう思うよね？", 1, 6965, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124801, "街を歩いてたらね？\nライブとか写真集の\n告知イベントだって\n思われちゃった！", 1, 6966, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124801, "私が持ってた剣に\n結んであったリボン…\n何か気づいてくれた？\nヒントは…色だよ♪", 1, 6967, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124801, "キミの気配ってすごく\n分かりやすいんだよ\n近くにいたらすぐに\nピンとくるんだ♪", 1, 6968, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124801, "あ、それ…はじめて\nキミにあげた音響盤！\nね、一緒に聴こうよ", 1, 6969, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124801, "ぼうっとしてたら\nイタズラしちゃうよ♪\nもっともっと私のこと\n見てないとダーメ！", 1, 6970, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 124801, "朝は今日のはじまり！\n昨日よりももっと\nいい一日にしようね♪", 1, 6971, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 124801, "レッスンがんばったし\nまだお昼だし…\nランチ大盛りにしても\nセーフだよね！", 1, 6972, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 124801, "寝る前に明日の予定を\n確認しておかないと！\nえっと、集合場所は…", 1, 6973, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124801, "一休みのつもりが\nなが～い休憩になるの\nあるあるだよね～", 1, 6975, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124801, "ノゾミンの熱烈ハグ♪\n…ここだけ聞いたら\nスクープになっちゃう\nかも？　なーんて！", 1, 6976, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124801, "香りってなんだか\n記憶に残るよねー\n…こういうの私だけ？\nみんなも同じだよね？", 1, 6977, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124801, "なんだかライブの\n熱気を思い出して\nきちゃった…！\nレッスンしよっと！", 1, 6978, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 124801, "お腹が減ってたら\nいいパフォーマンスは\nできないもんね！\n…んー、おいし～♪", 1, 6980, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 124801, "いい歌詞を書くには\nしっくりくる言葉を\n知っておかないと！", 1, 6981, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124901, "うだうだ悩んでる\nヤツがいたら\nオレのとこ連れてこい\n背中押してやるからさ", 1, 6983, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124901, "最近どことなく\nファミリーの連中が\n浮き足立ってんだよな\n…なんでだ？", 1, 6984, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124901, "イノリのヤツ\nやたらと脇腹\nつついてきやがって…\nくすぐったいっての", 1, 6985, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124901, "身軽になったのは\nいいが…くそっ\nどこもかしこも\nスースーする…", 1, 6986, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124901, "思い出に浸るのも\n悪くはないが\nやっぱ人間\n未来に生きないとな！", 1, 6987, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124901, "またムイミやノゾミと\nツルむのもいいな\nそこにカレもいたら\nなおいいぜ", 1, 6988, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124901, "未来のことを考えると\nなぜかいつもカレが\n頭に浮かんじまう…\nええい、やめだやめ！", 1, 6989, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 124901, "ちょっくらオレと\n腕相撲しねえか？\nこの新しい力\n試させてくれ！", 1, 6990, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124901, "なあ、悪いが上着\n貸してくれないか？\nこのカッコ、割と\n肌寒いんだよ…", 1, 6991, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124901, "おっ、生まれ変わった\n喧嘩屋にご用命か？\nいいぜ、キミの\n依頼なら二つ返事だ！", 1, 6992, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124901, "なんか悩みごとが\nあるなら、なんでも\n話してくれよな\nオレとキミの仲だろ？", 1, 6993, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 124901, "このカッコが\nきれいだって？\nや、やめろよ…\n照れくさいだろ…", 1, 6994, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 124901, "キミがいると、なにを\nするにも退屈しないぜ\nだから今日も\n付き合ってくれよな！", 1, 6995, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 124901, "おはよう！　新しい\n未来がやってきたぜ！\n一秒だって時間は\n無駄にするなよ？", 1, 6996, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 124901, "そろそろ昼飯に\nしないか？\n穴場のうまい店に\n案内してやるからさ", 1, 6997, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 124901, "夜更かしは\n未来の前借りだ\n体壊さないように\nほどほどにしとけよ？", 1, 6998, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 124901, "お、いいなこれ\nファミリーの\nアジトにも\n置いときたいぜ", 1, 7000, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 124901, "べ、別にかわいいもん\nなんて、そんなに\n好きじゃないし…\n嫌いでもないけどさ", 1, 7001, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 124901, "嗅いだことない\n香りだな\nなかなか気に入った！", 1, 7002, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 124901, "薄着で暖を取るのって\n最高にぜいたくだよな\nへへっ、堪能させて\nもらうとするか", 1, 7003, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 124901, "こいつはいいや\nこのカッコだと\n消耗が激しくて\nやたら腹が減るからな", 1, 7005, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 124901, "甘辛コーデ…\nへぇ、そんなのが\nあるのか…\nわ、悪くねえかもな", 1, 7006, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105501, "はぁ…\nもっともっと長い時間\n先輩と過ごせたらなぁ", 1, 7008, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105501, "えへへ…♪\n先輩が、すぐそばに\nいてくれるなんて…\n幸せだなぁ…♪", 1, 7009, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125001, "はーっはっは！\n強くてカッコいい\n大悪党、ここに参上！", 1, 7010, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125001, "みんなおしゃれで\nカッコいいけど\n今のアタシも\n負けてないぞ！", 1, 7011, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125001, "ギャングのアジトも\nよかったけど\nここも賑やかで\n悪くないな！", 1, 7012, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125001, "悪党にも休息は必要だ\nここならゆっくり\n休めそうだ！\n邪魔するぞ！", 1, 7013, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125001, "いっぱい人がいるな…\nアタシの悪党っぷりを\n広めるチャンスだ！", 1, 7014, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125001, "ここにいれば\nいい思い出がたくさん\n作れそうだな！", 1, 7015, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125001, "いつかアタシも\nオクトーと一緒に\nこんなアジトを手に\n入れてやるからな！", 1, 7016, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125001, "ん？　アタシの格好が\n気になるのか？\nならもっと近くで\n見てもいいぞ！", 1, 7017, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125001, "おい、暇だから\nアタシと遊べ！\nカヤとノゾミも呼んで\n大騒ぎするぞ！", 1, 7018, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125001, "オマエ、前より強く\nなってないか！？\nくっそー！　アタシも\n負けてらんないぞー！", 1, 7019, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125001, "今日は特別にアタシの\n思い出話を聞かせて\nやる！　さーて、どの\n悪事から話そうか…", 1, 7020, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125001, "オマエとの付き合いも\n結構長くなったよな？\nこれからも\nよろしく頼むぞ！", 1, 7021, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125001, "オマエまでいなく\nなったらアタシが困る\n勝手にどっか\n行くんじゃないぞ！", 1, 7022, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 125001, "起きろ、起きろー！\n大悪党の朝は早いんだ\nさっさと\n顔洗ってこい！", 1, 7023, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 125001, "昼飯どうしようか…\nそうだ！　オマエの\nおススメに連れてけ！\nいい店期待してるぞ！", 1, 7024, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 125001, "明日を思い切り\n楽しむためにも\n早く寝とけよ！\nまた明日な！", 1, 7025, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125001, "こうやって座ると…\nおお！　すっごく悪党\nっぽいぞ！", 1, 7027, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125001, "オマエ、仲間に\nなりたそうな目を\nしてるな！", 1, 7028, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125001, "うん、いい匂いだ！\n何の匂いかは全然\n分かんないけどな！", 1, 7029, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125001, "炎ってなんだか\nカッコいいよな！\n見てるだけで心が\n燃えてくるぞ！", 1, 7030, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 125001, "いっぱい食べれば\nちょっとは\n背が伸びるかな…？", 1, 7032, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 125001, "ぐぬぅ…たまには\n本でもと思ったけど\nやっぱりアタシは\nバトル担当だな！", 1, 7033, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118201, "お邪魔しまーす★\nなーんてもう\nすっかりくつろいで\nますけどー♪", 1, 7035, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118201, "騎士さんの\nギルドハウス…★\nなんだかドキドキ\nしちゃいます♪", 1, 7036, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118201, "ぺんぽこりんの\n置き場所はー…\nま、このへんで★", 1, 7037, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118201, "うーん、これは\n壊しがいがありそ…\nウソウソ、冗談です★", 1, 7038, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118201, "オトモダチでも\n作ってみますかー\nくすっ★", 1, 7039, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118201, "退屈ですねー\n騎士さん、構って\nくれないかなー", 1, 7040, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118201, "もっともっと早く\n騎士さんに逢えてたら\n…なんて、わたし\nらしくないですね", 1, 7041, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118201, "わたし一人にするの\n不安だったりします？\nちゃーんとイイコに\nしてますってー★", 1, 7042, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118201, "お出かけします？\nそれともおうちで\nまったりしますー？", 1, 7043, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118201, "女の子をすぐ家に\nあげちゃうなんて…\nふふ、やっぱり隅に\n置けないですねー★", 1, 7044, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118201, "騎士さん騎士さーん★\nウザ絡みしても\nいいですかー？\n騎士さんってばー★", 1, 7045, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118201, "一緒にゴロゴロ\nしませんかー？\nコイビトっぽくて\n楽しそうですし♪", 1, 7046, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118201, "うふふ★\nこーやってあなたに\n優しくされると…\nときめいちゃいます♪", 1, 7047, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 118201, "おはようございます★\nあれれー？\nまだおねむですかー？", 1, 7048, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 118201, "騎士さん、お昼は\nもう食べましたー？\nわたし、ラーメンが\n食べたいでーす★", 1, 7049, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 118201, "あ、夜更かししてちゃ\nダメですよー？\nほらほら、お布団に\nごーごー★", 1, 7050, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118201, "ふかふかのふわふわー\nついついまーったり\nしちゃいます★", 1, 7052, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118201, "かっわいー★\n名前はー、んー…\nぱんぱかぷんで★", 1, 7053, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118201, "くんくん★\nうーん…\nたぶん素敵な香り♪", 1, 7054, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118201, "あったかーい★\n心も体も\nぽっかぽかですねー★", 1, 7055, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 118201, "もぐもぐ★\nんー、おいしくって\n止まらないですねー★", 1, 7057, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 118201, "へー★\nほほー★\nふんふーん★", 1, 7058, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125101, "さ～て時間もあるし\nラテアートの練習でも\nするかにゃ～♪", 1, 7060, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125101, "カフェの猫たちも\n元気に働いてくれて\nお客さんからの評判も\n上々にゃ～♪", 1, 7061, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125101, "カフェのカウンターに\nファントムキャッツの\n目安箱を置こうかにゃ", 1, 7062, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125101, "あんこもコーヒーも\n原料は豆にゃ～\nそりゃ相性いいに\n決まってるにゃ♪", 1, 7063, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125101, "あいつの為に\nタマキ特製ブレンドを\n開発したいにゃ♪", 1, 7064, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125101, "あいつと働いてると\nなぜかあっという間に\n閉店時間が\n来ちゃうのにゃ～", 1, 7065, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125101, "あいつといると\n新しいアイデアが\n溢れるほど\n出てきちゃうのにゃ♪", 1, 7066, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125101, "キミがコーヒーを\n淹れてくれたのにゃ？\nにゃはは、常温で\n猫舌にも優しいにゃ♪", 1, 7067, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125101, "料理が上手くなったら\nカフェご飯メニューも\n充実させたいにゃ～", 1, 7068, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125101, "お店の経営は大変\nだけど、キミとなら\n乗り越えられるって\n確信してるにゃ！", 1, 7069, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125101, "ん？　この制服\n似合ってるにゃ？\nにゃはは！　褒めても\n１ルピも出ないにゃ♪", 1, 7070, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125101, "あたしが好きなのは\n猫に好かれる人にゃ♪\n…ん、キミ猫に懐かれ\nまくってるにゃ…", 1, 7071, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125101, "キミの制服姿\nけっこーイケてるにゃ\nあたしがお客さんなら\n通いつめちゃうにゃ♪", 1, 7072, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 125101, "朝は一杯の\nコーヒーから始まる…\nにゃふふん♪\nシャレた朝だにゃ～♪", 1, 7073, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 125101, "お昼はたい焼きと\nコーヒーのセットで\nまったり休憩\nしようにゃ～♪", 1, 7074, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 125101, "コーヒー淹れたにゃ\n星空を見ながら\n二人で飲もうにゃ…♪", 1, 7075, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125101, "ふにゃ～ん…ゴロゴロ\nコーヒーと同じくらい\n癒されるにゃ～…♪", 1, 7077, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125101, "こいつかわいいにゃ～\nうちの猫たちとも\nいい勝負なのにゃ！", 1, 7078, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125101, "ん～いい香りだにゃ～\nコーヒーもそうだけど\n香りの癒し効果は\n抜群にゃ～♪", 1, 7079, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125101, "猫に暖かいものは\nまるまれと言ってる\nようなもんにゃ…♪\nまるまる…", 1, 7080, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 125101, "にゃは～うまいにゃ♪\nカフェの新メニューに\n採用しちゃおう\nかにゃ～♪", 1, 7082, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 125101, "にゃるほど…\n調べれば調べるほど\nコーヒーは\n奥が深いのにゃ…", 1, 7083, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 107101, "ワタシの手料理は\n坊やの口に合うらしい\n胃袋は討ち取ったぞ♪", 1, 7085, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 107101, "胸に再度触れると\n誓約は無効になるのか\n…だと？　鋭い質問だ\n安心しろ。ならん☆", 1, 7086, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125201, "ようやくこの服にも\n慣れてきたかな？\nふふっ、たまには\nこういう服も悪くない", 1, 7087, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125201, "今日も店は好調だが、\n私は軍人だからな\n当初の目的も\n忘れないようにしよう", 1, 7088, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125201, "猫が近くに来ると\nついつい見てしまうな\n仕事が終わったら\n遊んでやるかな♪", 1, 7089, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125201, "コーヒーの淹れ方が\nうまくなってきたな♪\nどうせならお菓子も\n研究してみるか！", 1, 7090, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125201, "利益を考えると\n価格をあげるべきか？\n…いや、利益よりも\n皆の笑顔の方が大事だ", 1, 7091, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125201, "またあいつを\n呼んでしまったな…\n今度、コーヒーでも\n淹れて労ってやるか♪", 1, 7092, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125201, "次に入荷するお菓子は\nあいつの意見を聞くか\nどんな味が好きか\n気になるしな", 1, 7093, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125201, "なに、コーヒーを\n淹れてほしい？\nふふっ♪\n仕方のない奴だな！", 1, 7094, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125201, "…そうだ。\nコーヒーと乾パンは\n合うのではないか？\n今度試してみよう！", 1, 7095, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125201, "コーヒーもいいが、\nココアも悪くないぞ♪\n貴公も一緒にどうだ？", 1, 7096, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125201, "魔法少女に比べれば\n大人しい服だろう？\nだが、カフェには\nこの服でバッチリだ！", 1, 7097, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125201, "貴公はコーヒーを\n飲めるのかっ？それは\nうらやましいな…", 1, 7098, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125201, "貴公の働きぶりには\n目を見張るものがある\nその姿は、その…\n結構かっこいいぞっ！", 1, 7099, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 125201, "さぁ、今日も一日、\n頑張るぞっ！\n気合いを入れて\n開店準備だっ！", 1, 7100, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 125201, "昼は稼ぎ時だからな！\n荒波を乗り切るため\n貴公も一緒に\n頑張ってくれるか？", 1, 7101, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 125201, "ふあぁ…もう夜か\n今日は頑張ったからな\nはやめに休むとしよう", 1, 7102, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125201, "はふぅ～…\nたまにはこうして\nゆったりするのも\n悪くないな…", 1, 7104, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125201, "猫もかわいいが、\nこいつもなかなか…\n見ていると、\n撫でたくなるな♪", 1, 7105, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125201, "これは良い香りだな\nふむ、コーヒーも\nより香り高いものが\n作れるよう試すか！", 1, 7106, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125201, "ふぅ～…\n体を温めなければ、\nすぐに動けないからな", 1, 7107, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 125201, "これもうまいが、\n私はお菓子も…\nいや、食べすぎは\n体に良くないな", 1, 7109, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 125201, "…書物は参考になるな\n次はこのやり方を\n試してみるかな", 1, 7110, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120001, "愛と希望の魔法少女！\n弟くんの平和は\nお姉ちゃんが守るよ♪", 1, 7112, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120001, "魔法少女の戦い方を\n見せてあげる！\nお姉ちゃんチョップ♪", 1, 7113, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120001, "闇の力が高まってる…\n早く弟くんを\nギュ～ってしないと！", 1, 7114, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120001, "ミニ弟くんを\n部屋中に呼び出して\n弟くんハウスに～♪", 1, 7115, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120001, "決めポーズの練習でも\nしよっかな…\nはい、ポーズ♪", 1, 7116, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120001, "魔法少女の力で\n弟くんのそばに\n瞬間移動したいなぁ", 1, 7117, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120001, "禁じられた必殺技\nその名も──\n闇のお姉ちゃん頭突き", 1, 7118, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 120001, "魔法少女な\nお姉ちゃんに\n興味津々だねっ？\n全部教えてあげるね♪", 1, 7119, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120001, "闇のお姉ちゃんとして\n世界を弟くんのものに\n…な～んて冗談冗談♪", 1, 7120, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120001, "弟くんって魔法少女に\nなりたがってたよね？\n…うん。応援するね！", 1, 7121, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120001, "サインが欲しいの？\nふふっ、任せて！\n愛する弟くんへ、と♪", 1, 7122, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 120001, "ふんふふ～ん♪\n魔法少女お姉ちゃんの\nテーマソング完成っ♪", 1, 7123, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 120001, "えへへ、弟くんからの\n愛と希望を感じるよ…\n更に変身しちゃうぞ♪", 1, 7124, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 120001, "弟くんの朝を魔法で\n守りに来たよ！\n希望の朝だねっ♪", 1, 7125, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 120001, "弟くんのお昼を魔法で\n守りに来たよ！\n愛のお弁当をどうぞ♪", 1, 7126, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 120001, "弟くんの夜を魔法で\n守りに来たよ！\nここからは闇の時間♪", 1, 7127, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 120001, "魔法少女の一休み♪\n愛と希望と…闇の力が\n回復していくね～♪", 1, 7129, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 120001, "かわいいかわいい♪\nお姉ちゃんの魔法で\n増やしちゃうぞ…♪", 1, 7130, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 120001, "この香り…\n弟くんと同じ…\nくんくん、くんくん…", 1, 7131, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 120001, "体を温めておいて…\nこの熱を、ぜ～んぶ\n弟くんに伝えるよ…！", 1, 7132, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 120001, "ミニ弟くんを呼ぶには\nいっぱい食べないと…\n魔法ってお腹減るから", 1, 7134, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 120001, "弟くんの平和を守る…\nそのためには\nチェックチェック…♪", 1, 7135, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125301, "この服も、最初は\n慣れなかったのですが\n今ではすっかり\n体に馴染んでおります", 1, 7137, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125301, "自然を守るためにも\nお勉強は欠かせません\n日々精進でございます", 1, 7138, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125301, "採取した薬草を\n調合しておきましょう\nいつ必要になるか\n分かりませんからね", 1, 7139, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125301, "合宿は過酷でしたが\nおかげでちょっと\n力持ちになったような\n気がいたします…♪", 1, 7140, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125301, "わんわんのほうの\n主さまに、ごはんを\nあげる時間ですね…", 1, 7141, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125301, "もっともっと訓練や\nお勉強を重ねて\n主さまのお役に\n立たなくては…", 1, 7142, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125301, "おや、そろそろ\nわんわんのほうの\n主さまをお散歩に\n連れていく時間です", 1, 7143, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125301, "主さま、よろしければ\nお散歩に行きませんか\n今日の森は穏やかで\n過ごしやすいかと", 1, 7144, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125301, "わんわんのほうの\n主さまも、今では\n【美食殿】の大切な\n仲間でございます", 1, 7145, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125301, "森には危険が\nいっぱいでございます\nケガをなさらないよう\nお気をつけください", 1, 7146, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125301, "もし森や動物のことで\n気になることがあれば\n気軽にお尋ねください", 1, 7147, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125301, "森へ行く前には\n必ず装備の点検を\nお願いいたします\n約束ですよ、主さま？", 1, 7148, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125301, "森の中で主さまたちと\nゆったり過ごす時間は\nわたくしにとって\n大切なひとときです", 1, 7149, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 125301, "おはようございます\n朝ごはんをしっかり\n食べて、お勤めに\n備えましょう", 1, 7150, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 125301, "わんわんのほうの\n主さまにおやつを\nあげる時間です\nどうぞ、召し上がれ", 1, 7151, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 125301, "どれだけ野営に\n慣れたとしても\nベッドで眠れるなら\nそうしたいところです", 1, 7152, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125301, "こちらなのですが\nわんわんのほうの\n主さまもお気に入りの\nようです", 1, 7154, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125301, "わんわんのほうの\n主さまにも、こうした\n玩具を与えるべき\nでしょうか…？", 1, 7155, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125301, "心地よい香りです\n次は、自然由来の\n香料も試しましょう", 1, 7156, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125301, "暖をとるのは\n野営の時などに\nとても大事なことで\nございますね", 1, 7157, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 125301, "食べられる時に食べて\nおくのも、大自然では\n重要でございます", 1, 7159, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 125301, "より知識を蓄え\n自然の力を活用できる\nようにいたしましょう", 1, 7160, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100901, "今回の「冥風戦記」も\nなかなかよく書けたぞ\nふふっ、シグルドも\n喜んでくれるかな？", 1, 7162, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100901, "貴様のためならば\n我が真なる魔力\nいつでも\n解き放とうぞ！", 1, 7163, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125401, "ニャットちゃんに\nブラッシングを\nしてあげようかな", 1, 7164, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125401, "ふふ…\nこれがレンジャーの\n制服ですか", 1, 7165, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125401, "そろそろ\nパトロールの\n時間だ…", 1, 7166, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125401, "魔物について\nもっと知りたいな\n本を探しに行こう", 1, 7167, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125401, "レンジャーになって\n体調を崩す日が\n減った気がします…", 1, 7168, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125401, "本だけじゃわからない\n世界のことを、\nもっと知りたいです…", 1, 7169, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125401, "こんな私にも\nできることはある…\nそう信じています", 1, 7170, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125401, "あの…良かったら\n一緒に見回りへ\n行きませんか？", 1, 7171, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125401, "またあなたと一緒に\nレンジャーの仕事が\nできたらうれしいです", 1, 7172, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125401, "けほっこほっ\n…だ、大丈夫です\nたまに出るだけなので", 1, 7173, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125401, "ニャットちゃん\nあなたにも\n懐いてきましたね", 1, 7174, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125401, "ニャットちゃんと\n散歩に出かけようかな\n…あの人も誘って", 1, 7175, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125401, "あなたと肩を並べて\nレンジャーの仕事が\nできてうれしいです…", 1, 7176, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 125401, "おはようございます\nレンジャー・シオリ\n元気に出勤します", 1, 7177, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 125401, "ニャットちゃんにも\nお昼ごはんを\n用意しなくちゃ", 1, 7178, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 125401, "明日は仕事だから\n早起きしなくちゃ\nおやすみなさい", 1, 7179, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125401, "ふぅ…仕事で\n走り回ったので\n少し休憩します", 1, 7181, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125401, "仕事が終わった後に\nこうして触れていると\n癒されますね…", 1, 7182, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125401, "森の香りがします…\n今日はよく\n眠れそうです…", 1, 7183, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125401, "ニャットちゃんと\n同じぐらい\n暖かいです…", 1, 7184, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 125401, "レンジャーの仕事を\nしていると\nお腹が空きます", 1, 7186, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 125401, "どんなに忙しくても\n読書だけは\nやめられませんね", 1, 7187, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125801, "兄様のお留守の間\nわたくしが、兄様の\n御家を守ります！", 1, 7189, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125801, "に、に、兄様の御家…\n招待いただいた以上は\n失礼のないよう\n振る舞わなくては…！", 1, 7190, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125801, "ふふっ…兄様の素敵な\nお人柄が、家中から\nにじみ出ています…♪", 1, 7191, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125801, "靴を脱がずに\n御家に上がるのに\n慣れていなくて…あ！\n飛べばいいのでは…！", 1, 7192, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125801, "ここに花を生けたら\n華やぎそうですね\n兄様はお花…\n好きでしょうか…？", 1, 7193, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125801, "ずっとここにいたい…\nなどと言ったら\n兄様を困らせて\nしまいますよね…", 1, 7194, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125801, "お料理などを作って\n兄様を出迎えられたら\nいいのに…料理の腕も\n磨かなくては…！", 1, 7195, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125801, "このような姿で\n失礼しております…！\nネ、ネズミがいたので\nつい本気を…！", 1, 7196, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125801, "おかえりなさいませ\nお忙しい御身をどうぞ\n癒してくださいね", 1, 7197, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125801, "兄様の話し相手…！？\nど、どうしましょう…\nわたくしには面白い\n話なんて…はうぅ…！", 1, 7198, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125801, "なぜか、動物などに\n好かれる体質で…\n迷い猫捜しのバイトの\nお役に立てますか？", 1, 7199, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125801, "に、兄様の家に来たら\n『添い寝』をするのが\n礼儀とリアさんが…\n本当…でしょうか？", 1, 7200, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125801, "わたくしはここに\nいて…よいのですね？\n『仲間』として…", 1, 7201, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 125801, "朝は得意なんです\nもし兄様が苦手なら\nわたくしが起こして\n差し上げますよ", 1, 7202, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 125801, "兄様…昼餉を作って\nみたのですが、失敗\nしてしまいました…", 1, 7203, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 125801, "ふあぁ…\nはっ！　お…\nお恥ずかしい所を…！", 1, 7204, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125801, "こ、これは…！\n人様の御家なのに\nつい…駄目になって…\nしまいますぅ…！", 1, 7206, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125801, "と、飛びかかっては\nこない…ですよね…？", 1, 7207, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125801, "すぅ…はぁ…\nこのかぐわしさ\n心が洗われます…", 1, 7208, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125801, "なんて暖かい…\n兄様の優しくお広い\n御心のようです…♪", 1, 7209, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 125801, "こんなに食べていたら\nはしたない…\nでしょうか？", 1, 7211, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 125801, "兄様の本棚はまるで\n知識の宝庫ですね\n己の世間知らずぶりを\n実感してしまいます", 1, 7212, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125501, "次の冒険に向けて\n騎空士弁当を作ろっと\nまずはおにぎりから！", 1, 7214, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125501, "魔法の修行しなくちゃ\nもっともっと頼れる\n団長になりたいから！", 1, 7215, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125501, "ビカラちゃんを\n遊びに誘いたいけど…\nどういう場所が\n好きなんだろう？", 1, 7216, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125501, "仕事を真面目に\nしっかりこなすことが\n騎空艇への\n一番の近道だよね", 1, 7217, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125501, "ここにいる人の中で\n騎空団に興味ある人\nいたりしないかな…？", 1, 7218, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125501, "これまでも想い出は\nいっぱいできたけど…\nこれからもたくさん\n想い出を作りたいな！", 1, 7219, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125501, "仲間がいるから\n騎空士は限界だって\n超えられるんだね", 1, 7220, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125501, "この衣装\n魔法使いらしいでしょ\n服に着られないよう\n頑張らなくちゃね！", 1, 7221, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125501, "あなたとなら\nどんな相手だって\n怖くなんてないよ！", 1, 7222, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125501, "カリンさんから\n新しい依頼が来たよ！\n一緒に頑張ろ！", 1, 7223, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125501, "操舵士になるのは\nずいぶん難しそう…\nいつか頼れる仲間が\n見つかるといいな", 1, 7224, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125501, "手合わせしたいの？\nいいよ、たまには\n団長らしいところ\n見せてあげるね！", 1, 7225, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125501, "まだ二人きりだけど…\nこの騎空団は\n私の宝物だよ！", 1, 7226, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 125501, "おはよう～…\n昨日は遅くまで\n魔法の研究してて…\nふわぁ…眠いなぁ…", 1, 7227, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 125501, "さてと、お昼休み！\n紅茶淹れてくるから\n一緒に飲もうよ！", 1, 7228, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 125501, "遅くまでお疲れさま！\n無理しない程度に\nお互い頑張ろう！", 1, 7229, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125501, "魔法の研究は\n一旦、休憩！\n頭を休めなくちゃ", 1, 7231, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125501, "魔法みたいに\nかわいい！\n騎空団に入る？\nなんてね！", 1, 7232, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125501, "いい匂い…\nそれになんだか\n集中力が増したかも", 1, 7233, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125501, "体だけじゃなくて\n心までポカポカする…\nなんだか魔法みたい", 1, 7234, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 125501, "いろいろ考え事したら\nお腹が空いちゃった！\nいただきまーす！", 1, 7236, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 125501, "『摂理の彼方へ\n至るには…』\n…この魔導書\n勉強になるなあ", 1, 7237, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125701, "こちらの服を着ると\n冒険に出かけたく\nなっちゃいますね", 1, 7239, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125701, "冒険者の心得その一\n準備はできる限り\nしっかりとこなすべし\nなんてどうでしょう？", 1, 7240, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125701, "調合に必要な素材は\nあれとこれとそれと…\n時間があるとついつい\n考えちゃいますね", 1, 7241, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125701, "せっかくですから\nより良い素材を\n自分で集めたいですね", 1, 7242, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125701, "このポーションが\nみなさんの役に立つと\nうれしいですね", 1, 7243, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125701, "冒険者の心得その二\n危なくなった時は\nまず逃げるべし！\n大事なことですね", 1, 7244, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125701, "あの人の冒険は\nいつも楽しそうで\n元気がもらえます", 1, 7245, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125701, "ぜひおすすめしたい\n依頼があるんです\nこちらをどうぞ♪", 1, 7246, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125701, "ほしいお薬があれば\n錬金術で作って\nさしあげますよ", 1, 7247, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125701, "依頼で気になる\nところがあれば\n何でもご確認ください", 1, 7248, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125701, "このギルドハウスに\n錬金術の実験設備を…\nえ？　だめですか？", 1, 7249, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125701, "ポーションをどうぞ\n大丈夫、効果は\n確認済みですので♪", 1, 7250, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125701, "次はどんな冒険に\n行かれるんですか？\nいつでもお供しますよ", 1, 7251, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 125701, "今日もいい一日に\nなりそうですね\n冒険日和といった\nところでしょうか", 1, 7252, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 125701, "いい時間ですし\nランチにしませんか？\n次の冒険の\n作戦会議も兼ねて♪", 1, 7253, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 125701, "夜も遅いですし\n明日の冒険のため\n今日は早く寝ましょう", 1, 7254, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125701, "ふう…やはり冒険は\n事務仕事と違いますね\n少し休憩を…", 1, 7256, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125701, "冒険は緊張の連続です\nかわいい物で\n心を癒しましょう", 1, 7257, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125701, "ふむ、この香りは…\n素晴らしいバランスで\n調合されていますね", 1, 7258, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125701, "この火力…\n錬金術の調合に\n使えそうですね", 1, 7259, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 125701, "冒険の際は食べられる\n時に食べておく！\n…とペコリーヌさんも\nおっしゃってましたね", 1, 7261, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 125701, "ふむふむ…なるほど\nこんな調合も\nあるのですね…\n奥が深いです", 1, 7262, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 106101, "アイツどこ行った…？\n戦うならアタシも\n連れてけよなー！", 1, 7264, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 106101, "今日も悪いヤツ退治…\nの前に腹ごしらえだ！\nなんか食べに行くぞ！", 1, 7265, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125601, "みんなー！\nギルドハウスで\nぼくと握手！", 1, 7266, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125601, "種族も年齢も違うけど\nみんな楽しそうだね！\nやっぱりここはぼくの\nゆ～とぴあさ！", 1, 7267, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125601, "ハハッ！　まるで\nパーティー会場だね！\nこれは楽しませがいが\nあるってものだよ！", 1, 7268, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125601, "やぁみんな！\nご利益満点のネズ耳は\nいかがかな？\nひとつ３ルピだよ！", 1, 7269, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125601, "ここにいれば\n遊び相手に困らないね\nかしこみかしこみ～！", 1, 7270, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125601, "どこか遊びに\n行こうかな♪\nそうだ、あの人にも\n声をかけてみよう！", 1, 7271, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125601, "あの人といると\nぼくまで楽しくなって\nくるんだよね！\n毎日パレード気分さ！", 1, 7272, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 125601, "ネズ耳の在庫が\n切れたんだ！\n作るのを\n手伝ってくれるかい？", 1, 7273, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125601, "ん？　握手かい？\nいいとも！\nいつでも何度でも\nしてあげるよ！", 1, 7274, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125601, "この衣装に興味が\nあるのかい？\nきみ、なかなか\n見る目があるね！", 1, 7275, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125601, "しばらくここに厄介に\nなるつもりなんだ\nあとで街を案内\nしてくれるかい？", 1, 7276, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 125601, "とっておきのチーズ\nきみと半分こだ！\nみんなには内緒だよ？", 1, 7277, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 125601, "きみはいてほしい時に\nいつもそばに\nいてくれるね\n本当にかしこみだよ！", 1, 7278, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 125601, "やぁ、おはよう！\n気持ちのいい朝だね\n今日も楽しくネズ耳\n売りにはげもうか！", 1, 7279, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 125601, "お昼だね！\nおいしいチーズを\n持ってきたから\nぼくと半分こだ！", 1, 7280, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 125601, "今日も一日\n楽しかったね！\nいい夢が見られること\n間違いなしさ！", 1, 7281, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 125601, "ぼくにも休息は\n必要さ！\nリラックス〜\nリラックス〜♪", 1, 7283, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 125601, "ぼくのぬいぐるみも\n作って売ろうかな…\n修繕費も一気に\n集まるかもね！", 1, 7284, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 125601, "いい香りだね！\nトーゴクのお香に\nよく似てるよ", 1, 7285, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 125601, "はぁ…温まるねぇ…\nそうだ、この火で\nチーズでも炙って\nみようかな", 1, 7286, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 125601, "うーん、この味…\nトーゴクを思い出すよ\nどれ、もう一つ\nいただこうか！", 1, 7288, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 125601, "これはランドソルの\nガイド本だね！\nありがたく参考に\nさせてもらうよ！", 1, 7289, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126201, "まさか、わらわが\nこのような装束を\n纏う日がくるとはな…\n感慨深いものじゃ", 1, 7291, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126201, "わらわの美貌で\nこのギルドハウスも\nより華やかに\nなったようじゃな", 1, 7292, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126201, "つまらぬ戦いで\n晴れ着を汚しとうない\n今日は一日\nゆっくりするかの", 1, 7293, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126201, "今日はわらわが食事の\n準備をして、あやつの\n帰りを待ってやろう\n献立は何がよいかの…", 1, 7294, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126201, "ユキといったか…\n掴みどころのない\nやつじゃが、根性は\n認めてやるかの", 1, 7295, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126201, "こうして、仲間と\nのんびり過ごす時間も\n悪くないのう♪", 1, 7296, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126201, "あやつを誘って\n月を見に行こうかの\n夜が楽しみじゃ♪", 1, 7297, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126201, "いかなる衣装で\nあろうとも、わらわは\n完璧に着こなして\nしまうのじゃ♪", 1, 7298, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126201, "なんじゃ？\nわらわの姿に\n見惚れておったとな？\nす、素直でよいのじゃ", 1, 7299, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126201, "吸血鬼であるわらわに\n相応しい装束じゃろう\nあとは真っ赤な血を\n添えたいのう…", 1, 7300, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126201, "お主、疲れておるな？\nわらわが癒してやろう\nほれ、近う寄るのじゃ", 1, 7301, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126201, "お主がおるだけで\nギルドハウスが\n賑やかになるのう\nある種の才能じゃな♪", 1, 7302, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126201, "眷属よ、風呂にするか\nご飯にするか\nそれとも、わらわに…\nいや、忘れるのじゃ", 1, 7303, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 126201, "朝一番、わらわに\n会いたかったとな…\n眷属よ\n褒めてつかわすぞ♪", 1, 7304, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 126201, "眷属よ、わらわが\n食事を用意したぞ\n忌々しい太陽に負けず\n頑張るのじゃ", 1, 7305, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 126201, "眠るには惜しい夜じゃ\n眷属よ、もうしばらく\nわらわの傍に\nおるがよい", 1, 7306, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126201, "ふかふかじゃの♪\n今度あやつにも\n座らせてやろう", 1, 7308, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126201, "愛いやつじゃ\n少しばかり、わらわが\n遊んでやろうかの", 1, 7309, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126201, "気品ある香りじゃのう\nわらわの古城にも\n一つ欲しいところじゃ", 1, 7310, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126201, "よいぬくもりじゃ…\n忌々しい太陽とは\n大違いじゃの", 1, 7311, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 126201, "味付けの参考に\nあと、もう一つだけ…\n止まらんのじゃ…", 1, 7313, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 126201, "吸血鬼の弱点について\n記されておるのか…\nふむ、眠気が\nさめてしもうたぞ", 1, 7314, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100801, "このままボクが美しく\nなり続けても、彼なら\nきっと隣にいてくれる\nはずだよね！", 1, 7316, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100801, "もっとそばに来なよ\nボクの隣は\nキミのための\n特等席なんだから", 1, 7317, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126301, "ドレスの影響かな…\n前より黒や赤が\n好きになったかも", 1, 7318, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126301, "ボクがいるだけで\nここがぐんっと\n華やかになったね♪", 1, 7319, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126301, "ボクを描いた絵画\n今度持ってきて\n飾ってあげようかな", 1, 7320, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126301, "ボクはボクに\n魅了されっぱなしさ♪\nまさに魔性の存在だよ", 1, 7321, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126301, "美のアドバイスを\n聞きたい人いるかい？\nボクがもっともっと\n輝かせてあげるよ♪", 1, 7322, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126301, "あの人、今度はボクを\nどう褒めるだろう？\nもし語彙が尽きても\n許してあげよっと♪", 1, 7323, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126301, "あの人に似合うコーデ\n考えてあげよっと\n案外、なんでも\n着こなしそうだよね♪", 1, 7324, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126301, "吸血鬼になるなんて\nやっぱりゴメンだね！\n永遠の美よりも\n大事なことがあるのさ", 1, 7325, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126301, "お肌にいいものは\n大体好きだよ\nトマトジュースとかね", 1, 7326, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126301, "銀のアクセサリーが\nボクは大好きでね\nいつでもプレゼント\n待ってるよ♪", 1, 7327, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126301, "イリヤみたいにお城に\n住んでみたいんだ♪\n美を統べる者に\n相応しいでしょ？", 1, 7328, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126301, "今のボクがカップを\n触ると壊しちゃうんだ\nだからほら、キミが\nボクに紅茶を飲ませて", 1, 7329, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126301, "キミも美を磨けば\nもっと輝けるはずさ！\nボクがメイクとか\nしてあげようか？", 1, 7330, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 126301, "んん、まだ眠い…\n美のためにも、\nもうひと眠りするよ…", 1, 7331, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 126301, "ボクにぴったりな\n日傘を買ったんだ♪\n散歩にでも行こうよ！", 1, 7332, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 126301, "夜こそボクの時間！\nさぁ、あまねく人々へ\nボクの美を広めよう！", 1, 7333, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126301, "リラックスは大事だよ\n心の余裕こそが\n美の秘訣だからね♪", 1, 7335, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126301, "キミもボクの\n美の虜にしてあげる♪\nうれしいでしょ？", 1, 7336, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126301, "人々を魅了するほどの\n魔性の香り…\nボクにふさわしいね♪", 1, 7337, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126301, "冷えは美の大敵さ\nボクのように\n美しくなりたいなら\n体を温めるべきだよ", 1, 7338, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 126301, "食事をするボクの\nなまめかしさ…♪\n吸血鬼すらも\nドキッとするだろうね", 1, 7340, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 126301, "吸血鬼って\n美しい存在だよね…\nボクほどではないけど", 1, 7341, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126401, "ここがナイトさんの\nお家なんですねーっ！\n雰囲気そっくりっ！", 1, 7343, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126401, "ドアも壁もお菓子じゃ\nないんですよねっ？\n不思議ですねーっ♪", 1, 7344, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126401, "失礼のないように\n気をつけないとっ！\nライラエルさまとも\n約束してますからっ！", 1, 7345, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126401, "向こうに帰るころには\nお土産話がいっぱい\nできてるといいな～♪", 1, 7346, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126401, "戦闘用のアームの他に\n観光用のアームも\nあったらいいのにな～", 1, 7347, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126401, "ナイトさんの充填と\nライラエルさまの充填\nどっちも違って\nどっちも大好きです♪", 1, 7348, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126401, "…ナイトさんが\nマスターになって\nくれたら…えへへっ\nなんでもないですっ！", 1, 7349, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126401, "お手伝いだったら\n任せてくださいっ！\nお家の移転とか\nしちゃいますかっ？", 1, 7350, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126401, "こっちに来るときは\nポポおじいさんが\nしっかりメンテして\nくれるんですよっ♪", 1, 7351, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126401, "お家の中にいるのも\nいいですけど、お外も\n気になります～っ！\nお出かけしましょう♪", 1, 7352, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126401, "ヴォルガーノさんに\nそっくりの子猫さんを\n見かけたんですっ！\nご親戚ですかねっ？", 1, 7353, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126401, "ここだけの話ですよ？\n…実はケーブルって\n触られると、ちょっと\nくすぐったいんですっ", 1, 7354, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126401, "このお家の持ち主は\nナイトさんですよね？\nそれじゃここでは\nご主人さまですねっ", 1, 7355, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 126401, "おはようございますっ\n今日もフルパワーで\nがんばりましょうっ♪", 1, 7356, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 126401, "お昼の時間ですねっ！\nランチの充填、\nお願いしまーすっ♪", 1, 7357, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 126401, "そろそろ眠らなくて\n大丈夫ですかっ？\n夜ふかしさんなら\nお付き合いしますよ♪", 1, 7358, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126401, "エネルギーの消費を\n抑えるのも大切\nですよね～っ", 1, 7360, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126401, "わたしが触っても\n平気なんて…！\nかわいくて頑丈っ！\n好きですっ！", 1, 7361, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126401, "センサーによると…\nこの香りはいい香りに\n該当するみたいですっ\nなるほどーっ！", 1, 7362, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126401, "あったかいのって\nいいですよねっ！\nみなさんうれしそう\nですしっ♪", 1, 7363, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 126401, "これがこっちの世界の\nごはんなんですねっ\nおいしい気がします！", 1, 7365, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 126401, "そーっとそーっと…\n本を破かないように…\n書いてある内容より\n難しいです～っ！", 1, 7366, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126701, "ウィスタリア家の\nプライベートビーチも\nご案内しますわね♪", 1, 7368, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126701, "世界を巡る\nリゾートクルーズも\nご用意できますわよ", 1, 7369, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126701, "避暑地に専用の\n別荘がありますの\n今度ご招待しますわね", 1, 7370, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126701, "日焼けは乙女の\n天敵ですわね\n備えはしっかりと…", 1, 7371, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126701, "線香花火だけでは\n物足りませんわね\nもっとドカーンと…", 1, 7372, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126701, "ギルド活動も全力で！\nバカンスも全力で！\nそれが私ですわ！", 1, 7373, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126701, "私のセンスで選んだ\n特注の水着ですもの\n似合って当然ですわ♪", 1, 7374, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126701, "ご希望ならすぐにでも\n夏の飛空艇遊覧ツアー\nへご招待しますわ♪", 1, 7375, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126701, "夏の賑やかな\n雰囲気は好きですが…\n暑さには参りますわね", 1, 7376, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126701, "日の光を浴びるほどに\n輝きを増す夏の私！\n今日も絶好調ですわ♪", 1, 7377, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126701, "夏祭りに協力するなら\n資金と人材、どちらも\n用意しないとですわね", 1, 7378, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126701, "あなたさま…その…\nそうも見つめられると\n照れてしまいます…", 1, 7379, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126701, "タマキさんの働く\n海の家も訪れて\nみたいですわね♪", 1, 7380, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 126701, "港の市場が\n賑わう頃ですわね！\n私たちも参りますわよ", 1, 7381, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 126701, "冷たい飲み物でも\nご一緒にいかが？\nうちのシェフに用意\nさせてありますの", 1, 7382, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 126701, "まだもう少し…\nこの夏の夜を\nあなたさまと…", 1, 7383, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126701, "あなたさまも\n良ければこちらへ\nいらっしゃいな♪", 1, 7385, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126701, "夏に合わせた装いに\n替えてみるのも\n楽しそうですわね♪", 1, 7386, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126701, "この夏めいた爽やかな\n香り…期間限定で\n売り出したいですわ", 1, 7387, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126701, "く…我慢です…\nこの暑さに耐えてこそ\n美しさにも磨きが…", 1, 7388, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 126701, "暑い季節に働く\nみなさまへ差し入れも\n必要ですわね", 1, 7390, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 126701, "夏のトレンドを\n押さえておくのも\n大事ですわね", 1, 7391, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 126701, "上手くいけば\nヒット商品になる\nポテンシャルを\n感じますわね…！", 1, 7393, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 126701, "この極上の肌触り…\n言い値で買いますわ\n一体おいくらですの？", 1, 7394, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126601, "せっかくのバカンス…\n自由に羽を\n伸ばすとしましょう", 1, 7395, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126601, "たとえどのような\n秘密を持とうとも\n私には筒抜けですよ", 1, 7396, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126601, "子供だましの怪談に\n関心はありませんが…\n霊には興味があります\n観察対象として、ね", 1, 7397, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126601, "彼の絵日記…\nあとでゆっくりと\n読ませてもらいます", 1, 7398, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126601, "セミの鳴き声に入道雲\nそしてクリームソーダ\nこれでこそ夏ですね", 1, 7399, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126601, "夏休みの宿題でも\n作るとしましょうか\n彼の限界を少し超えた\n負荷を与えるには…", 1, 7400, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126601, "彼と過ごす夏を\n特別なものにしたい…\nそれが私の望みです", 1, 7401, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126601, "夏だからといって\n浮かれすぎないように\n節度を持って\n楽しみなさい？", 1, 7402, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126601, "ああ見えてもマサキは\n私のプリンセスナイト\nあまり侮られては\n困りますよ？", 1, 7403, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126601, "私の秘密ですか？\nまんじゅうが怖い…\nというのはどうです？", 1, 7404, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126601, "ここは誰かの秘密を\n盗み聞くのに\n最適な場所ですね\n…ジョークですよ", 1, 7405, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126601, "一緒に風鈴の音を\nのんびり楽しもう…？\n…別に構いませんよ\n貴方とならば", 1, 7406, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126601, "貴方に約束しましょう\n決して忘れられない\n夏にしてあげますよ", 1, 7407, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 126601, "早起きとは感心ですね\nでは一緒に\n体操でもしましょうか", 1, 7408, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 126601, "昼食は流しそうめん？\n非効率的ですね\n…食べないとは\n一言も言っていません", 1, 7409, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 126601, "…貴方が望むならば\nいくらでも怖い話を\nしてあげますが…\nいかがですか？", 1, 7410, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126601, "七冠にも休息は\n必要なのです\nしばらくここで\n休ませてもらいますよ", 1, 7412, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126601, "なかなか気に入った…\nというのは\n誰にも秘密です", 1, 7413, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126601, "薫風を思わせる\nこのさわやかな香り…\n悪くないセンスですね", 1, 7414, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126601, "夏だからこそ\n冷え対策は\n重要なのです\n…とはいえ暑いですね", 1, 7415, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 126601, "私がどのような味が\n好きなのかは…\n当然秘密です", 1, 7417, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 126601, "夏を舞台にした\n小説は数多くあります\n彼が好きそうな本を\n何冊か見繕いましょう", 1, 7418, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 126601, "見た目も手触りも\n完璧に覚えました\nいつでも模倣できます", 1, 7420, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 126601, "ふむ…夏らしい\nクッションですね\nナイトプールに\n映えそうです", 1, 7421, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105401, "レイ様も格好良いけど\n騎士さんもなかなか…\nって、今の聞こえて\nませんよねっ？", 1, 7422, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105401, "騎士さんはシンプルな\n服が似合いそうです！\n今度、一緒に\n見に行きましょう♪", 1, 7423, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126801, "最初はどうかな～って\n思ったけど…\n水着姿でも案外\n問題ないのよね…", 1, 7424, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126801, "水着で飲みすぎると\nお腹が冷える、って\n言うけど…麦しゅわ\nなら問題ないわね！", 1, 7425, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126801, "暑ければ暑いほど\nその後の麦しゅわは\nおいしくなるのよ…", 1, 7426, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126801, "しっかりと仕事を\nこなしてから、\nしっかりと麦しゅわ！\nやっぱりこれね！", 1, 7427, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126801, "ふふっ、デザインも\n着心地も最高ね\n奮発して用意して\n正解だったわ♪", 1, 7428, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126801, "伝説の麦しゅわ、\n飲めるものなら\nまた飲みたいわね…", 1, 7429, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126801, "二つ名に恥じない\n聖騎士として\n私…飲むわ！", 1, 7430, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126801, "この格好なら水に\n濡れても大丈夫！\nなんだけど、さすがに\n書類は濡らせないわね", 1, 7431, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126801, "そうやってじっと\n見られると…ちょっと\n恥ずかしいわ…", 1, 7432, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126801, "今日もいい天気ね\nふふっ、あとで\nビーチに遊びに\n行きましょ♪", 1, 7433, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126801, "水着姿も慣れると\n案外快適ね\nそうだ、キミも\n水着になっちゃう？", 1, 7434, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126801, "例え水着姿であろうと\n聖騎士としての誇りは\n忘れてないわ！", 1, 7435, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126801, "キミとだったら\n麦しゅわなしでも\n最高の夏のバカンスに\nなると思うわ♪", 1, 7436, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 126801, "仕事終わりの\n麦しゅわのために\n今日も一日\n頑張るわよ♪", 1, 7437, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 126801, "今日のお昼は\nどうしようかしら…\nさ、さすがに昼からは\n自重してるわよ？", 1, 7438, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 126801, "さ、一日の疲れを\n洗い流すために…\n軽～く飲むわよ！", 1, 7439, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126801, "こうやって\n直に触れると\n肌触りの良さが\nよく分かるわ♪", 1, 7441, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126801, "今日もかわいいわね～\nどう？　今度\nお姉さんの一人飲みに\n付き合わない？", 1, 7442, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126801, "いい香りね～…はっ！\n麦しゅわの匂いの\nアロマポットなんて\nあったらよくない？", 1, 7443, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126801, "こんな格好で火に\n当たってると焙られる\nスルメの気分が\nちょっと分かるわ…", 1, 7444, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 126801, "もぐもぐ…うん、\n結構おつまみにも\nむいてそうね～♪", 1, 7446, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 126801, "試験に使った参考書、\nたまには読み返して\nおかないとね", 1, 7447, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 126801, "ふふ♪\nいい手触りね～", 1, 7449, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 126801, "…はふう～…\nゴロゴロするのって\n最高ね～", 1, 7450, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126901, "ん…やっぱり\nこの水着…\n大胆、なんじゃ…？", 1, 7451, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126901, "海でもお世話って、\nできるのかしら…？\n浮き輪代わりになる、\nとか…？", 1, 7452, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126901, "～♪\nふふ…海に行く\n準備も、楽しい…かも", 1, 7453, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126901, "あうぅ…水着で\n歩いてると…お尻…\nすぐ食い込んじゃう…", 1, 7454, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126901, "海では、はしゃいだ\nほうがいいのよね…\nわ、わー、い…♪\n…こうかしら？", 1, 7455, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126901, "あなたとなら…\n楽しい夏の思い出、\n作れる…気がするの…", 1, 7456, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126901, "また来年も…\n再来年も…\nあなたと海へ\n行けますように…", 1, 7457, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126901, "水着の着方を\n練習してるの…\n後ろ、変じゃない…？", 1, 7458, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126901, "海に行く支度…\n進んでるかしら…？\nお手伝い、する…？", 1, 7459, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126901, "ひ、日除けの準備は\n万全よ…\nずっと太陽の下に\nいたら…溶けちゃう…", 1, 7460, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126901, "泳ぐ練習…\nこ、ここでするのは\n…さすがに変？", 1, 7461, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126901, "人混みに入るのは\nまだ不安で…\nでも、あなたといれば\nきっと…大丈夫…", 1, 7462, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126901, "あなたのおかげで\n海の楽しいところを\nたくさん知ったわ…\nありがとう…♪", 1, 7463, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 126901, "お出かけの日の朝は\n早い、のよね…？\nだから、ずっと\n起きていたわ…", 1, 7464, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 126901, "今頃ビーチの日差しは\n強いんでしょうね…\nそ、想像しただけで…\nめまいが…", 1, 7465, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 126901, "ビーチの夜といえば\n花火って、聞いたわ…\nバン、って言わない\n花火…ある…？", 1, 7466, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126901, "素肌にふわふわが…\n気持ちいい…♪", 1, 7468, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126901, "うふふ…\n一緒に海…行く？", 1, 7469, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126901, "ふぅ…♪\n気分まで…\n晴れやかになるわ…", 1, 7470, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126901, "暑いような…\n寒いような…\n不思議な気持ちに\nなるわ…ね？", 1, 7471, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 126901, "泳いだ後には\nいっぱい食べたほうが\n体によさそう、ね…", 1, 7473, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 126901, "ビーチの日陰で\n読書するのも…\n気持ちよさそう…♪", 1, 7474, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 126901, "これで合ってる…？\n遊び方わからなくて…", 1, 7476, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 126901, "ふわふわ…♪\n海の底にいるみたい…", 1, 7477, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127001, "恥ずかしい格好で\n室内を歩くこの快感\nクセになりそうです…", 1, 7478, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127001, "この水着…\nどうしてかポーズを\n決めたくなりますね…", 1, 7479, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127001, "海にはどんな生き物が\nいるのでしょう～\n胸が高鳴ります～♪", 1, 7480, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127001, "夏は暑いですね…\nクウカの体が熱いのも\n夏のせい…ぐふふ…♪", 1, 7481, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127001, "ドＳさんと無人島に\n取り残されて…という\n妄想はかなりイイです", 1, 7482, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127001, "ドＳさんの\n欲望に満ちた眼差し…\nクウカ、受け止めます", 1, 7483, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127001, "海に行くのが\n楽しみです…\nドＳさんと…ぐふふ♪", 1, 7484, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127001, "「もっと過激な水着を\n見せてみろよ」なんて\nいけませんドＳさん…", 1, 7485, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127001, "水着のままで\n踊ればいいのですね…\nドＳさんの望みなら！", 1, 7486, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127001, "ドＳさんの熱い視線を\n感じます…じゅるり…\nもっと見てください…", 1, 7487, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127001, "こんな格好のクウカを\n放っておくなんて…\nさすがですドＳさん！", 1, 7488, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127001, "海で水着といえば\n何かが起きる予感…！\nドＳさんの新たな攻め\n期待していますね…！", 1, 7489, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127001, "恥ずかしいですけど\n頑張っちゃいました\nドＳさん…こういう\n水着、好きですか？", 1, 7490, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 127001, "こんな朝からクウカを\n水着に着替えさせて…\n本当にドＳさんは\n欲望に正直ですね！", 1, 7491, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 127001, "こんな昼間から\nドＳさんがヌルヌルの\n液体をクウカに～！\nえっ…日焼け止め？", 1, 7492, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 127001, "ドＳさん…これから\nクウカと夜の海を\n見に行きませんか？\nきっと綺麗ですよ…♪", 1, 7493, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127001, "ここなら妄想に\n集中できます～♪\nじゅるり～♪", 1, 7495, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127001, "こうもかわいいと\n妄想に登場させるのも\n気が引けます…", 1, 7496, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127001, "クウカを昂らせる\nこの香り…！\nは！　ドＳさんの罠！", 1, 7497, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127001, "体を熱くさせて\n水着を脱がそうと！？\nテクニカルです…！", 1, 7498, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 127001, "クウカの口の中に\nたくさん…♪\n止まりません…！", 1, 7500, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 127001, "読書は心が穏やかに\nなりますね…！\nぐふふ…穏やかに…", 1, 7501, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 127001, "いい乗り心地です…♪\nあっ…普通に楽しんで\nしまいました…", 1, 7503, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 127001, "これは…\n妄想に役立ちそうな\n感触です…！", 1, 7504, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100501, "成長した自分に\nあわせて、もっと\nかっこいいポーズを\n研究するッス！", 1, 7505, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 100501, "これからも一緒に\n最高のヒーローを\n目指していくッスよ♪", 1, 7506, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127101, "お父さん、娘の\n水着姿がそんなに\nうれしいの？", 1, 7507, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127101, "怖い話を子どもたちに\n聞かせたら泣かれて\nしまいました…\n加減が難しいです…", 1, 7508, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127101, "海に行くと父が\n水着の女性ばかり\n追いかけて困ります…", 1, 7509, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127101, "拾った貝殻で\n新しい占いでも\nはじめてみようかな", 1, 7510, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127101, "貝殻を耳に当てると\n波の音が…あっ\nこれはダメです\nよくない声がします", 1, 7511, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127101, "この飲み物、スーッと\n体に沁み込んでくる…\nあの人にも\n教えてあげたいです", 1, 7512, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127101, "強い日差しも騒がしい\n海も苦手なのに\nあの人となら不思議と\n楽しく思えるんです", 1, 7513, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127101, "夏限定、水と氷を\n使った特別な占いは\nいかがですか？", 1, 7514, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127101, "暑い日々にはやはり\n怪談ですね\n厳選フルコースで\nお聞かせします", 1, 7515, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127101, "寒気がする？　それは\n霊ではなくかき氷の\n食べ過ぎですよ", 1, 7516, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127101, "今日の占い、あなたの\nラッキーパーソンは…\n白い水着の女性です", 1, 7517, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127101, "あの、よければ\n日焼け止めを塗って\nもらえませんか？\n背中は届かなくて…", 1, 7518, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127101, "海に行きませんか？\n二人きりで静かな\n砂浜を散歩したいです", 1, 7519, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 127101, "潮騒だけが響く朝の海\n心が落ち着きます\nこれから一緒に散歩に\n行ってみませんか？", 1, 7520, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 127101, "不思議です。どうして\n普通のご飯でも、海で\n食べると特別おいしく\n感じるのでしょう…", 1, 7521, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 127101, "夜の海に行きませんか\n今なら夜光虫が\n見られるかも\nしれませんよ", 1, 7522, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127101, "夏は知らぬ間に体力を\n消耗しています\n適度に休息して\nいきましょう", 1, 7524, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127101, "このぬいぐるみ…\n大丈夫です\n何も憑いては\nいませんよ", 1, 7525, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127101, "ほのかにお線香\nみたいな香り…\nなんだか\n落ち着きます", 1, 7526, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127101, "人魂、鬼火\nセントエルモの火…\n炎を見ていると色々と\n連想してしまいます", 1, 7527, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 127101, "お腹を壊さぬよう\n食い合わせには\nご注意ください", 1, 7529, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 127101, "これはいい\n怪談集ですね…\nつい読みふけって\nしまいます", 1, 7530, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 127101, "ふふ、こういうのに\n乗るとなんだか\n子どもに戻った\nみたいですね", 1, 7532, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 127101, "こ、これは…\nちょっとメルヘン\n過ぎて恥ずかしい\nですね…", 1, 7533, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126501, "本当に素敵なお家…♪\n騎士たちの人柄を\n感じられるようで…\n不思議と落ち着きます", 1, 7534, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126501, "調度品をつい間食…\nという習慣がこちらの\n世界にはないのですね\n…気をつけないと", 1, 7535, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126501, "手土産のお菓子は\nどちらに置けば\nよいでしょうか？", 1, 7536, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126501, "まるでおとぎ話の中に\n入ってしまったかの\nよう…別世界とは\nよく言ったものです", 1, 7537, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126501, "お忍び…ふふっ♪\nときめく響きです\nなんだかお茶目で…\n密やかな印象が", 1, 7538, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126501, "ふわ…\nああ、ついあくびが…\n居心地がよすぎるのも\n困り物ですね…ふふ♪", 1, 7539, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126501, "騎士には不思議な力が\nあるだけでなく\n何者かの加護が\nあるようですね…", 1, 7540, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126501, "この『王家の装備』は\n少し気難しいのです\nわたし以外が触れると\nその…ビリビリっと", 1, 7541, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126501, "子どもたちに新しい\n遊びを教わりました♪\nにーらめっこしましょ\nあっぷっ…ぷ～っ！", 1, 7542, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126501, "実は憧れていたのです\n友人の家を訪ねると\nいう経験に…ふふっ", 1, 7543, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126501, "ぎるどはうす…\nぎるど活動の拠点だと\n管理協会？　の方に\nうかがいました", 1, 7544, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126501, "あなたは運命を\n信じますか？\nわたしは…信じます\nよい運命だけですが♪", 1, 7545, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126501, "もしわたしが本当に\n天使だったとしたら…\nどうされますか？\n…ふふ、冗談です♪", 1, 7546, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 126501, "パンケーキの香りで\n目覚める朝は\nとても良いものですよ\nぜひともお試しを♪", 1, 7547, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 126501, "ひと休みの時間ですね\n食事にお昼寝…\nすべきことを行って\n英気を養いましょう", 1, 7548, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 126501, "そろそろ休まれては？\n眠れないようであれば\n寝物語か子守歌でも\nふふ、遠慮は無用です", 1, 7549, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126501, "すう………はっ！\n眠ってはいませんよ？\nいくら居心地が良い\nからといって…ふわぁ", 1, 7551, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126501, "まあ、あいらしい…♪\nぜひわたしの寝室に\n招待させてください", 1, 7552, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126501, "なんて、かぐわしい…\nオーブンからただよう\n焼き菓子の香りとは\nまた異なる魅力が…", 1, 7553, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126501, "みなで暖をとりながら\n互いに語り笑い合う\n…心休まる時間ですね", 1, 7554, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 126501, "食は心と体に\nゆとりを生みます\n…辛い食べ物の場合？\nそれは、その…", 1, 7556, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 126501, "こちらの世界の知識を\n蓄えておかなければ\nテーブルマナーに\nついてのページは…", 1, 7557, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127201, "新しい色の蛍光ペン\n買ったのよね\nこれで勉強が\n楽しくなるわ～♪", 1, 7559, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127201, "学食のランチ、結構\nおいしいのよね～\n今度ペコリーヌに\n教えてやろうかしら？", 1, 7560, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127201, "ふふふ…扉に黒板消し\n仕掛けてるの…あいつ\n引っかかるかしら？", 1, 7561, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127201, "部活するなら運動部も\n悪くないかもね～\n夕日に向かって\n突っ走ってやるわ！", 1, 7562, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127201, "毎日窮屈な制服着て\n学生も大変ね～\n…ま、かわいいけど", 1, 7563, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127201, "あいつに貸したノート\n変な落書きとか\nしてなかったよね…？\n…その、相合傘とか…", 1, 7564, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127201, "昨日の家庭科で\n作ったクッキー…\nあいつにどうやって\n渡そうかな…？", 1, 7565, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127201, "宿題教えてって？\nしょうがないわね～\nじゃあ一緒に\nやりましょ", 1, 7566, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127201, "あ、明日あたしたち\n日直当番よね？\nめんどい仕事は\nあんたに任せるわ～", 1, 7567, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127201, "寮生活は楽しいけど\n就寝時間が早すぎよね\nいきなり消灯されると\nビックリするわ", 1, 7568, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127201, "校則で禁止だけど、\nにけつでほうき通学\nとか青春っぽくない？", 1, 7569, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127201, "制服って好きな人とも\n自然にお揃いになれて\nいいわよね…って別に\nあんたとって訳じゃ…", 1, 7570, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127201, "あんたネクタイ\n曲がってるわよ～？\nほら直したげるから\nこっち向いて？", 1, 7571, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 127201, "おはよ～！\n今日も元気に登校よ！\n忘れ物ないようにね！", 1, 7572, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 127201, "午前の授業疲れたわ～\nしっかりお昼を食べて\n午後も頑張るわよ！", 1, 7573, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 127201, "うう…学校の怪談\n思い出しちゃった…\n…別に怖くないけど！", 1, 7574, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127201, "ふかふか～\n…気持ち良すぎて\n制服のまま\n寝ちゃいそうね…", 1, 7576, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127201, "かわいくて癒されるわ\n寮に持ち込んでも\n大丈夫かしら…？", 1, 7577, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127201, "集中力の高まる香りで\n勉強の効率爆上がりね\n次のテストで\nいい点取るわよ～！", 1, 7578, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127201, "うう、ちょっと冷えて\n来たわね…\nスカートの下に\nジャージ穿こうかしら", 1, 7579, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 127201, "もぐっ…もぐっ…\n食べとかないと教室で\nお腹なっちゃうわ～", 1, 7581, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 127201, "図書室で借りた本\n明日返却日なのよね…\n今日中に読むわよ！", 1, 7582, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104301, "あいつに背中を\n預けるのもいい気分だ\nいつかは、全部…", 1, 7584, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 104301, "な、なんだよ？\nじっと見るんじゃねぇ\n尻尾が揺れちまうだろ", 1, 7585, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127301, "魔法の練習！\nむむむ～…\nそれっ…！", 1, 7586, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127301, "そだ！　これ着て\nルー学行こっかな？　\n制服自慢しちゃお♪", 1, 7587, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127301, "赤点回避の魔法とか\nあったら最高じゃね？\n誰かうちに教えて～", 1, 7588, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127301, "制服が一番映える\n場所ってどこだろ？\n教室？　遊園地？", 1, 7589, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127301, "ベンキョーつらたん…\n今度キャルっちに\n助けてもらお", 1, 7590, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127301, "部活か…\nヒデサイと一緒に\nやったら楽し～かも♪", 1, 7591, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127301, "男子の制服\nもにカワだった…\n今度ヒデサイに\n借りよっかな？", 1, 7592, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127301, "ちょすー、うちの\nさいきょー魔法で\nテンアゲしてあげる～", 1, 7593, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127301, "九九って呪文ぽくね？\nほら、例えばヒチシ…\nヒチシ、なんだっけ？", 1, 7594, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127301, "ヒデサイセンセー\nうちにベンキョー\n教えて！", 1, 7595, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127301, "学割マジ助かるー\nヒデサイ、一緒に\nどんどん使ってこー♪", 1, 7596, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127301, "今度ヒデサイに\nお弁当作ったげる\n屋上で一緒に食べよ♪", 1, 7597, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127301, "ヒデサイのバイブス\n上がる言葉教えてよ\nつらたんな時、うちが\nいっぱい言ったげるよ", 1, 7598, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 127301, "ちょすちょす～\n今日も朝から\nバイブス十分♪", 1, 7599, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 127301, "お昼どうしよっかな\nたまにはガッツリ\nいっちゃう系？", 1, 7600, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 127301, "やべ、宿題まだ\nできてないし…\nヒデサイ助けて～", 1, 7601, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127301, "充電ちう～\n…ガッコーのイスも\nこれにして～", 1, 7603, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127301, "かわいいし、\n癒されるし、キュン死\nしちゃうじゃん…！", 1, 7604, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127301, "リフレッシュの\n魔法発見！\nいい香り～♪", 1, 7605, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127301, "ぽかぽかサイコー♪\n今日はもーずーっと\nここにいたいかも～", 1, 7606, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 127301, "超おいし～♪\n学食にあったら\n毎日リピする～", 1, 7608, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 127301, "ふむふむ…\nねむみパない…\n続きはまた今度～", 1, 7609, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127501, "今度は子どもたちも\nサラサリアに連れて\n行ってあげたいわ", 1, 7611, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127501, "子どもたちから\nお姫さまみたいって\n言われちゃった♪", 1, 7612, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127501, "この剣…火を点けたり\nするのに便利なのよ\nコントロールも効くし", 1, 7613, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127501, "あのサラサリアの\nカラっとした暑さが\nちょっと恋しいわ…", 1, 7614, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127501, "買ってきたお土産、\nお世話になってる\nみんなに配りましょ♪", 1, 7615, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127501, "あんたと一緒なら\nどこの国でだって\n暮らしていけそうね♪", 1, 7616, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127501, "今の事業を大きくして\nあんたと世界中の国を\n飛び回ったり…なんて\nすてきじゃない？", 1, 7617, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127501, "サラサリアで食べた\n料理が恋しい…？\nもう…また今度\n作ってあげるわよ", 1, 7618, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127501, "今度旅行するなら\nどこに行こうかしら？\n希望があれば教えてね", 1, 7619, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127501, "サラサリアの香辛料、\nランドソルでも\n人気が出そうよね\nいいビジネスの予感！", 1, 7620, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127501, "そんなにじっと\n見られるとさすがに\n恥ずかしいってば…", 1, 7621, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127501, "お嫁さんは憧れるけど\nお婿さんになって\nもらうのもありよね…\nあんたはどう思う？", 1, 7622, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127501, "やっぱり、あんたには\nいつもあたしの隣に\n居てほしいな…", 1, 7623, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 127501, "陽射しが強くなる前に\n雑多な用事は\n片付けちゃいましょ", 1, 7624, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 127501, "サラサリアで食べた\n料理を再現してみたの\n味見してくれる？", 1, 7625, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 127501, "暑さに負けないように\n睡眠はちゃんと\n取っておかないと", 1, 7626, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127501, "こっちでもあっちでも\n忙しいのは変わらない\nわね…ちょっと休憩～", 1, 7628, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127501, "サラサリアで見た\nぬいぐるみも\nかわいかったわね", 1, 7629, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127501, "お香って地域ごとの\n特色が出るわよね\nこの香りは…\nちょっと神秘的？", 1, 7630, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127501, "砂漠地方も夜は冷える\nのよね。温かいものが\nあると安心するわ…♪", 1, 7631, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 127501, "食べ慣れた味って\nどうしてほっと\nするのかしら…", 1, 7633, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 127501, "今後の取引相手に\nなるかもしれないし\n向こうの文化も\n勉強しておかないと…", 1, 7634, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101401, "犯人はこの中にいる！\n探偵として一度は\n言ってみたいセリフさ", 1, 7636, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 101401, "助手くんの情報を\n仕入れておかないと…\n理想の私を、ずっと\n見ていてほしいからね", 1, 7637, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127601, "久々の長旅で\n里心がついちまったね\nしばらくランドソルで\nのんびりしようか", 1, 7638, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127601, "たまには違う服装\n違う得物を使うのも\n悪くないね", 1, 7639, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127601, "サラサリアにいた時は\n砂まみれで大丈夫かと\n思ってたけど、今じゃ\n懐かしく感じるよ", 1, 7640, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127601, "魔法の絨毯、あれは\n楽だし速いし便利\nだったねえ…土産に\n一枚欲しかったな", 1, 7641, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127601, "砂上での戦いは\nなかなかいい鍛錬に\nなったね…今度は\n修行しに行くとしよう", 1, 7642, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127601, "サラサリアの人たちも\n気のいい連中だったが\n元気にやってるかねえ", 1, 7643, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127601, "旅の恥はかき捨てって\nいうけど、悪い旅じゃ\nなかったね。次は\nアイツとどこに行こう", 1, 7644, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127601, "この格好を冷やかしに\n来たのかい？\n生憎、もうすっかり\n慣れちまったよ", 1, 7645, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127601, "ギルドの連中に\n土産をわたしに\n行くところなんだ\n付き合っておくれよ", 1, 7646, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127601, "久々に水場で\n釣りがしたいねえ…\nもちろんお前さんも\nくるんだろう？", 1, 7647, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127601, "おっと\nサラサリアの連中から\n便りが来てるよ\nさ、一緒に読もうか", 1, 7648, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127601, "最近サラサリアの踊り\nを練習してるんだ\nあとで振付けを\n見ておくれよ", 1, 7649, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127601, "なんだいお前さん？\n…なんだか若奥さん\nっぽいって？　まあ、\nそりゃあ、ねえ…", 1, 7650, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 127601, "いい時間だね\n日が昇りきる前に\nやることをすませ\nとかないと", 1, 7651, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 127601, "昼はどうしようかね\nサラサリア風の\n食事でもつくろうか", 1, 7652, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 127601, "さて、早めに寝ようか\n砂漠の夜は冷えるから\n…っと、まだ旅行中の\n気分でいたよ", 1, 7653, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127601, "良い座り心地だ\nサラサリア宮殿の\n応接間を\n思い出すねえ…", 1, 7655, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127601, "ランプの魔神に\nあげたら喜ぶかねえ？\n次に会う時までに\n見繕ってみようか", 1, 7656, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127601, "この香り…\nサラサリア土産の\n香木かい？", 1, 7657, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127601, "旅先での焚火を\n思い出すよ\nなんだかほっとするね", 1, 7658, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 127601, "ふふ、慣れてる味も\nいいけど、むこうの\n味付けが懐かしく\nなっちまうね", 1, 7660, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 127601, "ふむふむ…\n千夜一夜物語か\nおもしろいもんだねえ", 1, 7661, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126001, "立派なギルドハウス♪\n帰る場所が素敵だと\n冒険に出るのも\n楽しくなりそうです♪", 1, 7663, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126001, "お掃除でも…と\n思いましたけど、\nさすがお兄さんのお家\nきれいにしてますね♪", 1, 7664, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126001, "わたしたちもいつか\n立派なギルドハウスが\n持てる冒険者に\nなりたいです…♪", 1, 7665, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126001, "わたし冒険者ですけど\n家を守ってお兄さんの\n帰りを待つのも\n悪くない…かも？", 1, 7666, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126001, "お菓子焼こうかな…♪\nちょっと多めに作って\nお兄さんにも\n食べてもらおうっと♪", 1, 7667, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126001, "このお家、とっても\n大好きになりました♪\nだからその…もう少し\nいてもいいですか…？", 1, 7668, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126001, "お兄さんに\n感謝の手紙を書いて\n枕元に隠して\nおこうかな…♪", 1, 7669, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126001, "…お兄さん\nお暇なんですか？\nじゃあ一緒に冒険へ\n出かけましょう♪", 1, 7670, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126001, "お兄さんは苦手な食材\nとかありますか？\nあの、もしよければ\n今から手料理を…♪", 1, 7671, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126001, "まったり過ごす\nお家時間もいいですね\n冒険者には羽休めも\n大事ですから♪", 1, 7672, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126001, "いつでもお兄さんを\n守れるように\nこの姿でいることに\n慣れておきますね…！", 1, 7673, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126001, "お兄さん眠そうです…\nちょっとお昼寝\nしちゃいましょうか♪\nはい、膝枕…♪", 1, 7674, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126001, "お兄さん怪我を…！？\nわたしが治療を…\nふー…ふー…", 1, 7675, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 126001, "おはようございます♪\n朝、元気に挨拶すれば\n幸せな一日が過ごせる\nそうですよ♪", 1, 7676, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 126001, "お昼ごはんの時間です\n…手がふさがってる？\nわかりました、じゃあ\nあ～んしますね♪", 1, 7677, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 126001, "ふわぁ…眠くなって\nきちゃいました…\nお兄さん…お泊りして\nいいですか…？", 1, 7678, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126001, "ふにゃ～～…♪\nこんな姿、他の人には\n見せられません…♪\nもふもふ～～…♪", 1, 7680, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126001, "お兄さんこういうの\n好きなのかな～…\nふふっ、わたしも♪\nぎゅ～～～っ♪", 1, 7681, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126001, "アロマの癒し効果…\n上手くアレンジして\nみなさんの治療に\n活かせないかな～？", 1, 7682, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126001, "は～気持ちいい～♪\n身体が暖まると\n心もぽかぽかです♪", 1, 7683, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 126001, "うふふ、おいしい♪\nプレシアちゃんなら\n無限にぱくぱく～って\nしちゃいそうです…♪", 1, 7685, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 126001, "本はわたしを\n知らない世界に\n連れていってくれる…\nまるでお兄さんです♪", 1, 7686, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103801, "話題の新刊…\nあの人も読んでるかな\n今度会う時、感想\n聞いてみたいな…♪", 1, 7688, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 103801, "うにゃっ…あ、熱を\n測ってくれたんですか\nありがとうございます\nバッチリ元気ですよ♪", 1, 7689, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126101, "お兄の、けはい…\nん、みつけた\nぎゅ", 1, 7690, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126101, "きょろきょろ\nここも\nぱとろる、するよ", 1, 7691, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126101, "おうち\nにぎやか\nさみしく、ない", 1, 7692, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126101, "きょだい、おにぎり\nきょだい、肉まん\nきょだい…お兄？", 1, 7693, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126101, "はっぴーちゃん\nどこ？\nお兄、たべた？", 1, 7694, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126101, "くんくん…この、へや\nお兄の、におい\nすんでる\nすめる？", 1, 7695, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126101, "おそうじ、にがて\nでも\nれんしゅう\nきゅっきゅ", 1, 7696, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 126101, "みみ、さわり、たい？\nんー…だめ…\nむずむず\nなので", 1, 7697, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126101, "くろぷれしあ\nかっこいい？\nん…あざます", 1, 7698, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126101, "お兄の、おうち\nきょうみ、ましまし\nたんけん\nするね", 1, 7699, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126101, "なでなで、される…と\nねむ…く…\nぶぅ…ぶぅ…ぶぅ…", 1, 7700, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 126101, "かみ、りぼん\nおてつだい\nよろ\nです", 1, 7701, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 126101, "ここ\nすむ\nね？\nまいにち、いっしょ", 1, 7702, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 126101, "ぐっもにん\nあさごはん\nあさごはん", 1, 7703, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 126101, "お兄\nおつかれ？\nれっつ、まいぺーす", 1, 7704, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 126101, "ひとりで、ねれる\nでも\nひとりじゃ、なくても\nねれる、よ…？", 1, 7705, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 126101, "ふかふか\nパパの、おなか\nみたい", 1, 7707, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 126101, "こんにちは\nぷれしあ、です\nおともだち、なろ？", 1, 7708, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 126101, "いい、におい\nでも、お兄\nもっと、いい、におい", 1, 7709, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 126101, "とりま\nお肉、やこ", 1, 7710, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 126101, "もぐもぐ…\nむげん肉まん、も\nきぼう", 1, 7712, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 126101, "ぜんぶ、よんだ\nもっと、ぶあついほん\nでも、いーよ？", 1, 7713, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127901, "幸せなクリスマスを\n過ごすため\nギルドハウスを\nきれいにしなくちゃ♪", 1, 7715, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127901, "えへへ♪\n彼へのプレゼント…\n枕元にそっと\nおいておきましょう☆", 1, 7716, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127901, "クリスマスといえば\n七面鳥の丸焼き…☆\nよだれを拭いて\nいざ準備です♪", 1, 7717, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127901, "彼に用意した\nプレゼント…\n大きすぎて\nくつ下に入りません…", 1, 7718, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127901, "プレゼントは\n貰う方も、贈る方も\nどちらも幸せを\n感じますね…☆", 1, 7719, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127901, "夜景が見える\nレストランであの人と\nクリスマスデート…\n憧れちゃいますね☆", 1, 7720, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127901, "プレゼントはわたし…\n恥ずかしいですけど\nこの贈り物、彼に\n喜んでほしいです…☆", 1, 7721, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 127901, "オーナメントを\nたくさん用意したので\n二人でツリーに\n飾りつけましょう☆", 1, 7722, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127901, "あはは、お腹\n空いちゃいました？\nパーティーまで\n我慢してください♪", 1, 7723, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127901, "ジングルベ～ル♪\nあなたも一緒に\nクリスマスソング\n歌っちゃいましょう♪", 1, 7724, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127901, "サプライズプレゼント\nの用意をして…って\nあなた！？　い、今の\n聞いてませんよね…？", 1, 7725, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 127901, "クリスマスの夜は\nパーティーしましょう\nできれば…その…\n二人っきりで…☆", 1, 7726, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 127901, "あなたと過ごした\n最高のクリスマス…\nずっとずっと心の奥に\nしまっておきますね", 1, 7727, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 127901, "おはようございます！\n皆の願いが叶うような\n素敵な一日に\nしましょうね☆", 1, 7728, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 127901, "お昼ですね！\nディナーの買い出しが\n済んだら、少しお昼寝\nしちゃいましょうか☆", 1, 7729, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 127901, "そろそろ就寝の\n時間ですね～\n枕元にくつ下の準備を\nお忘れなく～☆", 1, 7730, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 127901, "ふ～気持ちいい…☆\nお家でまったり過ごす\nクリスマスも\nいいですよね…♪", 1, 7732, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 127901, "かわいいですね～♪\nサンタさんからの\n贈り物でしょうか～？", 1, 7733, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 127901, "すう～…はあ～…\nすごく素敵な香り…\n炊き立てのご飯と\nいい勝負です…♪", 1, 7734, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 127901, "暖かいですけど…\nあの日の山小屋を\n思い出して\n恥ずかしいですね…☆", 1, 7735, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 127901, "むしゃむしゃもぐもぐ\n止まりませんよ\nクリスマスですから\nむしゃむしゃもぐもぐ", 1, 7737, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 127901, "物語に出てくる\nサンタさんみたいに\nわたしも幸せを\n運びたいです…☆", 1, 7738, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 127901, "わっ！\nこ…これは素敵な\nサプライズプレゼント\nですね…☆", 1, 7740, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 127901, "これを着ればわたしも\n立派なサンタさんに\nなれますかね…？", 1, 7741, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 106601, "ここが今のあたしの\n居場所ってやつです\nふふっ、なかなか\n悪くねー気分ですよ", 1, 7742, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 106601, "これからはボス代行\nとしてブイブイ\nいわせてやるです\nついてくるですよ！", 1, 7743, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128001, "陛下～♪\n貴女のクレジッタが\nおめかしして\nはせ参じましたわ～♪", 1, 7744, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128001, "パーティーとは景気の\n良さを見せつける場…\n倹約などは\nもっての外でしてよ！", 1, 7745, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128001, "この天井の高さなら\n搬入できそうですわね\n陛下用の特注ケーキ♪", 1, 7746, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128001, "…ぶぇっくしゅ！\nふう…少し背中を\n開けすぎたかしら…？", 1, 7747, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128001, "ここは秘書の目も\n届きませんから\nチクりメモの心配も\nありませんのよね～♪", 1, 7748, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128001, "いくらでも二度見して\nよろしくてよ？\n本当にわたくしだと\n心から納得できるまで", 1, 7749, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128001, "袖の下以外の贈り物も\nなかなかどうして…\n悪くないものですのね", 1, 7750, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128001, "こう見えてダンスの\nたしなみはありますの\nお相手してあげても\nよろしくってよ", 1, 7751, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128001, "ドレスはもちろん\n特注品ですわ♪\nもしや貸衣装だと\n思いまして？", 1, 7752, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128001, "バッグの中身？\n仕事に必要なものと…\n心を潤わせるための\n宝石がいくつか♪", 1, 7753, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128001, "やる気というものは\n仕事に必要な条件です\nいわばドレスコードの\nようなものですわね", 1, 7754, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128001, "全ての仕事は自分の\n血となり肉となり\nお金にもなるもの\nなのです。経験談です", 1, 7755, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128001, "今年の教訓を活かして\n来年は更に素晴らしい\nクリスマスにして\n差し上げますわ！", 1, 7756, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 128001, "おはようございます\n今日という稼ぎ時の\nはじまりですわよ！", 1, 7757, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 128001, "安くて早くておいしい\nそれがランチに必須の\n栄養素ですわ！", 1, 7758, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 128001, "宵越しの仕事は\n安眠の敵！\n熟睡したいのであれば\n今が勝負時ですわよ！", 1, 7759, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128001, "たまにはこうして\n休息を取ることも…\n…はっ！？\nドレスにシワが～っ！", 1, 7761, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128001, "…わたくしの勘が\n言っていますわ\nこの愛らしさ！\n爆売れ確・定！", 1, 7762, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128001, "いい香りですわね…\nお金の匂いには\n敵いませんけれども♪", 1, 7763, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128001, "……ハッ！？\n完全にボーッとして\nしまいましたわ…！\nぬくもり…恐ろしい！", 1, 7764, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 128001, "考えませんわ…\n考えませんわよ！\nカロリーのことはっ！", 1, 7766, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 128001, "ビジネス本以外の本を\n読むのも、たまには\n悪くありませんわね…", 1, 7767, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (14, 0, 0, 128001, "よくできていますわね\nこの絵本！\n子ども向けだと甘く\n見ていましたわ…！", 1, 7769, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (15, 0, 0, 128001, "この視界の狭さ…\n思い出しますわね\nティッシュ配りの\nアルバイト時代を…", 1, 7770, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128101, "ふふふ…こっそり\nスイーツを食べる\nリノちゃん見～っけ♪", 1, 7771, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128101, "お姉ちゃんの抜き打ち\nお部屋チェック～♪\n…ケーキの食べこぼし\nこれはリノちゃんだね", 1, 7772, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128101, "この衣装は目立つから\nどこにいても弟くんに\n気付いてもらえるね！", 1, 7773, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128101, "弟くん、協力して！\nリノちゃんにお勉強\nしなさいって圧を\nかけるんだよっ…♪", 1, 7774, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128101, "絆の力を感じるよ\nこれならもっと万全な\n弟くんのお世話が\nできそうだねっ！", 1, 7775, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128101, "お姉ちゃんもたまには\nリノちゃんみたいに\n甘えてみようかな…", 1, 7776, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128101, "弟くんとリノちゃん\nどっちも大切な\n私のきょうだいだよ", 1, 7777, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128101, "分かってるよ♪\nお姉ちゃんの膝枕を\nご所望だねっ？　え？\n違う？　違わないよ？", 1, 7778, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128101, "リノちゃんがね\nお姉ちゃんチョップを\n防ぐ妹白刃取りを\n訓練してるんだよね", 1, 7779, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128101, "お姉ちゃん頭突きは\n封印しないとね…\nだって、今使うと\n頭飾りで威力が…ね", 1, 7780, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128101, "ふふっ♪\n目をつぶってても\n弟くんは見えてるよ\n…そこでしょ？", 1, 7781, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128101, "お姉ちゃんも\nリノちゃんみたいに\nかわいさいっぱいに\n振舞っちゃうぞ♪", 1, 7782, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128101, "弟くんっ♪\nいつもお姉ちゃんの\n声が届く範囲にいてね\n…絶対だよ", 1, 7783, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 128101, "おはよう、弟くん！\nリノちゃんが起こして\nくれたんだね♪\n…先を越されちゃった", 1, 7784, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 128101, "お姉ちゃんランチの\n時間だぞ♪\nさ、食べさせてあげる\nお口を開けて…♪", 1, 7785, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 128101, "おやすみの時間だよ\n…弟くんは真ん中ね\n左右には私たち姉妹が\nいるから安心だよ♪", 1, 7786, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128101, "お姉ちゃんは全力で\nリラックスしてても\n弟くんの声一つで\nすぐにでも動けるよ♪", 1, 7788, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128101, "リノちゃんこういうの\n好きそうだよね\n今度プレゼントして\nみようかな？", 1, 7789, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128101, "いい匂いがするね～\nあれ？\nリノちゃんのケーキの\n甘い匂いも混ざってる", 1, 7790, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128101, "お姉ちゃんの\n弟くんへの愛も\n熱く燃えあがるね…！", 1, 7791, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 128101, "たくさん食べて\n栄養を補給して\nお姉ちゃん力に\n全て変換するよっ！", 1, 7793, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 128101, "ことわざ辞典…\nリノちゃんにこれを\n読ませようかな", 1, 7794, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128201, "そろ～り、そろり…\nお姉ちゃんが居ぬ間に\nスイーツを～…♪", 1, 7797, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128201, "いつか\nお姉ちゃんに似合う服\n作ってみたいですね", 1, 7798, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128201, "あいたっ！？\nひらひらしたとこ\n踏んづけちゃいました", 1, 7799, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128201, "お姉ちゃんと\nお兄ちゃんの二人から\n勉強しろって圧力が～", 1, 7800, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128201, "私の中にも秘めた力が\n眠っているはず…\nそう、妹パワーが", 1, 7801, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128201, "私もお兄ちゃんを\n甘やかしてみたいです\n…お姉ちゃんみたいに", 1, 7802, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128201, "お兄ちゃんの部屋を\nこっそり拝見～♪\nはっ！？　これは\nお姉ちゃんの視線…！", 1, 7803, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128201, "お姉ちゃん頭突きは\n大地を揺らす威力です\n私とお兄ちゃんは\n挨拶感覚で喰らいます", 1, 7804, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128201, "お姉ちゃんチョップは\n雲を裂く切れ味です\n早く妹白刃取りを\n完成させないと…", 1, 7805, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128201, "お姉ちゃんみたいに\n大人っぽく着こなす\nには一体どうしたら…", 1, 7806, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128201, "弓のデザインも\nかわいいんですよっ♪\n常に持ち歩きたいです\nけど…危ないですよね", 1, 7807, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128201, "きれいな服を着てると\nお姫さま気分に\nなれますね…あとは\n王子さまがいれば…♪", 1, 7808, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128201, "お兄ちゃんっ！\n私は褒められて伸びる\nタイプなのでもっと\nナデナデしてください", 1, 7809, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 128201, "妹モーニングコール♪\nお姉ちゃんより先に\n起こしに来れました！", 1, 7810, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 128201, "ああっ、お姉ちゃん！\nお兄ちゃんにアーン\nするなんてずるいです\n私もしたいですっ！", 1, 7811, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 128201, "おやすみの時間ですね\nえへへ、きょうだいで\n並んで寝るのって\n楽しいですよね！", 1, 7812, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128201, "スイーツを食べた後は\nお腹を休めましょう～\n平気平気…ちゃぁんと\n運動もしますからぁ…", 1, 7814, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128201, "お姉ちゃんも\nこういうかわいいもの\n好きなんですかね", 1, 7815, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128201, "くんくん…\nなんの匂いでしょうか\n新しいスイーツ？", 1, 7816, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128201, "きょうだい３人\n肩を寄せて暖まるとか\nよくないですかっ？", 1, 7817, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 128201, "いつも元気な妹で\nいるためには\nよく食べるのが大事！", 1, 7819, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 128201, "なんか姉に関する本が\nちらほらと混ざって…\nこれお姉ちゃんの\n仕業ですね！？", 1, 7820, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128301, "ふんふんふ～ん\nお正月で世間は\n浮かれてるですねえ\nま、いいですけど♪", 1, 7823, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128301, "は～、お正月は\nだらだらできて\n最高ですね\n…いつも通りですけど", 1, 7824, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128301, "ビッグな幹部は\nビッグなお年玉が\nもらえるはずです\n楽しみですね", 1, 7825, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128301, "今度ドラちゃんに\nお年玉を持っていって\nやりたいですね", 1, 7826, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128301, "そういえばアユアユと\n約束してたですよ…\nそろそろ出かける\nですかね", 1, 7827, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128301, "お年玉がもらえたら\n何に使うか…\n今のうちに\n考えとくです", 1, 7828, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128301, "年神の報酬の駄菓子が\nたまってきたですね…\nガキどもにでも\n配っちまうですよ", 1, 7829, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128301, "あたしのレアな\n晴れ着姿、今のうちに\nしっかり目に焼き付け\nとくがいいですよ", 1, 7830, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128301, "お兄さんは今日も\nヒマそうですねぇ…\nしょうがないから\nかまってやるですよ", 1, 7831, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128301, "そういえばカヤぴぃ\n見なかったですか？\n喧嘩屋初売りセール\nとか言ってたですけど", 1, 7832, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128301, "お正月ならではの\n大きなシノギ…\nなにかないですかね？", 1, 7833, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128301, "いいとこに来たですよ\nさっき走ったら帯が\n曲がっちゃったです\n直すの手伝うです！", 1, 7834, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128301, "年神の仕事ですか？\nこれからいくとこです\nお兄さんも一緒に\n来やがるですよ！", 1, 7835, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 128301, "ふぁあ…\nどうしたですか？\nお正月に早起きなんて\n無粋ってヤツですよ…", 1, 7836, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 128301, "小腹が減ったですね\n一緒におせちでも\nつつきにいくですよ！", 1, 7837, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 128301, "夜はあたしの時間\nと言いたいとこですが\n初夢のために早く\n寝とくですかね…", 1, 7838, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128301, "正月は食っちゃ寝\nするのが仕事…\nって聞いたこと\nあるですよ", 1, 7840, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128301, "ふふっ、晴れ着の\nあたしの次くらいには\nかわいいですね", 1, 7841, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128301, "なんかいい匂い…？\nよく知らんですけど\n晴れ着からも同じ\n匂いがしてるですね…", 1, 7842, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128301, "ふ～あったまるです…\nお正月の唯一の弱点は\n寒いことですからね…", 1, 7843, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 128301, "もぐもぐ…正月太り？\nあたしには関係ない\n言葉ですね", 1, 7845, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 128301, "あったあった…\nトーゴクには\nドラゴンの神宮も\nあるんですね", 1, 7846, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128401, "お正月のトラブルも\nハツネちゃんが\nめでたく解決\nしてみせるのだ～！", 1, 7848, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128401, "そうだ、新年会に\nサプライズ大作戦を\nしかけちゃおう！", 1, 7849, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128401, "イノリちゃんも\nカスミンも振袖姿が\nかわいかったな～", 1, 7850, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128401, "シオリンに\nお年玉あげたいし\n彼にバイトを紹介\nしてもらおうかな？", 1, 7851, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128401, "今度はどんな凧を\n飛ばそうかな？\nみんなが驚くような\n凧がいいよね～", 1, 7852, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128401, "ひらめいた！\n彼への年賀状は\nきらーんっ☆って\n感じにしよう！", 1, 7853, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128401, "来年も彼の隣で\n初日の出が\n見られるといいな～", 1, 7854, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128401, "今日はキミが\nギルドハウスに来る\n予知夢を見たんだよ～\nふふ、正夢になったね", 1, 7855, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128401, "この振袖どうかな？\nすっごくかわいいよね\nえへへ、ありがとう～", 1, 7856, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128401, "ハツネちゃんおみくじ\n開始！　きらーんっ☆\nキミは大吉なのだ～\nどう元気出たかな？", 1, 7857, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128401, "今度エルフの森にある\n薬草を使って七草粥を\n作ろうと思うんだ～\nぜひ食べてほしいな♪", 1, 7858, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128401, "お正月っぽい遊びを\n色々と用意したのだ～\nたまにはギルドハウス\nでゆっくり過ごそうよ", 1, 7859, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128401, "もしかして眠たいの？\n…じゃあ、また膝枕\nしてあげてもいいよ？", 1, 7860, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 128401, "いっぱい寝たから\n元気いっぱいだよ～\n今日もがんばろう！", 1, 7861, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 128401, "お昼だね～\nおせちがあるから\nキミも食べるでしょ？", 1, 7862, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 128401, "そろそろ寝る時間だね\n今日はどんな夢を\n見られるかな～", 1, 7863, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128401, "いい座り心地～\n寝正月で過ごすのも\n最高なのだ～", 1, 7865, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128401, "かわいいな～\n癒されてぐっすり\n眠れそうだよ～", 1, 7866, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128401, "いい香り～\nリラックスすると\n眠たくなっちゃうよ…", 1, 7867, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128401, "ポカポカいい気持ち～\n初日の出の温もりを\n思い出しちゃうな～♪", 1, 7868, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 128401, "もぐもぐ、おいし～\nでもいっぱい食べたら\n眠くなっちゃった…", 1, 7870, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 128401, "ふむふむ\nお正月料理も\n色々あるんだね～\n今度作ってみようかな", 1, 7871, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 106501, "最近カレを見てたら\n何故かおいしそうに\n思えてくるんだよな…", 1, 7873, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 106501, "手握っててくれるか？\nじゃないとオレ…\n力が抑えきれない…", 1, 7874, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128501, "つかの間の平和を享受\nしながらぬくぬくと\n読書に耽る…\nふふ、最高だね", 1, 7875, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128501, "お正月飾りなんて\n置いたらはしゃいでる\nように見えるかな…？", 1, 7876, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128501, "ま、まずい…\n一人遊びしすぎて\nお正月遊びをほぼ\n極めつつある…", 1, 7877, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128501, "ミステリーは\n時と場所を選ばない\nつまり…新年でも\n探偵は休まないよ！", 1, 7878, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128501, "お世話になった人に\n年賀状でも書こうかな\n助手くん、喜んで\nくれるかな…えへへっ", 1, 7879, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128501, "絵馬に願い事を書こう\n助手くんと一緒に\nいられますように…♪\n…は、恥ずかしいな", 1, 7880, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128501, "助手くんはもっと\n派手な着物のほうが\n好みだったかな…？\nき、気になる…", 1, 7881, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128501, "やあ。助手くんも\nお正月休みを\n持て余してるのかい？\nふふ、似た者同士だね", 1, 7882, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128501, "助手くん助手くん！\n凧あげしにいこう！\n…そ、捜査の一環で", 1, 7883, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128501, "着物は転びそうで\n怖いんだよなあ…\nお、押さないでね？\nフリじゃないよ！？", 1, 7884, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128501, "ひ、人目を憚る\n本を読むなら\nみんなが帰省中の今！\nはっ！　助手くん！？", 1, 7885, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128501, "ね、ねえ…助手くん…\n一緒に初詣いかない？\nあ、いや！　深い\n意味はないんだけど！", 1, 7886, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128501, "あはは…助手くんと\n一緒だと家でごろごろ\nしてるだけでも\n楽しいや…♪", 1, 7887, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 128501, "ふぁ～…\nお正月休みのおかげで\n昼夜逆転中だよ…", 1, 7888, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 128501, "お正月も依頼が\nひっきりなしだね\nさあ、街へ出よう！\nもちろん君も一緒にね", 1, 7889, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 128501, "も、もっと\n近くにきてもいいよ？\n冬の夜は…寒いからね", 1, 7890, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128501, "ううぅ～…！\nこのままじゃ\n寝正月だぁ…！", 1, 7892, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128501, "みんなが帰省中でも\nこれがあれば\n寂しくない…かな", 1, 7893, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128501, "うーん…♪\nお正月休みの\nリフレッシュに\nちょうどいいや♪", 1, 7894, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128501, "ふぅ…あったまるな\n冬は寒いから\n助かるよ…", 1, 7895, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 128501, "うーん、おいしい♪\n読書のお供に\nちょうどいい", 1, 7897, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 128501, "時間に余裕があると\n読書が進むよ\nいざ、本棚読破だ♪", 1, 7898, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128801, "この新しい力…\nさらに使いこなせる\nようになるには…", 1, 7900, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128801, "私に眠る属性の力…\n風の力以外には\nどんな力があるのかな", 1, 7901, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128801, "今の私なら\nどこまでいけるのか…\nやれるだけ\nやってみよう", 1, 7902, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128801, "風の力の制御…\n段々とコツが\n掴めてきたな", 1, 7903, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128801, "風の力か…\n応用の幅は広そうだ\n何ができそうか\n色々試してみよう", 1, 7904, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128801, "今度、私とキミで\n料理対決でもする？\n簡単には負けないよ", 1, 7905, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128801, "この胸に溢れる\n温かな気持ちは…\nきっと彼への…", 1, 7906, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128801, "一段と剣技が\n冴えてきたような…\nいや…油断禁物だ", 1, 7907, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128801, "かっこいいって？\nありがとう…そんなに\n真っすぐ褒められると\n照れてしまうな…", 1, 7908, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128801, "どれほど厚い壁が\n立ち塞がろうと\n打ち破ってみせる", 1, 7909, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128801, "手が空いてるなら\n剣の稽古に少し\n付き合ってくれるかな", 1, 7910, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128801, "試練を乗り越えて…\n私の中にあるものが\nはっきりと分かったよ", 1, 7911, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128801, "キミから流れてくる\n力の温かさ…これが\n私たちの絆なんだ", 1, 7912, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 128801, "朝だよ、そろそろ\n起きる時間だ\nそれとも私に起こして\nほしいのかな？", 1, 7913, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 128801, "昼食は私が作るよ\nリクエストはある？\nできる範囲で応えるよ", 1, 7914, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 128801, "おやすみ。また明日も\n一緒に冒険しよう\n約束だよ？", 1, 7915, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128801, "休める時は休む…\n戦い続けるには\n必要なことだからね", 1, 7917, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128801, "心の安らぎの大切さ…\nそれを知れたのは\nキミたちのおかげだね", 1, 7918, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128801, "気持ちが落ち着くな…\nこの香り、風の力で\n纏えたりしないかな", 1, 7919, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128801, "暖まった空気の流れ…\n以前より風の変化に\n気づくようになったな", 1, 7920, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 128801, "この味付け…\n今度試してみようか\n味見は任せたからね", 1, 7922, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 128801, "心技体、すべてを\n鍛えるには座学も\n必要だからね", 1, 7923, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128901, "瞑想するのに\nいいところはあるかな\n集中できて…お昼寝は\n我慢できるところ", 1, 7925, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128901, "ここにはあったかい\n空気が流れてるなぁ…\nみんなが受け入れて\nくれるおかげだね", 1, 7926, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128901, "まるで心の中に\n光が満ちてるみたい…\nこの気持ち、みんなに\nおすそ分けしたいな♪", 1, 7927, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128901, "この衣装は属性の力を\n反映しやすいんだよね\nキラキラしてるのも\n納得だよ", 1, 7928, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128901, "エルフの里のみなさん\n元気かなぁ…\nまた会いに行きたいな", 1, 7929, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128901, "まだ変身できてる…\nわたしと騎士クンの\n修行の成果\n出てるなぁ…ふふっ♪", 1, 7930, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128901, "この服を脱いだら\nどうなるのかな…\nた、試す勇気は\nないけど…", 1, 7931, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128901, "ふぅ…あ、騎士クン\nちょうど瞑想してたの\n一緒に…する？", 1, 7932, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128901, "不思議だけど\nいつの間にか\n出たり消えたりするの\n…背中の輪っか！", 1, 7933, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128901, "この服って肌触りが\nとってもいいんだよ\nさ、触ってみる？\nスベスベだよ？", 1, 7934, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128901, "アストラルの力…\nすごく持続できてるね\n修行の成果が出てるよ", 1, 7935, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128901, "この力があれば\n暗いところで\n落とし物をしても\n見つけやすいかも…？", 1, 7936, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128901, "冒険の準備なら\nできてるよ\nいつでも声をかけてね\n…楽しみにしてる！", 1, 7937, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 128901, "おはよう騎士クン\n一緒にお散歩とか…\nどうかな？", 1, 7938, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 128901, "お昼ご飯のあとの\n瞑想って難しいんだよ\n眠くなっちゃって…", 1, 7939, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 128901, "夜ふかしさんは\nほどほどにね？\n一日頑張った自分を\n休ませてあげないと", 1, 7940, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128901, "のんびりしてたら\nケーキが食べたく\nなっちゃうなぁ…\nでも太る…よね…？", 1, 7942, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128901, "わあ、かわいい…！\n見てるだけで\nにやけちゃう…♪", 1, 7943, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128901, "これは…花の香りかな\n知ってる花かも…？\n当ててみようっ", 1, 7944, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128901, "あったかいと\nどうしてこんなに\n落ち着くんだろう…", 1, 7945, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 128901, "食べ過ぎたらお腹が\n目立っちゃうかもって\n分かってるのに…！\n止められないよ～っ！", 1, 7947, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 128901, "こ、恋の精霊とか\nいたりするのかな…？\nはっ！　ダメダメっ！\n実力で頑張らないと", 1, 7948, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128701, "お部屋の中でも\n修行、修行っ♪\nでも燃やさないように\n注意しなきゃだね", 1, 7950, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128701, "うーん…この衣装\n厚着か薄着か\nどっちなんだろう…？", 1, 7951, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128701, "いつもの肉球がない分\nお片付けしやすいな♪\n騎士くんのお部屋\nピカピカにしとこー！", 1, 7952, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128701, "お休みは騎士くんと\nＢＢＱに行きたいなー\nあたしと一緒なら\n火の心配はいらないよ", 1, 7953, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128701, "この姿ならもっと\n人を助けられる…！\nえへへっ！\n燃えてきたーーっ！", 1, 7954, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128701, "騎士くんが帰ったら\nアイラみたいな笑顔で\nお疲れさまさまって\nお出迎えしよっと…♪", 1, 7955, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128701, "この姿の時、いつも\n胸が熱く燃えてるのは\nずっと騎士くんのこと\n考えてるからかな…？", 1, 7956, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 128701, "騎士くん騎士くん！\nあたしに触れると\nやけどしちゃうよ…？\nなんちゃってー♪", 1, 7957, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128701, "おっ、騎士くん\n修行に行きたいの？\nえへへっ、いいねー♪\n二人で燃えちゃおー！", 1, 7958, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128701, "そうだ騎士くん\nじゃんけんしよっか？\nグローブ外してるから\nチョキも出せるよっ♪", 1, 7959, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128701, "この姿でいられる時間\nすごく増えてきた…！\nえへへ！　あたしたち\n修行の成果出てるね！", 1, 7960, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 128701, "じっと見つめないでよ\n顔が真っ赤に…やば！\nほんとに顔から\n火噴いちゃいそう…！", 1, 7961, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 128701, "火には強くなれても\n騎士くんに触られた\nほっぺはずーっと\n熱くてたまんないや…", 1, 7962, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 128701, "おはよー騎士くん！\n今日も一日\n燃え滾っていくよー！\nファイヤーッ！！", 1, 7963, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 128701, "騎士くんお昼だよ！\nちゃんとご飯食べて\n午後からの修行も\n乗り切ろうねー！", 1, 7964, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 128701, "今日もお疲れさま！\nそうだ、寝る前に\nキャンプファイヤー\n囲んじゃおっか♪", 1, 7965, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 128701, "ぼふーんっ！\nえへへ、気持ちいい…\n修行の前に\nちょっと休憩ー♪", 1, 7967, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 128701, "かわいいな～♪\nなでなでなでなでなで\nなでなでなでなでっ！\nあははっ、摩擦熱！", 1, 7968, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 128701, "ん～いい香り♪\n少し熱したほうが\n香りが広がるね…って\n蒸発しちゃった！！！", 1, 7969, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 128701, "外でも自分の火で\nあったまれるけど\nお部屋でぬくぬく\nするのが一番だよね♪", 1, 7970, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 128701, "もぐもぐっ！\nお腹が減っては\n人助けはできずっ！\nもぐもぐもぐっ！", 1, 7972, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 128701, "ふむふむ…\nなるほどなるほど…\n…これが上手な\nＢＢＱのやり方か…", 1, 7973, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129001, "…壁も扉も柱も\n全て記録で見たまま\n本当に…来ているのね", 1, 7975, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129001, "…騒がしい場所\n騎士クンが受け入れて\nくれるのをいいことに", 1, 7976, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129001, "ここが…騎士クンの\n守りたかった場所\n騎士クンの思い出が\n詰まった場所…ね", 1, 7977, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129001, "視線を感じる…\nこちらの様子を\n探るような…心の動き\n取るに足らないけれど", 1, 7978, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129001, "…そこかしこで…\n言葉にもしたくない\n相手が過ごしていた\n記録を思い出す…", 1, 7979, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129001, "人は多いけれど…\n雪深い山中よりは\n過ごしやすい…か", 1, 7980, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129001, "自覚したことは\nあまりなかったけれど\nひどく…諦めの悪い女\nなのかもしれない", 1, 7981, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129001, "お招きありがとう\nそれで…何をして\nいればいい…かな？", 1, 7982, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129001, "ここは…明るいね\nわたしの城と比べると\nなんだか眩しいくらい", 1, 7983, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129001, "仮面はもう必要ないよ\nだって…わたしの顔を\n騎士クンに見て\nもらえなくなるから", 1, 7984, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129001, "騎士クンさえよければ\nわたしの城に専用の\n部屋を用意するよ\n…いつでも", 1, 7985, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129001, "…騎士クンのマント\nまた貸してほしいな\nきれいに使うから\n…だめ？", 1, 7986, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129001, "いつか二人の願いが\n重なり合えるって…\n信じているね\n…いつまでだって…", 1, 7987, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 129001, "おはよう、騎士クン\n…昨日は夢の中で\nお話ししすぎちゃって\nごめんね。ふふっ", 1, 7988, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 129001, "お腹は…空いてない？\n食べ物はないけれど\n空腹を忘れる魔法なら\nかけてあげられるよ", 1, 7989, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 129001, "騎士クン…\nまだ、眠らないの？\n…そう。それなら…\nわたしも起きているね", 1, 7990, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129001, "肩の力を抜くには…\nまだ時間がかかりそう\nきっと…騎士クンと\n二人きりなら別なのに", 1, 7992, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129001, "ぺんぽこりん…\nそう、確かあれも\nこんな感じだった…", 1, 7993, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129001, "花の香りは…好き\nつかの間\n心を穏やかに\nしてくれるから", 1, 7994, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129001, "あたたかい…けれど\n騎士クンの優しさに\n比べれば、まだまだ", 1, 7995, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 129001, "おい、しい…？\nこの感覚が、それ？\nなぜか…納得できる", 1, 7997, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 129001, "情報を入手するには\nわざわざ本を開く\n必要があるだなんて…\nプレイヤーは面倒ね", 1, 7998, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129301, "メルクリゾートか…\n良い記憶はないな\nよし忘れよう", 1, 8000, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129301, "知性の戦士ユニ！\nその恐るべき筋力！\nふおお！　うにゃー！", 1, 8001, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129301, "ぽーん！\n石用の水着ください！\nセクシーロゼッタが、\n渚の視線を独占です！", 1, 8002, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129301, "学院から奨学金を\n勝ち取るより某財団を\nだまくらかした方が\n早い気がしてきたな…", 1, 8003, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129301, "わんつぅ、わんつぅ…\nんぎゃあ足攣ったー！\nおのれカルミナとやら\n絶対に許さん！", 1, 8004, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129301, "ところで…なぜぼくは\nプールでもないのに\n水着のままなんだ…\nおのれ世界め！", 1, 8005, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129301, "彼と二人で撮った動画\nうう、凝視に耐えん…\nなんだこのムズ痒さは", 1, 8006, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129301, "なに？　水着姿で\n寒くないのかだと？\n普段からもっと薄着の\n娘が沢山いるだろう", 1, 8007, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129301, "シノブ君にメリゾの\n招待状を送ったのだが\n御尊父の暴走を\n心配していたな…", 1, 8008, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129301, "ミミ君らにメリゾの\n招待状を送ったのだよ\n迎える準備をせねば…\nおしゃぶり持ってる？", 1, 8009, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129301, "本質の賢者…？\nいや元気にしているが\nなぜ君がぼくの身内を\n知っているんだ…？", 1, 8010, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129301, "水着が似合ってる？\nな、なんだね突然…\nやめたまえよふひひ…", 1, 8011, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129301, "今度は夏にメリゾへ\n行きたいものだ\nむろん君も一緒だぞ", 1, 8012, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 129301, "ぐああっ、朝陽が…！\n朝陽が昇っている…！\n夜行性ユニ死んじゃう", 1, 8013, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 129301, "はぁ…もう昼か…\nそろそろ寝ないとな\n規則正しい生活を\nしないと不健康だ…", 1, 8014, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 129301, "ひゃっはー、夜だー！\nオレたちの時間だぜ！\nふおおブチ上がり～！", 1, 8015, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129301, "これは…ぼくにここで\n寝ろということか？\n一向に構わんが…", 1, 8017, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129301, "きゃっきゃっ！\nこれかわい～！\n…よし、今日も\n子供の擬態完璧だな", 1, 8018, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129301, "ふむ、良い香りだ\nだがそれだけか…\nとくに精神高揚効果は\nないのか…ちぇー", 1, 8019, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129301, "火を見ていると…\n魔導罐を思いだすな…\nかに座、元気かな…", 1, 8020, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 129301, "ええい、\n固形食物はいらん！　\nポーションを出せ！", 1, 8022, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 129301, "やはり書は文系に限る\nなんと心地よい筆致…\nぐああ突然理系文が！\nひぃ目が理系になる！", 1, 8023, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 129301, "ほああ～！\n知性の海上騎士ユニ！\nこれは新たな可能性！", 1, 8025, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 129301, "これは…なんだ？\n何が面白いんだ？\n難しいな…", 1, 8026, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 108901, "大切な人と過ごす\n正月の思い出、か…\n昔の私だったら\n考えられなかったな", 1, 8027, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 108901, "叶えたい願いなら\nたくさんあるよ\nそれでも一番を選ぶ\nなら、やはりキミと…", 1, 8028, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 108801, "神殿にあった\n恋愛おみくじ…\n引いてみようかな…", 1, 8029, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 108801, "騎士クン。来年も\nまた一緒におみくじ\n結ぼうね。約束だよ", 1, 8030, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 108701, "今度は騎士くんと\n一緒にうつった写真、\nおばあちゃんに\n送ろうっと♪", 1, 8031, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 108701, "あ、騎士くん！\n今年も…ううん\nこれからも、よろしく\nおねがいします♪", 1, 8032, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129401, "旧校舎の地下牢…\n真冬のサマー計画…\n等身大のＰＲ…\nうッ、頭が…！", 1, 8033, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129401, "暇だし鏡でも見るか。\nお、いい腹筋育って…\nあ？　何見てんだコラ", 1, 8034, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129401, "オレは金色の疾風！\nんん…なんか違うな。\n我が名は金色の…\nあ？　何見てんだコラ", 1, 8035, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129401, "モニカとユニ先輩って\n口調と身長似てるけど\n子供扱いに対する\n反応は真逆だよね…", 1, 8036, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129401, "シオリン元気かな…\n昔くちゃくちゃに\n撫でくり回して\n嫌がられたっけなァ…", 1, 8037, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129401, "べつにいんだけどさ…\nあんたの知り合いって\n普段から水着みたいな\nカッコの女多くない？", 1, 8038, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129401, "そだ、弟らがあんたに\n会いたがってたンだ。\n暇だったらいつでも\nウチ来てやってよ", 1, 8039, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129401, "特別講座は色々あった\nけど…メリゾ自体は\nまあ悪くなかったよね", 1, 8040, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129401, "水着と合わせる小物に\nセンス出るよね、\nって話をミィコ先輩が\nしててマジわかりみ", 1, 8041, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129401, "水着のミフユねえさん\nやっぱ仕上がってたね\nあれが竜殺しの体か…\nバリかっけーな…", 1, 8042, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129401, "はにょんっ！？\nや、だからさ…\n後ろから声かけんな、\nつってンじゃん…", 1, 8043, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129401, "メリゾの特別講座が\n上手くいったのも\n敏腕カメラマンがいた\nお陰だよ。ありがとね", 1, 8044, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129401, "あ？　この水着が？\n似合っ…え、なんて？\nごめ、聞こえんくて。\nもっかい言ってみて", 1, 8045, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 129401, "もう朝とかマジないわ\n昨日二人に付き合って\nオールでカラオケだよ", 1, 8046, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 129401, "はぁ授業行かなきゃ…\n真っ昼間から勉強とか\nマジつらたん過ぎ…", 1, 8047, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 129401, "今日これから夜勤で…\nいやバイトちゃーくて\nボランティアの話よ？\n校則違反よくない", 1, 8048, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129401, "うわ、ふっかふか…\nオニほど安らぐわ…\nやば、ユニってくる…", 1, 8050, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129401, "うちも妹いりゃ\nこーゆーのもなァ…\nま自分用でもいいか", 1, 8051, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129401, "ふーん…\nいいじゃんこの香り\n抜群に嗅げるわ", 1, 8052, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129401, "うちン家のルーツの森\n大火災でなくなった\nらしンだよね…\n生まれる前の話だけど", 1, 8053, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 129401, "何コレありがてぇー\n食料ほくほく過ぎて草\n米ガチで尊いわァ…", 1, 8055, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 129401, "いいけど、なんでうち\n水着で本読んでンだよ\nどーゆープレイだよ", 1, 8056, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 129401, "金色の海上騎士クロエ\n推して参る！　覇ァ！\nあ？　何見てんだコラ", 1, 8058, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 129401, "何コレ、貝の椅子？\nどーやって遊ぶんだ…\nいや座って終わりか…\nまァそりゃそーか…", 1, 8059, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129801, "さてと…\n明日の訓練メニュー\n考えねえとな", 1, 8060, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129801, "また新入りの女子に\n弁当もらっちまった…\nありがたく食うけどよ", 1, 8061, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129801, "種族なんて関係なく\nみんなが力を合わせて\nランドソルを守る…\nいい時代になったよな", 1, 8062, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129801, "誰にだって長所はある\n新入りのいいところを\n見つけてやるのも\n教官の仕事だ", 1, 8063, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129801, "厳し過ぎても\n甘過ぎてもダメ…\n教官ってのは難しいぜ", 1, 8064, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129801, "戦いが苦手な新入りを\nどうフォローするか…\n後であいつと\n相談してみるか", 1, 8065, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129801, "あいつは優し過ぎて\n戦いに向いてねえ\n…そういう甘さ\n嫌いじゃねえけどな", 1, 8066, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129801, "強くなる近道はねえ\n地道な鍛錬あるのみだ\nさ、訓練始めるぞ！", 1, 8067, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129801, "新入りにも\nいつも言ってるが…\n装備の手入れは\n念入りにしろよな？", 1, 8068, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129801, "座学も受けてえのか？\nいい心がけだな！\nなんでも教えてやるよ\n…魔法以外ならな", 1, 8069, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129801, "新入りたちがよく\n恋バナしててな…\nお、おまえは…誰か\n気になるやついるか？", 1, 8070, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129801, "なあ、ストレッチ\n手伝ってくれねえか？\n訓練前にしっかり\n筋肉ほぐさねえと…", 1, 8071, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129801, "ずいぶん頑張って\n鍛えたよな、おまえ\nよし！　ご褒美に\nお菓子作ってやるよ♪", 1, 8072, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 129801, "訓練日和だな！\nさぁて…まずは\n走り込みといくか！", 1, 8073, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 129801, "昼飯はしっかり食えよ\nでなきゃ、\nこの後もたねえぜ？", 1, 8074, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 129801, "とっくに消灯時間だ\nさっさと寝な？\n明日も早いんだからよ", 1, 8075, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129801, "休むのも訓練のうちだ\n今はのんびり\nさせてもらうぜ", 1, 8077, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129801, "いい面構えだ\n気に入ったぜ！\nあたしの班に入るか？\n…なんてな♪", 1, 8078, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129801, "…いやされる匂いだな\n新入りたちにも\nすすめてみるか…", 1, 8079, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129801, "あったけえからって\n寝ないようにしねえと\n教官としての\n威厳に関わるからな…", 1, 8080, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 129801, "食事も訓練の一環だ\nとはいえ食い過ぎには\n気をつけねえと…", 1, 8082, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 129801, "トーゴクの剣術…か\nなにか訓練に\n活かせるかもしれねえ", 1, 8083, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129901, "あのおかたに近付く\n不埒な輩には、特別な\n訓練で地獄を見て\nいただきましょう", 1, 8085, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129901, "これだけ広ければ\n多少派手に\n動き回っても\n問題なさそうですね", 1, 8086, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129901, "…今誰か私を\n「鬼」教官と\n呼びませんでしたか？", 1, 8087, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129901, "新しく開発したこの薬\n誰か試してくれる人は\nいませんか？", 1, 8088, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129901, "厳しくすれば投げ出し\n優しくすればサボる\n人というのはどこまで\n度し難いのでしょう", 1, 8089, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129901, "はじめましょう\nあのおかたの心を\n射貫くための\nシミュレーションを", 1, 8090, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129901, "あらゆる苦難と\n忌々しい女から守る\nためにも、私自身も\n鍛えておかねば", 1, 8091, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129901, "あなた様、そんなに\n見つめられると…え？\nエリコはカッコいい？\nええ、当然ですとも", 1, 8092, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129901, "不調があればすぐ\n報告してください\n体のケアも教官の\n大事な役目ですので", 1, 8093, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129901, "新しい訓練器具の\n使い心地はどう…え？\n重すぎる？　これでも\n軽くしたのですが…", 1, 8094, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129901, "運動後には特製の\n栄養ドリンクをどうぞ\nクスクス、何も\n入れたりしてませんわ", 1, 8095, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129901, "よくがんばりました\nあなた様。あぁ…\nお疲れになったお顔も\n素敵ですわ", 1, 8096, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129901, "あなた様、二人だけの\n遠征訓練へ\nまいりましょう。\n既に準備万端ですわ", 1, 8097, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 129901, "まだ眠いですか？\nならばストレッチで\n体を痛めつ…ではなく\nほぐしていきましょう", 1, 8098, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 129901, "お昼はお弁当をご用意\nいたしました。量も\nスタミナも増し増し\n残さず食べてください", 1, 8099, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 129901, "よい睡眠こそ健やかな\n体に不可欠。ですので\n私が寝ずの番をさせて\nいただきますわ", 1, 8100, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129901, "休憩用に使おうと思い\nましたが、心地よくて\n立てなくなって\nしまいますわ…", 1, 8102, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129901, "中に重しを詰めれば\n愛でながら運動が\n可能ですわ", 1, 8103, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129901, "香りでリラックスさせ\n同時に暗示を\nかけてみましょうか", 1, 8104, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129901, "…くっ、眠気を誘う\nこの暖かさ…\n油断も隙もありません", 1, 8105, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 129901, "主食はこちらで十分\nまかなえますわね\nそれでは私はおかずを\n作りましょう", 1, 8107, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 129901, "まあ、実用書の類も\n揃っているのですね\nとても助かりますわ", 1, 8108, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129601, "……（そわそわ）", 1, 8110, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129601, "ここに寄らずの花陣…\n作ったらダメですよね", 1, 8111, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129601, "部屋の隅が\n落ち着きます\nそれか、陽の当たる所", 1, 8112, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129601, "身を隠せる場所が\nあると…\n助かるんですけど", 1, 8113, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129601, "複雑な造りの家ですね\n触って壊れたり─\nしませんか？", 1, 8114, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129601, "出入りするひと\nみんな、笑顔…\n…変わった家ですね", 1, 8115, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129601, "この家なら安全─\nかもしれません\n魔物が出ないので", 1, 8116, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129601, "これも、視察の一環\n遊びに来たわけじゃ\nないです", 1, 8117, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129601, "長話─苦手です\n疲れるので…\nふぅ…", 1, 8118, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129601, "ここで友達を作れと\nネアさんが…\nそう、言われても…", 1, 8119, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129601, "世話をするのは\n得意です\n…花の、ですよ？", 1, 8120, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129601, "媛へのお土産を\n探してるんですけど…\n…貴方のおすすめ、\n聞いておきます", 1, 8121, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129601, "平和でのんきで…\n貴方らしい家です\n一応、褒めてます", 1, 8122, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 129601, "花に、お水─？\n…いりません", 1, 8123, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 129601, "日が高いうちは\nむやみに外へ出ません\n面倒事、多いので…", 1, 8124, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 129601, "夜番。私がします\nあまり眠らない\n性質なので…", 1, 8125, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129601, "ここなら根を張れる\nかもしれません\n…張りませんけど", 1, 8127, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129601, "こんなにやわな体じゃ\n身を守れませんよ─\n…あ。生き物じゃ\nないんですね", 1, 8128, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129601, "花みたいに─\nいい匂い、です", 1, 8129, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129601, "火を見てると、\nジオ・ゲヘナを\n思い出します…ふぅ", 1, 8130, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 129601, "食事も立派な戦支度\n媛が言ってました", 1, 8132, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 129601, "知識も武器の一つだと\n媛が…でも、\nこれでどうやって…？", 1, 8133, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 130701, "天気もいいしお花畑に\nピクニックでも\n行こうかな♪", 1, 8135, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 130701, "あ、どこからか\nお花の匂いが…\nいい香りですね", 1, 8136, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 130701, "新しいシャベルとか\nじょうろもほしいなぁ\nお小遣いしっかり\nためないとね", 1, 8137, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 130701, "お花飾りたいけど\n花瓶はどこかな…\nあとであの人に\n聞いてみよっと", 1, 8138, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 130701, "お花の観察日記\n書かなきゃ\n日々勉強は\n欠かせませんよ", 1, 8139, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 130701, "衣装にお花を足して\nみたけど、あの人\n気付いてくれるかな？", 1, 8140, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 130701, "この花冠、上手に\n作れたかも…\n日頃のお礼にあの人に\nあげようかな…", 1, 8141, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 130701, "あなたは何のお花が\n好きですか？　ほら、\n一応知っておいたら\n役に立つかもですし", 1, 8142, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 130701, "そのお花、乱暴に\n扱っちゃダメですよ\n花粉が服について\nとれなくなりますから", 1, 8143, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 130701, "土いじりしたから\n服が汚れてますよ\nはたいてあげますから\nじっとしててください", 1, 8144, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 130701, "そんなにすぐに芽は\n出ませんよ\nまあ、楽しみなのは\n私も同じですけど…", 1, 8145, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 130701, "この衣装、そんなに\n気になります？　なら\nこうしてお花を\n着ければおそろいです", 1, 8146, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 130701, "お花の種を私に？\nありがとうございます\nせっかくなので一緒に\nお世話しませんか？", 1, 8147, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 130701, "お花にお水をあげるの\n手伝ってもらえます？\nお水は朝にあげるのが\nいいんですよ", 1, 8148, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 130701, "とっておきのお花見\nスポットに\nご案内しますよ\nお弁当も忘れずに！", 1, 8149, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 130701, "もうこんな時間\n早く寝ましょう\n草木も眠るなんとか\nって言いますからね", 1, 8150, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 130701, "お花のお手入れって\nけっこう疲れますね…\nここでちょっと一息\nしちゃおっと", 1, 8152, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 130701, "耳にお花を着ければ…\nえへへ、すっごく\nかわいくなったね！", 1, 8153, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 130701, "これはラベンダー\nこっちはミモザ\nフローラル系の香り\nっていいですねぇ", 1, 8154, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 130701, "ポカポカしますね～\nまるで春の陽気\nみたいです～", 1, 8155, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 130701, "お花を一輪さすだけで\nご飯の時間がとても\n華やかになりますね", 1, 8157, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 130701, "お花の本が\nいっぱいある！\n今日はこれで\nお勉強です！", 1, 8158, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 130801, "ふんふ～ん♪\n華やかな服を着ると\n心がウキウキします♪", 1, 8160, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 130801, "花祭りで出会った\nみなさん…\nお元気でしょうか？\nまたお会いしたいです", 1, 8161, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 130801, "はっ！　そうでした\nこの服の時は\nメイドのお仕事は\nお休みでした…", 1, 8162, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 130801, "この服は替えが\nありませんから\n大きなドジには\n気を付けないと…", 1, 8163, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 130801, "目立ったほつれもなし\n…うん、いい感じに\n補修できました♪", 1, 8164, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 130801, "メイド姿でなくても\n心はいつでも\nお嬢さまの\nメイドです♪", 1, 8165, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 130801, "この服を着ると心が\n浮き立つのはあのかた\nと一緒にいる気持ちに\nなるからでしょうか？", 1, 8166, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 130801, "はい、今日は一日\nこの服で過ごそうと\n思います\n変じゃないですよね？", 1, 8167, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 130801, "花祭りのおかげで\n少しだけお花に\n詳しくなった…\nような気がします！", 1, 8168, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 130801, "花祭り、今度は\nお嬢さまと救護院の\nみんなと一緒に\n行ってみたいですね", 1, 8169, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 130801, "今度キョウカちゃんと\n花祭りの服でお花を\n見にお出かけ\nするんです", 1, 8170, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 130801, "メイド服を全部洗濯\nしちゃうなんて…\nドジっちゃいましたぁ", 1, 8171, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 130801, "花祭りの服は私たちの\n思い出の服ですから…\nこれからも\n大切にします♪", 1, 8172, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 130801, "はう、そうでした\nお花にお水を\nあげる時間です！", 1, 8173, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 130801, "お昼ご飯は\nバスケットを持って\nお花畑に食べ\nに行きませんか？", 1, 8174, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 130801, "ふぁあ…繕い物を\nしていたらこんな時間\nになってしまいました\n早く寝ないと…", 1, 8175, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 130801, "ふわふわしてて\nきもちいいです～\nどんな生地を\nつかってるんだろ～？", 1, 8177, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 130801, "かわいいですね～\nぬいぐるみさんにも\n服をつくって\nあげたいです♪", 1, 8178, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 130801, "いい香りです～\nあれ？　もしかして\n私の服についてる花と\n同じ香りが…？", 1, 8179, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 130801, "暖かいですけど\nあまり近づいちゃうと\n頭の花飾りが乾燥\nしちゃいそうですね…", 1, 8180, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 130801, "もぐもぐ…\nいい花を咲かせるには\nしっかりとした栄養が\n必要だそうです！", 1, 8182, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 130801, "なるほどなるほど…\nお花のお手入れは\n奥が深いですね…", 1, 8183, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131001, "マホマホ王国にも\nサーカス団設立や\n夢と希望を届けますえ", 1, 8185, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131001, "せっかくの晴れ舞台や\nかっこいいポーズも\nできるようにしたいわ", 1, 8186, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131001, "むむむむ～っ。うーん\nクルルはんみたいに\n胸から宝石を出すんは\n難しいみたいやわ", 1, 8187, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131001, "あむっ…んん～♪\nやっぱり練習のあとは\n甘いものに限るわぁ", 1, 8188, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131001, "アーサーたちの衣装も\n今のうちに考えんと…\n生地、足りるやろか", 1, 8189, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131001, "王子はんとなら\nどんなアイデアも\n見つけられます♪", 1, 8190, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131001, "うちの晴れ姿が\n王子はんの目に一等\n輝いて見えますように", 1, 8191, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131001, "キリっとしたうちも\nかっこいい？\nもう、上手やなぁ", 1, 8192, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131001, "新しい演目考えたんよ\nなぁ王子はん、意見\n聞かせてもらえます？", 1, 8193, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131001, "聞いてや王子はん\n動物さんたちとさらに\n息が合うてきたんよ\nみんな頑張り屋さんや", 1, 8194, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131001, "演技の感想なら\n大歓迎どすえ♪\nたんと聞かせてや～", 1, 8195, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131001, "王子はん、本番当日は\n最前席で見たってな\nそしたらうち、もっと\n頑張れますさかい♪", 1, 8196, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131001, "この技をあなたに\n捧げます…なんて♪\n驚いたやろ？\nどっきり作戦成功や♪", 1, 8197, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 131001, "素敵なショーのためや\n今日も焦らず一歩ずつ\n成長していきまひょ\nな、王子はん♪", 1, 8198, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 131001, "お弁当、王子はんも\n一緒にいかがどす？\nみんなで食べて団結や", 1, 8199, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 131001, "夜のサーカスも\n幻想的でとっても\n魅力的なんよ\n一緒にいきまひょか♪", 1, 8200, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131001, "練習が終わったあとは\nの～んびりして\n体力を回復せな…", 1, 8202, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131001, "この子たちとなら\n空中ブランコや\n玉乗りもええなぁ", 1, 8203, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131001, "いい香りやなぁ\n本番前の緊張も\n和らげてくれそうやわ", 1, 8204, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131001, "このあったかい感じ…\nスポットライトの熱を\n思い出すわぁ", 1, 8205, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 131001, "ちゃんと食べへんと\nいい演技はできまへん\n団長はんも言うてたわ", 1, 8207, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 131001, "動物さんたちのことも\nもっと勉強せな\n大事な仲間やさかい", 1, 8208, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105601, "器用じゃないが、\n情には厚いよ\nこういう女を「一途」\nってそう言うんだろ？", 1, 8210, 12, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103, 0, 0, 105601, "斬れないものはないと\n思ってきたさ。だけど\n…この気持ちだけは\n斬れないね、絶対に", 1, 8211, 12, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 130901, "んしょ、んしょっ…！\n日頃の感謝を込めて\nヒーローさんのお家を\nきれいにしますです♪", 1, 8212, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 130901, "ふむふむ…\n新アトラクションは\nこのギルドハウスを\nモチーフに…", 1, 8213, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 130901, "ごそごそ、こそこそ…\nヒーローさんに内緒で\nどこかにお礼の宝石を\n隠しておきましょう…", 1, 8214, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 130901, "パークの運営が\n落ち着いたら\nわたしもギルド活動？\nしてみたいですます♪", 1, 8215, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 130901, "こんなすてきな\nおうちに住めたら…\nいえ！　うさぎ小屋で\n十分です～っ", 1, 8216, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 130901, "ヒーローさんのおうち\nだって意識したら\nアトラクションよりも\nドキドキしてきます…", 1, 8217, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 130901, "ヒーローさんのことを\n考えると顔が赤く…\nこれってもしかして\nひょっとして…？", 1, 8218, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 130901, "あっ、ヒーローさん！\n話しかけてくれた記念\nということで、宝石\n受け取ってください♪", 1, 8219, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 130901, "元気がない時は\n語尾にぴょんですっ！\nさあヒーローさんも\nやってみるぴょん♪", 1, 8220, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 130901, "ヒーローさんへのご恩\nおばあちゃんうさぎに\nなるまでに、お返し\nできるでしょうか…？", 1, 8221, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 130901, "ヒーローさん！\n二人で一緒に\nパレードの練習\nしませんか～♪", 1, 8222, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 130901, "あはは♪\nヒーローさんは\n人を笑顔にする力が\nありますです♪", 1, 8223, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 130901, "ヒーローさんに\n一日一恩返しですっ！\n返し終わるまで\nやめませんですます♪", 1, 8224, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 130901, "おはようございます♪\nんしょ、んしょっ…！\n気持ちよく起きられた\n記念に宝石どうぞ♪", 1, 8225, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 130901, "お昼にフードコートで\nかばばん君バーガー\n買ってきましたよ！\n一緒に食べますです♪", 1, 8226, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 130901, "今日も一日\nお疲れさまですっ♪\nマッサージしますから\n寝そべってください♪", 1, 8227, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 130901, "はふぅ～…♪\nお仕事の疲れも\nぴょんぴょ～んって\n飛んでっちゃいます♪", 1, 8229, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 130901, "わぁっ、とっても\nかわいいですますっ！\nギルドハウスの\nマスコットですね♪", 1, 8230, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 130901, "ヒーローさんの好きな\n香り…これを宝石に\nつければ、受け取って\nもらえるでしょうか？", 1, 8231, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 130901, "はあ、気持ちいい♪\nヒーローさんの\n好きな温度に\n調整しときますです♪", 1, 8232, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 130901, "支配人は体が資本っ！\nたくさん食べ…すぎて\n動けなくなっちゃい\nましたぁ～…！", 1, 8234, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 130901, "パークの\nガイドブックとか\nあったらいいですね♪", 1, 8235, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131101, "外あつすぎ溶けるわ～\nやっぱ部屋ん中で\n水着がラクだねー", 1, 8237, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131101, "ン～\nカリザきゅんどこー？\nもしかして逃げた？", 1, 8238, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131101, "ミソラちゃんと\nリリちゃん誘って\nそろそろイイコト\nしよっかなー？", 1, 8239, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131101, "おねえちゃんに\n頼まれたシゴトは\nまた今度にすっかー\n夏休みだしー♪", 1, 8240, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131101, "騎士きゅんの\nプリンセスフォームも\n見てみたいんだよなー\nンフ♡", 1, 8241, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131101, "スライムを\nこーしてあーしたら\n騎士きゅんの泳ぐ\n練習に使えるっぽ？", 1, 8242, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131101, "おかえり～♡\nって水着で迎えたら\n騎士きゅん喜んで\nくれるかなー？", 1, 8243, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131101, "騎士きゅん\nこのサングラス\nかけてみる？\nぜってー似合うよー", 1, 8244, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131101, "日焼け痛かったら\nいつでも言ってねー\nスライムでひんやり\nしてあげるよ～♪", 1, 8245, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131101, "今から外で\n焼いてこっかなー\n騎士きゅんもどー？", 1, 8246, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131101, "スライムマッサージ\nしてあげよっかー\nほーれ、もみもみ～♡", 1, 8247, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131101, "騎士きゅんが選んだ\n水着も着てみたーい\nネアおねえちゃんに\nどんなの着てほしい？", 1, 8248, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131101, "騎士きゅ～ん\n尻相撲しよっかー♪\nおねえちゃんに\n勝てたらご褒美だぞ～", 1, 8249, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 131101, "暑すぎてムダに\n早起きしちった～\n二度寝すっか～\n…ふぁあ…", 1, 8250, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 131101, "夏バテしても\nスイカとかかき氷なら\n全然ウェルカム\nなんだよなー", 1, 8251, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 131101, "今日はこのまま\n水着で寝るかー\n夢ん中で泳ごー", 1, 8252, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131101, "いったんキューケイ…\nンー…よし\n充電カンリョ～♪", 1, 8254, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131101, "仲間になりたそうに\nこっち見てんねー\nきゃわいいしおっけ～", 1, 8255, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131101, "ン～いいニオーイ\nお日様あたった\n騎士きゅんの\n次くらいかな～", 1, 8256, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131101, "カラダ冷えてきたから\nたすかるーおかげで\nまだまだ水着イケるわ", 1, 8257, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 131101, "うめ～\nとまんねぇ～\n夏バテどこいった～？", 1, 8259, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 131101, "ふむふむ…\nこれは意外と\nぬるっと読めるっぽー", 1, 8260, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 131101, "ンフフ～\n海の戦士ってカンジ？\nアリよりのアリだね～", 1, 8262, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 131101, "あーやべ\n一瞬で寝そー\nヤバだわ～", 1, 8263, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129701, "服や紙が易々と\n燃えぬとは\nランドソルとはまこと\n住み易き国で候", 1, 8264, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129701, "どの世界にあっても\n民は活力に溢れ\n街は賑わうもの…\n善哉、善哉", 1, 8265, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129701, "食べ歩きに世界征服…\nギルドとはかくも\n自由気儘な徒党に\nありんすなぁ", 1, 8266, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129701, "連絡は…今日もなし\n我が妹子は相変わらず\n放埓なことよ喃", 1, 8267, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129701, "モネは相変わらず\n警戒しきりなれど…\nそのようなところも\n愛らしくあり申す", 1, 8268, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129701, "なんと長閑な世にや\nありけむ…この平和は\n騎士殿たちの努力の\n賜物にありんす喃", 1, 8269, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129701, "『王家の装備』は\n使わぬのかと？\n…フフ。すでに\n用いており申す", 1, 8270, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 129701, "今ばかりは媛という\n立場は置いて\nこの地の者らと\n歓談したく候", 1, 8271, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129701, "強さの秘密？　…フフ\n女子の秘密を暴くのは\n野暮にござんす\nご寛恕いただきたく候", 1, 8272, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129701, "此方の家屋、\n見目以上に\n堅牢であり申す\n何ぞ仕掛けがありや？", 1, 8273, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129701, "心地よい時間とは\n疾く過ぎ去りゆくもの\n此処で過ごすと改めて\n実感できる喃", 1, 8274, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 129701, "視線が気になる？\nこれはすみませぬ\nなにぶん、お前さまは\n見ていて飽きませぬ故", 1, 8275, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 129701, "お前さまの前では\n誰もが心を開く…\nなんとまぁ罪作りな\n色男にありんすな？", 1, 8276, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 129701, "よく眠れましたかの？\n寝る子は良く育つ、と\n古くより申します由", 1, 8277, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 129701, "美食殿の作法に倣い\n皆で共に昼餉を頂くと\n致しんしょう", 1, 8278, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 129701, "逢魔ヶ時は望まぬ客も\n参るもの…お前さまも\n気をつけなんし", 1, 8279, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 129701, "わっちの蛇に\n勝るとも劣らぬ\n座り心地で候", 1, 8281, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 129701, "魂、万物に宿る\nとの云いあらば\n情愛を注いでやるのが\nよかろうの", 1, 8282, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 129701, "いと香しき哉…\n騎士殿は香の道にも\n詳しくあられるか", 1, 8283, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 129701, "ジオ・ゲヘナの\n灼熱と比ぶれば\nそよ風と変わり\nござんせん", 1, 8284, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 129701, "美味なる哉…この味\nぺコリーヌ姫が好むも\n道理にありんす", 1, 8286, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 129701, "トーゴクに、オーマ…\n書で見るだけでも\n心惹かれる土地よの", 1, 8287, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131201, "暑い日には\n冷たいものですよねー\nかき氷とか食べて\nすっきりしたいです★", 1, 8289, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131201, "海への行き来は\nテレポーテーションに\nお任せです★", 1, 8290, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131201, "トロピカルジュースが\n飲みたいでーす♪\n海の家で見たんですよ\nカップル用ジュース★", 1, 8291, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131201, "ご飯どうしようかなー\nあ、海鮮ラーメンとか\n美味しそうかも★", 1, 8292, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131201, "水鉄砲、普通に撃つと\n少し物足りないかも…\nどるるるる～ん★って\n感じが欲しいですよね", 1, 8293, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131201, "コイビトっぽいこと\nアオハルっぽいこと\nいっぱいしたいです★\n修羅場とかも♪", 1, 8294, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131201, "騎士さんが溺れたら\n絶対助けに行きますね\n人工呼吸でもなんでも\nしてあげますから★", 1, 8295, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131201, "騎士さーん\n日焼け止めの塗り直し\nお願いします★\n優しくしてくださいね", 1, 8296, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131201, "海で食べたフルーツ？\nはい、まだありますよ\nこっちでもあーんして\n食べさせましょうか★", 1, 8297, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131201, "ぺんぽこりんは\nどうしたのかって？\nどうしたんでしょうね\nどーでもいいです★", 1, 8298, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131201, "今日はどんな\nイイコトしましょうか\n思い出作り？\n人助けもいいですね★", 1, 8299, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131201, "ヒモを引っ張ったら…\n「セ・キ・ニ・ン」\n取ってもらいますよ？\nなーんてウソですけど", 1, 8300, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131201, "みんなでイイコトも\n楽しいですけど～\n…二人でイイコト\nしちゃいますか？", 1, 8301, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 131201, "朝ですよー！\n今日も一日、\nイイコト日和ですね♪", 1, 8302, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 131201, "日差しにご注意ですね\n日焼け止め…誰かに\n塗ってほしいなー？\n…ちらっ★", 1, 8303, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 131201, "夜の海って\n惹かれちゃいますよね\n…ロマンチックです★", 1, 8304, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131201, "う～ん、まったり～\nこれぞ新ジャンル\nおうちバカンス★", 1, 8306, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131201, "よーしよーし★\nわたしとイイコト\n一緒にしましょうねー", 1, 8307, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131201, "いい香りですねー\nリラックスタイムに\nちょうどよさそう★", 1, 8308, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131201, "水遊びのあとは\nしっかり体を\n温めないとですよね★", 1, 8309, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 131201, "栄養補給もバッチリ★\n遊びもイイコトも\nお腹が減ってたら\nできませんからねー？", 1, 8311, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 131201, "今だと海関連の本とか\n興味あるんですよねー\n魚とか海難救助とか★", 1, 8312, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 131201, "かっこいー★\n名前はー…\nぷんぷくぽんとか？", 1, 8314, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 131201, "映えますねー\n写真撮ったら\n素敵な思い出に\nなりそうです★", 1, 8315, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131601, "海でもないのに\nこの姿でいるのは…\nやはり、場違いでは\nありませんか…？", 1, 8316, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131601, "スイカとは割ることと\n見つけたり！\n…ですが目隠しすれば\n見当たらないもの…", 1, 8317, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131601, "まぁ、膝に砂が…\n気がつきませんでした\nまるでわんぱくな\nお子様のよう…ふふっ", 1, 8318, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131601, "家の中でスイカを割る\nわけにはまいりません\nここは潔く包丁で\n切りましょう", 1, 8319, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131601, "ミソラ様やネア様の\n大胆さを見習って…\nき、騎士きゅーん…★\n難しいですね…", 1, 8320, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131601, "きゅ、急に暗く…！？\n…失礼しました\n鉢巻きが下がってきた\nだけでした…ううぅ", 1, 8321, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131601, "わたくしに剣の才は\nあるのでしょうか…\nいえ、何事もまずは\nやってみなくては", 1, 8322, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131601, "なにか羽織った方が\nよろしいですよね…？\nそのままでいい？\nそれはそれで…その…", 1, 8323, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131601, "あの、兄様…？\nスイカの種を飛ばす\n遊びもあるというのは\n…本当なのでしょうか", 1, 8324, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131601, "強くなるための修行は\nまだ始まったばかり\nまずは己の目指すべき\n道を見定めなくては", 1, 8325, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131601, "同じ薄着であっても\n黒い天使の姿より…\n気が楽です", 1, 8326, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131601, "はい、ただいまー！\n…あっ！\n海の家でのお手伝いの\nくせが、つい…", 1, 8327, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131601, "兄様さえよろしければ\nまた一緒に海へ…\nあ…申し訳ありません\n浮ついてしまいました", 1, 8328, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 131601, "本日もイイコトをして\n清々しい一日を\n過ごしたいものです\nまさに一日一善ですね", 1, 8329, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 131601, "お昼時になると\n海の家の料理を\n思い出してしまいます\n焼きそばにかき氷…♪", 1, 8330, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 131601, "兄様…よろしければ\n浜辺を歩きませんか？\nきっと、月が\nきれいでしょうから", 1, 8331, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131601, "ふわふわ心地よすぎて\n眠くなってしまいます\n水着姿では\nいけません…ふぁ…", 1, 8333, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131601, "なんてよい手触り…\n癖になってしまいます\nふふ、よしよし…♪", 1, 8334, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131601, "爽やかな香り…♪\n日焼け止めクリームを\n思い出してしまいます", 1, 8335, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131601, "冷たい水で冷えた体が\n温まります…♪\nまるで兄様のようです", 1, 8336, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 131601, "お腹が空いては\n修行ができません…！\n動きに障りない程度に\n栄養の補給を…！", 1, 8338, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 131601, "剣術とスイカ割りが\n上達する指南書…\n見つかりませんね…", 1, 8339, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 131601, "さあ、ともに大海原を\n参りましょう！\nなんて…ふふっ♪", 1, 8341, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 131601, "まるでおとぎ話の\n登場人物になった\nような心持ちです…♪", 1, 8342, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131701, "あたしはなんで水着で\n室内をうろついてるの\nかしら…不思議だわ", 1, 8343, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131701, "あ、天井が汚れてる\n仕方ないわね\nひとっ飛びしますか", 1, 8344, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131701, "はい、お疲れさま♪\n出迎えの言葉は\nやっぱこれよね♪", 1, 8345, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131701, "そういえばあたしって\n羽があるけど\n泳げるのかしら…", 1, 8346, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131701, "あいつのために\n浮き輪も用意したし\n抜かりはないわよ", 1, 8347, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131701, "海までのガイド計画も\nまとまったわね\nふふっ…楽しみだわ", 1, 8348, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131701, "この水着、あいつが\n選んでくれたのよね\n…大事にしないと", 1, 8349, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131701, "ネビアは最近どう？\n迷惑かけてない？\n…かけてるのね", 1, 8350, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131701, "あんた…部屋の片付け\nくらい自分でしなさい\n散らかってたわよ？\n…片付けておいたけど", 1, 8351, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131701, "もしかして暇なの？\nだったら記憶でも\n見せましょうか？\n凄く恥ずかしいやつ♪", 1, 8352, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131701, "夏ってこんなに\n暑いのね…\n海にばかり行く\n気持ちが分かったわ", 1, 8353, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131701, "あんたの水着って\n新しいの買わないの？\nなんならあたしが\n選んであげるわよ♪", 1, 8354, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131701, "気軽に触れ合える\n距離にいる…\nあたしにとって\nとても大事なことよ", 1, 8355, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 131701, "あらおはよう\n夏の朝は暑いわね\nシャワーでも浴びて\nさっぱりしなさい", 1, 8356, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 131701, "お昼は何を食べるの？\nガイド妖精として\n提案しましょうか？", 1, 8357, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 131701, "夜更かしは厳禁よ\n無理やり眠らせて\nあげましょうか？", 1, 8358, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131701, "アメスさまだって\nゴロゴロするときは\nあるのよ～", 1, 8360, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131701, "…ふふっ♪\n何よ、あたしが\nかわいいものを\n愛でちゃだめなの？", 1, 8361, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131701, "こういういい香り…\n塔の中じゃなかなか\n楽しめないのよね", 1, 8362, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131701, "水着を着てるのに\n温まるのって\n矛盾してない？\n気にしすぎかしら", 1, 8363, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 131701, "あんたと行動するなら\n沢山食べられるように\nならなきゃね…！", 1, 8365, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 131701, "文字を読むのと\nデータを読むのは\nまったく違うわ", 1, 8366, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 131701, "へぇ、面白いじゃない\nこういう経験、昔じゃ\nできなかったわ", 1, 8368, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 131701, "面白いクッションね\nところであんた…\nあたしで遊んでない？\n気のせいならいいけど", 1, 8369, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118601, "また新しい悪戯\n思いついちゃった♪\n誰に仕掛けようかしら", 1, 8370, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118601, "あ～あ、退屈…\nフィオでもからかって\nこようかしら♪", 1, 8371, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118601, "ミソギの悪戯って\nレベル高いのよね…\n負けてらんないわ！", 1, 8372, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118601, "家事ニガテなのよね～\nコッコロたちには\n感謝しないとね", 1, 8373, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118601, "私も魔物料理に\n慣れてきちゃった…\n毒されてるわね…", 1, 8374, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118601, "あいつに仕掛ける\n次の悪戯は～…♪\nこれ…ううん…\nやっぱこっちかしら？", 1, 8375, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118601, "結局ギルドハウスが\n一番落ち着くわね\n…私たちの家だもの", 1, 8376, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 118601, "悪戯のコツでも\n教えてあげよっか？\n…え～興味ないの？", 1, 8377, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118601, "これからもずーっと\n退屈しない毎日に\nしてあげる♪\n私の悪戯でね♪", 1, 8378, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118601, "ちょうど退屈してたの\nたーっぷりと\n構ってもらうわよ♪", 1, 8379, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118601, "案内が必要なら\n頼ってくれていいわよ\n一応は私だって\nガイド妖精だし！", 1, 8380, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 118601, "冒険のガイドなら\n私に任せなさい♪\nある意味忘れられない\n旅にしてあげる♪", 1, 8381, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 118601, "妖精がいる家は\n栄えるのよ♪　だから\nずっといてあげる\nあんたのそばにね♪", 1, 8382, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 118601, "きゃははっ！\n寝ぐせついてるわよ？\nこのままにしましょ！\nだって面白いし！", 1, 8383, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 118601, "自由に外へ行けるって\nすっごく幸せなことよ\nさ、散歩行きましょ♪", 1, 8384, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 118601, "安心していいわ\n寝てるあんたに\n悪戯しないから\n…あんたにはね♪", 1, 8385, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 118601, "悪戯しすぎて\n疲れたから、ちょっと\n休憩しよっと♪", 1, 8387, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 118601, "ガイド妖精たる私が、\nこんな造形物に\n惹かれる…わけが……\nかわいい♪", 1, 8388, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 118601, "この香りを囮にして、\n周りに悪戯を仕込む…\n完璧ね♪", 1, 8389, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 118601, "不思議ねぇ…私は\n温度とか関係ないのに\n離れられないわ", 1, 8390, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 118601, "食事の必要がない体…\nつまり、食べたければ\n食べていいのよね～♪", 1, 8392, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 118601, "へぇ…最近はこんな\n悪戯もあるのねぇ…\nあいつで試さないと♪", 1, 8393, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131801, "わる～い子は\nトゥンヌス漁船に\n乗ってもらおうかな～", 1, 8395, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131801, "たまにはファミリーの\nみんなにもお休みを\nあげないとね～☆", 1, 8396, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131801, "海辺に素敵な\nバーがあるんだ～\n彼が大人になったら\n一緒に行きたいな♪", 1, 8397, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131801, "夏の風を肌で\n感じるの好きかも～\nラーゴはどう？", 1, 8398, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131801, "海岸線を\nのんびり散歩もいいね\n砂や貝殻に紛れた\nお宝探しの旅だよ～☆", 1, 8399, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131801, "みんなで集まって\nビーチパーティーでも\n開いちゃおっかな～♪", 1, 8400, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131801, "さて、この水着…\n彼は気に入って\nくれてるかな～？", 1, 8401, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131801, "心ゆくまで夏を\n楽しんじゃお～☆\nほら君も♪", 1, 8402, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131801, "水着似合ってるって？\nふふ♪　うれしいこと\n言ってくれるね～☆", 1, 8403, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131801, "熱中症には\n気を付けないと\nだめだよ～？", 1, 8404, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131801, "海の家ならうちの\nファミリーでやってる\nとこにしよっか☆\n豪遊しちゃお～♪", 1, 8405, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131801, "君と一緒なら\nどんな夏になるのか\n楽しみだね～♪", 1, 8406, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131801, "君といると子供心を\nくすぐられちゃうな～\n大人だって思いっきり\n遊んでいいんだよね♪", 1, 8407, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 131801, "さ、今日は何をして\n過ごそっかな～…\nねえ、君は何したい？", 1, 8408, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 131801, "ふふ♪\n波音が心地良い\n時間だね～☆", 1, 8409, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 131801, "今夜は星空の下で\n乾杯しよっか～☆\n飲み物取ってくるね♪", 1, 8410, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131801, "ここ涼しい～♪\nちょっと一休み\nしていこっか☆", 1, 8412, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131801, "こういうのも良いけど\nやっぱりラーゴの方が\nかわいいかな～♪", 1, 8413, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131801, "爽やかな香りだね～\nこういう感覚も絵で\n表現してみたいな～", 1, 8414, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131801, "続きを描く前に\n原稿を乾かさないとね\nインクがにじむと\n直すの大変なんだよ～", 1, 8415, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 131801, "今日は水着だし…\n食べるのは控えめに\nしておこっかな", 1, 8417, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 131801, "話題の作品は\nチェックしないとね～\nあっ、この作家さん\n新刊出すんだ～", 1, 8418, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 131801, "うちの出店の客引きに\nこういう乗り物系を\n使うのもアリかな～☆", 1, 8420, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 131801, "これ、背景用の\n参考資料に\n一つ欲しいかも♪", 1, 8421, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 181201, "ここに帰るのも\n久しぶり…あれから\n変わったところは\nあるかしら？", 1, 8422, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 181201, "バイトも復帰しなきゃ\nうちのギルドにとって\n食費は重要だもの…！", 1, 8423, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 181201, "コッコロマ…っ\n…うぅ…\nこの癖は\n直らないのね…", 1, 8424, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 181201, "この世界はまだまだ\n知らないことだらけね\n『裏世界』とか…", 1, 8425, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 181201, "必ずみんなを\n助け出してみせるわ\n…もちろん、兄さんも", 1, 8426, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 181201, "またみんなと\n暮らせるなんて…\n夢じゃ、ないのよね", 1, 8427, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 181201, "たくさんの人が\n私に優しくしてくれる\n…だからこそ、私も\n頼れる人になりたいわ", 1, 8428, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 181201, "次はランドソルの\n街の外にも行きたいわ\nあの…よかったらまた\n一緒に回ってくれる？", 1, 8429, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 181201, "独りぼっちで眠るのは\nやっぱり寂しかったわ\n…みんなで雑魚寝？\nそんなの…絶対やるわ", 1, 8430, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 181201, "ネビアってば大泣きで\n私を出迎えてくれたの\n…すごく嬉しかったわ", 1, 8431, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 181201, "この翼、陽に当たると\n本当に綺麗よね…\n自分のだけれど、つい\nずっと見ちゃうもの", 1, 8432, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 181201, "おにーたんっ♪\n…呼びたくなっただけ\n本当にそれだけよっ？", 1, 8433, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 181201, "どうしたの？\n…ふふっ、安心して\nこれからもずっと\n私はここにいるわ", 1, 8434, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 181201, "みんな起きだす時間ね\n街が活気づいてく感じ\n嫌いじゃないわ", 1, 8435, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 181201, "【美食殿】のお昼は\nいつも美味しいから\nまた太っちゃいそうね", 1, 8436, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 181201, "おやすみなさい\n明日も、その先も\n一緒に冒険しましょう", 1, 8437, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 181201, "休憩も大事…よね\nじゃあ遠慮なく…\nふぅ～…♪", 1, 8439, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 181201, "はぁ…ふわふわ…♪\nこのかわいさには\nもう抗えないわ…", 1, 8440, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 181201, "落ち着く香り…\nこの匂いを嗅いでると\n帰ってきたなって\n思うわね", 1, 8441, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 181201, "みんなで野宿した時も\n焚火の明かりを見て\nすごく安心したのよね", 1, 8442, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 181201, "ペコリーヌさんには\n負けるけど…\n美味しくって手が\n止まらないわ…！", 1, 8444, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 181201, "プリンセスフォーム…\n一体どういう仕組みで\n発動するのか、文献に\n書いてないかしら…", 1, 8445, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131901, "すごーく暑いけど、\nこの暑さをどうやって\n乗り越えるかって\n考えるのも楽しいかも", 1, 8447, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131901, "氷で彫刻を作るの？\nそれは楽しそうだけど\nすぐに溶けちゃわ\nないかしら？", 1, 8448, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131901, "お日様の下で遊ぶのも\n部屋の中で涼むのも\nどっちも夏ならではの\n楽しみ方なのね", 1, 8449, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131901, "生き物の観察日記、\n私も書いてみたいな\nパックのお昼寝日記\nなんてどうかしら？", 1, 8450, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131901, "さまぁばけぇしょんは\n計画を立てるもの\nなんだって\n私も挑戦してみるわね", 1, 8451, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131901, "この世界のことを\nもっと知って\nアーラム村のみんなに\nお話ししてあげるの", 1, 8452, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131901, "いつルグニカに帰るか\nわからないからこそ\n毎日頑張らなきゃね", 1, 8453, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 131901, "今日はどこに行くの？\n私は、えーと…\nかき氷が食べたいわ！", 1, 8454, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131901, "ここにも精霊が\n住んでたりするの？\n…精霊じゃなくて\nがいど妖精？", 1, 8455, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131901, "パックったら、\n近所の子にいっぱい\nなでなでされてたわ\nすごーく人気者ね！", 1, 8456, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131901, "実は…線香花火の\n特訓をしてたの！\n次は負けないんだから", 1, 8457, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 131901, "昨日、みんなの\n似顔絵も絵日記に\n描いてみたんだけど…\n笑っちゃダメよ？", 1, 8458, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 131901, "何気ない時間だって\n後で思い返せば\nずっと忘れない\n素敵な思い出になるの", 1, 8459, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 131901, "気持ちよく起きて、\nいい一日を過ごしたい\nそんなときには\nラジーオタイソーね！", 1, 8460, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 131901, "お昼ごはんの時間ね\n暑さに負けないように\nしっかり食べましょう", 1, 8461, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 131901, "外は涼しいみたいだし\n夜の散歩に行ってみる\nのはどうかしら？", 1, 8462, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 131901, "遊び疲れたら\nここでひとやすみ…\nふわぁ…\n眠くなっちゃった…", 1, 8464, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 131901, "細かいところまで\nすごーく丁寧に\n作られてる…", 1, 8465, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 131901, "わっ、なんだか\nすーっとする香りね\nちょっぴり涼しく\nなったみたい", 1, 8466, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 131901, "海で遊ぶと\n体が冷えるから\nあったかいのも\nすごーく嬉しい…", 1, 8467, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 131901, "これも美味しい…！\nペコリーヌが\nどんどん食べちゃう\n気持ちが分かるかも", 1, 8469, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 131901, "このお話も面白い…！\nあっという間に時間が\n過ぎちゃったわ…\n…ね、寝てません！", 1, 8470, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 131901, "この子と一緒に海に\nいったら楽しそうね！\nどこに行けば\n会えるのかしら？", 1, 8472, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 131901, "ふかふかね！\nこうしてると波の音が\n聞こえてきそう…", 1, 8473, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 132101, "こちらでは水着姿で\n過ごすのも\n珍しくないようですね", 1, 8474, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 132101, "メイド服ではなく\n姉様と色違いの水着…\nレムは嬉しいです", 1, 8475, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 132101, "少し室温が高いかも\nしれませんね…\nいくつか氷を配置して\nおきましょうか", 1, 8476, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 132101, "清涼感があるとはいえ\n鉄球をビーチバレーの\nボールに代用するのは\n危ないでしょうか…", 1, 8477, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 132101, "ふぅー…ふぅー…\nようやく浮輪が\n膨らみました\n姉様にお渡ししないと", 1, 8478, 1, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 132101, "海のことをもっと\n教えていただけますか\n…スバルくんと海の\n話をしたいんです", 1, 8479, 4, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 132101, "もし海に行く機会が\nあればレムにも…\nお声がけくださいね\nぜひ、お願いします", 1, 8480, 8, 1);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 0, 0, 132101, "何を着ていても\nメイドはメイド\n掃除も料理も\nなんでもござれです", 1, 8481, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 132101, "スバルくんとあなたは\n兄弟のように\n仲がいいですね\n姉様とレムと一緒です", 1, 8482, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 132101, "この姿で子どもたちと\n遊ぶのは…さすがに\nはばかられますね\nやはり着替えてきます", 1, 8483, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 132101, "ナンパ…というものが\n海にはあるそうです\nスバルくんがとても\n警戒していました", 1, 8484, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101, 0, 0, 132101, "魚釣りでしたら\nレムもご一緒します\n氷漬けの魚の備蓄を\n補充したかったので", 1, 8485, 4, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102, 0, 0, 132101, "子どもたちと砂遊びの\n約束をしているんです\nあなたもご一緒に\nいかがですか？", 1, 8486, 8, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 0, 132101, "ラジーオタイソーは\nもうされましたか？\n体がほぐれますよ", 1, 8487, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 2, 0, 132101, "お昼ですね。では\n姉様に焼きそばを…\nもちろん皆様の分も\n用意してありますよ", 1, 8488, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 3, 0, 132101, "熱帯夜であっても\nお休みの際はお腹を\n冷やさないように\n気をつけてくださいね", 1, 8489, 1, 2);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 0, 0, 132101, "とても気持ちよさそう\nです…！\nさっそく姉様に\nお教えしないと", 1, 8491, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 0, 0, 132101, "季節に合わせた\n着せ替えができれば\n子どもたちも\n楽しめそうです", 1, 8492, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (4, 0, 0, 132101, "ふむふむ…この香りは\n疲れを癒やしてくれ\nますね。エミリア様に\nお持ちします", 1, 8493, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (5, 0, 0, 132101, "身も心もほぐれます\n思っていたよりも\n体が冷えていたみたい\nですね…ふう…", 1, 8494, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (7, 0, 0, 132101, "もぐもぐ…んうっ！？\nこのすっぱさは…\n梅干し、ですか…\nくせになりますね…！", 1, 8496, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (8, 0, 0, 132101, "海に関する詩文なども\nあるのでしょうか…\n気になります", 1, 8497, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (10, 0, 0, 132101, "地竜とは違った\n乗り心地です…\n本物にも乗ってみたい\nものですね", 1, 8499, 1, 3);
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (11, 0, 0, 132101, "この大きさで\n本物の貝であれば…\nどんな料理にするか\n想像が膨らみます", 1, 8500, 1, 3);
