INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100101, 0, 1, 1, "ここに困ってる人は\nいないよね…？\nよかった♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100101, 0, 1, 2, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100101, 0, 1, 3, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100101, 0, 1, 4, "ここで特訓していい？\n物は壊さないように\n気をつけるね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100101, 0, 1, 5, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100101, 0, 4, 6, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100101, 0, 8, 7, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100101, 0, 1, 8, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100101, 0, 1, 9, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100101, 0, 1, 10, "えっとね…しっぽは\n触っちゃダメだよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100101, 0, 1, 11, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100101, 0, 4, 12, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100101, 0, 8, 13, "えへへ…\n騎士くんの手だ…\nあったかいな…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100101, 0, 1, 14, "おはよー！\n今日も元気に\nいっちゃうよー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100101, 0, 1, 15, "ユイちゃんに\nおやつ作って\nもらおうかな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100101, 0, 1, 16, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100101, 0, 1, 17, "ちゃんとケアして\n傷が残らないように\nしないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100101, 0, 1, 18, "一回休むと\nこのままずるずる\nいきそう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100101, 0, 1, 19, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100101, 0, 1, 20, "落ち着くなぁ～\n心だけじゃなくて\n体もほぐれそう♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100101, 0, 1, 21, "あったかい所に\nいると体が丸く\nなっちゃう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100101, 0, 1, 22, "いつ何が起きるか\n分からないし、\nもっと力をつけないと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100101, 0, 1, 23, "美味しいけど、\n食べ過ぎないように\nしないと…！！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100101, 0, 1, 24, "文字が多いと\n眠くなっちゃう…\n危ない危ない！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100102, 0, 1, 26, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100102, 0, 1, 27, "のんびりするのって\n退屈かも…\n何かして遊ばない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100102, 0, 1, 28, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100102, 0, 1, 29, "あたしのお友達も\n呼んできていい？\nきっと楽しいよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100102, 0, 1, 30, "誰も困ってないよね？\n…何をすれば\nいいのか分かんないや");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100102, 0, 8, 31, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100102, 0, 8, 32, "…なんであたし\n騎士くんばっかり\n見てるんだろ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100102, 0, 1, 33, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100102, 0, 1, 34, "触るのって楽しい？\n…そっか\n少しだけだよ…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100102, 0, 1, 35, "あたしの元気を\nわけてあげるね！\nフレー！　フレー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100102, 0, 1, 36, "何かムズムズする！？\nって騎士くんかぁー\nどしたの？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100102, 0, 8, 37, "ひゃうっ！　もう…\nしっぽはだめだってば\n次やったらデコピン！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100102, 0, 8, 38, "えへへ…\n騎士くんの手だ…\nあったかいな…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100102, 0, 1, 39, "朝ごはんと運動！\nこれがあたしの\n元気の素だよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100102, 0, 1, 40, "ユイちゃんに\nおやつ作って\nもらおうかな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100102, 0, 1, 41, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100102, 0, 1, 42, "これでよし♪\n騎士くん\n気づくかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100102, 0, 1, 43, "一回休むと\nこのままずるずる\nいきそう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100102, 0, 1, 44, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100102, 0, 1, 45, "こういうの\nなんか気になって\nじーって見ちゃうんだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100102, 0, 1, 46, "あったかい所に\nいると体が丸く\nなっちゃう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100102, 0, 1, 47, "いつ何が起きるか\n分からないし\nもっと力をつけないと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100102, 0, 1, 48, "お腹が減ると\nなんか力が\n出ないんだよね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100102, 0, 1, 49, "色んなことを\n知っておくのは\n大事だよね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100201, 0, 1, 51, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100201, 0, 1, 52, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100201, 0, 1, 53, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100201, 0, 1, 54, "戦いは\n得意じゃないけど\nお料理なら…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100201, 0, 1, 55, "こういう平和な\n時間が、もっと\n続くといいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100201, 0, 4, 56, "わたし…\nもうちょっとだけ勇気\n出してみようかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100201, 0, 8, 57, "みんなのことが大好き\nも、もちろん、\n騎士クンのことも…\nな、仲間として！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100201, 0, 1, 58, "ひゃうっ！\nな、なに？\nびっくりしたぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100201, 0, 1, 59, "男の子って、\nやっぱりおっきいね。\nその…手とか\n他にもいろいろ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100201, 0, 1, 60, "困ったことが\nあったら\nいつでも話してね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100201, 0, 1, 61, "え！　む、虫っ！？\n取って取って！\nはやく～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100201, 0, 4, 62, "あのね…\n一緒におでかけ\nしたいんだけど…\nダメかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100201, 0, 8, 63, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100201, 0, 1, 64, "お、おはよう！\nあの…朝ごはんは\nもう食べちゃった？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100201, 0, 1, 65, "お昼休みだね\nあの…良かったら\nお弁当…食べない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100201, 0, 1, 66, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100201, 0, 1, 67, "よし…\nこれなら\n褒めてもらえるかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100201, 0, 1, 68, "がんばることも\n必要だけど、\nお休みも大切だよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100201, 0, 1, 69, "かわいいなぁ…\nわたしも今度\n作ってみよう。\nえっと、材料は…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100201, 0, 1, 70, "戦う前にもこうやって\n落ち着けたら\nいいのに");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100201, 0, 1, 71, "…はへっ？\nも、もしかして今\n寝ちゃってた…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100201, 0, 1, 72, "騎士クンと一緒に\n冒険するなら\n体力がないと…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100201, 0, 1, 73, "おいしいものって\n食べ過ぎちゃうけど…\nしょうがない…よね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100201, 0, 1, 74, "…そっか\nこうすれば魔法が\nもっと強くなるんだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100202, 0, 1, 76, "洗濯と掃除\nどっちから\nはじめよう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100202, 0, 1, 77, "新しい魔道書を\n買いに行かなくちゃ\nもっとたくさん\n魔法を覚えたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100202, 0, 1, 78, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100202, 0, 1, 79, "今日はどんな料理に\n挑戦しよう…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100202, 0, 1, 80, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100202, 0, 8, 81, "みんなのことが大好き\nも、もちろん\n騎士クンのことも…\nな、仲間として！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100202, 0, 8, 82, "騎士クンのそばに\nいられるだけで\n幸せだよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100202, 0, 1, 83, "えっと、その…\nこんな所で\nこういうことされると\n恥ずかしいよ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100202, 0, 1, 84, "え！　む、虫っ！？\n取って取って！\nはやく～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100202, 0, 1, 85, "あ、あの…実は\n騎士クンのこと…\nううん、やっぱり\nなんでもない");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100202, 0, 1, 86, "男の子って\nやっぱりおっきいね\nその…手とか\n他にもいろいろ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100202, 0, 8, 87, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100202, 0, 8, 88, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100202, 0, 1, 89, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100202, 0, 1, 90, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100202, 0, 1, 91, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100202, 0, 1, 92, "よし…\nこれなら\n褒めてもらえるかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100202, 0, 1, 93, "がんばることも\n必要だけど\nお休みも大切だよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100202, 0, 1, 94, "かわいい～♪\n騎士クンは\nこういうのって好き？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100202, 0, 1, 95, "戦う前にもこうやって\n落ち着けたら\nいいのに");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100202, 0, 1, 96, "あの…！\nよ、良かったら\n一緒に…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100202, 0, 1, 97, "騎士クンと一緒に\n冒険するなら\n体力がないと…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100202, 0, 1, 98, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100202, 0, 1, 99, "…やっぱり\n月刊恋愛指南は\nためになる…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100301, 0, 1, 101, "装備の手入れでも\nしようかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100301, 0, 1, 102, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100301, 0, 1, 103, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100301, 0, 1, 104, "仲間なんていらない\nそう思ってた頃も\nあったな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100301, 0, 1, 105, "考えごとをしていると\nつい部屋中を\n歩いてしまうな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100301, 0, 4, 106, "彼は\nどこまで強くなるのか\n私も負けられないな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100301, 0, 8, 107, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100301, 0, 1, 108, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100301, 0, 1, 109, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100301, 0, 1, 110, "なに？　私に相談？\n戦いについてなら\n喜んで");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100301, 0, 1, 111, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100301, 0, 4, 112, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100301, 0, 8, 113, "キミにはこれからも\n私のそばに…\nいや、なんでもない");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100301, 0, 1, 114, "おはよう\n今日も充実した\n一日にしていこう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100301, 0, 1, 115, "そろそろ\nお茶の時間か\nキミも一緒にどう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100301, 0, 1, 116, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100301, 0, 1, 117, "戦いを経てもなお\n美しくあるように…\nこれでよし♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100301, 0, 1, 118, "戦ってばかりで\n休まないと、また\nユイに怒られるな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100301, 0, 1, 119, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100301, 0, 1, 120, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100301, 0, 1, 121, "時々は落ち着いた\n時間を過ごすのも\n悪くない…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100301, 0, 1, 122, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100301, 0, 1, 123, "食事は大切だけど\n食べ過ぎには\n注意しておこう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100301, 0, 1, 124, "ユイ…！\nこ、こんな本を\n読んでいたのか…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100302, 0, 1, 126, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100302, 0, 1, 127, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100302, 0, 1, 128, "彼は\nどこまで強くなるのか\n私も負けられないな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100302, 0, 1, 129, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100302, 0, 1, 130, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100302, 0, 8, 131, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100302, 0, 8, 132, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100302, 0, 1, 133, "どうしたの？\nなんでもない？\nそれなら良かった");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100302, 0, 1, 134, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100302, 0, 1, 135, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100302, 0, 1, 136, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100302, 0, 8, 137, "ひょっとして\n今のも一本だなんて\n言わないよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100302, 0, 8, 138, "キミにはこれからも\n私のそばに…\nいや、なんでもない");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100302, 0, 1, 139, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100302, 0, 1, 140, "休息も時には\n必要だと、仲間から\n教わったよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100302, 0, 1, 141, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100302, 0, 1, 142, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100302, 0, 1, 143, "休息があってこそ\n万全な状態で\n戦えるんだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100302, 0, 1, 144, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100302, 0, 1, 145, "体と同様に\n心もほぐれるね…\nはぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100302, 0, 1, 146, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100302, 0, 1, 147, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100302, 0, 1, 148, "修行のせいか\n最近食べる量が\n増えてきたような…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100302, 0, 1, 149, "ユイ…！\nこ、こんな本を\n読んでいたのか…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100401, 0, 1, 151, "お宝の予感！\n部屋中を\n探検しちゃうぞ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100401, 0, 1, 152, "落とし穴…どこに\n作ったんだっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100401, 0, 1, 153, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100401, 0, 1, 154, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100401, 0, 1, 155, "かくれんぼする人\nこの指、と～まれ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100401, 0, 4, 156, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100401, 0, 8, 157, "ミソギと遊ぶ？\nミソギに遊ばれる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100401, 0, 1, 158, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100401, 0, 1, 159, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100401, 0, 1, 160, "にいちゃんの\n通りそうなところに\nバナナの皮を…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100401, 0, 1, 161, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100401, 0, 4, 162, "ここ、ミソギ探検隊の\n基地にしようよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100401, 0, 8, 163, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100401, 0, 1, 164, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100401, 0, 1, 165, "にいちゃんが\n昼寝したら、\nイタズラし放題♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100401, 0, 1, 166, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100401, 0, 1, 167, "イタズラには\nお色気も必要…\nうん、完璧！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100401, 0, 1, 168, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100401, 0, 1, 169, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100401, 0, 1, 170, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100401, 0, 1, 171, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100401, 0, 1, 172, "探検家は\n体力勝負だよー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100401, 0, 1, 173, "みんなに分けて\nあげようかな～\n激辛ソースも\n入れちゃうけどね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100401, 0, 1, 174, "こういう所に\n宝の地図が\n隠れてるんだよね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100402, 0, 1, 176, "お宝の予感！\n部屋中を\n探検しちゃうぞ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100402, 0, 1, 177, "うぅ、探検に出たい！\nじっとしてなんか\nいられないよ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100402, 0, 1, 178, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100402, 0, 1, 179, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100402, 0, 1, 180, "かくれんぼする人\nこの指、と～まれ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100402, 0, 8, 181, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100402, 0, 8, 182, "にいちゃんが喜ぶ\nイタズラ、何かない\nかな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100402, 0, 1, 183, "ミソギがしかけた\nイタズラ\n見つけられるかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100402, 0, 1, 184, "探検する？\n冒険する？\nそれとも…\nイタズラされる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100402, 0, 1, 185, "将来の夢は探検家！\n探したい物はないけど");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100402, 0, 1, 186, "にいちゃんに宿題！\n落とし穴を10個\n掘ること！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100402, 0, 8, 187, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100402, 0, 8, 188, "ここ、ミソギ探検隊の\n基地にしようよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100402, 0, 1, 189, "にいちゃん\n探検に行こうよ！\n今からなら遭難しても\n夜には帰れるよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100402, 0, 1, 190, "にいちゃんが昼寝したら\nイタズラし放題♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100402, 0, 1, 191, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100402, 0, 1, 192, "イタズラには\nお色気も必要…\nうん、完璧！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100402, 0, 1, 193, "ゆっくりまったりして\n次のイタズラを\n考えよ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100402, 0, 1, 194, "かわいい！\nミソギ探検隊の\nマスコットにしよう♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100402, 0, 1, 195, "吸うとくしゃみが\n止まらなくなるお香を\nここに…くしゅっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100402, 0, 1, 196, "にひひ…イタズラの\n証拠、ここで\n燃やしちゃおう～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100402, 0, 1, 197, "探検家は\n体力勝負だよー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100402, 0, 1, 198, "探検のおやつに\nぴったりかも～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100402, 0, 1, 199, "こういう所に\n宝の地図が\n隠れてるんだよね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100601, 0, 1, 201, "音楽は全部好きだけど\n一番好きなのは\nやっぱりジャズかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100601, 0, 1, 202, "はぁ、はぁ…\n一人でもいっぱい\nしないと…\n特訓…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100601, 0, 1, 203, "お姉ちゃんには\nもっと自信を\n持ってほしいなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100601, 0, 1, 204, "お姉ちゃんのマネ\nしちゃおうかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100601, 0, 1, 205, "双子って、\n好みのタイプも\n似ちゃうのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100601, 0, 4, 206, "お兄ちゃんと私は\n相性抜群です♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100601, 0, 8, 207, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100601, 0, 1, 208, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100601, 0, 1, 209, "うふふ、お兄ちゃん！\n今日は何を\nするんですか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100601, 0, 1, 210, "そんなところ\n触っちゃダメ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100601, 0, 1, 211, "お兄ちゃん、イジワル\nしないでください…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100601, 0, 4, 212, "お兄ちゃん、もっと\n上級編のテクニック、\n教えてくださいね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100601, 0, 8, 213, "コーチ、\n今日も特訓\nお願いします！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100601, 0, 1, 214, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100601, 0, 1, 215, "こんな明るい時間から\nす、するんですか…\n二人っきりの特訓…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100601, 0, 1, 216, "夜はこれからですよ\nお兄ちゃん♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100601, 0, 1, 217, "世界一、かわいく\nなるんだから♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100601, 0, 1, 218, "気持ちいい♪\nもう立てないよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100601, 0, 1, 219, "かわいい♪\nアカリのライバル\nだね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100601, 0, 1, 220, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100601, 0, 1, 221, "はぁ…アカリ、\nとろけちゃう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100601, 0, 1, 222, "はぁ…はぁはぁ、\n私…もう…ダメ\n限界です…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100601, 0, 1, 223, "わぁ、おいしい～\nけど食べ過ぎは美容に\nよくないですよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100601, 0, 1, 224, "この楽譜、\nサックスで吹いて\nみたいなぁ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100602, 0, 1, 226, "このごろ一人でいると\nお兄ちゃんのこと\nばかり考えちゃう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100602, 0, 1, 227, "はぁ、はぁ…もっと\nいっぱいしなくちゃ\n…練習");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100602, 0, 1, 228, "このお部屋、なんだか\n落ち着きます…\nお兄ちゃんが\nいるから？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100602, 0, 1, 229, "みんなかわいいな～\n世界一かわいくなる\nのは、難しいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100602, 0, 1, 230, "双子って、\n好みのタイプも\n似ちゃうのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100602, 0, 8, 231, "いつか世界でいちばん\nかわいくなって\nお兄ちゃんと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100602, 0, 8, 232, "もう待ちきれない…\nお兄ちゃん、\n早く帰って来て！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100602, 0, 1, 233, "あんっ…着替えよう\nと思ったのに\nお兄ちゃんのエッチ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100602, 0, 1, 234, "二人きりの秘密の特訓\n今度はいつですか？\n待ち遠しいです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100602, 0, 1, 235, "え？　ここでするの？\nみんなの前で恥ずか\nしいな…筋トレ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100602, 0, 1, 236, "お兄ちゃん、イジワル\nしないでください…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100602, 0, 8, 237, "お兄ちゃんといると\nわ、私…ドキドキ\nしてきちゃう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100602, 0, 8, 238, "コーチ、\n今日も特訓\nお願いします！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100602, 0, 1, 239, "おはようございます\n昨夜は楽しかった\nですね、うふふ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100602, 0, 1, 240, "お兄ちゃんに\n食べてもらいたいなぁ\n手作りのお昼ごはん");
