INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100602, 0, 1, 243, "気持ちいい♪\nもう立てないよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100602, 0, 1, 244, "かわいい♪\nアカリのライバル\nだね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100602, 0, 1, 245, "はぁ…気持ちいい、\n私、このままダメに\nなっちゃいそう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100602, 0, 1, 246, "あったか～い…\nまるでお兄ちゃんが\n隣にいる時みたい");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100602, 0, 1, 247, "汗をかくと\n気持ちいいですよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100602, 0, 1, 248, "お兄ちゃんの特訓に\n付き合うために\nたっぷり\n食べておかないと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100602, 0, 1, 249, "この楽譜\nサックスで吹いて\nみたいなぁ♪");
