INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100701, 0, 1, 252, "う～ら～め～し～や～\nなの～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100701, 0, 1, 253, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100701, 0, 1, 254, "幽霊の正体見たり、\nミヤコなの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100701, 0, 1, 255, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100701, 0, 4, 256, "アイツがいて、\nプリンもある。\nきっとここが天国なの");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100701, 0, 8, 257, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100701, 0, 1, 258, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100701, 0, 1, 259, "もし成仏した先に\nプリンがなかったら\n地獄なの～…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100701, 0, 1, 260, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100701, 0, 1, 261, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100701, 0, 4, 262, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100701, 0, 8, 263, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100701, 0, 1, 264, "う～　眠いの…\n二度目の二度寝を\nするの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100701, 0, 1, 265, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100701, 0, 1, 266, "ディナーは豪華に、\nプリンのフルコース\nなの～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100701, 0, 1, 267, "体が透けちゃうから\nうまく使えないの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100701, 0, 1, 268, "まったりなの～\nあとはプリンがあれば\n完璧なの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100701, 0, 1, 269, "む～…かわいいけど\nプリンは分けて\nあげないの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100701, 0, 1, 270, "は～　いい気分なの…\n成仏しそうなの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100701, 0, 1, 271, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100701, 0, 1, 272, "幽霊には、体力も\n筋力も関係ないの～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100701, 0, 1, 273, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100701, 0, 1, 274, "プリンの写真集が\nあればいいのに\nなの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100702, 0, 1, 276, "うらめしや～…\n裏の飯より\nプリンがいいの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100702, 0, 1, 277, "プリン♪　プリン♪\nプリン♪　なの～♪　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100702, 0, 1, 278, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100702, 0, 1, 279, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100702, 0, 1, 280, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100702, 0, 8, 281, "アイツがいて\nプリンもある\nきっとここが天国なの");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100702, 0, 8, 282, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100702, 0, 1, 283, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100702, 0, 1, 284, "もし成仏した先に\nプリンがなかったら\n地獄なの～…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100702, 0, 1, 285, "今日はオマエと\nプリンを買いに行くの\n強制なの～　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100702, 0, 1, 286, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100702, 0, 8, 287, "たくさんオマエと\n遊べて\n成仏しそうなくらい\nうれしいの～！ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100702, 0, 8, 288, "ミヤコのプリン\nオマエにだったら\n分けてあげるの～ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100702, 0, 1, 289, "う～　眠いの…\n二度目の二度寝を\nするの… ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100702, 0, 1, 290, "う～ら～め～し～や～\n…明るい時間だと\n驚かせないの～ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100702, 0, 1, 291, "ひゅ～どろどろ～ん☆\n真っ暗なうちに\nいたずらしまくるの～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100702, 0, 1, 292, "体が透けちゃうから\nうまく使えないの～ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100702, 0, 1, 293, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100702, 0, 1, 294, "む～…かわいいけど\nプリンは分けて\nあげないの～　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100702, 0, 1, 295, "は～　いい気分なの…\n成仏しそうなの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100702, 0, 1, 296, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100702, 0, 1, 297, "幽霊には、体力も\n筋力も関係ないの～！　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100702, 0, 1, 298, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100702, 0, 1, 299, "プリンの写真集が\nあればいいのに\nなの～ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100801, 0, 1, 301, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100801, 0, 1, 302, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100801, 0, 1, 303, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100801, 0, 1, 304, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100801, 0, 1, 305, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100801, 0, 4, 306, "このボクがいるだけで\nあの人への\nおもてなしだよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100801, 0, 8, 307, "ボクの可愛さは、\nふふ…あの人の\nためのものさ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100801, 0, 1, 308, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100801, 0, 1, 309, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100801, 0, 1, 310, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100801, 0, 1, 311, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100801, 0, 4, 312, "あの人も今、\n可愛いボクのこと\n考えてるかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100801, 0, 8, 313, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100801, 0, 1, 314, "美しいボクの\n美しい一日が\n始まるよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100801, 0, 1, 315, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100801, 0, 1, 316, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100801, 0, 1, 317, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100801, 0, 1, 318, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100801, 0, 1, 319, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100801, 0, 1, 320, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100801, 0, 1, 321, "気安く触ったら\n火傷する…\n炎はボクに似ているね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100801, 0, 1, 322, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100801, 0, 1, 323, "ボクが食べると、\n食べ物まで\n可愛いなあ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100801, 0, 1, 324, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100802, 0, 1, 326, "ボクの美しさは\nみんなのために\nあるんだよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100802, 0, 1, 327, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100802, 0, 1, 328, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100802, 0, 1, 329, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100802, 0, 1, 330, "ボクの可愛さについて\nみんな話し合わない\nのかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100802, 0, 8, 331, "このボクがいるだけで\nあの人への\nおもてなしだよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100802, 0, 8, 332, "あの人も今\n可愛いボクのこと\n考えてるかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100802, 0, 1, 333, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100802, 0, 1, 334, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100802, 0, 1, 335, "ボクの化粧品使う？\n一緒に可愛くなろうよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100802, 0, 1, 336, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100802, 0, 8, 337, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100802, 0, 8, 338, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100802, 0, 1, 339, "朝日と共に\nボクの美しさも\n周りを照らすよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100802, 0, 1, 340, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100802, 0, 1, 341, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100802, 0, 1, 342, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100802, 0, 1, 343, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100802, 0, 1, 344, "可愛いものを\n可愛いって言える\nボクって可愛いよね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100802, 0, 1, 345, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100802, 0, 1, 346, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100802, 0, 1, 347, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100802, 0, 1, 348, "ボクが食べると\n食べ物まで\n可愛いなあ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100802, 0, 1, 349, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100901, 0, 1, 351, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100901, 0, 1, 352, "今度シグルドに\n私の魔法を一つ\n伝授してやるか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100901, 0, 1, 353, "ここでは風が\n吹いてこないな…\n換気したい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100901, 0, 1, 354, "そうだ、ナナカに\nこの間借りた本を\n返さないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100901, 0, 1, 355, "まずは包帯をとって\n手から龍の形の\n炎を出して…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100901, 0, 4, 356, "シグルド…\n今頃何処で何を\nしているのだろう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100901, 0, 8, 357, "最近、彼の姿が\nまぶしく見える…\nどうして…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100901, 0, 1, 358, "辞書を知らないか？\n…探している\n理由は聞くな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100901, 0, 1, 359, "そうだ！\n貴様の技にも\n名前をつけてやろう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100901, 0, 1, 360, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100901, 0, 1, 361, "何事だ？\nまさか我が瞳に\n魅入られたのか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100901, 0, 4, 362, "貴様がそばにいると\nとても安心できる…\n…不思議だ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100901, 0, 8, 363, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100901, 0, 1, 364, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100901, 0, 1, 365, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100901, 0, 1, 366, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100901, 0, 1, 367, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100901, 0, 1, 368, "くっ…！\n私を眠りの国へ\n誘うつもりか！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100901, 0, 1, 369, "このようなものに\n懐柔されるものか！\n…されるものか！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100901, 0, 1, 370, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100901, 0, 1, 371, "これが我らの求めた\n安寧か…\n悪くない…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100901, 0, 1, 372, "この程度\n前世での激闘に\n比べれば…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100901, 0, 1, 373, "最近金欠でな…\nいやぁありがたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100901, 0, 1, 374, "ここにも\n前世の記憶が\n眠っていたとは…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 100902, 0, 1, 376, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 100902, 0, 1, 377, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 100902, 0, 1, 378, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 100902, 0, 1, 379, "そうだ、ナナカに\nこの間借りた本を\n返さないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 100902, 0, 1, 380, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 100902, 0, 1, 381, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 100902, 0, 8, 382, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 100902, 0, 8, 383, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 100902, 0, 1, 384, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 100902, 0, 1, 385, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 100902, 0, 1, 386, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 100902, 0, 1, 387, "貴様がそばにいると\nとても安心できる…\n…不思議だ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 100902, 0, 8, 388, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 100902, 0, 8, 389, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 100902, 0, 1, 390, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 100902, 0, 1, 391, "夜になると、力が\n増した気分になるな\n…なりませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 100902, 0, 1, 392, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 100902, 0, 1, 393, "お、おのれ\nヒュプノス…\n私は負けないぞ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 100902, 0, 1, 394, "くっくっくっ…\n貴様は特別団員に\nしてやろう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 100902, 0, 1, 395, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 100902, 0, 1, 396, "これが我らの求めた\n安寧か…\n悪くない…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 100902, 0, 1, 397, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 100902, 0, 1, 398, "食事は魔力に\n変換する体力の\n元になるのだ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 100902, 0, 1, 399, "フフフ…\nこれを覚えれば\nもっと強く…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 101001, 0, 1, 401, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 101001, 0, 1, 402, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 101001, 0, 1, 403, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 101001, 0, 1, 404, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 101001, 0, 1, 405, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 101001, 0, 4, 406, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 101001, 0, 8, 407, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 101001, 0, 1, 408, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 101001, 0, 1, 409, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 101001, 0, 1, 410, "王子はんは、\n絵本から出てきた人\nみたいやね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 101001, 0, 1, 411, "うちの話し方\nのんびりしてます\nやろか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 101001, 0, 4, 412, "マホ姫の手は\nつべたいから、\n王子はんが温めて？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 101001, 0, 8, 413, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 101001, 0, 1, 414, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 101001, 0, 1, 415, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 101001, 0, 1, 416, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 101001, 0, 1, 417, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 101001, 0, 1, 418, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 101001, 0, 1, 419, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 101001, 0, 1, 420, "心地ええどす。まるで\nお花畑にいるようやね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 101001, 0, 1, 421, "ぽかぽかどす。\n心まであったかく\nなりますわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 101001, 0, 1, 422, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 101001, 0, 1, 423, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 101001, 0, 1, 424, "絵本、絵本～\n幸せやわぁ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 101002, 0, 1, 426, "王子はんにとって\n自慢の姫になるえ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 101002, 0, 1, 427, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 101002, 0, 1, 428, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 101002, 0, 1, 429, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 101002, 0, 1, 430, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 101002, 0, 8, 431, "マホ姫と王子はん\n絵本の世界より\nベストカップルや");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 101002, 0, 8, 432, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 101002, 0, 1, 433, "たまには、王子はんの\n好きな絵本も\n知りたいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 101002, 0, 1, 434, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 101002, 0, 1, 435, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 101002, 0, 1, 436, "絵本読むんも\nええなぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 101002, 0, 8, 437, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 101002, 0, 8, 438, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 101002, 0, 1, 439, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 101002, 0, 1, 440, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 101002, 0, 1, 441, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 101002, 0, 1, 442, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 101002, 0, 1, 443, "王子はんも一緒に\nゆっくりしていっても\nええんよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 101002, 0, 1, 444, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 101002, 0, 1, 445, "癒しアイテムって\nほんま、好っきゃわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 101002, 0, 1, 446, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 101002, 0, 1, 447, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 101002, 0, 1, 448, "おいひいわ～\nマホマホ王国には\nない味やわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 101002, 0, 1, 449, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 101101, 0, 1, 451, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 101101, 0, 1, 452, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 101101, 0, 1, 453, "い、いけない！\n縫い針落としちゃった");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 101101, 0, 1, 454, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 101101, 0, 1, 455, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 101101, 0, 4, 456, "お兄ちゃんに、\nお洋服でも\n作ろうかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 101101, 0, 8, 457, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 101101, 0, 1, 458, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 101101, 0, 1, 459, "クリームついてた！？\nさっき食べた、\nケーキかも…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 101101, 0, 1, 460, "勉強、見てくれるん\nですか？　やった～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 101101, 0, 1, 461, "服がほつれてますよ？\n直してあげます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 101101, 0, 4, 462, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 101101, 0, 8, 463, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 101101, 0, 1, 464, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 101101, 0, 1, 465, "お昼休憩に\n甘いものが\n食べたいです～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 101101, 0, 1, 466, "ねむいです～\nでももうちょっと\n勉強しないと～…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 101101, 0, 1, 467, "この服、かわいい♪\n今度作って\nみよっと～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 101101, 0, 1, 468, "勉強で疲れたから\n休憩しよ～っと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 101101, 0, 1, 469, "かわいい♪\n私が服を作って\nあげよっか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 101101, 0, 1, 470, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 101101, 0, 1, 471, "のんびりしたら、\nまた勉強しよっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 101101, 0, 1, 472, "運動すると勉強も\nはかどるよね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 101101, 0, 1, 473, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 101101, 0, 1, 474, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 101102, 0, 1, 476, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 101102, 0, 1, 477, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 101102, 0, 1, 478, "い、いけない！\n縫い針落としちゃった");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 101102, 0, 1, 479, "笑う時には服着ます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 101102, 0, 1, 480, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 101102, 0, 8, 481, "お兄ちゃんに、\nお洋服でも\n作ろうかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 101102, 0, 8, 482, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 101102, 0, 1, 483, "勉強、見てくれるん\nですか？　やった～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 101102, 0, 1, 484, "クリームついてた！？\nさっき食べた、\nケーキかも…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 101102, 0, 1, 485, "おにいちゃん！\nそろそろ、おやつに\nしませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 101102, 0, 1, 486, "勉強は…\n明日にしようっと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 101102, 0, 8, 487, "妹だけど、大人な\nレディですからね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 101102, 0, 8, 488, "な、ナデナデとか\n恥ずかしいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 101102, 0, 1, 489, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 101102, 0, 1, 490, "お昼休憩に\n甘いものが\n食べたいです～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 101102, 0, 1, 491, "お兄ちゃんは\n夜強いんですね\n私は眠くて…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 101102, 0, 1, 492, "服装や髪形が変わると\n気分も変わるよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 101102, 0, 1, 493, "ここで寝ちゃったら\n受験勉強が～…\nぐーー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 101102, 0, 1, 494, "かわいい♪\n私が服を作って\nあげよっか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 101102, 0, 1, 495, "いまならどんな試験も\n合格できそうです！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 101102, 0, 1, 496, "のんびりしたら\nまた勉強しよっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 101102, 0, 1, 497, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 101102, 0, 1, 498, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 101102, 0, 1, 499, "この本の服\nかわいいなぁ♪\n私に作れるかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 101201, 0, 1, 501, "う～ん、今なら\nどこでだって\n眠れちゃいそう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 101201, 0, 1, 502, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 101201, 0, 1, 503, "…はっ！？\nね、寝てないよ？\nホントだよっ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 101201, 0, 1, 504, "ぐっすり眠って\nパワーを\nフルチャージっ☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 101201, 0, 1, 505, "もし、超能力のことが\nバレたら…ブルブル");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 101201, 0, 4, 506, "テレパシーで\nキミの心を覗けたら\n…なんてね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 101201, 0, 8, 507, "キミを抱き枕に…\nじょ、冗談冗談！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 101201, 0, 1, 508, "ん～、なになに？\n一緒にお昼寝する？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 101201, 0, 1, 509, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 101201, 0, 1, 510, "おおっ、相談ごと？\nなにかな\nなにかな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 101201, 0, 1, 511, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 101201, 0, 4, 512, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 101201, 0, 8, 513, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 101201, 0, 1, 514, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 101201, 0, 1, 515, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 101201, 0, 1, 516, "どんなにお昼寝しても\n夜は眠いんだ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 101201, 0, 1, 517, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 101201, 0, 1, 518, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 101201, 0, 1, 519, "これ…可愛い！\nシオリンに\n届けてあげたい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 101201, 0, 1, 520, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 101201, 0, 1, 521, "気持ちいい…\nはっ！　ダメダメ！\nまた寝るとこだった！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 101201, 0, 1, 522, "眠くならないように、\n運動しよっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 101201, 0, 1, 523, "お腹いっぱいになると\n眠くなるよね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 101201, 0, 1, 524, "睡眠学習って\n本を読みながら\n寝るんだっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 101202, 0, 1, 526, "う～ん、今なら\nどこでだって\n眠れちゃいそう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 101202, 0, 1, 527, "もっといっぱい\n超能力使えたら\nいいのにな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 101202, 0, 1, 528, "ねえねえ、\n今から人助け、\n付き合ってほしいなー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 101202, 0, 1, 529, "ぐっすり眠って\nパワーを\nフルチャージっ☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 101202, 0, 1, 530, "人助け、頑張ったら\nなんだか、眠く…\n…はっ、ダメダメ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 101202, 0, 8, 531, "キミと一緒なら、\nなんだって出来る！\n間違いない！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 101202, 0, 8, 532, "キミを抱き枕に…\nじょ、冗談冗談！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 101202, 0, 1, 533, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 101202, 0, 1, 534, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 101202, 0, 1, 535, "一緒に\nお出かけしたいなー\nデートしよっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 101202, 0, 1, 536, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 101202, 0, 8, 537, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 101202, 0, 8, 538, "う～だんだん眠く…\nねぇ一緒に寝よ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 101202, 0, 1, 539, "キミって早起きだね！\n私は寝ようかな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 101202, 0, 1, 540, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 101202, 0, 1, 541, "どんなにお昼寝しても\n夜は眠いんだ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 101202, 0, 1, 542, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 101202, 0, 1, 543, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 101202, 0, 1, 544, "これ…可愛い！\nシオリンに\n届けてあげたい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 101202, 0, 1, 545, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 101202, 0, 1, 546, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 101202, 0, 1, 547, "よーし\n困ってる人のために\nパワーアップ☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 101202, 0, 1, 548, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 101202, 0, 1, 549, "睡眠学習って\n本を読みながら\n寝るんだっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 101601, 0, 1, 551, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 101601, 0, 1, 552, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 101601, 0, 1, 553, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 101601, 0, 1, 554, "ここで撮影会、\nしようかな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 101601, 0, 1, 555, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 101601, 0, 4, 556, "やば…\nこのままじゃ、うち\nキュン死にかも…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 101601, 0, 8, 557, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 101601, 0, 1, 558, "うちの写真撮るの？\nどんなポーズ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 101601, 0, 1, 559, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイにしてあげる");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 101601, 0, 1, 560, "宿題見てくんない？\nひとりじゃムリ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 101601, 0, 1, 561, "みてこのネイル♪\n超エモくな～い？\nヒデサイもする～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 101601, 0, 4, 562, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 101601, 0, 8, 563, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 101601, 0, 1, 564, "早起きとか\nつらたんだよねー\n昼すぎまで寝てたい");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 101601, 0, 1, 565, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 101601, 0, 1, 566, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 101601, 0, 1, 567, "どんなシーンにも合う\nきれいめわがまま\nちゃんとコーデだよ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 101601, 0, 1, 568, "ちょっち疲れたかも？\nガン寝しちゃおっかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 101601, 0, 1, 569, "きゃわたーん☆\n君もおしゃれしよっか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 101601, 0, 1, 570, "いいにおいじゃん！\nこれと同じにおいの\n香水ないかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 101601, 0, 1, 571, "あったまるー\nこれ、サイツヨに\n癒しじゃない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 101601, 0, 1, 572, "あはは、なんか\nウォーキングの練習\nしてるみたーい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 101601, 0, 1, 573, "体形維持しないと\nいけないけど\nついつい食べちゃう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 101601, 0, 1, 574, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 101602, 0, 1, 576, "そろそろ\n仕事の時間だ～\n出かける準備\nしよっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 101602, 0, 1, 577, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 101602, 0, 1, 578, "ヒデサイを見習って\nうちも少し勉強して\nみよっかな…　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 101602, 0, 1, 579, "体形維持とか\n気にしてるんだよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 101602, 0, 1, 580, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 101602, 0, 8, 581, "やば…\nこのままじゃ、うち\nキュン死にかも…　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 101602, 0, 8, 582, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 101602, 0, 1, 583, "うちの写真撮るの？\nどんなポーズ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 101602, 0, 1, 584, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイに\nしてあげる♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 101602, 0, 1, 585, "この雑誌の服\nガチめにほしぃ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 101602, 0, 1, 586, "うちのコーデで\nヒデサイをオシャンに\nしてあげるね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 101602, 0, 8, 587, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 101602, 0, 8, 588, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 101602, 0, 1, 589, "今日は早起き\nできたんだー\nメイクもばっち\nきまってるっしょ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 101602, 0, 1, 590, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 101602, 0, 1, 591, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 101602, 0, 1, 592, "この服ね～\n今年のトレンドなんだ♪\nかわたんでしょ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 101602, 0, 1, 593, "ちょっち疲れたかも？\nガン寝しちゃおっかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 101602, 0, 1, 594, "うわー\nこのぬいぐるみ\nもきゅすぎでしょ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 101602, 0, 1, 595, "いいにおーい♪\nファンのみんなにも\n教えてあげよー♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 101602, 0, 1, 596, "あったまるー\nこれ、サイツヨに\n癒しじゃない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 101602, 0, 1, 597, "食べた分は\n消費しないとね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 101602, 0, 1, 598, "体形維持しないと\nいけないけど\nついつい食べちゃう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 101602, 0, 1, 599, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 101701, 0, 1, 601, "やっさー\n踊ってると、どんどん\n楽しくなるさー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 101701, 0, 1, 602, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 101701, 0, 1, 603, "ハイターイ！　元気な\nあいさつが一番さ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 101701, 0, 1, 604, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 101701, 0, 1, 605, "さぁ、みんなで歌って\n踊るさ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 101701, 0, 4, 606, "毎日、最高に幸せさ～\nキミのおかげだねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 101701, 0, 8, 607, "キミへの気持ちを\n踊りで表現\nしてみるさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 101701, 0, 1, 608, "海でひと泳ぎ\nしたいさ～\nキミもどう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 101701, 0, 1, 609, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 101701, 0, 1, 610, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 101701, 0, 1, 611, "空手の型の\n練習するさー\nキミもどう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 101701, 0, 4, 612, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 101701, 0, 8, 613, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 101701, 0, 1, 614, "ハイターイ！\n今日も一日、\nがんばるさー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 101701, 0, 1, 615, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 101701, 0, 1, 616, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 101701, 0, 1, 617, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 101701, 0, 1, 618, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 101701, 0, 1, 619, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 101701, 0, 1, 620, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 101701, 0, 1, 621, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 101701, 0, 1, 622, "汗をかくのは\n気持ちいいさー！\nどんどん行くよー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 101701, 0, 1, 623, "うーん、おいしいさ～\nゴーヤっぽいものを\n混ぜたいさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 101701, 0, 1, 624, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 101702, 0, 1, 626, "焦らなくても\nなんくるないさ～\nなるようになるさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 101702, 0, 1, 627, "さぁ、みんなで歌って\n踊るさ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 101702, 0, 1, 628, "元気がないときは\n私のところに来るさー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 101702, 0, 1, 629, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 101702, 0, 1, 630, "ノリノリな音楽と\n踊りで、疲れを\n吹き飛ばすさ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 101702, 0, 8, 631, "毎日、最高に幸せさ～\nキミのおかげだねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 101702, 0, 8, 632, "幸せすぎて踊らずには\nいられないさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 101702, 0, 1, 633, "海でひと泳ぎ\nしたいさ～\nキミもどう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 101702, 0, 1, 634, "ん？　どうしたの～？\nヒマなら一緒に\n踊ろうさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 101702, 0, 1, 635, "キミと一緒だから\n毎日楽しいこと\nばっかりさー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 101702, 0, 1, 636, "ハイターイ！　元気な\nあいさつが一番さ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 101702, 0, 8, 637, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 101702, 0, 8, 638, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 101702, 0, 1, 639, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 101702, 0, 1, 640, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 101702, 0, 1, 641, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 101702, 0, 1, 642, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 101702, 0, 1, 643, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 101702, 0, 1, 644, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 101702, 0, 1, 645, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 101702, 0, 1, 646, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 101702, 0, 1, 647, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 101702, 0, 1, 648, "もぐもぐ…もっと\nおかわりがほしいさー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 101702, 0, 1, 649, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 101801, 0, 1, 651, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 101801, 0, 1, 652, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 101801, 0, 1, 653, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 101801, 0, 1, 654, "キンコンカンコ～ン♪\n授業をはじめま～す");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 101801, 0, 1, 655, "はー…\nちょっと肩が\nこっちゃった…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 101801, 0, 4, 656, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 101801, 0, 8, 657, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 101801, 0, 1, 658, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 101801, 0, 1, 659, "勉強勉強で\n疲れたよね？\n膝枕してあげる！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 101801, 0, 1, 660, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 101801, 0, 1, 661, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 101801, 0, 4, 662, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 101801, 0, 8, 663, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 101801, 0, 1, 664, "学校に\n遅刻しないように\n早く準備しないとね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 101801, 0, 1, 665, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 101801, 0, 1, 666, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 101801, 0, 1, 667, "もしかして…\n少し露出多いかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 101801, 0, 1, 668, "リラックスすると\nα波が出て、勉強が\nはかどるんですって");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 101801, 0, 1, 669, "かわいい♪　あなたも\n私の生徒になる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 101801, 0, 1, 670, "学院にもこういう\n安らぐ設備が\n必要かしらね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 101801, 0, 1, 671, "まだ寒いわぁ\n誰か隣に\n来てくれたら…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 101801, 0, 1, 672, "運動部の顧問も\nできるかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 101801, 0, 1, 673, "学校の給食でも\n食べたいわぁ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 101801, 0, 1, 674, "いい参考書を\n見つけちゃった♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 101802, 0, 1, 676, "私の生徒に\n不良なんていないわ\nみーんないい子♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 101802, 0, 1, 677, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 101802, 0, 1, 678, "あの子、まだかな…\n宿題も見てあげたいし\n早く来ないかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 101802, 0, 1, 679, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 101802, 0, 1, 680, "学校の図書室に\n恋愛小説を\n置いて欲しいなあ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 101802, 0, 8, 681, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 101802, 0, 8, 682, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 101802, 0, 1, 683, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 101802, 0, 1, 684, "勉強勉強で\n疲れたよね？\n膝枕してあげる！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 101802, 0, 1, 685, "宿題はもうやった？\n苦手なところは\n先生が見てあげる♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 101802, 0, 1, 686, "んん？　なんだか\n元気がないみたい\n先生がぎゅぅ～って\nしてあげる…　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 101802, 0, 8, 687, "君のお勉強も\nだいぶ進んできたね～\n補習卒業も近いかな？\n少し寂しいけど…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 101802, 0, 8, 688, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 101802, 0, 1, 689, "朝の特別授業\nしてあげましょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 101802, 0, 1, 690, "午後の授業って\n眠いでしょう？\n気持ちは分かるけど\nがんばってね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 101802, 0, 1, 691, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 101802, 0, 1, 692, "もしかして…\n少し露出多いかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 101802, 0, 1, 693, "私の膝枕と、どっちが\nフカフカかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 101802, 0, 1, 694, "可愛いわね～\n教室に置いたら\n生徒が喜ぶかしら？　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 101802, 0, 1, 695, "は～落ち着くなぁ～\n教師には\nこういうゆとりも\n必要よね♪　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 101802, 0, 1, 696, "まだ寒いわぁ\n誰か隣に\n来てくれたら…　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 101802, 0, 1, 697, "運動部の顧問も\nできるかしら？　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 101802, 0, 1, 698, "あん！\nお胸にこぼしちゃった");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 101802, 0, 1, 699, "あぁーん♪\nこういう物語みたいな\n恋愛あこがれる～　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102001, 0, 1, 701, "おにいちゃんは、\nにんじん食べられる？");
