INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102001, 0, 1, 703, "あのね、ミミと\nあそんでほしいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102001, 0, 1, 704, "さっきね、きれいな\nお花見つけたよ。\nおにいちゃんにも\n見せてあげたいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102001, 0, 1, 705, "うさぎさん、\nぴょんぴょん\nぴょーん♪");
