INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102002, 0, 1, 746, "あったか～♪\nぬくぬく～♪\nポカポカ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102002, 0, 1, 747, "はぁはぁ…\nふえ～ミミ\n疲れちゃうよ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102002, 0, 1, 748, "はむ♪　いっぱい\n食べて、はやく\nおっきくなりたいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102002, 0, 1, 749, "ミミね！\nこのお話だーい好き！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102101, 0, 1, 751, "はぅぅ…\n部屋のどこにいたら\nいいんだろう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102101, 0, 1, 752, "お外より\nお部屋の中の方が\n落ち着くな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102101, 0, 1, 753, "クッキー焼いたら\nみんな食べてくれる\nかなぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102101, 0, 1, 754, "あ、あめんぼあかいな\nあいうえお！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102101, 0, 1, 755, "お芝居には\n人間観察が大事…\nじーーーーーー…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102101, 0, 4, 756, "お兄ちゃんを見ると、\n顔が赤くなっちゃうの\n…な、なんでかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102101, 0, 8, 757, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102101, 0, 1, 758, "こ、この本、とっても\nおもしろいんですよ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102101, 0, 1, 759, "い、一緒に遊んで\nくれるんですか？\nや、やったぁ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102101, 0, 1, 760, "ランドソルには\n慣れましたか？\nわ、私はまだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102101, 0, 1, 761, "お、お兄ちゃん、\nお、お話の練習に\n付き合って欲しいです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102101, 0, 4, 762, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102101, 0, 8, 763, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102101, 0, 1, 764, "今日は一日、\n恥ずかしがらずに\n過ごせるかなあ…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102101, 0, 1, 765, "お昼ご飯、どうぞ♪\nわ、私、お料理、\nうまくなったかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102101, 0, 1, 766, "おにいちゃんが\n帰ってくるまで、\nがんばって\n起きてました");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 102101, 0, 1, 767, "可愛い女の子に変身！\n出来ますように…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102101, 0, 1, 768, "ふかふかで\nあったかくて…\nお兄ちゃんみたい♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102101, 0, 1, 769, "この子を相手に、\n演技の練習～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102101, 0, 1, 770, "リボンや服に\n染み込ませておこう。\nいつでもいい匂い♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102101, 0, 1, 771, "えへへ。\nぽっかぽかです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102101, 0, 1, 772, "はぁはぁ…\nふひぃ…\nはうぅぅぅ～～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102101, 0, 1, 773, "もぐもぐ…えへへ、\n美味しいものを\n食べてると幸せです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102101, 0, 1, 774, "戯曲を読んで\n自分が演じるところを\n想像するの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102102, 0, 1, 776, "かわいいものを\nこうして並べて…\nはぁ…落ちつくぅ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102102, 0, 1, 777, "ふぁ～、この部屋は\n時間がゆったりと\n流れてるみたい");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102102, 0, 1, 778, "この台本、感動…\n涙が止まらないよぉ\nいつか演じてみたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102102, 0, 1, 779, "お芝居には\n人間観察が大事…\nじーーーーーー…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102102, 0, 1, 780, "あえいうえおあお\nかけきくけ、かっ…\nむ、難しいですぅぅ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102102, 0, 8, 781, "お、お腹が空いた時は\n言ってくださいね\nが、頑張って\n作ります！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102102, 0, 8, 782, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102102, 0, 1, 783, "こ、この本、とっても\nおもしろいんですよ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102102, 0, 1, 784, "お兄ちゃんと\nいっぱい話せた日は、\n嬉しくなっちゃいます");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102102, 0, 1, 785, "何か私にできる事\n…そうだ！\nお掃除しますね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102102, 0, 1, 786, "私、ちょっとずつ、\n自分に自信が\nついてきたかもです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102102, 0, 8, 787, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102102, 0, 8, 788, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102102, 0, 1, 789, "おはようございます♪\n声出しして、お芝居の\n練習しなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102102, 0, 1, 790, "お茶にしますか？\nクッキー焼いたので、\nよかったら…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102102, 0, 1, 791, "寝る前に\n台本を読むと、\n覚えやすいんです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 102102, 0, 1, 792, "可愛い女の子に変身！\n出来ますように…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102102, 0, 1, 793, "はぅ…\nフッカフカァ…\n癒されちゃいます…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102102, 0, 1, 794, "この子を相手に\n演技の練習～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102102, 0, 1, 795, "リボンや服に\n染み込ませておこう\nいつでもいい匂い♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102102, 0, 1, 796, "えへへ\nぽっかぽかです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102102, 0, 1, 797, "え、演劇には\nた、体力も重要\nだから～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102102, 0, 1, 798, "残さず\n食べますね…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102102, 0, 1, 799, "戯曲を読んで\n自分が演じるところを\n想像するの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102201, 0, 1, 801, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102201, 0, 1, 802, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102201, 0, 1, 803, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102201, 0, 1, 804, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102201, 0, 1, 805, "楽しいゲームって\nやめどきが\n見つからないわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102201, 0, 4, 806, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102201, 0, 8, 807, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102201, 0, 1, 808, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102201, 0, 1, 809, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102201, 0, 1, 810, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102201, 0, 1, 811, "アンタは人見知り\nしないタチよね？\nいいなあ～…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102201, 0, 4, 812, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102201, 0, 8, 813, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102201, 0, 1, 814, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102201, 0, 1, 815, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102201, 0, 1, 816, "夜はこれからよ！\n朝まで\nゲームするからね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 102201, 0, 1, 817, "アカリみたいに\n可愛くなるのは\n難しいかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102201, 0, 1, 818, "寝っ転がりながら\nゲームができれば\n気分が良さそう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102201, 0, 1, 819, "可愛い…けど、\n私には似合わないかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102201, 0, 1, 820, "はあ、リラックスして\nゲームができるわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102201, 0, 1, 821, "ん…暖かくて、\n寝落ちしちゃいそう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102201, 0, 1, 822, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102201, 0, 1, 823, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102201, 0, 1, 824, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102202, 0, 1, 826, "さあ、今日は\n何のゲームで\n遊ぼうかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102202, 0, 1, 827, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102202, 0, 1, 828, "レアカードをゲット！\nすごいでしょ！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102202, 0, 1, 829, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102202, 0, 1, 830, "楽しいゲームって\nやめどきが\n見つからないわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102202, 0, 8, 831, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102202, 0, 8, 832, "ね、ねえ\nもう少しその…\n一緒にゲームしない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102202, 0, 1, 833, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102202, 0, 1, 834, "欲しいゲームが\n見つからなくて…\n探すの手伝って！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102202, 0, 1, 835, "ねえ、見て！\n新しいゲームを\n買って来たの！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102202, 0, 1, 836, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102202, 0, 8, 837, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102202, 0, 8, 838, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102202, 0, 1, 839, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102202, 0, 1, 840, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102202, 0, 1, 841, "夜はこれからよ！\n朝まで\nゲームするからね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 102202, 0, 1, 842, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102202, 0, 1, 843, "ふああ…\n徹夜明けには\n辛い誘惑だな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102202, 0, 1, 844, "可愛い…けど、\n私には似合わないかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102202, 0, 1, 845, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102202, 0, 1, 846, "寒い日は\nここでゲームするのが\n最高のぜいたくよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102202, 0, 1, 847, "最近ゲームばかりだし\nたまにはいいかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102202, 0, 1, 848, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102202, 0, 1, 849, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102301, 0, 1, 851, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102301, 0, 1, 852, "ぷうきちがいれば、\nなんでも出来ちゃうよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102301, 0, 1, 853, "発声練習しよっと\nぷうきち\nフルスイ～ング！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102301, 0, 1, 854, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102301, 0, 1, 855, "この部屋、ぷうきちを\n置く場所はないのかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102301, 0, 4, 856, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102301, 0, 8, 857, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102301, 0, 1, 858, "おやつは、\n私とぷうきちの\n二人分用意してね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102301, 0, 1, 859, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102301, 0, 1, 860, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102301, 0, 1, 861, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102301, 0, 4, 862, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102301, 0, 8, 863, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102301, 0, 1, 864, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102301, 0, 1, 865, "ふぁ～…ぷうきちと\nお昼寝してくるね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102301, 0, 1, 866, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102301, 0, 1, 868, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102301, 0, 1, 869, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102301, 0, 1, 870, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102301, 0, 1, 871, "ぽわぽわする～\nぷうきちも\n気持ちいい？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102301, 0, 1, 873, "おいしい～\nぷうきちも\n食べてみる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102301, 0, 1, 874, "ぷうきち！\nこの本、読んで～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102501, 0, 1, 901, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102501, 0, 1, 902, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102501, 0, 1, 903, "どうしたら転ばなく\nなるんでしょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102501, 0, 1, 904, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102501, 0, 1, 905, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102501, 0, 4, 906, "おいしい料理で\nお出迎えしなくちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102501, 0, 8, 907, "一生懸命\nご奉仕しますよ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102501, 0, 1, 908, "耳かきしましょうか？\nえ？　こわい？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102501, 0, 1, 909, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102501, 0, 1, 910, "おかえりなさいませ\nご主人さま♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102501, 0, 1, 911, "すみません\nすみません\nすみません～～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102501, 0, 4, 912, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102501, 0, 8, 913, "私のこと、\n専属メイドと思って\n頼ってくださいね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102501, 0, 1, 914, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102501, 0, 1, 915, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102501, 0, 1, 916, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 102501, 0, 1, 917, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102501, 0, 1, 918, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102501, 0, 1, 919, "かわいい♪\nあとでお洗濯\nしてあげますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102501, 0, 1, 920, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102501, 0, 1, 921, "ここでお洗濯ものを\n乾かしましょうか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102501, 0, 1, 922, "あわわわっ…\nこ、転びそうです～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102501, 0, 1, 923, "これくらい\nおいしいものを\n作ってみたいです～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102501, 0, 1, 924, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102502, 0, 1, 926, "失敗したら、この\nお部屋追い出され\nちゃうかも～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102502, 0, 1, 927, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102502, 0, 1, 928, "どうやったら、立派な\nメイドになれるのかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102502, 0, 1, 929, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102502, 0, 1, 930, "ご用があれば\nなんでも言って\nくださいね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102502, 0, 8, 931, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102502, 0, 8, 932, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102502, 0, 1, 933, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102502, 0, 1, 934, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102502, 0, 1, 935, "私、またなにか\nやっちゃいましたか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102502, 0, 1, 936, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102502, 0, 8, 937, "転んだふりであの人に\n…って！\nなんでもないですよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102502, 0, 8, 938, "私、できることなら\nなんでもやりますよ\nあなたのためですから");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102502, 0, 1, 939, "おはようございます♪\n今日も一日がんば…\nう、うわあ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102502, 0, 1, 940, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102502, 0, 1, 941, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 102502, 0, 1, 942, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102502, 0, 1, 943, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102502, 0, 1, 944, "かわいい♪\nあとでお洗濯\nしてあげますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102502, 0, 1, 945, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102502, 0, 1, 946, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102502, 0, 1, 947, "運動しようとすると\nいつも怪我するん\nですよね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102502, 0, 1, 948, "こんなに美味しいもの\nメイドの私には\nもったいないです～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102502, 0, 1, 949, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102701, 0, 1, 951, "あの方と私だけの\n部屋にしたいですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102701, 0, 1, 952, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102701, 0, 1, 953, "運命とは決して\n抗えないもの…\nクスクス…クスクス");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102701, 0, 1, 954, "部屋からあの方の\n香りが…\nああ、至福ですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102701, 0, 1, 955, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102701, 0, 4, 956, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102701, 0, 8, 957, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102701, 0, 1, 958, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102701, 0, 1, 959, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102701, 0, 1, 960, "お願いがあるのです。\n新しい研究の実験台に\n…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102701, 0, 1, 961, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102701, 0, 4, 962, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102701, 0, 8, 963, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102701, 0, 1, 964, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102701, 0, 1, 965, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102701, 0, 1, 966, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 102701, 0, 1, 967, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102701, 0, 1, 968, "はっ！\nリラックスしたら\n閃きましたわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102701, 0, 1, 969, "あら、キメラの素体に\nぴったりですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102701, 0, 1, 970, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102701, 0, 1, 971, "あたたかい…\n加熱調合にも\n使えそうですわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102701, 0, 1, 972, "この運動エネルギー\n何かにうまく\n利用できないかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102701, 0, 1, 973, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102701, 0, 1, 974, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102702, 0, 1, 976, "あの方と私だけの\n部屋にしたいですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102702, 0, 1, 977, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102702, 0, 1, 978, "新しい薬\nどうやってあの方に\n飲ませようかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102702, 0, 1, 979, "部屋からあの方の\n香りが…\nああ、至福ですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102702, 0, 1, 980, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102702, 0, 8, 981, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102702, 0, 8, 982, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102702, 0, 1, 983, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102702, 0, 1, 984, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102702, 0, 1, 985, "お願いがあるのです\n新しい研究の\n実験台に…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102702, 0, 1, 986, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102702, 0, 8, 987, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102702, 0, 8, 988, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102702, 0, 1, 989, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102702, 0, 1, 990, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102702, 0, 1, 991, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 102702, 0, 1, 992, "私ももう少し\n外見に気を配る\nべきでしょうか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102702, 0, 1, 993, "はっ！\nリラックスしたら\n閃きましたわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102702, 0, 1, 994, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102702, 0, 1, 995, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102702, 0, 1, 996, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102702, 0, 1, 997, "この運動エネルギー\n何かにうまく\n利用できないかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102702, 0, 1, 998, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102702, 0, 1, 999, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102801, 0, 1, 1001, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102801, 0, 1, 1002, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102801, 0, 1, 1003, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102801, 0, 1, 1004, "お腹空いたわね、\n残り物で\n何か作ろうかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102801, 0, 1, 1005, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102801, 0, 4, 1006, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102801, 0, 8, 1007, "焼きそばパンが\n食べたいな～\nあいつと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102801, 0, 1, 1008, "ねぇあんた、どこか\n安いお店知らない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102801, 0, 1, 1009, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102801, 0, 1, 1010, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102801, 0, 1, 1011, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102801, 0, 4, 1012, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102801, 0, 8, 1013, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102801, 0, 1, 1014, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102801, 0, 1, 1015, "午後のティータイム\nとかどう？\n良いカフェがあるの♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102801, 0, 1, 1016, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 102801, 0, 1, 1017, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102801, 0, 1, 1018, "これで仕事は一段落ね\nちょっと休憩…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102801, 0, 1, 1019, "うふふ、\nかわいいものを\n見るのは幸せね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102801, 0, 1, 1020, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102801, 0, 1, 1021, "風邪を引かないように\n身体を温めなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102801, 0, 1, 1022, "さっ！　家の中でも\n効率的に\nトレーニングよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102801, 0, 1, 1023, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102801, 0, 1, 1024, "知識から過去を学んで\n未来に活かすわよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102802, 0, 1, 1026, "安売りのお店の情報\n整理しないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102802, 0, 1, 1027, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102802, 0, 1, 1028, "安く済んであいつが\n喜ぶ料理とか…\nないかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102802, 0, 1, 1029, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102802, 0, 1, 1030, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102802, 0, 8, 1031, "焼きそばパンが\n食べたいな～\nあいつと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102802, 0, 8, 1032, "あいつと一緒に…\n色々なところに\nいきたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102802, 0, 1, 1033, "今度買い物に\n付き合いなさい\nセールがあるのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102802, 0, 1, 1034, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102802, 0, 1, 1035, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102802, 0, 1, 1036, "あんたとこうして\nのんびりするのも\nいいものね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102802, 0, 8, 1037, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102802, 0, 8, 1038, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102802, 0, 1, 1039, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102802, 0, 1, 1040, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102802, 0, 1, 1041, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 102802, 0, 1, 1042, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102802, 0, 1, 1043, "これで仕事は一段落ね\nちょっと休憩…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102802, 0, 1, 1044, "ふふふ\nずっと眺めてたく\nなっちゃうわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102802, 0, 1, 1045, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102802, 0, 1, 1046, "風邪を引かないように\n身体を温めなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102802, 0, 1, 1047, "さっ！　家の中でも\n効率的に\nトレーニングよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102802, 0, 1, 1048, "しっかり食べて\n栄養補給、栄養補給！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102802, 0, 1, 1049, "知識から過去を学んで\n未来に活かすわよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102901, 0, 1, 1051, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102901, 0, 1, 1052, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102901, 0, 1, 1053, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102901, 0, 1, 1054, "彼とデュエット\nしてみたいな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102901, 0, 1, 1055, "キラキラ無病息災、\nドキドキ一日一善…\n作詞って難しい～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102901, 0, 4, 1056, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102901, 0, 8, 1057, "写真集見なくても、\n本物がここに\nいるのにな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102901, 0, 1, 1058, "握手会の練習、\n付き合ってくれない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102901, 0, 1, 1059, "写真集のポーズ\n練習中～！\nど、どうかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102901, 0, 1, 1060, "何て書いてあるか\n読めないって？\nもー！\nこれは私のサイン！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102901, 0, 1, 1061, "ね、次のライブ\n観に来てくれる？\nいい席とっとくから！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102901, 0, 4, 1062, "ねぇ、台本読むの\nつきあってくれる？\n恋人役に\nなってほしいんだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102901, 0, 8, 1063, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ。\nどこかは秘密だけど♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102901, 0, 1, 1064, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102901, 0, 1, 1065, "この時間は\nお腹すくけど…\n水着撮影があるから\nお昼抜いてるんだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102901, 0, 1, 1066, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ。");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 102901, 0, 1, 1067, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102901, 0, 1, 1068, "ふぅ…\nこんな姿、ファンには\n見せられないな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102901, 0, 1, 1069, "かわいい～♪\nこれは強力な\nライバル登場かな！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102901, 0, 1, 1070, "ステキな香り～♪\n歌詞のイメージが\n浮かんできそう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102901, 0, 1, 1071, "体も暖まったし！\nダンスレッスンに\n行こうかな♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102901, 0, 1, 1072, "ベストタイム更新！\nライブに向けて\n体力つけないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102901, 0, 1, 1073, "太っちゃったら\nどうしよう…？\nでも手が止まらない～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102901, 0, 1, 1074, "ステキなお話…\n新曲の作詞の\n参考になりそう♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 102902, 0, 1, 1076, "このお部屋すてき！\nファンに追われた時の\n隠れ家にピッタリね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 102902, 0, 1, 1077, "そろそろイメチェン\nしてみようかな…\nそれともこの路線で\nもう少し…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 102902, 0, 1, 1078, "みんながくれた\nファンレター…\n私の大事な宝物…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 102902, 0, 1, 1079, "彼とデュエット\nしてみたいな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 102902, 0, 1, 1080, "疾風怒濤、威風堂々\n獅子奮迅、唯我独尊…\nラブソングの作詞って\n難しいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 102902, 0, 8, 1081, "ファンはもちろん\n大事だけど…\n彼のことは、もっと\n大切にしたいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 102902, 0, 8, 1082, "トップアイドルの\nさらに先…\n彼とならその景色も\n見られるかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 102902, 0, 1, 1083, "新曲の衣装、もうすぐ\n出来あがるんだって！\n楽しみだな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 102902, 0, 1, 1084, "アイドルノゾミだけ\nじゃなくて、私の事も\nちゃんと見てね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 102902, 0, 1, 1085, "キミに響かないなら\nファンにも響くわけ\nないよ！　練習練習！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 102902, 0, 1, 1086, "衣装のスカートは\n鉄壁なんだよ！\n残念でした～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 102902, 0, 8, 1087, "新曲、聞いてくれた？\nキミとのことを\nテーマにしたんだよ\nどこかは秘密だけど♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 102902, 0, 8, 1088, "ねぇ、私がトップ\nアイドルになっても\nちゃんと隣にいてね\n遠くに行かないでね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 102902, 0, 1, 1089, "朝の発声練習に\n行くけど、キミも\n一緒に来ない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 102902, 0, 1, 1090, "お昼ごはん行かない？\n誰にも知られてない\nお店があるんだけど！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 102902, 0, 1, 1091, "夜の帳が二人を包み\n…こういう方向性の\n歌詞も悪くないなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 102902, 0, 1, 1092, "身だしなみオッケー！\nアイドルはいつでも\n完璧でいなくちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 102902, 0, 1, 1093, "ふぅ…\nこんな姿、ファンには\n見せられないな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 102902, 0, 1, 1094, "かわいい～♪\nこれは強力な\nライバル登場かな！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 102902, 0, 1, 1095, "みんなのイメージする\nアイドルの香りは\nこんな感じかな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 102902, 0, 1, 1096, "体も暖まったし！\nダンスレッスンに\n行こうかな♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 102902, 0, 1, 1097, "ベストタイム更新！\nライブに向けて\n体力つけないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 102902, 0, 1, 1098, "太っちゃったら\nどうしよう…？\nでも手が止まらない～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 102902, 0, 1, 1099, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 103001, 0, 1, 1101, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 103001, 0, 1, 1102, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 103001, 0, 1, 1103, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 103001, 0, 1, 1104, "むむ、アクダイカンの\n気配がするデース");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 103001, 0, 1, 1105, "クニトリ合戦、\n楽しみデース！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 103001, 0, 4, 1106, "ショーグンと\n出会えて、\nワタシは果報者デス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 103001, 0, 8, 1107, "最近、ショーグンの\nそばにいると\nドキドキするデス…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 103001, 0, 1, 1108, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 103001, 0, 1, 1109, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 103001, 0, 1, 1110, "さ、さすが\nショーグンデス…\nいつの間に背後を！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 103001, 0, 1, 1111, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 103001, 0, 4, 1112, "見つめられると\nドキドキするデス…\n新しい忍法デスか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 103001, 0, 8, 1113, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 103001, 0, 1, 1114, "おはようデス！\n今日も一日\n忍法日和デス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 103001, 0, 1, 1115, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 103001, 0, 1, 1116, "明日もきっと\nいい日デスよ、\nショーグン！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 103001, 0, 1, 1117, "ちょんまげに\nしてみるのも\n面白いかもデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 103001, 0, 1, 1118, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 103001, 0, 1, 1119, "これを変わり身の\n術に…かわいそう\nだから止めるデス");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 103001, 0, 1, 1120, "ワビサビというもの\nデスね！\n素晴らしいデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 103001, 0, 1, 1121, "身体を暖めて\nイクサに向かうデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 103001, 0, 1, 1122, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 103001, 0, 1, 1123, "うーん、\n美味しいデス…\n幸せデス～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 103001, 0, 1, 1124, "これは…\nＢＵＳＨＩＤＯ…\nデスか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 103002, 0, 1, 1126, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 103002, 0, 1, 1127, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 103002, 0, 1, 1128, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 103002, 0, 1, 1129, "むむ、アクダイカンの\n気配がするデース");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 103002, 0, 1, 1130, "クニトリ合戦\n楽しみデース！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 103002, 0, 8, 1131, "最近、ショーグンの\nそばにいると\nドキドキするデス…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 103002, 0, 8, 1132, "ショーグンの身は\n何があってもワタシが\n守るデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 103002, 0, 1, 1133, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 103002, 0, 1, 1134, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 103002, 0, 1, 1135, "さ、さすがショーグン\nデス…いつの間に\n背後を！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 103002, 0, 1, 1136, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 103002, 0, 8, 1137, "見つめられると\nドキドキするデス…\n新しい忍法デスか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 103002, 0, 8, 1138, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 103002, 0, 1, 1139, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 103002, 0, 1, 1140, "もうお昼デス\nお昼寝するデスか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 103002, 0, 1, 1141, "そろそろおネム\nデース…\n一緒に寝るデス？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 103002, 0, 1, 1142, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 103002, 0, 1, 1143, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 103002, 0, 1, 1144, "これを変わり身の\n術に…かわいそう\nだから止めるデス");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 103002, 0, 1, 1145, "ワビサビというもの\nデスね！\n素晴らしいデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 103002, 0, 1, 1146, "身体を暖めてイクサに\n向かうデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 103002, 0, 1, 1147, "ショーグンのお役に\n立つため、修行\nあるのみデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 103002, 0, 1, 1148, "美味しいデス…\n酢昆布を和えて\nみたいデス");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 103002, 0, 1, 1149, "物事を知って\n来たるべきイクサに\n活かすデス");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 103101, 0, 1, 1151, "お父さん、そろそろ\n子離れを…しない？\nそうですか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 103101, 0, 1, 1152, "父は、いつも男性に\nきびしいですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 103101, 0, 1, 1153, "占ってほしい人は、\nいつでも言って\nください");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 103101, 0, 1, 1154, "あなたの手相は…\nて、手を握るのって\n緊張しますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 103101, 0, 1, 1155, "この部屋、風水的にも\n運気が上がりそうです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 103101, 0, 4, 1156, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 103101, 0, 8, 1157, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 103101, 0, 1, 1158, "女難の相が\n出ていますね…\n気をつけてください");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 103101, 0, 1, 1159, "今日のあなたの運勢は\n大きょ…\n大吉です");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 103101, 0, 1, 1160, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 103101, 0, 1, 1161, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 103101, 0, 4, 1162, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 103101, 0, 8, 1163, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 103101, 0, 1, 1164, "おはようございます\n今日の運勢を\n占いましょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 103101, 0, 1, 1165, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 103101, 0, 1, 1166, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 103101, 0, 1, 1167, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 103101, 0, 1, 1168, "一休みしましょう。\n憑かれて疲れました");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 103101, 0, 1, 1169, "かわいい…\n付喪神が宿らないかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 103101, 0, 1, 1170, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 103101, 0, 1, 1171, "気持ちいいです…\n極楽浄土みたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 103101, 0, 1, 1172, "はぁはぁ…\nお、お父さん、\n代わりに運動して…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 103101, 0, 1, 1173, "お父さんも\n食べたいの？\n死んでるのに？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 103101, 0, 1, 1174, "この本…\nもしかして予言書？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 103102, 0, 1, 1176, "ここにいる幽霊は\nみんな幸せそうです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 103102, 0, 1, 1177, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 103102, 0, 1, 1178, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 103102, 0, 1, 1179, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 103102, 0, 1, 1180, "お父さん、そろそろ\n子離れを…しない？\nそうですか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 103102, 0, 8, 1181, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 103102, 0, 8, 1182, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 103102, 0, 1, 1183, "この部屋、風水的にも\n運気が上がりそうです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 103102, 0, 1, 1184, "シノブには指一本\n触れさせん！\nガブッ！！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 103102, 0, 1, 1185, "あなたの手相は…\nて、手を握るのって\n緊張しますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 103102, 0, 1, 1186, "ラップ音？\nいえ、父のいびきです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 103102, 0, 8, 1187, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 103102, 0, 8, 1188, "前世って信じますか？\n私とあなたは\nもしかすると…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 103102, 0, 1, 1189, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 103102, 0, 1, 1190, "明るい時間は\nあまり得意では\nないんです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 103102, 0, 1, 1191, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 103102, 0, 1, 1192, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 103102, 0, 1, 1193, "うとうと…\nあれ…\n走馬灯が見える…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 103102, 0, 1, 1194, "かわいい…\nお父さんみたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 103102, 0, 1, 1195, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 103102, 0, 1, 1196, "快適です…\n幽霊もどんどん\n集まってきますね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 103102, 0, 1, 1197, "はぁはぁ…\nお、お父さん、\n代わりに運動して…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 103102, 0, 1, 1198, "お父さんの分も、\n私が食べるね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 103102, 0, 1, 1199, "この本の占い\n私の占いと違う…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 103201, 0, 1, 1201, "私だって、一人で\n買い物ぐらい\nできますわ…きっと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 103201, 0, 1, 1202, "そろそろ新しい\n案件を始めないと\nいけませんわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 103201, 0, 1, 1203, "一日も早く、\n一人前の商人に\nなりたいものです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 103201, 0, 1, 1204, "平和を買うのには\nどれほどのお金が\n必要なのかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 103201, 0, 1, 1205, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 103201, 0, 4, 1206, "あの方はお金で雇った\nわけではないのに\n私といてくださる…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 103201, 0, 8, 1207, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 103201, 0, 1, 1208, "私と、庶民向けの\nお店へ一緒に\n参りませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 103201, 0, 1, 1209, "資産運用？\nお幾らほど\n増やしたいのです？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 103201, 0, 1, 1210, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 103201, 0, 1, 1211, "お金で解決するのは\n悪いことでは\nありませんわよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 103201, 0, 4, 1212, "その、よろしければ\n私とお散歩など\nいかがです？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 103201, 0, 8, 1213, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 103201, 0, 1, 1214, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 103201, 0, 1, 1215, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 103201, 0, 1, 1216, "よろしければ\nお休み前のミルクを\n御一緒にいかが？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 103201, 0, 1, 1217, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 103201, 0, 1, 1218, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 103201, 0, 1, 1219, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 103201, 0, 1, 1220, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 103201, 0, 1, 1221, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 103201, 0, 1, 1222, "健康はお金では\n買えませんが\n運動器具は別ですわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 103201, 0, 1, 1223, "なかなか\n美味ですわ\nシェフはどちらに？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 103201, 0, 1, 1224, "経済の仕組みを\nしっかりと\n学ばなくては…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 103202, 0, 1, 1226, "そろそろ新しい\n案件を始めないと\nいけませんわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 103202, 0, 1, 1227, "ええと、これは\nおいくらで買ったん\nでしたっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 103202, 0, 1, 1228, "お金を見せたら\n態度が変わる人は\n信用できませんわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 103202, 0, 1, 1229, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 103202, 0, 1, 1230, "この狭さも\n慣れれば\n快適ですわ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 103202, 0, 8, 1231, "あの方はお金で雇った\nわけではないのに\n私といてくださる…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 103202, 0, 8, 1232, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 103202, 0, 1, 1233, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 103202, 0, 1, 1234, "幼い頃\nお店屋さんごっこを\nなさいましたか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 103202, 0, 1, 1235, "資産運用？\nお幾らほど\n増やしたいのです？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 103202, 0, 1, 1236, "今度一緒に…\nパーティにでも\n行きませんこと？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 103202, 0, 8, 1237, "その、よろしければ\n私とお散歩など\nいかがです？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 103202, 0, 8, 1238, "あなたさまの優しさは\nお金には\n代えられませんわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 103202, 0, 1, 1239, "なぜだか\n眠り足りないような\n感じがしますわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 103202, 0, 1, 1240, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 103202, 0, 1, 1241, "よろしければ\nお休み前のミルクを\n御一緒にいかが？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 103202, 0, 1, 1242, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 103202, 0, 1, 1243, "こういったものに\n腰を沈めると…\nき、気が緩んで…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 103202, 0, 1, 1244, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 103202, 0, 1, 1245, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 103202, 0, 1, 1246, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 103202, 0, 1, 1247, "目標は遠くに\n置いた方が励みに\nなりますわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 103202, 0, 1, 1248, "なかなか\n美味ですわ\nシェフはどちらに？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 103202, 0, 1, 1249, "商人って、物語では\n悪役にされやすいの\nですね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 103301, 0, 1, 1251, "家の中のものを使って\n物ボケ、考えるべ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 103301, 0, 1, 1252, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 103301, 0, 1, 1253, "うちに塀ができたら、\nへー、って言いたい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 103301, 0, 1, 1254, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 103301, 0, 1, 1255, "エリザベスも\n連れてきてぇ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 103301, 0, 4, 1256, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 103301, 0, 8, 1257, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 103301, 0, 1, 1258, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 103301, 0, 1, 1259, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 103301, 0, 1, 1260, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 103301, 0, 1, 1261, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 103301, 0, 4, 1262, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 103301, 0, 8, 1263, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 103301, 0, 1, 1264, "おはよう！　一緒に\n牛の乳しぼりいくべ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 103301, 0, 1, 1265, "お昼だ～腹が減っては\n笑いはできぬ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 103301, 0, 1, 1266, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 103301, 0, 1, 1267, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 103301, 0, 1, 1268, "ナイスなイスに\n座りたい、\nなんちゃって！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 103301, 0, 1, 1269, "めんこいのは、\nスベっても許される\nからずるいべな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 103301, 0, 1, 1270, "必要以上に\n癒されちまうべや～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 103301, 0, 1, 1271, "ぽかぽかだ…\nウケた会場みてぇだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 103301, 0, 1, 1272, "お笑い芸人は、\n体が資本だべさ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 103301, 0, 1, 1273, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 103301, 0, 1, 1274, "ネタ作りのヒントが\n載ってねぇか探すべ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 103302, 0, 1, 1276, "さぁ今日も張り切って\n漫才の練習するべさ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 103302, 0, 1, 1277, "この部屋でも、商品\n売っていいんだべか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 103302, 0, 1, 1278, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 103302, 0, 1, 1279, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 103302, 0, 1, 1280, "エリザベスも\n連れてきてぇ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 103302, 0, 8, 1281, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 103302, 0, 8, 1282, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 103302, 0, 1, 1283, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 103302, 0, 1, 1284, "あんちゃん、百連\nツッコミ特訓だ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 103302, 0, 1, 1285, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 103302, 0, 1, 1286, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 103302, 0, 8, 1287, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 103302, 0, 8, 1288, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 103302, 0, 1, 1289, "おはよう！\n今日もいい\nネタ作り日和だ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 103302, 0, 1, 1290, "お昼だな\nご飯を食べたら、\nネタの練習するべ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 103302, 0, 1, 1291, "もうこんな時間だべ\nネタ作りは\n明日にするべ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 103302, 0, 1, 1292, "うーん…衣装、\nずれてねぇべか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 103302, 0, 1, 1293, "ナイスなイスに\n座りたい\nなんちゃって！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 103302, 0, 1, 1294, "はう～\nなまらめんこいべや～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 103302, 0, 1, 1295, "必要以上に\n癒されちまうべや～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 103302, 0, 1, 1296, "笑いをとることを\n忘れるほど\nあったけぇべ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 103302, 0, 1, 1297, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 103302, 0, 1, 1298, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 103302, 0, 1, 1299, "幅広く教養を\n身につけて\nいいネタ作るべ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 103401, 0, 1, 1301, "通りにいる\nカップル…爆発\nすればいいのに…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 103401, 0, 1, 1302, "う～ん、仕事なんて\nしないで、\n飲んでたいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 103401, 0, 1, 1303, "今日は一日、\n外に出ない！\nもう決めたわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 103401, 0, 1, 1304, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 103401, 0, 1, 1305, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 103401, 0, 4, 1306, "デートとか…\n誘っても嫌がられ\nないかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 103401, 0, 8, 1307, "もしかして運命の\n相手だったり…\nするのかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 103401, 0, 1, 1308, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 103401, 0, 1, 1309, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 103401, 0, 1, 1310, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 103401, 0, 1, 1311, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 103401, 0, 4, 1312, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 103401, 0, 8, 1313, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 103401, 0, 1, 1314, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 103401, 0, 1, 1315, "おっひるー♪\nなに食べよっかな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 103401, 0, 1, 1316, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 103401, 0, 1, 1317, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 103401, 0, 1, 1318, "自然体でゴロゴロ\nするのって\n幸せだなぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 103401, 0, 1, 1319, "こういうの男の子に\nプレゼントして\nもらいたいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 103401, 0, 1, 1320, "うわあ、\nこれ、私の好きな\n香りだー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 103401, 0, 1, 1321, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 103401, 0, 1, 1322, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 103401, 0, 1, 1323, "もぐもぐ…\nおーいしー！\n…太らないよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 103401, 0, 1, 1324, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 103402, 0, 1, 1326, "仕事終わりの一杯の\nために、今日も\n頑張らないと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 103402, 0, 1, 1327, "今日は\nのんびり\n過ごそうかな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 103402, 0, 1, 1328, "あ…\nお皿、洗って\nなかった…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 103402, 0, 1, 1329, "もう少し、大人の\n魅力が出せるように\nなりたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 103402, 0, 1, 1330, "素敵な彼氏…\n欲しいなぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 103402, 0, 8, 1331, "彼と私\n釣り合うかな…\n大丈夫よね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 103402, 0, 8, 1332, "今後のために\nちょっとは花嫁修業\nしてみようかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 103402, 0, 1, 1333, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 103402, 0, 1, 1334, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 103402, 0, 1, 1335, "もう…\n構ってくれないと\n泣いちゃうぞ～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 103402, 0, 1, 1336, "お姉さんが～\n可愛がって\nあげようか～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 103402, 0, 8, 1337, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 103402, 0, 8, 1338, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 103402, 0, 1, 1339, "さーて、今日も\nいっちょやるぞー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 103402, 0, 1, 1340, "後でのんびり\nストレッチでも\nしようっと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 103402, 0, 1, 1341, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 103402, 0, 1, 1342, "仕事の前に、髪とか\n整えないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 103402, 0, 1, 1343, "自然体でゴロゴロ\nするのって\n幸せだなぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 103402, 0, 1, 1344, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 103402, 0, 1, 1345, "うわあ\nこれ、私の好きな\n香りだー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 103402, 0, 1, 1346, "あったかーい…\nだ、だらけ\nちゃいそう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 103402, 0, 1, 1347, "地道な努力が\nリア充への\n第一歩！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 103402, 0, 1, 1348, "おーいしっ♪\nこのために\n生きてるわねえ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 103402, 0, 1, 1349, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 103801, 0, 1, 1351, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 103801, 0, 1, 1352, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 103801, 0, 1, 1353, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 103801, 0, 1, 1354, "ちょっと、休憩…\n本でも読もうかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 103801, 0, 1, 1355, "もっとたくさんの本に\n囲まれて生活したいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 103801, 0, 4, 1356, "むかしむかしある所に\nシオリと\nあの人が…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 103801, 0, 8, 1357, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 103801, 0, 1, 1358, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 103801, 0, 1, 1359, "あ…お姉ちゃんと\n約束があったんだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 103801, 0, 1, 1360, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 103801, 0, 1, 1361, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 103801, 0, 4, 1362, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 103801, 0, 8, 1363, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 103801, 0, 1, 1364, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 103801, 0, 1, 1365, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 103801, 0, 1, 1366, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 103801, 0, 1, 1367, "寝てばかりなので、\n髪が乱れてないかな\n…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 103801, 0, 1, 1368, "少し休もう\n…やっぱり\nたくさん休もう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 103801, 0, 1, 1369, "かわいいものが\n枕元にあると\n安心できるんですよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 103801, 0, 1, 1370, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 103801, 0, 1, 1371, "こうして温まって\nいると、病気も\n治る気がします");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 103801, 0, 1, 1372, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 103801, 0, 1, 1373, "少食ですけど\nこれならたくさん\n食べられそうです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 103801, 0, 1, 1374, "続きが気になって\n読むのを止める\nタイミングが…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 103802, 0, 1, 1376, "しっかり休まないと\n早く回復するように");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 103802, 0, 1, 1377, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 103802, 0, 1, 1378, "病室よりも…\nずっとこの部屋に\nいたいですね…　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 103802, 0, 1, 1379, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 103802, 0, 1, 1380, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 103802, 0, 8, 1381, "むかしむかしある所に\nシオリと\nあの人が…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 103802, 0, 8, 1382, "あの人の\nためにも早く\n病気を治さないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 103802, 0, 1, 1383, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 103802, 0, 1, 1384, "新記録です！\n一週間連続、\n平熱を保ちました！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 103802, 0, 1, 1385, "あ…お薬の時間…\n苦いけど我慢しなきゃ　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 103802, 0, 1, 1386, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 103802, 0, 8, 1387, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 103802, 0, 8, 1388, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 103802, 0, 1, 1389, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 103802, 0, 1, 1390, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 103802, 0, 1, 1391, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 103802, 0, 1, 1392, "寝てばかりなので、\n髪が乱れてないかな\n…？　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 103802, 0, 1, 1393, "少し休もう\n…やっぱり\nたくさん休もう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 103802, 0, 1, 1394, "この子がいれば\n病室に一人でも\n寂しくないですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 103802, 0, 1, 1395, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 103802, 0, 1, 1396, "こうして温まって\nいると、病気も\n治る気がします");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 103802, 0, 1, 1397, "私も、少しは\n体力をつけないと\nいけませんよね　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 103802, 0, 1, 1398, "いっぱい食べて\n健康な体にならないと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 103802, 0, 1, 1399, "続きが気になって\n読むのを止める\nタイミングが…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104001, 0, 1, 1401, "友達ができたら、\n山の上でお弁当…\n夢が広がります…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104001, 0, 1, 1402, "あ、忘れずに\nお花に水を\nあげなきゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104001, 0, 1, 1403, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104001, 0, 1, 1404, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104001, 0, 1, 1405, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104001, 0, 4, 1406, "ＢＢ団の活動日…\nまだかな…\nうふふふっ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104001, 0, 8, 1407, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104001, 0, 1, 1408, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104001, 0, 1, 1409, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104001, 0, 1, 1410, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104001, 0, 1, 1411, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104001, 0, 4, 1412, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104001, 0, 8, 1413, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104001, 0, 1, 1414, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104001, 0, 1, 1415, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104001, 0, 1, 1416, "もう眠いかもです…\n明日は友達、\nできるといいなあ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104001, 0, 1, 1417, "私がもっと\n可愛ければ、\n友達できるかなあ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104001, 0, 1, 1418, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104001, 0, 1, 1419, "かわいいなあ。\nこの子とも\n友達になりたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104001, 0, 1, 1420, "いい香りです…\n精神統一して\n友達のイメトレ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104001, 0, 1, 1421, "灯りを見てると、\n心がなごみます～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104001, 0, 1, 1422, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104001, 0, 1, 1423, "おいしいです…\n私に友達がいたら、\n分けてあげたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104001, 0, 1, 1424, "友達作りの\n参考になりそうなこと\n書いてないかな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104002, 0, 1, 1426, "友達作りは、焦らず\n慌てず、じっくりと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104002, 0, 1, 1427, "ひとつ屋根の下\nって感じ…\nいいですねえ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104002, 0, 1, 1428, "煮汁、煮汁が\n噴きこぼれて\nいませんか～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104002, 0, 1, 1429, "今度、みなさんと\nピクニックとか…\nいってみたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104002, 0, 1, 1430, "ぼっちでもいい\nたくましく育てば\n…なんて");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104002, 0, 8, 1431, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104002, 0, 8, 1432, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104002, 0, 1, 1433, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104002, 0, 1, 1434, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104002, 0, 1, 1435, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104002, 0, 1, 1436, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104002, 0, 8, 1437, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104002, 0, 8, 1438, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104002, 0, 1, 1439, "朝ですね！\nこんないい朝は\n友達作りのチャンス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104002, 0, 1, 1440, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104002, 0, 1, 1441, "もう眠いかもです…\n明日は友達\nできるといいなあ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104002, 0, 1, 1442, "私…見た目\nそこまで変じゃ\nないですよね…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104002, 0, 1, 1443, "ほわあ～…\nふかふかって\nいいものですね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104002, 0, 1, 1444, "この子みたいな\nあいくるしさを\n目指したいです！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104002, 0, 1, 1445, "灯りを見てると\n心がなごみます～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104002, 0, 1, 1446, "ぬくぬくで、なんだか\n眠くなっちゃいますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104002, 0, 1, 1447, "身体を鍛えて\nみなさんを\n守ります！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104002, 0, 1, 1448, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104002, 0, 1, 1449, "友達作りの\n参考になりそうなこと\n書いてないかな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104201, 0, 1, 1451, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104201, 0, 1, 1452, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104201, 0, 1, 1453, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104201, 0, 1, 1454, "人々の平和を守る旅に\n同行してくれる人を\n探していて…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104201, 0, 1, 1455, "あとで曲を復習して\nおかないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104201, 0, 4, 1456, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104201, 0, 8, 1457, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104201, 0, 1, 1458, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104201, 0, 1, 1459, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104201, 0, 1, 1460, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104201, 0, 1, 1461, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104201, 0, 4, 1462, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104201, 0, 8, 1463, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104201, 0, 1, 1464, "おはようございます♪\n少し歌の練習を\nしてきますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104201, 0, 1, 1465, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104201, 0, 1, 1466, "明日の予定は…\nアイドルはやることが\n多いみたいで…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104201, 0, 1, 1467, "見た目については\n前よりも気をつけてと\nノゾミに言われてます");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104201, 0, 1, 1468, "歌の調子をよくする\nためには休息も\n必要ですよね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104201, 0, 1, 1469, "この子たちを\n相手に練習して\nみようかな…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104201, 0, 1, 1470, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104201, 0, 1, 1471, "ここにいると\n穏やかな旋律が\n浮かんできます…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104201, 0, 1, 1472, "歌うためには\n体力づくりは\n欠かせません");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104201, 0, 1, 1473, "歌った後は\nお腹が空いて\nしまうんです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104201, 0, 1, 1474, "音楽について\n理論も勉強\nしないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104202, 0, 1, 1476, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104202, 0, 1, 1477, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104202, 0, 1, 1478, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104202, 0, 1, 1479, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104202, 0, 1, 1480, "楽譜を買いました\n思いついた歌を\n書き留めておくために");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104202, 0, 8, 1481, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104202, 0, 8, 1482, "私の中にある\n私だけの歌…\n作ってみようかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104202, 0, 1, 1483, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104202, 0, 1, 1484, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104202, 0, 1, 1485, "魔物退治ですか！？\n…違います？\n良かった…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104202, 0, 1, 1486, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104202, 0, 8, 1487, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104202, 0, 8, 1488, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104202, 0, 1, 1489, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104202, 0, 1, 1490, "少し休んだら\n午後の練習に\n行ってきますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104202, 0, 1, 1491, "曲を作っていたら\nもうこんな時間…\nおやすみなさい");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104202, 0, 1, 1492, "見た目については\n前よりも気をつけてと\nノゾミに言われてます");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104202, 0, 1, 1493, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104202, 0, 1, 1494, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104202, 0, 1, 1495, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104202, 0, 1, 1496, "ここにいると\n穏やかな旋律が\n浮かんできます…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104202, 0, 1, 1497, "もっと体力をつけて\n最高のライブを\nファンに届けます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104202, 0, 1, 1498, "これでも一応\n体形には気をつけて\n食べてるんですよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104202, 0, 1, 1499, "読書は歌に\n新しい風を\n吹き込んでくれます");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104301, 0, 1, 1501, "店の新メニューでも\n考えるかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104301, 0, 1, 1502, "あたしの店から、\n出前を届けようか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104301, 0, 1, 1503, "パトロール、\nパトロール…\nここは異常なしかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104301, 0, 1, 1504, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104301, 0, 1, 1505, "のんびりし過ぎるのも\n…性に合わねぇな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104301, 0, 4, 1506, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104301, 0, 8, 1507, "今日の献立は…\nあいつの好きなものに\nしてやるか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104301, 0, 1, 1508, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104301, 0, 1, 1509, "天気がいい日は\n外で運動したく\nならねぇか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104301, 0, 1, 1510, "腹減ったか？　なんか\n作ってやろうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104301, 0, 1, 1511, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104301, 0, 4, 1512, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104301, 0, 8, 1513, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104301, 0, 1, 1514, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104301, 0, 1, 1515, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104301, 0, 1, 1516, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104301, 0, 1, 1517, "た、たまにはその…\nかわいい格好とか\nしても…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104301, 0, 1, 1518, "ふわぁ、\n一休みすっかなぁ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104301, 0, 1, 1519, "うっ！　か、かわいい\nじゃねぇか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104301, 0, 1, 1520, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104301, 0, 1, 1521, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104301, 0, 1, 1522, "オラオラオラーーー！\n燃えるぜーーー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104301, 0, 1, 1523, "いい味してるぜ！\nうちの店でも\n出してみたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104301, 0, 1, 1524, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104302, 0, 1, 1526, "パトロール終了～\n本日も異常なしっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104302, 0, 1, 1527, "あたしの店から\n出前を届けようか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104302, 0, 1, 1528, "あいつ、早く\n帰ってこないかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104302, 0, 1, 1529, "あいつ\n腹すかせてないと\nいいんだけど…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104302, 0, 1, 1530, "のんびりし過ぎるのも\n…性に合わねぇな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104302, 0, 8, 1531, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104302, 0, 8, 1532, "今日の献立は…\nあいつの好きなものに\nしてやるか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104302, 0, 1, 1533, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104302, 0, 1, 1534, "天気がいい日は\n外で運動したく\nならねぇか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104302, 0, 1, 1535, "腹減ったか？　なんか\n作ってやろうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104302, 0, 1, 1536, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104302, 0, 8, 1537, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104302, 0, 8, 1538, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104302, 0, 1, 1539, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104302, 0, 1, 1540, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104302, 0, 1, 1541, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104302, 0, 1, 1542, "た、たまにはその…\nかわいい格好とか\nしても…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104302, 0, 1, 1543, "ふぅ～♪\n疲れがとれるな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104302, 0, 1, 1544, "うっ！　か、かわいい\nじゃねぇか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104302, 0, 1, 1545, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104302, 0, 1, 1546, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104302, 0, 1, 1547, "オラオラオラーーー！\n燃えるぜーーー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104302, 0, 1, 1548, "いい味してるぜ！\nうちの店でも\n出してみたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104302, 0, 1, 1549, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104501, 0, 1, 1551, "最近なにも\nしてくれません…\n焦らしてるんですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104501, 0, 1, 1552, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104501, 0, 1, 1553, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104501, 0, 1, 1554, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104501, 0, 1, 1555, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104501, 0, 4, 1556, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104501, 0, 8, 1557, "あっ…\nジロジロ見てたの\n気づいちゃいました？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104501, 0, 1, 1558, "視線を感じると\n思ったら…やはり\nドＳさんでしたか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104501, 0, 1, 1559, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104501, 0, 1, 1560, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104501, 0, 1, 1561, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104501, 0, 4, 1562, "クウカにナニをする\nつもりですか…\n期待してしまいます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104501, 0, 8, 1563, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104501, 0, 1, 1564, "おはようございます。\n朝からお盛んですね？\nぐふふ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104501, 0, 1, 1565, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104501, 0, 1, 1566, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104501, 0, 1, 1567, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104501, 0, 1, 1568, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104501, 0, 1, 1569, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104501, 0, 1, 1570, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104501, 0, 1, 1571, "ああ、身体が\n火照って…\nぐふ、ぐふふ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104501, 0, 1, 1572, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104501, 0, 1, 1573, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104501, 0, 1, 1574, "この本の下に、\nそういう本を\nカムフラージュ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104502, 0, 1, 1576, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104502, 0, 1, 1577, "最近なにも\nしてくれません…\n焦らしてるんですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104502, 0, 1, 1578, "も、妄想が\n足りません…\n禁断症状がっ…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104502, 0, 1, 1579, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104502, 0, 1, 1580, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104502, 0, 8, 1581, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104502, 0, 8, 1582, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104502, 0, 1, 1583, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104502, 0, 1, 1584, "あっ…\nジロジロ見てたの\n気づいちゃいました？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104502, 0, 1, 1585, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104502, 0, 1, 1586, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104502, 0, 8, 1587, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104502, 0, 8, 1588, "ドＳさんがお望みなら\nクウカは\nどんなことだって！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104502, 0, 1, 1589, "朝だからこそ\nできる妄想が\nあります…ぐふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104502, 0, 1, 1590, "こんな日の高いうち\nからドＳさんは…\nじゅるり");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104502, 0, 1, 1591, "今夜は一晩中…\nなんですね？\nぐふふ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104502, 0, 1, 1592, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104502, 0, 1, 1593, "くつろいで油断\nしているスキに何を\nされるのでしょう！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104502, 0, 1, 1594, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104502, 0, 1, 1595, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104502, 0, 1, 1596, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104502, 0, 1, 1597, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104502, 0, 1, 1598, "こんなにいっぱい\n入りません…\nお腹にっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104502, 0, 1, 1599, "どんな本も\nよく見ると\nいやらしいです…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104601, 0, 1, 1601, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104601, 0, 1, 1602, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104601, 0, 1, 1603, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104601, 0, 1, 1604, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104601, 0, 1, 1605, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104601, 0, 4, 1606, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104601, 0, 8, 1607, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104601, 0, 1, 1608, "うずうず…その指、\nじゃれついても\nいいかにゃ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104601, 0, 1, 1609, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104601, 0, 1, 1610, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104601, 0, 1, 1611, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104601, 0, 4, 1612, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104601, 0, 8, 1613, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104601, 0, 1, 1614, "いい朝にゃ！\n顔を洗ってくるにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104601, 0, 1, 1615, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104601, 0, 1, 1616, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104601, 0, 1, 1617, "客商売では、\n外見のチェックも\n欠かせないのにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104601, 0, 1, 1618, "うにゃ～！\nお昼寝に\nぴったりにゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104601, 0, 1, 1619, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104601, 0, 1, 1620, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104601, 0, 1, 1621, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104601, 0, 1, 1622, "本当は外で\n運動したいん\nだけどにゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104601, 0, 1, 1623, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104601, 0, 1, 1624, "ふむふむ…猫の\nしつけ方は、と…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104602, 0, 1, 1626, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104602, 0, 1, 1627, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104602, 0, 1, 1628, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104602, 0, 1, 1629, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104602, 0, 1, 1630, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104602, 0, 8, 1631, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104602, 0, 8, 1632, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104602, 0, 1, 1633, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104602, 0, 1, 1634, "あたしのこと…\nしっかり捕まえ\nといてにゃ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104602, 0, 1, 1635, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104602, 0, 1, 1636, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104602, 0, 8, 1637, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104602, 0, 8, 1638, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104602, 0, 1, 1639, "いい朝にゃ！\n顔を洗ってくるにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104602, 0, 1, 1640, "そろそろおっひる～\nにゃーんか\n食べたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104602, 0, 1, 1641, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104602, 0, 1, 1642, "にゃにゃ…\n髪が乱れてるにゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104602, 0, 1, 1643, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104602, 0, 1, 1644, "これ、猫と匹敵する\nぐらいかわいいにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104602, 0, 1, 1645, "猫でもこわくない\n優しい灯りにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104602, 0, 1, 1646, "ぽっかぽか\nしあわせな気分にゃ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104602, 0, 1, 1647, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104602, 0, 1, 1648, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104602, 0, 1, 1649, "本を読んで\n猫の気持ちを\n勉強するのにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104801, 0, 1, 1651, "内職手伝える人\nいないかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104801, 0, 1, 1652, "次の依頼は、\nこういう計画で…\nうん、完璧ね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104801, 0, 1, 1653, "次の特売セールに\n向けて、しっかり\n準備しないと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104801, 0, 1, 1654, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104801, 0, 1, 1655, "一日に受ける\nバイトの数、もう少し\n増やしたいわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104801, 0, 4, 1656, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104801, 0, 8, 1657, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104801, 0, 1, 1658, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104801, 0, 1, 1659, "相談事かしら？\nそれくらいなら\n無料でいいわよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104801, 0, 1, 1660, "背後の気配なら\nすぐ分かるわ\n試してみる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104801, 0, 1, 1661, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104801, 0, 4, 1662, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104801, 0, 8, 1663, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104801, 0, 1, 1664, "おはよう！\n今日も朝から\nバイトよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104801, 0, 1, 1665, "お昼時ね。\n少し休憩したら、\nまた働くわよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104801, 0, 1, 1666, "もうこんな時間！\n明日もバイトで\n朝早いのよ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104801, 0, 1, 1667, "接客業の依頼が\nある時は、外見も\nキレイにしないとね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104801, 0, 1, 1668, "はぁ～バイトで\n疲れたから癒される～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104801, 0, 1, 1669, "これを内職で\n作ったら\n儲かるかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104801, 0, 1, 1670, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104801, 0, 1, 1671, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104801, 0, 1, 1672, "働き続けるには\n健康第一！　それには\n運動が一番よ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104801, 0, 1, 1673, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104801, 0, 1, 1674, "日々絶えず学んで、\n生活に活かすのよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104802, 0, 1, 1676, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104802, 0, 1, 1677, "次の依頼は\nこういう計画で…\nうん、完璧ね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104802, 0, 1, 1678, "ギルドの経費\nあとちょっとだけ\n削れそうね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104802, 0, 1, 1679, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104802, 0, 1, 1680, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104802, 0, 8, 1681, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104802, 0, 8, 1682, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104802, 0, 1, 1683, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104802, 0, 1, 1684, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104802, 0, 1, 1685, "今から\n休憩するところなの\n一緒にどう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104802, 0, 1, 1686, "ん？　どうしたの？\n私のことが\n気になるの？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104802, 0, 8, 1687, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104802, 0, 8, 1688, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104802, 0, 1, 1689, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104802, 0, 1, 1690, "お昼時ね\n少し休憩したら\nまた働くわよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104802, 0, 1, 1691, "もうこんな時間！\n明日もバイトで\n朝早いのよ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104802, 0, 1, 1692, "女の子らしく…\nどうも忘れちゃう\nのよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104802, 0, 1, 1693, "ここで少し\n休んだら\nまたバイトよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104802, 0, 1, 1694, "これを内職で\n作ったら\n儲かるかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104802, 0, 1, 1695, "仕事から帰ってきて\nこういうのがあると\n素敵ね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104802, 0, 1, 1696, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104802, 0, 1, 1697, "これで休憩中も\n無駄なく\n鍛えられるわ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104802, 0, 1, 1698, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104802, 0, 1, 1699, "日々絶えず学んで\n生活に活かすのよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104901, 0, 1, 1701, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104901, 0, 1, 1702, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104901, 0, 1, 1703, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104901, 0, 1, 1704, "くんくん、\n弟くんのにおい…\n近くにいるね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104901, 0, 1, 1705, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104901, 0, 4, 1706, "この部屋を、\n私と弟くんの\n愛の巣に…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104901, 0, 8, 1707, "はぁ～…弟くん\n禁断症状だよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104901, 0, 1, 1708, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104901, 0, 1, 1709, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104901, 0, 1, 1710, "姉と弟の外出は\nデートって\nいうのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104901, 0, 1, 1711, "もう！\nイタズラする子には\nお姉ちゃんチョップ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104901, 0, 4, 1712, "お姉ちゃんが\nぎゅ～ってして\nあげるね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104901, 0, 8, 1713, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104901, 0, 1, 1714, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104901, 0, 1, 1715, "出かけるなら\n明るいうちに\n帰ってくるんだよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104901, 0, 1, 1716, "まだ起きてるの？\nそれじゃあ、\nお姉ちゃんも\n夜更かししちゃお☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104901, 0, 1, 1717, "きれいなお姉ちゃん\n好きだよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104901, 0, 1, 1718, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104901, 0, 1, 1719, "かわいい♪\nお姉ちゃんチョップ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104901, 0, 1, 1720, "この香り、\n弟くん好きかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104901, 0, 1, 1721, "これ、弟くんみたいに\nあったかいね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104901, 0, 1, 1722, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104901, 0, 1, 1723, "あ、これ\n弟くんが好きな\n味付けだね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104901, 0, 1, 1724, "弟くんが読んでいいか\nお姉ちゃんが先に\n中を確認しなくちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 104902, 0, 1, 1726, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 104902, 0, 1, 1727, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 104902, 0, 1, 1728, "弟くんのお友達は\nみんな私の妹だよ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 104902, 0, 1, 1729, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 104902, 0, 1, 1730, "はっ！\n弟くんが困ってる\n気がする！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 104902, 0, 8, 1731, "はぁ～…弟くん、\n禁断症状だよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 104902, 0, 8, 1732, "お姉ちゃんの痕跡を\n部屋中に\n残しちゃおっと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 104902, 0, 1, 1733, "お姉ちゃん\nキミのお願いなら\nなんでも聞いて\nあげちゃうぞっ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 104902, 0, 1, 1734, "弟くんの部屋は\nお姉ちゃんの\n部屋だよね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 104902, 0, 1, 1735, "お姉ちゃんの胸に\n飛び込んでおいでっ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 104902, 0, 1, 1736, "いいこいいこ～♪\nどう、お姉ちゃんの\nナデナデは？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 104902, 0, 8, 1737, "あ、あのね…\nたまにはお姉ちゃんも\n甘えていい？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 104902, 0, 8, 1738, "お風呂にする？\nごはんにする？\nそれとも…うふふ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 104902, 0, 1, 1739, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 104902, 0, 1, 1740, "お姉ちゃんが\nお昼ごはん\n作ってあげようか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 104902, 0, 1, 1741, "眠れないの？\nお姉ちゃんが子守歌\n歌ってあげるね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 104902, 0, 1, 1742, "弟くんを\n誘惑しちゃうぞ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 104902, 0, 1, 1743, "ここにお姉ちゃんの\n匂いを、た～っぷり\n残しておこうっと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 104902, 0, 1, 1744, "かわいい♪\nお姉ちゃんチョップ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 104902, 0, 1, 1745, "お肌に潤いが\nでてきたみたい～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 104902, 0, 1, 1746, "これ、弟くんみたいに\nあったかいね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 104902, 0, 1, 1747, "ダイエットダイエット\nきれいなお姉ちゃんに\nならなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 104902, 0, 1, 1748, "弟くんの手作りなら\nもっとおいしくなると\n思うんだけどなぁ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 104902, 0, 1, 1749, "弟くんをきちんと\n教育するために\nお姉ちゃんは勉強を\nかかさないのだ☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 105001, 0, 1, 1751, "ん～、エレガントで\n大人なお部屋ね！\nアタシにぴったり！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 105001, 0, 1, 1752, "身長とバスト以外は\n完璧な大人\nなのになぁ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 105001, 0, 1, 1753, "アタシの大人コーデで\nあいつも夢中ね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 105001, 0, 1, 1754, "朝顔の観察したら\n理系女子になれる\nかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 105001, 0, 1, 1755, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 105001, 0, 4, 1756, "大人っぽい子が\n多いわね…アタシも\n負けてられないわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 105001, 0, 8, 1757, "アタシが待ってる\nなんて、アイツは\n幸せものよねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 105001, 0, 1, 1758, "わーい、アメだー♪\n食べる食べるー♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 105001, 0, 1, 1759, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 105001, 0, 1, 1760, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 105001, 0, 1, 1761, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 105001, 0, 4, 1762, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 105001, 0, 8, 1763, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 105001, 0, 1, 1764, "朝のスキンケアは\n大事なの！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 105001, 0, 1, 1765, "お腹すいたー\nダイエット\nやめちゃおうかなー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 105001, 0, 1, 1766, "エイジングケアを\nしないとねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 105001, 0, 1, 1767, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 105001, 0, 1, 1768, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 105001, 0, 1, 1769, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 105001, 0, 1, 1770, "オシャレ～♪\n大人なレディって\n感じよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 105001, 0, 1, 1771, "あったかーい♪\nここで宿題\nしようかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 105001, 0, 1, 1772, "運動したら成長\nしないかしら？\n背とか胸とか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 105001, 0, 1, 1773, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 105001, 0, 1, 1774, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 105002, 0, 1, 1776, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 105002, 0, 1, 1777, "恋人にするなら\nイケメンか\nただイケよね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 105002, 0, 1, 1778, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 105002, 0, 1, 1779, "朝顔の観察したら\n理系女子になれる\nかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 105002, 0, 1, 1780, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 105002, 0, 8, 1781, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 105002, 0, 8, 1782, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 105002, 0, 1, 1783, "なーに？\nアタシに甘えたいの？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 105002, 0, 1, 1784, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 105002, 0, 1, 1785, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 105002, 0, 1, 1786, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 105002, 0, 8, 1787, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 105002, 0, 8, 1788, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 105002, 0, 1, 1789, "レディの朝は\n忙しいのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 105002, 0, 1, 1790, "お腹すいたー\nダイエット\nやめちゃおうかなー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 105002, 0, 1, 1791, "エイジングケアを\nしないとねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 105002, 0, 1, 1792, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 105002, 0, 1, 1793, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 105002, 0, 1, 1794, "あたしがもっと\nオシャレにかわいく\nしてあげようか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 105002, 0, 1, 1795, "オシャレ～♪\n大人なレディって\n感じよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 105002, 0, 1, 1796, "あったかーい♪\nここで宿題\nしようかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 105002, 0, 1, 1797, "運動したら成長\nしないかしら？\n背とか胸とか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 105002, 0, 1, 1798, "レディは、食べ物にも\n気を使うのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 105002, 0, 1, 1799, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 105101, 0, 1, 1801, "魔法と科学\nどっちも極めて\nみせるわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 105101, 0, 1, 1802, "仕事も片付いたし\n部屋で\n実験実験…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 105101, 0, 1, 1803, "たまった治療代の\n請求書を\nまとめないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 105101, 0, 1, 1804, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 105101, 0, 1, 1805, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 105101, 0, 4, 1806, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 105101, 0, 8, 1807, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 105101, 0, 1, 1808, "あら、ヒマなら\n薬草の整理を\n手伝って");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 105101, 0, 1, 1809, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 105101, 0, 1, 1810, "具合が悪い？\nなら診てあげる\nまずは触診から…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 105101, 0, 1, 1811, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 105101, 0, 4, 1812, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 105101, 0, 8, 1813, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 105101, 0, 1, 1814, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 105101, 0, 1, 1815, "あら、もうお昼？\nそろそろ一息\n入れましょうか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 105101, 0, 1, 1816, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 105101, 0, 1, 1817, "医者たるもの\nいつだって\n清潔に…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 105101, 0, 1, 1818, "いいわねこれ\n仮眠の時に\n使わせてもらうわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 105101, 0, 1, 1819, "ぬいぐるみなら\n動物も…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 105101, 0, 1, 1820, "いい香り…\n仕事場にも\n置いてみようかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 105101, 0, 1, 1821, "ここにいると\nついウトウト\nしちゃうわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 105101, 0, 1, 1822, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 105101, 0, 1, 1823, "研究していると\nつい食べるのを\n忘れちゃうのよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 105101, 0, 1, 1824, "実験もいいけど\nいつか成果を\n本にしないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 105102, 0, 1, 1826, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 105102, 0, 1, 1827, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 105102, 0, 1, 1828, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 105102, 0, 1, 1829, "毒を薄めると\n薬になるのって\n今でも不思議だわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 105102, 0, 1, 1830, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 105102, 0, 8, 1831, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 105102, 0, 8, 1832, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 105102, 0, 1, 1833, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 105102, 0, 1, 1834, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 105102, 0, 1, 1835, "欲しい薬が\nあったら言ってね\n調合してみるから");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 105102, 0, 1, 1836, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 105102, 0, 8, 1837, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 105102, 0, 8, 1838, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 105102, 0, 1, 1839, "朝しか取れない\n植物があるの\n採取しに行くわよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 105102, 0, 1, 1840, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 105102, 0, 1, 1841, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 105102, 0, 1, 1842, "身なりについて\n研究するのも\n楽しそうね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 105102, 0, 1, 1843, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 105102, 0, 1, 1844, "たまにはこういう\n可愛らしいものに\n触れないとね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 105102, 0, 1, 1845, "いい香り…\n仕事場にも\n置いてみようかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 105102, 0, 1, 1846, "仕事がなかったら\nこのまますーっと\n眠りたいのに…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 105102, 0, 1, 1847, "運動しないと\n体型の維持が…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 105102, 0, 1, 1848, "適度な食事も\n大切よね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 105102, 0, 1, 1849, "まさか私が\n恋愛読本を読む\n日が来るなんて…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 105201, 0, 1, 1851, "ぺっぺっ！\n…い、いけないっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 105201, 0, 1, 1852, "いっけな～い！\nひづめで床に傷が！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 105201, 0, 1, 1853, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 105201, 0, 1, 1854, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 105201, 0, 1, 1855, "みんな、体重\nどれくらいなのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 105201, 0, 4, 1856, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 105201, 0, 8, 1857, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 105201, 0, 1, 1858, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 105201, 0, 1, 1859, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 105201, 0, 1, 1860, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 105201, 0, 1, 1861, "力仕事ならまかせて！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 105201, 0, 4, 1862, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 105201, 0, 8, 1863, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 105201, 0, 1, 1864, "今朝は寝癖がひどいわ\nしっぽの辺りとか～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 105201, 0, 1, 1865, "ランチは\nおしゃれなカフェで\nしてみたいな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 105201, 0, 1, 1866, "お肌のケアに\nパックしながら\n寝ようかな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 105201, 0, 1, 1867, "やだっ、\nまた寝ぐせついてる！\n直さないと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 105201, 0, 1, 1868, "ミシッっていった！\n私、重すぎるかも！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 105201, 0, 1, 1869, "あ！　あなたも\n美人さんね～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 105201, 0, 1, 1870, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 105201, 0, 1, 1871, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 105201, 0, 1, 1872, "な、なんだか野生に\n帰っちゃいそう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 105201, 0, 1, 1873, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 105201, 0, 1, 1874, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 105202, 0, 1, 1876, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 105202, 0, 1, 1877, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 105202, 0, 1, 1878, "動物苑…って\n嫌なギルド名ね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 105202, 0, 1, 1879, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 105202, 0, 1, 1880, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 105202, 0, 8, 1881, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 105202, 0, 8, 1882, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 105202, 0, 1, 1883, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 105202, 0, 1, 1884, "どうしたの？\n急に抱きついてきて…\n癒されたいの？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 105202, 0, 1, 1885, "ぺっぺっ！\n…い、いけないっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 105202, 0, 1, 1886, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 105202, 0, 8, 1887, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 105202, 0, 8, 1888, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 105202, 0, 1, 1889, "今朝は寝癖がひどいわ\nしっぽの辺りとか～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 105202, 0, 1, 1890, "ダイエット中だから\nお昼は抜いてるの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 105202, 0, 1, 1891, "お肌のケアに\nパックしながら\n寝ようかな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 105202, 0, 1, 1892, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 105202, 0, 1, 1893, "ミシッっていった！\n私、重すぎるかも！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 105202, 0, 1, 1894, "あ！　あなたも\n美人さんね～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 105202, 0, 1, 1895, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 105202, 0, 1, 1896, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 105202, 0, 1, 1897, "な、なんだか野生に\n帰っちゃいそう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 105202, 0, 1, 1898, "何を食べるかより\n誰と食べるかが\n大事なのよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 105202, 0, 1, 1899, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 105301, 0, 1, 1901, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 105301, 0, 1, 1902, "私は子供ではない…！\nが、お菓子は\nいただこう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 105301, 0, 1, 1903, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 105301, 0, 1, 1904, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 105301, 0, 1, 1905, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 105301, 0, 4, 1906, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 105301, 0, 8, 1907, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 105301, 0, 1, 1908, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 105301, 0, 1, 1909, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 105301, 0, 1, 1910, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 105301, 0, 1, 1911, "私ともっと高く、\n共に飛ぼう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 105301, 0, 4, 1912, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 105301, 0, 8, 1913, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 105301, 0, 1, 1914, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 105301, 0, 1, 1915, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 105301, 0, 1, 1916, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 105301, 0, 1, 1917, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 105301, 0, 1, 1918, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 105301, 0, 1, 1919, "うむ…このかわいさ…\n本国に持って\n帰りたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 105301, 0, 1, 1920, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 105301, 0, 1, 1921, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 105301, 0, 1, 1922, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 105301, 0, 1, 1923, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 105301, 0, 1, 1924, "これを読んで、新たな\n戦術を編み出すぞっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 105302, 0, 1, 1926, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 105302, 0, 1, 1927, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 105302, 0, 1, 1928, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 105302, 0, 1, 1929, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 105302, 0, 1, 1930, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 105302, 0, 8, 1931, "あいつは今頃、何を\nしているのだろうな…\nふふっ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 105302, 0, 8, 1932, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 105302, 0, 1, 1933, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 105302, 0, 1, 1934, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 105302, 0, 1, 1935, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 105302, 0, 1, 1936, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 105302, 0, 8, 1937, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 105302, 0, 8, 1938, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 105302, 0, 1, 1939, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 105302, 0, 1, 1940, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 105302, 0, 1, 1941, "寝る前に…あと少し\nだけ、鍛錬を…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 105302, 0, 1, 1942, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 105302, 0, 1, 1943, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 105302, 0, 1, 1944, "うむ…このかわいさ…\n本国に持って\n帰りたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 105302, 0, 1, 1945, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 105302, 0, 1, 1946, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 105302, 0, 1, 1947, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 105302, 0, 1, 1948, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 105302, 0, 1, 1949, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 105701, 0, 1, 1951, "私たちの騎空艇\nどんなデザインに\nしよう…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 105701, 0, 1, 1952, "団員募集の\nポスター\n作ろうっと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 105701, 0, 1, 1953, "父さん…\n今頃どこで\n何してるんだろ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 105701, 0, 1, 1954, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 105701, 0, 1, 1955, "父さん以上の\n世界一の\n騎空士になりたい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 105701, 0, 4, 1956, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 105701, 0, 8, 1957, "父さん…\n私にもね、大切な\n仲間ができたよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 105701, 0, 1, 1958, "仕事の依頼？\nすぐに支度\nしてくるね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 105701, 0, 1, 1959, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 105701, 0, 1, 1960, "なーにー？\nどうしたの？\n今呼んだよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 105701, 0, 1, 1961, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 105701, 0, 4, 1962, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 105701, 0, 8, 1963, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 105701, 0, 1, 1964, "おはよう！\n体の調子はどう？\n今日は頑張れそう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 105701, 0, 1, 1965, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 105701, 0, 1, 1966, "…あれ？\nおしゃべりしてたら\nもうこんな時間？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 105701, 0, 1, 1967, "どうせならオシャレな\n騎空士って\n呼ばれたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 105701, 0, 1, 1968, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 105701, 0, 1, 1969, "よし！　キミは\n今から団長代理だ！\n…なんてね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 105701, 0, 1, 1970, "いい香り～♪\n気持ちが\n落ち着く～…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 105701, 0, 1, 1971, "これって\n騎空艇に\n置けるかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 105701, 0, 1, 1972, "どんな仕事でも\nこなせるような\n体力がないとね…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 105701, 0, 1, 1973, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 105701, 0, 1, 1974, "空を冒険するには\n色んなことを\n知っておかないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 105702, 0, 1, 1976, "…戻らなくなったよ\nどうすれば元に\n戻る…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 105702, 0, 1, 1977, "暇だよ…\nどこかに強い人は\nいないか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 105702, 0, 1, 1978, "ジョブチェンジ！\n…叫んでも\n戻らないよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 105702, 0, 1, 1979, "今のうちに、\n魔物退治の\n仕事に行くね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 105702, 0, 1, 1980, "ハァ～…\nうん、気の巡りは\n問題ないね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 105702, 0, 8, 1981, "私の力の源は\n仲間との\n絆だよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 105702, 0, 8, 1982, "いつかこの拳を\n空を越えた、星まで\n届かせたいね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 0, 105702, 0, 1, 1983, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 0, 105702, 0, 1, 1984, "私はいつも通りに\n喋ってるつもり\nだよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 0, 105702, 0, 1, 1985, "どうした？\nもしかして手合せ\nしたいか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 4, 0, 105702, 0, 1, 1986, "騎空士としても\n拳法家としても\n目指すは世界一ね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 101, 0, 105702, 0, 8, 1987, "背中はあなたに\n預けたよ！\nよろしくね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 102, 0, 105702, 0, 8, 1988, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 1, 1, 105702, 0, 1, 1989, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 2, 2, 105702, 0, 1, 1990, "腹減ったね…\nなにか食べに\n行くよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (2, 1, 3, 3, 105702, 0, 1, 1991, "修行のしすぎは\n良くないよ？\n気をつけるね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 1, 0, 105702, 0, 1, 1992, "美しい技は\n美しい体が\nあってこそ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 2, 0, 105702, 0, 1, 1993, "よいしょっと…\nちょっとのんびり\nさせてもらうよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 3, 0, 105702, 0, 1, 1994, "…練習に\n使うのはさすがに\n可哀そうね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 4, 0, 105702, 0, 1, 1995, "これは…\n瞑想の時にも\n使ってみたいね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 5, 0, 105702, 0, 1, 1996, "出来ればもっと\n熱いほうが\n嬉しいよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 6, 0, 105702, 0, 1, 1997, "ここでも\n修行出来るなんて\n最高だよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 7, 0, 105702, 0, 1, 1998, "気力の源は体力！\n体力の源は\n健康とご飯！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (3, 1, 8, 0, 105702, 0, 1, 1999, "んー…\n難しいことは\nわかんないよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 1, 0, 105801, 0, 1, 2001, "わたしたちの\nギルドハウスへ\nようこそ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 2, 0, 105801, 0, 1, 2002, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 3, 0, 105801, 0, 1, 2003, "ん？\n食べ物のにおい？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 4, 0, 105801, 0, 1, 2004, "みんなと仲良く食卓を\n囲みたいですね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 5, 0, 105801, 0, 1, 2005, "この部屋が、わたしの\n帰る場所です♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 101, 0, 105801, 0, 4, 2006, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (1, 1, 102, 0, 105801, 0, 8, 2007, "あの人、\n大食いの子って\n嫌いかな？");
