INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 1, 0, 1, 1, 0, 1, 1, "ここに困ってる人は\nいないよね…？\nよかった♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 1, 0, 1, 2, 0, 2, 1, "冒険の準備準備…\nうん、忘れ物はなし！\n…あっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 1, 0, 1, 3, 0, 3, 1, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 1, 0, 1, 4, 0, 4, 1, "ここで特訓していい？\n物は壊さないように\n気をつけるね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 1, 0, 1, 5, 0, 5, 1, "なんか落ち着かないや\nもしかして、\n困ってる人が\n近くにいるのかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 1, 0, 4, 101, 0, 6, 1, "えへへ…いつも\n一緒にいてくれて\nありがとねっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 1, 0, 8, 102, 0, 7, 1, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 2, 0, 1, 1, 0, 8, 1, "あははっ！\nやったなー！\nお返しだー！\nこちょこちょこちょ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 2, 0, 1, 2, 0, 9, 1, "どうしたの？\nもしかして困ってる？\nあたしにまかせて！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 2, 0, 1, 3, 0, 10, 1, "えっとね…しっぽは\n触っちゃダメだよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 2, 0, 1, 4, 0, 11, 1, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 2, 0, 4, 101, 0, 12, 1, "ふぁふぃ？\nやられた～！　ほっぺ\nぷにぷにしないでぇ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 2, 0, 8, 102, 0, 13, 1, "えへへ…\n騎士くんの手だ…\nあったかいな…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 2, 0, 1, 1, 1, 14, 1, "おはよー！\n今日も元気に\nいっちゃうよー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 2, 0, 1, 2, 2, 15, 1, "ユイちゃんに\nおやつ作って\nもらおうかな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 2, 0, 1, 3, 3, 16, 1, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 3, 0, 1, 1, 0, 17, 1, "ちゃんとケアして\n傷が残らないように\nしないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 3, 0, 1, 2, 0, 18, 1, "一回休むと\nこのままずるずる\nいきそう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 3, 0, 1, 3, 0, 19, 1, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 3, 0, 1, 4, 0, 20, 1, "落ち着くなぁ～\n心だけじゃなくて\n体もほぐれそう♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 3, 0, 1, 5, 0, 21, 1, "あったかい所に\nいると体が丸く\nなっちゃう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 3, 0, 1, 6, 0, 22, 1, "いつ何が起きるか\n分からないし、\nもっと力をつけないと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 3, 0, 1, 7, 0, 23, 1, "美味しいけど、\n食べ過ぎないように\nしないと…！！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100101, 3, 0, 1, 8, 0, 24, 1, "文字が多いと\n眠くなっちゃう…\n危ない危ない！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 1, 0, 1, 1, 0, 26, 1, "リズムを意識して…\nわんつーわんつー！\nうん、調子はバッチリ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 1, 0, 1, 2, 0, 27, 1, "のんびりするのって\n退屈かも…\n何かして遊ばない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 1, 0, 1, 3, 0, 28, 1, "留守番はちょっと\n苦手かも…\nなんか寂しいんだもん");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 1, 0, 1, 4, 0, 29, 1, "あたしのお友達も\n呼んできていい？\nきっと楽しいよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 1, 0, 1, 5, 0, 30, 1, "誰も困ってないよね？\n…何をすれば\nいいのか分かんないや");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 1, 0, 8, 101, 0, 31, 1, "ちゃんと覚えてるよ\n騎士くんの声…\nあの時はありがとね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 1, 0, 8, 102, 0, 32, 1, "…なんであたし\n騎士くんばっかり\n見てるんだろ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 2, 0, 1, 1, 0, 33, 1, "反応しない特訓…\nだったけど無理～！\n悔しいよぉ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 2, 0, 1, 2, 0, 34, 1, "触るのって楽しい？\n…そっか\n少しだけだよ…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 2, 0, 1, 3, 0, 35, 1, "あたしの元気を\nわけてあげるね！\nフレー！　フレー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 2, 0, 1, 4, 0, 36, 1, "何かムズムズする！？\nって騎士くんかぁー\nどしたの？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 2, 0, 8, 101, 0, 37, 1, "ひゃうっ！　もう…\nしっぽはだめだってば\n次やったらデコピン！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 2, 0, 8, 102, 0, 38, 1, "えへへ…\n騎士くんの手だ…\nあったかいな…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 2, 0, 1, 1, 1, 39, 1, "朝ごはんと運動！\nこれがあたしの\n元気の素だよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 2, 0, 1, 2, 2, 40, 1, "ユイちゃんに\nおやつ作って\nもらおうかな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 2, 0, 1, 3, 3, 41, 1, "寝るのも修行だって\nこないだレイさんが\n言ってたんだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 3, 0, 1, 1, 0, 42, 1, "これでよし♪\n騎士くん\n気づくかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 3, 0, 1, 2, 0, 43, 1, "一回休むと\nこのままずるずる\nいきそう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 3, 0, 1, 3, 0, 44, 1, "新しい練習相手に…\nって冗談だよ冗談！\n本気じゃないから！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 3, 0, 1, 4, 0, 45, 1, "こういうの\nなんか気になって\nじーって見ちゃうんだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 3, 0, 1, 5, 0, 46, 1, "あったかい所に\nいると体が丸く\nなっちゃう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 3, 0, 1, 6, 0, 47, 1, "いつ何が起きるか\n分からないし\nもっと力をつけないと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 3, 0, 1, 7, 0, 48, 1, "お腹が減ると\nなんか力が\n出ないんだよね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100102, 3, 0, 1, 8, 0, 49, 1, "色んなことを\n知っておくのは\n大事だよね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 1, 0, 1, 1, 0, 51, 1, "お片付けも済んだし\n今度は洗濯ものを\nたたもうかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 1, 0, 1, 2, 0, 52, 1, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 1, 0, 1, 3, 0, 53, 1, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 1, 0, 1, 4, 0, 54, 1, "戦いは\n得意じゃないけど\nお料理なら…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 1, 0, 1, 5, 0, 55, 1, "こういう平和な\n時間が、もっと\n続くといいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 1, 0, 4, 101, 0, 56, 1, "わたし…\nもうちょっとだけ勇気\n出してみようかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 1, 0, 8, 102, 0, 57, 1, "みんなのことが大好き\nも、もちろん、\n騎士クンのことも…\nな、仲間として！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 2, 0, 1, 1, 0, 58, 1, "ひゃうっ！\nな、なに？\nびっくりしたぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 2, 0, 1, 2, 0, 59, 1, "男の子って、\nやっぱりおっきいね。\nその…手とか\n他にもいろいろ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 2, 0, 1, 3, 0, 60, 1, "困ったことが\nあったら\nいつでも話してね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 2, 0, 1, 4, 0, 61, 1, "え！　む、虫っ！？\n取って取って！\nはやく～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 2, 0, 4, 101, 0, 62, 1, "あのね…\n一緒におでかけ\nしたいんだけど…\nダメかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 2, 0, 8, 102, 0, 63, 1, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 2, 0, 1, 1, 1, 64, 1, "お、おはよう！\nあの…朝ごはんは\nもう食べちゃった？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 2, 0, 1, 2, 2, 65, 1, "お昼休みだね\nあの…良かったら\nお弁当…食べない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 2, 0, 1, 3, 3, 66, 1, "遅くまでお疲れ様\n明日も頑張ろうね\nふわぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 3, 0, 1, 1, 0, 67, 1, "よし…\nこれなら\n褒めてもらえるかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 3, 0, 1, 2, 0, 68, 1, "がんばることも\n必要だけど、\nお休みも大切だよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 3, 0, 1, 3, 0, 69, 1, "かわいいなぁ…\nわたしも今度\n作ってみよう。\nえっと、材料は…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 3, 0, 1, 4, 0, 70, 1, "戦う前にもこうやって\n落ち着けたら\nいいのに");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 3, 0, 1, 5, 0, 71, 1, "…はへっ？\nも、もしかして今\n寝ちゃってた…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 3, 0, 1, 6, 0, 72, 1, "騎士クンと一緒に\n冒険するなら\n体力がないと…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 3, 0, 1, 7, 0, 73, 1, "おいしいものって\n食べ過ぎちゃうけど…\nしょうがない…よね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100201, 3, 0, 1, 8, 0, 74, 1, "…そっか\nこうすれば魔法が\nもっと強くなるんだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 1, 0, 1, 1, 0, 76, 1, "洗濯と掃除\nどっちから\nはじめよう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 1, 0, 1, 2, 0, 77, 1, "新しい魔道書を\n買いに行かなくちゃ\nもっとたくさん\n魔法を覚えたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 1, 0, 1, 3, 0, 78, 1, "お茶をいれようと\n思うんだけど\n他に飲みたい人は\nいるかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 1, 0, 1, 4, 0, 79, 1, "今日はどんな料理に\n挑戦しよう…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 1, 0, 1, 5, 0, 80, 1, "この気持ち\nもっとちゃんと\n伝えなくっちゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 1, 0, 8, 101, 0, 81, 1, "みんなのことが大好き\nも、もちろん\n騎士クンのことも…\nな、仲間として！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 1, 0, 8, 102, 0, 82, 1, "騎士クンのそばに\nいられるだけで\n幸せだよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 2, 0, 1, 1, 0, 83, 1, "えっと、その…\nこんな所で\nこういうことされると\n恥ずかしいよ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 2, 0, 1, 2, 0, 84, 1, "え！　む、虫っ！？\n取って取って！\nはやく～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 2, 0, 1, 3, 0, 85, 1, "あ、あの…実は\n騎士クンのこと…\nううん、やっぱり\nなんでもない");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 2, 0, 1, 4, 0, 86, 1, "男の子って\nやっぱりおっきいね\nその…手とか\n他にもいろいろ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 2, 0, 8, 101, 0, 87, 1, "騎士クンに\n伝えたいことがあるの\nとっても大切な…\nこ、今度、話すね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 2, 0, 8, 102, 0, 88, 1, "ふふふっ…そんなとこ\nツンツンしないで\nくすぐったいよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 2, 0, 1, 1, 1, 89, 1, "おはよう\nよく眠れた？　今日も\n一日、頑張ろうね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 2, 0, 1, 2, 2, 90, 1, "お腹空いちゃった…\nそうだ。騎士クンも\n一緒にお昼食べない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 2, 0, 1, 3, 3, 91, 1, "もうこんな時間…\nそろそろ寝なくちゃ\nおやすみなさい");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 3, 0, 1, 1, 0, 92, 1, "よし…\nこれなら\n褒めてもらえるかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 3, 0, 1, 2, 0, 93, 1, "がんばることも\n必要だけど\nお休みも大切だよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 3, 0, 1, 3, 0, 94, 1, "かわいい～♪\n騎士クンは\nこういうのって好き？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 3, 0, 1, 4, 0, 95, 1, "戦う前にもこうやって\n落ち着けたら\nいいのに");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 3, 0, 1, 5, 0, 96, 1, "あの…！\nよ、良かったら\n一緒に…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 3, 0, 1, 6, 0, 97, 1, "騎士クンと一緒に\n冒険するなら\n体力がないと…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 3, 0, 1, 7, 0, 98, 1, "最近服のサイズが\n合わないんだけど…\n食べ過ぎなのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100202, 3, 0, 1, 8, 0, 99, 1, "…やっぱり\n月刊恋愛指南は\nためになる…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 1, 0, 1, 1, 0, 101, 1, "装備の手入れでも\nしようかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 1, 0, 1, 2, 0, 102, 1, "次に鍛えるべきは\n肉体面か精神面か\nどちらにしよう…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 1, 0, 1, 3, 0, 103, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 1, 0, 1, 4, 0, 104, 1, "仲間なんていらない\nそう思ってた頃も\nあったな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 1, 0, 1, 5, 0, 105, 1, "考えごとをしていると\nつい部屋中を\n歩いてしまうな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 1, 0, 4, 101, 0, 106, 1, "彼は\nどこまで強くなるのか\n私も負けられないな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 1, 0, 8, 102, 0, 107, 1, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 2, 0, 1, 1, 0, 108, 1, "ああ、キミだったのか\n人の気配がしたから\n誰かと思ったよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 2, 0, 1, 2, 0, 109, 1, "斬りかかるつもりなら\nちゃんとかかってきて\n…呼んだだけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 2, 0, 1, 3, 0, 110, 1, "なに？　私に相談？\n戦いについてなら\n喜んで");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 2, 0, 1, 4, 0, 111, 1, "今のが突き？\n軽い！　もっと鋭く！\nさあ、もう一回！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 2, 0, 4, 101, 0, 112, 1, "ひゃっ！　ど、どこを\n触ってるの！？\n…油断した");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 2, 0, 8, 102, 0, 113, 1, "キミにはこれからも\n私のそばに…\nいや、なんでもない");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 2, 0, 1, 1, 1, 114, 1, "おはよう\n今日も充実した\n一日にしていこう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 2, 0, 1, 2, 2, 115, 1, "そろそろ\nお茶の時間か\nキミも一緒にどう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 2, 0, 1, 3, 3, 116, 1, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 3, 0, 1, 1, 0, 117, 1, "戦いを経てもなお\n美しくあるように…\nこれでよし♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 3, 0, 1, 2, 0, 118, 1, "戦ってばかりで\n休まないと、また\nユイに怒られるな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 3, 0, 1, 3, 0, 119, 1, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 3, 0, 1, 4, 0, 120, 1, "これはいい…\n戦いの前の精神統一に\nもってこいじゃないか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 3, 0, 1, 5, 0, 121, 1, "時々は落ち着いた\n時間を過ごすのも\n悪くない…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 3, 0, 1, 6, 0, 122, 1, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 3, 0, 1, 7, 0, 123, 1, "食事は大切だけど\n食べ過ぎには\n注意しておこう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100301, 3, 0, 1, 8, 0, 124, 1, "ユイ…！\nこ、こんな本を\n読んでいたのか…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 1, 0, 1, 1, 0, 126, 1, "海だけじゃなくて\n川でも釣りがしたいな\n道具も新調しよう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 1, 0, 1, 2, 0, 127, 1, "ツムギが作ってくれた\nこの服…一体\nいつサイズを…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 1, 0, 1, 3, 0, 128, 1, "彼は\nどこまで強くなるのか\n私も負けられないな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 1, 0, 1, 4, 0, 129, 1, "そろそろ修行の\n内容を新しくしよう\nより厳しいものにね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 1, 0, 1, 5, 0, 130, 1, "強くなって…誰にも\n妨げられない自由な\n生き方がしたい");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 1, 0, 8, 101, 0, 131, 1, "まさか風邪を引くとは\n彼の看病のおかげで\nすっかり良く\nなったけど");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 1, 0, 8, 102, 0, 132, 1, "私が一本を取られる\nなんてね…願い事は\nもう決まったのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 2, 0, 1, 1, 0, 133, 1, "どうしたの？\nなんでもない？\nそれなら良かった");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 2, 0, 1, 2, 0, 134, 1, "あんまり突っつくと\nお返しするよ？\n覚悟は出来てる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 2, 0, 1, 3, 0, 135, 1, "ねぇ、今度また\n釣りに行かない？\n大きいのを狙おう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 2, 0, 1, 4, 0, 136, 1, "修行がしたいの？\nそれを早く言ってよ\nすぐに出発しよう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 2, 0, 8, 101, 0, 137, 1, "ひょっとして\n今のも一本だなんて\n言わないよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 2, 0, 8, 102, 0, 138, 1, "キミにはこれからも\n私のそばに…\nいや、なんでもない");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 2, 0, 1, 1, 1, 139, 1, "今日は仕事？\nそれとも修行？\n良ければ付き合うよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 2, 0, 1, 2, 2, 140, 1, "休息も時には\n必要だと、仲間から\n教わったよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 2, 0, 1, 3, 3, 141, 1, "遅くまで大変だね\nまだ仕事があるなら\n手伝おうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 3, 0, 1, 1, 0, 142, 1, "髪の長さを少し\n変えてみようかな…\nわ、私は何を…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 3, 0, 1, 2, 0, 143, 1, "休息があってこそ\n万全な状態で\n戦えるんだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 3, 0, 1, 3, 0, 144, 1, "ヒヨリみたいに\n練習相手に…\nいや、やめておこう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 3, 0, 1, 4, 0, 145, 1, "体と同様に\n心もほぐれるね…\nはぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 3, 0, 1, 5, 0, 146, 1, "休む時は休む\nヒヨリもそろそろ\n覚えて欲しいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 3, 0, 1, 6, 0, 147, 1, "彼に負けないよう\n私も更に強くなろう\n共に戦うために！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 3, 0, 1, 7, 0, 148, 1, "修行のせいか\n最近食べる量が\n増えてきたような…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100302, 3, 0, 1, 8, 0, 149, 1, "ユイ…！\nこ、こんな本を\n読んでいたのか…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 1, 0, 1, 1, 0, 151, 1, "お宝の予感！\n部屋中を\n探検しちゃうぞ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 1, 0, 1, 2, 0, 152, 1, "落とし穴…どこに\n作ったんだっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 1, 0, 1, 3, 0, 153, 1, "うえ～ん！\n…嘘泣きしたら\n相手は油断するかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 1, 0, 1, 4, 0, 154, 1, "ハニートラップって\nすごく甘いお菓子で\nイタズラするのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 1, 0, 1, 5, 0, 155, 1, "かくれんぼする人\nこの指、と～まれ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 1, 0, 4, 101, 0, 156, 1, "ミソギ以外の人に\nイタズラされちゃ\nダメだよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 1, 0, 8, 102, 0, 157, 1, "ミソギと遊ぶ？\nミソギに遊ばれる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 2, 0, 1, 1, 0, 158, 1, "いいこと思いついた！\nい、イタズラとかじゃ\nないよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 2, 0, 1, 2, 0, 159, 1, "うわっ！？\n　にいちゃんが、\nミソギにイタズラ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 2, 0, 1, 3, 0, 160, 1, "にいちゃんの\n通りそうなところに\nバナナの皮を…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 2, 0, 1, 4, 0, 161, 1, "にいちゃんのオデコと\nホッペと鼻と目と耳に\n落書きを…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 2, 0, 4, 101, 0, 162, 1, "ここ、ミソギ探検隊の\n基地にしようよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 2, 0, 8, 102, 0, 163, 1, "だ～れだ…って？\nにいちゃんの声くらい\nわかるよ！\n大好きだもん♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 2, 0, 1, 1, 1, 164, 1, "にいちゃん、おはよ！\n目覚まし時計を\n止めておいたのに、\nよく起きられたね～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 2, 0, 1, 2, 2, 165, 1, "にいちゃんが\n昼寝したら、\nイタズラし放題♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 2, 0, 1, 3, 3, 166, 1, "夜の探検も\nおもしろそう！\nねぇ出かけちゃダメ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 3, 0, 1, 1, 0, 167, 1, "イタズラには\nお色気も必要…\nうん、完璧！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100401, 3, 0, 1, 2, 0, 168, 1, "座ったら、ブーッって\nいう音が出たら、\nおもしろいな～♪");
