INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 1, 0, 1, 4, 0, 254, 1, "幽霊の正体見たり、\nミヤコなの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 1, 0, 1, 5, 0, 255, 1, "ミヤコがいきなり\n消えたら、みんな\n驚くかもなの～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 1, 0, 4, 101, 0, 256, 1, "アイツがいて、\nプリンもある。\nきっとここが天国なの");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 1, 0, 8, 102, 0, 257, 1, "アイツはプリンセス\nナイトじゃなくて\nプリンナイトなの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 2, 0, 1, 1, 0, 258, 1, "姿を消して、オマエを\n見張ってやるの～\nむふふ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 2, 0, 1, 2, 0, 259, 1, "もし成仏した先に\nプリンがなかったら\n地獄なの～…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 2, 0, 1, 3, 0, 260, 1, "右にプリン左にプリン\n後ろにもプリン♪\n前にはオマエなの～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 2, 0, 1, 4, 0, 261, 1, "死んだ人には\nお供え物をするの～\nミヤコにも、プリンを\n毎日お供えするの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 2, 0, 4, 101, 0, 262, 1, "たくさんオマエと\n遊べて、\n成仏しそうなくらい\nうれしいの～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 2, 0, 8, 102, 0, 263, 1, "オマエのこと\n気に入ったからずっと\nとり憑いてやるの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 2, 0, 1, 1, 1, 264, 1, "う～　眠いの…\n二度目の二度寝を\nするの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 2, 0, 1, 2, 2, 265, 1, "お昼ご飯はプリンなの\n３時のおやつも、\nプリンなの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 2, 0, 1, 3, 3, 266, 1, "ディナーは豪華に、\nプリンのフルコース\nなの～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 3, 0, 1, 1, 0, 267, 1, "体が透けちゃうから\nうまく使えないの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 3, 0, 1, 2, 0, 268, 1, "まったりなの～\nあとはプリンがあれば\n完璧なの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 3, 0, 1, 3, 0, 269, 1, "む～…かわいいけど\nプリンは分けて\nあげないの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 3, 0, 1, 4, 0, 270, 1, "は～　いい気分なの…\n成仏しそうなの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 3, 0, 1, 5, 0, 271, 1, "も、もしかして\nミヤコ、ここで\n火葬されちゃうの？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 3, 0, 1, 6, 0, 272, 1, "幽霊には、体力も\n筋力も関係ないの～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 3, 0, 1, 7, 0, 273, 1, "ぱくぱくモグモグ…\nミヤコ、幽霊だけど、\nお腹はすくの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100701, 3, 0, 1, 8, 0, 274, 1, "プリンの写真集が\nあればいいのに\nなの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100702, 1, 0, 1, 1, 0, 276, 1, "うらめしや～…\n裏の飯より\nプリンがいいの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100702, 1, 0, 1, 2, 0, 277, 1, "プリン♪　プリン♪\nプリン♪　なの～♪　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100702, 1, 0, 1, 3, 0, 278, 1, "世界中のケーキ屋が\nプリンのおいしさを\n競い合えばいいの～！\n審査員はミヤコなの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100702, 1, 0, 1, 4, 0, 279, 1, "買ってきたプリンには\n全部ミヤコの名前を\n書いておくの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100702, 1, 0, 1, 5, 0, 280, 1, "プリンがないなら\n死んだほうがマシなの\nあ、もう死んでたの～ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100702, 1, 0, 8, 101, 0, 281, 1, "アイツがいて\nプリンもある\nきっとここが天国なの");
