INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100702, 2, 0, 1, 3, 0, 285, 1, "今日はオマエと\nプリンを買いに行くの\n強制なの～　");
