INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100702, 3, 0, 1, 2, 0, 293, 1, "落ち着くの～\nいまプリン食べたら\nきっといつもより\nおいしいの～♪ ");
