INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100702, 3, 0, 1, 6, 0, 297, 1, "幽霊には、体力も\n筋力も関係ないの～！　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100702, 3, 0, 1, 7, 0, 298, 1, "おいしいの～\nプリン味だったら\nもっとおいしいの～ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100702, 3, 0, 1, 8, 0, 299, 1, "プリンの写真集が\nあればいいのに\nなの～ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 1, 0, 1, 1, 0, 301, 1, "もし太陽が滅んでも\nボクの美しさで大地を\n照らしてあげる！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 1, 0, 1, 2, 0, 302, 1, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 1, 0, 1, 3, 0, 303, 1, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 1, 0, 1, 4, 0, 304, 1, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 1, 0, 1, 5, 0, 305, 1, "うわっ昨日より可愛く\nなってる。明日は\nどうなっちゃう\nのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 1, 0, 4, 101, 0, 306, 1, "このボクがいるだけで\nあの人への\nおもてなしだよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 1, 0, 8, 102, 0, 307, 1, "ボクの可愛さは、\nふふ…あの人の\nためのものさ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 2, 0, 1, 1, 0, 308, 1, "ねぇ～、ボクって\n可愛い？\nふふ、知ってる☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 2, 0, 1, 2, 0, 309, 1, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 2, 0, 1, 3, 0, 310, 1, "ふふ、ボクに触ると\nキミも美しく\nなっちゃうかもよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 2, 0, 1, 4, 0, 311, 1, "どうかな、\n今日のボク。\n美しい？　可愛い？\nねえ、どっち？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 2, 0, 4, 101, 0, 312, 1, "あの人も今、\n可愛いボクのこと\n考えてるかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 2, 0, 8, 102, 0, 313, 1, "美しい…\nキミの瞳に写るボクは\nより一層きれいだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 2, 0, 1, 1, 1, 314, 1, "美しいボクの\n美しい一日が\n始まるよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 2, 0, 1, 2, 2, 315, 1, "さーて、午後からも\n可愛く美しく\n華麗に優雅に♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 2, 0, 1, 3, 3, 316, 1, "バラの花びら\nいっぱい浮かべた\nお風呂に入ってから\n寝ようっと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 3, 0, 1, 1, 0, 317, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 3, 0, 1, 2, 0, 318, 1, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 3, 0, 1, 3, 0, 319, 1, "わあ、可愛い！\n…まあ、ボクほど\nじゃないけどさ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 3, 0, 1, 4, 0, 320, 1, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 3, 0, 1, 5, 0, 321, 1, "気安く触ったら\n火傷する…\n炎はボクに似ているね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 3, 0, 1, 6, 0, 322, 1, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 3, 0, 1, 7, 0, 323, 1, "ボクが食べると、\n食べ物まで\n可愛いなあ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100801, 3, 0, 1, 8, 0, 324, 1, "ボクが触った瞬間に、\nこの本は美を\n携えてしまうんだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 1, 0, 1, 1, 0, 326, 1, "ボクの美しさは\nみんなのために\nあるんだよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 1, 0, 1, 2, 0, 327, 1, "う～ん、この部屋には\nボクの肖像画や\n彫像が必要だね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 1, 0, 1, 3, 0, 328, 1, "これ以上美しくなると\nボクをめぐって\n大陸中で戦争が\n始まっちゃう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 1, 0, 1, 4, 0, 329, 1, "ボクの誕生とともに\n全ての美しさは過去の\n遺物になったんだよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 1, 0, 1, 5, 0, 330, 1, "ボクの可愛さについて\nみんな話し合わない\nのかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 1, 0, 8, 101, 0, 331, 1, "このボクがいるだけで\nあの人への\nおもてなしだよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 1, 0, 8, 102, 0, 332, 1, "あの人も今\n可愛いボクのこと\n考えてるかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 2, 0, 1, 1, 0, 333, 1, "美の女神と愛の女神が\nボクを作る時、本気\n出しちゃったんだね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 2, 0, 1, 2, 0, 334, 1, "ボクの美しさに嫉妬\nしたって意味ないよね\n次元が違うんだから");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 2, 0, 1, 3, 0, 335, 1, "ボクの化粧品使う？\n一緒に可愛くなろうよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 2, 0, 1, 4, 0, 336, 1, "ボクがいるだけで\nこの部屋は美の神殿♪\nランドソルの新たな\n観光名所になるね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 2, 0, 8, 101, 0, 337, 1, "呼んだ？\n可愛いボクと話したい\n気持ちはわかるよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 2, 0, 8, 102, 0, 338, 1, "美しい…\nキミの瞳に写るボクは\nより一層綺麗だ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 2, 0, 1, 1, 1, 339, 1, "朝日と共に\nボクの美しさも\n周りを照らすよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 2, 0, 1, 2, 2, 340, 1, "ボクの美しさを\n維持するために\nお昼ご飯食べなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 2, 0, 1, 3, 3, 341, 1, "バラの花びらいっぱい\n浮かべたお風呂に\n入ってから\n寝ようっと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 3, 0, 1, 1, 0, 342, 1, "鏡よ、鏡。この世で\n一番可愛いボクに\nお似合いの人はだれ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 3, 0, 1, 2, 0, 343, 1, "ボクが座ったソファは\n「美しいソファ」に\nランクアップした！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 3, 0, 1, 3, 0, 344, 1, "可愛いものを\n可愛いって言える\nボクって可愛いよね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 3, 0, 1, 4, 0, 345, 1, "服を羽織るように\n香りをまとうボク…\n美しい、美しすぎる！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 3, 0, 1, 5, 0, 346, 1, "冷えは美容の大敵\nなんだから！\nここ、ボクの場所ね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 3, 0, 1, 6, 0, 347, 1, "運動は苦手なのに\n運動するボクの姿は\n可愛いなんて…罪♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 3, 0, 1, 7, 0, 348, 1, "ボクが食べると\n食べ物まで\n可愛いなあ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100802, 3, 0, 1, 8, 0, 349, 1, "ボクが触った瞬間に\nこの本は美を\n携えてしまうんだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 1, 0, 1, 1, 0, 351, 1, "「第１０部２０章\n終焉戦争、開幕」\n…これでよし！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 1, 0, 1, 2, 0, 352, 1, "今度シグルドに\n私の魔法を一つ\n伝授してやるか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 1, 0, 1, 3, 0, 353, 1, "ここでは風が\n吹いてこないな…\n換気したい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 1, 0, 1, 4, 0, 354, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 1, 0, 1, 5, 0, 355, 1, "まずは包帯をとって\n手から龍の形の\n炎を出して…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 1, 0, 4, 101, 0, 356, 1, "シグルド…\n今頃何処で何を\nしているのだろう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 1, 0, 8, 102, 0, 357, 1, "最近、彼の姿が\nまぶしく見える…\nどうして…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 2, 0, 1, 1, 0, 358, 1, "辞書を知らないか？\n…探している\n理由は聞くな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 2, 0, 1, 2, 0, 359, 1, "そうだ！\n貴様の技にも\n名前をつけてやろう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 2, 0, 1, 3, 0, 360, 1, "私に触れても力を\n奪われないのは\n貴様くらいだぞ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 2, 0, 1, 4, 0, 361, 1, "何事だ？\nまさか我が瞳に\n魅入られたのか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 2, 0, 4, 101, 0, 362, 1, "貴様がそばにいると\nとても安心できる…\n…不思議だ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 2, 0, 8, 102, 0, 363, 1, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 2, 0, 1, 1, 1, 364, 1, "「覚醒」めたか…\n行くぞ、悪を討つ\n戦いに出発だ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 2, 0, 1, 2, 2, 365, 1, "昼間は「ヤツら」も\n襲ってはこない…\n束の間の休息だ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 2, 0, 1, 3, 3, 366, 1, "どうした？　夜はまだ\nこれから…ふぁ…\nね、眠くなどないぞ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 3, 0, 1, 1, 0, 367, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 3, 0, 1, 2, 0, 368, 1, "くっ…！\n私を眠りの国へ\n誘うつもりか！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 3, 0, 1, 3, 0, 369, 1, "このようなものに\n懐柔されるものか！\n…されるものか！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 3, 0, 1, 4, 0, 370, 1, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 3, 0, 1, 5, 0, 371, 1, "これが我らの求めた\n安寧か…\n悪くない…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 3, 0, 1, 6, 0, 372, 1, "この程度\n前世での激闘に\n比べれば…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 3, 0, 1, 7, 0, 373, 1, "最近金欠でな…\nいやぁありがたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100901, 3, 0, 1, 8, 0, 374, 1, "ここにも\n前世の記憶が\n眠っていたとは…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 1, 0, 1, 1, 0, 376, 1, "「第１３部１章\n閃光のシグルド」\n…ついに登場だな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 1, 0, 1, 2, 0, 377, 1, "何故だ…何故\nこうもあっさり金欠に\n逆戻りするのだ！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 1, 0, 1, 3, 0, 378, 1, "やはり転生者という\n設定はカッコイイ…\n天才だな、私！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 1, 0, 1, 4, 0, 379, 1, "そうだ、ナナカに\nこの間借りた本を\n返さないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 1, 0, 1, 5, 0, 380, 1, "そろそろ今日の分の\n魔力を溜めないと\nまた魔力切れが…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 1, 0, 1, 101, 0, 381, 1, "最近奴がそばに\nいるだけで、鼓動が\n早くなるのは何故だ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 1, 0, 8, 102, 0, 382, 1, "シ、シグルド…！\nだめだ…妄想でも\n上手く言えない…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 2, 0, 8, 1, 0, 383, 1, "なんだ？　新たな\n賞金の情報を\n持ってきたのか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 2, 0, 1, 2, 0, 384, 1, "名乗りやポーズは\nその強度で相手を\n圧倒する技術だ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 2, 0, 1, 3, 0, 385, 1, "いいか、魔法の\n詠唱中は絶対に\n声をかけるなよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 2, 0, 1, 4, 0, 386, 1, "おお、ちょうど今\n貴様専用の設定集を\n作成中だぞ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 2, 0, 1, 101, 0, 387, 1, "貴様がそばにいると\nとても安心できる…\n…不思議だ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 2, 0, 8, 102, 0, 388, 1, "い、いきなり目の前に\n現れるな！　ドキドキ\nするだろうがぁ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 2, 0, 8, 1, 1, 389, 1, "夜更かしのせいか\nまだ眠気が…いや！\nこれは組織の罠だ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 2, 0, 1, 2, 2, 390, 1, "平穏な時に\n出来るだけ魔力を\n溜めておかねば…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 2, 0, 1, 3, 3, 391, 1, "夜になると、力が\n増した気分になるな\n…なりませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 3, 0, 1, 1, 0, 392, 1, "何か足りないな…\n暗黒騎士の紋章を\n作ろうかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 3, 0, 1, 2, 0, 393, 1, "お、おのれ\nヒュプノス…\n私は負けないぞ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 3, 0, 1, 3, 0, 394, 1, "くっくっくっ…\n貴様は特別団員に\nしてやろう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 3, 0, 1, 4, 0, 395, 1, "これを使えば\nもっとそれっぽい\n雰囲気が出そうだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 3, 0, 1, 5, 0, 396, 1, "これが我らの求めた\n安寧か…\n悪くない…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 3, 0, 1, 6, 0, 397, 1, "新たな戦いに備えて\n魔力だけでなく\n体力も鍛えねば！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 3, 0, 1, 7, 0, 398, 1, "食事は魔力に\n変換する体力の\n元になるのだ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (100902, 3, 0, 1, 8, 0, 399, 1, "フフフ…\nこれを覚えれば\nもっと強く…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 1, 0, 1, 1, 0, 401, 1, "王子はんほんまに\nはんなりしてて、\n王子はんって感じやわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 1, 0, 1, 2, 0, 402, 1, "この絵本の王子はん、\n王子はんみたいで\nええなぁ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 1, 0, 1, 3, 0, 403, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 1, 0, 1, 4, 0, 404, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 1, 0, 1, 5, 0, 405, 1, "王子はんのために\n糸を紡いで、\n編み物でもしまひょか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 1, 0, 4, 101, 0, 406, 1, "マホ姫と王子はん、\n絵本の世界より\nベストカップルや");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 1, 0, 8, 102, 0, 407, 1, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 2, 0, 1, 1, 0, 408, 1, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 2, 0, 1, 2, 0, 409, 1, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 2, 0, 1, 3, 0, 410, 1, "王子はんは、\n絵本から出てきた人\nみたいやね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 2, 0, 1, 4, 0, 411, 1, "うちの話し方\nのんびりしてます\nやろか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 2, 0, 4, 101, 0, 412, 1, "マホ姫の手は\nつべたいから、\n王子はんが温めて？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 2, 0, 8, 102, 0, 413, 1, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 2, 0, 1, 1, 1, 414, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 2, 0, 1, 2, 2, 415, 1, "お茶請けはお大福が\nよろしおすけど\nクッキーもええなあ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 2, 0, 1, 3, 3, 416, 1, "うち、普段から\n夢見がちやけど…\nほんに眠い時間やわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 3, 0, 1, 1, 0, 417, 1, "王子はんに\n会いに行くなら、\nおめかしせな\nあかんなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 3, 0, 1, 2, 0, 418, 1, "はんなりした\nええお椅子やわぁ\nほっこりしますなあ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 3, 0, 1, 3, 0, 419, 1, "かいらしもんが\nいっぱいで、\nマホマホ王国みたい\nやわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 3, 0, 1, 4, 0, 420, 1, "心地ええどす。まるで\nお花畑にいるようやね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 3, 0, 1, 5, 0, 421, 1, "ぽかぽかどす。\n心まであったかく\nなりますわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 3, 0, 1, 6, 0, 422, 1, "はぁはぁ、お姫はんは\n激しい運動は\nしぃひんもんどす～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 3, 0, 1, 7, 0, 423, 1, "もぐもぐ…あんまり\n速う食べられへんけど\n美味しいわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101001, 3, 0, 1, 8, 0, 424, 1, "絵本、絵本～\n幸せやわぁ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 1, 0, 1, 1, 0, 426, 1, "王子はんにとって\n自慢の姫になるえ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 1, 0, 1, 2, 0, 427, 1, "カボチャの馬車に\nガラスの靴…\n欲しいなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 1, 0, 1, 3, 0, 428, 1, "うちがガラスの靴を\n落としたら、王子はん\n見つけてくれるやろか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 1, 0, 1, 4, 0, 429, 1, "マホマホ王国\n次期国王の王子はん\n…見てみたいわ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 1, 0, 1, 5, 0, 430, 1, "ほんまなぁ\nこの絵本が面白いんよ\n王子はんに\n読んであげよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 1, 0, 8, 101, 0, 431, 1, "マホ姫と王子はん\n絵本の世界より\nベストカップルや");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 1, 0, 8, 102, 0, 432, 1, "王子はんと\n一緒にいると\n胸がせわしのー\nうごいてかなん");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 2, 0, 1, 1, 0, 433, 1, "たまには、王子はんの\n好きな絵本も\n知りたいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 2, 0, 1, 2, 0, 434, 1, "王子はんを\n国に連れて帰ったら\nみんな大歓迎して\nくれるやろなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 2, 0, 1, 3, 0, 435, 1, "そのうち、王子はんの\nお国にも連れてって\nもらえますやろか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 2, 0, 1, 4, 0, 436, 1, "絵本読むんも\nええなぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 2, 0, 8, 101, 0, 437, 1, "うちがもし悪い魔法に\nかかったら、王子はん\n口づけしてくれはる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 2, 0, 8, 102, 0, 438, 1, "うちら並んでたら\nお雛様みたいに\n見えると思いまへん？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 2, 0, 1, 1, 1, 439, 1, "朝は小鳥はんも\nおなかがすいてるから\nよう飛び回りはるねえ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 2, 0, 1, 2, 2, 440, 1, "マホマホ王国では\nこの時分は\nみんなお昼寝\nしてたんよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 2, 0, 1, 3, 3, 441, 1, "夜に飛んだり鳴いたり\nする鳥はんもおるんよ\n昼は寝てはるんかねぇ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 3, 0, 1, 1, 0, 442, 1, "王子はんに\n会いに行くなら\nおめかしせな\nあかんなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 3, 0, 1, 2, 0, 443, 1, "王子はんも一緒に\nゆっくりしていっても\nええんよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 3, 0, 1, 3, 0, 444, 1, "かいらしい子やねぇ\nうちとお友だちに\nなってくれるん？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 3, 0, 1, 4, 0, 445, 1, "癒しアイテムって\nほんま、好っきゃわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 3, 0, 1, 5, 0, 446, 1, "うちのお城にも\nほしいわぁ。帰ったら\nさっそく準備せな\nあかんなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 3, 0, 1, 6, 0, 447, 1, "部屋の中で運動\nできるのも、なかなか\n面白いもんやわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 3, 0, 1, 7, 0, 448, 1, "おいひいわ～\nマホマホ王国には\nない味やわぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101002, 3, 0, 1, 8, 0, 449, 1, "しっかり知識を\n身につけんと\n魔法も使えへん\nからなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 1, 0, 1, 1, 0, 451, 1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 1, 0, 1, 2, 0, 452, 1, "おやつのケーキは\n苺かチョコ…\nどっちも食べよう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 1, 0, 1, 3, 0, 453, 1, "い、いけない！\n縫い針落としちゃった");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 1, 0, 1, 4, 0, 454, 1, "ケーキをクレープで\n巻いたら…\n大ヒット商品の予感！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 1, 0, 1, 5, 0, 455, 1, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 1, 0, 4, 101, 0, 456, 1, "お兄ちゃんに、\nお洋服でも\n作ろうかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 1, 0, 8, 102, 0, 457, 1, "ここがお兄ちゃんの\n部屋…\nドキドキしちゃう～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 2, 0, 1, 1, 0, 458, 1, "ねぇ、おにいちゃん\n一緒に、スイーツでも\n食べにいかない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 2, 0, 1, 2, 0, 459, 1, "クリームついてた！？\nさっき食べた、\nケーキかも…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 2, 0, 1, 3, 0, 460, 1, "勉強、見てくれるん\nですか？　やった～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 2, 0, 1, 4, 0, 461, 1, "服がほつれてますよ？\n直してあげます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 2, 0, 4, 101, 0, 462, 1, "私をお嫁さんに\nしたいなら、予約は\nお早目に、ですよ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 2, 0, 8, 102, 0, 463, 1, "ねぇねぇお兄ちゃん！\n…えへへ、\n呼んでみただけ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 2, 0, 1, 1, 1, 464, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 2, 0, 1, 2, 2, 465, 1, "お昼休憩に\n甘いものが\n食べたいです～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 2, 0, 1, 3, 3, 466, 1, "ねむいです～\nでももうちょっと\n勉強しないと～…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 3, 0, 1, 1, 0, 467, 1, "この服、かわいい♪\n今度作って\nみよっと～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 3, 0, 1, 2, 0, 468, 1, "勉強で疲れたから\n休憩しよ～っと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 3, 0, 1, 3, 0, 469, 1, "かわいい♪\n私が服を作って\nあげよっか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 3, 0, 1, 4, 0, 470, 1, "リラックスできます～\nお兄ちゃんが隣に\nいるみたい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 3, 0, 1, 5, 0, 471, 1, "のんびりしたら、\nまた勉強しよっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 3, 0, 1, 6, 0, 472, 1, "運動すると勉強も\nはかどるよね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 3, 0, 1, 7, 0, 473, 1, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101101, 3, 0, 1, 8, 0, 474, 1, "お兄ちゃんの記憶を\n戻す方法、どこかに\n書いてないかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 1, 0, 1, 1, 0, 476, 1, "ケーキの食べ過ぎで\nお腹が…うぅ、\n因果横暴だよー…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 1, 0, 1, 2, 0, 477, 1, "１、２、３、４…\nあれ？　いま、\n何段目まで\n編んだっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 1, 0, 1, 3, 0, 478, 1, "い、いけない！\n縫い針落としちゃった");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 1, 0, 1, 4, 0, 479, 1, "笑う時には服着ます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 1, 0, 1, 5, 0, 480, 1, "「どうだ！妹暮らし」\nってことわざ\nなかったっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 1, 0, 8, 101, 0, 481, 1, "お兄ちゃんに、\nお洋服でも\n作ろうかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 1, 0, 8, 102, 0, 482, 1, "お兄ちゃんが帰って\nきたら、何して遊ぼう\nかな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 2, 0, 1, 1, 0, 483, 1, "勉強、見てくれるん\nですか？　やった～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 2, 0, 1, 2, 0, 484, 1, "クリームついてた！？\nさっき食べた、\nケーキかも…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 2, 0, 1, 3, 0, 485, 1, "おにいちゃん！\nそろそろ、おやつに\nしませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 2, 0, 1, 4, 0, 486, 1, "勉強は…\n明日にしようっと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 2, 0, 8, 101, 0, 487, 1, "妹だけど、大人な\nレディですからね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 2, 0, 8, 102, 0, 488, 1, "な、ナデナデとか\n恥ずかしいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 2, 0, 1, 1, 1, 489, 1, "今日も１日\n勉強がんばります！\n…少しは遊ぼうかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 2, 0, 1, 2, 2, 490, 1, "お昼休憩に\n甘いものが\n食べたいです～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 2, 0, 1, 3, 3, 491, 1, "お兄ちゃんは\n夜強いんですね\n私は眠くて…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 3, 0, 1, 1, 0, 492, 1, "服装や髪形が変わると\n気分も変わるよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 3, 0, 1, 2, 0, 493, 1, "ここで寝ちゃったら\n受験勉強が～…\nぐーー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 3, 0, 1, 3, 0, 494, 1, "かわいい♪\n私が服を作って\nあげよっか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 3, 0, 1, 4, 0, 495, 1, "いまならどんな試験も\n合格できそうです！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 3, 0, 1, 5, 0, 496, 1, "のんびりしたら\nまた勉強しよっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 3, 0, 1, 6, 0, 497, 1, "お兄ちゃんを\n守るためには\n毎日、鍛えなくちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 3, 0, 1, 7, 0, 498, 1, "おいし～！\nお兄ちゃんにも\nおすそ上げしますね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101102, 3, 0, 1, 8, 0, 499, 1, "この本の服\nかわいいなぁ♪\n私に作れるかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 1, 0, 1, 1, 0, 501, 1, "う～ん、今なら\nどこでだって\n眠れちゃいそう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 1, 0, 1, 2, 0, 502, 1, "たくさん寝すぎて、\n夢の中か現実か\nわかんなく\nなっちゃうんだよね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 1, 0, 1, 3, 0, 503, 1, "…はっ！？\nね、寝てないよ？\nホントだよっ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 1, 0, 1, 4, 0, 504, 1, "ぐっすり眠って\nパワーを\nフルチャージっ☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 1, 0, 1, 5, 0, 505, 1, "もし、超能力のことが\nバレたら…ブルブル");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 1, 0, 4, 101, 0, 506, 1, "テレパシーで\nキミの心を覗けたら\n…なんてね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 1, 0, 8, 102, 0, 507, 1, "キミを抱き枕に…\nじょ、冗談冗談！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 2, 0, 1, 1, 0, 508, 1, "ん～、なになに？\n一緒にお昼寝する？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 2, 0, 1, 2, 0, 509, 1, "忘れ物しちゃった。\nま、いっか！\nえ、ダメ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 2, 0, 1, 3, 0, 510, 1, "おおっ、相談ごと？\nなにかな\nなにかな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 2, 0, 1, 4, 0, 511, 1, "う～ん、もしかして\nキミにも仲良しになる\n超能力があるのかも！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 2, 0, 4, 101, 0, 512, 1, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 2, 0, 8, 102, 0, 513, 1, "えっ？　私とキミとの\n相性？　むむ～…\nうん、バッチリだよ☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 2, 0, 1, 1, 1, 514, 1, "まだこんな時間？\nベッドに行って、\n二度寝しなくちゃ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 2, 0, 1, 2, 2, 515, 1, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 2, 0, 1, 3, 3, 516, 1, "どんなにお昼寝しても\n夜は眠いんだ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 3, 0, 1, 1, 0, 517, 1, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 3, 0, 1, 2, 0, 518, 1, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 3, 0, 1, 3, 0, 519, 1, "これ…可愛い！\nシオリンに\n届けてあげたい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 3, 0, 1, 4, 0, 520, 1, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 3, 0, 1, 5, 0, 521, 1, "気持ちいい…\nはっ！　ダメダメ！\nまた寝るとこだった！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 3, 0, 1, 6, 0, 522, 1, "眠くならないように、\n運動しよっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 3, 0, 1, 7, 0, 523, 1, "お腹いっぱいになると\n眠くなるよね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101201, 3, 0, 1, 8, 0, 524, 1, "睡眠学習って\n本を読みながら\n寝るんだっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 1, 0, 1, 1, 0, 526, 1, "う～ん、今なら\nどこでだって\n眠れちゃいそう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 1, 0, 1, 2, 0, 527, 1, "もっといっぱい\n超能力使えたら\nいいのにな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 1, 0, 1, 3, 0, 528, 1, "ねえねえ、\n今から人助け、\n付き合ってほしいなー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 1, 0, 1, 4, 0, 529, 1, "ぐっすり眠って\nパワーを\nフルチャージっ☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 1, 0, 1, 5, 0, 530, 1, "人助け、頑張ったら\nなんだか、眠く…\n…はっ、ダメダメ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 1, 0, 8, 101, 0, 531, 1, "キミと一緒なら、\nなんだって出来る！\n間違いない！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 1, 0, 8, 102, 0, 532, 1, "キミを抱き枕に…\nじょ、冗談冗談！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 2, 0, 1, 1, 0, 533, 1, "忘れ物しちゃった\nま、いっか！\nえ、ダメ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 2, 0, 1, 2, 0, 534, 1, "なに？　困ってる？\nちょうど良かった！\n今は体力満タンだよ☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 2, 0, 1, 3, 0, 535, 1, "一緒に\nお出かけしたいなー\nデートしよっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 2, 0, 1, 4, 0, 536, 1, "うんうん、そうだねっ\n困ってる人がいたら\n見過ごせないよねっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 2, 0, 8, 101, 0, 537, 1, "むむ～…どう？\nテレパシーで、私の\n気持ちを送ったよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 2, 0, 8, 102, 0, 538, 1, "う～だんだん眠く…\nねぇ一緒に寝よ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 2, 0, 1, 1, 1, 539, 1, "キミって早起きだね！\n私は寝ようかな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 2, 0, 1, 2, 2, 540, 1, "適度なお昼寝は\n健康にいいんだって！\n８時間くらいかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 2, 0, 1, 3, 3, 541, 1, "どんなにお昼寝しても\n夜は眠いんだ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 3, 0, 1, 1, 0, 542, 1, "ん～、また寝癖…\nいっつも\n寝てるからかなぁ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 3, 0, 1, 2, 0, 543, 1, "一休み、一休み…\nどうせなら百休み\nくらいしようかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 3, 0, 1, 3, 0, 544, 1, "これ…可愛い！\nシオリンに\n届けてあげたい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 3, 0, 1, 4, 0, 545, 1, "ん～…気分爽快！\nそれじゃ、おやすみ～\n…すやぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 3, 0, 1, 5, 0, 546, 1, "うう～ん、なんだか\nいつもより眠ーく\nなってきたよ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 3, 0, 1, 6, 0, 547, 1, "よーし\n困ってる人のために\nパワーアップ☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 3, 0, 1, 7, 0, 548, 1, "食べてすぐ寝ると\n牛になるんだっけ？\nまぁ、いいかぁ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101202, 3, 0, 1, 8, 0, 549, 1, "睡眠学習って\n本を読みながら\n寝るんだっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 1, 0, 1, 1, 0, 551, 1, "モデルは楽しいよ！\nファンの子みんな\n応援してくれるしー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 1, 0, 1, 2, 0, 552, 1, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 1, 0, 1, 3, 0, 553, 1, "んー、今日のおやつは\n何にしよう？\nやっぱアメ玉かな！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 1, 0, 1, 4, 0, 554, 1, "ここで撮影会、\nしようかな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 1, 0, 1, 5, 0, 555, 1, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 1, 0, 4, 101, 0, 556, 1, "やば…\nこのままじゃ、うち\nキュン死にかも…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 1, 0, 8, 102, 0, 557, 1, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 2, 0, 1, 1, 0, 558, 1, "うちの写真撮るの？\nどんなポーズ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 2, 0, 1, 2, 0, 559, 1, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイにしてあげる");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 2, 0, 1, 3, 0, 560, 1, "宿題見てくんない？\nひとりじゃムリ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 2, 0, 1, 4, 0, 561, 1, "みてこのネイル♪\n超エモくな～い？\nヒデサイもする～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 2, 0, 4, 101, 0, 562, 1, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 2, 0, 8, 102, 0, 563, 1, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 2, 0, 1, 1, 1, 564, 1, "早起きとか\nつらたんだよねー\n昼すぎまで寝てたい");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 2, 0, 1, 2, 2, 565, 1, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 2, 0, 1, 3, 3, 566, 1, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 3, 0, 1, 1, 0, 567, 1, "どんなシーンにも合う\nきれいめわがまま\nちゃんとコーデだよ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 3, 0, 1, 2, 0, 568, 1, "ちょっち疲れたかも？\nガン寝しちゃおっかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 3, 0, 1, 3, 0, 569, 1, "きゃわたーん☆\n君もおしゃれしよっか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 3, 0, 1, 4, 0, 570, 1, "いいにおいじゃん！\nこれと同じにおいの\n香水ないかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 3, 0, 1, 5, 0, 571, 1, "あったまるー\nこれ、サイツヨに\n癒しじゃない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 3, 0, 1, 6, 0, 572, 1, "あはは、なんか\nウォーキングの練習\nしてるみたーい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 3, 0, 1, 7, 0, 573, 1, "体形維持しないと\nいけないけど\nついつい食べちゃう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101601, 3, 0, 1, 8, 0, 574, 1, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 1, 0, 1, 1, 0, 576, 1, "そろそろ\n仕事の時間だ～\n出かける準備\nしよっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 1, 0, 1, 2, 0, 577, 1, "今日のおやつは\n駄菓子に決定～！\nいただきまーす！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 1, 0, 1, 3, 0, 578, 1, "ヒデサイを見習って\nうちも少し勉強して\nみよっかな…　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 1, 0, 1, 4, 0, 579, 1, "体形維持とか\n気にしてるんだよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 1, 0, 1, 5, 0, 580, 1, "７かける８はぁ\n７＋７＋７＋７＋…\nん、何個数えたっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 1, 0, 8, 101, 0, 581, 1, "やば…\nこのままじゃ、うち\nキュン死にかも…　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 1, 0, 8, 102, 0, 582, 1, "ヒデサイと会ってると\n顔熱くなるんだよね\n熱なんかないのに…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 2, 0, 1, 1, 0, 583, 1, "うちの写真撮るの？\nどんなポーズ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 2, 0, 1, 2, 0, 584, 1, "ネイルの勉強中～♪\nうまくなったら、\nヒデサイに\nしてあげる♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 2, 0, 1, 3, 0, 585, 1, "この雑誌の服\nガチめにほしぃ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 2, 0, 1, 4, 0, 586, 1, "うちのコーデで\nヒデサイをオシャンに\nしてあげるね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 2, 0, 8, 101, 0, 587, 1, "新しい美容液で\n肌まじスベスベ～\nヒデサイも\n触ってみる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 2, 0, 8, 102, 0, 588, 1, "好きなことして\nヒデサイもいて\nうちって超～\nリア充だよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 2, 0, 1, 1, 1, 589, 1, "今日は早起き\nできたんだー\nメイクもばっち\nきまってるっしょ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 2, 0, 1, 2, 2, 590, 1, "撮影にむけて\nダイエットちうだから\nお昼ぬいてるんだー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 2, 0, 1, 3, 3, 591, 1, "お腹すいたー\nでもこの時間に\n食べたら絶対に\n太るやつだよねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 3, 0, 1, 1, 0, 592, 1, "この服ね～\n今年のトレンドなんだ♪\nかわたんでしょ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 3, 0, 1, 2, 0, 593, 1, "ちょっち疲れたかも？\nガン寝しちゃおっかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 3, 0, 1, 3, 0, 594, 1, "うわー\nこのぬいぐるみ\nもきゅすぎでしょ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 3, 0, 1, 4, 0, 595, 1, "いいにおーい♪\nファンのみんなにも\n教えてあげよー♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 3, 0, 1, 5, 0, 596, 1, "あったまるー\nこれ、サイツヨに\n癒しじゃない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 3, 0, 1, 6, 0, 597, 1, "食べた分は\n消費しないとね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 3, 0, 1, 7, 0, 598, 1, "体形維持しないと\nいけないけど\nついつい食べちゃう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101602, 3, 0, 1, 8, 0, 599, 1, "全然読めない～\nそだ、ヒデサイに\n読んでもらおっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 1, 0, 1, 1, 0, 601, 1, "やっさー\n踊ってると、どんどん\n楽しくなるさー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 1, 0, 1, 2, 0, 602, 1, "このお菓子、でーじ\nおいしいねー！\nキミも食べる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 1, 0, 1, 3, 0, 603, 1, "ハイターイ！　元気な\nあいさつが一番さ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 1, 0, 1, 4, 0, 604, 1, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 1, 0, 1, 5, 0, 605, 1, "さぁ、みんなで歌って\n踊るさ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 1, 0, 4, 101, 0, 606, 1, "毎日、最高に幸せさ～\nキミのおかげだねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 1, 0, 8, 102, 0, 607, 1, "キミへの気持ちを\n踊りで表現\nしてみるさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 2, 0, 1, 1, 0, 608, 1, "海でひと泳ぎ\nしたいさ～\nキミもどう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 2, 0, 1, 2, 0, 609, 1, "あぎひゃっ！\n急に声かけたら、\nビックリするさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 2, 0, 1, 3, 0, 610, 1, "踊りなら、\n私が手とり足とり\n教えてあげるさ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 2, 0, 1, 4, 0, 611, 1, "空手の型の\n練習するさー\nキミもどう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 2, 0, 4, 101, 0, 612, 1, "よ～し、手をつないで\n踊るさ～！\n恥ずかしがること\nないさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 2, 0, 8, 102, 0, 613, 1, "キミと一緒だから、\n毎日楽しいこと\nばっかりさー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 2, 0, 1, 1, 1, 614, 1, "ハイターイ！\n今日も一日、\nがんばるさー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 2, 0, 1, 2, 2, 615, 1, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 2, 0, 1, 3, 3, 616, 1, "故郷では見えない星が\n見られるんだねえ。\nなんか不思議さぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 3, 0, 1, 1, 0, 617, 1, "夢中で踊ってたら、\n髪の毛ぼさぼさに\nなってるさ～！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 3, 0, 1, 2, 0, 618, 1, "よく食べてよく踊って\nよく休む。\nそれが元気の秘訣さ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 3, 0, 1, 3, 0, 619, 1, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 3, 0, 1, 4, 0, 620, 1, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 3, 0, 1, 5, 0, 621, 1, "故郷はあんまり暖房を\n使わなかったから、\n新鮮さー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 3, 0, 1, 6, 0, 622, 1, "汗をかくのは\n気持ちいいさー！\nどんどん行くよー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 3, 0, 1, 7, 0, 623, 1, "うーん、おいしいさ～\nゴーヤっぽいものを\n混ぜたいさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101701, 3, 0, 1, 8, 0, 624, 1, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 1, 0, 1, 1, 0, 626, 1, "焦らなくても\nなんくるないさ～\nなるようになるさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 1, 0, 1, 2, 0, 627, 1, "さぁ、みんなで歌って\n踊るさ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 1, 0, 1, 3, 0, 628, 1, "元気がないときは\n私のところに来るさー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 1, 0, 1, 4, 0, 629, 1, "あまった食材があれば\nチャンプルーにして\n食べたいさー！ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 1, 0, 1, 5, 0, 630, 1, "ノリノリな音楽と\n踊りで、疲れを\n吹き飛ばすさ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 1, 0, 8, 101, 0, 631, 1, "毎日、最高に幸せさ～\nキミのおかげだねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 1, 0, 8, 102, 0, 632, 1, "幸せすぎて踊らずには\nいられないさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 2, 0, 1, 1, 0, 633, 1, "海でひと泳ぎ\nしたいさ～\nキミもどう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 2, 0, 1, 2, 0, 634, 1, "ん？　どうしたの～？\nヒマなら一緒に\n踊ろうさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 2, 0, 1, 3, 0, 635, 1, "キミと一緒だから\n毎日楽しいこと\nばっかりさー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 2, 0, 1, 4, 0, 636, 1, "ハイターイ！　元気な\nあいさつが一番さ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 2, 0, 8, 101, 0, 637, 1, "キミと踊ってる時が\n一番幸せさ～\nキミもそう思ってたら\nうれしいさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 2, 0, 8, 102, 0, 638, 1, "海に沈む夕日は\nとっても綺麗さ～！\n今度一緒に見ようね～ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 2, 0, 1, 1, 1, 639, 1, "気持ちのいい朝さー！\nひとっ走り\nしてくるさー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 2, 0, 1, 2, 2, 640, 1, "午後も頑張るために\nお昼ご飯は\nてびちにするさー！ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 2, 0, 1, 3, 3, 641, 1, "故郷では見えない星が\n見られるんだねえ\nなんか不思議さぁ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 3, 0, 1, 1, 0, 642, 1, "夢中で踊ってたら\n髪の毛ぼさぼさに\nなってるさ～！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 3, 0, 1, 2, 0, 643, 1, "よく食べてよく踊って\nよく休む\nそれが元気の秘訣さ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 3, 0, 1, 3, 0, 644, 1, "ちゅらかーぎーねー！\nキミはどこから\n来たのー？ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 3, 0, 1, 4, 0, 645, 1, "いい香り～、って\n私もカオリだったさ！\nなんだか照れるね☆ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 3, 0, 1, 5, 0, 646, 1, "故郷はあんまり暖房を\n使わなかったから\n新鮮さー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 3, 0, 1, 6, 0, 647, 1, "徹夜で踊り続ける\nためにも、まずは\n体を鍛えるさ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 3, 0, 1, 7, 0, 648, 1, "もぐもぐ…もっと\nおかわりがほしいさー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101702, 3, 0, 1, 8, 0, 649, 1, "おおー、故郷の民話！\nこんなとこで見るとは\n懐かしいさぁ～！ ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 1, 0, 1, 1, 0, 651, 1, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 1, 0, 1, 2, 0, 652, 1, "このお部屋\n女の子ばかりね。\n魔族の女子校を\n思い出しちゃう♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 1, 0, 1, 3, 0, 653, 1, "次のテスト\n恋愛小説から\n出題したら\nダメかしら…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 1, 0, 1, 4, 0, 654, 1, "キンコンカンコ～ン♪\n授業をはじめま～す");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 1, 0, 1, 5, 0, 655, 1, "はー…\nちょっと肩が\nこっちゃった…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 1, 0, 4, 101, 0, 656, 1, "私、あの子のこと\n無意識に特別扱い\nしちゃってるかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 1, 0, 8, 102, 0, 657, 1, "物語だと生徒が先生を\n好きになるのにな…\n逆の場合は…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 2, 0, 1, 1, 0, 658, 1, "あん！　見ちゃダメよ\n明日のテストの問題を\n作ってるとこなの！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 2, 0, 1, 2, 0, 659, 1, "勉強勉強で\n疲れたよね？\n膝枕してあげる！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 2, 0, 1, 3, 0, 660, 1, "テストがんばったね～\nご褒美に先生が\n特別な授業\nしてあげるね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 2, 0, 1, 4, 0, 661, 1, "学院の外でも\n私は君の先生よ\nなんでも聞いてね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 2, 0, 4, 101, 0, 662, 1, "今読んでるのは\nヒロインが年下の子と\n恋に落ちる話…あ、\n深い意味はないのよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 2, 0, 8, 102, 0, 663, 1, "生徒は全員可愛いけど\n君は少し特別…\nみんなには内緒よ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 2, 0, 1, 1, 1, 664, 1, "学校に\n遅刻しないように\n早く準備しないとね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 2, 0, 1, 2, 2, 665, 1, "男の子といっしょに\nお弁当を食べるの、\n夢なんだ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 2, 0, 1, 3, 3, 666, 1, "明日の予習はやった？\n先生も手伝うから\n寝る前にやりましょ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 3, 0, 1, 1, 0, 667, 1, "もしかして…\n少し露出多いかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101801, 3, 0, 1, 2, 0, 668, 1, "リラックスすると\nα波が出て、勉強が\nはかどるんですって");
