INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101802, 1, 0, 1, 1, 0, 676, 1, "私の生徒に\n不良なんていないわ\nみーんないい子♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101802, 1, 0, 1, 2, 0, 677, 1, "このお部屋\n女の子ばかりね\n魔族の女子校を\n思い出しちゃう♪");
