INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (101802, 1, 0, 1, 4, 0, 679, 1, "はあ…\nすてきな小説だった～\nキュンキュンしちゃう");
