INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102002, 3, 0, 1, 5, 0, 746, 1, "あったか～♪\nぬくぬく～♪\nポカポカ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102002, 3, 0, 1, 6, 0, 747, 1, "はぁはぁ…\nふえ～ミミ\n疲れちゃうよ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102002, 3, 0, 1, 7, 0, 748, 1, "はむ♪　いっぱい\n食べて、はやく\nおっきくなりたいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102002, 3, 0, 1, 8, 0, 749, 1, "ミミね！\nこのお話だーい好き！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 1, 0, 1, 1, 0, 751, 1, "はぅぅ…\n部屋のどこにいたら\nいいんだろう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 1, 0, 1, 2, 0, 752, 1, "お外より\nお部屋の中の方が\n落ち着くな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 1, 0, 1, 3, 0, 753, 1, "クッキー焼いたら\nみんな食べてくれる\nかなぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 1, 0, 1, 4, 0, 754, 1, "あ、あめんぼあかいな\nあいうえお！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 1, 0, 1, 5, 0, 755, 1, "お芝居には\n人間観察が大事…\nじーーーーーー…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 1, 0, 4, 101, 0, 756, 1, "お兄ちゃんを見ると、\n顔が赤くなっちゃうの\n…な、なんでかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 1, 0, 8, 102, 0, 757, 1, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 2, 0, 1, 1, 0, 758, 1, "こ、この本、とっても\nおもしろいんですよ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 2, 0, 1, 2, 0, 759, 1, "い、一緒に遊んで\nくれるんですか？\nや、やったぁ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 2, 0, 1, 3, 0, 760, 1, "ランドソルには\n慣れましたか？\nわ、私はまだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 2, 0, 1, 4, 0, 761, 1, "お、お兄ちゃん、\nお、お話の練習に\n付き合って欲しいです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 2, 0, 4, 101, 0, 762, 1, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 2, 0, 8, 102, 0, 763, 1, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 2, 0, 1, 1, 1, 764, 1, "今日は一日、\n恥ずかしがらずに\n過ごせるかなあ…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 2, 0, 1, 2, 2, 765, 1, "お昼ご飯、どうぞ♪\nわ、私、お料理、\nうまくなったかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 2, 0, 1, 3, 3, 766, 1, "おにいちゃんが\n帰ってくるまで、\nがんばって\n起きてました");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 3, 0, 1, 1, 0, 767, 1, "可愛い女の子に変身！\n出来ますように…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 3, 0, 1, 2, 0, 768, 1, "ふかふかで\nあったかくて…\nお兄ちゃんみたい♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 3, 0, 1, 3, 0, 769, 1, "この子を相手に、\n演技の練習～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 3, 0, 1, 4, 0, 770, 1, "リボンや服に\n染み込ませておこう。\nいつでもいい匂い♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 3, 0, 1, 5, 0, 771, 1, "えへへ。\nぽっかぽかです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 3, 0, 1, 6, 0, 772, 1, "はぁはぁ…\nふひぃ…\nはうぅぅぅ～～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 3, 0, 1, 7, 0, 773, 1, "もぐもぐ…えへへ、\n美味しいものを\n食べてると幸せです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102101, 3, 0, 1, 8, 0, 774, 1, "戯曲を読んで\n自分が演じるところを\n想像するの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 1, 0, 1, 1, 0, 776, 1, "かわいいものを\nこうして並べて…\nはぁ…落ちつくぅ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 1, 0, 1, 2, 0, 777, 1, "ふぁ～、この部屋は\n時間がゆったりと\n流れてるみたい");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 1, 0, 1, 3, 0, 778, 1, "この台本、感動…\n涙が止まらないよぉ\nいつか演じてみたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 1, 0, 1, 4, 0, 779, 1, "お芝居には\n人間観察が大事…\nじーーーーーー…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 1, 0, 1, 5, 0, 780, 1, "あえいうえおあお\nかけきくけ、かっ…\nむ、難しいですぅぅ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 1, 0, 8, 101, 0, 781, 1, "お、お腹が空いた時は\n言ってくださいね\nが、頑張って\n作ります！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 1, 0, 8, 102, 0, 782, 1, "れ、恋愛劇…\nお、お兄ちゃんと\n練習…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 2, 0, 1, 1, 0, 783, 1, "こ、この本、とっても\nおもしろいんですよ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 2, 0, 1, 2, 0, 784, 1, "お兄ちゃんと\nいっぱい話せた日は、\n嬉しくなっちゃいます");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 2, 0, 1, 3, 0, 785, 1, "何か私にできる事\n…そうだ！\nお掃除しますね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 2, 0, 1, 4, 0, 786, 1, "私、ちょっとずつ、\n自分に自信が\nついてきたかもです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 2, 0, 8, 101, 0, 787, 1, "リボンがかわいい？\nほ、ほぇぇぇ～～\nは、恥ずかしいですぅ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 2, 0, 8, 102, 0, 788, 1, "お兄ちゃん…♪\nえへへ、呼んでみた\nだけです…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 2, 0, 1, 1, 1, 789, 1, "おはようございます♪\n声出しして、お芝居の\n練習しなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 2, 0, 1, 2, 2, 790, 1, "お茶にしますか？\nクッキー焼いたので、\nよかったら…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 2, 0, 1, 3, 3, 791, 1, "寝る前に\n台本を読むと、\n覚えやすいんです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 3, 0, 1, 1, 0, 792, 1, "可愛い女の子に変身！\n出来ますように…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 3, 0, 1, 2, 0, 793, 1, "はぅ…\nフッカフカァ…\n癒されちゃいます…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 3, 0, 1, 3, 0, 794, 1, "この子を相手に\n演技の練習～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 3, 0, 1, 4, 0, 795, 1, "リボンや服に\n染み込ませておこう\nいつでもいい匂い♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 3, 0, 1, 5, 0, 796, 1, "えへへ\nぽっかぽかです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 3, 0, 1, 6, 0, 797, 1, "え、演劇には\nた、体力も重要\nだから～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 3, 0, 1, 7, 0, 798, 1, "残さず\n食べますね…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102102, 3, 0, 1, 8, 0, 799, 1, "戯曲を読んで\n自分が演じるところを\n想像するの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 1, 0, 1, 1, 0, 801, 1, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 1, 0, 1, 2, 0, 802, 1, "アカリみたいに\nかわいくなるには、\nどうすればいいのかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 1, 0, 1, 3, 0, 803, 1, "レアカードをゲット！\nねえねえ、\nすごいでしょ！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 1, 0, 1, 4, 0, 804, 1, "ア、アイツと同じ\n部屋にいるのよね…\nって、だから何よ！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 1, 0, 1, 5, 0, 805, 1, "楽しいゲームって\nやめどきが\n見つからないわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 1, 0, 4, 101, 0, 806, 1, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 1, 0, 8, 102, 0, 807, 1, "うぅ…最近、\nアイツを見るとなんか\nドキドキするのよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 2, 0, 1, 1, 0, 808, 1, "ゲームに負けた罰\nだからって、\nくすぐるのは…\nあはは…ダメだって！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 2, 0, 1, 2, 0, 809, 1, "アンタと話すのは割と\n緊張しないのよね\n…なんでかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 2, 0, 1, 3, 0, 810, 1, "待ちきれない…\n新作ゲームの発売日が\n待ちきれない…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 2, 0, 1, 4, 0, 811, 1, "アンタは人見知り\nしないタチよね？\nいいなあ～…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 2, 0, 4, 101, 0, 812, 1, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 2, 0, 8, 102, 0, 813, 1, "アンタと…そ、その\nこうして話してるのも\nわわ、悪くないわね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 2, 0, 1, 1, 1, 814, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 2, 0, 1, 2, 2, 815, 1, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 2, 0, 1, 3, 3, 816, 1, "夜はこれからよ！\n朝まで\nゲームするからね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 3, 0, 1, 1, 0, 817, 1, "アカリみたいに\n可愛くなるのは\n難しいかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 3, 0, 1, 2, 0, 818, 1, "寝っ転がりながら\nゲームができれば\n気分が良さそう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 3, 0, 1, 3, 0, 819, 1, "可愛い…けど、\n私には似合わないかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 3, 0, 1, 4, 0, 820, 1, "はあ、リラックスして\nゲームができるわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 3, 0, 1, 5, 0, 821, 1, "ん…暖かくて、\n寝落ちしちゃいそう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 3, 0, 1, 6, 0, 822, 1, "ゲーム感覚でやると、\n体動かすのも\n楽しいわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 3, 0, 1, 7, 0, 823, 1, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102201, 3, 0, 1, 8, 0, 824, 1, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 1, 0, 1, 1, 0, 826, 1, "さあ、今日は\n何のゲームで\n遊ぼうかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 1, 0, 1, 2, 0, 827, 1, "一緒にゲームができる\n人がいるっていうのも\n新鮮だな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 1, 0, 1, 3, 0, 828, 1, "レアカードをゲット！\nすごいでしょ！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 1, 0, 1, 4, 0, 829, 1, "アカリってどうやって\n友達作ってるんだろ？\nコツがあるのかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 1, 0, 1, 5, 0, 830, 1, "楽しいゲームって\nやめどきが\n見つからないわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 1, 0, 8, 101, 0, 831, 1, "うぅ…最近\nアイツを見るとなんか\nドキドキするのよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 1, 0, 8, 102, 0, 832, 1, "ね、ねえ\nもう少しその…\n一緒にゲームしない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 2, 0, 1, 1, 0, 833, 1, "ねえ、人見知りを\n治すための練習に\n付き合ってくれない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 2, 0, 1, 2, 0, 834, 1, "欲しいゲームが\n見つからなくて…\n探すの手伝って！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 2, 0, 1, 3, 0, 835, 1, "ねえ、見て！\n新しいゲームを\n買って来たの！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 2, 0, 1, 4, 0, 836, 1, "ゲームに負けた罰\nだからって、くすぐる\nのは…あ、あはは！\nだ、ダメだって！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 2, 0, 8, 101, 0, 837, 1, "ア、アンタってさ\n好きな人とかい…\nなな、何でもない！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 2, 0, 8, 102, 0, 838, 1, "あ、あのさ…\nもう少し、そこに\nいてくれてもいいわよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 2, 0, 1, 1, 1, 839, 1, "おおおおはよう！\n…よし！　ちゃんと\nあいさつできたわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 2, 0, 1, 2, 2, 840, 1, "一緒にごはんとか…\nううん、やっぱいい！\nなんでもないからっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 2, 0, 1, 3, 3, 841, 1, "夜はこれからよ！\n朝まで\nゲームするからね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 3, 0, 1, 1, 0, 842, 1, "もっと女の子らしく\nなったらアイツも\n嬉しいかな…って、\n何考えてるの！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 3, 0, 1, 2, 0, 843, 1, "ふああ…\n徹夜明けには\n辛い誘惑だな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 3, 0, 1, 3, 0, 844, 1, "可愛い…けど、\n私には似合わないかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 3, 0, 1, 4, 0, 845, 1, "こういうのに詳しいと\n女の子っぽく\n見えたりするのかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 3, 0, 1, 5, 0, 846, 1, "寒い日は\nここでゲームするのが\n最高のぜいたくよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 3, 0, 1, 6, 0, 847, 1, "最近ゲームばかりだし\nたまにはいいかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 3, 0, 1, 7, 0, 848, 1, "ゲームは頭使うから\nそのぶん\n栄養がいるのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102202, 3, 0, 1, 8, 0, 849, 1, "ふむふむ、なるほど\n友だちを作るには\n笑顔が大事っと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 1, 0, 1, 1, 0, 851, 1, "じゃんけんぽん！\nあっち向いてほい！\nあ～ん、負けたぁ\n　『勝ったぜ！』");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 1, 0, 1, 2, 0, 852, 1, "ぷうきちがいれば、\nなんでも出来ちゃうよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 1, 0, 1, 3, 0, 853, 1, "発声練習しよっと\nぷうきち\nフルスイ～ング！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 1, 0, 1, 4, 0, 854, 1, "ここで、ぷうきち\nフルスイングは、\nまずいかな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 1, 0, 1, 5, 0, 855, 1, "この部屋、ぷうきちを\n置く場所はないのかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 1, 0, 4, 101, 0, 856, 1, "お兄ちゃんって…\nちょっとぷうきちに\n似てるかも♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 1, 0, 8, 102, 0, 857, 1, "お兄ちゃんにも、\nぷうきちのこと\n好きになって\nもらいたいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 2, 0, 1, 1, 0, 858, 1, "おやつは、\n私とぷうきちの\n二人分用意してね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 2, 0, 1, 2, 0, 859, 1, "ぷうきちが、お腹\nペコペコだって～\n何かちょうだい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 2, 0, 1, 3, 0, 860, 1, "『アヤネに手出したら\n…坊ちゃんでも、\n分かってんな？』");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 2, 0, 1, 4, 0, 861, 1, "ぷうきちとの\n連携を磨いてるの！\nもっと仲良くなるよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 2, 0, 4, 101, 0, 862, 1, "特別だからね？\nもっと触っていいよ\n…ぷうきちのこと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 2, 0, 8, 102, 0, 863, 1, "お兄ちゃんのこと、\n「ぷう兄」って\n呼ぼうかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 2, 0, 1, 1, 1, 864, 1, "お兄ちゃんって\n朝早いんだね～\n私はまだ眠いよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 2, 0, 1, 2, 2, 865, 1, "ふぁ～…ぷうきちと\nお昼寝してくるね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 2, 0, 1, 3, 3, 866, 1, "ぷうきちと\nおしゃべりしてたら\nもうこんな時間…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 3, 0, 1, 2, 0, 868, 1, "『座り心地がいいな』\nぷうきちは座れない\nでしょ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 3, 0, 1, 3, 0, 869, 1, "かわいい～！\nぷうきちのお友達に\nしちゃおうかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 3, 0, 1, 4, 0, 870, 1, "いいニオイ～♪\nぷうきちも\n気に入ったみたい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 3, 0, 1, 5, 0, 871, 1, "ぽわぽわする～\nぷうきちも\n気持ちいい？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 3, 0, 1, 7, 0, 873, 1, "おいしい～\nぷうきちも\n食べてみる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102301, 3, 0, 1, 8, 0, 874, 1, "ぷうきち！\nこの本、読んで～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 1, 0, 1, 1, 0, 901, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 1, 0, 1, 2, 0, 902, 1, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 1, 0, 1, 3, 0, 903, 1, "どうしたら転ばなく\nなるんでしょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 1, 0, 1, 4, 0, 904, 1, "わわわわ～～っ\nふう…\nきゃ、きゃあっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 1, 0, 1, 5, 0, 905, 1, "私のドジで、\nこのお部屋を\n壊しちゃったら\nどうしましょう～～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 1, 0, 4, 101, 0, 906, 1, "おいしい料理で\nお出迎えしなくちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 1, 0, 8, 102, 0, 907, 1, "一生懸命\nご奉仕しますよ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 2, 0, 1, 1, 0, 908, 1, "耳かきしましょうか？\nえ？　こわい？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 2, 0, 1, 2, 0, 909, 1, "お疲れでしたら、\nお昼寝しませんか？\n膝枕もしますよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 2, 0, 1, 3, 0, 910, 1, "おかえりなさいませ\nご主人さま♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 2, 0, 1, 4, 0, 911, 1, "すみません\nすみません\nすみません～～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 2, 0, 4, 101, 0, 912, 1, "メイドの私が、\nこんな気持ちを持って\nいいんでしょうか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 2, 0, 8, 102, 0, 913, 1, "私のこと、\n専属メイドと思って\n頼ってくださいね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 2, 0, 1, 1, 1, 914, 1, "おはようございます♪\n今日も一日がんば…\nきゃ、きゃあ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 2, 0, 1, 2, 2, 915, 1, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 2, 0, 1, 3, 3, 916, 1, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 3, 0, 1, 1, 0, 917, 1, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 3, 0, 1, 2, 0, 918, 1, "休憩が終わったら\nお洗濯にお料理に\nお掃除もしないと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 3, 0, 1, 3, 0, 919, 1, "かわいい♪\nあとでお洗濯\nしてあげますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 3, 0, 1, 4, 0, 920, 1, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 3, 0, 1, 5, 0, 921, 1, "ここでお洗濯ものを\n乾かしましょうか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 3, 0, 1, 6, 0, 922, 1, "あわわわっ…\nこ、転びそうです～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 3, 0, 1, 7, 0, 923, 1, "これくらい\nおいしいものを\n作ってみたいです～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102501, 3, 0, 1, 8, 0, 924, 1, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 1, 0, 1, 1, 0, 926, 1, "失敗したら、この\nお部屋追い出され\nちゃうかも～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 1, 0, 1, 2, 0, 927, 1, "あれ？　あれ？\nお部屋の中で迷子に\nなっちゃいました～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 1, 0, 1, 3, 0, 928, 1, "どうやったら、立派な\nメイドになれるのかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 1, 0, 1, 4, 0, 929, 1, "塩にはＳマーク\n砂糖にはＳマーク\n…あ、あれ！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 1, 0, 1, 5, 0, 930, 1, "ご用があれば\nなんでも言って\nくださいね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 1, 0, 8, 101, 0, 931, 1, "迷惑をかけすぎて…\n嫌われちゃったら\nどうしましょう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 1, 0, 8, 102, 0, 932, 1, "メイドの私が\nこんな気持ちを持って\nいいんでしょうか…　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 2, 0, 1, 1, 0, 933, 1, "おでかけですか？\nいってらっしゃいませ\nご主人さま♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 2, 0, 1, 2, 0, 934, 1, "どうして失敗ばっかり\nこうなったら\n腹を切るしか～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 2, 0, 1, 3, 0, 935, 1, "私、またなにか\nやっちゃいましたか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 2, 0, 1, 4, 0, 936, 1, "おいしいお茶っ葉が\n手に入りました～♪\n一緒に飲みませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 2, 0, 8, 101, 0, 937, 1, "転んだふりであの人に\n…って！\nなんでもないですよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 2, 0, 8, 102, 0, 938, 1, "私、できることなら\nなんでもやりますよ\nあなたのためですから");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 2, 0, 1, 1, 1, 939, 1, "おはようございます♪\n今日も一日がんば…\nう、うわあ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 2, 0, 1, 2, 2, 940, 1, "お昼ごはん…\n失敗しちゃいましたぁ\nお、おやつでリベンジします！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 2, 0, 1, 3, 3, 941, 1, "明日の目標は\nドジをしないです！\nき、昨日の目標も\nドジをしないでした");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 3, 0, 1, 1, 0, 942, 1, "あ、メイド服が\nほつれてる\n後でお裁縫しなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 3, 0, 1, 2, 0, 943, 1, "いい座り心地です～\nこれならお嬢さまも\nゆったりできますね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 3, 0, 1, 3, 0, 944, 1, "かわいい♪\nあとでお洗濯\nしてあげますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 3, 0, 1, 4, 0, 945, 1, "いい香り…\nお嬢様のお部屋にも\n置いてあげたいです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 3, 0, 1, 5, 0, 946, 1, "暖かいですけど\n…私が火に近づいて\n大丈夫でしょうか！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 3, 0, 1, 6, 0, 947, 1, "運動しようとすると\nいつも怪我するん\nですよね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 3, 0, 1, 7, 0, 948, 1, "こんなに美味しいもの\nメイドの私には\nもったいないです～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102502, 3, 0, 1, 8, 0, 949, 1, "物語のメイドさんって\n御主人さまを好きに\nなるんですよね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 1, 0, 1, 1, 0, 951, 1, "あの方と私だけの\n部屋にしたいですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 1, 0, 1, 2, 0, 952, 1, "あの方は私のもの…\n眼も耳も鼻も口も髪も\n首も胸も腰も手も脚も\nぬくもりも心も…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 1, 0, 1, 3, 0, 953, 1, "運命とは決して\n抗えないもの…\nクスクス…クスクス");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 1, 0, 1, 4, 0, 954, 1, "部屋からあの方の\n香りが…\nああ、至福ですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 1, 0, 1, 5, 0, 955, 1, "毒にも薬にもならない\n存在なんて嫌ですわ\nどうせならとびきりの\n毒でありたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 1, 0, 4, 101, 0, 956, 1, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 1, 0, 8, 102, 0, 957, 1, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 2, 0, 1, 1, 0, 958, 1, "運命を信じますか？\nもちろん絶対当然\n信じますわよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 2, 0, 1, 2, 0, 959, 1, "この薬は起床時就寝前\n食前食後食間に、毎日\n服用してください。\n惚れ薬ですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 2, 0, 1, 3, 0, 960, 1, "お願いがあるのです。\n新しい研究の実験台に\n…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 2, 0, 1, 4, 0, 961, 1, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 2, 0, 4, 101, 0, 962, 1, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 2, 0, 8, 102, 0, 963, 1, "私の身も心も、全てが\nあなた様のもの。\nあなた様の全ても…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 2, 0, 1, 1, 1, 964, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 2, 0, 1, 2, 2, 965, 1, "お昼を用意しましたの\n何も入れていませんわ\nクスクス…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 2, 0, 1, 3, 3, 966, 1, "あなた様が眠るまで\nお傍で見守りますわ。\n…あの、どうして\n眠らないのですか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 3, 0, 1, 1, 0, 967, 1, "いっそ鏡の中に\n閉じ込めてしまえたら\n私だけのものに…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 3, 0, 1, 2, 0, 968, 1, "はっ！\nリラックスしたら\n閃きましたわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 3, 0, 1, 3, 0, 969, 1, "あら、キメラの素体に\nぴったりですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 3, 0, 1, 4, 0, 970, 1, "このお部屋は\n良い香りがしますわ。\nはっ、あなた様の匂い\nなのでは…！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 3, 0, 1, 5, 0, 971, 1, "あたたかい…\n加熱調合にも\n使えそうですわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 3, 0, 1, 6, 0, 972, 1, "この運動エネルギー\n何かにうまく\n利用できないかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 3, 0, 1, 7, 0, 973, 1, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102701, 3, 0, 1, 8, 0, 974, 1, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 1, 0, 1, 1, 0, 976, 1, "あの方と私だけの\n部屋にしたいですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 1, 0, 1, 2, 0, 977, 1, "こっそり栽培している\n毒きのこが毒々しく\n育ちましたわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 1, 0, 1, 3, 0, 978, 1, "新しい薬\nどうやってあの方に\n飲ませようかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 1, 0, 1, 4, 0, 979, 1, "部屋からあの方の\n香りが…\nああ、至福ですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 1, 0, 1, 5, 0, 980, 1, "記憶を戻す研究より\n記憶をねつ造する研究\nのほうが良いかしら？\n伴侶という設定で…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 1, 0, 8, 101, 0, 981, 1, "クスクス…\nあの方とデートする\nイメトレ中ですわ\nクスクスクスクス…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 1, 0, 8, 102, 0, 982, 1, "魔法も科学も錬金術も\n全て駆使して\nあの方のために…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 2, 0, 1, 1, 0, 983, 1, "この部屋には雌犬…\n失礼、女の方がとても\n沢山いますのね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 2, 0, 1, 2, 0, 984, 1, "あなた様には私の伴侶\nとして高い教養を…\nあ、逃げられてしまい\nましたわ…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 2, 0, 1, 3, 0, 985, 1, "お願いがあるのです\n新しい研究の\n実験台に…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 2, 0, 1, 4, 0, 986, 1, "あなた様が寝ている時\n私は…クスクス\nこれ以上は言えません");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 2, 0, 8, 101, 0, 987, 1, "想像の中のあなた様は\nすっかり私の虜なのに\n現実は…\nもどかしいですわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 2, 0, 8, 102, 0, 988, 1, "あなた様の前では\n普通の女の子…\n少し毒薬づくりが\n得意なだけの…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 2, 0, 1, 1, 1, 989, 1, "おはようございます\n一晩中、あなた様の\n寝顔を見ていましたわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 2, 0, 1, 2, 2, 990, 1, "さあ、お食事ですよ\n味は保証しますわ\n特製ソースで少しだけ\n体が痺れますけど…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 2, 0, 1, 3, 3, 991, 1, "あなた様が眠るまで\nお傍で見守りますわ\n…あの、どうして\n眠らないのですか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 3, 0, 1, 1, 0, 992, 1, "私ももう少し\n外見に気を配る\nべきでしょうか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 3, 0, 1, 2, 0, 993, 1, "はっ！\nリラックスしたら\n閃きましたわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 3, 0, 1, 3, 0, 994, 1, "この私を差し置いて\nあなた様に媚びるなど\n言語道断…っ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 3, 0, 1, 4, 0, 995, 1, "このお部屋は\n良い香りがしますわ\nはっ、あなた様の匂い\nなのでは…！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 3, 0, 1, 5, 0, 996, 1, "素敵なぬくもり…\nまるであの方に\n抱きしめられたような");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 3, 0, 1, 6, 0, 997, 1, "この運動エネルギー\n何かにうまく\n利用できないかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 3, 0, 1, 7, 0, 998, 1, "美味しい。あの方にも\n差し上げましょう…\n薬を入れて…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102702, 3, 0, 1, 8, 0, 999, 1, "あの方を虜にする\n方法がどこかに\n載ってるはずですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 1, 0, 1, 1, 0, 1001, 1, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 1, 0, 1, 2, 0, 1002, 1, "使えるものは\n再利用しないと\n…あ、これとか\nまだ使えるわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 1, 0, 1, 3, 0, 1003, 1, "贅沢は敵！　とはいえ\n気持ちに余裕のない\n暮らしはダメよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 1, 0, 1, 4, 0, 1004, 1, "お腹空いたわね、\n残り物で\n何か作ろうかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 1, 0, 1, 5, 0, 1005, 1, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 1, 0, 4, 101, 0, 1006, 1, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 1, 0, 8, 102, 0, 1007, 1, "焼きそばパンが\n食べたいな～\nあいつと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 2, 0, 1, 1, 0, 1008, 1, "ねぇあんた、どこか\n安いお店知らない？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 2, 0, 1, 2, 0, 1009, 1, "あたし、エレガントに\n振る舞えてる？\n…そう？　嬉しい♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 2, 0, 1, 3, 0, 1010, 1, "いっぱい働いて遊んで\nよく休んでまた働く。\nそれが健全な生活よね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 2, 0, 1, 4, 0, 1011, 1, "一緒に部屋の\n整頓でもする？\nハマると楽しいわよ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 2, 0, 4, 101, 0, 1012, 1, "あんたがいると\n子供たちも喜ぶわ♪\nもちろんあたしもね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 2, 0, 8, 102, 0, 1013, 1, "ね、あんた今、幸せ？\n何びっくりしてるのよ\n大事なことじゃないの");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 2, 0, 1, 1, 1, 1014, 1, "面倒な用事は午前中に\n片づけてしまうのが、\n時間の使い方のコツよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 2, 0, 1, 2, 2, 1015, 1, "午後のティータイム\nとかどう？\n良いカフェがあるの♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 2, 0, 1, 3, 3, 1016, 1, "さ、エレガントな夜を\n楽しみましょ。\nモダンな音楽でも\nかけましょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 3, 0, 1, 1, 0, 1017, 1, "レディたるもの\n身嗜みはしっかりとね\n見本にならないと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 3, 0, 1, 2, 0, 1018, 1, "これで仕事は一段落ね\nちょっと休憩…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 3, 0, 1, 3, 0, 1019, 1, "うふふ、\nかわいいものを\n見るのは幸せね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 3, 0, 1, 4, 0, 1020, 1, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 3, 0, 1, 5, 0, 1021, 1, "風邪を引かないように\n身体を温めなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 3, 0, 1, 6, 0, 1022, 1, "さっ！　家の中でも\n効率的に\nトレーニングよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 3, 0, 1, 7, 0, 1023, 1, "もぐもぐ…はっ、\n口の周り、\n汚れてないわよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102801, 3, 0, 1, 8, 0, 1024, 1, "知識から過去を学んで\n未来に活かすわよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 1, 0, 1, 1, 0, 1026, 1, "安売りのお店の情報\n整理しないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 1, 0, 1, 2, 0, 1027, 1, "どんなに苦労してても\nそう見せないのが真の\nエレガントなレディよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 1, 0, 1, 3, 0, 1028, 1, "安く済んであいつが\n喜ぶ料理とか…\nないかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 1, 0, 1, 4, 0, 1029, 1, "子供たちの遊びって\nあたしが子供の頃と\nあまり変わらないのね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 1, 0, 1, 5, 0, 1030, 1, "商売はノンゼロサムが\n理想だなぁ。誰も損を\nしないってことだもの");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 1, 0, 8, 101, 0, 1031, 1, "焼きそばパンが\n食べたいな～\nあいつと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 1, 0, 8, 102, 0, 1032, 1, "あいつと一緒に…\n色々なところに\nいきたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 2, 0, 1, 1, 0, 1033, 1, "今度買い物に\n付き合いなさい\nセールがあるのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 2, 0, 1, 2, 0, 1034, 1, "あんたがいると子供\nたちも喜ぶわ♪\nもちろんあたしもね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 2, 0, 1, 3, 0, 1035, 1, "いっぱい働いて遊んで\nよく休んでまた働く\nそれが健全な生活よね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 2, 0, 1, 4, 0, 1036, 1, "あんたとこうして\nのんびりするのも\nいいものね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 2, 0, 8, 101, 0, 1037, 1, "ねえ、今度遊びに\n行きたいな\nもちろん一緒に、よ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 2, 0, 8, 102, 0, 1038, 1, "あんたが手伝って\nくれるから、仕事も\n前より楽しいのよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 2, 0, 1, 1, 1, 1039, 1, "面倒な用事は午前中に\n片づけてしまうのが\n時間の使い方のコツよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 2, 0, 1, 2, 2, 1040, 1, "ランチにして午後の\n仕事に備えましょ！\nやることは一杯よ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 2, 0, 1, 3, 3, 1041, 1, "ふわあ～\nもうこんな時間\n子供たちは\nもう寝たかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 3, 0, 1, 1, 0, 1042, 1, "忙しい時こそ\n身だしなみは隙なく\nぴしっとね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 3, 0, 1, 2, 0, 1043, 1, "これで仕事は一段落ね\nちょっと休憩…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 3, 0, 1, 3, 0, 1044, 1, "ふふふ\nずっと眺めてたく\nなっちゃうわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 3, 0, 1, 4, 0, 1045, 1, "部屋中がいい香り…\nなんだかすごい贅沢\nしてる気分♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 3, 0, 1, 5, 0, 1046, 1, "風邪を引かないように\n身体を温めなくちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 3, 0, 1, 6, 0, 1047, 1, "さっ！　家の中でも\n効率的に\nトレーニングよ！");
