INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102802, 3, 0, 1, 8, 0, 1049, 1, "知識から過去を学んで\n未来に活かすわよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102901, 1, 0, 1, 1, 0, 1051, 1, "可愛い子が\nいっぱいいるなぁ…\n誰か私とユニット\n組んでくれないかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102901, 1, 0, 1, 2, 0, 1052, 1, "今度のライブ会場、\nいつもより大きいから\n楽しみなんだ～！");
