INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102902, 3, 0, 1, 6, 0, 1097, 1, "ベストタイム更新！\nライブに向けて\n体力つけないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102902, 3, 0, 1, 7, 0, 1098, 1, "太っちゃったら\nどうしよう…？\nでも手が止まらない～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (102902, 3, 0, 1, 8, 0, 1099, 1, "この恋愛小説ステキ！\n私もアイドルじゃ\nなかったら…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 1, 0, 1, 1, 0, 1101, 1, "手裏剣の数が\n合わないデス！\nどこかに落とした\nデスか…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 1, 0, 1, 2, 0, 1102, 1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 1, 0, 1, 3, 0, 1103, 1, "金は天下の\nマワリモノ！\nところで、\nどういう意味デス？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 1, 0, 1, 4, 0, 1104, 1, "むむ、アクダイカンの\n気配がするデース");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 1, 0, 1, 5, 0, 1105, 1, "クニトリ合戦、\n楽しみデース！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 1, 0, 4, 101, 0, 1106, 1, "ショーグンと\n出会えて、\nワタシは果報者デス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 1, 0, 8, 102, 0, 1107, 1, "最近、ショーグンの\nそばにいると\nドキドキするデス…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 2, 0, 1, 1, 0, 1108, 1, "なんでも\n命令するデス！\nショーグンの命は\n絶対デス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 2, 0, 1, 2, 0, 1109, 1, "あっ、ショーグン、\n出陣はいつで\nゴザルデス！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 2, 0, 1, 3, 0, 1110, 1, "さ、さすが\nショーグンデス…\nいつの間に背後を！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 2, 0, 1, 4, 0, 1111, 1, "出陣の合図に\n備えるデス！\nほら貝は\nどこデスか！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 2, 0, 4, 101, 0, 1112, 1, "見つめられると\nドキドキするデス…\n新しい忍法デスか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 2, 0, 8, 102, 0, 1113, 1, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 2, 0, 1, 1, 1, 1114, 1, "おはようデス！\n今日も一日\n忍法日和デス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 2, 0, 1, 2, 2, 1115, 1, "そろそろお腹\n空いてきたデス。\n昼ごはんにするデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 2, 0, 1, 3, 3, 1116, 1, "明日もきっと\nいい日デスよ、\nショーグン！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 3, 0, 1, 1, 0, 1117, 1, "ちょんまげに\nしてみるのも\n面白いかもデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 3, 0, 1, 2, 0, 1118, 1, "心地よくて\n眠くなってきマス…\nもしやテキコクの\n罠デスか！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 3, 0, 1, 3, 0, 1119, 1, "これを変わり身の\n術に…かわいそう\nだから止めるデス");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 3, 0, 1, 4, 0, 1120, 1, "ワビサビというもの\nデスね！\n素晴らしいデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 3, 0, 1, 5, 0, 1121, 1, "身体を暖めて\nイクサに向かうデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 3, 0, 1, 6, 0, 1122, 1, "アクロバットな忍法を\n使うには、体を\n鍛えないと危険デス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 3, 0, 1, 7, 0, 1123, 1, "うーん、\n美味しいデス…\n幸せデス～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103001, 3, 0, 1, 8, 0, 1124, 1, "これは…\nＢＵＳＨＩＤＯ…\nデスか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 1, 0, 1, 1, 0, 1126, 1, "ふんふんふ～ん\n武器の手入れは\n念入りにしマスよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 1, 0, 1, 2, 0, 1127, 1, "クセモノ、隠れても\nむだデス！\n…気のせいデシタ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 1, 0, 1, 3, 0, 1128, 1, "金は天下の\nマワリモノ！\nところで\nどういう意味デス？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 1, 0, 1, 4, 0, 1129, 1, "むむ、アクダイカンの\n気配がするデース");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 1, 0, 1, 5, 0, 1130, 1, "クニトリ合戦\n楽しみデース！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 1, 0, 8, 101, 0, 1131, 1, "最近、ショーグンの\nそばにいると\nドキドキするデス…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 1, 0, 8, 102, 0, 1132, 1, "ショーグンの身は\n何があってもワタシが\n守るデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 2, 0, 1, 1, 0, 1133, 1, "あっ、ショーグン\n出陣はいつで\nゴザルデス！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 2, 0, 1, 2, 0, 1134, 1, "イクサの前に\nホネ休めも大事デスよ\nゆっくりするデース！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 2, 0, 1, 3, 0, 1135, 1, "さ、さすがショーグン\nデス…いつの間に\n背後を！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 2, 0, 1, 4, 0, 1136, 1, "一緒に忍法考える\nデス！\nショーグン用の\n忍法も必要デスね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 2, 0, 8, 101, 0, 1137, 1, "見つめられると\nドキドキするデス…\n新しい忍法デスか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 2, 0, 8, 102, 0, 1138, 1, "ショーグン！\nえへへ…これからも\nおそばに置いて\nクダサイね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 2, 0, 1, 1, 1, 1139, 1, "いい朝デス！\nトーゴク風の朝ゴハン\n作るデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 2, 0, 1, 2, 2, 1140, 1, "もうお昼デス\nお昼寝するデスか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 2, 0, 1, 3, 3, 1141, 1, "そろそろおネム\nデース…\n一緒に寝るデス？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 3, 0, 1, 1, 0, 1142, 1, "服の乱れは心の乱れ\nデス！　きっちり\nしないといけないデス");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 3, 0, 1, 2, 0, 1143, 1, "心地よくて眠く\nなってきマス…\nもしやテキコクの\n罠デスか！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 3, 0, 1, 3, 0, 1144, 1, "これを変わり身の\n術に…かわいそう\nだから止めるデス");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 3, 0, 1, 4, 0, 1145, 1, "ワビサビというもの\nデスね！\n素晴らしいデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 3, 0, 1, 5, 0, 1146, 1, "身体を暖めてイクサに\n向かうデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 3, 0, 1, 6, 0, 1147, 1, "ショーグンのお役に\n立つため、修行\nあるのみデス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 3, 0, 1, 7, 0, 1148, 1, "美味しいデス…\n酢昆布を和えて\nみたいデス");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103002, 3, 0, 1, 8, 0, 1149, 1, "物事を知って\n来たるべきイクサに\n活かすデス");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 1, 0, 1, 1, 0, 1151, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 1, 0, 1, 2, 0, 1152, 1, "父は、いつも男性に\nきびしいですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 1, 0, 1, 3, 0, 1153, 1, "占ってほしい人は、\nいつでも言って\nください");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 1, 0, 1, 4, 0, 1154, 1, "あなたの手相は…\nて、手を握るのって\n緊張しますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 1, 0, 1, 5, 0, 1155, 1, "この部屋、風水的にも\n運気が上がりそうです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 1, 0, 4, 101, 0, 1156, 1, "あの人とのこと占って\nみようかな…\n好き…嫌い…\n好き♪…き、嫌い");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 1, 0, 8, 102, 0, 1157, 1, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 2, 0, 1, 1, 0, 1158, 1, "女難の相が\n出ていますね…\n気をつけてください");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 2, 0, 1, 2, 0, 1159, 1, "今日のあなたの運勢は\n大きょ…\n大吉です");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 2, 0, 1, 3, 0, 1160, 1, "父と散歩中です。\n毎日決まったコースを\n散歩させないと、\nストレスが溜まるので");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 2, 0, 1, 4, 0, 1161, 1, "小僧！\nシノブに触るとは\nいい度胸じゃねぇか！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 2, 0, 4, 101, 0, 1162, 1, "あなたに触られると、\n動悸が早くなります\nもしかして心霊現象？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 2, 0, 8, 102, 0, 1163, 1, "今日もたくさんの幽霊\nに慕われていますね。\n素敵です");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 2, 0, 1, 1, 1, 1164, 1, "おはようございます\n今日の運勢を\n占いましょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 2, 0, 1, 2, 2, 1165, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 2, 0, 1, 3, 3, 1166, 1, "今日のあなたの運気は\n最高潮でした。\nもうすぐ今日は\n終わりますが");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 3, 0, 1, 1, 0, 1167, 1, "お父さん。\nいつも、身だしなみに\n気を使ってるけど、\nあまり変わってないよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 3, 0, 1, 2, 0, 1168, 1, "一休みしましょう。\n憑かれて疲れました");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 3, 0, 1, 3, 0, 1169, 1, "かわいい…\n付喪神が宿らないかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 3, 0, 1, 4, 0, 1170, 1, "お父さん、\nリラックスし過ぎると\n成仏しちゃうよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 3, 0, 1, 5, 0, 1171, 1, "気持ちいいです…\n極楽浄土みたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 3, 0, 1, 6, 0, 1172, 1, "はぁはぁ…\nお、お父さん、\n代わりに運動して…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 3, 0, 1, 7, 0, 1173, 1, "お父さんも\n食べたいの？\n死んでるのに？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103101, 3, 0, 1, 8, 0, 1174, 1, "この本…\nもしかして予言書？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 1, 0, 1, 1, 0, 1176, 1, "ここにいる幽霊は\nみんな幸せそうです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 1, 0, 1, 2, 0, 1177, 1, "お父さんが\n女の子ばかりに\n声をかけるので\n困っています");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 1, 0, 1, 3, 0, 1178, 1, "成仏はうれしいこと\nです…でも友達が\n減るのは寂しいですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 1, 0, 1, 4, 0, 1179, 1, "幽霊に好かれる人は\n素敵な方です\nもちろん、あなたも");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 1, 0, 1, 5, 0, 1180, 1, "お父さん、そろそろ\n子離れを…しない？\nそうですか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 1, 0, 8, 101, 0, 1181, 1, "生きている人に\n好かれるには、\nどうすればいい\nのでしょう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 1, 0, 8, 102, 0, 1182, 1, "お父さん、どうしたら\nあの人のことを\n認めてくれますか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 2, 0, 1, 1, 0, 1183, 1, "この部屋、風水的にも\n運気が上がりそうです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 2, 0, 1, 2, 0, 1184, 1, "シノブには指一本\n触れさせん！\nガブッ！！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 2, 0, 1, 3, 0, 1185, 1, "あなたの手相は…\nて、手を握るのって\n緊張しますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 2, 0, 1, 4, 0, 1186, 1, "ラップ音？\nいえ、父のいびきです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 2, 0, 8, 101, 0, 1187, 1, "私との相性ですか？\nそれなら占う必要は\nありませんよ…\nふふっ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 2, 0, 8, 102, 0, 1188, 1, "前世って信じますか？\n私とあなたは\nもしかすると…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 2, 0, 1, 1, 1, 1189, 1, "今日のラッキー\nアイテムはドクロです\n良ければ父を\nお貸ししましょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 2, 0, 1, 2, 2, 1190, 1, "明るい時間は\nあまり得意では\nないんです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 2, 0, 1, 3, 3, 1191, 1, "夜のお散歩に\n行きませんか？\n知り合いの幽霊に\n挨拶して回るんです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 3, 0, 1, 1, 0, 1192, 1, "お父さん\nいつも、身だしなみに\n気を使ってるけど\nあまり変わってないよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 3, 0, 1, 2, 0, 1193, 1, "うとうと…\nあれ…\n走馬灯が見える…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 3, 0, 1, 3, 0, 1194, 1, "かわいい…\nお父さんみたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 3, 0, 1, 4, 0, 1195, 1, "お父さん\nリラックスし過ぎると\n成仏しちゃうよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 3, 0, 1, 5, 0, 1196, 1, "快適です…\n幽霊もどんどん\n集まってきますね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 3, 0, 1, 6, 0, 1197, 1, "はぁはぁ…\nお、お父さん、\n代わりに運動して…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 3, 0, 1, 7, 0, 1198, 1, "お父さんの分も、\n私が食べるね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103102, 3, 0, 1, 8, 0, 1199, 1, "この本の占い\n私の占いと違う…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 1, 0, 1, 1, 0, 1201, 1, "私だって、一人で\n買い物ぐらい\nできますわ…きっと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 1, 0, 1, 2, 0, 1202, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 1, 0, 1, 3, 0, 1203, 1, "一日も早く、\n一人前の商人に\nなりたいものです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 1, 0, 1, 4, 0, 1204, 1, "平和を買うのには\nどれほどのお金が\n必要なのかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 1, 0, 1, 5, 0, 1205, 1, "ここももう少し広いと\n落ち着くのですけど。\n改築しませんこと？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 1, 0, 4, 101, 0, 1206, 1, "あの方はお金で雇った\nわけではないのに\n私といてくださる…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 1, 0, 8, 102, 0, 1207, 1, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 2, 0, 1, 1, 0, 1208, 1, "私と、庶民向けの\nお店へ一緒に\n参りませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 2, 0, 1, 2, 0, 1209, 1, "資産運用？\nお幾らほど\n増やしたいのです？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 2, 0, 1, 3, 0, 1210, 1, "どんぶり勘定だとよく\n言われるのですが…\n例えが小さすぎます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 2, 0, 1, 4, 0, 1211, 1, "お金で解決するのは\n悪いことでは\nありませんわよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 2, 0, 4, 101, 0, 1212, 1, "その、よろしければ\n私とお散歩など\nいかがです？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 2, 0, 8, 102, 0, 1213, 1, "もしあなたさまが\n道に迷われるなら、\n私もお供いたします！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 2, 0, 1, 1, 1, 1214, 1, "早起きは三ルピの徳と\n申しますが…\nもっとお得ですわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 2, 0, 1, 2, 2, 1215, 1, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 2, 0, 1, 3, 3, 1216, 1, "よろしければ\nお休み前のミルクを\n御一緒にいかが？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 3, 0, 1, 1, 0, 1217, 1, "だらしない身なりでは\n取引相手に信用して\nいただけませんものね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 3, 0, 1, 2, 0, 1218, 1, "この感触と安らぎ…\n素晴らしい\n費用対効果ですわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 3, 0, 1, 3, 0, 1219, 1, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 3, 0, 1, 4, 0, 1220, 1, "目に見えないものに\nお金をかけるのが\n真の贅沢ですわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 3, 0, 1, 5, 0, 1221, 1, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 3, 0, 1, 6, 0, 1222, 1, "健康はお金では\n買えませんが\n運動器具は別ですわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 3, 0, 1, 7, 0, 1223, 1, "なかなか\n美味ですわ\nシェフはどちらに？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103201, 3, 0, 1, 8, 0, 1224, 1, "経済の仕組みを\nしっかりと\n学ばなくては…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 1, 0, 1, 1, 0, 1226, 1, "そろそろ新しい\n案件を始めないと\nいけませんわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 1, 0, 1, 2, 0, 1227, 1, "ええと、これは\nおいくらで買ったん\nでしたっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 1, 0, 1, 3, 0, 1228, 1, "お金を見せたら\n態度が変わる人は\n信用できませんわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 1, 0, 1, 4, 0, 1229, 1, "地図が読めずとも\n道に迷わない方って\nいらっしゃるのですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 1, 0, 1, 5, 0, 1230, 1, "この狭さも\n慣れれば\n快適ですわ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 1, 0, 8, 101, 0, 1231, 1, "あの方はお金で雇った\nわけではないのに\n私といてくださる…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 1, 0, 8, 102, 0, 1232, 1, "たとえ私が間違っても\nきっとあの方が\n正してくれますわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 2, 0, 1, 1, 0, 1233, 1, "一人前の商人に\n相応しい居振る舞いを\nしたいものですわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 2, 0, 1, 2, 0, 1234, 1, "幼い頃\nお店屋さんごっこを\nなさいましたか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 2, 0, 1, 3, 0, 1235, 1, "資産運用？\nお幾らほど\n増やしたいのです？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 2, 0, 1, 4, 0, 1236, 1, "今度一緒に…\nパーティにでも\n行きませんこと？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 2, 0, 8, 101, 0, 1237, 1, "その、よろしければ\n私とお散歩など\nいかがです？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 2, 0, 8, 102, 0, 1238, 1, "あなたさまの優しさは\nお金には\n代えられませんわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 2, 0, 1, 1, 1, 1239, 1, "なぜだか\n眠り足りないような\n感じがしますわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 2, 0, 1, 2, 2, 1240, 1, "昼食は町の屋台で\n立ったまま食べて\nみたいですわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 2, 0, 1, 3, 3, 1241, 1, "よろしければ\nお休み前のミルクを\n御一緒にいかが？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 3, 0, 1, 1, 0, 1242, 1, "ドレスは汚れていない\nようですが…やだ\n髪がほつれてますわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 3, 0, 1, 2, 0, 1243, 1, "こういったものに\n腰を沈めると…\nき、気が緩んで…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 3, 0, 1, 3, 0, 1244, 1, "何の見返りも求めず\nただひたすら愛でて\nいたいのです！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 3, 0, 1, 4, 0, 1245, 1, "香りは質のいいものと\nそうでないものが\nはっきり分かりますわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 3, 0, 1, 5, 0, 1246, 1, "誰ひとり寒い思いを\nしない町にするために\n私にできる事は…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 3, 0, 1, 6, 0, 1247, 1, "目標は遠くに\n置いた方が励みに\nなりますわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 3, 0, 1, 7, 0, 1248, 1, "なかなか\n美味ですわ\nシェフはどちらに？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103202, 3, 0, 1, 8, 0, 1249, 1, "商人って、物語では\n悪役にされやすいの\nですね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 1, 0, 1, 1, 0, 1251, 1, "家の中のものを使って\n物ボケ、考えるべ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 1, 0, 1, 2, 0, 1252, 1, "毎日の日課…それは\n炊事、洗濯、大喜利\nだべ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 1, 0, 1, 3, 0, 1253, 1, "うちに塀ができたら、\nへー、って言いたい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 1, 0, 1, 4, 0, 1254, 1, "オラが作る料理は\nこの後スタッフで\n美味しくいただくべよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 1, 0, 1, 5, 0, 1255, 1, "エリザベスも\n連れてきてぇ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 1, 0, 4, 101, 0, 1256, 1, "あんちゃんも、\nエリザベスと同じ位\nめんこいべ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 1, 0, 8, 102, 0, 1257, 1, "オラとあんちゃん、\n息がぴったりに\nなってきたべ…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 2, 0, 1, 1, 0, 1258, 1, "おぉ！　そんな急に\nツッコまれたら、\nびっくらこくべやー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 2, 0, 1, 2, 0, 1259, 1, "ネタはオラが書くけど\nあんちゃんが\n書きたかったら\n書いてもいいんだぞ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 2, 0, 1, 3, 0, 1260, 1, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 2, 0, 1, 4, 0, 1261, 1, "押すなよ、\n絶対押すなよ\n…なんて\n言ってないべさ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 2, 0, 4, 101, 0, 1262, 1, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 2, 0, 8, 102, 0, 1263, 1, "将来的には夫婦漫才\nってのも…あ、ひ、\nひとりごとだべさ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 2, 0, 1, 1, 1, 1264, 1, "おはよう！　一緒に\n牛の乳しぼりいくべ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 2, 0, 1, 2, 2, 1265, 1, "お昼だ～腹が減っては\n笑いはできぬ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 2, 0, 1, 3, 3, 1266, 1, "今日もよく働いたべ！\nさ、今からは\nお笑いの練習だ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 3, 0, 1, 1, 0, 1267, 1, "舞台に上がる前に\n身なりは整えねぇと\nだめだべ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 3, 0, 1, 2, 0, 1268, 1, "ナイスなイスに\n座りたい、\nなんちゃって！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 3, 0, 1, 3, 0, 1269, 1, "めんこいのは、\nスベっても許される\nからずるいべな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 3, 0, 1, 4, 0, 1270, 1, "必要以上に\n癒されちまうべや～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 3, 0, 1, 5, 0, 1271, 1, "ぽかぽかだ…\nウケた会場みてぇだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 3, 0, 1, 6, 0, 1272, 1, "お笑い芸人は、\n体が資本だべさ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 3, 0, 1, 7, 0, 1273, 1, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103301, 3, 0, 1, 8, 0, 1274, 1, "ネタ作りのヒントが\n載ってねぇか探すべ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 1, 0, 1, 1, 0, 1276, 1, "さぁ今日も張り切って\n漫才の練習するべさ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 1, 0, 1, 2, 0, 1277, 1, "この部屋でも、商品\n売っていいんだべか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 1, 0, 1, 3, 0, 1278, 1, "眼鏡を頭の上に乗せて\n「メガネメガネ～」\nって言う練習した方が\nいいべか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 1, 0, 1, 4, 0, 1279, 1, "漫才を持ち時間に\n収めるには、何度も\n練習するしかねぇべ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 1, 0, 1, 5, 0, 1280, 1, "エリザベスも\n連れてきてぇ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 1, 0, 8, 101, 0, 1281, 1, "うう…あんちゃんに\nツッコまれたくて\nうずうずしてきたべ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 1, 0, 8, 102, 0, 1282, 1, "あんちゃんに\n採れたての食材を\n食わしてやりてぇ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 2, 0, 1, 1, 0, 1283, 1, "押すなよ\n絶対押すなよ\n…なんて\n言ってないべさ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 2, 0, 1, 2, 0, 1284, 1, "あんちゃん、百連\nツッコミ特訓だ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 2, 0, 1, 3, 0, 1285, 1, "あんちゃん、オラには\n容赦なんかせずに\nツッコんでくれて\nいいから！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 2, 0, 1, 4, 0, 1286, 1, "「なんでだべ～！」\nって言いながら\nツッコむといいべ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 2, 0, 8, 101, 0, 1287, 1, "あはは～あんちゃんの\nツッコミはやっぱり\n気持ちいいなぁ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 2, 0, 8, 102, 0, 1288, 1, "将来的には夫婦漫才\nってのも…あ、ひ\nひとりごとだべさ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 2, 0, 1, 1, 1, 1289, 1, "おはよう！\n今日もいい\nネタ作り日和だ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 2, 0, 1, 2, 2, 1290, 1, "お昼だな\nご飯を食べたら、\nネタの練習するべ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 2, 0, 1, 3, 3, 1291, 1, "もうこんな時間だべ\nネタ作りは\n明日にするべ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 3, 0, 1, 1, 0, 1292, 1, "うーん…衣装、\nずれてねぇべか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 3, 0, 1, 2, 0, 1293, 1, "ナイスなイスに\n座りたい\nなんちゃって！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 3, 0, 1, 3, 0, 1294, 1, "はう～\nなまらめんこいべや～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 3, 0, 1, 4, 0, 1295, 1, "必要以上に\n癒されちまうべや～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 3, 0, 1, 5, 0, 1296, 1, "笑いをとることを\n忘れるほど\nあったけぇべ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 3, 0, 1, 6, 0, 1297, 1, "たくさん運動して\n動きでも笑いがとれる\nように頑張るべ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 3, 0, 1, 7, 0, 1298, 1, "もぐもぐ…\nボケることも\n忘れちまう美味さだ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103302, 3, 0, 1, 8, 0, 1299, 1, "幅広く教養を\n身につけて\nいいネタ作るべ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 1, 0, 1, 1, 0, 1301, 1, "通りにいる\nカップル…爆発\nすればいいのに…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 1, 0, 1, 2, 0, 1302, 1, "う～ん、仕事なんて\nしないで、\n飲んでたいな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 1, 0, 1, 3, 0, 1303, 1, "今日は一日、\n外に出ない！\nもう決めたわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 1, 0, 1, 4, 0, 1304, 1, "お腹空いた…\nけど、外に出るのは\n面倒だなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 1, 0, 1, 5, 0, 1305, 1, "うぅ…頭がガンガン\nする…昨日、\n飲み過ぎたかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 1, 0, 4, 101, 0, 1306, 1, "デートとか…\n誘っても嫌がられ\nないかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 1, 0, 8, 102, 0, 1307, 1, "もしかして運命の\n相手だったり…\nするのかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 2, 0, 1, 1, 0, 1308, 1, "やっぱり一杯目が\n一番おいしいわ～！\n…え、見てた！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 2, 0, 1, 2, 0, 1309, 1, "頼れるお姉さんだって\nところ、もっと見せて\nあげるからね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 2, 0, 1, 3, 0, 1310, 1, "ねえ、一緒に買い物に\n行かない？　その…\nの、飲み物とか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 2, 0, 1, 4, 0, 1311, 1, "ひっく、うふふ…\nもっとお姉さんと\nお話しようよ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 2, 0, 4, 101, 0, 1312, 1, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 2, 0, 8, 102, 0, 1313, 1, "いつか…\nキミと一緒に飲めたら\n楽しいだろうな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 2, 0, 1, 1, 1, 1314, 1, "おはよう…朝は\nちょっとつらいな…\n低血圧なのよ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 2, 0, 1, 2, 2, 1315, 1, "おっひるー♪\nなに食べよっかな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 2, 0, 1, 3, 3, 1316, 1, "ふわあ…\n眠る前にちょっと\n一杯…冗談よ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 3, 0, 1, 1, 0, 1317, 1, "うう…\n髪がボサボサ…\nちゃんとしなきゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 3, 0, 1, 2, 0, 1318, 1, "自然体でゴロゴロ\nするのって\n幸せだなぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 3, 0, 1, 3, 0, 1319, 1, "こういうの男の子に\nプレゼントして\nもらいたいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 3, 0, 1, 4, 0, 1320, 1, "うわあ、\nこれ、私の好きな\n香りだー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 3, 0, 1, 5, 0, 1321, 1, "ぽっかぽか…\nあとは熱い一杯が\nあれば…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 3, 0, 1, 6, 0, 1322, 1, "家でゴロゴロして\n付いちゃったお肉を\n落とさないと…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 3, 0, 1, 7, 0, 1323, 1, "もぐもぐ…\nおーいしー！\n…太らないよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103401, 3, 0, 1, 8, 0, 1324, 1, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 1, 0, 1, 1, 0, 1326, 1, "仕事終わりの一杯の\nために、今日も\n頑張らないと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 1, 0, 1, 2, 0, 1327, 1, "今日は\nのんびり\n過ごそうかな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 1, 0, 1, 3, 0, 1328, 1, "あ…\nお皿、洗って\nなかった…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 1, 0, 1, 4, 0, 1329, 1, "もう少し、大人の\n魅力が出せるように\nなりたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 1, 0, 1, 5, 0, 1330, 1, "素敵な彼氏…\n欲しいなぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 1, 0, 8, 101, 0, 1331, 1, "彼と私\n釣り合うかな…\n大丈夫よね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 1, 0, 8, 102, 0, 1332, 1, "今後のために\nちょっとは花嫁修業\nしてみようかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 2, 0, 1, 1, 0, 1333, 1, "何事も\nお姉さんと一緒に\n楽しく頑張りましょ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 2, 0, 1, 2, 0, 1334, 1, "ね、ねえ！\nキミは年上と年下\nどっちが好きかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 2, 0, 1, 3, 0, 1335, 1, "もう…\n構ってくれないと\n泣いちゃうぞ～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 2, 0, 1, 4, 0, 1336, 1, "お姉さんが～\n可愛がって\nあげようか～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 2, 0, 8, 101, 0, 1337, 1, "極力人付き合いは\nしない主義なんだけど\nキミは例外かも…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 2, 0, 8, 102, 0, 1338, 1, "お姉さんとチュー\nする？　ふふふ～\n冗談で～す！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 2, 0, 1, 1, 1, 1339, 1, "さーて、今日も\nいっちょやるぞー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 2, 0, 1, 2, 2, 1340, 1, "後でのんびり\nストレッチでも\nしようっと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 2, 0, 1, 3, 3, 1341, 1, "一日よく働いた～！\n自分にごほうび\nあげたいなー♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 3, 0, 1, 1, 0, 1342, 1, "仕事の前に、髪とか\n整えないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 3, 0, 1, 2, 0, 1343, 1, "自然体でゴロゴロ\nするのって\n幸せだなぁ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 3, 0, 1, 3, 0, 1344, 1, "もう大人だし…\nあんまり可愛いのは\n似合わないかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 3, 0, 1, 4, 0, 1345, 1, "うわあ\nこれ、私の好きな\n香りだー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 3, 0, 1, 5, 0, 1346, 1, "あったかーい…\nだ、だらけ\nちゃいそう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 3, 0, 1, 6, 0, 1347, 1, "地道な努力が\nリア充への\n第一歩！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 3, 0, 1, 7, 0, 1348, 1, "おーいしっ♪\nこのために\n生きてるわねえ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103402, 3, 0, 1, 8, 0, 1349, 1, "大人の女らしく\n色んなことを学んで\nおかなくっちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 1, 0, 1, 1, 0, 1351, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 1, 0, 1, 2, 0, 1352, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 1, 0, 1, 3, 0, 1353, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 1, 0, 1, 4, 0, 1354, 1, "ちょっと、休憩…\n本でも読もうかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 1, 0, 1, 5, 0, 1355, 1, "もっとたくさんの本に\n囲まれて生活したいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 1, 0, 4, 101, 0, 1356, 1, "むかしむかしある所に\nシオリと\nあの人が…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 1, 0, 8, 102, 0, 1357, 1, "この本の主人公、\nあの人に\n似ているかも…\nかっこいいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 2, 0, 1, 1, 0, 1358, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 2, 0, 1, 2, 0, 1359, 1, "あ…お姉ちゃんと\n約束があったんだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 2, 0, 1, 3, 0, 1360, 1, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 2, 0, 1, 4, 0, 1361, 1, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 2, 0, 4, 101, 0, 1362, 1, "読みたい本も\nあなたと行きたい所も\n増えるばっかりです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 2, 0, 8, 102, 0, 1363, 1, "私の病気、\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 2, 0, 1, 1, 1, 1364, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 2, 0, 1, 2, 2, 1365, 1, "ふぅ…午前中\n動いたので体力が…\nすこし休みますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 2, 0, 1, 3, 3, 1366, 1, "早く寝ないと\n体に良くないですよね\nでも気になっている\n本があって…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 3, 0, 1, 1, 0, 1367, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 3, 0, 1, 2, 0, 1368, 1, "少し休もう\n…やっぱり\nたくさん休もう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 3, 0, 1, 3, 0, 1369, 1, "かわいいものが\n枕元にあると\n安心できるんですよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 3, 0, 1, 4, 0, 1370, 1, "いい香り…\n病は気からと言うので\n雰囲気も大事ですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 3, 0, 1, 5, 0, 1371, 1, "こうして温まって\nいると、病気も\n治る気がします");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 3, 0, 1, 6, 0, 1372, 1, "運動…イメージ\nトレーニングだけじゃ\nだめでしょうか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 3, 0, 1, 7, 0, 1373, 1, "少食ですけど\nこれならたくさん\n食べられそうです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103801, 3, 0, 1, 8, 0, 1374, 1, "続きが気になって\n読むのを止める\nタイミングが…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 1, 0, 1, 1, 0, 1376, 1, "しっかり休まないと\n早く回復するように");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 1, 0, 1, 2, 0, 1377, 1, "き、きらーん☆\nお姉ちゃんに\n似てるかな…？　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 1, 0, 1, 3, 0, 1378, 1, "病室よりも…\nずっとこの部屋に\nいたいですね…　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 1, 0, 1, 4, 0, 1379, 1, "そろそろ好きな\n作家さんの新刊が\n出るから楽しみ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 1, 0, 1, 5, 0, 1380, 1, "大切な人に\n本をすすめるのって\nけっこう難しいなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 1, 0, 8, 101, 0, 1381, 1, "むかしむかしある所に\nシオリと\nあの人が…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 1, 0, 8, 102, 0, 1382, 1, "あの人の\nためにも早く\n病気を治さないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 2, 0, 1, 1, 0, 1383, 1, "ケホケホ…\nコンコン…\nゴホッゴホッ…\nへ、平気ですよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 2, 0, 1, 2, 0, 1384, 1, "新記録です！\n一週間連続、\n平熱を保ちました！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 2, 0, 1, 3, 0, 1385, 1, "あ…お薬の時間…\n苦いけど我慢しなきゃ　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 2, 0, 1, 4, 0, 1386, 1, "最近調子がいいのは\n牧場の牛乳やチーズの\nおかげでしょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 2, 0, 8, 101, 0, 1387, 1, "しおりを探している？\nあ、本に挟む方ですね\nドキドキしました…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 2, 0, 8, 102, 0, 1388, 1, "私の病気\n悪化してるのかも！？\nすごく胸がドキドキ\nするんです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 2, 0, 1, 1, 1, 1389, 1, "えっと、朝食の前には\nこの薬を飲んで…\n食後にはこの薬を…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 2, 0, 1, 2, 2, 1390, 1, "…お昼の薬？\nあ、飲み忘れてました\nありがとうございます");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 2, 0, 1, 3, 3, 1391, 1, "この薬を飲むと\n眠くなっちゃうんです\nでもまだ、あなたと\nお話したいし～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 3, 0, 1, 1, 0, 1392, 1, "寝てばかりなので、\n髪が乱れてないかな\n…？　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 3, 0, 1, 2, 0, 1393, 1, "少し休もう\n…やっぱり\nたくさん休もう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 3, 0, 1, 3, 0, 1394, 1, "この子がいれば\n病室に一人でも\n寂しくないですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 3, 0, 1, 4, 0, 1395, 1, "いろんな効能が？\n健康になる香りも\nあるのでしょうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 3, 0, 1, 5, 0, 1396, 1, "こうして温まって\nいると、病気も\n治る気がします");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 3, 0, 1, 6, 0, 1397, 1, "私も、少しは\n体力をつけないと\nいけませんよね　");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 3, 0, 1, 7, 0, 1398, 1, "いっぱい食べて\n健康な体にならないと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (103802, 3, 0, 1, 8, 0, 1399, 1, "続きが気になって\n読むのを止める\nタイミングが…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 1, 0, 1, 1, 0, 1401, 1, "友達ができたら、\n山の上でお弁当…\n夢が広がります…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 1, 0, 1, 2, 0, 1402, 1, "あ、忘れずに\nお花に水を\nあげなきゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 1, 0, 1, 3, 0, 1403, 1, "お菓子を作って、\nみなさんに振る舞って\n友達作戦！　とか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 1, 0, 1, 4, 0, 1404, 1, "そろそろ新しい\n草人形を作って\n友達作りの特訓を…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 1, 0, 1, 5, 0, 1405, 1, "植物のお世話って、\nぼっちにとっては、\n救い…なんです…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 1, 0, 4, 101, 0, 1406, 1, "ＢＢ団の活動日…\nまだかな…\nうふふふっ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 1, 0, 8, 102, 0, 1407, 1, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 2, 0, 1, 1, 0, 1408, 1, "あ、こんにちは！\n今日もＢＢ団で\nお世話になります！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 2, 0, 1, 2, 0, 1409, 1, "あの…私の友達作りの\n練習方法、間違って\nませんよね…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 2, 0, 1, 3, 0, 1410, 1, "そうだ、今度、\n森に一緒に木の苗を\n植えに行きませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 2, 0, 1, 4, 0, 1411, 1, "人とお話するのって、\n私、大好きです！\n友達ふやすぞ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 2, 0, 4, 101, 0, 1412, 1, "あなたと話してると、\nまるで私がぼっちじゃ\nないみたいです！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 2, 0, 8, 102, 0, 1413, 1, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 2, 0, 1, 1, 1, 1414, 1, "おはようございます！\n今日もＢＢ団、\nがんばるぞー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 2, 0, 1, 2, 2, 1415, 1, "なんだか、お腹が\n空いてきました。\nお昼ごはんー♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 2, 0, 1, 3, 3, 1416, 1, "もう眠いかもです…\n明日は友達、\nできるといいなあ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 3, 0, 1, 1, 0, 1417, 1, "私がもっと\n可愛ければ、\n友達できるかなあ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 3, 0, 1, 2, 0, 1418, 1, "ふわあ…のんびり\nしすぎないように\n気を付けなきゃです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 3, 0, 1, 3, 0, 1419, 1, "かわいいなあ。\nこの子とも\n友達になりたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 3, 0, 1, 4, 0, 1420, 1, "いい香りです…\n精神統一して\n友達のイメトレ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 3, 0, 1, 5, 0, 1421, 1, "灯りを見てると、\n心がなごみます～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 3, 0, 1, 6, 0, 1422, 1, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 3, 0, 1, 7, 0, 1423, 1, "おいしいです…\n私に友達がいたら、\n分けてあげたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104001, 3, 0, 1, 8, 0, 1424, 1, "友達作りの\n参考になりそうなこと\n書いてないかな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 1, 0, 1, 1, 0, 1426, 1, "友達作りは、焦らず\n慌てず、じっくりと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 1, 0, 1, 2, 0, 1427, 1, "ひとつ屋根の下\nって感じ…\nいいですねえ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 1, 0, 1, 3, 0, 1428, 1, "煮汁、煮汁が\n噴きこぼれて\nいませんか～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 1, 0, 1, 4, 0, 1429, 1, "今度、みなさんと\nピクニックとか…\nいってみたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 1, 0, 1, 5, 0, 1430, 1, "ぼっちでもいい\nたくましく育てば\n…なんて");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 1, 0, 8, 101, 0, 1431, 1, "最近、毎日あの人の\nこと考えちゃいます\nどうしてだろう…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 1, 0, 8, 102, 0, 1432, 1, "もっともっとあの人の\nそばで、一緒に友達を\n作っていきたいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 2, 0, 1, 1, 0, 1433, 1, "あ！　ＢＢ団の活動\nですか？　嬉しくて\n飛び跳ねちゃいます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 2, 0, 1, 2, 0, 1434, 1, "あなたとのお話で友達\n作りの練習になるなら\n一石二鳥です！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 2, 0, 1, 3, 0, 1435, 1, "私少しは人と話しても\nテンパらなくなって\nきましたかね…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 2, 0, 1, 4, 0, 1436, 1, "あの、友達作りの\n新しい作戦\n考えてみたんですっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 2, 0, 8, 101, 0, 1437, 1, "あなたとお話する時の\nこの気持ち、私の宝物\nなんです…えへへ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 2, 0, 8, 102, 0, 1438, 1, "友達もほしいけど…\nあなたに会えたことが\n私の一番の幸せです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 2, 0, 1, 1, 1, 1439, 1, "朝ですね！\nこんないい朝は\n友達作りのチャンス！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 2, 0, 1, 2, 2, 1440, 1, "あの…そろそろお腹\nすきませんか？\nお昼ごはんは…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 2, 0, 1, 3, 3, 1441, 1, "もう眠いかもです…\n明日は友達\nできるといいなあ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 3, 0, 1, 1, 0, 1442, 1, "私…見た目\nそこまで変じゃ\nないですよね…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 3, 0, 1, 2, 0, 1443, 1, "ほわあ～…\nふかふかって\nいいものですね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 3, 0, 1, 3, 0, 1444, 1, "この子みたいな\nあいくるしさを\n目指したいです！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 3, 0, 1, 4, 0, 1445, 1, "灯りを見てると\n心がなごみます～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 3, 0, 1, 5, 0, 1446, 1, "ぬくぬくで、なんだか\n眠くなっちゃいますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 3, 0, 1, 6, 0, 1447, 1, "身体を鍛えて\nみなさんを\n守ります！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 3, 0, 1, 7, 0, 1448, 1, "もぐもぐ…\nおいしいです…！\nみなさんもぜひ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104002, 3, 0, 1, 8, 0, 1449, 1, "友達作りの\n参考になりそうなこと\n書いてないかな～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 1, 0, 1, 1, 0, 1451, 1, "唱喚の歌は、みだりに\n歌っていいものでは\nないんです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 1, 0, 1, 2, 0, 1452, 1, "ここでアイドルの\n練習は、さすがに\n迷惑ですよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 1, 0, 1, 3, 0, 1453, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 1, 0, 1, 4, 0, 1454, 1, "人々の平和を守る旅に\n同行してくれる人を\n探していて…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 1, 0, 1, 5, 0, 1455, 1, "あとで曲を復習して\nおかないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 1, 0, 4, 101, 0, 1456, 1, "踊りを見られるのは\nまだ恥ずかしいけど\nあの人になら…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 1, 0, 8, 102, 0, 1457, 1, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 2, 0, 1, 1, 0, 1458, 1, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 2, 0, 1, 2, 0, 1459, 1, "え、もう練習の時間？\nごめんなさい\nすぐ準備します！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 2, 0, 1, 3, 0, 1460, 1, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 2, 0, 1, 4, 0, 1461, 1, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 2, 0, 4, 101, 0, 1462, 1, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 2, 0, 8, 102, 0, 1463, 1, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 2, 0, 1, 1, 1, 1464, 1, "おはようございます♪\n少し歌の練習を\nしてきますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 2, 0, 1, 2, 2, 1465, 1, "もうお昼なんですか？\n練習に夢中で\n気づきませんでした");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 2, 0, 1, 3, 3, 1466, 1, "明日の予定は…\nアイドルはやることが\n多いみたいで…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 3, 0, 1, 1, 0, 1467, 1, "見た目については\n前よりも気をつけてと\nノゾミに言われてます");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 3, 0, 1, 2, 0, 1468, 1, "歌の調子をよくする\nためには休息も\n必要ですよね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 3, 0, 1, 3, 0, 1469, 1, "この子たちを\n相手に練習して\nみようかな…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 3, 0, 1, 4, 0, 1470, 1, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 3, 0, 1, 5, 0, 1471, 1, "ここにいると\n穏やかな旋律が\n浮かんできます…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 3, 0, 1, 6, 0, 1472, 1, "歌うためには\n体力づくりは\n欠かせません");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 3, 0, 1, 7, 0, 1473, 1, "歌った後は\nお腹が空いて\nしまうんです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104201, 3, 0, 1, 8, 0, 1474, 1, "音楽について\n理論も勉強\nしないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 1, 0, 1, 1, 0, 1476, 1, "歌うことの楽しさ\n分かってきました\nもっと歌いたいな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 1, 0, 1, 2, 0, 1477, 1, "練習してもあんまり\n疲れなくなった…\nアイドルのおかげかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 1, 0, 1, 3, 0, 1478, 1, "精霊も強くなったし\nもっとたくさんの人を\n守りたいです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 1, 0, 1, 4, 0, 1479, 1, "最近、よく頭の中に\n歌が浮かんできます\n不思議な感覚…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 1, 0, 1, 5, 0, 1480, 1, "楽譜を買いました\n思いついた歌を\n書き留めておくために");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 1, 0, 8, 101, 0, 1481, 1, "浜辺で曲を書こうと\n思うんですけど…\n一緒に行きませんか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 1, 0, 8, 102, 0, 1482, 1, "私の中にある\n私だけの歌…\n作ってみようかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 2, 0, 1, 1, 0, 1483, 1, "守るべき人たちに\n笑顔でいてもらうのも\n大切なんですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 2, 0, 1, 2, 0, 1484, 1, "歌える曲を増やそうと\n思って。どんな曲が\n聞きたいですか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 2, 0, 1, 3, 0, 1485, 1, "魔物退治ですか！？\n…違います？\n良かった…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 2, 0, 1, 4, 0, 1486, 1, "いつも応援ありがとう\nございます♪　これは\nあ、握手の練習です！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 2, 0, 8, 101, 0, 1487, 1, "…歌って欲しい？\n喜んで♪\nどの歌にしましょう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 2, 0, 8, 102, 0, 1488, 1, "～～♪　あれ？\n今私、歌ってました？\n気づきませんでした");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 2, 0, 1, 1, 1, 1489, 1, "呼吸を深く、大きく…\nこれでちゃんと\n目が覚めますよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 2, 0, 1, 2, 2, 1490, 1, "少し休んだら\n午後の練習に\n行ってきますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 2, 0, 1, 3, 3, 1491, 1, "曲を作っていたら\nもうこんな時間…\nおやすみなさい");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 3, 0, 1, 1, 0, 1492, 1, "見た目については\n前よりも気をつけてと\nノゾミに言われてます");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 3, 0, 1, 2, 0, 1493, 1, "あ～…♪\nよく体がほぐれます…\nいい歌が歌えそうです");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 3, 0, 1, 3, 0, 1494, 1, "かわいいと思う\n気持ちを、歌にしても\nいいかも知れませんね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 3, 0, 1, 4, 0, 1495, 1, "…疲れてるのかな\n落ち着けているのが\n自分でもわかります");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 3, 0, 1, 5, 0, 1496, 1, "ここにいると\n穏やかな旋律が\n浮かんできます…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 3, 0, 1, 6, 0, 1497, 1, "もっと体力をつけて\n最高のライブを\nファンに届けます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 3, 0, 1, 7, 0, 1498, 1, "これでも一応\n体形には気をつけて\n食べてるんですよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104202, 3, 0, 1, 8, 0, 1499, 1, "読書は歌に\n新しい風を\n吹き込んでくれます");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 1, 0, 1, 1, 0, 1501, 1, "店の新メニューでも\n考えるかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 1, 0, 1, 2, 0, 1502, 1, "あたしの店から、\n出前を届けようか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 1, 0, 1, 3, 0, 1503, 1, "パトロール、\nパトロール…\nここは異常なしかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 1, 0, 1, 4, 0, 1504, 1, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 1, 0, 1, 5, 0, 1505, 1, "のんびりし過ぎるのも\n…性に合わねぇな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 1, 0, 4, 101, 0, 1506, 1, "そろそろあいつ、\n腹すかしてるん\nじゃないかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 1, 0, 8, 102, 0, 1507, 1, "今日の献立は…\nあいつの好きなものに\nしてやるか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 2, 0, 1, 1, 0, 1508, 1, "強くなりたいなら、\nあたしがビシバシ\n鍛えてやるよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 2, 0, 1, 2, 0, 1509, 1, "天気がいい日は\n外で運動したく\nならねぇか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 2, 0, 1, 3, 0, 1510, 1, "腹減ったか？　なんか\n作ってやろうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 2, 0, 1, 4, 0, 1511, 1, "ん？　なんだよ、\n相談事か？\nなんでも言ってくれ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 2, 0, 4, 101, 0, 1512, 1, "お前が好きな飯って\nなんだ？\n今度、作ってやるよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 2, 0, 8, 102, 0, 1513, 1, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 2, 0, 1, 1, 1, 1514, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 2, 0, 1, 2, 2, 1515, 1, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 2, 0, 1, 3, 3, 1516, 1, "日中たっぷり動いたら\nそのぶん夜はしっかり\n休んどけよな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 3, 0, 1, 1, 0, 1517, 1, "た、たまにはその…\nかわいい格好とか\nしても…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 3, 0, 1, 2, 0, 1518, 1, "ふわぁ、\n一休みすっかなぁ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 3, 0, 1, 3, 0, 1519, 1, "うっ！　か、かわいい\nじゃねぇか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 3, 0, 1, 4, 0, 1520, 1, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 3, 0, 1, 5, 0, 1521, 1, "ふわぁ〜！\n温かいと、つい\nウトウトしちまうな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 3, 0, 1, 6, 0, 1522, 1, "オラオラオラーーー！\n燃えるぜーーー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 3, 0, 1, 7, 0, 1523, 1, "いい味してるぜ！\nうちの店でも\n出してみたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104301, 3, 0, 1, 8, 0, 1524, 1, "文字を読んでると\nどうしても眠く\nなっちまうんだよなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 1, 0, 1, 1, 0, 1526, 1, "パトロール終了～\n本日も異常なしっと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 1, 0, 1, 2, 0, 1527, 1, "あたしの店から\n出前を届けようか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 1, 0, 1, 3, 0, 1528, 1, "あいつ、早く\n帰ってこないかな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 1, 0, 1, 4, 0, 1529, 1, "あいつ\n腹すかせてないと\nいいんだけど…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 1, 0, 1, 5, 0, 1530, 1, "のんびりし過ぎるのも\n…性に合わねぇな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 1, 0, 8, 101, 0, 1531, 1, "あいつの事を思うと\n胸があったかくなる？\nなんだコレ…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 1, 0, 8, 102, 0, 1532, 1, "今日の献立は…\nあいつの好きなものに\nしてやるか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 2, 0, 1, 1, 0, 1533, 1, "ん？　なんだよ\n相談事か？\nなんでも言ってくれ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 2, 0, 1, 2, 0, 1534, 1, "天気がいい日は\n外で運動したく\nならねぇか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 2, 0, 1, 3, 0, 1535, 1, "腹減ったか？　なんか\n作ってやろうか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 2, 0, 1, 4, 0, 1536, 1, "腹へったら食う！\n悩んだら寝る！\nそれが一番だろ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 2, 0, 8, 101, 0, 1537, 1, "あのさ…できれば\nもっとそばに…いや\nなんでもねぇ忘れろ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 2, 0, 8, 102, 0, 1538, 1, "お前って不思議だよな\n一緒にいるとなんだか\n…なんでもねーよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 2, 0, 1, 1, 1, 1539, 1, "気持ちいい朝だな！\n体を動かしたい気分だ\n付き合ってくれるか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 2, 0, 1, 2, 2, 1540, 1, "昼飯もう食ったか？\nまだなら、あたしが\nなんか作ってやるよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 2, 0, 1, 3, 3, 1541, 1, "あんま夜更しすんなよ\n明日に響いたら\n大変だろ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 3, 0, 1, 1, 0, 1542, 1, "た、たまにはその…\nかわいい格好とか\nしても…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 3, 0, 1, 2, 0, 1543, 1, "ふぅ～♪\n疲れがとれるな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 3, 0, 1, 3, 0, 1544, 1, "うっ！　か、かわいい\nじゃねぇか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 3, 0, 1, 4, 0, 1545, 1, "へー　いい匂いだな\nあいつもこういうの\n好きなのかな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 3, 0, 1, 5, 0, 1546, 1, "ふわぁ～！\n温かいと、つい\nウトウトしちまうな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 3, 0, 1, 6, 0, 1547, 1, "オラオラオラーーー！\n燃えるぜーーー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 3, 0, 1, 7, 0, 1548, 1, "いい味してるぜ！\nうちの店でも\n出してみたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104302, 3, 0, 1, 8, 0, 1549, 1, "料理だけじゃなく\n経営術とかも\n学んどかねぇと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 1, 0, 1, 1, 0, 1551, 1, "最近なにも\nしてくれません…\n焦らしてるんですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 1, 0, 1, 2, 0, 1552, 1, "ぐふふ、じゅるり…\nい、いけない、また\nいつの間にかよだれが");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 1, 0, 1, 3, 0, 1553, 1, "好きな武器といえば\n剣より槍より\nやっぱり鞭…ぐふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 1, 0, 1, 4, 0, 1554, 1, "「無数の触手が\nクウカの柔肌に\n食い込み」…ぐふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 1, 0, 1, 5, 0, 1555, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 1, 0, 4, 101, 0, 1556, 1, "ドＳさんを登場\nさせるだけで、妄想に\n無限の広がりが！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 1, 0, 8, 102, 0, 1557, 1, "あっ…\nジロジロ見てたの\n気づいちゃいました？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 2, 0, 1, 1, 0, 1558, 1, "視線を感じると\n思ったら…やはり\nドＳさんでしたか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 2, 0, 1, 2, 0, 1559, 1, "今日は\nどんなヒドイことを\nしてくれるんですか？\nぐふふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 2, 0, 1, 3, 0, 1560, 1, "いつの間にそこに！？\nまさかクウカを監視\nしていたのですか！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 2, 0, 1, 4, 0, 1561, 1, "クウカに触るつもり\nですか？　触るつもり\nなのですねっ！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 2, 0, 4, 101, 0, 1562, 1, "クウカにナニをする\nつもりですか…\n期待してしまいます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 2, 0, 8, 102, 0, 1563, 1, "ドＳさんの視界に入る\nだけでドキドキが…\nこれが恋！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 2, 0, 1, 1, 1, 1564, 1, "おはようございます。\n朝からお盛んですね？\nぐふふ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 2, 0, 1, 2, 2, 1565, 1, "こんなにも日の高い\nうちからドＳさんは…\nじゅるり");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 2, 0, 1, 3, 3, 1566, 1, "夜はこれから、\nですよ…\nぐふ、ぐふふふ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 3, 0, 1, 1, 0, 1567, 1, "キレイにおめかし\nしたのに力ずくで…\nぐふふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 3, 0, 1, 2, 0, 1568, 1, "ソファの背にクウカを\n押し付けて…ぐふふ\nいいですね\nソファドン");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 3, 0, 1, 3, 0, 1569, 1, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 3, 0, 1, 4, 0, 1570, 1, "香りを吸ったクウカの\n頬は紅潮し目はうるみ\nもはや抗う力も…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 3, 0, 1, 5, 0, 1571, 1, "ああ、身体が\n火照って…\nぐふ、ぐふふ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 3, 0, 1, 6, 0, 1572, 1, "はぁ、はぁ、こうして\nクウカの体力を\n奪ってから…ぐふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 3, 0, 1, 7, 0, 1573, 1, "食欲が満たされたら\nつ、次はやはり…！\nそうなのですね！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104501, 3, 0, 1, 8, 0, 1574, 1, "この本の下に、\nそういう本を\nカムフラージュ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 1, 0, 1, 1, 0, 1576, 1, "「ドＳさんと踊り子\n乱舞の一夜」…\n次の日記はこれで…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 1, 0, 1, 2, 0, 1577, 1, "最近なにも\nしてくれません…\n焦らしてるんですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 1, 0, 1, 3, 0, 1578, 1, "も、妄想が\n足りません…\n禁断症状がっ…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 1, 0, 1, 4, 0, 1579, 1, "もしドＳさんが\nＭに転じたらと思うと\n身の毛がよだちます！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 1, 0, 1, 5, 0, 1580, 1, "次はどんなドＳを\nされるのか、想像する\nだけで…ぐふふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 1, 0, 8, 101, 0, 1581, 1, "じっとしてると\nドＳさんのことばかり\n考えてしまいますね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 1, 0, 8, 102, 0, 1582, 1, "妄想が止まらなくて\nクウカどうにかなって\nしまいそうです！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 2, 0, 1, 1, 0, 1583, 1, "ドＳさんといると体が\n火照って…クウカは\nいやらしい子です！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 2, 0, 1, 2, 0, 1584, 1, "あっ…\nジロジロ見てたの\n気づいちゃいました？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 2, 0, 1, 3, 0, 1585, 1, "ドＳさんのおかげで\n新しい扉が開けそう\nです…じゅるり");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 2, 0, 1, 4, 0, 1586, 1, "いやー！　ドＳさんに\n襲われ…襲って\nくれないんですか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 2, 0, 8, 101, 0, 1587, 1, "クウカはドＳさんに\n身も心も堕とされて\nしまったのですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 2, 0, 8, 102, 0, 1588, 1, "ドＳさんがお望みなら\nクウカは\nどんなことだって！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 2, 0, 1, 1, 1, 1589, 1, "朝だからこそ\nできる妄想が\nあります…ぐふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 2, 0, 1, 2, 2, 1590, 1, "こんな日の高いうち\nからドＳさんは…\nじゅるり");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 2, 0, 1, 3, 3, 1591, 1, "今夜は一晩中…\nなんですね？\nぐふふ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 3, 0, 1, 1, 0, 1592, 1, "意外と難しいんですよ\n正しい亀甲縛りと\nいうものは…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 3, 0, 1, 2, 0, 1593, 1, "くつろいで油断\nしているスキに何を\nされるのでしょう！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 3, 0, 1, 3, 0, 1594, 1, "「見た目とは裏腹に\nそれは激しく攻め\nたてて」…ぐふふふ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 3, 0, 1, 4, 0, 1595, 1, "これは禁断の香り…\nああっ一段階上の\n妄想が開花するうぅ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 3, 0, 1, 5, 0, 1596, 1, "ああっ、暖気がまるで\nロウソクのように\nクウカを攻めますっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 3, 0, 1, 6, 0, 1597, 1, "激しい攻めを受けきる\nためには、鍛えないと\nいけないのですね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 3, 0, 1, 7, 0, 1598, 1, "こんなにいっぱい\n入りません…\nお腹にっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104502, 3, 0, 1, 8, 0, 1599, 1, "どんな本も\nよく見ると\nいやらしいです…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 1, 0, 1, 1, 0, 1601, 1, "この前遊んだ猫、\nモッフモフ\nだったにゃあ…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 1, 0, 1, 2, 0, 1602, 1, "ほっぺやお腹が、\n肉球ぐらいぷにぷに…\nこれはまずいにゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 1, 0, 1, 3, 0, 1603, 1, "大きな仕事が\n決まったにゃ！\n腕が鳴るにゃ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 1, 0, 1, 4, 0, 1604, 1, "おやつのたい焼き、\nつぶあんとこしあん、\nどっちに\nしようかにゃ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 1, 0, 1, 5, 0, 1605, 1, "うにゃー…\nもうちょっとお昼寝\nしたかったにゃ…\nむにゃむにゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 1, 0, 4, 101, 0, 1606, 1, "キミに呼ばれると、\n猫まっしぐらな気分に\nなっちゃうにゃ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 1, 0, 8, 102, 0, 1607, 1, "キミのおかげで\nギルドの仕事も\n大成功だにゃ♪\nありがとにゃ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 2, 0, 1, 1, 0, 1608, 1, "うずうず…その指、\nじゃれついても\nいいかにゃ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 2, 0, 1, 2, 0, 1609, 1, "にゃ？　あたしに何か\nご入り用かにゃ？\nお安くしとくにゃ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 2, 0, 1, 3, 0, 1610, 1, "うーん、なんだか\n退屈にゃ～\n遊んで、遊んで～？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 2, 0, 1, 4, 0, 1611, 1, "素早い動きのコツ？\nタダじゃ教えて\nあげられないにゃ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 2, 0, 4, 101, 0, 1612, 1, "そんなにあたしを\nなでたいにゃ？\nキミならなでさせて\nあげてもいいかにゃー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 2, 0, 8, 102, 0, 1613, 1, "ふにゃあ、ねえねえ、\n一緒にゴロゴロ\nしようにゃあ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 2, 0, 1, 1, 1, 1614, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 2, 0, 1, 2, 2, 1615, 1, "むーん、そろそろ\nお昼…猫たちにも\n猫缶を…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 2, 0, 1, 3, 3, 1616, 1, "ふわあ…眠いにゃ…\n猫は布団で\n丸くなるにゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 3, 0, 1, 1, 0, 1617, 1, "客商売では、\n外見のチェックも\n欠かせないのにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 3, 0, 1, 2, 0, 1618, 1, "うにゃ～！\nお昼寝に\nぴったりにゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 3, 0, 1, 3, 0, 1619, 1, "これ…量産したら\n子どもにバカ売れ\nしそうだにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 3, 0, 1, 4, 0, 1620, 1, "この灯り、まるで\n猫みたいな\n癒し効果にゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 3, 0, 1, 5, 0, 1621, 1, "ふにゃあ、\nあったかいと猫は丸く\nなっちゃうのにゃあ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 3, 0, 1, 6, 0, 1622, 1, "本当は外で\n運動したいん\nだけどにゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 3, 0, 1, 7, 0, 1623, 1, "むーん、\n美味しいにゃー！\nたい焼きに\n匹敵するにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104601, 3, 0, 1, 8, 0, 1624, 1, "ふむふむ…猫の\nしつけ方は、と…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 1, 0, 1, 1, 0, 1626, 1, "かわいいネコの置物を\nゲットしたにゃ！\nどこに飾ろうか\nにゃ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 1, 0, 1, 2, 0, 1627, 1, "仕事の交渉がうまく\n行ったにゃ～♪\nいい気分だにゃ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 1, 0, 1, 3, 0, 1628, 1, "ねこじゃらしには\n食いついちゃうにゃ\nびしっ！　びしっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 1, 0, 1, 4, 0, 1629, 1, "ほっぺやお腹が、肉球\nぐらいぷにぷに…\nこれはまずいにゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 1, 0, 1, 5, 0, 1630, 1, "さっきのたいやき\nおいしかったにゃ♪\nごろごろごろ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 1, 0, 8, 101, 0, 1631, 1, "手作りのたい焼き\nあいつは食べて\nくれるかにゃ…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 1, 0, 8, 102, 0, 1632, 1, "あいつには、いつか\nたっぷり恩返しが\nしたいにゃ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 2, 0, 1, 1, 0, 1633, 1, "にゃーに？　あたしの\nことがそんなに気に\nなるのかにゃ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 2, 0, 1, 2, 0, 1634, 1, "あたしのこと…\nしっかり捕まえ\nといてにゃ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 2, 0, 1, 3, 0, 1635, 1, "あたしの力が\n必要かにゃ？　猫の手\n貸してあげるにゃ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 2, 0, 1, 4, 0, 1636, 1, "たいやきとクレープを\n合わせれば…\n新しい商売のニオイが\nするにゃ…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 2, 0, 8, 101, 0, 1637, 1, "ふにゃあ、ねえねえ\n一緒にゴロゴロ\nしようにゃあ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 2, 0, 8, 102, 0, 1638, 1, "キミと一緒にいると\n楽しくなってつい\nじゃれちゃうにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 2, 0, 1, 1, 1, 1639, 1, "いい朝にゃ！\n顔を洗ってくるにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 2, 0, 1, 2, 2, 1640, 1, "そろそろおっひる～\nにゃーんか\n食べたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 2, 0, 1, 3, 3, 1641, 1, "うーん、眠くなって\nきたにゃ。あたしは\n夜行性じゃないのにゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 3, 0, 1, 1, 0, 1642, 1, "にゃにゃ…\n髪が乱れてるにゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 3, 0, 1, 2, 0, 1643, 1, "ふっかふかにゃ～\n猫がねころん\nじゃうにゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 3, 0, 1, 3, 0, 1644, 1, "これ、猫と匹敵する\nぐらいかわいいにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 3, 0, 1, 4, 0, 1645, 1, "猫でもこわくない\n優しい灯りにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 3, 0, 1, 5, 0, 1646, 1, "ぽっかぽか\nしあわせな気分にゃ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 3, 0, 1, 6, 0, 1647, 1, "雨の日でも運動\nできるのは、濡れ\nなくて便利だにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 3, 0, 1, 7, 0, 1648, 1, "美味しいにゃ～！\nこれ、朝昼晩\n食べたいにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104602, 3, 0, 1, 8, 0, 1649, 1, "本を読んで\n猫の気持ちを\n勉強するのにゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 1, 0, 1, 1, 0, 1651, 1, "内職手伝える人\nいないかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 1, 0, 1, 2, 0, 1652, 1, "次の依頼は、\nこういう計画で…\nうん、完璧ね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 1, 0, 1, 3, 0, 1653, 1, "次の特売セールに\n向けて、しっかり\n準備しないと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 1, 0, 1, 4, 0, 1654, 1, "配達の後に売り子\n次に採取のバイト…\nうん、効率的ね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 1, 0, 1, 5, 0, 1655, 1, "一日に受ける\nバイトの数、もう少し\n増やしたいわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 1, 0, 4, 101, 0, 1656, 1, "師匠として\n頼りにされたいし\nもっと頑張らなきゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 1, 0, 8, 102, 0, 1657, 1, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 2, 0, 1, 1, 0, 1658, 1, "あら、\nちょうどよかった\n今から一緒に\nバイトでもどう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 2, 0, 1, 2, 0, 1659, 1, "相談事かしら？\nそれくらいなら\n無料でいいわよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 2, 0, 1, 3, 0, 1660, 1, "背後の気配なら\nすぐ分かるわ\n試してみる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 2, 0, 1, 4, 0, 1661, 1, "道具やお金の管理は\n大丈夫？　手伝うから\nいつでも言って");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 2, 0, 4, 101, 0, 1662, 1, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 2, 0, 8, 102, 0, 1663, 1, "ねえ、あの…\n私はキミにとって…\nいや、何でもないわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 2, 0, 1, 1, 1, 1664, 1, "おはよう！\n今日も朝から\nバイトよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 2, 0, 1, 2, 2, 1665, 1, "お昼時ね。\n少し休憩したら、\nまた働くわよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 2, 0, 1, 3, 3, 1666, 1, "もうこんな時間！\n明日もバイトで\n朝早いのよ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 3, 0, 1, 1, 0, 1667, 1, "接客業の依頼が\nある時は、外見も\nキレイにしないとね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 3, 0, 1, 2, 0, 1668, 1, "はぁ～バイトで\n疲れたから癒される～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 3, 0, 1, 3, 0, 1669, 1, "これを内職で\n作ったら\n儲かるかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 3, 0, 1, 4, 0, 1670, 1, "リフレッシュできたわ\nこれでまた、バリバリ\n働けるわね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 3, 0, 1, 5, 0, 1671, 1, "風邪を引いたら効率が\n落ちちゃうから、\n暖めないと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 3, 0, 1, 6, 0, 1672, 1, "働き続けるには\n健康第一！　それには\n運動が一番よ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 3, 0, 1, 7, 0, 1673, 1, "もぐもぐ…\n腹が減っては\n戦ができぬ、よ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104801, 3, 0, 1, 8, 0, 1674, 1, "日々絶えず学んで、\n生活に活かすのよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 1, 0, 1, 1, 0, 1676, 1, "家計簿をつけなきゃ！\n覚えている時に\nやっておかないとね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 1, 0, 1, 2, 0, 1677, 1, "次の依頼は\nこういう計画で…\nうん、完璧ね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 1, 0, 1, 3, 0, 1678, 1, "ギルドの経費\nあとちょっとだけ\n削れそうね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 1, 0, 1, 4, 0, 1679, 1, "少しでも時給のいい\nバイトがしたいけど\nなかなか\n出会えないのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 1, 0, 1, 5, 0, 1680, 1, "今度のバイトも\nなかなか大変そう\nね…腕が鳴るわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 1, 0, 8, 101, 0, 1681, 1, "男の子と効率的に\n仲良くなる方法…\nこればっかりは難題ね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 1, 0, 8, 102, 0, 1682, 1, "私、どうしたんだろ？\n仕事中もずっと\n彼のこと考えてる…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 2, 0, 1, 1, 0, 1683, 1, "この間はヘルプに\n来てくれてありがとう\n頼んで正解だったわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 2, 0, 1, 2, 0, 1684, 1, "見つけた！　ちょうど\n頼みたいことが\nあるんだけど、いい？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 2, 0, 1, 3, 0, 1685, 1, "今から\n休憩するところなの\n一緒にどう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 2, 0, 1, 4, 0, 1686, 1, "ん？　どうしたの？\n私のことが\n気になるの？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 2, 0, 8, 101, 0, 1687, 1, "…たまには甘えても\nいい？　私だって\n弱る時はあるのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 2, 0, 8, 102, 0, 1688, 1, "暇ができたから\n働きたいけど…\nキミとの時間も大切ね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 2, 0, 1, 1, 1, 1689, 1, "おはよう！\nいい朝ね。一緒に\nランニングでもいく？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 2, 0, 1, 2, 2, 1690, 1, "お昼時ね\n少し休憩したら\nまた働くわよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 2, 0, 1, 3, 3, 1691, 1, "もうこんな時間！\n明日もバイトで\n朝早いのよ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 3, 0, 1, 1, 0, 1692, 1, "女の子らしく…\nどうも忘れちゃう\nのよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 3, 0, 1, 2, 0, 1693, 1, "ここで少し\n休んだら\nまたバイトよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 3, 0, 1, 3, 0, 1694, 1, "これを内職で\n作ったら\n儲かるかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 3, 0, 1, 4, 0, 1695, 1, "仕事から帰ってきて\nこういうのがあると\n素敵ね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 3, 0, 1, 5, 0, 1696, 1, "しっかり身体を\n温めて、血流を\nよくしなくっちゃ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 3, 0, 1, 6, 0, 1697, 1, "これで休憩中も\n無駄なく\n鍛えられるわ♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 3, 0, 1, 7, 0, 1698, 1, "もぐもぐ…よく\n食べて、よく働く！\nそれが私のモットーよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104802, 3, 0, 1, 8, 0, 1699, 1, "日々絶えず学んで\n生活に活かすのよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104901, 1, 0, 1, 1, 0, 1701, 1, "暇だし、お料理と\nお掃除とお洗濯と\n買い物しようかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104901, 1, 0, 1, 2, 0, 1702, 1, "こうして歩いていれば\n弟くんが声をかけて\nくれるはず…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104901, 1, 0, 1, 3, 0, 1703, 1, "弟くんがどんな生活を\nしてるのか調べるのも\nお姉ちゃんの務め！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104901, 1, 0, 1, 4, 0, 1704, 1, "くんくん、\n弟くんのにおい…\n近くにいるね？");
