INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104901, 2, 0, 1, 1, 1, 1714, 1, "着替えは？　荷物は？\nお出かけの準備は\nお姉ちゃんが手伝うね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (104901, 2, 0, 1, 2, 2, 1715, 1, "出かけるなら\n明るいうちに\n帰ってくるんだよ！");
