INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 1, 0, 1, 2, 0, 1752, 1, "身長とバスト以外は\n完璧な大人\nなのになぁ～");
