INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 2, 0, 1, 1, 0, 1758, 1, "わーい、アメだー♪\n食べる食べるー♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 2, 0, 1, 2, 0, 1759, 1, "熱いハニーラテが\n飲みたーい！\n淹れてー淹れてー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 2, 0, 1, 3, 0, 1760, 1, "なによ？　もしかして\n好きな子にイタズラ\nしたくなるってやつ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 2, 0, 1, 4, 0, 1761, 1, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ、\nデザインとかの話よ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 2, 0, 4, 101, 0, 1762, 1, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 2, 0, 8, 102, 0, 1763, 1, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 2, 0, 1, 1, 1, 1764, 1, "朝のスキンケアは\n大事なの！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 2, 0, 1, 2, 2, 1765, 1, "お腹すいたー\nダイエット\nやめちゃおうかなー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 2, 0, 1, 3, 3, 1766, 1, "エイジングケアを\nしないとねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 3, 0, 1, 1, 0, 1767, 1, "この角度のほうが\n大人っぽく見えるかな\nそれともこっちかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 3, 0, 1, 2, 0, 1768, 1, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 3, 0, 1, 3, 0, 1769, 1, "か、かわいい♪　けど\n大人はこんなもの…\nでも、かわいいし～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 3, 0, 1, 4, 0, 1770, 1, "オシャレ～♪\n大人なレディって\n感じよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 3, 0, 1, 5, 0, 1771, 1, "あったかーい♪\nここで宿題\nしようかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 3, 0, 1, 6, 0, 1772, 1, "運動したら成長\nしないかしら？\n背とか胸とか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 3, 0, 1, 7, 0, 1773, 1, "食べすぎると\n太っちゃうな…\nでも、おいしいし～っ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105001, 3, 0, 1, 8, 0, 1774, 1, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 1, 0, 1, 1, 0, 1776, 1, "レディは、どんな時も\nおしとやかに…って\nアタシも騒ぎたいー！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 1, 0, 1, 2, 0, 1777, 1, "恋人にするなら\nイケメンか\nただイケよね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 1, 0, 1, 3, 0, 1778, 1, "もう少し背が欲しいな\n…もっと牛乳を\n飲まなくちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 1, 0, 1, 4, 0, 1779, 1, "朝顔の観察したら\n理系女子になれる\nかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 1, 0, 1, 5, 0, 1780, 1, "こ、これは補習の\nお知らせプリント！\nアイツに見つかる前に\n隠さなくちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 1, 0, 8, 101, 0, 1781, 1, "アイツおっそいなー\n別に会いたいわけじゃ\nないけど…\n来ないと気になる…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 1, 0, 8, 102, 0, 1782, 1, "アタシが待ってる\nなんて、アイツは\n幸せものよねー ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 2, 0, 1, 1, 0, 1783, 1, "なーに？\nアタシに甘えたいの？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 2, 0, 1, 2, 0, 1784, 1, "アタシに合う服\n雑誌で探そうっと！\nサイズじゃないわ\nデザインとかの話よ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 2, 0, 1, 3, 0, 1785, 1, "頭撫でないで！\nお子様みたいに\nあつかわないでって\n言ってるのに～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 2, 0, 1, 4, 0, 1786, 1, "壁ドンの練習…\nあれ？　壁ドンって\n男がするんだっけ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 2, 0, 8, 101, 0, 1787, 1, "ねーねー！\nアタシ、かわいい？\nかわいいわよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 2, 0, 8, 102, 0, 1788, 1, "これから出かけるの？\nしっかりアタシを\nエスコートしてね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 2, 0, 1, 1, 1, 1789, 1, "レディの朝は\n忙しいのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 2, 0, 1, 2, 2, 1790, 1, "お腹すいたー\nダイエット\nやめちゃおうかなー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 2, 0, 1, 3, 3, 1791, 1, "エイジングケアを\nしないとねー");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 3, 0, 1, 1, 0, 1792, 1, "身だしなみチェック\nオッケー！　これで\nいつアイツが来ても\n大丈夫ね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 3, 0, 1, 2, 0, 1793, 1, "あ！　これ雑誌で\n見たことあるわ～\nだからきっと\nオシャレなのよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 3, 0, 1, 3, 0, 1794, 1, "あたしがもっと\nオシャレにかわいく\nしてあげようか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 3, 0, 1, 4, 0, 1795, 1, "オシャレ～♪\n大人なレディって\n感じよね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 3, 0, 1, 5, 0, 1796, 1, "あったかーい♪\nここで宿題\nしようかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 3, 0, 1, 6, 0, 1797, 1, "運動したら成長\nしないかしら？\n背とか胸とか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 3, 0, 1, 7, 0, 1798, 1, "レディは、食べ物にも\n気を使うのよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105002, 3, 0, 1, 8, 0, 1799, 1, "おもしろーい！\n…でも絵本って\n子供っぽいかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 1, 0, 1, 1, 0, 1801, 1, "魔法と科学\nどっちも極めて\nみせるわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 1, 0, 1, 2, 0, 1802, 1, "仕事も片付いたし\n部屋で\n実験実験…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 1, 0, 1, 3, 0, 1803, 1, "たまった治療代の\n請求書を\nまとめないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 1, 0, 1, 4, 0, 1804, 1, "新薬が出来たし\nすぐに試したいけど…\n実験台がいないわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 1, 0, 1, 5, 0, 1805, 1, "部屋にこもりすぎると\n曜日の感覚が\nなくなるわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 1, 0, 4, 101, 0, 1806, 1, "あの子の記憶を\n取り戻せる薬…\n作れないものかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 1, 0, 8, 102, 0, 1807, 1, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 2, 0, 1, 1, 0, 1808, 1, "あら、ヒマなら\n薬草の整理を\n手伝って");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 2, 0, 1, 2, 0, 1809, 1, "もしかして実験に\n付き合ってくれるの？\nえ？　違う？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 2, 0, 1, 3, 0, 1810, 1, "具合が悪い？\nなら診てあげる\nまずは触診から…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 2, 0, 1, 4, 0, 1811, 1, "植物に興味あるの？\nなら教えてあげるわ\n色々とね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 2, 0, 4, 101, 0, 1812, 1, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 2, 0, 8, 102, 0, 1813, 1, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 2, 0, 1, 1, 1, 1814, 1, "お昼までに調合用の\n花を集めたいの\n手伝ってくれる？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 2, 0, 1, 2, 2, 1815, 1, "あら、もうお昼？\nそろそろ一息\n入れましょうか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 2, 0, 1, 3, 3, 1816, 1, "夜も更けて来たわね\n私は朝まで実験だけど\n見学していく？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 3, 0, 1, 1, 0, 1817, 1, "医者たるもの\nいつだって\n清潔に…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 3, 0, 1, 2, 0, 1818, 1, "いいわねこれ\n仮眠の時に\n使わせてもらうわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 3, 0, 1, 3, 0, 1819, 1, "ぬいぐるみなら\n動物も…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 3, 0, 1, 4, 0, 1820, 1, "いい香り…\n仕事場にも\n置いてみようかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 3, 0, 1, 5, 0, 1821, 1, "ここにいると\nついウトウト\nしちゃうわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 3, 0, 1, 6, 0, 1822, 1, "座ってる時間が多いと\n運動不足になるし\nたまにはいいわね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 3, 0, 1, 7, 0, 1823, 1, "研究していると\nつい食べるのを\n忘れちゃうのよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105101, 3, 0, 1, 8, 0, 1824, 1, "実験もいいけど\nいつか成果を\n本にしないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 1, 0, 1, 1, 0, 1826, 1, "徹夜はツラいけど\n集中すると時間の\n感覚がなくなって…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 1, 0, 1, 2, 0, 1827, 1, "痛みに悶える顔って\nどうしてあんなに\n興奮するのかしら？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 1, 0, 1, 3, 0, 1828, 1, "彼に罵られたら\nどんな気分かしら\n意外と快感かも？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 1, 0, 1, 4, 0, 1829, 1, "毒を薄めると\n薬になるのって\n今でも不思議だわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 1, 0, 1, 5, 0, 1830, 1, "綺麗なバラには\nトゲがあるけど\n魅力の一つよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 1, 0, 8, 101, 0, 1831, 1, "カレをさりげなく\n惚れさせる方法を\n研究するわよ…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 1, 0, 8, 102, 0, 1832, 1, "好意に気づいて貰える\n薬って、どうすれば\n作れるのかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 2, 0, 1, 1, 0, 1833, 1, "構って欲しいの？\nじゃあ、君の体を\n調べさせて？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 2, 0, 1, 2, 0, 1834, 1, "実験に付き合って\n大丈夫よ、試すのは\nただの栄養剤だから");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 2, 0, 1, 3, 0, 1835, 1, "欲しい薬が\nあったら言ってね\n調合してみるから");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 2, 0, 1, 4, 0, 1836, 1, "おしおきされたいの？\nキミにだったら\nいつでもしてあげる");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 2, 0, 8, 101, 0, 1837, 1, "キミの傍にいるだけで\n高揚しちゃうのは\n何故かしら…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 2, 0, 8, 102, 0, 1838, 1, "私はおしおきする\n側のことが多いけど…\nキミにはされたいかも");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 2, 0, 1, 1, 1, 1839, 1, "朝しか取れない\n植物があるの\n採取しに行くわよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 2, 0, 1, 2, 2, 1840, 1, "お昼時ってやけに\n眠たくなるわよね\n…調べたくなるわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 2, 0, 1, 3, 3, 1841, 1, "今日くらいは\nゆっくり眠りたいわ\n…キミと一緒にね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 3, 0, 1, 1, 0, 1842, 1, "身なりについて\n研究するのも\n楽しそうね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 3, 0, 1, 2, 0, 1843, 1, "休息に必要な道具は\n妥協せずに選ぶ！\nこれだけは譲れないわ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 3, 0, 1, 3, 0, 1844, 1, "たまにはこういう\n可愛らしいものに\n触れないとね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 3, 0, 1, 4, 0, 1845, 1, "いい香り…\n仕事場にも\n置いてみようかしら");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 3, 0, 1, 5, 0, 1846, 1, "仕事がなかったら\nこのまますーっと\n眠りたいのに…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 3, 0, 1, 6, 0, 1847, 1, "運動しないと\n体型の維持が…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 3, 0, 1, 7, 0, 1848, 1, "適度な食事も\n大切よね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105102, 3, 0, 1, 8, 0, 1849, 1, "まさか私が\n恋愛読本を読む\n日が来るなんて…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 1, 0, 1, 1, 0, 1851, 1, "ぺっぺっ！\n…い、いけないっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 1, 0, 1, 2, 0, 1852, 1, "いっけな～い！\nひづめで床に傷が！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 1, 0, 1, 3, 0, 1853, 1, "太っちゃったかも～！\n伐採でダイエット\nしなくっちゃ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 1, 0, 1, 4, 0, 1854, 1, "きちんと生活すると\n毛づやが良くなる\nみたいなのよね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 1, 0, 1, 5, 0, 1855, 1, "みんな、体重\nどれくらいなのかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 1, 0, 4, 101, 0, 1856, 1, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 1, 0, 8, 102, 0, 1857, 1, "いつか人間の女の子\nみたいなデート、\nしてみたいな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 2, 0, 1, 1, 0, 1858, 1, "私ね「どこにでもいる\n普通の女の子」って\n言葉が憧れなんだ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 2, 0, 1, 2, 0, 1859, 1, "チャームポイント？\n自分では分からない\nけど…まつげかなぁ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 2, 0, 1, 3, 0, 1860, 1, "リマリマ～♪\nはっ！？\nわ、私ったら今何を？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 2, 0, 1, 4, 0, 1861, 1, "力仕事ならまかせて！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 2, 0, 4, 101, 0, 1862, 1, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 2, 0, 8, 102, 0, 1863, 1, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 2, 0, 1, 1, 1, 1864, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 2, 0, 1, 2, 2, 1865, 1, "ランチは\nおしゃれなカフェで\nしてみたいな～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 2, 0, 1, 3, 3, 1866, 1, "お肌のケアに\nパックしながら\n寝ようかな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 3, 0, 1, 1, 0, 1867, 1, "やだっ、\nまた寝ぐせついてる！\n直さないと！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 3, 0, 1, 2, 0, 1868, 1, "ミシッっていった！\n私、重すぎるかも！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 3, 0, 1, 3, 0, 1869, 1, "あ！　あなたも\n美人さんね～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 3, 0, 1, 4, 0, 1870, 1, "はぁ、まったり…\n四足歩行しちゃい\nそうだわ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 3, 0, 1, 5, 0, 1871, 1, "あったか～い♪\n…私、毛皮だから\n暑すぎるかも");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 3, 0, 1, 6, 0, 1872, 1, "な、なんだか野生に\n帰っちゃいそう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 3, 0, 1, 7, 0, 1873, 1, "ぱくぱく、もぐもぐ、\nぺっぺっ…\nやだ、はしたない！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105201, 3, 0, 1, 8, 0, 1874, 1, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 1, 0, 1, 1, 0, 1876, 1, "素敵ね～、居るだけで\n女子力があがりそうな\nお部屋だわ～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 1, 0, 1, 2, 0, 1877, 1, "私、大丈夫かしら？\n他の子に怖がられたり\nしないかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 1, 0, 1, 3, 0, 1878, 1, "動物苑…って\n嫌なギルド名ね…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 1, 0, 1, 4, 0, 1879, 1, "好き、嫌い、好き…\nあっ！　うっかりして\n花占いのお花\n食べちゃった！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 1, 0, 1, 5, 0, 1880, 1, "ここ、女の子らしい\nものでいっぱいね～！\nときめいちゃうわ～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 1, 0, 8, 101, 0, 1881, 1, "体力なら自信\nあるんだけど…\n恋にはちょっぴり\n臆病になっちゃう");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 1, 0, 8, 102, 0, 1882, 1, "あの人が\n来てくれるまで薪割り\nでもしてよっかな？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 2, 0, 1, 1, 0, 1883, 1, "一緒にお出かけ？\nうれしいけど…\n心の準備が！\nはわわわ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 2, 0, 1, 2, 0, 1884, 1, "どうしたの？\n急に抱きついてきて…\n癒されたいの？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 2, 0, 1, 3, 0, 1885, 1, "ぺっぺっ！\n…い、いけないっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 2, 0, 1, 4, 0, 1886, 1, "メタモルアップルを\nここで食べたら、\nみんな驚くわよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 2, 0, 8, 101, 0, 1887, 1, "しょうがないなあ、\nこんなにモフらせるの\nあなただけだよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 2, 0, 8, 102, 0, 1888, 1, "人間になりたいな～\nそれか、あなたが\n私みたいな姿に…♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 2, 0, 1, 1, 1, 1889, 1, "今朝は寝癖がひどいわ\nしっぽの辺りとか～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 2, 0, 1, 2, 2, 1890, 1, "ダイエット中だから\nお昼は抜いてるの～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 2, 0, 1, 3, 3, 1891, 1, "お肌のケアに\nパックしながら\n寝ようかな～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 3, 0, 1, 1, 0, 1892, 1, "ちゃんとかわいく\nしてないと刈られ…\n狩られちゃうもんね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 3, 0, 1, 2, 0, 1893, 1, "ミシッっていった！\n私、重すぎるかも！？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 3, 0, 1, 3, 0, 1894, 1, "あ！　あなたも\n美人さんね～♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 3, 0, 1, 4, 0, 1895, 1, "いい香り～\n森の中にいるみたいに\nとっても落ち着く～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 3, 0, 1, 5, 0, 1896, 1, "素敵～♪　…\nきゃっ！　私の毛が\nたいへんなことに～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 3, 0, 1, 6, 0, 1897, 1, "な、なんだか野生に\n帰っちゃいそう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 3, 0, 1, 7, 0, 1898, 1, "何を食べるかより\n誰と食べるかが\n大事なのよね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105202, 3, 0, 1, 8, 0, 1899, 1, "きゃーーー！\nこの恋愛小説\nとっても\nドキドキしちゃう～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 1, 0, 1, 1, 0, 1901, 1, "ヴァイスフリューゲル\nを盛り上げる方法、\n共に語らおう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 1, 0, 1, 2, 0, 1902, 1, "私は子供ではない…！\nが、お菓子は\nいただこう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 1, 0, 1, 3, 0, 1903, 1, "部屋はきちんと\n片づけておかなくては\n部屋の汚れは\n心の汚れだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 1, 0, 1, 4, 0, 1904, 1, "むむむ…明日\n目が覚めたら、身長が\n伸びていないものか…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 1, 0, 1, 5, 0, 1905, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 1, 0, 4, 101, 0, 1906, 1, "あいつとの新たな\n鍛錬が\n待ち遠しいな…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 1, 0, 8, 102, 0, 1907, 1, "最近、あいつと\n過ごしていて、毎日が\n充実しているな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 2, 0, 1, 1, 0, 1908, 1, "軍人の本分は日々の\n研鑽にあるからな。\n今日も鍛錬に\n行かないか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 2, 0, 1, 2, 0, 1909, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 2, 0, 1, 3, 0, 1910, 1, "貴公にも、飴をくれて\nやろうか？\nあまいぞ～！\n美味しいぞ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 2, 0, 1, 4, 0, 1911, 1, "私ともっと高く、\n共に飛ぼう！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 2, 0, 4, 101, 0, 1912, 1, "貴公は、本当に頼りに\nなる男だな…いや、\nふとそう思っただけだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 2, 0, 8, 102, 0, 1913, 1, "貴公と共にいられる\nことは私の喜びだ。\nこれからも\nよろしく頼むぞ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 2, 0, 1, 1, 1, 1914, 1, "うむ、いい朝だ。\n本国の夜明けを\n思い出してしまうな！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 2, 0, 1, 2, 2, 1915, 1, "そろそろ昼食に\nするか。腹が減っては\n戦はできないからな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 2, 0, 1, 3, 3, 1916, 1, "ふわああ…私は子供\nではないが…眠い時は\n眠いのだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 3, 0, 1, 1, 0, 1917, 1, "身だしなみは、心の\n在りようを映すから\n気をつけなくてはな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 3, 0, 1, 2, 0, 1918, 1, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 3, 0, 1, 3, 0, 1919, 1, "うむ…このかわいさ…\n本国に持って\n帰りたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 3, 0, 1, 4, 0, 1920, 1, "このともし火、戦いに\n明け暮れる我らの心を\n癒すものだな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 3, 0, 1, 5, 0, 1921, 1, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 3, 0, 1, 6, 0, 1922, 1, "うおおおおおっ！\n鍛錬、鍛錬、\n鍛錬っ！！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 3, 0, 1, 7, 0, 1923, 1, "いつ戦いになっても\n困らぬように、\n腹八分目に\nしておくのだ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105301, 3, 0, 1, 8, 0, 1924, 1, "これを読んで、新たな\n戦術を編み出すぞっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 1, 0, 1, 1, 0, 1926, 1, "たまには掃除でも\nするか。部屋が\n綺麗になるのは\n喜ばしいことだ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 1, 0, 1, 2, 0, 1927, 1, "部屋の中では\n剣を振り回せないのが\n玉に瑕だな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 1, 0, 1, 3, 0, 1928, 1, "お菓子が常備されて\nいる部屋。そんな\nものが、私の好みだ…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 1, 0, 1, 4, 0, 1929, 1, "毎日牛乳を飲んでいる\nのだが、なかなか背が\n伸びないものだな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 1, 0, 1, 5, 0, 1930, 1, "家庭を守れない者が\n国を守れるはずが\nない。この部屋も\n私が守るぞ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 1, 0, 8, 101, 0, 1931, 1, "あいつは今頃、何を\nしているのだろうな…\nふふっ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 1, 0, 8, 102, 0, 1932, 1, "あいつと会える日が\nこんなにも待ち遠しく\nなるとはな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 2, 0, 1, 1, 0, 1933, 1, "軍人の本分は日々の\n研鑽にあるからな\n今日も鍛錬に\n行かないか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 2, 0, 1, 2, 0, 1934, 1, "おお、今日も鍛錬の\n時間だったか！\nすまないな\nすぐ行く！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 2, 0, 1, 3, 0, 1935, 1, "私は子供ではない！\nが…たまには童心に\n帰りたい時もある…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 2, 0, 1, 4, 0, 1936, 1, "貴公にも、飴を\nくれてやろうか？\nあまいぞ～！\n美味しいぞ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 2, 0, 8, 101, 0, 1937, 1, "私と歩きたいのか…？\nそうだな。二人で\n散歩でも行こうか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 2, 0, 8, 102, 0, 1938, 1, "貴公と共にいられる\nことは私の喜びだ\nこれからも\nよろしく頼むぞ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 2, 0, 1, 1, 1, 1939, 1, "うむ、いい朝だ\n本国の夜明けを\n思い出してしまうな！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 2, 0, 1, 2, 2, 1940, 1, "そろそろ昼時だな\n鍛錬の手を緩めて\n昼食にするか");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 2, 0, 1, 3, 3, 1941, 1, "寝る前に…あと少し\nだけ、鍛錬を…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 3, 0, 1, 1, 0, 1942, 1, "ヴァイス\nフリューゲルの一員と\nして恥ずかしくないよう\n身なりを整えなくては");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 3, 0, 1, 2, 0, 1943, 1, "ふう…軍人たるもの\n常日頃、油断は\nできないのだが…\nお、落ち着くな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 3, 0, 1, 3, 0, 1944, 1, "うむ…このかわいさ…\n本国に持って\n帰りたい…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 3, 0, 1, 4, 0, 1945, 1, "このともし火\n戦いに明け暮れる\n我らの心を\n癒すものだな…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 3, 0, 1, 5, 0, 1946, 1, "はあ～、ぽかぽか…\nい、いや！\n気を緩めないぞっ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 3, 0, 1, 6, 0, 1947, 1, "うおおおおおっ！\n鍛錬、鍛錬\n鍛錬っ！！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 3, 0, 1, 7, 0, 1948, 1, "いつ戦いになっても\n困らぬように\n腹八分目に\nしておくのだ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105302, 3, 0, 1, 8, 0, 1949, 1, "うむ…脳細胞が\n活性化してきたぞ\n読書は大事だな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 1, 0, 1, 1, 0, 1951, 1, "私たちの騎空艇\nどんなデザインに\nしよう…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 1, 0, 1, 2, 0, 1952, 1, "団員募集の\nポスター\n作ろうっと♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 1, 0, 1, 3, 0, 1953, 1, "父さん…\n今頃どこで\n何してるんだろ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 1, 0, 1, 4, 0, 1954, 1, "騎空艇に乗って、\n誰もいったことがない\n場所を見つけたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 1, 0, 1, 5, 0, 1955, 1, "父さん以上の\n世界一の\n騎空士になりたい！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 1, 0, 4, 101, 0, 1956, 1, "仲間と行く空の旅…\n何度想像しても\nドキドキするなぁ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 1, 0, 8, 102, 0, 1957, 1, "父さん…\n私にもね、大切な\n仲間ができたよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 2, 0, 1, 1, 0, 1958, 1, "仕事の依頼？\nすぐに支度\nしてくるね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 2, 0, 1, 2, 0, 1959, 1, "ねえねえ！\n団員募集のポスター\n一緒に作ろうよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 2, 0, 1, 3, 0, 1960, 1, "なーにー？\nどうしたの？\n今呼んだよね？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 2, 0, 1, 4, 0, 1961, 1, "今は休憩中だよ？\nだからあなたも\nゆっくりしてて");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 2, 0, 4, 101, 0, 1962, 1, "私にとってあなたは\nとっても頼りになる、\n自慢の団員だよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 2, 0, 8, 102, 0, 1963, 1, "お父さんに会えたら、\n大切な仲間として\nあなたを紹介するね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 2, 0, 1, 1, 1, 1964, 1, "おはよう！\n体の調子はどう？\n今日は頑張れそう？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 2, 0, 1, 2, 2, 1965, 1, "ねえねえ、お昼\n食べに行こうよ！\nお腹空いちゃった");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 2, 0, 1, 3, 3, 1966, 1, "…あれ？\nおしゃべりしてたら\nもうこんな時間？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 3, 0, 1, 1, 0, 1967, 1, "どうせならオシャレな\n騎空士って\n呼ばれたいな");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 3, 0, 1, 2, 0, 1968, 1, "ふぅ、疲れた～…\nちょっとだけ\n休んじゃおっと");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 3, 0, 1, 3, 0, 1969, 1, "よし！　キミは\n今から団長代理だ！\n…なんてね♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 3, 0, 1, 4, 0, 1970, 1, "いい香り～♪\n気持ちが\n落ち着く～…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 3, 0, 1, 5, 0, 1971, 1, "これって\n騎空艇に\n置けるかな…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 3, 0, 1, 6, 0, 1972, 1, "どんな仕事でも\nこなせるような\n体力がないとね…！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 3, 0, 1, 7, 0, 1973, 1, "あむっ…うん！\n美味しい～！\n今度作ってみよう♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105701, 3, 0, 1, 8, 0, 1974, 1, "空を冒険するには\n色んなことを\n知っておかないと…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 1, 0, 1, 1, 0, 1976, 1, "…戻らなくなったよ\nどうすれば元に\n戻る…？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 1, 0, 1, 2, 0, 1977, 1, "暇だよ…\nどこかに強い人は\nいないか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 1, 0, 1, 3, 0, 1978, 1, "ジョブチェンジ！\n…叫んでも\n戻らないよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 1, 0, 1, 4, 0, 1979, 1, "今のうちに、\n魔物退治の\n仕事に行くね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 1, 0, 1, 5, 0, 1980, 1, "ハァ～…\nうん、気の巡りは\n問題ないね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 1, 0, 8, 101, 0, 1981, 1, "私の力の源は\n仲間との\n絆だよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 1, 0, 8, 102, 0, 1982, 1, "いつかこの拳を\n空を越えた、星まで\n届かせたいね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 2, 0, 1, 1, 0, 1983, 1, "功夫が足りない人は\nどうすればいいか？\nもちろん修行だよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 2, 0, 1, 2, 0, 1984, 1, "私はいつも通りに\n喋ってるつもり\nだよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 2, 0, 1, 3, 0, 1985, 1, "どうした？\nもしかして手合せ\nしたいか？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 2, 0, 1, 4, 0, 1986, 1, "騎空士としても\n拳法家としても\n目指すは世界一ね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 2, 0, 8, 101, 0, 1987, 1, "背中はあなたに\n預けたよ！\nよろしくね！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 2, 0, 8, 102, 0, 1988, 1, "あなたと一緒なら\nきっと、今よりもっと\n強くなれるよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 2, 0, 1, 1, 1, 1989, 1, "ちゃんと起きてるか？\n寝ぼけてたら\nダメよ？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 2, 0, 1, 2, 2, 1990, 1, "腹減ったね…\nなにか食べに\n行くよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 2, 0, 1, 3, 3, 1991, 1, "修行のしすぎは\n良くないよ？\n気をつけるね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 3, 0, 1, 1, 0, 1992, 1, "美しい技は\n美しい体が\nあってこそ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 3, 0, 1, 2, 0, 1993, 1, "よいしょっと…\nちょっとのんびり\nさせてもらうよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 3, 0, 1, 3, 0, 1994, 1, "…練習に\n使うのはさすがに\n可哀そうね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 3, 0, 1, 4, 0, 1995, 1, "これは…\n瞑想の時にも\n使ってみたいね");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 3, 0, 1, 5, 0, 1996, 1, "出来ればもっと\n熱いほうが\n嬉しいよ");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 3, 0, 1, 6, 0, 1997, 1, "ここでも\n修行出来るなんて\n最高だよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 3, 0, 1, 7, 0, 1998, 1, "気力の源は体力！\n体力の源は\n健康とご飯！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105702, 3, 0, 1, 8, 0, 1999, 1, "んー…\n難しいことは\nわかんないよ！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105801, 1, 0, 1, 1, 0, 2001, 1, "わたしたちの\nギルドハウスへ\nようこそ～！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105801, 1, 0, 1, 2, 0, 2002, 1, "ぐるるる～…\nあ、お腹が\n鳴っちゃいました…");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105801, 1, 0, 1, 3, 0, 2003, 1, "ん？\n食べ物のにおい？");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105801, 1, 0, 1, 4, 0, 2004, 1, "みんなと仲良く食卓を\n囲みたいですね～");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105801, 1, 0, 1, 5, 0, 2005, 1, "この部屋が、わたしの\n帰る場所です♪");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105801, 1, 0, 4, 101, 0, 2006, 1, "あの人を見ると\n胸が満たされます～\nお腹は空いてますが！");
INSERT INTO `v1_31b01a8c067e9466863e95cc74bf2910a2c7c2f7378da9a92b897ae6bdea7fac` VALUES (105801, 1, 0, 8, 102, 0, 2007, 1, "あの人、\n大食いの子って\n嫌いかな？");
