INSERT INTO `v1_324b69f34b2c1eee41defd71e1113960bdab70cbadc1a94bd736b2c886af53ef` VALUES ("界雷", 2017, 137, "魔法攻撃のダメージが小アップし、物理攻撃を行うキャラが行動する度にそのキャラの最大ＨＰに応じて最大99999の割合ダメージを与える。");
INSERT INTO `v1_324b69f34b2c1eee41defd71e1113960bdab70cbadc1a94bd736b2c886af53ef` VALUES ("百花繚乱", 2017, 146, "一定時間が経過するごとに、敵、味方全体のＴＰを中回復させる。");
INSERT INTO `v1_324b69f34b2c1eee41defd71e1113960bdab70cbadc1a94bd736b2c886af53ef` VALUES ("炎天烈日", 2017, 147, "一定時間が経過するごとに、敵、味方全体に最大ＨＰに応じた最大20万の割合ダメージを与える。さらに、付与される継続ダメージが上昇する。");
INSERT INTO `v1_324b69f34b2c1eee41defd71e1113960bdab70cbadc1a94bd736b2c886af53ef` VALUES ("秋高馬肥", 2017, 148, "一定時間が経過するごとに、敵、味方全体の物理攻撃力、魔法攻撃力、物理防御力、魔法防御力をアップさせる。この効果の効果量は敵、味方を合わせた数に応じて増加する。");
INSERT INTO `v1_324b69f34b2c1eee41defd71e1113960bdab70cbadc1a94bd736b2c886af53ef` VALUES ("寒気凛冽", 2017, 149, "敵、味方全体の行動速度を小ダウンさせる。このスキルの効果は他の行動速度変化スキルと重複する。さらに一定時間が経過するごとに、初期値より行動速度が低い状態のキャラに凍結状態を付与する。");
INSERT INTO `v1_324b69f34b2c1eee41defd71e1113960bdab70cbadc1a94bd736b2c886af53ef` VALUES ("妨魔塵", 2017, 162, "魔法攻撃のダメージが小ダウンする。");
