INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ペコリーヌのプリン", 100, 1, "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", "やばいですね☆なの！", 3, 5, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", 1);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("コッコロのプリン", 101, 1, "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", "主さま、なの！", 3, 5, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", 2);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("キャルのプリン", 102, 0, "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", 3, 5, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", 3);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("シェフィのプリン", 103, 1, "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", "おにーたん♪なの！", 3, 5, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", 4);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("イリヤのプリン", 104, 1, "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", "ハーハッハッハ！\n世界征服なのじゃなのー！", 3, 5, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", 5);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("アカリのプリン", 105, 1, "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", "お兄ちゃんのエッチ！　なの！", 3, 5, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", 6);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ヨリのプリン", 106, 1, "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", 3, 5, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", 7);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("シノブのプリン", 107, 0, "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", 3, 5, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", 8);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ジュンのプリン", 108, 1, "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", "インフェルノシールド！なの！", 3, 5, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", 9);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("トモのプリン", 109, 1, "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", 3, 5, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", 10);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("マツリのプリン", 110, 1, "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", "タイガーヒーローボンバー！\nなの！", 3, 5, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", 11);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("クロエのプリン", 111, 1, "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", "ま、いいんじゃん？\nしらんけどなの", 3, 5, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", 13);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("チエルのプリン", 112, 1, "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", "ぷりーん★なの！", 3, 5, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", 14);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ユニのプリン", 113, 1, "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", "端的に換言すれば\n青春の味なの？", 3, 5, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", 15);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("サレンのプリン", 114, 1, "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", "幼なじみはあんた…なの？", 3, 5, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", 16);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("スズメのプリン", 115, 1, "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", "ひゃああんなの～！", 3, 5, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", 17);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("クルミのプリン", 116, 0, "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", "サルキデバレヤ！なの！", 3, 5, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", 18);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("アヤネのプリン", 117, 1, "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", "プリンフルスイングなの～！", 3, 5, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", 19);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("マホのプリン", 118, 1, "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", "みらくるみやりん\nくるりんぱ～☆なの！", 3, 5, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", 20);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("マコトのプリン", 119, 1, "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", "ウルフェンバイトなの！", 3, 5, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", 21);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("カオリのプリン", 120, 1, "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", "なんくるないさ～！なの！", 3, 5, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", 22);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("カスミのプリン", 121, 1, "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", "ふふん。簡単な推理なの", 3, 5, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", 23);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("マヒルのプリン", 122, 1, "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", "エリザベス～！なの～！", 3, 5, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", 24);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("シオリのプリン", 123, 1, "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", "私の好きな本…読んで\nもらえたらうれしいです…\nなの", 3, 5, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", 25);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("リマのプリン", 124, 1, "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", "もふもふにしてあげるなの！", 3, 5, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", 26);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("リンのプリン", 125, 0, "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", "めんどくさいな～なの…", 3, 5, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", 27);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ラビリスタのプリン", 126, 1, "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", "プリンの権能を解放するの～！", 3, 5, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", 28);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("シズルのプリン", 127, 0, "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", "お姉ちゃんパワー全開！なの！", 3, 5, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", 29);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("リノのプリン", 128, 1, "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", "お兄ちゃん、プリンをよこすの！", 3, 5, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", 30);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ヒヨリのプリン", 129, 1, "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", "困った時はお互いさまさま！\nなの！", 3, 5, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", 31);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ユイのプリン", 130, 1, "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", "フラワリングヒールなの～♪", 3, 5, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", 32);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("レイのプリン", 131, 1, "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", "スラッシュテンペスト！なの。", 3, 5, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", 33);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("アキノのプリン", 132, 1, "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", "おーほっほほほ！なの！", 3, 5, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", 34);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ユカリのプリン", 133, 1, "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", "プリしゅわ最高～！なの！", 3, 5, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", 35);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("タマキのプリン", 134, 1, "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", "にゃにゃにゃ！なの！", 3, 5, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", 36);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ミフユのプリン", 135, 0, "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", "効率全開！なの！", 3, 5, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", 37);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ミサトのプリン", 136, 1, "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", "あらあら～なの～♪", 3, 5, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", 38);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ハツネのプリン", 137, 1, "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", "スヤスヤ～…なの…", 3, 5, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", 39);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("アオイのプリン", 138, 1, "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", "ぼっちは卒業なの～！", 3, 5, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", 40);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ノゾミのプリン", 139, 1, "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", "リズムあげるのー！", 3, 5, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", 41);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("チカのプリン", 140, 1, "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", "風の加護を！なの！", 3, 5, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", 42);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ツムギのプリン", 141, 1, "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", "騎士さんのために\nしたんじゃありません！なの！", 3, 5, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", 43);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("クリスティーナのプリン", 142, 0, "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", "宴の始まりなの！", 3, 5, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", 12);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ルカのプリン", 143, 1, "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", "一刀両断、覚悟しな！なの！", 3, 5, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", 44);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ナナカのプリン", 144, 1, "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", "きゅぴーん！キタコレ！なの！", 3, 5, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", 45);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("アンナのプリン", 145, 1, "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", "人呼んで、疾風のミヤコなの！", 3, 5, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", 46);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("エリコのプリン", 146, 1, "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", "クスクス…クスクスクス…なの", 3, 5, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", 47);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ミツキのプリン", 147, 1, "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", 3, 5, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", 48);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("モニカのプリン", 148, 1, "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", "私は子どもではない！なの！", 3, 5, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", 49);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ニノンのプリン", 149, 1, "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", 3, 5, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", 50);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("クウカのプリン", 150, 0, "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", "…ぐふふ、じゅるり～なの", 3, 5, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", 51);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ユキのプリン", 151, 1, "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", "あぁ、ミヤコはなんて美しいの～", 3, 5, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", 52);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("アユミのプリン", 152, 0, "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", 3, 5, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", 53);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ミソギのプリン", 153, 0, "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", "にひひ…イタズラの時間なの！", 3, 5, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", 54);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ミミのプリン", 154, 0, "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", "ぴょんぴょこぴょーん、なの♪", 3, 5, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", 55);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("キョウカのプリン", 155, 0, "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", "へんたいふしんしゃさんなの！", 3, 5, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", 56);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("スズナのプリン", 156, 1, "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", "ハート狙い撃ち！なの！", 3, 5, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", 57);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("イオのプリン", 157, 0, "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", "授業を始めます♪なの！", 3, 5, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", 58);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ミサキのプリン", 158, 1, "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", "立派なレディ、なの！", 3, 5, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", 59);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ホマレのプリン", 159, 1, "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", "プリンを持ってこないと\nお仕置きだよ～☆なの！", 3, 5, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", 60);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("カヤのプリン", 160, 1, "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", "たぎってきたの！", 3, 5, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", 61);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("イノリのプリン", 161, 0, "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", "下剋上、やってやるですなの！", 3, 5, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", 62);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ムイミのプリン", 162, 1, "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", "どけどけ～！なの～！", 3, 5, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", 63);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("ネネカのプリン", 163, 1, "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", "ミラーミラー！なの！", 3, 5, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", 64);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("クレジッタのプリン", 164, 1, "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", "プリンに忠誠を誓いますわ！\nなの！", 3, 5, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", 65);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES ("カリンのプリン", 165, 1, "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", "ギルド管理協会へようこそ！\nなの！", 3, 5, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", 66);
