INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "プリンセスなプリンなの\nお店にあったいろんな食べもの\nを入れられるだけ入れてみたの\nどんな味なのか楽しみなの", 1, 100, "ペコリーヌのプリン", 5, "やばいですね☆なの！", "おいしすぎて食べ始めたら\n止まらない最高のプリンなの\n今のミヤコを止められるヤツは\n誰もいないの", 1);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "いつもオマエのそばに寄りそう\nヤツのぷりんなの。ミヤコの\nワガママもやさしく受け入れて\nくれそうなの", 1, 101, "コッコロのプリン", 5, "主さま、なの！", "なんだかすごくやさしい気持ち\nになる味なの…\nおはようからおやすみまで\nずっと一緒な味なの…", 2);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "ツンツンデレデレなヤツの\nプリンなの。砂糖とスパイスを\n混ぜてみたの。どんな味になる\nか楽しみなの", 0, 102, "キャルのプリン", 5, "ぶっ殺すぞ！なの！\n…デレデレはどこなの？", "いろんな味が一度で楽しめる\nプリンなの。ツンデレはプリン\nにするとおいしいの。ミヤコも\nツンデレを目指してみるの", 3);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "オマエと同じで、一度赤ちゃん\nみたいになったヤツのプリン\nなの。ヒンヤリ、プリンアイス\nにしてみたの", 1, 103, "シェフィのプリン", 5, "おにーたん♪なの！", "あったかいところで食べる\nプリンアイスは最高なの～♪\n安心しすぎて昔の記憶のない\nミヤコも心が子どもになるの～", 4);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "イリヤを見てたら思いついた\nトマトジュースが隠し味の\nプリンなの。栄養満点なの", 1, 104, "イリヤのプリン", 5, "ハーハッハッハ！\n世界征服なのじゃなのー！", "トマトで栄養補給も完璧なの\nどんどん力がみなぎってきて\n復活も夢じゃないの\nミヤコが復活したら…", 5);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "アカリのプリンなの。ヨリと\n同じくらいかわいく作って\nやったの。正直ミヤコは\nおいしければなんでもいいの", 1, 105, "アカリのプリン", 5, "お兄ちゃんのエッチ！　なの！", "見た目がかわいくてもミヤコは\n味にも厳しいの。あむっ…\nこ、これは…！なんだかすごく\n変な気分になってきたの…", 6);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "かわいくつくらないとアカリが\n怒りそうだからヨリのプリンは\nかわいく作ったの。自信作なの", 1, 106, "ヨリのプリン", 5, "えっと…その…あんたのこと…\nだ、大っ嫌いなのーー！", "んむぅ…これはなかなか感想が\n難しいプリンなの…率直に言い\nたいような…心のうちに秘めて\nおきたいような…", 7);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "シノブはいつもミヤコに\nやさしくしてくれるから\n丁寧に作ったの。ドクロ親父は\nいらないから作ってないの", 0, 107, "シノブのプリン", 5, "お父さん…\nいいかげんにしてなの！\nん？プリンの未来は…\n見えなかったの…", "このプリンを食べるとシノブが\nミヤコの中にいるみたいなの！\nシノブの力があれば未来の\nプリンを見放題なの！", 8);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "黒い鎧を脱がないヤツのプリン\nなの。固めのプリンを目指したら\nカチカチになりすぎたの", 1, 108, "ジュンのプリン", 5, "インフェルノシールド！なの！", "固すぎてかじれないから丸吞み\nにしたの。なんだか体が硬く\nなった気がするの。みんなを\n守れる気がするの！", 9);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "オマエをいっつもからかってる\nヤツのプリンなの\nちょっぴり変わったスパイスで\nからかいを表現したの", 1, 109, "トモのプリン", 5, "あなたといると調子が狂うな\n…なの\n翻弄してたのはオマエなの？", "口の中でスパイスがプチプチ\nはじけて新食感なの！\nプチプチがミヤコを\n翻弄するの！", 10);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "王宮騎士団にいるちっこいヤツ\nなの。ちっこいけどやるヤツ\nらしいから、濃いめの味付けに\nしてみたの", 1, 110, "マツリのプリン", 5, "タイガーヒーローボンバー！\nなの！", "ん～見かけによらず濃厚な\nパンチのある味わいが\n口いっぱいに広がるの！", 11);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "なんかすごくクールなプリンなの\nなんかテンションも少し\n低そうなの。これ\n食べちゃっても大丈夫なの？", 1, 111, "クロエのプリン", 5, "ま、いいんじゃん？\nしらんけどなの", "クールな見た目なのにとっても\n優しい味なの！なんだか\n懐かしさを感じるの…", 13);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "きれいにデコれたの！\nカラフルでおいしそうなの\nこれは期待できそうなの", 1, 112, "チエルのプリン", 5, "ぷりーん★なの！", "なんか変わった食感なの…\nちゃる…ちょる…ちぇる？\nちぇるってしてるの\nでもちぇるってなんなの？", 14);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "見た目は完ぺきなの！\nこれをつくったやつは\nきっと天才なの！\n早く食べたいの！", 1, 113, "ユニのプリン", 5, "端的に換言すれば\n青春の味なの？", "味はなんか妙に\n甘酸っぱいの…\n食べたことない味なの\nこの味ってもしかして…", 15);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "オマエから聞いた懐かしい\n味ってヤツを全部入れてみたの\nオマエ記憶がないのに懐かしい\nってどういうことなの？", 1, 114, "サレンのプリン", 5, "幼なじみはあんた…なの？", "ミヤコも幽霊になる前の記憶\nがないのに、なんだか懐かしい\n気持ちになってくるの…", 16);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "たまたま会ったメイドに教えて\nもらった作り方なの。メイドは\nプリンも作れるはずなの。\nなのにこの不安は何なの…？", 1, 115, "スズメのプリン", 5, "ひゃああんなの～！", "おかしいの。このプリンを\n食べてから、３回も転んだの\nミヤコは幽霊だから転んだり\nしないの。なんでなの…？", 17);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "怖がりな子どもが教えてくれた\n小さくてかわいいプリンなの　\n触ってないのにぷるぷる震える\n驚きのやわらかさなの", 0, 116, "クルミのプリン", 5, "サルキデバレヤ！なの！", "甘さのなかに少しのしょっぱさ\nクセになる味なの\nこのプリンはがんばればきっと\nいろんな美味しいになれるの", 18);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "くまのぬいぐるみを見ていたら\nひらめいたの。小さいけど\nしっかりした甘さの元気に\nなれるプリンなの！", 1, 117, "アヤネのプリン", 5, "プリンフルスイングなの～！", "このプリン、食べるとなんだか\nお姉ちゃんになった気分に\nなるの。ピンチのプリンは\nミヤコが守ってやるの！", 19);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "マホマホ王国って国にいる\nお姫様のプリンなの\n魔物も虜にしちゃう\nすてきなプリンなの", 1, 118, "マホのプリン", 5, "みらくるみやりん\nくるりんぱ～☆なの！", "メルヘンパワーがあふれてきて\nいまならミヤコにも魔法が\n使える気がするの！\nほな、いきまひょ～なの！", 20);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "カッコいいプリンを目指して\nワイルドなものをガンガン\n入れてみたの。きっと熱い\nプリンができるはずなの", 1, 119, "マコトのプリン", 5, "ウルフェンバイトなの！", "うおーん！ミヤコなんだか\n強くなった気がするの！\n困ったことがあればミヤコが\nまとめて面倒見てやるの！", 21);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "どっかの国でよくとれる\nゴーヤーに似てるやつを\nいれてみたの！ちょっと\n苦いらしいけど…", 1, 120, "カオリのプリン", 5, "なんくるないさ～！なの！", "一口食べたら元気が\nあふれて止まらないの！\n気分がいいから、一曲\n踊りでも披露してやるの！", 22);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "勘の鋭いやつが好きな食べ物を\nたくさん入れてみたの\n難しいこと考えるのにぴったり\nらしいけど、なんでなの？", 1, 121, "カスミのプリン", 5, "ふふん。簡単な推理なの", "むむむ…なるほどなの\n上質な糖分をふんだんに\n使うことで、脳をより活性化\nできるからなの！", 23);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "搾りたての牛乳を\nたっぷり使ったプリンなの～\nマヒルに手伝ってもらって\nミヤコ直々に搾ってきたの！", 1, 122, "マヒルのプリン", 5, "エリザベス～！なの～！", "ミルクがとっても濃厚で\nほっぺたが落ちちゃうの～♪\nこのプリンからは牧場の…\n自然の息吹を感じるの！", 24);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "マヒルからやさしいヤツだって\n聞いたの。やさしさを表現\nするために奥ゆかしい甘さに\n仕上げてみたの", 1, 123, "シオリのプリン", 5, "私の好きな本…読んで\nもらえたらうれしいです…\nなの", "ほのかな甘さでありながら\nしっかりとした主張も\n感じさせる\n味わい深いプリンなの", 25);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "牧場にいるヤツらしいの\n牛とは違うらしいの\nもふもふ気持ちいいらしいから\n食感にこだわって作ったの", 1, 124, "リマのプリン", 5, "もふもふにしてあげるなの！", "こ、これは…！口の中に入れた\n瞬間にふんわり溶けて\nなくなっちゃうの！\nその食感がやみつきなの！", 26);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "いっつも寝てるやつらしいの\nぐっすり眠れる薬草をたくさん\n入れてみたの。プリンとして\nおいしくなるかはわからないの", 0, 125, "リンのプリン", 5, "めんどくさいな～なの…", "んむぅ…評価が難しい味なの…\nおいしいのはおいしいの…\nん～考えてたら…\n眠くなってきたの…", 27);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "クレープ屋の真っ赤な人に\n教わったプリンなの\n作り方を丁寧に教えてくれて\nとっても優しい人だったの！", 1, 126, "ラビリスタのプリン", 5, "プリンの権能を解放するの～！", "プ…プリンなのにクレープの味\nがするの。味もすごく普通なの\nでも…なんだかプリンの新しい\n扉を開けそうな気がするの…！", 28);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "お店に落ちてた謎のレシピの\nプリンなの。レシピの表紙には\n『お姉ちゃんの愛情たっぷり』\nって書いてあったの", 0, 127, "シズルのプリン", 5, "お姉ちゃんパワー全開！なの！", "とろける食感が絶品なの～♪\n食べるとなんだか幸せな\n気持ちになれるの…これが\n愛情ってやつなの？", 29);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "リノから作り方を教わったの！\nさすがクレープ屋で働いてる\nだけあって美味しそうなの！", 1, 128, "リノのプリン", 5, "お兄ちゃん、プリンをよこすの！", "これで妹力？があがるらしいの。\n妹になればお兄ちゃんに\nプリンをたかり放題なの！\n最高なの～！", 30);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "プリンのレシピで悩んでたら\n通りすがりのおねーさんが\n一緒に考えてくれたプリンなの\nいいヤツもたまにはいるの～♪", 1, 129, "ヒヨリのプリン", 5, "困った時はお互いさまさま！\nなの！", "食べたら元気が湧いてくるの\nだからプリンのレシピのお礼に\nミヤコも人助けを手伝って\nやったの。ミヤコはえらいの！", 31);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "ミヤコががんばって作った\nこだわりのプリンなの～\nプリンは癒しなの。だから優しい\n気持ちで丁寧に作ったの", 1, 130, "ユイのプリン", 5, "フラワリングヒールなの～♪", "はっ…思わずお花畑が見えたの\n身も心も包まれるような\nとっても優しい味がするの\nこれは…まさに癒しなの～♪", 32);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "究極のプリンを作るため\nミヤコは厳しい修行をしたの…\nその結果あって完成した\nクールで完璧なプリンなの！", 1, 131, "レイのプリン", 5, "スラッシュテンペスト！なの。", "確かに完璧なプリンなの\nでも究極のプリンと呼ぶには\nまだまだ修行が必要なの…\nプリン作りは奥が深いの…！", 33);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "豪華で高級な\nお嬢様プリンなの\n中には宝石みたいな\n大きいイチゴを入れるの！", 1, 132, "アキノのプリン", 5, "おーほっほほほ！なの！", "味にも素材にもこだわりが\nあるの。一流は妥協しないの\nもっと豪華に、もっとおいしく\nできるはず…", 34);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "外はしっかり、中はふわとろの\nプリンにするの\n泡みたいなしゅわしゅわの\n生クリームも入れるの♪", 1, 133, "ユカリのプリン", 5, "プリしゅわ最高～！なの！", "プリンは飲み物とも言うの\nぐいっていってみるの\n…ふわしゅわでとろけるの～！\n体に染みわたるの～", 35);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "餡子を入れて\nオーブンでこんがり焼くの\nたい…焼きプリンなの\n斬新なの！", 1, 134, "タマキのプリン", 5, "にゃにゃにゃ！なの！", "このプリンは焼きたてのうちに\n食べるのが一番なの…！\n熱いけどおいしくて\nうずうずしてきたの…！", 36);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "栄養バランスを考えて\n木の実をたくさん入れるの\nあれこれ忙しいときでも\n頼りになるプリンなの！", 0, 135, "ミフユのプリン", 5, "効率全開！なの！", "プリンはいつでも手軽に\n食べられるのも魅力なの\nこれは食べ応えもあるから一つ\n食べればいっぱい動けるの", 37);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "エルフの森で子どもにもらった\n薬草を使って作ったの\nこんな優しい味のプリンは\n初めてなの…", 1, 136, "ミサトのプリン", 5, "あらあら～なの～♪", "このプリンを食べてから\nいい子になった気がするの\n大好きなプリンを泣いてる子に\nあげたの。いい気分なの", 38);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "お昼寝でみた夢の中に\nでてきた不思議なプリンを\n作ってみたの。見てたらまた\n眠くなってきたの…", 1, 137, "ハツネのプリン", 5, "スヤスヤ～…なの…", "あまいプリンが口の中で\nぽんぽん弾ける未知の味なの！\n今なら不思議な力で世界も\n救えそうなの！でも眠くなるの…", 39);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "お店のはしっこに一つだけ\n残ったおもちゃを見て思い\nついたの。他のプリンと一緒に\n置いておくと震えだすの", 1, 138, "アオイのプリン", 5, "ぼっちは卒業なの～！", "知らないうちにはしっこに\n隠れちゃってたの。食べたら\nミヤコ、シノブたちに会いに\nいきたくなってきたの", 40);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "トップオブアイドルの\nプリンなの。キラキラした\nものを入れて、あつ～い\nオーブンで焼いてみたの", 1, 139, "ノゾミのプリン", 5, "リズムあげるのー！", "口の中に華やかな甘さが\n広がる…これがアイドルなの？\n急に歌って踊りたく\nなってきたの！", 41);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "ミヤコにプリンをくれるすごく\nいいヤツのプリンなの\n精霊に呼びかけながら\n一生懸命かき混ぜたの", 1, 140, "チカのプリン", 5, "風の加護を！なの！", "プリンの生地がとっても\nなめらかで最高の舌触りなの！\n精霊、ミヤコのプリンのために…", 42);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "歌って踊って衣装も作れるヤツ\nのプリンなの。このプリンは\nきれいに包装するの。プリンを\n最高の形で着飾らせるの", 1, 141, "ツムギのプリン", 5, "騎士さんのために\nしたんじゃありません！なの！", "包装は食べるまでの手間が\n増えてめんどくさいの。でも\nそのめんどくささもプリンの\n味を引き立てるの", 43);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "プリンをくれないならどうでも\nいいヤツなの。でも横で\nいろいろ言ってきたから\nがんばって作ったの", 0, 142, "クリスティーナのプリン", 5, "宴の始まりなの！", "んむっ！？\nく、口のなかでプリンの\nおいしさが大暴れなの！\n止まらないの！", 12);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "大人な甘さ控えめのプリンを\n作るの。試しにお魚を入れて\nみるの。プリンの可能性は\n無限大なの！", 1, 143, "ルカのプリン", 5, "一刀両断、覚悟しな！なの！", "大人の味なの。今やミヤコは\nみんなの頼れる姐御なの！\n悪さするやつはミヤコが\n許さないの！", 44);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "サイカワサイツヨな\n魔法のプリンなの\n中身をカラフルにするの～\n甘いプリンは頭が冴えるの！", 1, 144, "ナナカのプリン", 5, "きゅぴーん！キタコレ！なの！", "一口食べる度に味が変わって\n魔法みたいなの！\nもしかして全部食べたら\nミヤコも魔法少女になれるの？", 45);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "小難しい言葉をささやきながら\n作ったら魔力あふれるプリンが\n爆誕したの！でも、不思議と\nイタイタしいのはなぜなの～？", 1, 145, "アンナのプリン", 5, "人呼んで、疾風のミヤコなの！", "一口食べたら謎の組織に\n狙われたり、前世の記憶が蘇る\n気がするの～設定盛りすぎなの\nミヤコは、ミヤコは…", 46);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "甘酸っぱい赤い木の実を\n切り刻んで入れるの\n甘くてちょっぴり刺激的な\nヤンデレのプリンなの", 1, 146, "エリコのプリン", 5, "クスクス…クスクスクス…なの", "なんか普通のプリンよりも\nいい匂いがするの～…\nプリンのことがもっともっと\n好きになっちゃうの…", 47);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "健康に気を遣うミヤコは\nプリンにお薬を入れてみたの～\nでもなんだか毒々しいから\n彩りにバラを添えてみたの～", 1, 147, "ミツキのプリン", 5, "どんなプリンも\nミヤコの隻眼からは\n逃れられないの～！", "鼻に抜ける薬のにおいがアレ\nだけど大人っぽくて頭が\nよくなった気がするの。無性に\n実験したくてしかたないの", 48);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "小さいけどしっかりした\n軍人のプリンなの\nちょっと固めにして\n砂糖はいっぱい入れるの", 1, 148, "モニカのプリン", 5, "私は子どもではない！なの！", "ん～王道なプリンは\n甘くて安定したおいしさなの～\n思わず笑顔になっちゃうの\nにこにこなの～♪…はっ！", 49);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "オーエド町で手に入れた素材と\nトーゴクに伝わる術をかけて\nプリンを作るの。シュババ！\n忍法隠し味の術なの！", 1, 149, "ニノンのプリン", 5, "ショーグン！　ミヤコと一緒に\n天下統一するデースなの！", "このプリンを食べてから\n誰かにお仕えしなきゃ\nいけない気がするデースなの\nそれはショ、ショー…", 50);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "オマエが持ってきた手錠を\nヒントに作ったプリンなの\nプリンが動けないように\nカチカチに固めたの", 0, 150, "クウカのプリン", 5, "…ぐふふ、じゅるり～なの", "カチカチに固めたせいで食感が\nプリンのようで\nプリンじゃないの…でもこの\nもどかしさが癖になるの…", 51);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "とっても美しいプリンなの\n美しいものを手当たり次第\n入れたらすごいことになったの", 1, 151, "ユキのプリン", 5, "あぁ、ミヤコはなんて美しいの～", "味も美しい以外の感想が\n出てこないの。ていうかこれを\n食べたミヤコも美しいの！\n鏡、鏡はどこなの！", 52);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "知らないうちにお菓子が売れた\n時に思いついたプリンなの。\n作り方もよく覚えてない…\nけど、きっとおいしいの！", 0, 152, "アユミのプリン", 5, "ジー…ってミヤコなにしてるの\nミヤコはストーカーじゃないの！", "うん、やっぱりおいしく…\nん？あそこにいるのは\nアイツなの。あれ？\n体が勝手にアイツの方に…", 53);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "イタズラ好きの手下と一緒に\n作ったプリンなの。見た目は\nかわいいけど、作り方は\n意外と複雑なの～", 0, 153, "ミソギのプリン", 5, "にひひ…イタズラの時間なの！", "見た目通り甘々で優しい味なの\nでも、レシピを見たら裏面に\n別の作り方が書いてあるの。\nこれだと激辛プリンに…", 54);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "素直でやさしい手下と一緒に\n動物のぬいぐるみをいっぱい\n並べて作ったの", 0, 154, "ミミのプリン", 5, "ぴょんぴょこぴょーん、なの♪", "ん～、ふわふわの甘々なの！\nなんだか楽しくなって\n心も体もるんたったしてくるの", 55);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "幽霊を怖がっている手下と\n一緒に作ったプリンなの。\n小さいクセにきっちりしてて\nとってもかわいいプリンなの", 0, 155, "キョウカのプリン", 5, "へんたいふしんしゃさんなの！", "ひんやりしてて暑い日でも\nおいしく食べられそうなの！\nん？これを買いたい大人が\nいる？それって…", 56);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "カリスマモデルなプリンなの\nモリモリにデコレーションして\nカリスマ感を演出したの", 1, 156, "スズナのプリン", 5, "ハート狙い撃ち！なの！", "見て食べて楽しめる新機軸の\nプリンなの。ミヤコは幽霊\nだけど自分の足でランウェイを\n歩きたくなってきたの！", 57);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "学校の先生をしてるヤツの\nプリンなの。お堅くしようと\nしたら、いつの間にか色っぽく\nなっちゃったの。なんでなの？", 0, 157, "イオのプリン", 5, "授業を始めます♪なの！", "プ、プリンは全部ミヤコのもの\nなのに、なんだかこのプリンは\nいろんな人に教えたく\nなっちゃう不思議な味なの！", 58);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "少し背伸びしたおませな子を\n表現したプリンなの。あえて\n甘さ控えめなの。カラメルは\n後乗せできるから安心するの", 1, 158, "ミサキのプリン", 5, "立派なレディ、なの！", "ミヤコはプリンならどんなもの\nでも食べるの。でもカラメルを\nちょっとだけ足すの。\nちょっとだけなら…", 59);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "謎に包まれたプリンができたの\n何か隠してるようなの…\nこれは危険なプリンなの！", 1, 159, "ホマレのプリン", 5, "プリンを持ってこないと\nお仕置きだよ～☆なの！", "味はすごいゴージャスなの！\nボスの風格があるの！\nビッグなミヤコに\nプリンを捧げるの！", 60);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "ワイルドな見た目なの！\nこれは期待できそうなの。\nきっと躍動感あふれる味なの！", 1, 160, "カヤのプリン", 5, "たぎってきたの！", "最初の一口目が\nすごいインパクトなの！\n無性にケンカしたくなるの！\nプリンをよこせなの！", 61);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "なんか見た目がパッとしないの\nこれほんとに大丈夫なの？\nなんか頼りないの…", 0, 161, "イノリのプリン", 5, "下剋上、やってやるですなの！", "見た目はアレだけど味はおいし\nいの…あと主張もはっきりして\nるの！やる気と自信だけは\n出てきたのうおおおーなの！", 62);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "天下の大悪党のプリンなの\nこれまでのジョーシキに\n捉われないプリンを\n作ってみたの～", 1, 162, "ムイミのプリン", 5, "どけどけ～！なの～！", "むむむ…食べたら\nやる気が湧いてきたの！\nミヤコを邪魔するやつはみんな\nまとめてぶっ飛ばしてやるの～", 63);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "いろんなものに変身したり\n分身したりできるヤツの\nプリンなの。型で同じプリンを\nたくさんたくさん作ったの", 1, 163, "ネネカのプリン", 5, "ミラーミラー！なの！", "いつのまにか作ったの全部\n食べちゃったの。なんで\nプリンは分身しないの？\n無限に増えろなの！", 64);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "金の亡者のプリンなの\n金にものを言わせて作って\nみたの。おばあちゃんの\nプリンと対極なの", 1, 164, "クレジッタのプリン", 5, "プリンに忠誠を誓いますわ！\nなの！", "むぅ…認めたくないけど\nおいしいの…これを何個も\n作れるほどのお金を稼ぐのは\n大変なの…でもおいしいから…", 65);
INSERT INTO `v1_32d50b6548d185ae036a9e5b3f38aeb863ab0e1f76fd2280871d7b1c4b419b47` VALUES (3, "みんなお世話になるギルド\n管理協会職員のプリンなの\nお役所感を出すために手堅く\n王道に作ったの", 1, 165, "カリンのプリン", 5, "ギルド管理協会へようこそ！\nなの！", "手堅い…！手堅いの…！\n隙のないプリンなの！\n今のミヤコなら完璧な\n書類作成ができそうなの！", 66);
