INSERT INTO `v1_361c05539f0a2aaf405bb253064073e8babd2dac99735d2ef75878fe719cb3fa` VALUES (24001, 10046, 102301, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アヤネ", 401, 0.0, 24, 0.0, 2, "やったね、ぷうきち！\nこれならお兄ちゃんたちの\nお手本になれたよね！", 2, 0.0);
