INSERT INTO `v1_361c05539f0a2aaf405bb253064073e8babd2dac99735d2ef75878fe719cb3fa` VALUES (24004, 10046, 192201, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "アサヒ", 701, 1.99, 24, 0.0, 2, "特訓の成果が出たみたいだ！\nもっと練習を重ねて\nこの調子で上達していこう！", 1, 0.0);
