INSERT INTO `v1_361c05539f0a2aaf405bb253064073e8babd2dac99735d2ef75878fe719cb3fa` VALUES (24007, 10046, 192301, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "部員３", 801, 0.0, 24, 0.0, 2, "このくらい余裕だ\nオレたちは全国大会を\n目指してるんだからな", 2, 0.0);
