INSERT INTO `v1_361c05539f0a2aaf405bb253064073e8babd2dac99735d2ef75878fe719cb3fa` VALUES (24018, 10046, 192401, "2030/12/17 14:59:59", "2020/07/31 12:00:00", "ダイチ", 613, 0.0, 24, 0.0, 2, "おっしゃあ！\nこの勢いで全国に行ってやるぜ！\n全員三振で仕留めてやる！", 2, 0.0);
